/**
 * AI Assistant component for natural language rule queries and game assistance
 */

import React, { useState, useRef, useEffect } from 'react';
import './AIAssistant.css';

export interface AssistantMessage {
  id: string;
  type: 'user' | 'assistant';
  content: string;
  timestamp: Date;
  metadata?: {
    provider?: string;
    model?: string;
    costUSD?: number;
    latencyMs?: number;
  };
}

export interface AIAssistantProps {
  gameSystem?: string;
  campaignId?: string;
  playerLevel?: number;
  characterClass?: string;
  onClose?: () => void;
}

export const AIAssistant: React.FC<AIAssistantProps> = ({_gameSystem = 'D&D 5e', _campaignId, _playerLevel, _characterClass, _onClose, _}) => {
  const [messages, setMessages] = useState<AssistantMessage[]>([
    {
      id: '1',
      type: 'assistant',
      content: `Hello! I'm your D&D 5e rules assistant. I can help you with:
• Rule explanations and clarifications
• Spell descriptions and mechanics
• Combat action suggestions
• Character creation advice
• DM rulings and interpretations

What would you like to know?`,
      timestamp: new Date(),
    },
  ]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!input.trim() || isLoading) return;

    const userMessage: AssistantMessage = {
      id: Date.now().toString(),
      type: 'user',
      content: input.trim(),
      timestamp: new Date(),
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      const response = await fetch('/api/assistant/query', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          question: userMessage.content,
          context: {
            gameSystem,
            campaignId,
            playerLevel,
            characterClass,
          },
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to get assistant response');
      }

      const data = await response.json();

      const assistantMessage: AssistantMessage = {
        id: (Date.now() + 1).toString(),
        type: 'assistant',
        content: data.answer,
        timestamp: new Date(),
        metadata: data.metadata,
      };

      setMessages(prev => [...prev, assistantMessage]);
    } catch (_error) {
      const errorMessage: AssistantMessage = {
        id: (Date.now() + 1).toString(),
        type: 'assistant',
        content: 'Sorry, I encountered an error while processing your request. Please try again.',
        timestamp: new Date(),
      };

      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleQuickAction = async (_action: string, _prompt: string) => {
    setInput(prompt);
    // Trigger form submission
    setTimeout(() => {
      const form = document.querySelector('.ai-assistant-form') as HTMLFormElement;
      form?.requestSubmit();
    }, 100);
  };

  const formatTimestamp = (timestamp: Date) => {
    return timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <div className="ai-assistant">
      <div className="ai-assistant-header">
        <h3>AI Rules Assistant</h3>
        <div className="ai-assistant-context">
          {gameSystem} • Level {playerLevel} {characterClass}
        </div>
        {onClose && (
          <button className="ai-assistant-close" onClick={onClose}>
            ×
          </button>
        )}
      </div>

      <div className="ai-assistant-quick-actions">
        <button
          onClick={() => handleQuickAction('spell', 'Explain the spell Fireball')}
          className="quick-action-btn"
        >
          Explain Spell
        </button>
        <button
          onClick={() => handleQuickAction('combat', 'What are my combat options?')}
          className="quick-action-btn"
        >
          Combat Help
        </button>
        <button
          onClick={() => handleQuickAction('rule', 'How does advantage work?')}
          className="quick-action-btn"
        >
          Rule Query
        </button>
      </div>

      <div className="ai-assistant-messages">
        {messages.map((message) => (
          <div
            key={message.id}
            className={`message ${message.type === 'user' ? 'message-user' : 'message-assistant'}`}
          >
            <div className="message-content">
              {message.content.split('\n').map((_line, __index) => (
                <p key={index}>{line}</p>
              ))}
            </div>
            <div className="message-meta">
              <span className="message-time">{formatTimestamp(message.timestamp)}</span>
              {message.metadata && (
                <span className="message-provider">
                  {message.metadata.provider} • {message.metadata.latencyMs}ms
                  {message.metadata.costUSD && ` • $${message.metadata.costUSD.toFixed(4)}`}
                </span>
              )}
            </div>
          </div>
        ))}
        {isLoading && (
          <div className="message message-assistant">
            <div className="message-content">
              <div className="typing-indicator">
                <span></span>
                <span></span>
                <span></span>
              </div>
            </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>

      <form className="ai-assistant-form" onSubmit={handleSubmit}>
        <div className="ai-assistant-input-group">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Ask about rules, spells, combat, or anything D&D related..."
            className="ai-assistant-input"
            disabled={isLoading}
          />
          <button
            type="submit"
            disabled={!input.trim() || isLoading}
            className="ai-assistant-send"
          >
            Send
          </button>
        </div>
      </form>
    </div>
  );
};

/**
 * Combat tracker component for managing initiative and combat flow
 */

import React, { useState, _useEffect} from 'react';
import './CombatTracker.css';

export interface Combatant {
  id: string;
  name: string;
  type: 'player' | 'npc' | 'monster';
  initiative: number;
  hitPoints: {
    current: number;
    max: number;
    temporary: number;
  };
  armorClass: number;
  conditions: Array<{
    name: string;
    duration: number;
    description: string;
  }>;
  isActive: boolean;
  isVisible: boolean;
}

export interface CombatTrackerProps {
  combatants: Combatant[];
  currentTurn: number;
  round: number;
  isActive: boolean;
  onInitiativeChange: (_combatantId: string, _initiative: number) => void;
  onHealthChange: (_combatantId: string, _current: number, _temporary?: number) => void;
  onConditionAdd: (_combatantId: string, condition: { name: string; duration: number; description: string }) => void;
  onConditionRemove: (_combatantId: string, _conditionName: string) => void;
  onNextTurn: () => void;
  onPreviousTurn: () => void;
  onStartCombat: () => void;
  onEndCombat: () => void;
  onAddCombatant: (combatant: Omit<Combatant, 'id'>) => void;
  onRemoveCombatant: (_combatantId: string) => void;
  readOnly?: boolean;
}

export const CombatTracker: React.FC<CombatTrackerProps> = ({_combatants, _currentTurn, _round, _isActive, _onInitiativeChange, _onHealthChange, _onConditionAdd, _onConditionRemove, _onNextTurn, _onPreviousTurn, _onStartCombat, _onEndCombat, _onAddCombatant, _onRemoveCombatant, _readOnly = false, _}) => {
  const [showAddForm, setShowAddForm] = useState(false);
  const [newCombatant, setNewCombatant] = useState({
    name: '',
    type: 'monster' as const,
    initiative: 10,
    hitPoints: { current: 10, max: 10, temporary: 0 },
    armorClass: 10,
    conditions: [],
    isActive: true,
    isVisible: true,
  });

  const sortedCombatants = [...combatants].sort((_a, _b) => b.initiative - a.initiative);
  const currentCombatant = sortedCombatants[currentTurn];

  const handleAddCombatant = () => {
    if (newCombatant.name.trim()) {
      onAddCombatant(newCombatant);
      setNewCombatant({
        name: '',
        type: 'monster',
        initiative: 10,
        hitPoints: { current: 10, max: 10, temporary: 0 },
        armorClass: 10,
        conditions: [],
        isActive: true,
        isVisible: true,
      });
      setShowAddForm(false);
    }
  };

  const rollInitiative = (combatant: Combatant) => {
    const roll = Math.floor(Math.random() * 20) + 1;
    const dexMod = Math.floor((10 - 10) / 2); // Simplified - would use actual dex modifier
    onInitiativeChange(combatant.id, roll + dexMod);
  };

  const getHealthPercentage = (combatant: Combatant) => {
    return (combatant.hitPoints.current / combatant.hitPoints.max) * 100;
  };

  const getHealthStatus = (_percentage: number) => {
    if (percentage <= 0) return 'dead';
    if (percentage <= 25) return 'critical';
    if (percentage <= 50) return 'bloodied';
    if (percentage <= 75) return 'injured';
    return 'healthy';
  };

  return (
    <div className="combat-tracker">
      <div className="combat-header">
        <div className="combat-status">
          {isActive ? (
            <>
              <h3>Combat Active</h3>
              <div className="round-info">
                <span>Round {round}</span>
                {currentCombatant && (
                  <span className="current-turn">
                    Current: {currentCombatant.name}
                  </span>
                )}
              </div>
            </>
          ) : (
            <h3>Combat Inactive</h3>
          )}
        </div>

        {!readOnly && (
          <div className="combat-controls">
            {!isActive ? (
              <button className="start-combat-btn" onClick={onStartCombat}>
                Start Combat
              </button>
            ) : (
              <>
                <button className="prev-turn-btn" onClick={onPreviousTurn}>
                  Previous
                </button>
                <button className="next-turn-btn" onClick={onNextTurn}>
                  Next Turn
                </button>
                <button className="end-combat-btn" onClick={onEndCombat}>
                  End Combat
                </button>
              </>
            )}
          </div>
        )}
      </div>

      <div className="combatants-list">
        {sortedCombatants.map((combatant, _index) => (
          <div
            key={combatant.id}
            className={`combatant-card ${index === currentTurn ? 'current-turn' : ''} ${
              combatant.hitPoints.current <= 0 ? 'unconscious' : ''
            }`}
          >
            <div className="combatant-header">
              <div className="combatant-name">
                <span className="name">{combatant.name}</span>
                <span className={`type-badge ${combatant.type}`}>
                  {combatant.type}
                </span>
              </div>

              <div className="initiative-section">
                <label>Initiative:</label>
                {!readOnly ? (
                  <div className="initiative-controls">
                    <input
                      type="number"
                      value={combatant.initiative}
                      onChange={(e) => onInitiativeChange(combatant.id, parseInt(e.target.value) || 0)}
                      className="initiative-input"
                    />
                    <button
                      className="roll-initiative-btn"
                      onClick={() => rollInitiative(combatant)}
                      title="Roll Initiative"
                    >
                      🎲
                    </button>
                  </div>
                ) : (
                  <span className="initiative-display">{combatant.initiative}</span>
                )}
              </div>

              {!readOnly && (
                <button
                  className="remove-combatant-btn"
                  onClick={() => onRemoveCombatant(combatant.id)}
                  title="Remove Combatant"
                >
                  ✕
                </button>
              )}
            </div>

            <div className="combatant-stats">
              <div className="health-section">
                <label>HP:</label>
                <div className="health-controls">
                  {!readOnly ? (
                    <>
                      <input
                        type="number"
                        value={combatant.hitPoints.current}
                        onChange={(e) => onHealthChange(combatant.id, parseInt(e.target.value) || 0)}
                        className="health-input"
                        min="0"
                        max={combatant.hitPoints.max}
                      />
                      <span>/</span>
                      <span className="max-hp">{combatant.hitPoints.max}</span>
                      {combatant.hitPoints.temporary > 0 && (
                        <span className="temp-hp">+{combatant.hitPoints.temporary}</span>
                      )}
                    </>
                  ) : (
                    <span>
                      {combatant.hitPoints.current}/{combatant.hitPoints.max}
                      {combatant.hitPoints.temporary > 0 && (
                        <span className="temp-hp">+{combatant.hitPoints.temporary}</span>
                      )}
                    </span>
                  )}
                </div>
                <div className={`health-bar ${getHealthStatus(getHealthPercentage(combatant))}`}>
                  <div
                    className="health-fill"
                    style={{ width: `${Math.max(0, getHealthPercentage(combatant))}%` }}
                  />
                </div>
              </div>

              <div className="ac-section">
                <label>AC:</label>
                <span className="ac-value">{combatant.armorClass}</span>
              </div>
            </div>

            {combatant.conditions.length > 0 && (
              <div className="conditions-section">
                <label>Conditions:</label>
                <div className="conditions-list">
                  {combatant.conditions.map((condition, _idx) => (
                    <div key={idx} className="condition-badge">
                      <span className="condition-name">{condition.name}</span>
                      {condition.duration > 0 && (
                        <span className="condition-duration">({condition.duration})</span>
                      )}
                      {!readOnly && (
                        <button
                          className="remove-condition-btn"
                          onClick={() => onConditionRemove(combatant.id, condition.name)}
                          title="Remove Condition"
                        >
                          ✕
                        </button>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        ))}
      </div>

      {!readOnly && (
        <div className="add-combatant-section">
          {!showAddForm ? (
            <button
              className="add-combatant-btn"
              onClick={() => setShowAddForm(true)}
            >
              Add Combatant
            </button>
          ) : (
            <div className="add-combatant-form">
              <div className="form-row">
                <input
                  type="text"
                  placeholder="Name"
                  value={newCombatant.name}
                  onChange={(e) => setNewCombatant({ ...newCombatant, name: e.target.value })}
                  className="name-input"
                />
                <select
                  value={newCombatant.type}
                  onChange={(e) => setNewCombatant({ ...newCombatant, type: e.target.value as any })}
                  className="type-select"
                >
                  <option value="player">Player</option>
                  <option value="npc">NPC</option>
                  <option value="monster">Monster</option>
                </select>
              </div>

              <div className="form-row">
                <label>Initiative:</label>
                <input
                  type="number"
                  value={newCombatant.initiative}
                  onChange={(e) => setNewCombatant({ ...newCombatant, initiative: parseInt(e.target.value) || 0 })}
                  className="stat-input"
                />

                <label>HP:</label>
                <input
                  type="number"
                  value={newCombatant.hitPoints.max}
                  onChange={(e) => {
                    const hp = parseInt(e.target.value) || 0;
                    setNewCombatant({
                      ...newCombatant,
                      hitPoints: { current: hp, max: hp, temporary: 0 }
                    });
                  }}
                  className="stat-input"
                />

                <label>AC:</label>
                <input
                  type="number"
                  value={newCombatant.armorClass}
                  onChange={(e) => setNewCombatant({ ...newCombatant, armorClass: parseInt(e.target.value) || 0 })}
                  className="stat-input"
                />
              </div>

              <div className="form-actions">
                <button onClick={handleAddCombatant} className="confirm-btn">
                  Add
                </button>
                <button onClick={() => setShowAddForm(false)} className="cancel-btn">
                  Cancel
                </button>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

/**
 * Integrated Combat Tracker - Uses real encounter data and API integration
 */

import React, { useState, useEffect, useCallback } from 'react';
import { useEncounter, CombatActor, EncounterData } from '../hooks/useEncounter';
import { useCharacter } from '../hooks/useCharacter';
import './CombatTracker.css';

interface CombatTrackerIntegratedProps {
  encounterId?: string;
  campaignId: string;
  onEncounterChange?: (encounter: EncounterData | null) => void;
  readOnly?: boolean;
}

interface AddCombatantData {
  type: 'character' | 'monster';
  id: string;
  name?: string; // For monster instances
}

export const CombatTrackerIntegrated: React.FC<CombatTrackerIntegratedProps> = ({_encounterId, _campaignId, _onEncounterChange, _readOnly = false}) => {
  const { encounter, 
    isLoading, 
    error, 
    createEncounter, 
    getEncounter, 
    startEncounter, 
    endEncounter, 
    addCharacterToEncounter, 
    addMonsterToEncounter, 
    updateActorHealth
   } = useEncounter();

  const { characters,  getCharacters  } = useCharacter();

  const [showAddDialog, setShowAddDialog] = useState(false);
  const [addCombatantData, setAddCombatantData] = useState<AddCombatantData>({
    type: 'character',
    id: ''
  });

  // Load encounter data
  useEffect(() => {
    if (encounterId) {
      getEncounter(encounterId);
    }
  }, [encounterId, getEncounter]);

  // Load available characters
  useEffect(() => {
    getCharacters();
  }, [getCharacters]);

  // Notify parent of encounter changes
  useEffect(() => {
    if (onEncounterChange) {
      onEncounterChange(encounter);
    }
  }, [encounter, onEncounterChange]);

  const handleCreateEncounter = useCallback(async (name: string) => {
    try {
      await createEncounter({
        name,
        campaignId,
        characterIds: [],
        monsters: []
      });
    } catch (error) {
      console.error('Failed to create encounter:', error);
    }
  }, [createEncounter, campaignId]);

  const handleStartCombat = useCallback(async () => {
    if (!encounter?.id) return;

    try {
      await startEncounter(encounter.id);
    } catch (error) {
      console.error('Failed to start encounter:', error);
    }
  }, [encounter?.id, startEncounter]);

  const handleEndCombat = useCallback(async () => {
    if (!encounter?.id) return;

    try {
      await endEncounter(encounter.id);
    } catch (error) {
      console.error('Failed to end encounter:', error);
    }
  }, [encounter?.id, endEncounter]);

  const handleHealthChange = useCallback(async (
    actorId: string, 
    _current: number, 
    _temporary: number = 0
  ) => {
    const actor = encounter?.actors.find(a => a.id === actorId);
    if (!actor) return;

    try {
      await updateActorHealth(actorId, {
        current,
        max: actor.hitPoints.max,
        temporary
      });
    } catch (error) {
      console.error('Failed to update actor health:', error);
    }
  }, [encounter?.actors, updateActorHealth]);

  const handleAddCombatant = useCallback(async () => {
    if (!encounter?.id || !addCombatantData.id) return;

    try {
      if (addCombatantData.type === 'character') {
        await addCharacterToEncounter(encounter.id, addCombatantData.id);
      } else {
        await addMonsterToEncounter(encounter.id, addCombatantData.id, addCombatantData.name);
      }

      setShowAddDialog(false);
      setAddCombatantData({ type: 'character', id: '' });
    } catch (error) {
      console.error('Failed to add combatant:', error);
    }
  }, [encounter?.id, addCombatantData, addCharacterToEncounter, addMonsterToEncounter]);

  const getHealthBarColor = (actor: CombatActor): string => {
    const percentage = (actor.hitPoints.current / actor.hitPoints.max) * 100;
    if (percentage <= 25) return '#dc3545'; // Red
    if (percentage <= 50) return '#fd7e14'; // Orange
    if (percentage <= 75) return '#ffc107'; // Yellow
    return '#28a745'; // Green
  };

  const getHealthStatus = (actor: CombatActor): string => {
    const percentage = (actor.hitPoints.current / actor.hitPoints.max) * 100;
    if (actor.hitPoints.current <= 0) return 'Unconscious';
    if (percentage <= 25) return 'Critical';
    if (percentage <= 50) return 'Bloodied';
    if (percentage <= 75) return 'Injured';
    return 'Healthy';
  };

  if (isLoading) {
    return <div className="combat-tracker loading">Loading encounter...</div>;
  }

  if (error) {
    return <div className="combat-tracker error">Error: {error}</div>;
  }

  if (!encounter) {
    return (
      <div className="combat-tracker no-encounter">
        <h3>No Encounter Active</h3>
        <button 
          onClick={() => handleCreateEncounter('New Encounter')}
          disabled={readOnly}
        >
          Create Encounter
        </button>
      </div>
    );
  }

  const sortedActors = [...encounter.actors].sort((_a, _b) => b.initiative - a.initiative);
  const _currentActor = sortedActors[encounter.currentTurn] || null;

  return (
    <div className="combat-tracker">
      <div className="combat-header">
        <h3>{encounter.name}</h3>
        <div className="combat-controls">
          {encounter.isActive ? (
            <>
              <span className="round-counter">Round {encounter.currentRound}</span>
              <button onClick={handleEndCombat} disabled={readOnly}>End Combat</button>
            </>
          ) : (
            <button 
              onClick={handleStartCombat} 
              disabled={readOnly || encounter.actors.length === 0}
            >
              Start Combat
            </button>
          )}
        </div>
      </div>

      <div className="combatants-list">
        {sortedActors.map((actor, _index) => (
          <div 
            key={actor.id} 
            className={`combatant ${index === encounter.currentTurn ? 'active-turn' : ''} ${actor.type}`}
          >
            <div className="combatant-header">
              <div className="combatant-name">
                <span className="name">{actor.name}</span>
                <span className="type-badge">{actor.type === 'character' ? 'PC' : 'NPC'}</span>
              </div>
              <div className="initiative">
                <label>Init:</label>
                <input 
                  type="number" 
                  value={actor.initiative} 
                  onChange={(_e) => {
                    // TODO: Implement initiative change
                    console.log('Initiative change not implemented yet');
                  }}
                  disabled={readOnly}
                  className="initiative-input"
                />
              </div>
            </div>

            <div className="combatant-stats">
              <div className="health-section">
                <div className="health-bar-container">
                  <div 
                    className="health-bar" 
                    style={{ 
                      width: `${(actor.hitPoints.current / actor.hitPoints.max) * 100}%`,
                      backgroundColor: getHealthBarColor(actor)
                    }}
                  />
                </div>
                <div className="health-numbers">
                  <input 
                    type="number" 
                    value={actor.hitPoints.current}
                    onChange={(e) => handleHealthChange(actor.id, parseInt(e.target.value) || 0, actor.hitPoints.temporary)}
                    disabled={readOnly}
                    className="health-input current"
                    min="0"
                    max={actor.hitPoints.max + actor.hitPoints.temporary}
                  />
                  <span>/</span>
                  <span className="max-hp">{actor.hitPoints.max}</span>
                  {actor.hitPoints.temporary > 0 && (
                    <>
                      <span>+</span>
                      <input 
                        type="number" 
                        value={actor.hitPoints.temporary}
                        onChange={(e) => handleHealthChange(actor.id, actor.hitPoints.current, parseInt(e.target.value) || 0)}
                        disabled={readOnly}
                        className="health-input temp"
                        min="0"
                      />
                      <span className="temp-label">temp</span>
                    </>
                  )}
                </div>
                <div className="health-status">{getHealthStatus(actor)}</div>
              </div>

              <div className="combat-stats">
                <span className="ac">AC: {actor.armorClass}</span>
                <span className="speed">Speed: {actor.speed}ft</span>
              </div>

              {actor.conditions.length > 0 && (
                <div className="conditions">
                  {actor.conditions.map((condition, _idx) => (
                    <span key={idx} className="condition-badge">
                      {condition.type}
                      {condition.duration > 0 && ` (${condition.duration})`}
                    </span>
                  ))}
                </div>
              )}

              {actor.actions.length > 0 && (
                <div className="actions">
                  <details>
                    <summary>Actions ({actor.actions.length})</summary>
                    <div className="actions-list">
                      {actor.actions.slice(0, 3).map(action => (
                        <div key={action.id} className="action-item">
                          <span className="action-name">{action.name}</span>
                          <span className="action-type">({action.type.replace('', ' ')})</span>
                        </div>
                      ))}
                      {actor.actions.length > 3 && (
                        <div className="action-item">+ {actor.actions.length - 3} more</div>
                      )}
                    </div>
                  </details>
                </div>
              )}
            </div>
          </div>
        ))}

        {encounter.actors.length === 0 && (
          <div className="no-combatants">
            <p>No combatants added to this encounter.</p>
            <p>Click "Add Combatant" to get started.</p>
          </div>
        )}
      </div>

      {!readOnly && (
        <div className="combat-actions">
          <button 
            onClick={() => setShowAddDialog(true)}
            className="add-combatant-btn"
          >
            Add Combatant
          </button>
        </div>
      )}

      {showAddDialog && (
        <div className="modal-overlay">
          <div className="modal">
            <h4>Add Combatant</h4>

            <div className="form-group">
              <label>Type:</label>
              <select 
                value={addCombatantData.type} 
                onChange={(e) => setAddCombatantData(prev => ({ 
                  ...prev, 
                  type: e.target.value as 'character' | 'monster',
                  id: '' 
                }))}
              >
                <option value="character">Character</option>
                <option value="monster">Monster</option>
              </select>
            </div>

            {addCombatantData.type === 'character' ? (
              <div className="form-group">
                <label>Character:</label>
                <select 
                  value={addCombatantData.id} 
                  onChange={(e) => setAddCombatantData(prev => ({ 
                    ...prev, 
                    id: e.target.value 
                  }))}
                >
                  <option value="">Select a character...</option>
                  {characters.map(char => (
                    <option key={char.id} value={char.id}>
                      {char.name} (Level {char.level} {char.class})
                    </option>
                  ))}
                </select>
              </div>
            ) : (
              <>
                <div className="form-group">
                  <label>Monster ID:</label>
                  <input 
                    type="text" 
                    value={addCombatantData.id}
                    onChange={(e) => setAddCombatantData(prev => ({ 
                      ...prev, 
                      id: e.target.value 
                    }))}
                    placeholder="Enter monster ID..."
                  />
                </div>
                <div className="form-group">
                  <label>Instance Name (optional):</label>
                  <input 
                    type="text" 
                    value={addCombatantData.name || ''}
                    onChange={(e) => setAddCombatantData(prev => ({ 
                      ...prev, 
                      name: e.target.value 
                    }))}
                    placeholder="e.g., Goblin 1, Boss Orc..."
                  />
                </div>
              </>
            )}

            <div className="modal-actions">
              <button onClick={handleAddCombatant} disabled={!addCombatantData.id}>
                Add
              </button>
              <button onClick={() => setShowAddDialog(false)}>
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CombatTrackerIntegrated;

/**
 * Dice roller component for making rolls and displaying results
 */

import React, { useState, _useEffect} from 'react';
import './DiceRoller.css';

export interface DiceRoll {
  id: string;
  expression: string;
  result: number;
  breakdown: string;
  timestamp: Date;
  roller: string;
  type: 'attack' | 'damage' | 'save' | 'skill' | 'ability' | 'custom';
  advantage?: boolean | undefined;
  disadvantage?: boolean | undefined;
}

export interface DiceRollerProps {
  onRoll: (roll: DiceRoll) => void;
  recentRolls: DiceRoll[];
  playerName: string;
  readOnly?: boolean;
}

export const DiceRoller: React.FC<DiceRollerProps> = ({_onRoll, _recentRolls, _playerName, _readOnly = false, _}) => {
  const [expression, setExpression] = useState('1d20');
  const [rollType, setRollType] = useState<DiceRoll['type']>('custom');
  const [advantage, setAdvantage] = useState(false);
  const [disadvantage, setDisadvantage] = useState(false);
  const [modifier, setModifier] = useState(0);

  const commonRolls = [
    { label: 'd4', expression: '1d4' },
    { label: 'd6', expression: '1d6' },
    { label: 'd8', expression: '1d8' },
    { label: 'd10', expression: '1d10' },
    { label: 'd12', expression: '1d12' },
    { label: 'd20', expression: '1d20' },
    { label: 'd100', expression: '1d100' },
  ];

  const rollDice = (diceExpression: string): { result: number; breakdown: string } => {
    // Parse dice expression (e.g., "2d6+3", "1d20", "3d8-2")
    const match = diceExpression.match(/(\d+)?d(\d+)([+-]\d+)?/i);
    if (!match) {
      return { result: 0, breakdown: 'Invalid expression' };
    }

    const numDice = parseInt(match[1] || '1');
    const dieSize = parseInt(match[2]!);
    const modifierMatch = match[3];
    const mod = modifierMatch ? parseInt(modifierMatch) : 0;

    const rolls: number[] = [];
    for (let i = 0; i < numDice; i++) {
      rolls.push(Math.floor(Math.random() * dieSize) + 1);
    }

    const sum = rolls.reduce((_acc, _roll) => acc + roll, 0);
    const total = sum + mod;

    let breakdown = `${numDice}d${dieSize}`;
    if (rolls.length <= 10) {
      breakdown += ` (${rolls.join(', ')})`;
    }
    if (mod !== 0) {
      breakdown += ` ${mod >= 0 ? '+' : ''}${mod}`;
    }
    breakdown += ` = ${total}`;

    return { result: total, breakdown };
  };

  const rollWithAdvantage = (baseExpression: string): { result: number; breakdown: string } => {
    if (!advantage && !disadvantage) {
      return rollDice(baseExpression);
    }

    // For advantage/disadvantage, we assume d20 rolls
    if (!baseExpression.includes('d20')) {
      return rollDice(baseExpression);
    }

    const roll1 = Math.floor(Math.random() * 20) + 1;
    const roll2 = Math.floor(Math.random() * 20) + 1;

    const chosenRoll = advantage ? Math.max(roll1, roll2) : Math.min(roll1, roll2);
    const total = chosenRoll + modifier;

    const advantageType = advantage ? 'advantage' : 'disadvantage';
    const breakdown = `1d20 with ${advantageType} (${roll1}, ${roll2}) + ${modifier} = ${total}`;

    return { result: total, breakdown };
  };

  const handleRoll = () => {
    if (readOnly) return;

    let finalExpression = expression;
    let rollResult: { result: number; breakdown: string };

    if ((advantage || disadvantage) && expression.includes('d20')) {
      rollResult = rollWithAdvantage(expression);
    } else {
      // Add modifier to expression if it doesn't already have one
      if (modifier !== 0 && !expression.includes('+') && !expression.includes('-')) {
        finalExpression = `${expression}${modifier >= 0 ? '+' : ''}${modifier}`;
      }
      rollResult = rollDice(finalExpression);
    }

    const roll: DiceRoll = {
      id: `roll_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      expression: finalExpression,
      result: rollResult.result,
      breakdown: rollResult.breakdown,
      timestamp: new Date(),
      roller: playerName,
      type: rollType,
      advantage: advantage ? true : undefined,
      disadvantage: disadvantage ? true : undefined,
    };

    onRoll(roll);
  };

  const handleQuickRoll = (_quickExpression: string) => {
    if (readOnly) return;

    const rollResult = rollDice(quickExpression);
    const roll: DiceRoll = {
      id: `roll_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      expression: quickExpression,
      result: rollResult.result,
      breakdown: rollResult.breakdown,
      timestamp: new Date(),
      roller: playerName,
      type: 'custom',
    };

    onRoll(roll);
  };

  const formatTime = (date: Date) => {
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const getRollTypeColor = (_type: DiceRoll['type']) => {
    const colors = {
      attack: '#dc3545',
      damage: '#fd7e14',
      save: '#28a745',
      skill: '#007bff',
      ability: '#6f42c1',
      custom: '#6c757d',
    };
    return colors[type];
  };

  return (
    <div className="dice-roller">
      <div className="roller-header">
        <h3>Dice Roller</h3>
      </div>

      {!readOnly && (
        <div className="roll-controls">
          <div className="expression-input">
            <label>Dice Expression:</label>
            <input
              type="text"
              value={expression}
              onChange={(e) => setExpression(e.target.value)}
              placeholder="e.g., 1d20+5, 2d6, 3d8-1"
              className="dice-input"
            />
          </div>

          <div className="roll-modifiers">
            <div className="modifier-input">
              <label>Modifier:</label>
              <input
                type="number"
                value={modifier}
                onChange={(e) => setModifier(parseInt(e.target.value) || 0)}
                className="modifier-field"
              />
            </div>

            <div className="advantage-controls">
              <label className={`advantage-btn ${advantage ? 'active' : ''}`}>
                <input
                  type="checkbox"
                  checked={advantage}
                  onChange={(e) => {
                    setAdvantage(e.target.checked);
                    if (e.target.checked) setDisadvantage(false);
                  }}
                />
                Advantage
              </label>
              <label className={`disadvantage-btn ${disadvantage ? 'active' : ''}`}>
                <input
                  type="checkbox"
                  checked={disadvantage}
                  onChange={(e) => {
                    setDisadvantage(e.target.checked);
                    if (e.target.checked) setAdvantage(false);
                  }}
                />
                Disadvantage
              </label>
            </div>
          </div>

          <div className="roll-type">
            <label>Roll Type:</label>
            <select
              value={rollType}
              onChange={(e) => setRollType(e.target.value as DiceRoll['type'])}
              className="type-select"
            >
              <option value="custom">Custom</option>
              <option value="attack">Attack</option>
              <option value="damage">Damage</option>
              <option value="save">Saving Throw</option>
              <option value="skill">Skill Check</option>
              <option value="ability">Ability Check</option>
            </select>
          </div>

          <button className="roll-btn" onClick={handleRoll}>
            Roll Dice
          </button>

          <div className="quick-rolls">
            <label>Quick Rolls:</label>
            <div className="quick-roll-buttons">
              {commonRolls.map((roll) => (
                <button
                  key={roll.expression}
                  className="quick-roll-btn"
                  onClick={() => handleQuickRoll(roll.expression)}
                >
                  {roll.label}
                </button>
              ))}
            </div>
          </div>
        </div>
      )}

      <div className="recent-rolls">
        <h4>Recent Rolls</h4>
        <div className="rolls-list">
          {recentRolls.length === 0 ? (
            <div className="no-rolls">No rolls yet</div>
          ) : (
            recentRolls.slice(0, 10).map((roll) => (
              <div key={roll.id} className="roll-result">
                <div className="roll-header">
                  <span className="roller-name">{roll.roller}</span>
                  <span className="roll-time">{formatTime(roll.timestamp)}</span>
                  <span
                    className="roll-type-badge"
                    style={{ backgroundColor: getRollTypeColor(roll.type) }}
                  >
                    {roll.type}
                  </span>
                </div>

                <div className="roll-details">
                  <div className="roll-expression">{roll.expression}</div>
                  <div className="roll-breakdown">{roll.breakdown}</div>
                  {(roll.advantage || roll.disadvantage) && (
                    <div className="roll-advantage">
                      {roll.advantage ? 'With Advantage' : 'With Disadvantage'}
                    </div>
                  )}
                </div>

                <div className="roll-total">{roll.result}</div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

import React, { useRef, useEffect, useState, useCallback } from 'react';
import { useGame } from '../hooks/useGame';
import PerformanceMonitor from './PerformanceMonitor';
import { WebGLRenderer, RenderObject, Camera } from '@vtt/renderer';
import { useWebSocket } from '../hooks/useWebSocket';

export interface GameCanvasProps {
  width: number;
  height: number;
  gameId: string;
  isGM: boolean;
}

export interface Token {
  id: string;
  x: number;
  y: number;
  width: number;
  height: number;
  textureId: string;
  name: string;
  color: [number, number, number, number];
  rotation: number;
  scale: [number, number];
  visible: boolean;
  layer: number;
  selected?: boolean;
  actorId?: string;
}

export interface MapData {
  id: string;
  imageUrl: string;
  width: number;
  height: number;
  gridSize: number;
}

export const GameCanvas: React.FC<GameCanvasProps> = ({_width, _height, _gameId, _isGM}) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const rendererRef = useRef<WebGLRenderer | null>(null);
  const animationFrameRef = useRef<number>(0);

  const [tokens, setTokens] = useState<Token[]>([]);
  const [selectedTokens, setSelectedTokens] = useState<Set<string>>(new Set());
  const [isLoading, setIsLoading] = useState(true);
  const [showPerformanceMonitor, setShowPerformanceMonitor] = useState(false);
  const [dragState, setDragState] = useState<{
    isDragging: boolean;
    startPos: { x: number; y: number };
    tokenId?: string;
  }>({ isDragging: false, startPos: { x: 0, y: 0 } });

  const [camera, setCamera] = useState<Camera>({
    position: [0, 0],
    zoom: 1,
    rotation: 0,
    viewport: [0, 0, width, height]
  });

  const [mapData, setMapData] = useState<MapData | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  const { socket  } = useWebSocket();
  const { currentGame  } = useGame();

  // Initialize WebGL renderer
  useEffect(() => {
    if (!canvasRef.current) return;

    try {
      const renderer = new WebGLRenderer(canvasRef.current);
      rendererRef.current = renderer;

      // Set initial camera
      renderer.setCamera(camera);
      renderer.resize(width, height);

      console.log('WebGL renderer initialized');
      setIsLoading(false);
    } catch (error) {
      console.error('Failed to initialize WebGL renderer:', error);
      setIsLoading(false);
    }

    return () => {
      if (rendererRef.current) {
        rendererRef.current.dispose();
      }
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [width, height]);

  // Handle canvas resize
  useEffect(() => {
    if (rendererRef.current) {
      rendererRef.current.resize(width, height);
      setCamera(prev => ({ ...prev, viewport: [0, 0, width, height] }));
    }
  }, [width, height]);

  // Load map and initial game state
  useEffect(() => {
    const loadGameState = async () => {
      if (!gameId || !rendererRef.current) return;

      try {
        // Load game data from server
        const response = await fetch(`/api/games/${gameId}`);
        const gameData = await response.json();

        // Load map if available
        if (gameData.mapId) {
          const mapResponse = await fetch(`/api/maps/${gameData.mapId}`);
          const mapData = await mapResponse.json();
          setMapData(mapData);

          // Load map texture
          if (mapData.imageUrl) {
            const img = new Image();
            img.onload = () => {
              rendererRef.current?.loadTexture('map', img);
            };
            img.src = mapData.imageUrl;
          }
        }

        // Preload common textures
        const commonTextures = [
          '/assets/textures/grid.png',
          '/assets/textures/token-placeholder.png'
        ];

        for (const texture of commonTextures) {
          if (!rendererRef.current?.hasTexture(texture)) {
            const img = new Image();
            img.onload = () => {
              rendererRef.current?.loadTexture(texture, img);
            };
            img.src = texture;
          }
        }

        // Load tokens
        if (gameData.tokens) {
          setTokens(gameData.tokens);

          // Load token textures
          gameData.tokens.forEach((token: Token) => {
            if (token.textureId && !rendererRef.current?.hasTexture(token.textureId)) {
              const img = new Image();
              img.onload = () => {
                rendererRef.current?.loadTexture(token.textureId, img);
              };
              img.src = `/api/assets/${token.textureId}/file`;
            }
          });
        }
      } catch (error) {
        console.error('Failed to load game state:', error);
      }
    };

    loadGameState();
  }, [gameId]);

  // WebSocket event handlers
  useEffect(() => {
    if (!socket) return;

    const handleTokenMove = (data: { tokenId: string; x: number; y: number }) => {
      setTokens(prev => prev.map(token => 
        token.id === data.tokenId 
          ? { ...token, x: data.x, y: data.y }
          : token
      ));
    };

    const handleTokenAdd = (token: Token) => {
      setTokens(prev => [...prev, token]);

      // Load token texture if needed
      if (token.textureId && rendererRef.current) {
        const img = new Image();
        img.onload = () => {
          rendererRef.current?.loadTexture(token.textureId, img);
        };
        img.src = `/api/assets/${token.textureId}/file`;
      }
    };

    const handleTokenRemove = (data: { tokenId: string }) => {
      setTokens(prev => prev.filter(token => token.id !== data.tokenId));
      setSelectedTokens(prev => {
        const newSet = new Set(prev);
        newSet.delete(data.tokenId);
        return newSet;
      });
    };

    const handleCameraUpdate = (_cameraData: Partial<Camera>) => {
      setCamera(prev => ({ ...prev, ...cameraData }));
    };

    socket.on('token:move', handleTokenMove);
    socket.on('token:add', handleTokenAdd);
    socket.on('token:remove', handleTokenRemove);
    socket.on('camera:update', handleCameraUpdate);

    return () => {
      socket.off('token:move', handleTokenMove);
      socket.off('token:add', handleTokenAdd);
      socket.off('token:remove', handleTokenRemove);
      socket.off('camera:update', handleCameraUpdate);
    };
  }, [socket]);

  // Render loop
  const render = useCallback(() => {
    if (!rendererRef.current) return;

    const renderer = rendererRef.current;
    renderer.setCamera(camera);

    // Clear render queue
    renderer.clearRenderQueue();

    // Add map to render queue
    if (mapData) {
      const mapObject: RenderObject = {
        id: 'map',
        position: [0, 0, -1],
        rotation: 0,
        scale: [mapData.width, mapData.height],
        textureId: 'map',
        color: [1, 1, 1, 1],
        visible: true,
        layer: 0
      };
      renderer.addRenderObject(mapObject);
    }

    // Add tokens to render queue with viewport culling optimization
    const viewportPadding = 100; // Extra padding for smooth edge transitions
    const viewLeft = camera.position[0] - (width / 2) / camera.zoom - viewportPadding;
    const viewRight = camera.position[0] + (width / 2) / camera.zoom + viewportPadding;
    const viewTop = camera.position[1] - (height / 2) / camera.zoom - viewportPadding;
    const viewBottom = camera.position[1] + (height / 2) / camera.zoom + viewportPadding;

    const _culledCount = 0;
    const _renderedCount = 0;

    tokens.forEach(token => {
      if (!token.visible) return;

      // Viewport culling - only render tokens within camera view
      const tokenLeft = token.x - token.width / 2;
      const tokenRight = token.x + token.width / 2;
      const tokenTop = token.y - token.height / 2;
      const tokenBottom = token.y + token.height / 2;

      // Check if token is outside viewport bounds
      if (tokenRight < viewLeft || tokenLeft > viewRight || 
          tokenBottom < viewTop || tokenTop > viewBottom) {
        culledCount++;
        return; // Skip rendering this token
      }

      renderedCount++;
      const isSelected = selectedTokens.has(token.id);
      const color: [number, number, number, number] = isSelected 
        ? [1, 1, 0.5, 1] // Yellow tint for selected
        : token.color;

      const renderObject: RenderObject = {
        id: token.id,
        position: [token.x, token.y, token.layer],
        rotation: token.rotation,
        scale: token.scale,
        textureId: token.textureId,
        color,
        visible: token.visible,
        layer: token.layer + 1 // Tokens above map
      };

      renderer.addRenderObject(renderObject);
    });

    // Render frame
    renderer.render();

    // Schedule next frame
    animationFrameRef.current = requestAnimationFrame(render);
  }, [tokens, selectedTokens, camera, mapData]);

  // Start render loop
  useEffect(() => {
    if (!isLoading && rendererRef.current) {
      render();
    }

    return () => {
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [isLoading, render]);

  // Mouse event handlers
  const handleMouseDown = useCallback((event: React.MouseEvent<HTMLCanvasElement>) => {
    if (!canvasRef.current || !rendererRef.current) return;

    const rect = canvasRef.current.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;

    // Convert screen to world coordinates
    const worldX = (x - width / 2) / camera.zoom + camera.position[0];
    const worldY = (y - height / 2) / camera.zoom + camera.position[1];

    // Find token at position
    const clickedToken = tokens.find(token => 
      worldX >= token.x - token.width / 2 &&
      worldX <= token.x + token.width / 2 &&
      worldY >= token.y - token.height / 2 &&
      worldY <= token.y + token.height / 2
    );

    if (clickedToken) {
      // Select token
      if (event.ctrlKey || event.metaKey) {
        // Multi-select
        setSelectedTokens(prev => {
          const newSet = new Set(prev);
          if (newSet.has(clickedToken.id)) {
            newSet.delete(clickedToken.id);
          } else {
            newSet.add(clickedToken.id);
          }
          return newSet;
        });
      } else {
        // Single select
        setSelectedTokens(new Set([clickedToken.id]));
      }

      // Start drag if GM or owner
      if (isGM || clickedToken.actorId === currentGame?.currentUserId) {
        setDragState({
          isDragging: true,
          startPos: { x: worldX, y: worldY },
          tokenId: clickedToken.id
        });
      }
    } else {
      // Clear selection
      setSelectedTokens(new Set());
    }
  }, [tokens, camera, width, height, isGM, currentGame]);

  const handleMouseMove = useCallback((event: React.MouseEvent<HTMLCanvasElement>) => {
    if (!dragState.isDragging || !dragState.tokenId || !canvasRef.current) return;

    const rect = canvasRef.current.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;

    // Convert screen to world coordinates
    const worldX = (x - width / 2) / camera.zoom + camera.position[0];
    const worldY = (y - height / 2) / camera.zoom + camera.position[1];

    // Update token position locally
    setTokens(prev => prev.map(token => 
      token.id === dragState.tokenId
        ? { ...token, x: worldX, y: worldY }
        : token
    ));
  }, [dragState, camera, width, height]);

  const handleMouseUp = useCallback(() => {
    if (dragState.isDragging && dragState.tokenId && socket) {
      // Send final position to server
      const token = tokens.find(t => t.id === dragState.tokenId);
      if (token) {
        socket.emit('token:move', {
          gameId,
          tokenId: token.id,
          x: token.x,
          y: token.y
        });
      }
    }

    setDragState({ isDragging: false, startPos: { x: 0, y: 0 } });
  }, [dragState, tokens, socket, gameId]);

  // Camera controls
  const handleWheel = useCallback((event: React.WheelEvent<HTMLCanvasElement>) => {
    event.preventDefault();

    const zoomSpeed = 0.1;
    const newZoom = Math.max(0.1, Math.min(5, camera.zoom - event.deltaY * zoomSpeed * 0.01));

    setCamera(prev => ({ ...prev, zoom: newZoom }));

    // Broadcast camera update if GM
    if (isGM && socket) {
      socket.emit('camera:update', { gameId, zoom: newZoom });
    }
  }, [camera.zoom, isGM, socket, gameId]);

  const handleDoubleClick = useCallback((__event: React.MouseEvent<HTMLCanvasElement>) => {
    // Reset camera to center
    const newCamera = {
      position: [0, 0] as [number, number],
      zoom: 1,
      rotation: 0,
      viewport: [0, 0, width, height] as [number, number, number, number]
    };

    setCamera(newCamera);

    if (isGM && socket) {
      socket.emit('camera:update', { gameId, ...newCamera });
    }
  }, [width, height, isGM, socket, gameId]);

  if (isLoading) {
    return (
      <div className="flex items-center justify-center" style={{ width, height }}>
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500 mx-auto mb-2"></div>
          <p className="text-sm text-gray-600">Initializing game canvas...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="relative" style={{ width, height }}>
      <canvas
        ref={canvasRef}
        width={width}
        height={height}
        className="border border-gray-300 cursor-crosshair"
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onWheel={handleWheel}
        onDoubleClick={handleDoubleClick}
      />

      {/* Enhanced Debug info with performance monitoring */}
      {typeof window !== 'undefined' && (window as any).__DEV__ && (
        <div className="absolute top-2 left-2 bg-black bg-opacity-75 text-white text-xs p-2 rounded space-y-1">
          <div className="font-semibold text-green-400">Performance Monitor</div>
          <div>FPS: <span className={`${(rendererRef.current?.getStats()?.fps || 0) > 55 ? 'text-green-400' : (rendererRef.current?.getStats()?.fps || 0) > 30 ? 'text-yellow-400' : 'text-red-400'}`}>{rendererRef.current?.getStats()?.fps || 0}</span></div>
          <div>Frame Time: {rendererRef.current?.getStats()?.frameTime?.toFixed(2) || '0.00'}ms</div>
          <div>Draw Calls: {rendererRef.current?.getStats()?.drawCalls || 0}</div>
          <div>Triangles: {rendererRef.current?.getStats()?.triangles || 0}</div>
          <div className="border-t border-gray-600 pt-1 mt-1">
            <div className="font-semibold text-blue-400">Scene Info</div>
            <div>Tokens: <span className="text-cyan-400">{tokens.length}</span> (visible: {tokens.filter(t => t.visible).length})</div>
            <div>Selected: <span className="text-yellow-400">{selectedTokens.size}</span></div>
            <div>Camera: ({camera.position[0].toFixed(1)}, {camera.position[1].toFixed(1)}) @ {camera.zoom.toFixed(2)}x</div>
            <div>Viewport: {width}x{height}</div>
          </div>
          <div className="border-t border-gray-600 pt-1">
            <div className="font-semibold text-purple-400">Rendering</div>
            <div>Viewport Culled: <span className="text-orange-400">{tokens.length - tokens.filter(t => t.visible).length}</span> tokens</div>
            <div>Rendered: <span className="text-green-400">{tokens.filter(t => t.visible).length}</span> tokens</div>
            <div>WebGL State: <span className="text-green-400">Active</span></div>
          </div>
        </div>
      )}

      {/* Performance Monitor Component */}
      <PerformanceMonitor 
        isVisible={showPerformanceMonitor}
        onToggle={() => setShowPerformanceMonitor(!showPerformanceMonitor)}
      />

      {/* Token count indicator */}
      <div className="absolute bottom-2 right-2 bg-gray-800 text-white text-sm px-2 py-1 rounded">
        {tokens.filter(t => t.visible).length} tokens
      </div>
    </div>
  );
};

