/**
 * Game lobby component for joining and managing games
 */

import React, { useState, useEffect } from 'react';
import './GameLobby.css';

export interface GameSession {
  id: string;
  name: string;
  description: string;
  gameSystem: string;
  gameMaster: {
    id: string;
    username: string;
    displayName: string;
  };
  players: Array<{
    id: string;
    username: string;
    displayName: string;
    character?: {
      name: string;
      level: number;
      class: string;
    };
  }>;
  maxPlayers: number;
  isPrivate: boolean;
  status: 'waiting' | 'active' | 'paused' | 'ended';
  createdAt: Date;
  lastActivity: Date;
}

export interface GameLobbyProps {
  onJoinGame: (_gameId: string) => void;
  onCreateGame: () => void;
  onSpectateGame: (_gameId: string) => void;
  currentUserId?: string;
}

export const GameLobby: React.FC<GameLobbyProps> = ({
  onJoinGame, _onCreateGame,
  _onSpectateGame, _currentUserId,
_}) => {
  const [games, setGames] = useState<GameSession[]>([]);
  const [filter, setFilter] = useState<'all' | 'open' | 'active' | 'my-games'>('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadGames();
  }, [filter]);

  const _loadGames = async () => {
    setLoading(true);
    try {
      // Mock data - replace with actual API call
      const mockGames: GameSession[] = [
        {
          id: '1',
          name: 'The Lost Mines of Phandelver',
          description: 'A classic D&D 5e adventure for new players',
          gameSystem: 'D&D 5e',
          gameMaster: {
            id: 'gm1',
            username: 'dungeonmaster',
            displayName: 'The DM',
          },
          players: [
            {
              id: 'p1',
              username: 'fighter123',
              displayName: 'Bob',
              character: { name: 'Thorin', level: 3, class: 'Fighter' },
            },
            {
              id: 'p2',
              username: 'wizard456',
              displayName: 'Alice',
              character: { name: 'Elara', level: 3, class: 'Wizard' },
            },
          ],
          maxPlayers: 5,
          isPrivate: false,
          status: 'waiting',
          createdAt: new Date('2024-01-15'),
          lastActivity: new Date(),
        },
        {
          id: '2',
          name: 'Curse of Strahd',
          description: 'Gothic horror campaign in Barovia',
          gameSystem: 'D&D 5e',
          gameMaster: {
            id: 'gm2',
            username: 'horrormaster',
            displayName: 'Count GM',
          },
          players: [
            {
              id: 'p3',
              username: 'paladin789',
              displayName: 'Charlie',
              character: { name: 'Sir Gareth', level: 8, class: 'Paladin' },
            },
          ],
          maxPlayers: 4,
          isPrivate: false,
          status: 'active',
          createdAt: new Date('2024-01-10'),
          lastActivity: new Date(),
        },
      ];

      setGames(mockGames);
    } catch (error) {
      console.error('Failed to load games:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredGames = games.filter(game => {
    // Apply filter
    switch (filter) {
      case 'open':
        return game.status === 'waiting' && game.players.length < game.maxPlayers;
      case 'active':
        return game.status === 'active';
      case 'my-games':
        return game.gameMaster.id === currentUserId || 
               game.players.some(p => p.id === currentUserId);
      default:
        return true;
    }
  }).filter(game => {
    // Apply search
    if (!searchTerm) return true;
    const search = searchTerm.toLowerCase();
    return game.name.toLowerCase().includes(search) ||
           game.description.toLowerCase().includes(search) ||
           game.gameSystem.toLowerCase().includes(search) ||
           game.gameMaster.displayName.toLowerCase().includes(search);
  });

  const getStatusBadge = (_status: GameSession['status']) => {
    const statusClasses = {
      waiting: 'status-waiting',
      active: 'status-active',
      paused: 'status-paused',
      ended: 'status-ended',
    };

    const statusLabels = {
      waiting: 'Waiting for Players',
      active: 'Active',
      paused: 'Paused',
      ended: 'Ended',
    };

    return (
      <span className={`status-badge ${statusClasses[status]}`}>
        {statusLabels[status]}
      </span>
    );
  };

  const canJoinGame = (game: GameSession) => {
    return game.status === 'waiting' && 
           game.players.length < game.maxPlayers &&
           !game.players.some(p => p.id === currentUserId) &&
           game.gameMaster.id !== currentUserId;
  };

  const canSpectateGame = (game: GameSession) => {
    return game.status === 'active' && 
           !game.isPrivate &&
           !game.players.some(p => p.id === currentUserId) &&
           game.gameMaster.id !== currentUserId;
  };

  if (loading) {
    return (
      <div className="game-lobby loading">
        <div className="loading-spinner">Loading games...</div>
      </div>
    );
  }

  return (
    <div className="game-lobby">
      <div className="lobby-header">
        <h1>Game Lobby</h1>
        <button className="create-game-btn" onClick={onCreateGame}>
          Create New Game
        </button>
      </div>

      <div className="lobby-filters">
        <div className="filter-tabs">
          {[
            { key: 'all', _label: 'All Games' },
            { key: 'open', _label: 'Open Games' },
            { key: 'active', _label: 'Active Games' },
            { key: 'my-games', _label: 'My Games' },
          _].map(tab => (
            <button
              key={tab.key}
              className={`filter-tab ${filter === tab.key ? 'active' : ''}`}
              onClick={() => setFilter(tab.key as typeof filter)}
            >
              {tab.label}
            </button>
          ))}
        </div>

        <div className="search-bar">
          <input
            type="text"
            placeholder="Search games..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
        </div>
      </div>

      <div className="games-list">
        {filteredGames.length === 0 ? (
          <div className="no-games">
            <p>No games found matching your criteria.</p>
            {filter === 'all' && (
              <button onClick={onCreateGame} className="create-first-game">
                Create the first game!
              </button>
            )}
          </div>
        ) : (
          filteredGames.map(game => (
            <div key={game.id} className="game-card">
              <div className="game-header">
                <div className="game-title">
                  <h3>{game.name}</h3>
                  {getStatusBadge(game.status)}
                </div>
                <div className="game-system">{game.gameSystem}</div>
              </div>

              <div className="game-description">
                {game.description}
              </div>

              <div className="game-info">
                <div className="game-master">
                  <strong>GM:</strong> {game.gameMaster.displayName}
                </div>
                <div className="player-count">
                  <strong>Players:</strong> {game.players.length}/{game.maxPlayers}
                </div>
                <div className="last-activity">
                  <strong>Last Activity:</strong> {game.lastActivity.toLocaleDateString()}
                </div>
              </div>

              <div className="players-list">
                <h4>Players:</h4>
                {game.players.length === 0 ? (
                  <p className="no-players">No players yet</p>
                ) : (
                  <div className="players">
                    {game.players.map(player => (
                      <div key={player.id} className="player">
                        <span className="player-name">{player.displayName}</span>
                        {player.character && (
                          <span className="character-info">
                            ({player.character.name}, Level {player.character.level} {player.character.class})
                          </span>
                        )}
                      </div>
                    ))}
                  </div>
                )}
              </div>

              <div className="game-actions">
                {canJoinGame(game) && (
                  <button
                    className="join-btn"
                    onClick={() => onJoinGame(game.id)}
                  >
                    Join Game
                  </button>
                )}
                {canSpectateGame(game) && (
                  <button
                    className="spectate-btn"
                    onClick={() => onSpectateGame(game.id)}
                  >
                    Spectate
                  </button>
                )}
                {(game.gameMaster.id === currentUserId || game.players.some(p => p.id === currentUserId)) && (
                  <button
                    className="enter-btn"
                    onClick={() => onJoinGame(game.id)}
                  >
                    Enter Game
                  </button>
                )}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

/**
 * Interactive Map Editor with drawing tools for VTT
 */

import React, { useState, useRef, useEffect } from 'react';
import { Canvas2DRenderer, GridConfig, MapLayer, Token } from '@vtt/renderer';
import './MapEditor.css';

export interface DrawingTool {
  type: 'select' | 'pen' | 'brush' | 'line' | 'rectangle' | 'circle' | 'text' | 'token' | 'wall' | 'door';
  size: number;
  color: string;
  opacity: number;
}

export interface DrawingPath {
  id: string;
  tool: DrawingTool;
  points: Array<{ x: number; y: number }>;
  timestamp: number;
}

interface MapEditorProps {
  onMapChange?: (layers: MapLayer[], _paths: DrawingPath[]) => void;
  onTokenChange?: (tokens: Token[]) => void;
  readOnly?: boolean;
  initialLayers?: MapLayer[];
  initialTokens?: Token[];
}

export const MapEditor: React.FC<MapEditorProps> = ({_onMapChange, _onTokenChange, _readOnly = false, _initialLayers = [], _initialTokens = []}) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const rendererRef = useRef<Canvas2DRenderer | null>(null);
  const [currentTool, setCurrentTool] = useState<DrawingTool>({
    type: 'select',
    size: 5,
    color: '#000000',
    opacity: 1
  });

  const [layers, setLayers] = useState<MapLayer[]>(initialLayers);
  const [tokens, setTokens] = useState<Token[]>([]);
  const [mapId] = useState('map-1');
  const [activeTool, setActiveTool] = useState<'select' | 'brush' | 'eraser' | 'token'>('select');
  const [brushSize, setBrushSize] = useState(10);
  const [_selectedLayer, setSelectedLayer] = useState<string | null>(null);
  const [aiPrompt, setAiPrompt] = useState('');
  const [isGenerating, setIsGenerating] = useState(false);
  const [generatedImages, setGeneratedImages] = useState<Array<{uri: string; width: number; height: number}>>([]);
  const [selectedTokens, setSelectedTokens] = useState<Set<string>>(new Set());
  const [showGrid, setShowGrid] = useState(true);
  const [gridConfig, setGridConfig] = useState<GridConfig>({
    size: 70,
    cellSize: 70,
    color: '#333333',
    opacity: 0.3,
    enabled: true,
    type: 'square',
    offsetX: 0,
    offsetY: 0,
    lineWidth: 1,
  });
  const [drawingPaths, setDrawingPaths] = useState<DrawingPath[]>([]);
  const isDrawingRef = useRef(false);

  // Initialize renderer
  useEffect(() => {
    if (canvasRef.current && !rendererRef.current) {
      rendererRef.current = new Canvas2DRenderer(canvasRef.current);

      // Set initial grid config
      rendererRef.current.setGridConfig(gridConfig);

      // Add initial layers
      initialLayers.forEach(layer => {
        rendererRef.current?.addLayer(layer);
      });

      // Add initial tokens
      initialTokens.forEach(token => {
        rendererRef.current?.addToken(token);
      });
    }

    return () => {
      if (rendererRef.current) {
        rendererRef.current.destroy();
        rendererRef.current = null;
      }
    };
  }, []);

  // Sync grid visibility to renderer
  useEffect(() => {
    if (!rendererRef.current) return;
    rendererRef.current.setGridConfig({ enabled: showGrid });
  }, [showGrid]);

  // Notify external listeners
  useEffect(() => {
    if (onMapChange) onMapChange(layers, drawingPaths);
  }, [layers, drawingPaths, onMapChange]);

  useEffect(() => {
    if (onTokenChange) onTokenChange(tokens);
  }, [tokens, onTokenChange]);

  // Handlers and helpers
  const screenToLocal = (e: React.MouseEvent<HTMLCanvasElement>) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    // Current renderer maps 1:1
    const worldX = rendererRef.current?.screenToWorldX(x) ?? x;
    const worldY = rendererRef.current?.screenToWorldY(y) ?? y;
    return { x: worldX, y: worldY };
  };

  const updateGridConfig = (_updates: Partial<GridConfig>) => {
    setGridConfig(prev => {
      const next = { ...prev, ...updates };
      rendererRef.current?.setGridConfig(updates);
      return next;
    });
  };

  const undoLastPath = () => {
    setDrawingPaths(prev => prev.slice(0, -1));
  };

  const clearDrawing = () => {
    setDrawingPaths([]);
  };

  const deleteSelectedTokens = () => {
    setTokens(prev => {
      const next = prev.filter(t => !selectedTokens.has(t.id));
      selectedTokens.forEach(id => rendererRef.current?.removeToken(id));
      setSelectedTokens(new Set());
      return next;
    });
  };

  const _addLayer = () => {
    const newLayer: MapLayer = {
      id: `layer-${Date.now()}`,
      name: 'New Layer',
      visible: true,
      opacity: 1,
      zIndex: layers.length,
      tiles: [],
    } as MapLayer;
    setLayers(prev => [...prev, newLayer]);
    rendererRef.current?.addLayer(newLayer);
  };

  const _toggleLayerVisibility = (_layerId: string) => {
    setLayers(prev => prev.map(l => l.id === layerId ? { ...l, visible: !l.visible } : l));
    rendererRef.current?.updateLayer(layerId, {});
  };

  const _updateTokenSelection = (selection: Set<string>) => {
    // Update renderer token selection state
    tokens.forEach(t => {
      const selected = selection.has(t.id);
      rendererRef.current?.updateToken(t.id, { selected });
    });
  };

  const handleGenerateScene = async () => {
    if (!aiPrompt.trim()) return;

    setIsGenerating(true);
    try {
      const response = await fetch('/api/ai/textToImage', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          prompt: aiPrompt,
          width: 1024,
          height: 1024,
          mapId: mapId
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to generate image');
      }

      const result = await response.json();
      const newImage = {
        uri: result.job.output.image.uri,
        width: result.job.output.image.width,
        height: result.job.output.image.height
      };

      setGeneratedImages(prev => [...prev, newImage]);
    } catch (error) {
      console.error('Error generating scene:', error);
      alert('Failed to generate scene. Please try again.');
    } finally {
      setIsGenerating(false);
    }
  };

  const handleUseGeneratedImage = (image: {uri: string; width: number; height: number}) => {
    const newLayer: MapLayer = {
      id: `generated-layer-${Date.now()}`,
      name: 'AI Generated Background',
      visible: true,
      opacity: 1,
      zIndex: 0,
      imageUrl: image.uri,
      tiles: [],
    } as MapLayer;
    setLayers(prev => [...prev, newLayer]);
    rendererRef.current?.addLayer(newLayer);
    setSelectedLayer(newLayer.id);
    // Clear generated images after use
    setGeneratedImages([]);
    setAiPrompt('');
  };

  const handleMouseMove = (e: React.MouseEvent<HTMLCanvasElement>) => {
    if (!canvasRef.current || !rendererRef.current) return;
    const { x,  y  } = screenToLocal(e);
    if (isDrawingRef.current && (activeTool === 'brush' || activeTool === 'eraser')) {
      setDrawingPaths(prev => {
        if (prev.length === 0) return prev;
        const last = prev[prev.length - 1]!;
        const updated = { ...last, points: [...last.points, { x, y }] };
        return [...prev.slice(0, -1), updated];
      });
    }
  };

  const handleMouseDown = (e: React.MouseEvent<HTMLCanvasElement>) => {
    if (readOnly) return;
    const { x,  y  } = screenToLocal(e);
    if (activeTool === 'brush' || activeTool === 'eraser') {
      isDrawingRef.current = true;
      const path: DrawingPath = {
        id: `path-${Date.now()}`,
        tool: currentTool,
        points: [{ x, y }],
        timestamp: Date.now(),
      };
      setDrawingPaths(prev => [...prev, path]);
    }
  };

  const handleMouseUp = (e: React.MouseEvent<HTMLCanvasElement>) => {
    if (readOnly) return;
    const { x,  y  } = screenToLocal(e);
    if (activeTool === 'token') {
      const gridPos = rendererRef.current?.worldToGrid(x, y) ?? { x: 0, y: 0 };
      const sizePx = gridConfig.cellSize || gridConfig.size;
      const newToken: Token = {
        id: `token-${Date.now()}`,
        x: gridPos.x * sizePx,
        y: gridPos.y * sizePx,
        width: sizePx,
        height: sizePx,
        imageUrl: '',
        name: 'Token',
        visible: true,
        size: 1,
        selected: false,
        sprite: 'default-token',
        rotation: 0,
        tint: '#ffffff',
        conditions: [],
      };
      setTokens(prev => [...prev, newToken]);
      rendererRef.current?.addToken(newToken);
    }
    isDrawingRef.current = false;
  };

  const _tools = [
    { type: 'select', icon: '⭐', label: 'Select' },
    { type: 'pen', icon: '✏️', label: 'Pen' },
    { type: 'brush', icon: '🖌️', label: 'Brush' },
    { type: 'line', icon: '📏', label: 'Line' },
    { type: 'rectangle', icon: '⬜', label: 'Rectangle' },
    { type: 'circle', icon: '⭕', label: 'Circle' },
    { type: 'text', icon: '📝', label: 'Text' },
    { type: 'token', icon: '🎭', label: 'Token' },
    { type: 'wall', icon: '🧱', label: 'Wall' },
    { type: 'door', icon: '🚪', label: 'Door' },
  ] as const;

  return (
    <div className="map-editor">
      <div className="editor-sidebar">
        <div className="tool-section">
          <h3>Tools</h3>
          <div className="tool-grid">
            <button 
              className={`tool-btn ${activeTool === 'select' ? 'active' : ''}`}
              onClick={() => setActiveTool('select')}
            >
              Select
            </button>
            <button 
              className={`tool-btn ${activeTool === 'brush' ? 'active' : ''}`}
              onClick={() => setActiveTool('brush')}
            >
              Brush
            </button>
            <button 
              className={`tool-btn ${activeTool === 'eraser' ? 'active' : ''}`}
              onClick={() => setActiveTool('eraser')}
            >
              Eraser
            </button>
            <button 
              className={`tool-btn ${activeTool === 'token' ? 'active' : ''}`}
              onClick={() => setActiveTool('token')}
            >
              Token
            </button>
          </div>
        </div>

        <div className="ai-generation-section">
          <h3>AI Generation</h3>
          <div className="ai-controls">
            <input
              type="text"
              placeholder="Describe the scene to generate..."
              value={aiPrompt}
              onChange={(e) => setAiPrompt(e.target.value)}
              className="ai-prompt-input"
            />
            <button
              onClick={handleGenerateScene}
              disabled={isGenerating || !aiPrompt.trim()}
              className="ai-generate-btn"
            >
              {isGenerating ? 'Generating...' : 'Generate Scene'}
            </button>
            {generatedImages.length > 0 && (
              <div className="generated-images">
                <h4>Generated Images</h4>
                <div className="image-grid">
                  {generatedImages.map((img, _index) => (
                    <div key={index} className="generated-image">
                      <img src={img.uri} alt={`Generated ${index + 1}`} />
                      <button
                        onClick={() => handleUseGeneratedImage(img)}
                        className="use-image-btn"
                      >
                        Use as Background
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>

      </div>

      <div className="editor-main">
        <div className="canvas-container">
          <canvas
            ref={canvasRef}
            className="map-canvas"
            onMouseDown={handleMouseDown}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
          />
        </div>

        <div className="property-group">
          <label>Brush Size:</label>
          <input
            type="range"
            min="1"
            max="50"
            value={brushSize}
            onChange={(e) => setBrushSize(parseInt(e.target.value))}
            disabled={readOnly}
          />
          <span>{brushSize}px</span>
        </div>

        <div className="property-group">
          <label>Color:</label>
          <input
            type="color"
            value={currentTool.color}
            onChange={(e) => setCurrentTool(prev => ({ ...prev, color: e.target.value }))}
            disabled={readOnly}
          />
        </div>

        <div className="property-group">
          <label>Opacity:</label>
          <input
            type="range"
            min="0"
            max="1"
            step="0.1"
            value={currentTool.opacity}
            onChange={(e) => setCurrentTool(prev => ({ ...prev, opacity: parseFloat(e.target.value) }))}
            disabled={readOnly}
          />
          <span>{Math.round(currentTool.opacity * 100)}%</span>
        </div>

        <div className="grid-controls">
          <h4>Grid</h4>
          <div className="property-group">
            <label>
              <input
                type="checkbox"
                checked={showGrid}
                onChange={(e) => setShowGrid(e.target.checked)}
              />
              Show Grid
            </label>
          </div>
          <div className="property-group">
            <label>Size:</label>
            <input
              type="range"
              min="20"
              max="200"
              value={gridConfig.cellSize}
              onChange={(e) => updateGridConfig({ cellSize: parseInt(e.target.value), size: parseInt(e.target.value) })}
              disabled={readOnly}
            />
            <span>{gridConfig.cellSize}px</span>
          </div>
          <div className="property-group">
            <label>Type:</label>
            <select
              value={gridConfig.type}
              onChange={(e) => updateGridConfig({ type: e.target.value as 'square' | 'hex' })}
              disabled={readOnly}
            >
              <option value="square">Square</option>
              <option value="hex">Hexagon</option>
            </select>
          </div>
        </div>

        <div className="action-buttons">
          <button onClick={undoLastPath} disabled={readOnly || drawingPaths.length === 0}>
            Undo
          </button>
          <button onClick={clearDrawing} disabled={readOnly || drawingPaths.length === 0}>
            Clear
          </button>
          <button onClick={deleteSelectedTokens} disabled={readOnly || selectedTokens.size === 0}>
            Delete Selected
          </button>
        </div>
      </div>
    </div>
  );
};

/**
 * Enhanced Monster Browser - Integrated with real monster data and combat system
 */

import React, { useState, useEffect, useCallback } from 'react';
import { _CombatActor } from '../hooks/useEncounter';

interface Monster {
  id: string;
  name: string;
  source: string;
  statblock: {
    size: string;
    type: string;
    armorClass: number;
    hitPoints: number;
    speed: Record<string, number>;
    abilities: {
      STR: number;
      DEX: number;
      CON: number;
      INT: number;
      WIS: number;
      CHA: number;
    };
    challengeRating: string;
    actions: Array<{
      name: string;
      description: string;
      attackBonus?: number;
      damage?: any;
    }>;
    skills?: Record<string, number>;
    senses?: Record<string, number>;
    languages?: string[];
    conditionImmunities?: string[];
    damageResistances?: string[];
    damageImmunities?: string[];
  };
  tags: string[];
}

interface MonsterBrowserProps {
  onSelectMonster?: (monster: Monster) => void;
  onAddToEncounter?: (_monsterId: string, _instanceName?: string) => void;
  encounterId?: string;
  multiSelect?: boolean;
  showActions?: boolean;
}

export const MonsterBrowser: React.FC<MonsterBrowserProps> = ({_onSelectMonster, _onAddToEncounter, _encounterId, _multiSelect = false, _showActions = true}) => {
  const [monsters, setMonsters] = useState<Monster[]>([]);
  const [filteredMonsters, setFilteredMonsters] = useState<Monster[]>([]);
  const [selectedMonsters, setSelectedMonsters] = useState<Set<string>>(new Set());
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Filters
  const [searchTerm, setSearchTerm] = useState('');
  const [crFilter, setCrFilter] = useState('');
  const [typeFilter, setTypeFilter] = useState('');
  const [sizeFilter, setSizeFilter] = useState('');
  const [sourceFilter, setSourceFilter] = useState('');

  // Pagination
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(20);

  // Load monsters
  useEffect(() => {
    loadMonsters();
  }, []);

  // Apply filters
  useEffect(() => {
    let filtered = monsters;

    if (searchTerm) {
      filtered = filtered.filter(monster =>
        monster.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        monster.statblock.type.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (crFilter) {
      filtered = filtered.filter(monster => monster.statblock.challengeRating === crFilter);
    }

    if (typeFilter) {
      filtered = filtered.filter(monster => monster.statblock.type === typeFilter);
    }

    if (sizeFilter) {
      filtered = filtered.filter(monster => monster.statblock.size === sizeFilter);
    }

    if (sourceFilter) {
      filtered = filtered.filter(monster => monster.source === sourceFilter);
    }

    setFilteredMonsters(filtered);
    setCurrentPage(1);
  }, [monsters, searchTerm, crFilter, typeFilter, sizeFilter, sourceFilter]);

  const loadMonsters = useCallback(async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch('/api/monsters');
      if (!response.ok) {
        throw new Error('Failed to load monsters');
      }

      const data = await response.json();
      setMonsters(data.monsters || []);
    } catch (err: any) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, []);

  const handleSelectMonster = useCallback((monster: Monster) => {
    if (multiSelect) {
      setSelectedMonsters(prev => {
        const newSet = new Set(prev);
        if (newSet.has(monster.id)) {
          newSet.delete(monster.id);
        } else {
          newSet.add(monster.id);
        }
        return newSet;
      });
    } else {
      setSelectedMonsters(new Set([monster.id]));
    }

    if (onSelectMonster) {
      onSelectMonster(monster);
    }
  }, [multiSelect, onSelectMonster]);

  const handleAddToEncounter = useCallback(async (monster: Monster) => {
    if (!onAddToEncounter) return;

    const instanceName = prompt(`Instance name for ${monster.name}:`, monster.name);
    if (instanceName !== null) {
      try {
        await onAddToEncounter(monster.id, instanceName);
      } catch (error) {
        console.error('Failed to add monster to encounter:', error);
        alert('Failed to add monster to encounter');
      }
    }
  }, [onAddToEncounter]);

  const getCRColor = (cr: string): string => {
    const numericCR = cr.includes('/') ? parseFloat(cr.split('/')[0]) / parseFloat(cr.split('/')[1]) : parseFloat(cr);

    if (numericCR < 1) return '#28a745'; // Green for easy
    if (numericCR < 5) return '#ffc107'; // Yellow for moderate  
    if (numericCR < 10) return '#fd7e14'; // Orange for hard
    return '#dc3545'; // Red for deadly
  };

  const getAbilityModifier = (score: number): number => {
    return Math.floor((score - 10) / 2);
  };

  // Get unique values for filters
  const uniqueCRs = [...new Set(monsters.map(m => m.statblock.challengeRating))].sort();
  const uniqueTypes = [...new Set(monsters.map(m => m.statblock.type))].sort();
  const uniqueSizes = [...new Set(monsters.map(m => m.statblock.size))].sort();
  const uniqueSources = [...new Set(monsters.map(m => m.source))].sort();

  // Pagination
  const totalPages = Math.ceil(filteredMonsters.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const paginatedMonsters = filteredMonsters.slice(startIndex, startIndex + itemsPerPage);

  if (loading) {
    return <div className="monster-browser loading">Loading monsters...</div>;
  }

  if (error) {
    return (
      <div className="monster-browser error">
        <p>Error: {error}</p>
        <button onClick={loadMonsters}>Retry</button>
      </div>
    );
  }

  return (
    <div className="monster-browser">
      <div className="monster-browser-header">
        <h3>Monster Library</h3>
        <div className="monster-stats">
          <span>{filteredMonsters.length} of {monsters.length} monsters</span>
          {selectedMonsters.size > 0 && (
            <span className="selected-count">({selectedMonsters.size} selected)</span>
          )}
        </div>
      </div>

      <div className="monster-filters">
        <div className="filter-row">
          <input
            type="text"
            placeholder="Search monsters..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <button onClick={() => {
            setSearchTerm('');
            setCrFilter('');
            setTypeFilter('');
            setSizeFilter('');
            setSourceFilter('');
          }}>
            Clear Filters
          </button>
        </div>

        <div className="filter-row">
          <select value={crFilter} onChange={(e) => setCrFilter(e.target.value)}>
            <option value="">All CR</option>
            {uniqueCRs.map(cr => (
              <option key={cr} value={cr}>CR {cr}</option>
            ))}
          </select>

          <select value={typeFilter} onChange={(e) => setTypeFilter(e.target.value)}>
            <option value="">All Types</option>
            {uniqueTypes.map(type => (
              <option key={type} value={type}>{type}</option>
            ))}
          </select>

          <select value={sizeFilter} onChange={(e) => setSizeFilter(e.target.value)}>
            <option value="">All Sizes</option>
            {uniqueSizes.map(size => (
              <option key={size} value={size}>{size}</option>
            ))}
          </select>

          <select value={sourceFilter} onChange={(e) => setSourceFilter(e.target.value)}>
            <option value="">All Sources</option>
            {uniqueSources.map(source => (
              <option key={source} value={source}>{source}</option>
            ))}
          </select>
        </div>
      </div>

      <div className="monster-grid">
        {paginatedMonsters.map(monster => (
          <div 
            key={monster.id}
            className={`monster-card ${selectedMonsters.has(monster.id) ? 'selected' : ''}`}
            onClick={() => handleSelectMonster(monster)}
          >
            <div className="monster-card-header">
              <h4 className="monster-name">{monster.name}</h4>
              <div className="monster-badges">
                <span 
                  className="cr-badge"
                  style={{ backgroundColor: getCRColor(monster.statblock.challengeRating) }}
                >
                  CR {monster.statblock.challengeRating}
                </span>
                <span className="size-type-badge">
                  {monster.statblock.size} {monster.statblock.type}
                </span>
              </div>
            </div>

            <div className="monster-stats-grid">
              <div className="stat-block">
                <span className="stat-label">AC</span>
                <span className="stat-value">{monster.statblock.armorClass}</span>
              </div>
              <div className="stat-block">
                <span className="stat-label">HP</span>
                <span className="stat-value">{monster.statblock.hitPoints}</span>
              </div>
              <div className="stat-block">
                <span className="stat-label">Speed</span>
                <span className="stat-value">
                  {Object.entries(monster.statblock.speed)
                    .map(([type, value]) => `${type} ${value}ft`)
                    .join(', ')}
                </span>
              </div>
            </div>

            <div className="ability-scores">
              <div className="ability">
                <span className="ability-label">STR</span>
                <span className="ability-score">
                  {monster.statblock.abilities.STR} ({getAbilityModifier(monster.statblock.abilities.STR) >= 0 ? '+' : ''}{getAbilityModifier(monster.statblock.abilities.STR)})
                </span>
              </div>
              <div className="ability">
                <span className="ability-label">DEX</span>
                <span className="ability-score">
                  {monster.statblock.abilities.DEX} ({getAbilityModifier(monster.statblock.abilities.DEX) >= 0 ? '+' : ''}{getAbilityModifier(monster.statblock.abilities.DEX)})
                </span>
              </div>
              <div className="ability">
                <span className="ability-label">CON</span>
                <span className="ability-score">
                  {monster.statblock.abilities.CON} ({getAbilityModifier(monster.statblock.abilities.CON) >= 0 ? '+' : ''}{getAbilityModifier(monster.statblock.abilities.CON)})
                </span>
              </div>
            </div>

            {showActions && monster.statblock.actions.length > 0 && (
              <div className="monster-actions">
                <h5>Actions</h5>
                <div className="action-list">
                  {monster.statblock.actions.slice(0, 2).map((_action, __idx) => (
                    <div key={idx} className="action-item">
                      <span className="action-name">{action.name}</span>
                      {action.attackBonus && (
                        <span className="attack-bonus">+{action.attackBonus}</span>
                      )}
                    </div>
                  ))}
                  {monster.statblock.actions.length > 2 && (
                    <div className="action-item more-actions">
                      +{monster.statblock.actions.length - 2} more
                    </div>
                  )}
                </div>
              </div>
            )}

            {monster.statblock.conditionImmunities && monster.statblock.conditionImmunities.length > 0 && (
              <div className="immunities">
                <span className="immunity-label">Immunities:</span>
                <span className="immunity-list">
                  {monster.statblock.conditionImmunities.join(', ')}
                </span>
              </div>
            )}

            {showActions && onAddToEncounter && encounterId && (
              <div className="monster-actions-row">
                <button 
                  className="add-to-encounter-btn"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleAddToEncounter(monster);
                  }}
                >
                  Add to Encounter
                </button>
              </div>
            )}

            <div className="monster-source">
              <small>Source: {monster.source}</small>
            </div>
          </div>
        ))}
      </div>

      {totalPages > 1 && (
        <div className="pagination">
          <button 
            disabled={currentPage === 1}
            onClick={() => setCurrentPage(prev => prev - 1)}
          >
            Previous
          </button>

          <span className="page-info">
            Page {currentPage} of {totalPages} ({filteredMonsters.length} monsters)
          </span>

          <button 
            disabled={currentPage === totalPages}
            onClick={() => setCurrentPage(prev => prev + 1)}
          >
            Next
          </button>
        </div>
      )}

      {multiSelect && selectedMonsters.size > 0 && onAddToEncounter && encounterId && (
        <div className="bulk-actions">
          <button 
            className="bulk-add-btn"
            onClick={() => {
              selectedMonsters.forEach(async (monsterId) => {
                const monster = monsters.find(m => m.id === monsterId);
                if (monster && onAddToEncounter) {
                  await onAddToEncounter(monsterId, monster.name);
                }
              });
              setSelectedMonsters(new Set());
            }}
          >
            Add Selected to Encounter ({selectedMonsters.size})
          </button>
        </div>
      )}
    </div>
  );
};

export default MonsterBrowser;

import React, { useState, useEffect, useRef } from 'react';

interface PerformanceMetrics {
  fps: number;
  frameTime: number;
  memoryUsage: number;
  renderCalls: number;
  tokenCount: number;
  networkLatency: number;
  spellEffects: number;
  physicsObjects: number;
}

interface PerformanceMonitorProps {
  isVisible: boolean;
  onToggle: () => void;
}

export const PerformanceMonitor: React.FC<PerformanceMonitorProps> = ({_isVisible, _onToggle}) => {
  const [metrics, setMetrics] = useState<PerformanceMetrics>({
    fps: 0,
    frameTime: 0,
    memoryUsage: 0,
    renderCalls: 0,
    tokenCount: 0,
    networkLatency: 0,
    spellEffects: 0,
    physicsObjects: 0
  });

  const frameCountRef = useRef(0);
  const lastTimeRef = useRef(performance.now());
  const fpsHistoryRef = useRef<number[]>([]);
  const animationFrameRef = useRef<number>();

  // Performance monitoring loop
  useEffect(() => {
    const updateMetrics = () => {
      const now = performance.now();
      const deltaTime = now - lastTimeRef.current;

      frameCountRef.current++;

      // Calculate FPS every second
      if (deltaTime >= 1000) {
        const fps = Math.round((frameCountRef.current * 1000) / deltaTime);
        const frameTime = deltaTime / frameCountRef.current;

        // Update FPS history for smoothing
        fpsHistoryRef.current.push(fps);
        if (fpsHistoryRef.current.length > 10) {
          fpsHistoryRef.current.shift();
        }

        const avgFps = fpsHistoryRef.current.reduce((_a, __b) => a + b, 0) / fpsHistoryRef.current.length;

        // Get memory usage if available
        const memoryUsage = (performance as any).memory 
          ? Math.round((performance as any).memory.usedJSHeapSize / 1024 / 1024)
          : 0;

        // Get network timing if available
        const navigation = performance.getEntriesByType('navigation')[0] as PerformanceNavigationTiming;
        const networkLatency = navigation ? Math.round(navigation.responseEnd - navigation.requestStart) : 0;

        setMetrics(prev => ({
          ...prev,
          fps: Math.round(avgFps),
          frameTime: Math.round(frameTime * 100) / 100,
          memoryUsage,
          networkLatency,
          // These would be updated by the actual game systems
          renderCalls: prev.renderCalls,
          tokenCount: prev.tokenCount,
          spellEffects: prev.spellEffects,
          physicsObjects: prev.physicsObjects
        }));

        frameCountRef.current = 0;
        lastTimeRef.current = now;
      }

      animationFrameRef.current = requestAnimationFrame(updateMetrics);
    };

    if (isVisible) {
      animationFrameRef.current = requestAnimationFrame(updateMetrics);
    }

    return () => {
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [isVisible]);

  // Update game-specific metrics from global state
  useEffect(() => {
    const updateGameMetrics = () => {
      // In a real implementation, these would come from the game state
      const gameState = (window as any).__VTT_GAME_STATE__;
      if (gameState) {
        setMetrics(prev => ({
          ...prev,
          tokenCount: gameState.tokens?.length || 0,
          spellEffects: gameState.activeSpellEffects?.size || 0,
          physicsObjects: gameState.physicsObjects?.length || 0,
          renderCalls: gameState.lastFrameRenderCalls || 0
        }));
      }
    };

    const interval = setInterval(updateGameMetrics, 1000);
    return () => clearInterval(interval);
  }, []);

  const getPerformanceColor = (_value: number, thresholds: { good: number; warning: number }) => {
    if (value >= thresholds.good) return 'text-green-400';
    if (value >= thresholds.warning) return 'text-yellow-400';
    return 'text-red-400';
  };

  const getMemoryColor = (_mb: number) => {
    if (mb < 100) return 'text-green-400';
    if (mb < 200) return 'text-yellow-400';
    return 'text-red-400';
  };

  if (!isVisible) {
    return (
      <button
        onClick={onToggle}
        className="fixed top-4 right-4 bg-gray-800 text-white px-3 py-1 rounded text-sm hover:bg-gray-700 z-50"
      >
        Performance
      </button>
    );
  }

  return (
    <div className="fixed top-4 right-4 bg-gray-900 text-white p-4 rounded-lg shadow-lg z-50 min-w-64">
      <div className="flex justify-between items-center mb-3">
        <h3 className="font-bold text-lg text-blue-400">Performance Monitor</h3>
        <button
          onClick={onToggle}
          className="text-gray-400 hover:text-white text-xl leading-none"
        >
          ×
        </button>
      </div>

      <div className="space-y-2 text-sm">
        {/* Core Performance */}
        <div className="border-b border-gray-700 pb-2">
          <div className="font-semibold text-purple-400 mb-1">Core Performance</div>
          <div className="grid grid-cols-2 gap-2">
            <div>FPS: <span className={getPerformanceColor(metrics.fps, { good: 55, warning: 30 })}>{metrics.fps}</span></div>
            <div>Frame: <span className="text-cyan-400">{metrics.frameTime}ms</span></div>
            <div>Memory: <span className={getMemoryColor(metrics.memoryUsage)}>{metrics.memoryUsage}MB</span></div>
            <div>Latency: <span className="text-orange-400">{metrics.networkLatency}ms</span></div>
          </div>
        </div>

        {/* Game Systems */}
        <div className="border-b border-gray-700 pb-2">
          <div className="font-semibold text-green-400 mb-1">Game Systems</div>
          <div className="grid grid-cols-2 gap-2">
            <div>Tokens: <span className="text-yellow-400">{metrics.tokenCount}</span></div>
            <div>Renders: <span className="text-blue-400">{metrics.renderCalls}</span></div>
            <div>Spells: <span className="text-purple-400">{metrics.spellEffects}</span></div>
            <div>Physics: <span className="text-red-400">{metrics.physicsObjects}</span></div>
          </div>
        </div>

        {/* Performance Indicators */}
        <div>
          <div className="font-semibold text-orange-400 mb-1">Status</div>
          <div className="space-y-1">
            <div className="flex items-center">
              <div className={`w-2 h-2 rounded-full mr-2 ${metrics.fps >= 55 ? 'bg-green-400' : metrics.fps >= 30 ? 'bg-yellow-400' : 'bg-red-400'}`}></div>
              <span className="text-xs">Rendering: {metrics.fps >= 55 ? 'Excellent' : metrics.fps >= 30 ? 'Good' : 'Poor'}</span>
            </div>
            <div className="flex items-center">
              <div className={`w-2 h-2 rounded-full mr-2 ${metrics.memoryUsage < 100 ? 'bg-green-400' : metrics.memoryUsage < 200 ? 'bg-yellow-400' : 'bg-red-400'}`}></div>
              <span className="text-xs">Memory: {metrics.memoryUsage < 100 ? 'Optimal' : metrics.memoryUsage < 200 ? 'Moderate' : 'High'}</span>
            </div>
            <div className="flex items-center">
              <div className={`w-2 h-2 rounded-full mr-2 ${metrics.networkLatency < 100 ? 'bg-green-400' : metrics.networkLatency < 300 ? 'bg-yellow-400' : 'bg-red-400'}`}></div>
              <span className="text-xs">Network: {metrics.networkLatency < 100 ? 'Fast' : metrics.networkLatency < 300 ? 'Moderate' : 'Slow'}</span>
            </div>
          </div>
        </div>

        {/* Quick Actions */}
        <div className="border-t border-gray-700 pt-2">
          <div className="flex space-x-2">
            <button 
              className="text-xs bg-blue-600 hover:bg-blue-700 px-2 py-1 rounded"
              onClick={() => {
                if ((performance as any).memory) {
                  console.log('Memory Details:', (performance as any).memory);
                }
              }}
            >
              Log Memory
            </button>
            <button 
              className="text-xs bg-purple-600 hover:bg-purple-700 px-2 py-1 rounded"
              onClick={() => {
                const gameState = (window as any).__VTT_GAME_STATE__;
                console.log('Game State:', gameState);
              }}
            >
              Log State
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PerformanceMonitor;

