          character,
          allies,
          enemies,
          battlefield,
          objectives: ['Survive encounter', 'Defeat enemies']
        })
      });

      const result = await response.json();
      if (result.success) {
        setRecommendation(result.data);
      }
    } catch (error) {
      console.error('Failed to get tactical recommendation:', error);
    } finally {
      setIsAnalyzing(false);
    }
  };

  const getPositioning = async () => {
    try {
      const response = await fetch('/api/combat/positioning', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          character,
          battlefield,
          allies,
          enemies
        })
      });

