        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getToken('token')}`
        },
        body: JSON.stringify({
          party: [character, ...allies],
          enemies,
          battlefield,
          maxRounds: 10
        })
      });

      const result = await response.json();
      if (result.success) {
        // Show simulation results
