      <div className="bg-gray-800 rounded-lg p-6">
        <h3 className="text-xl font-bold text-white mb-4">Character Summary</h3>

        {currentGeneration?.character && (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-3">
              <div>
                <span className="text-gray-400">Name:</span>
                <span className="ml-2 text-white font-medium">{currentGeneration.character.name}</span>
              </div>
              <div>
                <span className="text-gray-400">Race:</span>
                <span className="ml-2 text-white">{currentGeneration.character.race}</span>
              </div>
              <div>
                <span className="text-gray-400">Class:</span>
                <span className="ml-2 text-white">{currentGeneration.character.class}</span>
              </div>
              <div>
                <span className="text-gray-400">Background:</span>
                <span className="ml-2 text-white">{currentGeneration.character.background}</span>
              </div>
            </div>

            <div className="space-y-3">
              <div>
                <span className="text-gray-400">Level:</span>
                <span className="ml-2 text-white">{currentGeneration.character.level}</span>
              </div>
              <div>
                <span className="text-gray-400">Hit Points:</span>
                <span className="ml-2 text-white">{currentGeneration.character.hitPoints}</span>
              </div>
              <div>
                <span className="text-gray-400">Armor Class:</span>
                <span className="ml-2 text-white">{currentGeneration.character.armorClass}</span>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Actions */}
      <div className="flex justify-center gap-4">
        <button
          onClick={acceptCharacter}
          className="flex items-center gap-2 px-6 py-3 bg-gradient-to-r from-green-600 to-blue-600 text-white font-medium rounded-lg hover:from-green-700 hover:to-blue-700 transition-all"
        >
          <Download className="w-5 h-5" />
          Accept Character
        </button>

        <button
          onClick={resetWizard}
          className="flex items-center gap-2 px-6 py-3 bg-gray-700 text-gray-300 font-medium rounded-lg hover:bg-gray-600 transition-colors"
        >
          <Wand2 className="w-5 h-5" />
          Create Another
        </button>
      </div>
    </motion.div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-900 via-gray-900 to-purple-900 p-6">
      <AnimatePresence mode="wait">
        {!currentGeneration && !isGenerating && renderConceptForm()}
        {(isGenerating || (currentGeneration && !currentGeneration.isComplete)) && renderGenerationProgress()}
        {currentGeneration?.isComplete && renderCharacterResult()}
      </AnimatePresence>
    </div>
  );
};

/**
 * Login Form - Elegant authentication form with validation and error handling
 */
import React, { useState } from 'react';
import { useRouter } from 'next/router';
import { Eye, EyeOff, Mail, Lock, ArrowRight, Github, Google } from 'lucide-react';
import { Button } from '../ui/Button';
import { Input } from '../ui/Input';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '../ui/Card';
import { useAuth } from '../../hooks/useAuth';
import { _cn, isValidEmail } from '../../lib/utils';

interface LoginFormData {
  identifier: string;
  password: string;
  rememberMe: boolean;
}

interface LoginFormErrors {
  identifier?: string;
  password?: string;
  general?: string;
}

export function LoginForm() {
  const router = useRouter();
  const { login,  loading,  error,  clearError  } = useAuth();

  const [formData, setFormData] = useState<LoginFormData>({
    identifier: '',
    password: '',
    rememberMe: false,
  });

  const [errors, setErrors] = useState<LoginFormErrors>({});
  const [showPassword, setShowPassword] = useState(false);

  // Handle input changes
  const handleChange = (_field: keyof LoginFormData, _value: string | boolean) => {
    setFormData(prev => ({ ...prev, [field]: value }));

    // Clear field-specific errors when user starts typing
    if (errors[field as keyof LoginFormErrors]) {
      setErrors(prev => ({ ...prev, [field]: undefined }));
    }

    // Clear general errors
    if (error) {
      clearError();
    }
  };

  // Validate form
  const validateForm = (): boolean => {
    const newErrors: LoginFormErrors = {};

    if (!formData.identifier.trim()) {
      newErrors.identifier = 'Email or username is required';
    } else if (formData.identifier.includes('@') && !isValidEmail(formData.identifier)) {
      newErrors.identifier = 'Please enter a valid email address';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Handle form submission
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) return;

    try {
      await login(formData.identifier, formData.password, formData.rememberMe);

      // Redirect to dashboard or intended page
      const redirectTo = (router.query.redirect as string) || '/dashboard';
      router.push(redirectTo);
    } catch (err) {
      // Error is handled by useAuth hook
      console.error('Login failed:', err);
    }
  };

  // Handle social login
  const handleSocialLogin = (_provider: 'google' | 'github') => {
    // Redirect to OAuth provider
    const redirectUri = encodeURIComponent(window.location.origin + '/auth/callback');
    window.location.href = `/api/auth/${provider}?redirect_uri=${redirectUri}`;
  };

  return (
    <Card className="w-full max-w-md mx-auto">
      <CardHeader className="space-y-1 text-center">
        <CardTitle className="text-2xl font-bold">Welcome back</CardTitle>
        <CardDescription>
          Sign in to your account to continue your adventure
        </CardDescription>
      </CardHeader>

      <CardContent className="space-y-6">
        {/* Social Login Buttons */}
        <div className="grid grid-cols-2 gap-3">
          <Button
            type="button"
            variant="secondary"
            size="md"
            onClick={() => handleSocialLogin('google')}
            leftIcon={<Google className="h-4 w-4" />}
          >
            Google
          </Button>
          <Button
            type="button"
            variant="secondary"
            size="md"
            onClick={() => handleSocialLogin('github')}
            leftIcon={<Github className="h-4 w-4" />}
          >
            GitHub
          </Button>
        </div>

        {/* Divider */}
        <div className="relative">
          <div className="absolute inset-0 flex items-center">
            <span className="w-full border-t border-neutral-300" />
          </div>
          <div className="relative flex justify-center text-xs uppercase">
            <span className="bg-white px-2 text-neutral-500">Or continue with</span>
          </div>
        </div>

        {/* Login Form */}
        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Email/Username Field */}
          <Input
            label="Email or Username"
            type="text"
            placeholder="Enter your email or username"
            value={formData.identifier}
            onChange={(e) => handleChange('identifier', e.target.value)}
            error={errors.identifier}
            leftIcon={<Mail className="h-4 w-4" />}
            disabled={loading}
            autoComplete="username"
            autoFocus
          />

          {/* Password Field */}
          <Input
            label="Password"
            type={showPassword ? 'text' : 'password'}
            placeholder="Enter your password"
            value={formData.password}
            onChange={(e) => handleChange('password', e.target.value)}
            error={errors.password}
            leftIcon={<Lock className="h-4 w-4" />}
            rightIcon={
              <button
                type="button"
                onClick={() => setShowPassword(!showPassword)}
                className="text-neutral-400 hover:text-neutral-600 focus:outline-none"
              >
                {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
              </button>
            }
            disabled={loading}
            autoComplete="current-password"
          />

          {/* Remember Me & Forgot Password */}
          <div className="flex items-center justify-between">
            <label className="flex items-center space-x-2 text-sm">
              <input
                type="checkbox"
                checked={formData.rememberMe}
                onChange={(e) => handleChange('rememberMe', e.target.checked)}
                className="rounded border-neutral-300 text-primary-600 focus:ring-primary-500"
                disabled={loading}
              />
              <span className="text-neutral-700">Remember me</span>
            </label>

            <Button
              type="button"
              variant="link"
              size="sm"
              onClick={() => router.push('/auth/forgot-password')}
              disabled={loading}
              className="text-sm"
            >
              Forgot password?
            </Button>
          </div>

          {/* General Error Message */}
          {error && (
            <div className="p-3 text-sm text-error-700 bg-error-50 border border-error-200 rounded-lg">
              {error}
            </div>
          )}

          {/* Submit Button */}
          <Button
            type="submit"
            variant="primary"
            size="lg"
            fullWidth
            loading={loading}
            rightIcon={!loading && <ArrowRight className="h-4 w-4" />}
          >
            {loading ? 'Signing in...' : 'Sign in'}
          </Button>
        </form>

        {/* Sign Up Link */}
        <div className="text-center text-sm text-neutral-600">
          Don't have an account?{' '}
          <Button
            type="button"
            variant="link"
            size="sm"
            onClick={() => router.push('/auth/register')}
            disabled={loading}
            className="font-medium"
          >
            Sign up
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

/**
 * Registration Form - Comprehensive signup form with validation and terms acceptance
 */
import React, { useState } from 'react';
import { useRouter } from 'next/router';
import { User, Mail, Lock, Eye, EyeOff, ArrowRight, _Check} from 'lucide-react';
import { Button } from '../ui/Button';
import { Input } from '../ui/Input';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '../ui/Card';
import { useAuth, type RegisterData } from '../../hooks/useAuth';
import { cn, isValidEmail, validatePassword } from '../../lib/utils';

interface RegisterFormData extends RegisterData {
  confirmPassword: string;
}

interface RegisterFormErrors {
  username?: string;
  email?: string;
  password?: string;
  confirmPassword?: string;
  firstName?: string;
  lastName?: string;
  acceptedTerms?: string;
  general?: string;
}

export function RegisterForm() {
  const router = useRouter();
  const { register,  loading,  error,  clearError  } = useAuth();

  const [formData, setFormData] = useState<RegisterFormData>({
    username: '',
    email: '',
    password: '',
    confirmPassword: '',
    firstName: '',
    lastName: '',
    timezone: Intl.DateTimeFormat().resolvedOptions().timeZone,
    acceptedTerms: false,
  });

  const [errors, setErrors] = useState<RegisterFormErrors>({});
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [passwordStrength, setPasswordStrength] = useState({ score: 0, feedback: [] as string[] });

  // Handle input changes
  const handleChange = (_field: keyof RegisterFormData, _value: string | boolean) => {
    setFormData(prev => ({ ...prev, [field]: value }));

    // Clear field-specific errors
    if (errors[field as keyof RegisterFormErrors]) {
      setErrors(prev => ({ ...prev, [field]: undefined }));
    }

    // Clear general errors
    if (error) {
      clearError();
    }

    // Update password strength on password change
    if (field === 'password' && typeof value === 'string') {
      setPasswordStrength(validatePassword(value));
    }
  };

  // Validate form
  const validateForm = (): boolean => {
    const newErrors: RegisterFormErrors = {};

    // Username validation
    if (!formData.username.trim()) {
      newErrors.username = 'Username is required';
    } else if (formData.username.length < 3) {
      newErrors.username = 'Username must be at least 3 characters';
    } else if (!/^[a-zA-Z0-9_-]+$/.test(formData.username)) {
      newErrors.username = 'Username can only contain letters, numbers, dashes, and underscores';
    }

    // Email validation
    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!isValidEmail(formData.email)) {
      newErrors.email = 'Please enter a valid email address';
    }

    // First name validation
    if (!formData.firstName.trim()) {
      newErrors.firstName = 'First name is required';
    }

    // Last name validation
    if (!formData.lastName.trim()) {
      newErrors.lastName = 'Last name is required';
    }

    // Password validation
    const passwordValidation = validatePassword(formData.password);
    if (!passwordValidation.isValid) {
      newErrors.password = passwordValidation.feedback[0] || 'Password does not meet requirements';
    }

    // Confirm password validation
    if (!formData.confirmPassword) {
      newErrors.confirmPassword = 'Please confirm your password';
    } else if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match';
    }

    // Terms acceptance validation
    if (!formData.acceptedTerms) {
      newErrors.acceptedTerms = 'You must accept the terms and conditions';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Handle form submission
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) return;

    try {
      const { _confirmPassword,  _...registerData  } = formData;
      await register(registerData);

      // Redirect to dashboard or email verification prompt
      router.push('/auth/welcome');
    } catch (err) {
      console.error('Registration failed:', err);
    }
  };

  // Password strength indicator
  const getPasswordStrengthColor = (_score: number) => {
    if (score < 2) return 'bg-error-500';
    if (score < 4) return 'bg-warning-500';
    return 'bg-success-500';
  };

  const getPasswordStrengthText = (_score: number) => {
    if (score < 2) return 'Weak';
    if (score < 4) return 'Medium';
    return 'Strong';
  };

  return (
    <Card className="w-full max-w-md mx-auto">
      <CardHeader className="space-y-1 text-center">
        <CardTitle className="text-2xl font-bold">Create your account</CardTitle>
        <CardDescription>
          Join the adventure and start your tabletop journey
        </CardDescription>
      </CardHeader>

      <CardContent className="space-y-4">
        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Name Fields */}
          <div className="grid grid-cols-2 gap-3">
            <Input
              label="First Name"
              type="text"
              placeholder="John"
              value={formData.firstName}
              onChange={(e) => handleChange('firstName', e.target.value)}
              error={errors.firstName}
              disabled={loading}
              autoComplete="given-name"
            />
            <Input
              label="Last Name"
              type="text"
              placeholder="Doe"
              value={formData.lastName}
              onChange={(e) => handleChange('lastName', e.target.value)}
              error={errors.lastName}
              disabled={loading}
              autoComplete="family-name"
            />
          </div>

          {/* Username Field */}
          <Input
            label="Username"
            type="text"
            placeholder="Choose a unique username"
            value={formData.username}
            onChange={(e) => handleChange('username', e.target.value)}
            error={errors.username}
            leftIcon={<User className="h-4 w-4" />}
            disabled={loading}
            autoComplete="username"
          />

          {/* Email Field */}
          <Input
            label="Email"
            type="email"
            placeholder="Enter your email address"
            value={formData.email}
            onChange={(e) => handleChange('email', e.target.value)}
            error={errors.email}
            leftIcon={<Mail className="h-4 w-4" />}
            disabled={loading}
            autoComplete="email"
          />

          {/* Password Field */}
          <div className="space-y-2">
            <Input
              label="Password"
              type={showPassword ? 'text' : 'password'}
              placeholder="Create a strong password"
              value={formData.password}
              onChange={(e) => handleChange('password', e.target.value)}
              error={errors.password}
              leftIcon={<Lock className="h-4 w-4" />}
              rightIcon={
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="text-neutral-400 hover:text-neutral-600 focus:outline-none"
                >
                  {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                </button>
              }
              disabled={loading}
              autoComplete="new-password"
            />

            {/* Password Strength Indicator */}
            {formData.password && (
              <div className="space-y-1">
                <div className="flex items-center justify-between text-xs">
                  <span className="text-neutral-600">Password strength</span>
                  <span className={cn(
                    "font-medium",
                    passwordStrength.score < 2 ? 'text-error-600' :
                    passwordStrength.score < 4 ? 'text-warning-600' : 'text-success-600'
                  )}>
                    {getPasswordStrengthText(passwordStrength.score)}
                  </span>
                </div>
                <div className="w-full bg-neutral-200 rounded-full h-1.5">
                  <div
                    className={cn(
                      "h-1.5 rounded-full transition-all duration-300",
                      getPasswordStrengthColor(passwordStrength.score)
                    )}
                    style={{ width: `${(passwordStrength.score / 5) * 100}%` }}
                  />
                </div>
              </div>
            )}
          </div>

          {/* Confirm Password Field */}
          <Input
            label="Confirm Password"
            type={showConfirmPassword ? 'text' : 'password'}
            placeholder="Confirm your password"
            value={formData.confirmPassword}
            onChange={(e) => handleChange('confirmPassword', e.target.value)}
            error={errors.confirmPassword}
            leftIcon={<Lock className="h-4 w-4" />}
            rightIcon={
              <button
                type="button"
                onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                className="text-neutral-400 hover:text-neutral-600 focus:outline-none"
              >
                {showConfirmPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
              </button>
            }
            disabled={loading}
            autoComplete="new-password"
          />

          {/* Terms Acceptance */}
          <div className="space-y-2">
            <label className="flex items-start space-x-2 text-sm">
              <input
                type="checkbox"
                checked={formData.acceptedTerms}
                onChange={(e) => handleChange('acceptedTerms', e.target.checked)}
                className={cn(
                  "mt-1 rounded border-neutral-300 text-primary-600 focus:ring-primary-500",
                  errors.acceptedTerms && "border-error-300"
                )}
                disabled={loading}
              />
              <span className="text-neutral-700 leading-relaxed">
                I agree to the{' '}
                <a href="/terms" target="_blank" className="text-primary-600 hover:underline font-medium">
                  Terms of Service
                </a>{' '}
                and{' '}
                <a href="/privacy" target="_blank" className="text-primary-600 hover:underline font-medium">
                  Privacy Policy
                </a>
              </span>
            </label>
            {errors.acceptedTerms && (
              <p className="text-xs text-error-600">{errors.acceptedTerms}</p>
            )}
          </div>

          {/* General Error Message */}
          {error && (
            <div className="p-3 text-sm text-error-700 bg-error-50 border border-error-200 rounded-lg">
              {error}
            </div>
          )}

          {/* Submit Button */}
          <Button
            type="submit"
            variant="primary"
            size="lg"
            fullWidth
            loading={loading}
            rightIcon={!loading && <ArrowRight className="h-4 w-4" />}
          >
            {loading ? 'Creating account...' : 'Create account'}
          </Button>
        </form>

        {/* Sign In Link */}
        <div className="text-center text-sm text-neutral-600">
          Already have an account?{' '}
          <Button
            type="button"
            variant="link"
            size="sm"
            onClick={() => router.push('/auth/login')}
            disabled={loading}
            className="font-medium"
          >
            Sign in
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

