/**
 * Campaign Map Manager - Interface for managing scenes and maps within a campaign
 */
import React, { useState, useEffect } from 'react';
import { 
  Map, 
  Plus, 
  Play, 
  Pause, 
  Settings, 
  Users, 
  Eye,
  EyeOff,
  Trash2,
  Upload,
  Grid3X3,
  Layers
} from 'lucide-react';
import { Button } from '../ui/Button';
import { Input } from '../ui/Input';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/Card';
import { cn } from '../../lib/utils';

interface Scene {
  id: string;
  name: string;
  mapId: string | null;
  isActive: boolean;
  createdAt: Date;
}

interface CampaignMapManagerProps {
  campaignId: string;
  isGM: boolean;
  onSceneSelect: (_sceneId: string) => void;
  onLaunchSession: (_sceneId: string) => void;
}

interface CreateSceneModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSubmit: (name: string, _mapId?: string) => void;
  availableMaps: Array<{ id: string; name: string; }>;
}

function CreateSceneModal({_ isOpen, _onClose, _onSubmit, availableMaps }: CreateSceneModalProps) {
  const [sceneName, setSceneName] = useState('');
  const [selectedMapId, setSelectedMapId] = useState<string>('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!sceneName.trim()) return;

    onSubmit(sceneName.trim(), selectedMapId || undefined);
    setSceneName('');
    setSelectedMapId('');
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <Card className="w-full max-w-md">
        <CardHeader>
          <CardTitle>Create New Scene</CardTitle>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-sm font-medium mb-2">Scene Name</label>
              <Input
                value={sceneName}
                onChange={(e) => setSceneName(e.target.value)}
                placeholder="Enter scene name..."
                autoFocus
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-2">Map (Optional)</label>
              <select
                value={selectedMapId}
                onChange={(e) => setSelectedMapId(e.target.value)}
                className="w-full px-3 py-2 border border-neutral-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
              >
                <option value="">No map - Empty scene</option>
                {availableMaps.map(map => (
                  <option key={map.id} value={map.id}>{map.name}</option>
                ))}
              </select>
            </div>

            <div className="flex gap-2 justify-end">
              <Button variant="ghost" onClick={onClose}>
                Cancel
              </Button>
              <Button type="submit" disabled={!sceneName.trim()}>
                Create Scene
              </Button>
            </div>
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

export function CampaignMapManager({_ campaignId, _isGM, _onSceneSelect, _onLaunchSession}: CampaignMapManagerProps) {
  const [scenes, setScenes] = useState<Scene[]>([]);
  const [loading, setLoading] = useState(true);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [availableMaps, setAvailableMaps] = useState<Array<{ id: string; name: string; }>>([]);
  const [activeSceneId, setActiveSceneId] = useState<string | null>(null);

  // Load scenes for campaign
  useEffect(() => {
    const loadScenes = async () => {
      try {
        const response = await fetch(`/api/campaigns/${campaignId}/scenes`);
        if (response.ok) {
          const data = await response.json();
          setScenes(data.scenes || []);
          setActiveSceneId(data.activeSceneId || null);
        }
      } catch (error) {
        console.error('Failed to load scenes:', error);
      } finally {
        setLoading(false);
      }
    };

    loadScenes();
  }, [campaignId]);

  // Load available maps
  useEffect(() => {
    const loadMaps = async () => {
      try {
        const response = await fetch('/api/maps');
        if (response.ok) {
          const data = await response.json();
          setAvailableMaps(data.maps || []);
        }
      } catch (error) {
        console.error('Failed to load maps:', error);
      }
    };

    loadMaps();
  }, []);

  const handleCreateScene = async (name: string, _mapId?: string) => {
    try {
      const response = await fetch(`/api/campaigns/${campaignId}/scenes`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name,
          mapId,
          userId: 'temp-user-id' // TODO: Get from auth
        }),
      });

      if (response.ok) {
        const data = await response.json();
        setScenes(prev => [...prev, data.scene]);
      }
    } catch (error) {
      console.error('Failed to create scene:', error);
    }
  };

  const handleSetActiveScene = async (_sceneId: string) => {
    if (!isGM) return;

    try {
      const response = await fetch(`/api/campaigns/${campaignId}/active-scene`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          sceneId,
          userId: 'temp-user-id'
        }),
      });

      if (response.ok) {
        setActiveSceneId(sceneId);
      }
    } catch (error) {
      console.error('Failed to set active scene:', error);
    }
  };

  const handleDeleteScene = async (_sceneId: string) => {
    if (!isGM || !confirm('Are you sure you want to delete this scene?')) return;

    try {
      const response = await fetch(`/api/maps/scenes/${sceneId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        setScenes(prev => prev.filter(s => s.id !== sceneId));
        if (activeSceneId === sceneId) {
          setActiveSceneId(null);
        }
      }
    } catch (error) {
      console.error('Failed to delete scene:', error);
    }
  };

  if (loading) {
    return (
      <Card className="h-full">
        <CardContent className="flex items-center justify-center h-full">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="h-full flex flex-col">
      <Card className="flex-1">
        <CardHeader className="pb-3">
          <div className="flex items-center justify-between">
            <CardTitle className="flex items-center gap-2">
              <Map className="h-5 w-5" />
              Scenes & Maps
            </CardTitle>
            {isGM && (
              <div className="flex gap-2">
                <Button 
                  variant="ghost" 
                  size="sm"
                  onClick={() => setShowCreateModal(true)}
                >
                  <Plus className="h-4 w-4 mr-1" />
                  New Scene
                </Button>
                <Button variant="ghost" size="sm">
                  <Upload className="h-4 w-4 mr-1" />
                  Upload Map
                </Button>
              </div>
            )}
          </div>
        </CardHeader>

        <CardContent className="space-y-3">
          {scenes.length === 0 ? (
            <div className="text-center py-8">
              <Map className="h-12 w-12 text-neutral-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-neutral-900 mb-2">No scenes yet</h3>
              <p className="text-neutral-600 mb-4">
                Create your first scene to start building your campaign world
              </p>
              {isGM && (
                <Button onClick={() => setShowCreateModal(true)}>
                  <Plus className="h-4 w-4 mr-2" />
                  Create First Scene
                </Button>
              )}
            </div>
          ) : (
            scenes.map(scene => (
              <div
                key={scene.id}
                className={cn(
                  "group flex items-center gap-3 p-3 border rounded-lg transition-all cursor-pointer hover:border-neutral-300",
                  activeSceneId === scene.id 
                    ? "border-primary-500 bg-primary-50" 
                    : "border-neutral-200"
                )}
                onClick={() => onSceneSelect(scene.id)}
              >
                <div className={cn(
                  "flex-shrink-0 w-10 h-10 rounded-lg flex items-center justify-center",
                  scene.mapId 
                    ? "bg-primary-100 text-primary-600" 
                    : "bg-neutral-100 text-neutral-600"
                )}>
                  {scene.mapId ? <Map className="h-5 w-5" /> : <Grid3X3 className="h-5 w-5" />}
                </div>

                <div className="flex-1 min-w-0">
                  <div className="flex items-center gap-2 mb-1">
                    <h4 className="font-medium text-neutral-900 truncate">{scene.name}</h4>
                    {activeSceneId === scene.id && (
                      <span className="inline-flex items-center gap-1 px-2 py-1 bg-success-100 text-success-800 text-xs font-medium rounded-full">
                        <Play className="h-3 w-3" />
                        Active
                      </span>
                    )}
                  </div>
                  <div className="flex items-center gap-2 text-sm text-neutral-600">
                    <span>{scene.mapId ? 'Has map' : 'Empty scene'}</span>
                    <span>•</span>
                    <span>Created {new Date(scene.createdAt).toLocaleDateString()}</span>
                  </div>
                </div>

                <div className="flex items-center gap-1 opacity-0 group-hover:opacity-100 transition-opacity">
                  {isGM && (
                    <>
                      {activeSceneId !== scene.id ? (
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleSetActiveScene(scene.id);
                          }}
                          title="Set as active scene"
                        >
                          <Play className="h-4 w-4" />
                        </Button>
                      ) : (
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={(e) => {
                            e.stopPropagation();
                            onLaunchSession(scene.id);
                          }}
                          title="Launch session"
                        >
                          <Users className="h-4 w-4" />
                        </Button>
                      )}

                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={(e) => {
                          e.stopPropagation();
                          // TODO: Open scene settings
                        }}
                        title="Scene settings"
                      >
                        <Settings className="h-4 w-4" />
                      </Button>

                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleDeleteScene(scene.id);
                        }}
                        title="Delete scene"
                        className="text-red-600 hover:text-red-700"
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </>
                  )}
                </div>
              </div>
            ))
          )}
        </CardContent>
      </Card>

      {/* Active Session Status */}
      {activeSceneId && (
        <Card className="mt-4">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <div className="flex items-center gap-2">
                  <div className="w-2 h-2 bg-success-500 rounded-full animate-pulse"></div>
                  <span className="text-sm font-medium">Session Active</span>
                </div>
                <span className="text-sm text-neutral-600">
                  {scenes.find(s => s.id === activeSceneId)?.name}
                </span>
              </div>

              {isGM && (
                <div className="flex gap-2">
                  <Button 
                    variant="ghost" 
                    size="sm"
                    onClick={() => onLaunchSession(activeSceneId)}
                  >
                    <Users className="h-4 w-4 mr-1" />
                    Manage Session
                  </Button>
                  <Button 
                    variant="ghost" 
                    size="sm"
                    onClick={() => setActiveSceneId(null)}
                  >
                    <Pause className="h-4 w-4 mr-1" />
                    End Session
                  </Button>
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      )}

      <CreateSceneModal
        isOpen={showCreateModal}
        onClose={() => setShowCreateModal(false)}
        onSubmit={handleCreateScene}
        availableMaps={availableMaps}
      />
    </div>
  );
}

/**
 * Campaigns List - Grid/list view of user's campaigns with filtering and search
 */
import React, { useState, useEffect } from 'react';
import { 
  Search, 
  Filter, 
  Grid3X3, 
  List, 
  Plus,
  Users,
  Calendar,
  Clock,
  MoreHorizontal,
  Play,
  Pause,
  Settings,
  Archive,
  Gamepad2,
  Loader2
} from 'lucide-react';
import { Button } from '../ui/Button';
import { Input } from '../ui/Input';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/Card';
import { cn, formatDate, formatRelativeTime } from '../../lib/utils';
import { CampaignsApi, Campaign as ApiCampaign } from '../../api/campaigns';

interface Campaign {
  id: string;
  name: string;
  system: string;
  description: string;
  status: 'active' | 'paused' | 'completed' | 'planning';
  players: number;
  maxPlayers: number;
  sessions: number;
  totalHours: number;
  lastSession?: Date;
  nextSession?: Date;
  createdAt: Date;
  coverImage?: string;
  visibility: 'public' | 'private' | 'friends';
}

/**
 * Transform API campaign data to UI campaign format
 */
function transformApiCampaign(apiCampaign: ApiCampaign): Campaign {
  return {
    id: apiCampaign.id,
    name: apiCampaign.name,
    system: apiCampaign.gameSystem,
    description: apiCampaign.description,
    status: apiCampaign.isActive ? 'active' : 'paused',
    players: apiCampaign.players.length,
    maxPlayers: 6, // Default max players
    sessions: 0, // TODO: Add session tracking
    totalHours: 0, // TODO: Add hour tracking
    createdAt: new Date(apiCampaign.createdAt),
    visibility: 'private' // Default visibility
  };
}

type ViewMode = 'grid' | 'list';
type FilterStatus = 'all' | 'active' | 'paused' | 'completed' | 'planning';

export function CampaignsList() {
  const [campaigns, setCampaigns] = useState<Campaign[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [filterStatus, setFilterStatus] = useState<FilterStatus>('all');
  const [viewMode, setViewMode] = useState<ViewMode>('grid');
  const [selectedCampaign, setSelectedCampaign] = useState<string | null>(null);

  // Load campaigns from API
  useEffect(() => {
    const loadCampaigns = async () => {
      try {
        setLoading(true);
        setError(null);
        const response = await CampaignsApi.getCampaigns();

        if (response.success && response.data) {
          const transformedCampaigns = response.data.map(transformApiCampaign);
          setCampaigns(transformedCampaigns);
        } else {
          setError(response.error || 'Failed to load campaigns');
        }
      } catch (err) {
        setError('An unexpected error occurred while loading campaigns');
        console.error('Failed to load campaigns:', err);
      } finally {
        setLoading(false);
      }
    };

    loadCampaigns();
  }, []);

  // Filter campaigns based on search and status
  const filteredCampaigns = campaigns.filter(campaign => {
    const matchesSearch = campaign.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         campaign.system.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         campaign.description.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesStatus = filterStatus === 'all' || campaign.status === filterStatus;

    return matchesSearch && matchesStatus;
  });

  const getStatusColor = (_status: Campaign['status']) => {
    switch (status) {
      case 'active': return 'bg-success-100 text-success-800 border-success-200';
      case 'paused': return 'bg-warning-100 text-warning-800 border-warning-200';
      case 'completed': return 'bg-neutral-100 text-neutral-800 border-neutral-200';
      case 'planning': return 'bg-info-100 text-info-800 border-info-200';
      default: return 'bg-neutral-100 text-neutral-800 border-neutral-200';
    }
  };

  const getStatusIcon = (_status: Campaign['status']) => {
    switch (status) {
      case 'active': return <Play className="h-3 w-3" />;
      case 'paused': return <Pause className="h-3 w-3" />;
      case 'completed': return <Archive className="h-3 w-3" />;
      case 'planning': return <Settings className="h-3 w-3" />;
      default: return null;
    }
  };

  const CampaignCard = ({_campaign}: { campaign: Campaign }) => (
    <Card 
      key={campaign.id}
      interactive
      className={cn(
        "group cursor-pointer transition-all duration-200",
        selectedCampaign === campaign.id && "ring-2 ring-primary-500"
      )}
      onClick={() => setSelectedCampaign(campaign.id)}
    >
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <div className="flex-1 min-w-0">
            <CardTitle className="text-lg truncate">{campaign.name}</CardTitle>
            <p className="text-sm text-neutral-600 mt-1">{campaign.system}</p>
          </div>
          <div className="flex items-center gap-2 ml-4">
            <span className={cn(
              "inline-flex items-center gap-1 px-2 py-1 rounded-full text-xs font-medium border",
              getStatusColor(campaign.status)
            )}>
              {getStatusIcon(campaign.status)}
              {campaign.status}
            </span>
            <Button variant="ghost" size="icon" className="opacity-0 group-hover:opacity-100 transition-opacity">
              <MoreHorizontal className="h-4 w-4" />
            </Button>
          </div>
        </div>
      </CardHeader>

      <CardContent className="space-y-4">
        <p className="text-sm text-neutral-700 line-clamp-2">
          {campaign.description}
        </p>

        <div className="grid grid-cols-2 gap-4 text-sm">
          <div className="flex items-center gap-2">
            <Users className="h-4 w-4 text-neutral-500" />
            <span className="text-neutral-700">
              {campaign.players}/{campaign.maxPlayers} players
            </span>
          </div>
          <div className="flex items-center gap-2">
            <Gamepad2 className="h-4 w-4 text-neutral-500" />
            <span className="text-neutral-700">
              {campaign.sessions} sessions
            </span>
          </div>
          <div className="flex items-center gap-2">
            <Clock className="h-4 w-4 text-neutral-500" />
            <span className="text-neutral-700">
              {campaign.totalHours}h played
            </span>
          </div>
          <div className="flex items-center gap-2">
            <Calendar className="h-4 w-4 text-neutral-500" />
            <span className="text-neutral-700">
              {campaign.lastSession ? formatRelativeTime(campaign.lastSession) : 'No sessions yet'}
            </span>
          </div>
        </div>

        {campaign.nextSession && (
          <div className="p-3 bg-primary-50 rounded-lg border border-primary-200">
            <div className="flex items-center gap-2 text-sm">
              <Calendar className="h-4 w-4 text-primary-600" />
              <span className="text-primary-800 font-medium">
                Next: {formatDate(campaign.nextSession, { 
                  weekday: 'short',
                  month: 'short',
                  day: 'numeric',
                  hour: 'numeric',
                  minute: '2-digit'
                })}
              </span>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );

  const CampaignRow = ({_campaign}: { campaign: Campaign }) => (
    <div 
      key={campaign.id}
      className={cn(
        "group flex items-center gap-4 p-4 rounded-lg border border-neutral-200 hover:border-neutral-300 cursor-pointer transition-all",
        selectedCampaign === campaign.id && "border-primary-500 bg-primary-50"
      )}
      onClick={() => setSelectedCampaign(campaign.id)}
    >
      <div className="h-12 w-12 rounded-lg bg-neutral-200 flex items-center justify-center">
        <Gamepad2 className="h-6 w-6 text-neutral-600" />
      </div>

      <div className="flex-1 min-w-0">
        <div className="flex items-center justify-between mb-1">
          <h3 className="font-medium text-neutral-900 truncate">{campaign.name}</h3>
          <span className={cn(
            "inline-flex items-center gap-1 px-2 py-1 rounded-full text-xs font-medium border",
            getStatusColor(campaign.status)
          )}>
            {getStatusIcon(campaign.status)}
            {campaign.status}
          </span>
        </div>
        <div className="flex items-center gap-4 text-sm text-neutral-600">
          <span>{campaign.system}</span>
          <span>•</span>
          <span>{campaign.players}/{campaign.maxPlayers} players</span>
          <span>•</span>
          <span>{campaign.sessions} sessions</span>
          <span>•</span>
          <span>{campaign.totalHours}h played</span>
          {campaign.lastSession && (
            <>
              <span>•</span>
              <span>Last: {formatRelativeTime(campaign.lastSession)}</span>
            </>
          )}
        </div>
      </div>

      <Button 
        variant="ghost" 
        size="icon" 
        className="opacity-0 group-hover:opacity-100 transition-opacity"
      >
        <MoreHorizontal className="h-4 w-4" />
      </Button>
    </div>
  );

  return (
    <div className="space-y-6">
      {/* Header Actions */}
      <div className="flex flex-col sm:flex-row gap-4 justify-between">
        <div className="flex-1 max-w-md">
          <Input
            placeholder="Search campaigns..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            leftIcon={<Search className="h-4 w-4" />}
          />
        </div>

        <div className="flex items-center gap-2">
          {/* Status Filter */}
          <select
            value={filterStatus}
            onChange={(e) => setFilterStatus(e.target.value as FilterStatus)}
            className="px-3 py-2 border border-neutral-300 rounded-lg text-sm focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
          >
            <option value="all">All Status</option>
            <option value="active">Active</option>
            <option value="paused">Paused</option>
            <option value="planning">Planning</option>
            <option value="completed">Completed</option>
          </select>

          {/* View Toggle */}
          <div className="flex border border-neutral-300 rounded-lg p-1">
            <Button
              variant={viewMode === 'grid' ? 'primary' : 'ghost'}
              size="sm"
              onClick={() => setViewMode('grid')}
            >
              <Grid3X3 className="h-4 w-4" />
            </Button>
            <Button
              variant={viewMode === 'list' ? 'primary' : 'ghost'}
              size="sm"
              onClick={() => setViewMode('list')}
            >
              <List className="h-4 w-4" />
            </Button>
          </div>

          <Button variant="primary" leftIcon={<Plus className="h-4 w-4" />}>
            New Campaign
          </Button>
        </div>
      </div>

      {/* Campaigns Display */}
      {loading ? (
        <Card className="text-center py-12">
          <CardContent>
            <Loader2 className="h-12 w-12 text-primary-500 mx-auto mb-4 animate-spin" />
            <h3 className="text-lg font-medium text-neutral-900 mb-2">Loading campaigns...</h3>
            <p className="text-neutral-600">Please wait while we fetch your campaigns</p>
          </CardContent>
        </Card>
      ) : error ? (
        <Card className="text-center py-12 border-error-200 bg-error-50">
          <CardContent>
            <div className="h-12 w-12 rounded-full bg-error-100 flex items-center justify-center mx-auto mb-4">
              <span className="text-error-600 text-xl">⚠</span>
            </div>
            <h3 className="text-lg font-medium text-error-900 mb-2">Failed to load campaigns</h3>
            <p className="text-error-700 mb-6">{error}</p>
            <Button 
              variant="primary" 
              onClick={() => window.location.reload()}
            >
              Try Again
            </Button>
          </CardContent>
        </Card>
      ) : filteredCampaigns.length === 0 ? (
        <Card className="text-center py-12">
          <CardContent>
            <Gamepad2 className="h-12 w-12 text-neutral-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-neutral-900 mb-2">
              {searchQuery || filterStatus !== 'all' ? 'No campaigns found' : 'No campaigns yet'}
            </h3>
            <p className="text-neutral-600 mb-6">
              {searchQuery || filterStatus !== 'all' 
                ? 'Try adjusting your search or filters'
                : 'Create your first campaign to get started with your tabletop adventures'
              }
            </p>
            {!searchQuery && filterStatus === 'all' && (
              <Button variant="primary" leftIcon={<Plus className="h-4 w-4" />}>
                Create Your First Campaign
              </Button>
            )}
          </CardContent>
        </Card>
      ) : (
        <div className={cn(
          viewMode === 'grid' 
            ? 'grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6'
            : 'space-y-2'
        )}>
          {filteredCampaigns.map(campaign => 
            viewMode === 'grid' 
              ? <CampaignCard key={campaign.id} campaign={campaign} />
              : <CampaignRow key={campaign.id} campaign={campaign} />
          )}
        </div>
      )}

      {/* Results Summary */}
      {filteredCampaigns.length > 0 && (
        <div className="text-sm text-neutral-600 text-center">
          Showing {filteredCampaigns.length} of {campaigns.length} campaigns
        </div>
      )}
    </div>
  );
}

/**
 * Create Campaign Modal - Form for creating new campaigns with system selection
 */
import React, { useState } from 'react';
import { X, Upload, _Users, Globe, Lock, UserCheck } from 'lucide-react';
import { Button } from '../ui/Button';
import { Input } from '../ui/Input';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/Card';
import { cn, generateId } from '../../lib/utils';

interface CreateCampaignModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSuccess: (campaign: any) => void;
}

interface CampaignFormData {
  name: string;
  system: string;
  description: string;
  maxPlayers: number;
  visibility: 'public' | 'private' | 'friends';
  autoAccept: boolean;
  coverImage?: File;
}

const gameSystems = [
  { id: 'dnd5e', name: 'Dungeons & Dragons 5th Edition', popular: true },
  { id: 'pathfinder2e', name: 'Pathfinder 2nd Edition', popular: true },
  { id: 'cyberpunk-red', name: 'Cyberpunk Red', popular: false },
  { id: 'call-of-cthulhu', name: 'Call of Cthulhu 7th Edition', popular: true },
  { id: 'vampire-masquerade', name: 'Vampire: The Masquerade 5th Edition', popular: false },
  { id: 'savage-worlds', name: 'Savage Worlds Adventure Edition', popular: false },
  { id: 'fate-core', name: 'Fate Core System', popular: false },
  { id: 'pbta', name: 'Powered by the Apocalypse', popular: false },
  { id: 'custom', name: 'Custom System', popular: false },
];

export function CreateCampaignModal({ isOpen, _onClose, _onSuccess }: CreateCampaignModalProps) {
  const [formData, setFormData] = useState<CampaignFormData>({
    name: '',
    system: '',
    description: '',
    maxPlayers: 6,
    visibility: 'private',
    autoAccept: false,
  });

  const [errors, setErrors] = useState<Record<string, string>>({});
  const [loading, setLoading] = useState(false);
  const [step, setStep] = useState(1);

  const handleChange = (_field: keyof CampaignFormData, _value: any) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      if (file.size > 5 * 1024 * 1024) { // 5MB limit
        setErrors(prev => ({ ...prev, coverImage: 'Image must be less than 5MB' }));
        return;
      }
      if (!file.type.startsWith('image/')) {
        setErrors(prev => ({ ...prev, coverImage: 'Please select a valid image file' }));
        return;
      }
      handleChange('coverImage', file);
      setErrors(prev => ({ ...prev, coverImage: '' }));
    }
  };

  const validateStep1 = (): boolean => {
    const newErrors: Record<string, string> = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Campaign name is required';
    }

    if (!formData.system) {
      newErrors.system = 'Please select a game system';
    }

    if (!formData.description.trim()) {
      newErrors.description = 'Campaign description is required';
    } else if (formData.description.length < 20) {
      newErrors.description = 'Description should be at least 20 characters';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleNext = () => {
    if (validateStep1()) {
      setStep(2);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    setLoading(true);
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      const newCampaign = {
        id: generateId('campaign'),
        ...formData,
        status: 'planning' as const,
        players: 0,
        sessions: 0,
        totalHours: 0,
        createdAt: new Date(),
      };

      onSuccess(newCampaign);
      onClose();

      // Reset form
      setFormData({
        name: '',
        system: '',
        description: '',
        maxPlayers: 6,
        visibility: 'private',
        autoAccept: false,
      });
      setStep(1);
    } catch (_error) {
      setErrors({ general: 'Failed to create campaign. Please try again.' });
    } finally {
      setLoading(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/50">
      <Card className="w-full max-w-2xl max-h-[90vh] overflow-y-auto">
        <CardHeader className="flex flex-row items-center justify-between border-b">
          <CardTitle>Create New Campaign</CardTitle>
          <Button variant="ghost" size="icon" onClick={onClose}>
            <X className="h-5 w-5" />
          </Button>
        </CardHeader>

        <CardContent className="p-6">
          {/* Progress Steps */}
          <div className="flex items-center justify-center mb-8">
            <div className="flex items-center space-x-4">
              <div className={cn(
                "flex items-center justify-center w-8 h-8 rounded-full text-sm font-medium",
                step >= 1 ? "bg-primary-600 text-white" : "bg-neutral-200 text-neutral-600"
              )}>
                1
              </div>
              <div className={cn(
                "w-16 h-px",
                step > 1 ? "bg-primary-600" : "bg-neutral-200"
              )} />
              <div className={cn(
                "flex items-center justify-center w-8 h-8 rounded-full text-sm font-medium",
                step >= 2 ? "bg-primary-600 text-white" : "bg-neutral-200 text-neutral-600"
              )}>
                2
              </div>
            </div>
          </div>

          <form onSubmit={handleSubmit}>
            {step === 1 && (
              <div className="space-y-6">
                <h3 className="text-lg font-medium text-neutral-900">Campaign Details</h3>

                {/* Campaign Name */}
                <Input
                  label="Campaign Name"
                  placeholder="Enter a memorable campaign name"
                  value={formData.name}
                  onChange={(e) => handleChange('name', e.target.value)}
                  error={errors.name}
                  disabled={loading}
                />

                {/* Game System */}
                <div className="space-y-2">
                  <label className="block text-sm font-medium text-neutral-700">
                    Game System
                  </label>
                  <div className="grid grid-cols-1 sm:grid-cols-2 gap-2 max-h-48 overflow-y-auto border border-neutral-200 rounded-lg p-2">
                    {gameSystems.map((_system) => (
                      <label
                        key={system.id}
                        className={cn(
                          "flex items-center p-3 rounded-lg border-2 cursor-pointer transition-colors",
                          formData.system === system.id
                            ? "border-primary-500 bg-primary-50"
                            : "border-neutral-200 hover:border-neutral-300",
                          system.popular && "ring-1 ring-warning-200"
                        )}
                      >
                        <input
                          type="radio"
                          name="system"
                          value={system.id}
                          checked={formData.system === system.id}
                          onChange={(e) => handleChange('system', e.target.value)}
                          className="sr-only"
                        />
                        <div className="flex-1 min-w-0">
                          <div className="flex items-center justify-between">
                            <span className="text-sm font-medium text-neutral-900 truncate">
                              {system.name}
                            </span>
                            {system.popular && (
                              <span className="ml-2 px-2 py-1 text-xs bg-warning-100 text-warning-800 rounded-full">
                                Popular
                              </span>
                            )}
                          </div>
                        </div>
                      </label>
                    ))}
                  </div>
                  {errors.system && (
                    <p className="text-xs text-error-600">{errors.system}</p>
                  )}
                </div>

                {/* Description */}
                <div className="space-y-2">
                  <label className="block text-sm font-medium text-neutral-700">
                    Campaign Description
                  </label>
                  <textarea
                    placeholder="Describe your campaign setting, tone, and what players can expect..."
                    value={formData.description}
                    onChange={(e) => handleChange('description', e.target.value)}
                    rows={4}
                    className={cn(
                      "w-full px-3 py-2 border rounded-lg text-sm resize-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500",
                      errors.description ? "border-error-300" : "border-neutral-300"
                    )}
                    disabled={loading}
                  />
                  <div className="flex justify-between text-xs text-neutral-500">
                    <span>{formData.description.length}/500 characters</span>
                    {errors.description && (
                      <span className="text-error-600">{errors.description}</span>
                    )}
                  </div>
                </div>

                <div className="flex justify-end">
                  <Button type="button" onClick={handleNext} disabled={loading}>
                    Next Step
                  </Button>
                </div>
              </div>
            )}

            {step === 2 && (
              <div className="space-y-6">
                <h3 className="text-lg font-medium text-neutral-900">Campaign Settings</h3>

                {/* Max Players */}
                <div className="space-y-2">
                  <label className="block text-sm font-medium text-neutral-700">
                    Maximum Players
                  </label>
                  <div className="flex items-center space-x-4">
                    <input
                      type="range"
                      min="2"
                      max="8"
                      value={formData.maxPlayers}
                      onChange={(e) => handleChange('maxPlayers', parseInt(e.target.value))}
                      className="flex-1"
                    />
                    <div className="w-16 text-center">
                      <span className="text-lg font-medium text-primary-600">
                        {formData.maxPlayers}
                      </span>
                    </div>
                  </div>
                  <p className="text-xs text-neutral-500">
                    You can always adjust this later
                  </p>
                </div>

                {/* Visibility */}
                <div className="space-y-3">
                  <label className="block text-sm font-medium text-neutral-700">
                    Campaign Visibility
                  </label>
                  <div className="space-y-2">
                    {[
                      { value: 'private', icon: Lock, label: 'Private', desc: 'Only you can see and manage this campaign' },
                      { value: 'friends', icon: UserCheck, label: 'Friends Only', desc: 'Only your friends can find and join' },
                      { value: 'public', icon: Globe, label: 'Public', desc: 'Anyone can find and request to join' },
                    ].map((_option) => (
                      <label
                        key={option.value}
                        className={cn(
                          "flex items-center p-3 rounded-lg border-2 cursor-pointer transition-colors",
                          formData.visibility === option.value
                            ? "border-primary-500 bg-primary-50"
                            : "border-neutral-200 hover:border-neutral-300"
                        )}
                      >
                        <input
                          type="radio"
                          name="visibility"
                          value={option.value}
                          checked={formData.visibility === option.value}
                          onChange={(e) => handleChange('visibility', e.target.value)}
                          className="sr-only"
                        />
                        <option.icon className="h-5 w-5 text-neutral-600 mr-3" />
                        <div>
                          <div className="font-medium text-neutral-900">{option.label}</div>
                          <div className="text-sm text-neutral-600">{option.desc}</div>
                        </div>
                      </label>
                    ))}
                  </div>
                </div>

                {/* Auto Accept */}
                {formData.visibility !== 'private' && (
                  <div className="flex items-center justify-between p-4 bg-neutral-50 rounded-lg">
                    <div>
                      <h4 className="font-medium text-neutral-900">Auto-accept join requests</h4>
                      <p className="text-sm text-neutral-600">
                        Players can join immediately without your approval
                      </p>
                    </div>
                    <label className="relative inline-flex items-center cursor-pointer">
                      <input
                        type="checkbox"
                        checked={formData.autoAccept}
                        onChange={(e) => handleChange('autoAccept', e.target.checked)}
                        className="sr-only peer"
                      />
                      <div className="w-11 h-6 bg-neutral-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-primary-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-primary-600"></div>
                    </label>
                  </div>
                )}

                {/* Cover Image Upload */}
                <div className="space-y-2">
                  <label className="block text-sm font-medium text-neutral-700">
                    Cover Image (Optional)
                  </label>
                  <div className="flex items-center justify-center w-full">
                    <label className="flex flex-col items-center justify-center w-full h-32 border-2 border-neutral-300 border-dashed rounded-lg cursor-pointer bg-neutral-50 hover:bg-neutral-100">
                      <div className="flex flex-col items-center justify-center pt-5 pb-6">
                        <Upload className="w-8 h-8 mb-2 text-neutral-400" />
                        <p className="mb-2 text-sm text-neutral-500">
                          <span className="font-semibold">Click to upload</span> or drag and drop
                        </p>
                        <p className="text-xs text-neutral-500">PNG, JPG or GIF (MAX. 5MB)</p>
                      </div>
                      <input
                        type="file"
                        className="hidden"
                        accept="image/*"
                        onChange={handleImageUpload}
                      />
                    </label>
                  </div>
                  {errors.coverImage && (
                    <p className="text-xs text-error-600">{errors.coverImage}</p>
                  )}
                  {formData.coverImage && (
                    <p className="text-sm text-success-600">
                      ✓ {formData.coverImage.name} uploaded
                    </p>
                  )}
                </div>

                {errors.general && (
                  <div className="p-3 text-sm text-error-700 bg-error-50 border border-error-200 rounded-lg">
                    {errors.general}
                  </div>
                )}

                <div className="flex justify-between">
                  <Button type="button" variant="secondary" onClick={() => setStep(1)}>
                    Back
                  </Button>
                  <Button type="submit" loading={loading}>
                    Create Campaign
                  </Button>
                </div>
              </div>
            )}
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

/**
 * Ability Scores Component - Display and edit character ability scores and modifiers
 */

import React from 'react';
import { _Button } from '../ui/Button';
import { Input } from '../ui/Input';
import { cn } from '../../lib/utils';
import type { Character } from './CharacterSheet';

interface AbilityScoresProps {
  character: Character;
  isEditing: boolean;
  onUpdate: (_updates: Partial<Character>) => void;
}

const ABILITIES = [
  { key: 'strength', label: 'Strength', shortLabel: 'STR' },
  { key: 'dexterity', label: 'Dexterity', shortLabel: 'DEX' },
  { key: 'constitution', label: 'Constitution', shortLabel: 'CON' },
  { key: 'intelligence', label: 'Intelligence', shortLabel: 'INT' },
  { key: 'wisdom', label: 'Wisdom', shortLabel: 'WIS' },
  { key: 'charisma', label: 'Charisma', shortLabel: 'CHA' }
] as const;

export function AbilityScores({_ character, _isEditing, _onUpdate }: AbilityScoresProps) {
  const getModifier = (score: number): number => {
    return Math.floor((score - 10) / 2);
  };

  const formatModifier = (modifier: number): string => {
    return modifier >= 0 ? `+${modifier}` : `${modifier}`;
  };

  const updateAbilityScore = (_ability: keyof Character['abilities'], _value: number) => {
    const newAbilities = {
      ...character.abilities,
      [ability]: Math.max(1, Math.min(30, value))
    };

    // Recalculate saving throws
    const newSavingThrows = { ...character.savingThrows };
    const modifier = getModifier(value);
    const profBonus = character.proficiencyBonus;

    newSavingThrows[ability] = {
      ...newSavingThrows[ability],
      value: modifier + (newSavingThrows[ability].proficient ? profBonus : 0)
    };

    onUpdate({
      abilities: newAbilities,
      savingThrows: newSavingThrows
    });
  };

  const toggleSavingThrowProficiency = (_ability: keyof Character['savingThrows']) => {
    const currentProf = character.savingThrows[ability].proficient;
    const modifier = getModifier(character.abilities[ability]);
    const profBonus = character.proficiencyBonus;

    const newSavingThrows = {
      ...character.savingThrows,
      [ability]: {
        proficient: !currentProf,
        value: modifier + (!currentProf ? profBonus : 0)
      }
    };

    onUpdate({ savingThrows: newSavingThrows });
  };

  const updateCharacterField = (_field: keyof Character, _value: any) => {
    onUpdate({ [field]: value });
  };

  return (
    <div className="p-4 space-y-6">
      {/* Basic Character Info */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <div>
          <label className="block text-sm font-medium text-text-primary mb-1">Name</label>
          {isEditing ? (
            <Input
              value={character.name}
              onChange={(e) => updateCharacterField('name', e.target.value)}
              placeholder="Character name"
            />
          ) : (
            <div className="p-2 bg-bg-tertiary rounded border text-text-primary">
              {character.name}
            </div>
          )}
        </div>

        <div>
          <label className="block text-sm font-medium text-text-primary mb-1">Class</label>
          {isEditing ? (
            <Input
              value={character.class}
              onChange={(e) => updateCharacterField('class', e.target.value)}
              placeholder="Character class"
            />
          ) : (
            <div className="p-2 bg-bg-tertiary rounded border text-text-primary">
              {character.class}
            </div>
          )}
        </div>

        <div>
          <label className="block text-sm font-medium text-text-primary mb-1">Race</label>
          {isEditing ? (
            <Input
              value={character.race}
              onChange={(e) => updateCharacterField('race', e.target.value)}
              placeholder="Character race"
            />
          ) : (
            <div className="p-2 bg-bg-tertiary rounded border text-text-primary">
              {character.race}
            </div>
          )}
        </div>

        <div>
          <label className="block text-sm font-medium text-text-primary mb-1">Background</label>
          {isEditing ? (
            <Input
              value={character.background}
              onChange={(e) => updateCharacterField('background', e.target.value)}
              placeholder="Character background"
            />
          ) : (
            <div className="p-2 bg-bg-tertiary rounded border text-text-primary">
              {character.background}
            </div>
          )}
        </div>
      </div>

      {/* Level and Experience */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        <div>
          <label className="block text-sm font-medium text-text-primary mb-1">Level</label>
          {isEditing ? (
            <Input
              type="number"
              min="1"
              max="20"
              value={character.level}
              onChange={(e) => updateCharacterField('level', parseInt(e.target.value) || 1)}
            />
          ) : (
            <div className="p-2 bg-bg-tertiary rounded border text-text-primary text-center text-lg font-bold">
              {character.level}
            </div>
          )}
        </div>

        <div>
          <label className="block text-sm font-medium text-text-primary mb-1">Experience</label>
          {isEditing ? (
            <Input
              type="number"
              min="0"
              value={character.experience}
              onChange={(e) => updateCharacterField('experience', parseInt(e.target.value) || 0)}
            />
          ) : (
            <div className="p-2 bg-bg-tertiary rounded border text-text-primary">
              {character.experience.toLocaleString()}
            </div>
          )}
        </div>

        <div>
          <label className="block text-sm font-medium text-text-primary mb-1">Proficiency Bonus</label>
          <div className="p-2 bg-bg-tertiary rounded border text-text-primary text-center font-mono">
            {formatModifier(character.proficiencyBonus)}
          </div>
        </div>

        <div>
          <label className="block text-sm font-medium text-text-primary mb-1">Inspiration</label>
          <button
            onClick={() => updateCharacterField('inspiration', !character.inspiration)}
            disabled={!isEditing}
            className={cn(
              'w-full p-2 rounded border text-center font-medium transition-colors',
              character.inspiration
                ? 'bg-accent-primary text-white border-accent-primary'
                : 'bg-bg-tertiary text-text-secondary border-border-primary',
              isEditing && 'hover:opacity-80 cursor-pointer',
              !isEditing && 'cursor-default'
            )}
          >
            {character.inspiration ? 'Inspired' : 'No Inspiration'}
          </button>
        </div>
      </div>

      {/* Health and Defense */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        <div>
          <label className="block text-sm font-medium text-text-primary mb-1">Armor Class</label>
          {isEditing ? (
            <Input
              type="number"
              min="1"
              value={character.armorClass}
              onChange={(e) => updateCharacterField('armorClass', parseInt(e.target.value) || 10)}
            />
          ) : (
            <div className="p-2 bg-bg-tertiary rounded border text-text-primary text-center text-lg font-bold">
              {character.armorClass}
            </div>
          )}
        </div>

        <div>
          <label className="block text-sm font-medium text-text-primary mb-1">Hit Points</label>
          {isEditing ? (
            <div className="flex gap-1">
              <Input
                type="number"
                min="0"
                value={character.hitPoints}
                onChange={(e) => updateCharacterField('hitPoints', parseInt(e.target.value) || 0)}
                placeholder="Current"
              />
              <Input
                type="number"
                min="1"
                value={character.maxHitPoints}
                onChange={(e) => updateCharacterField('maxHitPoints', parseInt(e.target.value) || 1)}
                placeholder="Max"
              />
            </div>
          ) : (
            <div className="p-2 bg-bg-tertiary rounded border text-text-primary text-center">
              <span className="text-lg font-bold">{character.hitPoints}</span>
              <span className="text-text-secondary">/{character.maxHitPoints}</span>
            </div>
          )}
        </div>

        <div>
          <label className="block text-sm font-medium text-text-primary mb-1">Temp HP</label>
          {isEditing ? (
            <Input
              type="number"
              min="0"
              value={character.tempHitPoints}
              onChange={(e) => updateCharacterField('tempHitPoints', parseInt(e.target.value) || 0)}
            />
          ) : (
            <div className="p-2 bg-bg-tertiary rounded border text-text-primary text-center">
              {character.tempHitPoints}
            </div>
          )}
        </div>

        <div>
          <label className="block text-sm font-medium text-text-primary mb-1">Speed</label>
          {isEditing ? (
            <Input
              type="number"
              min="0"
              value={character.speed}
              onChange={(e) => updateCharacterField('speed', parseInt(e.target.value) || 30)}
            />
          ) : (
            <div className="p-2 bg-bg-tertiary rounded border text-text-primary text-center">
              {character.speed} ft
            </div>
          )}
        </div>
      </div>

      {/* Ability Scores */}
      <div>
        <h3 className="text-lg font-semibold text-text-primary mb-4">Ability Scores</h3>
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-6 gap-4">
          {ABILITIES.map(_({ key, _label, _shortLabel }) => {
            const score = character.abilities[key];
            const modifier = getModifier(score);

            return (
              <div key={key} className="bg-bg-tertiary rounded-lg border border-border-primary p-3">
                <div className="text-center">
                  <div className="text-xs font-medium text-text-secondary uppercase tracking-wide mb-1">
                    {shortLabel}
                  </div>

                  {isEditing ? (
                    <Input
                      type="number"
                      min="1"
                      max="30"
                      value={score}
                      onChange={(e) => updateAbilityScore(key, parseInt(e.target.value) || 1)}
                      className="text-center font-bold text-lg mb-2"
                    />
                  ) : (
                    <div className="text-lg font-bold text-text-primary mb-2">
                      {score}
                    </div>
                  )}

                  <div className="text-sm font-mono text-text-secondary">
                    {formatModifier(modifier)}
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>

      {/* Saving Throws */}
      <div>
        <h3 className="text-lg font-semibold text-text-primary mb-4">Saving Throws</h3>
        <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
          {ABILITIES.map(_({ key, _shortLabel}) => {
            const savingThrow = character.savingThrows[key];

            return (
              <div key={key} className="flex items-center gap-3 p-2 bg-bg-tertiary rounded border">
                <button
                  onClick={() => toggleSavingThrowProficiency(key)}
                  disabled={!isEditing}
                  className={cn(
                    'w-4 h-4 rounded border-2 flex items-center justify-center',
                    savingThrow.proficient
                      ? 'bg-accent-primary border-accent-primary'
                      : 'border-border-primary',
                    isEditing && 'hover:opacity-80 cursor-pointer',
                    !isEditing && 'cursor-default'
                  )}
                >
                  {savingThrow.proficient && (
                    <div className="w-2 h-2 bg-white rounded-full" />
                  )}
                </button>

                <div className="flex-1 flex justify-between items-center">
                  <span className="text-sm text-text-primary">{shortLabel}</span>
                  <span className="font-mono text-sm text-text-secondary">
                    {formatModifier(savingThrow.value)}
                  </span>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

/**
 * Character Sheet Component - Main character management interface
 */

import React, { useState, useEffect } from 'react';
import { useAuth } from '../../providers/AuthProvider';
import { useWebSocket } from '../../providers/WebSocketProvider';
import { AbilityScores } from './AbilityScores';
import { SkillsPanel } from './SkillsPanel';
import { EquipmentPanel } from './EquipmentPanel';
import { SpellsPanel } from './SpellsPanel';
import { NotesPanel } from './NotesPanel';
import { Button } from '../ui/Button';
import { LoadingSpinner } from '../ui/LoadingSpinner';
import { 
  User, 
  Shield, 
  Sword, 
  Book, 
  Backpack, 
  Scroll,
  FileText,
  Save,
  Download,
  Upload,
  Settings
} from 'lucide-react';
import { cn } from '../../lib/utils';

export interface Character {
  id: string;
  userId: string;
  name: string;
  class: string;
  race: string;
  background: string;
  level: number;
  experience: number;
  hitPoints: number;
  maxHitPoints: number;
  tempHitPoints: number;
  armorClass: number;
  speed: number;
  proficiencyBonus: number;
  inspiration: boolean;
  abilities: {
    strength: number;
    dexterity: number;
    constitution: number;
    intelligence: number;
    wisdom: number;
    charisma: number;
  };
  savingThrows: {
    strength: { proficient: boolean; value: number };
    dexterity: { proficient: boolean; value: number };
    constitution: { proficient: boolean; value: number };
    intelligence: { proficient: boolean; value: number };
    wisdom: { proficient: boolean; value: number };
    charisma: { proficient: boolean; value: number };
  };
  skills: Record<string, { proficient: boolean; expertise: boolean; value: number }>;
  equipment: Equipment[];
  spells: Spell[];
  features: Feature[];
  notes: string;
  avatar?: string;
  createdAt: string;
  updatedAt: string;
}

export interface Equipment {
  id: string;
  name: string;
  type: 'weapon' | 'armor' | 'tool' | 'consumable' | 'treasure' | 'other';
  quantity: number;
  weight: number;
  value: number;
  description: string;
  equipped: boolean;
  properties: string[];
}

export interface Spell {
  id: string;
  name: string;
  level: number;
  school: string;
  castingTime: string;
  range: string;
  components: string[];
  duration: string;
  description: string;
  prepared: boolean;
  known: boolean;
}

export interface Feature {
  id: string;
  name: string;
  source: string;
  description: string;
  type: 'class' | 'race' | 'background' | 'feat' | 'other';
  uses?: {
    current: number;
    max: number;
    resetOn: 'short' | 'long' | 'other';
  };
}

interface CharacterSheetProps {
  characterId?: string | undefined;
  className?: string;
  onCharacterUpdate?: (character: Character) => void;
}

type TabType = 'stats' | 'skills' | 'equipment' | 'spells' | 'notes';

export function CharacterSheet({ characterId, _className, _onCharacterUpdate }: CharacterSheetProps) {
  const { user  } = useAuth();
  const { send  } = useWebSocket();
  const [character, setCharacter] = useState<Character | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isEditing, setIsEditing] = useState(false);
  const [activeTab, setActiveTab] = useState<TabType>('stats');
  const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);

  // Load character data
  useEffect(() => {
    const loadCharacter = async () => {
      if (!characterId) {
        // Create new character
        const newCharacter: Character = createDefaultCharacter();
        setCharacter(newCharacter);
        setIsEditing(true);
        setIsLoading(false);
        return;
      }

      setIsLoading(true);
      try {
        // Mock character data for now
        await new Promise(resolve => setTimeout(resolve, 1000));
        const mockCharacter = createMockCharacter(characterId);
        setCharacter(mockCharacter);
      } catch (error) {
        console.error('Failed to load character:', error);
      } finally {
        setIsLoading(false);
      }
    };

    loadCharacter();
  }, [characterId]);

  const createDefaultCharacter = (): Character => {
    return {
      id: `char_${Date.now()}`,
      userId: user?.id || '',
      name: 'New Character',
      class: 'Fighter',
      race: 'Human',
      background: 'Folk Hero',
      level: 1,
      experience: 0,
      hitPoints: 10,
      maxHitPoints: 10,
      tempHitPoints: 0,
      armorClass: 10,
      speed: 30,
      proficiencyBonus: 2,
      inspiration: false,
      abilities: {
        strength: 15,
        dexterity: 14,
        constitution: 13,
        intelligence: 12,
        wisdom: 10,
        charisma: 8
      },
      savingThrows: {
        strength: { proficient: true, value: 4 },
        dexterity: { proficient: false, value: 2 },
        constitution: { proficient: true, value: 3 },
        intelligence: { proficient: false, value: 1 },
        wisdom: { proficient: false, value: 0 },
        charisma: { proficient: false, value: -1 }
      },
      skills: {
        'Animal Handling': { proficient: true, expertise: false, value: 2 },
        'Athletics': { proficient: true, expertise: false, value: 4 },
        'Intimidation': { proficient: false, expertise: false, value: -1 },
        'Perception': { proficient: false, expertise: false, value: 0 }
      },
      equipment: [],
      spells: [],
      features: [],
      notes: '',
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString()
    };
  };

  const _createMockCharacter = (id: string): Character => {
    const base = createDefaultCharacter();
    return {
      ...base,
      id,
      name: 'Thorin Ironbeard',
      class: 'Fighter',
      race: 'Dwarf',
      background: 'Soldier',
      level: 3,
      experience: 900,
      hitPoints: 28,
      maxHitPoints: 28,
      armorClass: 16,
      equipment: [
        {
          id: 'eq1',
          name: 'Longsword',
          type: 'weapon',
          quantity: 1,
          weight: 3,
          value: 15,
          description: 'A versatile martial weapon',
          equipped: true,
          properties: ['Versatile (1d10)']
        },
        {
          id: 'eq2',
          name: 'Chain Mail',
          type: 'armor',
          quantity: 1,
          weight: 55,
          value: 75,
          description: 'Heavy armor made of interlocking metal rings',
          equipped: true,
          properties: ['AC 16', 'Disadvantage on Stealth']
        }
      ],
      features: [
        {
          id: 'f1',
          name: 'Second Wind',
          source: 'Fighter Class',
          description: 'Regain hit points equal to 1d10 + fighter level',
          type: 'class',
          uses: { current: 1, max: 1, resetOn: 'short' }
        },
        {
          id: 'f2',
          name: 'Action Surge',
          source: 'Fighter Class',
          description: 'Take an additional action on your turn',
          type: 'class',
          uses: { current: 1, max: 1, resetOn: 'short' }
        }
      ]
    };
  };

  const updateCharacter = (_updates: Partial<Character>) => {
    if (!character) return;

    const updatedCharacter = {
      ...character,
      ...updates,
      updatedAt: new Date().toISOString()
    };

    setCharacter(updatedCharacter);
    setHasUnsavedChanges(true);
    onCharacterUpdate?.(updatedCharacter);
  };

  const saveCharacter = async () => {
    if (!character) return;

    try {
      // Send character data to server
      send({
        type: 'UPDATE_CHARACTER',
        character
      });

      setHasUnsavedChanges(false);
      setIsEditing(false);
    } catch (error) {
      console.error('Failed to save character:', error);
    }
  };

  const exportCharacter = () => {
    if (!character) return;

    const dataStr = JSON.stringify(character, null, 2);
    const dataBlob = new Blob([dataStr], { type: 'application/json' });
    const url = URL.createObjectURL(dataBlob);

    const link = document.createElement('a');
    link.href = url;
    link.download = `${character.name.replace(/\s+/g, '')}_character.json`;
    link.click();

    URL.revokeObjectURL(url);
  };

  const renderTabContent = () => {
    if (!character) return null;

    switch (activeTab) {
      case 'stats':
        return (
          <AbilityScores
            character={character}
            isEditing={isEditing}
            onUpdate={updateCharacter}
          />
        );
      case 'skills':
        return (
          <SkillsPanel
            character={character}
            isEditing={isEditing}
            onUpdate={updateCharacter}
          />
        );
      case 'equipment':
        return (
          <EquipmentPanel
            character={character}
            isEditing={isEditing}
            onUpdate={updateCharacter}
          />
        );
      case 'spells':
        return (
          <SpellsPanel
            character={character}
            isEditing={isEditing}
            onUpdate={updateCharacter}
          />
        );
      case 'notes':
        return (
          <NotesPanel
            character={character}
            isEditing={isEditing}
            onUpdate={updateCharacter}
          />
        );
      default:
        return null;
    }
  };

  if (isLoading) {
    return (
      <div className={cn('flex items-center justify-center p-8', className)}>
        <LoadingSpinner size="lg" showLabel label="Loading character..." />
      </div>
    );
  }

  if (!character) {
    return (
      <div className={cn('flex items-center justify-center p-8', className)}>
        <div className="text-center">
          <User className="h-12 w-12 text-text-tertiary mx-auto mb-4" />
          <h3 className="text-lg font-medium text-text-primary mb-2">
            Character Not Found
          </h3>
          <p className="text-text-secondary">
            The requested character could not be loaded.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className={cn('bg-bg-secondary rounded-lg border border-border-primary flex flex-col', className)}>
      {/* Character Header */}
      <div className="p-4 border-b border-border-primary">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-4">
            <div className="w-12 h-12 rounded-full bg-accent-primary flex items-center justify-center text-white font-bold text-lg">
              {character.avatar ? (
                <img 
                  src={character.avatar} 
                  alt={character.name}
                  className="w-full h-full rounded-full object-cover"
                />
              ) : (
                character.name.charAt(0).toUpperCase()
              )}
            </div>

            <div>
              <h2 className="text-xl font-bold text-text-primary">
                {character.name}
              </h2>
              <p className="text-text-secondary">
                Level {character.level} {character.race} {character.class}
              </p>
              <p className="text-xs text-text-tertiary">
                {character.background} • {character.experience} XP
              </p>
            </div>
          </div>

          <div className="flex items-center gap-2">
            {hasUnsavedChanges && (
              <span className="text-warning text-sm">Unsaved changes</span>
            )}

            <Button
              variant="ghost"
              size="sm"
              onClick={exportCharacter}
              leftIcon={<Download className="h-4 w-4" />}
            >
              Export
            </Button>

            {isEditing ? (
              <Button
                variant="primary"
                size="sm"
                onClick={saveCharacter}
                leftIcon={<Save className="h-4 w-4" />}
              >
                Save
              </Button>
            ) : (
              <Button
                variant="secondary"
                size="sm"
                onClick={() => setIsEditing(true)}
                leftIcon={<Settings className="h-4 w-4" />}
              >
                Edit
              </Button>
            )}
          </div>
        </div>
      </div>

      {/* Tab Navigation */}
      <div className="border-b border-border-primary">
        <div className="flex">
          {[
            { key: 'stats', label: 'Stats', icon: Shield },
            { key: 'skills', label: 'Skills', icon: Sword },
            { key: 'equipment', label: 'Equipment', icon: Backpack },
            { key: 'spells', label: 'Spells', icon: Book },
            { key: 'notes', label: 'Notes', icon: FileText }
          ].map(_({ key, _label, _icon: Icon }) => (
            <button
              key={key}
              onClick={() => setActiveTab(key as TabType)}
              className={cn(
                'flex items-center gap-2 px-4 py-3 text-sm font-medium transition-colors border-b-2',
                activeTab === key
                  ? 'text-accent-primary border-accent-primary bg-accent-light'
                  : 'text-text-secondary border-transparent hover:text-text-primary hover:bg-bg-tertiary'
              )}
            >
              <Icon className="h-4 w-4" />
              {label}
            </button>
          ))}
        </div>
      </div>

      {/* Tab Content */}
      <div className="flex-1 overflow-hidden">
        {renderTabContent()}
      </div>
    </div>
  );
}

/**
 * Equipment Panel Component - Manage character equipment and inventory
 */

import React, { useState } from 'react';
import { Button } from '../ui/Button';
import { Input } from '../ui/Input';
import { cn } from '../../lib/utils';
import { 
  Plus, 
  Trash2, 
  Edit3, 
  Package, 
  Sword, 
  Shield, 
  Wrench, 
  Gem,
  Search,
  Weight,
  Coins,
  Star
} from 'lucide-react';
import type { Character, Equipment } from './CharacterSheet';

interface EquipmentPanelProps {
  character: Character;
  isEditing: boolean;
  onUpdate: (_updates: Partial<Character>) => void;
}

interface EquipmentFormData {
  name: string;
  type: Equipment['type'];
  quantity: number;
  weight: number;
  value: number;
  description: string;
  properties: string[];
}

const EQUIPMENT_TYPES = [
  { key: 'weapon', label: 'Weapon', icon: Sword },
  { key: 'armor', label: 'Armor', icon: Shield },
  { key: 'tool', label: 'Tool', icon: Wrench },
  { key: 'consumable', label: 'Consumable', icon: Package },
  { key: 'treasure', label: 'Treasure', icon: Gem },
  { key: 'other', label: 'Other', icon: Package }
] as const;

export function EquipmentPanel({_ character, _isEditing, _onUpdate }: EquipmentPanelProps) {
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState<Equipment['type'] | 'all'>('all');
  const [showEquippedOnly, setShowEquippedOnly] = useState(false);
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingItem, setEditingItem] = useState<Equipment | null>(null);
  const [formData, setFormData] = useState<EquipmentFormData>({
    name: '',
    type: 'other',
    quantity: 1,
    weight: 0,
    value: 0,
    description: '',
    properties: []
  });

  const resetForm = () => {
    setFormData({
      name: '',
      type: 'other',
      quantity: 1,
      weight: 0,
      value: 0,
      description: '',
      properties: []
    });
    setEditingItem(null);
    setShowAddForm(false);
  };

  const addOrUpdateEquipment = () => {
    if (!formData.name.trim()) return;

    const newItem: Equipment = {
      id: editingItem?.id || `eq_${Date.now()}`,
      name: formData.name.trim(),
      type: formData.type,
      quantity: Math.max(1, formData.quantity),
      weight: Math.max(0, formData.weight),
      value: Math.max(0, formData.value),
      description: formData.description.trim(),
      equipped: editingItem?.equipped || false,
      properties: formData.properties.filter(p => p.trim())
    };

    let newEquipment;
    if (editingItem) {
      newEquipment = character.equipment.map(item => 
        item.id === editingItem.id ? newItem : item
      );
    } else {
      newEquipment = [...character.equipment, newItem];
    }

    onUpdate({ equipment: newEquipment });
    resetForm();
  };

  const removeEquipment = (_id: string) => {
    const newEquipment = character.equipment.filter(item => item.id !== id);
    onUpdate({ equipment: newEquipment });
  };

  const toggleEquipped = (_id: string) => {
    const newEquipment = character.equipment.map(item =>
      item.id === id ? { ...item, equipped: !item.equipped } : item
    );
    onUpdate({ equipment: newEquipment });
  };

  const startEdit = (item: Equipment) => {
    setFormData({
      name: item.name,
      type: item.type,
      quantity: item.quantity,
      weight: item.weight,
      value: item.value,
      description: item.description,
      properties: [...item.properties]
    });
    setEditingItem(item);
    setShowAddForm(true);
  };

  const filteredEquipment = character.equipment.filter(item => {
    if (searchTerm && !item.name.toLowerCase().includes(searchTerm.toLowerCase())) {
      return false;
    }
    if (filterType !== 'all' && item.type !== filterType) {
      return false;
    }
    if (showEquippedOnly && !item.equipped) {
      return false;
    }
    return true;
  });

  const totalWeight = character.equipment.reduce((_sum, _item) => sum + (item.weight * item.quantity), 0);
  const totalValue = character.equipment.reduce((_sum, _item) => sum + (item.value * item.quantity), 0);
  const equippedItems = character.equipment.filter(item => item.equipped);

  const getTypeIcon = (_type: Equipment['type']) => {
    const typeData = EQUIPMENT_TYPES.find(t => t.key === type);
    return typeData?.icon || Package;
  };

  const addProperty = () => {
    setFormData(prev => ({ ...prev, properties: [...prev.properties, ''] }));
  };

  const updateProperty = (_index: number, _value: string) => {
    setFormData(prev => ({
      ...prev,
      properties: prev.properties.map((prop, _i) => i === index ? value : prop)
    }));
  };

  const removeProperty = (_index: number) => {
    setFormData(prev => ({
      ...prev,
      properties: prev.properties.filter((_, _i) => i !== index)
    }));
  };

  return (
    <div className="p-4 space-y-4">
      {/* Equipment Summary */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        <div className="bg-bg-tertiary rounded-lg border border-border-primary p-3 text-center">
          <Package className="h-6 w-6 text-accent-primary mx-auto mb-1" />
          <div className="text-lg font-bold text-text-primary">{character.equipment.length}</div>
          <div className="text-xs text-text-secondary">Total Items</div>
        </div>

        <div className="bg-bg-tertiary rounded-lg border border-border-primary p-3 text-center">
          <Star className="h-6 w-6 text-warning mx-auto mb-1" />
          <div className="text-lg font-bold text-text-primary">{equippedItems.length}</div>
          <div className="text-xs text-text-secondary">Equipped</div>
        </div>

        <div className="bg-bg-tertiary rounded-lg border border-border-primary p-3 text-center">
          <Weight className="h-6 w-6 text-text-tertiary mx-auto mb-1" />
          <div className="text-lg font-bold text-text-primary">{totalWeight}</div>
          <div className="text-xs text-text-secondary">lbs Total</div>
        </div>

        <div className="bg-bg-tertiary rounded-lg border border-border-primary p-3 text-center">
          <Coins className="h-6 w-6 text-warning mx-auto mb-1" />
          <div className="text-lg font-bold text-text-primary">{totalValue}</div>
          <div className="text-xs text-text-secondary">gp Value</div>
        </div>
      </div>

      {/* Controls */}
      <div className="flex flex-col sm:flex-row gap-3">
        <div className="flex-1 relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-text-tertiary" />
          <Input
            placeholder="Search equipment..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-9"
          />
        </div>

        <div className="flex gap-2">
          <select
            value={filterType}
            onChange={(e) => setFilterType(e.target.value as Equipment['type'] | 'all')}
            className="px-3 py-2 bg-bg-secondary border border-border-primary rounded-md text-text-primary text-sm"
          >
            <option value="all">All Types</option>
            {EQUIPMENT_TYPES.map(_({ key, _label}) => (
              <option key={key} value={key}>{label}</option>
            ))}
          </select>

          <Button
            variant={showEquippedOnly ? 'primary' : 'ghost'}
            size="sm"
            onClick={() => setShowEquippedOnly(!showEquippedOnly)}
            leftIcon={<Star className="h-4 w-4" />}
          >
            Equipped
          </Button>

          {isEditing && (
            <Button
              variant="primary"
              size="sm"
              onClick={() => setShowAddForm(true)}
              leftIcon={<Plus className="h-4 w-4" />}
            >
              Add Item
            </Button>
          )}
        </div>
      </div>

      {/* Add/Edit Form */}
      {showAddForm && isEditing && (
        <div className="bg-bg-tertiary rounded-lg border border-border-primary p-4 space-y-4">
          <div className="flex items-center justify-between">
            <h3 className="text-lg font-semibold text-text-primary">
              {editingItem ? 'Edit Equipment' : 'Add Equipment'}
            </h3>
            <Button variant="ghost" size="sm" onClick={resetForm}>
              Cancel
            </Button>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-text-primary mb-1">Name</label>
              <Input
                value={formData.name}
                onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                placeholder="Equipment name"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-text-primary mb-1">Type</label>
              <select
                value={formData.type}
                onChange={(e) => setFormData(prev => ({ ...prev, type: e.target.value as Equipment['type'] }))}
                className="w-full px-3 py-2 bg-bg-secondary border border-border-primary rounded-md text-text-primary"
              >
                {EQUIPMENT_TYPES.map(_({ key, _label}) => (
                  <option key={key} value={key}>{label}</option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-text-primary mb-1">Quantity</label>
              <Input
                type="number"
                min="1"
                value={formData.quantity}
                onChange={(e) => setFormData(prev => ({ ...prev, quantity: parseInt(e.target.value) || 1 }))}
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-text-primary mb-1">Weight (lbs)</label>
              <Input
                type="number"
                min="0"
                step="0.1"
                value={formData.weight}
                onChange={(e) => setFormData(prev => ({ ...prev, weight: parseFloat(e.target.value) || 0 }))}
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-text-primary mb-1">Value (gp)</label>
              <Input
                type="number"
                min="0"
                step="0.01"
                value={formData.value}
                onChange={(e) => setFormData(prev => ({ ...prev, value: parseFloat(e.target.value) || 0 }))}
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-text-primary mb-1">Description</label>
            <textarea
              value={formData.description}
              onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
              className="w-full px-3 py-2 bg-bg-secondary border border-border-primary rounded-md text-text-primary resize-none"
              rows={3}
              placeholder="Equipment description..."
            />
          </div>

          <div>
            <div className="flex items-center justify-between mb-2">
              <label className="text-sm font-medium text-text-primary">Properties</label>
              <Button variant="ghost" size="sm" onClick={addProperty}>
                <Plus className="h-4 w-4" />
              </Button>
            </div>
            <div className="space-y-2">
              {formData.properties.map((_property, __index) => (
                <div key={index} className="flex gap-2">
                  <Input
                    value={property}
                    onChange={(e) => updateProperty(index, e.target.value)}
                    placeholder="Property name"
                  />
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => removeProperty(index)}
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              ))}
            </div>
          </div>

          <div className="flex gap-2">
            <Button variant="primary" onClick={addOrUpdateEquipment}>
              {editingItem ? 'Update' : 'Add'} Equipment
            </Button>
            <Button variant="ghost" onClick={resetForm}>
              Cancel
            </Button>
          </div>
        </div>
      )}

      {/* Equipment List */}
      <div className="space-y-2">
        {filteredEquipment.map((item) => {
          const TypeIcon = getTypeIcon(item.type);

          return (
            <div
              key={item.id}
              className={cn(
                'bg-bg-tertiary rounded-lg border border-border-primary p-3 transition-colors',
                item.equipped && 'bg-accent-light border-accent-primary'
              )}
            >
              <div className="flex items-start justify-between">
                <div className="flex items-start gap-3 flex-1">
                  <div className="flex flex-col items-center gap-1">
                    <TypeIcon className="h-5 w-5 text-accent-primary" />
                    {isEditing && (
                      <button
                        onClick={() => toggleEquipped(item.id)}
                        className={cn(
                          'w-4 h-4 rounded border transition-colors',
                          item.equipped
                            ? 'bg-accent-primary border-accent-primary'
                            : 'border-border-primary hover:border-accent-primary'
                        )}
                      >
                        {item.equipped && (
                          <Star className="h-3 w-3 text-white fill-current" />
                        )}
                      </button>
                    )}
                  </div>

                  <div className="flex-1">
                    <div className="flex items-center gap-2">
                      <h4 className="font-medium text-text-primary">{item.name}</h4>
                      {item.equipped && (
                        <span className="text-xs bg-accent-primary text-white px-2 py-1 rounded">
                          Equipped
                        </span>
                      )}
                      <span className="text-xs bg-bg-primary text-text-tertiary px-2 py-1 rounded capitalize">
                        {item.type}
                      </span>
                    </div>

                    {item.description && (
                      <p className="text-sm text-text-secondary mt-1">{item.description}</p>
                    )}

                    {item.properties.length > 0 && (
                      <div className="flex flex-wrap gap-1 mt-2">
                        {item.properties.map((property, _index) => (
                          <span
                            key={index}
                            className="text-xs bg-accent-secondary text-accent-primary px-2 py-1 rounded"
                          >
                            {property}
                          </span>
                        ))}
                      </div>
                    )}

                    <div className="flex items-center gap-4 mt-2 text-sm text-text-secondary">
                      <span>Qty: {item.quantity}</span>
                      <span>Weight: {item.weight} lbs</span>
                      <span>Value: {item.value} gp</span>
                    </div>
                  </div>
                </div>

                {isEditing && (
                  <div className="flex gap-1">
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => startEdit(item)}
                    >
                      <Edit3 className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => removeEquipment(item.id)}
                      className="text-danger hover:text-danger"
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                )}
              </div>
            </div>
          );
        })}
      </div>

      {filteredEquipment.length === 0 && (
        <div className="text-center py-8">
          <Package className="h-12 w-12 text-text-tertiary mx-auto mb-4" />
          <h3 className="text-lg font-medium text-text-primary mb-2">
            No Equipment Found
          </h3>
          <p className="text-text-secondary">
            {searchTerm 
              ? `No equipment matches "${searchTerm}"`
              : showEquippedOnly
              ? 'No equipped items found'
              : isEditing
              ? 'Add some equipment to get started'
              : 'This character has no equipment'
            }
          </p>
          {isEditing && !searchTerm && (
            <Button
              variant="primary"
              size="sm"
              onClick={() => setShowAddForm(true)}
              leftIcon={<Plus className="h-4 w-4" />}
              className="mt-4"
            >
              Add Equipment
            </Button>
          )}
        </div>
      )}
    </div>
  );
}

/**
 * Notes Panel Component - Manage character notes and features
 */

import React, { useState } from 'react';
import { Button } from '../ui/Button';
import { Input } from '../ui/Input';
import { cn } from '../../lib/utils';
import { 
  Plus, 
  Trash2, 
  Edit3, 
  FileText, 
  Bookmark,
  User,
  Shield,
  Zap,
  Star,
  Search,
  Save,
  X
} from 'lucide-react';
import type { Character, Feature } from './CharacterSheet';

interface NotesPanelProps {
  character: Character;
  isEditing: boolean;
  onUpdate: (_updates: Partial<Character>) => void;
}

interface FeatureFormData {
  name: string;
  source: string;
  description: string;
  type: Feature['type'];
  uses?: {
    current: number;
    max: number;
    resetOn: 'short' | 'long' | 'other';
  } | undefined;
}

const FEATURE_TYPES = [
  { key: 'class', label: 'Class Feature', icon: Shield },
  { key: 'race', label: 'Racial Trait', icon: User },
  { key: 'background', label: 'Background Feature', icon: Bookmark },
  { key: 'feat', label: 'Feat', icon: Star },
  { key: 'other', label: 'Other', icon: Zap }
] as const;

export function NotesPanel({_ character, _isEditing, _onUpdate }: NotesPanelProps) {
  const [activeTab, setActiveTab] = useState<'notes' | 'features'>('notes');
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState<Feature['type'] | 'all'>('all');
  const [showAddFeature, setShowAddFeature] = useState(false);
  const [editingFeature, setEditingFeature] = useState<Feature | null>(null);
  const [featureForm, setFeatureForm] = useState<FeatureFormData>({
    name: '',
    source: '',
    description: '',
    type: 'other'
  });

  const updateNotes = (notes: string) => {
    onUpdate({ notes });
  };

  const resetFeatureForm = () => {
    setFeatureForm({
      name: '',
      source: '',
      description: '',
      type: 'other'
    });
    setEditingFeature(null);
    setShowAddFeature(false);
  };

  const addOrUpdateFeature = () => {
    if (!featureForm.name.trim()) return;

    const newFeature: Feature = {
      id: editingFeature?.id || `feature_${Date.now()}`,
      name: featureForm.name.trim(),
      source: featureForm.source.trim(),
      description: featureForm.description.trim(),
      type: featureForm.type,
      ...(featureForm.uses && { uses: featureForm.uses })
    };

    let newFeatures;
    if (editingFeature) {
      newFeatures = character.features.map(feature => 
        feature.id === editingFeature.id ? newFeature : feature
      );
    } else {
      newFeatures = [...character.features, newFeature];
    }

    onUpdate({ features: newFeatures });
    resetFeatureForm();
  };

  const removeFeature = (_id: string) => {
    const newFeatures = character.features.filter(feature => feature.id !== id);
    onUpdate({ features: newFeatures });
  };

  const startEditFeature = (feature: Feature) => {
    setFeatureForm({
      name: feature.name,
      source: feature.source,
      description: feature.description,
      type: feature.type,
      uses: feature.uses ? { ...feature.uses } : undefined
    } as FeatureFormData);
    setEditingFeature(feature);
    setShowAddFeature(true);
  };

  const updateFeatureUses = (_featureId: string, _current: number) => {
    const newFeatures = character.features.map(feature => {
      if (feature.id === featureId && feature.uses) {
        return {
          ...feature,
          uses: {
            ...feature.uses,
            current: Math.max(0, Math.min(feature.uses.max, current))
          }
        };
      }
      return feature;
    });
    onUpdate({ features: newFeatures });
  };

  const toggleUsesTracking = () => {
    if (featureForm.uses) {
      setFeatureForm(prev => ({ ...prev, uses: undefined }));
    } else {
      setFeatureForm(prev => ({
        ...prev,
        uses: { current: 1, max: 1, resetOn: 'short' }
      }));
    }
  };

  const filteredFeatures = character.features.filter(feature => {
    if (searchTerm && !feature.name.toLowerCase().includes(searchTerm.toLowerCase())) {
      return false;
    }
    if (filterType !== 'all' && feature.type !== filterType) {
      return false;
    }
    return true;
  });

  const getTypeIcon = (_type: Feature['type']) => {
    const typeData = FEATURE_TYPES.find(t => t.key === type);
    return typeData?.icon || Zap;
  };

  const getTypeLabel = (_type: Feature['type']) => {
    const typeData = FEATURE_TYPES.find(t => t.key === type);
    return typeData?.label || 'Other';
  };

  return (
    <div className="p-4 space-y-4">
      {/* Tab Navigation */}
      <div className="flex border-b border-border-primary">
        <button
          onClick={() => setActiveTab('notes')}
          className={cn(
            'flex items-center gap-2 px-4 py-2 text-sm font-medium transition-colors border-b-2',
            activeTab === 'notes'
              ? 'text-accent-primary border-accent-primary'
              : 'text-text-secondary border-transparent hover:text-text-primary'
          )}
        >
          <FileText className="h-4 w-4" />
          Notes
        </button>
        <button
          onClick={() => setActiveTab('features')}
          className={cn(
            'flex items-center gap-2 px-4 py-2 text-sm font-medium transition-colors border-b-2',
            activeTab === 'features'
              ? 'text-accent-primary border-accent-primary'
              : 'text-text-secondary border-transparent hover:text-text-primary'
          )}
        >
          <Star className="h-4 w-4" />
          Features ({character.features.length})
        </button>
      </div>

      {/* Notes Tab */}
      {activeTab === 'notes' && (
        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <h3 className="text-lg font-semibold text-text-primary">Character Notes</h3>
            {!isEditing && character.notes && (
              <span className="text-sm text-text-secondary">
                {character.notes.length} characters
              </span>
            )}
          </div>

          {isEditing ? (
            <div className="space-y-3">
              <textarea
                value={character.notes}
                onChange={(e) => updateNotes(e.target.value)}
                className="w-full h-96 px-3 py-2 bg-bg-secondary border border-border-primary rounded-md text-text-primary resize-none focus:ring-2 focus:ring-accent-primary focus:border-transparent"
                placeholder="Write your character notes here... Include backstory, personality traits, goals, secrets, or anything else important about your character."
              />
              <div className="text-xs text-text-tertiary">
                Use this space for character backstory, personality traits, goals, relationships, and any other notes.
              </div>
            </div>
          ) : (
            <div className="bg-bg-tertiary rounded-lg border border-border-primary p-4">
              {character.notes ? (
                <div className="whitespace-pre-wrap text-text-primary leading-relaxed">
                  {character.notes}
                </div>
              ) : (
                <div className="text-center py-8">
                  <FileText className="h-12 w-12 text-text-tertiary mx-auto mb-4" />
                  <h4 className="text-lg font-medium text-text-primary mb-2">
                    No Notes Yet
                  </h4>
                  <p className="text-text-secondary">
                    Character notes will appear here when added.
                  </p>
                </div>
              )}
            </div>
          )}
        </div>
      )}

      {/* Features Tab */}
      {activeTab === 'features' && (
        <div className="space-y-4">
          {/* Features Controls */}
          <div className="flex flex-col sm:flex-row gap-3">
            <div className="flex-1 relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-text-tertiary" />
              <Input
                placeholder="Search features..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-9"
              />
            </div>

            <div className="flex gap-2">
              <select
                value={filterType}
                onChange={(e) => setFilterType(e.target.value as Feature['type'] | 'all')}
                className="px-3 py-2 bg-bg-secondary border border-border-primary rounded-md text-text-primary text-sm"
              >
                <option value="all">All Types</option>
                {FEATURE_TYPES.map(_({ key, _label}) => (
                  <option key={key} value={key}>{label}</option>
                ))}
              </select>

              {isEditing && (
                <Button
                  variant="primary"
                  size="sm"
                  onClick={() => setShowAddFeature(true)}
                  leftIcon={<Plus className="h-4 w-4" />}
                >
                  Add Feature
                </Button>
              )}
            </div>
          </div>

          {/* Add/Edit Feature Form */}
          {showAddFeature && isEditing && (
            <div className="bg-bg-tertiary rounded-lg border border-border-primary p-4 space-y-4">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold text-text-primary">
                  {editingFeature ? 'Edit Feature' : 'Add Feature'}
                </h3>
                <Button variant="ghost" size="sm" onClick={resetFeatureForm}>
                  <X className="h-4 w-4" />
                </Button>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-text-primary mb-1">Name</label>
                  <Input
                    value={featureForm.name}
                    onChange={(e) => setFeatureForm(prev => ({ ...prev, name: e.target.value }))}
                    placeholder="Feature name"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-text-primary mb-1">Source</label>
                  <Input
                    value={featureForm.source}
                    onChange={(e) => setFeatureForm(prev => ({ ...prev, source: e.target.value }))}
                    placeholder="e.g., Fighter Class, Human Race"
                  />
                </div>

                <div className="md:col-span-2">
                  <label className="block text-sm font-medium text-text-primary mb-1">Type</label>
                  <select
                    value={featureForm.type}
                    onChange={(e) => setFeatureForm(prev => ({ ...prev, type: e.target.value as Feature['type'] }))}
                    className="w-full px-3 py-2 bg-bg-secondary border border-border-primary rounded-md text-text-primary"
                  >
                    {FEATURE_TYPES.map(_({ key, _label}) => (
                      <option key={key} value={key}>{label}</option>
                    ))}
                  </select>
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-text-primary mb-1">Description</label>
                <textarea
                  value={featureForm.description}
                  onChange={(e) => setFeatureForm(prev => ({ ...prev, description: e.target.value }))}
                  className="w-full px-3 py-2 bg-bg-secondary border border-border-primary rounded-md text-text-primary resize-none"
                  rows={4}
                  placeholder="Feature description..."
                />
              </div>

              {/* Uses Tracking */}
              <div>
                <div className="flex items-center gap-2 mb-2">
                  <input
                    type="checkbox"
                    id="hasUses"
                    checked={!!featureForm.uses}
                    onChange={toggleUsesTracking}
                    className="rounded border-border-primary"
                  />
                  <label htmlFor="hasUses" className="text-sm font-medium text-text-primary">
                    Track uses per rest
                  </label>
                </div>

                {featureForm.uses && (
                  <div className="grid grid-cols-3 gap-2">
                    <div>
                      <label className="block text-xs text-text-secondary mb-1">Current</label>
                      <Input
                        type="number"
                        min="0"
                        value={featureForm.uses.current}
                        onChange={(e) => setFeatureForm(prev => ({
                          ...prev,
                          uses: prev.uses ? {
                            ...prev.uses,
                            current: parseInt(e.target.value) || 0
                          } : undefined
                        }))}
                      />
                    </div>
                    <div>
                      <label className="block text-xs text-text-secondary mb-1">Max</label>
                      <Input
                        type="number"
                        min="1"
                        value={featureForm.uses.max}
                        onChange={(e) => setFeatureForm(prev => ({
                          ...prev,
                          uses: prev.uses ? {
                            ...prev.uses,
                            max: parseInt(e.target.value) || 1
                          } : undefined
                        }))}
                      />
                    </div>
                    <div>
                      <label className="block text-xs text-text-secondary mb-1">Reset On</label>
                      <select
                        value={featureForm.uses.resetOn}
                        onChange={(e) => setFeatureForm(prev => ({
                          ...prev,
                          uses: prev.uses ? {
                            ...prev.uses,
                            resetOn: e.target.value as 'short' | 'long' | 'other'
                          } : undefined
                        }))}
                        className="w-full px-2 py-1 bg-bg-secondary border border-border-primary rounded text-sm"
                      >
                        <option value="short">Short Rest</option>
                        <option value="long">Long Rest</option>
                        <option value="other">Other</option>
                      </select>
                    </div>
                  </div>
                )}
              </div>

              <div className="flex gap-2">
                <Button variant="primary" onClick={addOrUpdateFeature}>
                  {editingFeature ? 'Update' : 'Add'} Feature
                </Button>
                <Button variant="ghost" onClick={resetFeatureForm}>
                  Cancel
                </Button>
              </div>
            </div>
          )}

          {/* Features List */}
          <div className="space-y-2">
            {filteredFeatures.map((feature) => {
              const TypeIcon = getTypeIcon(feature.type);

              return (
                <div
                  key={feature.id}
                  className="bg-bg-tertiary rounded-lg border border-border-primary p-3"
                >
                  <div className="flex items-start justify-between">
                    <div className="flex items-start gap-3 flex-1">
                      <TypeIcon className="h-5 w-5 text-accent-primary mt-1" />

                      <div className="flex-1">
                        <div className="flex items-center gap-2 flex-wrap">
                          <h4 className="font-medium text-text-primary">{feature.name}</h4>
                          <span className="text-xs bg-bg-primary text-text-tertiary px-2 py-1 rounded">
                            {getTypeLabel(feature.type)}
                          </span>
                          {feature.source && (
                            <span className="text-xs text-text-secondary">
                              • {feature.source}
                            </span>
                          )}
                        </div>

                        {feature.description && (
                          <p className="text-sm text-text-secondary mt-1 whitespace-pre-wrap">
                            {feature.description}
                          </p>
                        )}

                        {feature.uses && (
                          <div className="flex items-center gap-2 mt-2">
                            <div className="flex items-center gap-1">
                              {isEditing ? (
                                <>
                                  <Button
                                    variant="ghost"
                                    size="sm"
                                    onClick={() => updateFeatureUses(feature.id, feature.uses!.current - 1)}
                                    disabled={feature.uses.current <= 0}
                                  >
                                    -
                                  </Button>
                                  <span className="mx-2 font-mono">
                                    {feature.uses.current}/{feature.uses.max}
                                  </span>
                                  <Button
                                    variant="ghost"
                                    size="sm"
                                    onClick={() => updateFeatureUses(feature.id, feature.uses!.current + 1)}
                                    disabled={feature.uses.current >= feature.uses.max}
                                  >
                                    +
                                  </Button>
                                </>
                              ) : (
                                <span className="font-mono text-sm">
                                  {feature.uses.current}/{feature.uses.max} uses
                                </span>
                              )}
                            </div>
                            <span className="text-xs text-text-tertiary">
                              • Resets on {feature.uses.resetOn} rest
                            </span>
                          </div>
                        )}
                      </div>
                    </div>

                    {isEditing && (
                      <div className="flex gap-1">
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => startEditFeature(feature)}
                        >
                          <Edit3 className="h-4 w-4" />
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => removeFeature(feature.id)}
                          className="text-danger hover:text-danger"
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </div>
                    )}
                  </div>
                </div>
              );
            })}
          </div>

          {filteredFeatures.length === 0 && (
            <div className="text-center py-8">
              <Star className="h-12 w-12 text-text-tertiary mx-auto mb-4" />
              <h3 className="text-lg font-medium text-text-primary mb-2">
                No Features Found
              </h3>
              <p className="text-text-secondary">
                {searchTerm 
                  ? `No features match "${searchTerm}"`
                  : isEditing
                  ? 'Add character features and abilities'
                  : 'This character has no features'
                }
              </p>
              {isEditing && !searchTerm && (
                <Button
                  variant="primary"
                  size="sm"
                  onClick={() => setShowAddFeature(true)}
                  leftIcon={<Plus className="h-4 w-4" />}
                  className="mt-4"
                >
                  Add Feature
                </Button>
              )}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

/**
 * Skills Panel Component - Display and manage character skills and proficiencies
 */

import React, { useState } from 'react';
import { Button } from '../ui/Button';
import { Input } from '../ui/Input';
import { cn } from '../../lib/utils';
import { Search, Star, Circle } from 'lucide-react';
import type { Character } from './CharacterSheet';

interface SkillsPanelProps {
  character: Character;
  isEditing: boolean;
  onUpdate: (_updates: Partial<Character>) => void;
}

// D&D 5e Skills with their associated ability scores
const SKILLS_DATA = {
  'Acrobatics': { ability: 'dexterity', description: 'Stay on your feet in difficult situations' },
  'Animal Handling': { ability: 'wisdom', description: 'Calm down a domesticated animal' },
  'Arcana': { ability: 'intelligence', description: 'Recall lore about spells, magic items, symbols' },
  'Athletics': { ability: 'strength', description: 'Climb, jump, or swim in difficult circumstances' },
  'Deception': { ability: 'charisma', description: 'Hide the truth convincingly' },
  'History': { ability: 'intelligence', description: 'Recall lore about historical events' },
  'Insight': { ability: 'wisdom', description: 'Determine true intentions of a creature' },
  'Intimidation': { ability: 'charisma', description: 'Influence someone through threats' },
  'Investigation': { ability: 'intelligence', description: 'Look around for clues and make deductions' },
  'Medicine': { ability: 'wisdom', description: 'Stabilize dying companion or diagnose illness' },
  'Nature': { ability: 'intelligence', description: 'Recall lore about terrain, plants, animals' },
  'Perception': { ability: 'wisdom', description: 'Notice something with one of your senses' },
  'Performance': { ability: 'charisma', description: 'Delight an audience with music, dance, or acting' },
  'Persuasion': { ability: 'charisma', description: 'Influence someone in good faith' },
  'Religion': { ability: 'intelligence', description: 'Recall lore about deities, rites, prayers' },
  'Sleight of Hand': { ability: 'dexterity', description: 'Pick a pocket or perform a trick' },
  'Stealth': { ability: 'dexterity', description: 'Conceal yourself from enemies' },
  'Survival': { ability: 'wisdom', description: 'Follow tracks, hunt, guide others, avoid hazards' }
} as const;

export function SkillsPanel({ character, _isEditing, _onUpdate }: SkillsPanelProps) {
  const [searchTerm, setSearchTerm] = useState('');
  const [filterBy, setFilterBy] = useState<'all' | 'proficient' | 'expertise'>('all');

  const getModifier = (score: number): number => {
    return Math.floor((score - 10) / 2);
  };

  const formatModifier = (modifier: number): string => {
    return modifier >= 0 ? `+${modifier}` : `${modifier}`;
  };

  const getSkillModifier = (skillName: string): number => {
    const skillData = SKILLS_DATA[skillName as keyof typeof SKILLS_DATA];
    if (!skillData) return 0;

    const abilityScore = character.abilities[skillData.ability as keyof Character['abilities']];
    const baseModifier = getModifier(abilityScore);
    const skill = character.skills[skillName];

    if (!skill) return baseModifier;

    let modifier = baseModifier;
    if (skill.proficient) {
      modifier += character.proficiencyBonus;
    }
    if (skill.expertise) {
      modifier += character.proficiencyBonus; // Expertise doubles proficiency bonus
    }

    return modifier;
  };

  const toggleSkillProficiency = (skillName: string) => {
    const currentSkill = character.skills[skillName] || { proficient: false, expertise: false, value: 0 };
    const newSkills = {
      ...character.skills,
      [skillName]: {
        ...currentSkill,
        proficient: !currentSkill.proficient,
        expertise: currentSkill.proficient ? false : currentSkill.expertise, // Remove expertise if removing proficiency
        value: getSkillModifier(skillName)
      }
    };

    onUpdate({ skills: newSkills });
  };

  const toggleSkillExpertise = (skillName: string) => {
    const currentSkill = character.skills[skillName] || { proficient: false, expertise: false, value: 0 };
    if (!currentSkill.proficient) return; // Can't have expertise without proficiency

    const newSkills = {
      ...character.skills,
      [skillName]: {
        ...currentSkill,
        expertise: !currentSkill.expertise,
        value: getSkillModifier(skillName)
      }
    };

    onUpdate({ skills: newSkills });
  };

  const filteredSkills = Object.entries(SKILLS_DATA).filter(_([skillName]) => {
    const skill = character.skills[skillName];

    // Filter by search term
    if (searchTerm && !skillName.toLowerCase().includes(searchTerm.toLowerCase())) {
      return false;
    }

    // Filter by proficiency status
    if (filterBy === 'proficient' && (!skill || !skill.proficient)) {
      return false;
    }
    if (filterBy === 'expertise' && (!skill || !skill.expertise)) {
      return false;
    }

    return true;
  });

  const getAbilityShortName = (ability: string): string => {
    const map: Record<string, string> = {
      strength: 'STR',
      dexterity: 'DEX',
      constitution: 'CON',
      intelligence: 'INT',
      wisdom: 'WIS',
      charisma: 'CHA'
    };
    return map[ability] || ability.toUpperCase();
  };

  return (
    <div className="p-4 space-y-4">
      {/* Search and Filter Controls */}
      <div className="flex flex-col sm:flex-row gap-3">
        <div className="flex-1 relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-text-tertiary" />
          <Input
            placeholder="Search skills..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-9"
          />
        </div>

        <div className="flex gap-2">
          {[
            { key: 'all', label: 'All Skills' },
            { key: 'proficient', label: 'Proficient' },
            { key: 'expertise', label: 'Expertise' }
          ].map(_({ key, _label}) => (
            <Button
              key={key}
              variant={filterBy === key ? 'primary' : 'ghost'}
              size="sm"
              onClick={() => setFilterBy(key as any)}
            >
              {label}
            </Button>
          ))}
        </div>
      </div>

      {/* Skills Legend */}
      <div className="bg-bg-tertiary rounded-lg p-3 border border-border-primary">
        <div className="flex items-center gap-4 text-sm text-text-secondary">
          <div className="flex items-center gap-2">
            <Circle className="h-4 w-4" />
            <span>Not Proficient</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-4 h-4 rounded-full bg-accent-primary" />
            <span>Proficient</span>
          </div>
          <div className="flex items-center gap-2">
            <Star className="h-4 w-4 fill-current text-warning" />
            <span>Expertise</span>
          </div>
        </div>
      </div>

      {/* Skills List */}
      <div className="space-y-2">
        {filteredSkills.map([skillName, _skillData] => {
          const skill = character.skills[skillName] || { proficient: false, expertise: false, value: 0 };
          const modifier = getSkillModifier(skillName);
          const abilityName = getAbilityShortName(skillData.ability);

          return (
            <div
              key={skillName}
              className="bg-bg-tertiary rounded-lg border border-border-primary p-3 hover:bg-bg-secondary transition-colors"
            >
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-3 flex-1">
                  {/* Proficiency/Expertise Indicators */}
                  <div className="flex items-center gap-1">
                    <button
                      onClick={() => toggleSkillProficiency(skillName)}
                      disabled={!isEditing}
                      className={cn(
                        'w-5 h-5 rounded-full border-2 flex items-center justify-center transition-colors',
                        skill.proficient
                          ? 'bg-accent-primary border-accent-primary'
                          : 'border-border-primary hover:border-accent-primary',
                        isEditing && 'cursor-pointer',
                        !isEditing && 'cursor-default'
                      )}
                    >
                      {skill.proficient && (
                        <div className="w-2 h-2 bg-white rounded-full" />
                      )}
                    </button>

                    <button
                      onClick={() => toggleSkillExpertise(skillName)}
                      disabled={!isEditing || !skill.proficient}
                      className={cn(
                        'w-5 h-5 transition-colors',
                        skill.expertise
                          ? 'text-warning'
                          : 'text-text-tertiary hover:text-warning',
                        (!isEditing || !skill.proficient) && 'cursor-default opacity-50',
                        isEditing && skill.proficient && 'cursor-pointer'
                      )}
                    >
                      <Star className={cn('h-4 w-4', skill.expertise && 'fill-current')} />
                    </button>
                  </div>

                  {/* Skill Info */}
                  <div className="flex-1">
                    <div className="flex items-center gap-2">
                      <h4 className="font-medium text-text-primary">{skillName}</h4>
                      <span className="text-xs text-text-tertiary bg-bg-primary px-2 py-1 rounded">
                        {abilityName}
                      </span>
                    </div>
                    <p className="text-xs text-text-secondary mt-1">{skillData.description}</p>
                  </div>

                  {/* Modifier */}
                  <div className="text-right">
                    <div className="font-mono text-lg font-bold text-text-primary">
                      {formatModifier(modifier)}
                    </div>
                    {skill.proficient && (
                      <div className="text-xs text-accent-primary">
                        {skill.expertise ? 'Expertise' : 'Proficient'}
                      </div>
                    )}
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {filteredSkills.length === 0 && (
        <div className="text-center py-8">
          <Circle className="h-12 w-12 text-text-tertiary mx-auto mb-4" />
          <h3 className="text-lg font-medium text-text-primary mb-2">
            No Skills Found
          </h3>
          <p className="text-text-secondary">
            {searchTerm 
              ? `No skills match "${searchTerm}"`
              : filterBy === 'proficient'
              ? 'No proficient skills found'
              : 'No skills with expertise found'
            }
          </p>
        </div>
      )}

      {/* Skills Summary */}
      <div className="bg-bg-tertiary rounded-lg border border-border-primary p-4 mt-6">
        <h4 className="font-medium text-text-primary mb-3">Skills Summary</h4>
        <div className="grid grid-cols-3 gap-4 text-center">
          <div>
            <div className="text-2xl font-bold text-text-primary">
              {Object.values(character.skills).filter(skill => skill.proficient).length}
            </div>
            <div className="text-sm text-text-secondary">Proficient</div>
          </div>
          <div>
            <div className="text-2xl font-bold text-warning">
              {Object.values(character.skills).filter(skill => skill.expertise).length}
            </div>
            <div className="text-sm text-text-secondary">Expertise</div>
          </div>
          <div>
            <div className="text-2xl font-bold text-text-primary">
              {Object.keys(SKILLS_DATA).length}
            </div>
            <div className="text-sm text-text-secondary">Total Skills</div>
          </div>
        </div>
      </div>
    </div>
  );
}

/**
 * Spells Panel Component - Manage character spells and spell casting
 */

import React, { useState } from 'react';
import { Button } from '../ui/Button';
import { Input } from '../ui/Input';
import { cn } from '../../lib/utils';
import { 
  Plus, 
  Trash2, 
  Edit3, 
  Book, 
  Star,
  Search,
  Filter,
  Zap,
  Clock,
  Target,
  Eye,
  EyeOff
} from 'lucide-react';
import type { Character, Spell } from './CharacterSheet';

interface SpellsPanelProps {
  character: Character;
  isEditing: boolean;
  onUpdate: (_updates: Partial<Character>) => void;
}

interface SpellFormData {
  name: string;
  level: number;
  school: string;
  castingTime: string;
  range: string;
  components: string[];
  duration: string;
  description: string;
  prepared: boolean;
  known: boolean;
}

const SPELL_SCHOOLS = [
  'Abjuration',
  'Conjuration', 
  'Divination',
  'Enchantment',
  'Evocation',
  'Illusion',
  'Necromancy',
  'Transmutation'
];

const SPELL_COMPONENTS = ['V', 'S', 'M'];

const SPELL_LEVELS = [
  { value: 0, label: 'Cantrip' },
  { value: 1, label: '1st Level' },
  { value: 2, label: '2nd Level' },
  { value: 3, label: '3rd Level' },
  { value: 4, label: '4th Level' },
  { value: 5, label: '5th Level' },
  { value: 6, label: '6th Level' },
  { value: 7, label: '7th Level' },
  { value: 8, label: '8th Level' },
  { value: 9, label: '9th Level' }
];

export function SpellsPanel({_ character, _isEditing, _onUpdate }: SpellsPanelProps) {
  const [searchTerm, setSearchTerm] = useState('');
  const [filterLevel, setFilterLevel] = useState<number | 'all'>('all');
  const [filterSchool, setFilterSchool] = useState<string | 'all'>('all');
  const [showPreparedOnly, setShowPreparedOnly] = useState(false);
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingSpell, setEditingSpell] = useState<Spell | null>(null);
  const [formData, setFormData] = useState<SpellFormData>({
    name: '',
    level: 0,
    school: 'Evocation',
    castingTime: '1 action',
    range: '60 feet',
    components: ['V', 'S'],
    duration: 'Instantaneous',
    description: '',
    prepared: false,
    known: true
  });

  const resetForm = () => {
    setFormData({
      name: '',
      level: 0,
      school: 'Evocation',
      castingTime: '1 action',
      range: '60 feet',
      components: ['V', 'S'],
      duration: 'Instantaneous',
      description: '',
      prepared: false,
      known: true
    });
    setEditingSpell(null);
    setShowAddForm(false);
  };

  const addOrUpdateSpell = () => {
    if (!formData.name.trim()) return;

    const newSpell: Spell = {
      id: editingSpell?.id || `spell_${Date.now()}`,
      name: formData.name.trim(),
      level: formData.level,
      school: formData.school,
      castingTime: formData.castingTime,
      range: formData.range,
      components: [...formData.components],
      duration: formData.duration,
      description: formData.description.trim(),
      prepared: formData.prepared,
      known: formData.known
    };

    let newSpells;
    if (editingSpell) {
      newSpells = character.spells.map(spell => 
        spell.id === editingSpell.id ? newSpell : spell
      );
    } else {
      newSpells = [...character.spells, newSpell];
    }

    onUpdate({ spells: newSpells });
    resetForm();
  };

  const removeSpell = (_id: string) => {
    const newSpells = character.spells.filter(spell => spell.id !== id);
    onUpdate({ spells: newSpells });
  };

  const togglePrepared = (_id: string) => {
    const newSpells = character.spells.map(spell =>
      spell.id === id ? { ...spell, prepared: !spell.prepared } : spell
    );
    onUpdate({ spells: newSpells });
  };

  const toggleKnown = (_id: string) => {
    const newSpells = character.spells.map(spell =>
      spell.id === id ? { ...spell, known: !spell.known } : spell
    );
    onUpdate({ spells: newSpells });
  };

  const startEdit = (spell: Spell) => {
    setFormData({
      name: spell.name,
      level: spell.level,
      school: spell.school,
      castingTime: spell.castingTime,
      range: spell.range,
      components: [...spell.components],
      duration: spell.duration,
      description: spell.description,
      prepared: spell.prepared,
      known: spell.known
    });
    setEditingSpell(spell);
    setShowAddForm(true);
  };

  const toggleComponent = (_component: string) => {
    setFormData(prev => ({
      ...prev,
      components: prev.components.includes(component)
        ? prev.components.filter(c => c !== component)
        : [...prev.components, component]
    }));
  };

  const filteredSpells = character.spells.filter(spell => {
    if (searchTerm && !spell.name.toLowerCase().includes(searchTerm.toLowerCase())) {
      return false;
    }
    if (filterLevel !== 'all' && spell.level !== filterLevel) {
      return false;
    }
    if (filterSchool !== 'all' && spell.school !== filterSchool) {
      return false;
    }
    if (showPreparedOnly && !spell.prepared) {
      return false;
    }
    return true;
  });

  const spellsByLevel = SPELL_LEVELS.reduce(_(acc, _{ value }) => {
    acc[value] = filteredSpells.filter(spell => spell.level === value);
    return acc;
  }, {} as Record<number, Spell[]>);

  const preparedSpells = character.spells.filter(spell => spell.prepared);
  const knownSpells = character.spells.filter(spell => spell.known);

  const _getSpellLevelLabel = (level: number): string => {
    return level === 0 ? 'Cantrip' : `Level ${level}`;
  };

  return (
    <div className="p-4 space-y-4">
      {/* Spells Summary */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        <div className="bg-bg-tertiary rounded-lg border border-border-primary p-3 text-center">
          <Book className="h-6 w-6 text-accent-primary mx-auto mb-1" />
          <div className="text-lg font-bold text-text-primary">{knownSpells.length}</div>
          <div className="text-xs text-text-secondary">Known Spells</div>
        </div>

        <div className="bg-bg-tertiary rounded-lg border border-border-primary p-3 text-center">
          <Star className="h-6 w-6 text-warning mx-auto mb-1" />
          <div className="text-lg font-bold text-text-primary">{preparedSpells.length}</div>
          <div className="text-xs text-text-secondary">Prepared</div>
        </div>

        <div className="bg-bg-tertiary rounded-lg border border-border-primary p-3 text-center">
          <Zap className="h-6 w-6 text-text-tertiary mx-auto mb-1" />
          <div className="text-lg font-bold text-text-primary">
            {preparedSpells.filter(s => s.level === 0).length}
          </div>
          <div className="text-xs text-text-secondary">Cantrips</div>
        </div>

        <div className="bg-bg-tertiary rounded-lg border border-border-primary p-3 text-center">
          <Filter className="h-6 w-6 text-text-tertiary mx-auto mb-1" />
          <div className="text-lg font-bold text-text-primary">
            {new Set(character.spells.map(s => s.school)).size}
          </div>
          <div className="text-xs text-text-secondary">Schools</div>
        </div>
      </div>

      {/* Controls */}
      <div className="flex flex-col sm:flex-row gap-3">
        <div className="flex-1 relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-text-tertiary" />
          <Input
            placeholder="Search spells..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-9"
          />
        </div>

        <div className="flex gap-2">
          <select
            value={filterLevel}
            onChange={(e) => setFilterLevel(e.target.value === 'all' ? 'all' : parseInt(e.target.value))}
            className="px-3 py-2 bg-bg-secondary border border-border-primary rounded-md text-text-primary text-sm"
          >
            <option value="all">All Levels</option>
            {SPELL_LEVELS.map(_({ value, _label}) => (
              <option key={value} value={value}>{label}</option>
            ))}
          </select>

          <select
            value={filterSchool}
            onChange={(e) => setFilterSchool(e.target.value)}
            className="px-3 py-2 bg-bg-secondary border border-border-primary rounded-md text-text-primary text-sm"
          >
            <option value="all">All Schools</option>
            {SPELL_SCHOOLS.map(school => (
              <option key={school} value={school}>{school}</option>
            ))}
          </select>

          <Button
            variant={showPreparedOnly ? 'primary' : 'ghost'}
            size="sm"
            onClick={() => setShowPreparedOnly(!showPreparedOnly)}
            leftIcon={<Star className="h-4 w-4" />}
          >
            Prepared
          </Button>

          {isEditing && (
            <Button
              variant="primary"
              size="sm"
              onClick={() => setShowAddForm(true)}
              leftIcon={<Plus className="h-4 w-4" />}
            >
              Add Spell
            </Button>
          )}
        </div>
      </div>

      {/* Add/Edit Form */}
      {showAddForm && isEditing && (
        <div className="bg-bg-tertiary rounded-lg border border-border-primary p-4 space-y-4">
          <div className="flex items-center justify-between">
            <h3 className="text-lg font-semibold text-text-primary">
              {editingSpell ? 'Edit Spell' : 'Add Spell'}
            </h3>
            <Button variant="ghost" size="sm" onClick={resetForm}>
              Cancel
            </Button>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-text-primary mb-1">Name</label>
              <Input
                value={formData.name}
                onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                placeholder="Spell name"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-text-primary mb-1">Level</label>
              <select
                value={formData.level}
                onChange={(e) => setFormData(prev => ({ ...prev, level: parseInt(e.target.value) }))}
                className="w-full px-3 py-2 bg-bg-secondary border border-border-primary rounded-md text-text-primary"
              >
                {SPELL_LEVELS.map(_({ value, _label}) => (
                  <option key={value} value={value}>{label}</option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-text-primary mb-1">School</label>
              <select
                value={formData.school}
                onChange={(e) => setFormData(prev => ({ ...prev, school: e.target.value }))}
                className="w-full px-3 py-2 bg-bg-secondary border border-border-primary rounded-md text-text-primary"
              >
                {SPELL_SCHOOLS.map(school => (
                  <option key={school} value={school}>{school}</option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-text-primary mb-1">Casting Time</label>
              <Input
                value={formData.castingTime}
                onChange={(e) => setFormData(prev => ({ ...prev, castingTime: e.target.value }))}
                placeholder="1 action"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-text-primary mb-1">Range</label>
              <Input
                value={formData.range}
                onChange={(e) => setFormData(prev => ({ ...prev, range: e.target.value }))}
                placeholder="60 feet"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-text-primary mb-1">Duration</label>
              <Input
                value={formData.duration}
                onChange={(e) => setFormData(prev => ({ ...prev, duration: e.target.value }))}
                placeholder="Instantaneous"
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-text-primary mb-2">Components</label>
            <div className="flex gap-2">
              {SPELL_COMPONENTS.map(_component => (
                <button
                  key={component}
                  onClick={() => toggleComponent(component)}
                  className={cn(
                    'px-3 py-2 rounded border text-sm font-medium transition-colors',
                    formData.components.includes(component)
                      ? 'bg-accent-primary text-white border-accent-primary'
                      : 'bg-bg-secondary text-text-primary border-border-primary hover:border-accent-primary'
                  )}
                >
                  {component}
                </button>
              ))}
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-text-primary mb-1">Description</label>
            <textarea
              value={formData.description}
              onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
              className="w-full px-3 py-2 bg-bg-secondary border border-border-primary rounded-md text-text-primary resize-none"
              rows={4}
              placeholder="Spell description..."
            />
          </div>

          <div className="flex gap-4">
            <label className="flex items-center gap-2">
              <input
                type="checkbox"
                checked={formData.known}
                onChange={(e) => setFormData(prev => ({ ...prev, known: e.target.checked }))}
                className="rounded border-border-primary"
              />
              <span className="text-sm text-text-primary">Known</span>
            </label>
            <label className="flex items-center gap-2">
              <input
                type="checkbox"
                checked={formData.prepared}
                onChange={(e) => setFormData(prev => ({ ...prev, prepared: e.target.checked }))}
                className="rounded border-border-primary"
              />
              <span className="text-sm text-text-primary">Prepared</span>
            </label>
          </div>

          <div className="flex gap-2">
            <Button variant="primary" onClick={addOrUpdateSpell}>
              {editingSpell ? 'Update' : 'Add'} Spell
            </Button>
            <Button variant="ghost" onClick={resetForm}>
              Cancel
            </Button>
          </div>
        </div>
      )}

      {/* Spells List by Level */}
      <div className="space-y-4">
        {SPELL_LEVELS.map(_({ value: level, _label}) => {
          const levelSpells = spellsByLevel[level] || [];
          if (levelSpells.length === 0) return null;

          return (
            <div key={level} className="space-y-2">
              <h3 className="text-lg font-semibold text-text-primary border-b border-border-primary pb-2">
                {label} ({levelSpells.length})
              </h3>

              <div className="space-y-2">
                {levelSpells.map((spell) => (
                  <div
                    key={spell.id}
                    className={cn(
                      'bg-bg-tertiary rounded-lg border border-border-primary p-3 transition-colors',
                      spell.prepared && 'bg-accent-light border-accent-primary'
                    )}
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex items-start gap-3 flex-1">
                        <div className="flex flex-col items-center gap-1">
                          <Book className="h-5 w-5 text-accent-primary" />
                          {isEditing && (
                            <div className="flex gap-1">
                              <button
                                onClick={() => toggleKnown(spell.id)}
                                className={cn(
                                  'w-4 h-4 transition-colors',
                                  spell.known ? 'text-accent-primary' : 'text-text-tertiary'
                                )}
                                title={spell.known ? 'Known' : 'Unknown'}
                              >
                                {spell.known ? <Eye className="h-4 w-4" /> : <EyeOff className="h-4 w-4" />}
                              </button>
                              <button
                                onClick={() => togglePrepared(spell.id)}
                                className={cn(
                                  'w-4 h-4 transition-colors',
                                  spell.prepared ? 'text-warning' : 'text-text-tertiary'
                                )}
                                title={spell.prepared ? 'Prepared' : 'Not Prepared'}
                              >
                                <Star className={cn('h-4 w-4', spell.prepared && 'fill-current')} />
                              </button>
                            </div>
                          )}
                        </div>

                        <div className="flex-1">
                          <div className="flex items-center gap-2 flex-wrap">
                            <h4 className="font-medium text-text-primary">{spell.name}</h4>
                            {spell.prepared && (
                              <span className="text-xs bg-accent-primary text-white px-2 py-1 rounded">
                                Prepared
                              </span>
                            )}
                            {!spell.known && (
                              <span className="text-xs bg-text-tertiary text-white px-2 py-1 rounded">
                                Unknown
                              </span>
                            )}
                            <span className="text-xs bg-bg-primary text-text-tertiary px-2 py-1 rounded">
                              {spell.school}
                            </span>
                          </div>

                          <div className="flex items-center gap-4 mt-1 text-sm text-text-secondary">
                            <span className="flex items-center gap-1">
                              <Clock className="h-3 w-3" />
                              {spell.castingTime}
                            </span>
                            <span className="flex items-center gap-1">
                              <Target className="h-3 w-3" />
                              {spell.range}
                            </span>
                            <span>{spell.components.join(', ')}</span>
                            <span>{spell.duration}</span>
                          </div>

                          {spell.description && (
                            <p className="text-sm text-text-secondary mt-2">{spell.description}</p>
                          )}
                        </div>
                      </div>

                      {isEditing && (
                        <div className="flex gap-1">
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => startEdit(spell)}
                          >
                            <Edit3 className="h-4 w-4" />
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => removeSpell(spell.id)}
                            className="text-danger hover:text-danger"
                          >
                            <Trash2 className="h-4 w-4" />
                          </Button>
                        </div>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          );
        })}
      </div>

      {filteredSpells.length === 0 && (
        <div className="text-center py-8">
          <Book className="h-12 w-12 text-text-tertiary mx-auto mb-4" />
          <h3 className="text-lg font-medium text-text-primary mb-2">
            No Spells Found
          </h3>
          <p className="text-text-secondary">
            {searchTerm 
              ? `No spells match "${searchTerm}"`
              : showPreparedOnly
              ? 'No prepared spells found'
              : isEditing
              ? 'Add some spells to get started'
              : 'This character has no spells'
            }
          </p>
          {isEditing && !searchTerm && (
            <Button
              variant="primary"
              size="sm"
              onClick={() => setShowAddForm(true)}
              leftIcon={<Plus className="h-4 w-4" />}
              className="mt-4"
            >
              Add Spell
            </Button>
          )}
        </div>
      )}
    </div>
  );
}

/**
 * Combat Encounter Panel - Manage combat encounters with initiative, HP tracking, and conditions
 */
import React, { useState, useEffect } from 'react';
import { 
  Sword, 
  Shield, 
  Heart, 
  Plus, 
  Minus,
  SkipForward,
  RotateCcw,
  Settings,
  Users,
  Timer,
  Zap,
  AlertTriangle,
  Eye,
  EyeOff,
  Trash2,
  Edit,
  Save,
  X
} from 'lucide-react';
import { Button } from '../ui/Button';
import { Input } from '../ui/Input';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/Card';
import { Badge } from '../ui/Badge';
import { cn } from '../../lib/utils';

interface CombatEncounterPanelProps {
  campaignId: string;
  sceneId: string;
  isVisible: boolean;
  onToggleVisibility: () => void;
}

interface Combatant {
  id: string;
  name: string;
  initiative: number;
  maxHp: number;
  currentHp: number;
  armorClass: number;
  isPlayer: boolean;
  isVisible: boolean;
  conditions: Condition[];
  tokenId?: string;
  notes: string;
}

interface Condition {
  id: string;
  name: string;
  description: string;
  duration: number; // -1 for indefinite
  color: string;
}

interface CombatEncounter {
  id: string;
  name: string;
  isActive: boolean;
  currentRound: number;
  currentTurn: number;
  combatants: Combatant[];
  startTime?: Date;
}

const COMMON_CONDITIONS = [
  { name: 'Blinded', color: '#6B7280', description: 'Cannot see, attacks have disadvantage' },
  { name: 'Charmed', color: '#EC4899', description: 'Cannot attack charmer, charmer has advantage on social interactions' },
  { name: 'Deafened', color: '#9CA3AF', description: 'Cannot hear, automatically fails hearing-based checks' },
  { name: 'Frightened', color: '#7C2D12', description: 'Disadvantage on ability checks and attacks while source is in sight' },
  { name: 'Grappled', color: '#92400E', description: 'Speed becomes 0, cannot benefit from bonuses to speed' },
  { name: 'Incapacitated', color: '#991B1B', description: 'Cannot take actions or reactions' },
  { name: 'Paralyzed', color: '#450A0A', description: 'Incapacitated, cannot move or speak, fails Strength and Dexterity saves' },
  { name: 'Poisoned', color: '#166534', description: 'Disadvantage on attack rolls and ability checks' },
  { name: 'Prone', color: '#A16207', description: 'Disadvantage on attack rolls, attacks within 5 feet have advantage' },
  { name: 'Restrained', color: '#7C2D12', description: 'Speed becomes 0, disadvantage on attacks and Dexterity saves' },
  { name: 'Stunned', color: '#7E22CE', description: 'Incapacitated, cannot move, fails Strength and Dexterity saves' },
  { name: 'Unconscious', color: '#000000', description: 'Incapacitated, cannot move or speak, drops items, falls prone' },
];

export function CombatEncounterPanel({_ 
  campaignId, _sceneId, 
  _isVisible, _onToggleVisibility}: CombatEncounterPanelProps) {
  const [encounter, setEncounter] = useState<CombatEncounter | null>(null);
  const [loading, setLoading] = useState(false);
  const [_editingCombatant, _setEditingCombatant] = useState<string | null>(null);
  const [showAddCombatant, setShowAddCombatant] = useState(false);
  const [newCombatant, setNewCombatant] = useState({
    name: '',
    initiative: 10,
    maxHp: 10,
    currentHp: 10,
    armorClass: 10,
    isPlayer: false,
  });

  useEffect(() => {
    if (isVisible && sceneId) {
      loadEncounter();
    }
  }, [isVisible, sceneId]);

  const loadEncounter = async () => {
    setLoading(true);
    try {
      const response = await fetch(`/api/scenes/${sceneId}/encounter`, {
        credentials: 'include'
      });

      if (response.ok) {
        const data = await response.json();
        setEncounter(data.encounter);
      } else if (response.status === 404) {
        // No encounter exists yet
        setEncounter(null);
      }
    } catch (error) {
      console.error('Error loading encounter:', error);
    } finally {
      setLoading(false);
    }
  };

  const startNewEncounter = async () => {
    try {
      const response = await fetch(`/api/scenes/${sceneId}/encounter`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({
          name: `Combat - ${new Date().toLocaleDateString()}`,
        }),
      });

      if (response.ok) {
        const data = await response.json();
        setEncounter(data.encounter);
      }
    } catch (error) {
      console.error('Error starting encounter:', error);
    }
  };

  const endEncounter = async () => {
    if (!encounter) return;

    const confirm = window.confirm('Are you sure you want to end this encounter?');
    if (!confirm) return;

    try {
      await fetch(`/api/encounters/${encounter.id}/end`, {
        method: 'POST',
        credentials: 'include'
      });

      setEncounter(null);
    } catch (error) {
      console.error('Error ending encounter:', error);
    }
  };

  const addCombatant = async () => {
    if (!encounter || !newCombatant.name.trim()) return;

    try {
      const response = await fetch(`/api/encounters/${encounter.id}/combatants`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({
          ...newCombatant,
          currentHp: newCombatant.maxHp,
        }),
      });

      if (response.ok) {
        await loadEncounter();
        setNewCombatant({
          name: '',
          initiative: 10,
          maxHp: 10,
          currentHp: 10,
          armorClass: 10,
          isPlayer: false,
        });
        setShowAddCombatant(false);
      }
    } catch (error) {
      console.error('Error adding combatant:', error);
    }
  };

  const updateCombatant = async (_combatantId: string, _updates: Partial<Combatant>) => {
    if (!encounter) return;

    try {
      await fetch(`/api/combatants/${combatantId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify(updates),
      });

      // Update local state
      setEncounter(prev => {
        if (!prev) return prev;
        return {
          ...prev,
          combatants: prev.combatants.map(c => 
            c.id === combatantId ? { ...c, ...updates } : c
          )
        };
      });
    } catch (error) {
      console.error('Error updating combatant:', error);
    }
  };

  const removeCombatant = async (_combatantId: string) => {
    if (!encounter) return;

    try {
      await fetch(`/api/combatants/${combatantId}`, {
        method: 'DELETE',
        credentials: 'include'
      });

      setEncounter(prev => {
        if (!prev) return prev;
        return {
          ...prev,
          combatants: prev.combatants.filter(c => c.id !== combatantId)
        };
      });
    } catch (error) {
      console.error('Error removing combatant:', error);
    }
  };

  const nextTurn = async () => {
    if (!encounter || encounter.combatants.length === 0) return;

    const nextTurnIndex = (encounter.currentTurn + 1) % encounter.combatants.length;
    const nextRound = nextTurnIndex === 0 ? encounter.currentRound + 1 : encounter.currentRound;

    try {
      await fetch(`/api/encounters/${encounter.id}/next-turn`, {
        method: 'POST',
        credentials: 'include'
      });

      setEncounter(prev => {
        if (!prev) return prev;
        return {
          ...prev,
          currentTurn: nextTurnIndex,
          currentRound: nextRound
        };
      });
    } catch (error) {
      console.error('Error advancing turn:', error);
    }
  };

  const addCondition = async (_combatantId: string, _conditionName: string) => {
    const condition = COMMON_CONDITIONS.find(c => c.name === conditionName);
    if (!condition) return;

    const newCondition = {
      id: Date.now().toString(),
      name: condition.name,
      description: condition.description,
      duration: -1,
      color: condition.color,
    };

    await updateCombatant(combatantId, {
      conditions: [
        ...(encounter?.combatants.find(c => c.id === combatantId)?.conditions || []),
        newCondition
      ]
    });
  };

  const removeCondition = async (_combatantId: string, _conditionId: string) => {
    const combatant = encounter?.combatants.find(c => c.id === combatantId);
    if (!combatant) return;

    await updateCombatant(combatantId, {
      conditions: combatant.conditions.filter(c => c.id !== conditionId)
    });
  };

  if (!isVisible) {
    return (
      <Button
        variant="primary"
        size="sm"
        onClick={onToggleVisibility}
        className="fixed bottom-4 right-4 z-40"
      >
        <Sword className="h-4 w-4 mr-2" />
        Combat
      </Button>
    );
  }

  return (
    <Card className="fixed bottom-4 right-4 w-96 max-h-[80vh] z-40 shadow-xl">
      <CardHeader className="flex flex-row items-center justify-between pb-3">
        <CardTitle className="flex items-center gap-2 text-lg">
          <Sword className="h-5 w-5" />
          Combat Tracker
        </CardTitle>
        <div className="flex items-center gap-2">
          {encounter && (
            <Badge variant="secondary" className="text-xs">
              Round {encounter.currentRound}
            </Badge>
          )}
          <Button variant="ghost" size="icon" onClick={onToggleVisibility}>
            <X className="h-4 w-4" />
          </Button>
        </div>
      </CardHeader>

      <CardContent className="space-y-4 max-h-[60vh] overflow-y-auto">
        {loading ? (
          <div className="text-center py-8 text-neutral-500">
            Loading encounter...
          </div>
        ) : !encounter ? (
          <div className="text-center py-8">
            <Sword className="h-12 w-12 mx-auto text-neutral-400 mb-4" />
            <p className="text-neutral-600 mb-4">No active encounter</p>
            <Button onClick={startNewEncounter} variant="primary">
              <Plus className="h-4 w-4 mr-2" />
              Start Encounter
            </Button>
          </div>
        ) : (
          <>
            {/* Current Turn Display */}
            {encounter.combatants.length > 0 && (
              <div className="bg-primary-50 border border-primary-200 rounded-lg p-3">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-primary-800">
                      Current Turn
                    </p>
                    <p className="text-lg font-bold text-primary-900">
                      {encounter.combatants[encounter.currentTurn]?.name || 'Unknown'}
                    </p>
                  </div>
                  <Button onClick={nextTurn} variant="primary" size="sm">
                    <SkipForward className="h-4 w-4 mr-1" />
                    Next
                  </Button>
                </div>
              </div>
            )}

            {/* Combatants List */}
            <div className="space-y-2">
              {encounter.combatants
                .sort((_a, _b) => b.initiative - a.initiative)
                .map((combatant, _index) => (
                <div
                  key={combatant.id}
                  className={cn(
                    "border rounded-lg p-3 transition-colors",
                    index === encounter.currentTurn ? "border-primary-300 bg-primary-50" : "border-neutral-200"
                  )}
                >
                  <div className="flex items-center justify-between mb-2">
                    <div className="flex items-center gap-2">
                      <Badge variant={combatant.isPlayer ? "success" : "secondary"} className="text-xs">
                        {combatant.initiative}
                      </Badge>
                      <span className="font-medium">{combatant.name}</span>
                      {!combatant.isVisible && (
                        <EyeOff className="h-3 w-3 text-neutral-400" />
                      )}
                    </div>
                    <div className="flex items-center gap-1">
                      <Button
                        variant="ghost"
                        size="icon"
                        onClick={() => updateCombatant(combatant.id, { isVisible: !combatant.isVisible })}
                        className="h-6 w-6"
                      >
                        {combatant.isVisible ? <Eye className="h-3 w-3" /> : <EyeOff className="h-3 w-3" />}
                      </Button>
                      <Button
                        variant="ghost"
                        size="icon"
                        onClick={() => removeCombatant(combatant.id)}
                        className="h-6 w-6 text-error-600 hover:text-error-700"
                      >
                        <Trash2 className="h-3 w-3" />
                      </Button>
                    </div>
                  </div>

                  {/* HP and AC */}
                  <div className="flex items-center gap-4 mb-2">
                    <div className="flex items-center gap-1">
                      <Heart className="h-4 w-4 text-error-500" />
                      <div className="flex items-center gap-1">
                        <Button
                          variant="ghost"
                          size="icon"
                          onClick={() => updateCombatant(combatant.id, { 
                            currentHp: Math.max(0, combatant.currentHp - 1) 
                          })}
                          className="h-6 w-6"
                        >
                          <Minus className="h-3 w-3" />
                        </Button>
                        <span className={cn(
                          "text-sm font-mono min-w-[3rem] text-center",
                          combatant.currentHp <= 0 ? "text-error-600 font-bold" : ""
                        )}>
                          {combatant.currentHp}/{combatant.maxHp}
                        </span>
                        <Button
                          variant="ghost"
                          size="icon"
                          onClick={() => updateCombatant(combatant.id, { 
                            currentHp: Math.min(combatant.maxHp, combatant.currentHp + 1) 
                          })}
                          className="h-6 w-6"
                        >
                          <Plus className="h-3 w-3" />
                        </Button>
                      </div>
                    </div>
                    <div className="flex items-center gap-1">
                      <Shield className="h-4 w-4 text-neutral-500" />
                      <span className="text-sm font-mono">AC {combatant.armorClass}</span>
                    </div>
                  </div>

                  {/* Conditions */}
                  {combatant.conditions.length > 0 && (
                    <div className="flex flex-wrap gap-1 mb-2">
                      {combatant.conditions.map((condition) => (
                        <Badge
                          key={condition.id}
                          variant="outline"
                          className="text-xs cursor-pointer"
                          style={{ borderColor: condition.color, _color: condition.color }}
                          onClick={() => removeCondition(combatant.id, condition.id)}
                        >
                          {condition.name}
                        </Badge>
                      ))}
                    </div>
                  )}

                  {/* Add Condition */}
                  <div className="flex gap-1">
                    <select
                      onChange={(e) => {
                        if (e.target.value) {
                          addCondition(combatant.id, e.target.value);
                          e.target.value = '';
                        }
                      }}
                      className="text-xs px-2 py-1 border border-neutral-300 rounded flex-1"
                    >
                      <option value="">Add condition...</option>
                      {COMMON_CONDITIONS.map((_condition) => (
                        <option key={condition.name} value={condition.name}>
                          {condition.name}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>
              ))}
            </div>

            {/* Add Combatant */}
            {showAddCombatant ? (
              <div className="border border-neutral-300 rounded-lg p-3 space-y-3">
                <Input
                  placeholder="Combatant name"
                  value={newCombatant.name}
                  onChange={(e) => setNewCombatant(prev => ({ ...prev, name: e.target.value }))}
                />
                <div className="grid grid-cols-2 gap-2">
                  <Input
                    type="number"
                    placeholder="Initiative"
                    value={newCombatant.initiative}
                    onChange={(e) => setNewCombatant(prev => ({ ...prev, initiative: parseInt(e.target.value) || 0 }))}
                  />
                  <Input
                    type="number"
                    placeholder="Max HP"
                    value={newCombatant.maxHp}
                    onChange={(e) => setNewCombatant(prev => ({ 
                      ...prev, 
                      maxHp: parseInt(e.target.value) || 0,
                      currentHp: parseInt(e.target.value) || 0
                    }))}
                  />
                </div>
                <div className="flex items-center gap-2">
                  <Input
                    type="number"
                    placeholder="AC"
                    value={newCombatant.armorClass}
                    onChange={(e) => setNewCombatant(prev => ({ ...prev, armorClass: parseInt(e.target.value) || 0 }))}
                    className="flex-1"
                  />
                  <label className="flex items-center gap-2 text-sm">
                    <input
                      type="checkbox"
                      checked={newCombatant.isPlayer}
                      onChange={(e) => setNewCombatant(prev => ({ ...prev, isPlayer: e.target.checked }))}
                    />
                    Player
                  </label>
                </div>
                <div className="flex gap-2">
                  <Button onClick={addCombatant} variant="primary" size="sm" className="flex-1">
                    <Save className="h-4 w-4 mr-1" />
                    Add
                  </Button>
                  <Button 
                    onClick={() => setShowAddCombatant(false)} 
                    variant="outline" 
                    size="sm"
                  >
                    Cancel
                  </Button>
                </div>
              </div>
            ) : (
              <Button 
                onClick={() => setShowAddCombatant(true)} 
                variant="outline" 
                className="w-full"
              >
                <Plus className="h-4 w-4 mr-2" />
                Add Combatant
              </Button>
            )}

            {/* Encounter Actions */}
            <div className="flex gap-2 pt-2 border-t">
              <Button onClick={endEncounter} variant="outline" size="sm" className="flex-1">
                <RotateCcw className="h-4 w-4 mr-1" />
                End Encounter
              </Button>
            </div>
          </>
        )}
      </CardContent>
    </Card>
  );
}

