/**
 * Chat System Component - Real-time messaging for game sessions
 */

import React, { useState, useRef, useEffect } from 'react';
import { useWebSocket } from '../../providers/WebSocketProvider';
import { useAuth } from '../../providers/AuthProvider';
import { useGame } from '../../providers/GameProvider';
import { Button } from '../ui/Button';
import { Send, Dice6, Users, Eye, Crown, Volume2, VolumeX } from 'lucide-react';
import { cn, formatRelativeTime } from '../../lib/utils';

export interface ChatMessage {
  id: string;
  type: 'message' | 'roll' | 'system' | 'whisper' | 'ooc';
  content: string;
  author: {
    id: string;
    username: string;
    displayName: string;
    avatar?: string;
    role: 'gm' | 'player' | 'spectator';
  };
  timestamp: string;
  recipients?: string[]; // For whispers
  rollResult?: {
    dice: string;
    total: number;
    individual: number[];
    modifier: number;
  };
}

interface ChatSystemProps {
  className?: string;
}

export function ChatSystem({_ className }: ChatSystemProps) {
  const { user  } = useAuth();
  const { session,  isGM  } = useGame();
  const { send,  subscribe  } = useWebSocket();

  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [newMessage, setNewMessage] = useState('');
  const [messageType, setMessageType] = useState<'ic' | 'ooc' | 'whisper'>('ic');
  const [whisperTarget, setWhisperTarget] = useState<string>('');
  const [isExpanded, setIsExpanded] = useState(true);
  const [unreadCount, setUnreadCount] = useState(0);
  const [isSoundEnabled, setIsSoundEnabled] = useState(true);

  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);

  // Auto-scroll to bottom when new messages arrive
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Subscribe to chat messages
  useEffect(() => {
    const unsubscribe = subscribe('CHAT_MESSAGE', (message) => {
      const chatMessage = message as ChatMessage;
      setMessages(prev => [...prev, chatMessage]);

      // Play sound notification
      if (isSoundEnabled && chatMessage.author.id !== user?.id) {
        const audio = new Audio('/sounds/message.mp3');
        audio.volume = 0.3;
        audio.play().catch(() => {}); // Ignore errors
      }

      // Update unread count if chat is collapsed
      if (!isExpanded) {
        setUnreadCount(prev => prev + 1);
      }
    });

    return unsubscribe;
  }, [subscribe, isSoundEnabled, user?.id, isExpanded]);

  // Clear unread count when expanding
  useEffect(() => {
    if (isExpanded) {
      setUnreadCount(0);
    }
  }, [isExpanded]);

  const sendMessage = () => {
    if (!newMessage.trim() || !session || !user) return;

    const messageData = {
      type: 'SEND_CHAT_MESSAGE',
      sessionId: session.id,
      content: newMessage.trim(),
      messageType,
      whisperTarget: messageType === 'whisper' ? whisperTarget : undefined
    };

    send(messageData);
    setNewMessage('');
    inputRef.current?.focus();
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const handleQuickRoll = (_dice: string) => {
    if (!session || !user) return;

    const rollData = {
      type: 'ROLL_DICE',
      sessionId: session.id,
      dice,
      public: true
    };

    send(rollData);
  };

  const getMessageTypeColor = (_type: ChatMessage['type']) => {
    switch (type) {
      case 'roll': return 'text-accent-primary';
      case 'system': return 'text-warning';
      case 'whisper': return 'text-purple-400';
      case 'ooc': return 'text-text-tertiary';
      default: return 'text-text-primary';
    }
  };

  const getMessageTypeIcon = (_type: ChatMessage['type']) => {
    switch (type) {
      case 'roll': return <Dice6 className="h-3 w-3" />;
      case 'system': return <Crown className="h-3 w-3" />;
      case 'whisper': return <Eye className="h-3 w-3" />;
      default: return null;
    }
  };

  const formatMessageContent = (message: ChatMessage) => {
    if (message.type === 'roll' && message.rollResult) {
      const { dice,  total,  individual,  modifier  } = message.rollResult;
      return (
        <div className="space-y-1">
          <p>{message.content}</p>
          <div className="bg-bg-tertiary rounded p-2 font-mono text-sm">
            <div className="flex items-center gap-2">
              <Dice6 className="h-4 w-4 text-accent-primary" />
              <span className="font-semibold">Rolling {dice}</span>
            </div>
            <div className="mt-1">
              Individual: [{individual.join(', ')}]
              {modifier !== 0 && ` + ${modifier}`}
            </div>
            <div className="text-lg font-bold text-accent-primary">
              Total: {total}
            </div>
          </div>
        </div>
      );
    }

    return <p>{message.content}</p>;
  };

  if (!session) {
    return (
      <div className={cn('bg-bg-secondary rounded-lg border border-border-primary p-4', className)}>
        <p className="text-text-secondary text-center">
          Join a game session to access chat
        </p>
      </div>
    );
  }

  return (
    <div className={cn('bg-bg-secondary rounded-lg border border-border-primary flex flex-col', className)} ref={containerRef}>
      {/* Chat Header */}
      <div className="p-3 border-b border-border-primary flex items-center justify-between">
        <div className="flex items-center gap-2">
          <button
            onClick={() => setIsExpanded(!isExpanded)}
            className="flex items-center gap-2 hover:text-accent-primary transition-colors"
          >
            <Users className="h-4 w-4" />
            <span className="font-medium">Chat</span>
            {unreadCount > 0 && (
              <span className="bg-accent-primary text-white text-xs rounded-full px-2 py-0.5 min-w-[1.25rem] text-center">
                {unreadCount > 99 ? '99+' : unreadCount}
              </span>
            )}
          </button>
        </div>

        <div className="flex items-center gap-1">
          <button
            onClick={() => setIsSoundEnabled(!isSoundEnabled)}
            className={cn(
              'p-1 rounded hover:bg-bg-tertiary transition-colors',
              isSoundEnabled ? 'text-text-primary' : 'text-text-tertiary'
            )}
            title={isSoundEnabled ? 'Disable sounds' : 'Enable sounds'}
          >
            {isSoundEnabled ? <Volume2 className="h-4 w-4" /> : <VolumeX className="h-4 w-4" />}
          </button>
        </div>
      </div>

      {isExpanded && (
        <>
          {/* Messages Area */}
          <div className="flex-1 overflow-y-auto p-3 space-y-3 max-h-96">
            {messages.length === 0 ? (
              <div className="text-center text-text-secondary py-8">
                <Users className="h-8 w-8 mx-auto mb-2 opacity-50" />
                <p>No messages yet. Start the conversation!</p>
              </div>
            ) : (
              _messages.map((message) => (
                <div key={message.id} className="group">
                  <div className="flex items-start gap-2">
                    {/* Avatar */}
                    <div className="w-6 h-6 rounded-full bg-accent-primary flex items-center justify-center text-xs text-white font-medium flex-shrink-0">
                      {message.author.avatar ? (
                        <img 
                          src={message.author.avatar} 
                          alt={message.author.displayName}
                          className="w-full h-full rounded-full object-cover"
                        />
                      ) : (
                        message.author.displayName.charAt(0).toUpperCase()
                      )}
                    </div>

                    {/* Message Content */}
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center gap-2 mb-1">
                        <span className={cn(
                          'font-medium text-sm',
                          message.author.role === 'gm' ? 'text-gm-accent' : 'text-text-primary'
                        )}>
                          {message.author.displayName}
                        </span>
                        {message.author.role === 'gm' && (
                          <Crown className="h-3 w-3 text-gm-accent" />
                        )}
                        {getMessageTypeIcon(message.type)}
                        <span className="text-xs text-text-tertiary">
                          {formatRelativeTime(message.timestamp)}
                        </span>
                      </div>

                      <div className={cn('text-sm', getMessageTypeColor(message.type))}>
                        {message.type === 'whisper' && (
                          <span className="text-purple-400 font-medium">
                            [Whisper] 
                          </span>
                        )}
                        {message.type === 'ooc' && (
                          <span className="text-text-tertiary font-medium">
                            [OOC] 
                          </span>
                        )}
                        {formatMessageContent(message)}
                      </div>
                    </div>
                  </div>
                </div>
              ))
            )}
            <div ref={messagesEndRef} />
          </div>

          {/* Quick Actions */}
          <div className="px-3 py-2 border-t border-border-primary">
            <div className="flex items-center gap-1 mb-2">
              <span className="text-xs text-text-secondary">Quick Rolls:</span>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => handleQuickRoll('1d20')}
                className="text-xs h-6 px-2"
              >
                d20
              </Button>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => handleQuickRoll('1d100')}
                className="text-xs h-6 px-2"
              >
                d100
              </Button>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => handleQuickRoll('4d6')}
                className="text-xs h-6 px-2"
              >
                4d6
              </Button>
            </div>
          </div>

          {/* Message Input */}
          <div className="p-3 border-t border-border-primary">
            <div className="flex items-center gap-2 mb-2">
              <select
                value={messageType}
                onChange={(e) => setMessageType(e.target.value as any)}
                className="text-xs bg-bg-tertiary border border-border-primary rounded px-2 py-1"
              >
                <option value="ic">In Character</option>
                <option value="ooc">Out of Character</option>
                {isGM && <option value="whisper">Whisper</option>}
              </select>

              {messageType === 'whisper' && session.players.length > 0 && (
                <select
                  value={whisperTarget}
                  onChange={(e) => setWhisperTarget(e.target.value)}
                  className="text-xs bg-bg-tertiary border border-border-primary rounded px-2 py-1"
                >
                  <option value="">Select target...</option>
                  {session.players.map(player => (
                    <option key={player.id} value={player.id}>
                      {player.displayName}
                    </option>
                  ))}
                </select>
              )}
            </div>

            <div className="flex items-center gap-2">
              <input
                ref={inputRef}
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder={
                  messageType === 'whisper' 
                    ? 'Type a whisper...' 
                    : messageType === 'ooc' 
                    ? 'Type an OOC message...'
                    : 'Type a message...'
                }
                className="flex-1 bg-bg-tertiary border border-border-primary rounded px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-accent-primary"
                disabled={messageType === 'whisper' && !whisperTarget}
              />
              <Button
                variant="primary"
                size="sm"
                onClick={sendMessage}
                disabled={!newMessage.trim() || (messageType === 'whisper' && !whisperTarget)}
                leftIcon={<Send className="h-4 w-4" />}
              >
                Send
              </Button>
            </div>
          </div>
        </>
      )}
    </div>
  );
}

/**
 * Dice Roller Component - Advanced dice rolling mechanics with modifiers and custom rolls
 */

import React, { useState, useRef } from 'react';
import { useWebSocket } from '../../providers/WebSocketProvider';
import { useAuth } from '../../providers/AuthProvider';
import { useGame } from '../../providers/GameProvider';
import { Button } from '../ui/Button';
import { Input } from '../ui/Input';
import { Dice1, Dice2, Dice3, Dice4, Dice5, Dice6, _Plus, _Minus, RotateCcw, History } from 'lucide-react';
import { cn } from '../../lib/utils';

interface DiceRoll {
  id: string;
  dice: string;
  total: number;
  individual: number[];
  modifier: number;
  timestamp: string;
  public: boolean;
}

interface DiceRollerProps {
  className?: string;
  onRoll?: (roll: DiceRoll) => void;
}

const COMMON_DICE = [
  { sides: 4, icon: Dice1, color: 'text-red-400' },
  { sides: 6, icon: Dice2, color: 'text-blue-400' },
  { sides: 8, icon: Dice3, color: 'text-green-400' },
  { sides: 10, icon: Dice4, color: 'text-yellow-400' },
  { sides: 12, icon: Dice5, color: 'text-purple-400' },
  { sides: 20, icon: Dice6, color: 'text-accent-primary' },
];

const PRESET_ROLLS = [
  { label: 'Attack', dice: '1d20', modifier: 0 },
  { label: 'Damage', dice: '1d8', modifier: 0 },
  { label: 'Initiative', dice: '1d20', modifier: 0 },
  { label: 'Ability Check', dice: '1d20', modifier: 0 },
  { label: 'Saving Throw', dice: '1d20', modifier: 0 },
  { label: 'Ability Score', dice: '4d6', modifier: 0, dropLowest: true },
];

export function DiceRoller({_ className, _onRoll}: DiceRollerProps) {
  const { user  } = useAuth();
  const { session  } = useGame();
  const { send  } = useWebSocket();

  const [diceCount, setDiceCount] = useState(1);
  const [diceSides, setDiceSides] = useState(20);
  const [modifier, setModifier] = useState(0);
  const [customDice, setCustomDice] = useState('');
  const [isPublic, setIsPublic] = useState(true);
  const [rollHistory, setRollHistory] = useState<DiceRoll[]>([]);
  const [showHistory, setShowHistory] = useState(false);
  const [advantage, setAdvantage] = useState<'none' | 'advantage' | 'disadvantage'>('none');

  const inputRef = useRef<HTMLInputElement>(null);

  const parseDiceNotation = (notation: string): { count: number; sides: number; modifier: number } => {
    const match = notation.match(/^(\d+)?d(\d+)([+-]\d+)?$/i);
    if (!match) throw new Error('Invalid dice notation');

    const count = parseInt(match[1] || '1');
    const sides = parseInt(match[2]);
    const mod = parseInt(match[3] || '0');

    return { count, sides, modifier: mod };
  };

  const rollDice = (sides: number, count: number = 1): number[] => {
    return Array.from({_ length: count }, () => Math.floor(Math.random() * sides) + 1);
  };

  const calculateTotal = (rolls: number[], modifier: number): number => {
    return rolls.reduce((_sum, _roll) => sum + roll, 0) + modifier;
  };

  const executeRoll = (_diceNotation?: string, _rollModifier?: number, _isRollPublic?: boolean) => {
    if (!session || !user) return;

    let rollCount: number;
    let rollSides: number;
    let rollMod: number;

    try {
      if (diceNotation) {
        const parsed = parseDiceNotation(diceNotation);
        rollCount = parsed.count;
        rollSides = parsed.sides;
        rollMod = parsed.modifier + (rollModifier || 0);
      } else {
        rollCount = diceCount;
        rollSides = diceSides;
        rollMod = modifier;
      }

      // Handle advantage/disadvantage for d20 rolls
      let finalRolls: number[];
      if (rollSides === 20 && rollCount === 1 && advantage !== 'none') {
        const rolls = rollDice(rollSides, 2);
        if (advantage === 'advantage') {
          finalRolls = [Math.max(...rolls)];
        } else {
          finalRolls = [Math.min(...rolls)];
        }
      } else {
        finalRolls = rollDice(rollSides, rollCount);
      }

      const total = calculateTotal(finalRolls, rollMod);
      const notation = `${rollCount}d${rollSides}${rollMod >= 0 ? '+' : ''}${rollMod !== 0 ? rollMod : ''}`;

      const roll: DiceRoll = {
        id: `roll_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        dice: notation,
        total,
        individual: finalRolls,
        modifier: rollMod,
        timestamp: new Date().toISOString(),
        public: isRollPublic ?? isPublic
      };

      // Add to history
      setRollHistory(prev => [roll, ...prev.slice(0, 19)]); // Keep last 20 rolls

      // Send to server
      const rollData = {
        type: 'ROLL_DICE',
        sessionId: session.id,
        ...roll
      };

      send(rollData);
      onRoll?.(roll);

    } catch (error) {
      console.error('Failed to execute roll:', error);
    }
  };

  const handlePresetRoll = (preset: typeof PRESET_ROLLS[0]) => {
    executeRoll(preset.dice, preset.modifier, isPublic);
  };

  const handleCustomRoll = () => {
    if (!customDice.trim()) return;

    try {
      executeRoll(customDice.trim(), 0, isPublic);
      setCustomDice('');
    } catch (error) {
      console.error('Invalid dice notation:', error);
    }
  };

  const handleQuickRoll = (_sides: number) => {
    setDiceSides(sides);
    executeRoll(`1d${sides}`, 0, isPublic);
  };

  const clearHistory = () => {
    setRollHistory([]);
  };

  const _getDiceIcon = (_sides: number) => {
    const dice = COMMON_DICE.find(d => d.sides === sides);
    return dice ? dice.icon : Dice6;
  };

  const getDiceColor = (_sides: number) => {
    const dice = COMMON_DICE.find(d => d.sides === sides);
    return dice ? dice.color : 'text-text-primary';
  };

  if (!session) {
    return (
      <div className={cn('bg-bg-secondary rounded-lg border border-border-primary p-4', className)}>
        <p className="text-text-secondary text-center">
          Join a game session to access dice rolling
        </p>
      </div>
    );
  }

  return (
    <div className={cn('bg-bg-secondary rounded-lg border border-border-primary p-4 space-y-4', className)}>
      {/* Header */}
      <div className="flex items-center justify-between">
        <h3 className="font-semibold text-text-primary flex items-center gap-2">
          <Dice6 className="h-5 w-5 text-accent-primary" />
          Dice Roller
        </h3>

        <div className="flex items-center gap-2">
          <label className="flex items-center gap-2 text-sm">
            <input
              type="checkbox"
              checked={isPublic}
              onChange={(e) => setIsPublic(e.target.checked)}
              className="rounded"
            />
            Public Roll
          </label>

          <Button
            variant="ghost"
            size="sm"
            onClick={() => setShowHistory(!showHistory)}
            leftIcon={<History className="h-4 w-4" />}
          >
            History
          </Button>
        </div>
      </div>

      {/* Quick Dice Buttons */}
      <div className="grid grid-cols-6 gap-2">
        {COMMON_DICE.map(_({ sides, _icon: Icon, _color}) => (
          <Button
            key={sides}
            variant="ghost"
            size="sm"
            onClick={() => handleQuickRoll(sides)}
            className="flex flex-col items-center gap-1 h-auto py-2"
          >
            <Icon className={cn('h-5 w-5', color)} />
            <span className="text-xs">d{sides}</span>
          </Button>
        ))}
      </div>

      {/* Custom Roll Builder */}
      <div className="space-y-3">
        <div className="grid grid-cols-3 gap-2">
          <div>
            <label className="block text-xs text-text-secondary mb-1">Count</label>
            <Input
              type="number"
              min="1"
              max="100"
              value={diceCount}
              onChange={(e) => setDiceCount(parseInt(e.target.value) || 1)}
              className="text-center"
            />
          </div>

          <div>
            <label className="block text-xs text-text-secondary mb-1">Sides</label>
            <Input
              type="number"
              min="2"
              max="1000"
              value={diceSides}
              onChange={(e) => setDiceSides(parseInt(e.target.value) || 20)}
              className="text-center"
            />
          </div>

          <div>
            <label className="block text-xs text-text-secondary mb-1">Modifier</label>
            <Input
              type="number"
              min="-100"
              max="100"
              value={modifier}
              onChange={(e) => setModifier(parseInt(e.target.value) || 0)}
              className="text-center"
            />
          </div>
        </div>

        {/* Advantage/Disadvantage for d20 */}
        {diceSides === 20 && diceCount === 1 && (
          <div className="flex justify-center gap-1">
            <Button
              variant={advantage === 'none' ? 'primary' : 'ghost'}
              size="sm"
              onClick={() => setAdvantage('none')}
            >
              Normal
            </Button>
            <Button
              variant={advantage === 'advantage' ? 'success' : 'ghost'}
              size="sm"
              onClick={() => setAdvantage('advantage')}
            >
              Advantage
            </Button>
            <Button
              variant={advantage === 'disadvantage' ? 'destructive' : 'ghost'}
              size="sm"
              onClick={() => setAdvantage('disadvantage')}
            >
              Disadvantage
            </Button>
          </div>
        )}

        <Button
          variant="primary"
          fullWidth
          onClick={() => executeRoll()}
          leftIcon={<Dice6 className="h-4 w-4" />}
        >
          Roll {diceCount}d{diceSides}{modifier !== 0 && (modifier > 0 ? `+${modifier}` : modifier)}
        </Button>
      </div>

      {/* Custom Dice Notation */}
      <div className="space-y-2">
        <label className="block text-xs text-text-secondary">Custom Dice (e.g., 3d6+2, 1d20-1)</label>
        <div className="flex gap-2">
          <Input
            ref={inputRef}
            type="text"
            value={customDice}
            onChange={(e) => setCustomDice(e.target.value)}
            placeholder="2d8+3, 1d100, etc."
            onKeyPress={(e) => e.key === 'Enter' && handleCustomRoll()}
          />
          <Button
            variant="secondary"
            onClick={handleCustomRoll}
            disabled={!customDice.trim()}
          >
            Roll
          </Button>
        </div>
      </div>

      {/* Preset Rolls */}
      <div className="space-y-2">
        <label className="block text-xs text-text-secondary">Common Rolls</label>
        <div className="grid grid-cols-2 gap-2">
          {PRESET_ROLLS.map((preset) => (
            <Button
              key={preset.label}
              variant="ghost"
              size="sm"
              onClick={() => handlePresetRoll(preset)}
              className="justify-start"
            >
              <span className="text-xs">{preset.label}</span>
            </Button>
          ))}
        </div>
      </div>

      {/* Roll History */}
      {showHistory && (
        <div className="border-t border-border-primary pt-4">
          <div className="flex items-center justify-between mb-2">
            <h4 className="text-sm font-medium text-text-primary">Roll History</h4>
            {rollHistory.length > 0 && (
              <Button
                variant="ghost"
                size="sm"
                onClick={clearHistory}
                leftIcon={<RotateCcw className="h-3 w-3" />}
              >
                Clear
              </Button>
            )}
          </div>

          <div className="space-y-2 max-h-32 overflow-y-auto">
            {rollHistory.length === 0 ? (
              <p className="text-text-tertiary text-sm text-center py-4">No rolls yet</p>
            ) : (
              _rollHistory.map((roll) => (
                <div key={roll.id} className="flex items-center justify-between text-sm bg-bg-tertiary rounded p-2">
                  <div className="flex items-center gap-2">
                    <span className={cn('font-mono', getDiceColor(roll.individual.length > 0 ? 20 : 6))}>
                      {roll.dice}
                    </span>
                    <span className="text-text-tertiary">
                      [{roll.individual.join(', ')}]
                    </span>
                  </div>
                  <div className="flex items-center gap-2">
                    <span className="font-bold text-accent-primary">
                      {roll.total}
                    </span>
                    {!roll.public && (
                      <span className="text-xs text-text-tertiary">Private</span>
                    )}
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      )}
    </div>
  );
}

/**
 * Player Panel Component - Display player information, status, and controls
 */

import React, { useState } from 'react';
import { useAuth } from '../../providers/AuthProvider';
import { useGame } from '../../providers/GameProvider';
import { useWebSocket } from '../../providers/WebSocketProvider';
import { Button } from '../ui/Button';
import { 
  Crown, 
  Users, 
  Shield, 
  Heart, 
  Zap, 
  Eye, 
  EyeOff, 
  Volume2, 
  VolumeX, 
  Settings,
  UserX,
  MoreVertical,
  Wifi,
  WifiOff
} from 'lucide-react';
import { cn } from '../../lib/utils';
import type { Player } from '../../providers/GameProvider';

interface PlayerPanelProps {
  className?: string;
}

interface PlayerStatusProps {
  player: Player;
  isCurrentUser: boolean;
  isGM: boolean;
  onKick?: (_playerId: string) => void;
  onUpdatePermissions?: (_playerId: string, permissions: Partial<Player['permissions']>) => void;
}

function PlayerStatus({_ player, _isCurrentUser, _isGM, _onKick, _onUpdatePermissions }: PlayerStatusProps) {
  const [showOptions, setShowOptions] = useState(false);
  const [showPermissions, setShowPermissions] = useState(false);

  const getStatusColor = (_isConnected: boolean) => {
    return isConnected ? 'bg-success' : 'bg-error';
  };

  const getAvatarFallback = (_displayName: string) => {
    return displayName
      .split(' ')
      .map(word => word.charAt(0))
      .join('')
      .toUpperCase()
      .slice(0, 2);
  };

  const togglePermission = (_permission: keyof Player['permissions']) => {
    if (!onUpdatePermissions) return;

    onUpdatePermissions(player.id, {
      [permission]: !player.permissions[permission]
    });
  };

  return (
    <div className={cn(
      'relative p-3 bg-bg-tertiary rounded-lg border',
      isCurrentUser ? 'border-accent-primary bg-accent-light' : 'border-border-primary'
    )}>
      {/* Player Info */}
      <div className="flex items-center gap-3">
        {/* Avatar */}
        <div className="relative">
          <div className="w-10 h-10 rounded-full bg-accent-primary flex items-center justify-center text-white font-medium">
            {player.avatar ? (
              <img 
                src={player.avatar} 
                alt={player.displayName}
                className="w-full h-full rounded-full object-cover"
              />
            ) : (
              getAvatarFallback(player.displayName)
            )}
          </div>

          {/* Connection Status */}
          <div className={cn(
            'absolute -bottom-0.5 -right-0.5 w-3 h-3 rounded-full border-2 border-bg-tertiary',
            getStatusColor(player.isConnected)
          )} />
        </div>

        {/* Player Details */}
        <div className="flex-1 min-w-0">
          <div className="flex items-center gap-2">
            <h4 className="font-medium text-text-primary truncate">
              {player.displayName}
            </h4>
            {isCurrentUser && (
              <span className="text-xs bg-accent-primary text-white px-1.5 py-0.5 rounded">
                You
              </span>
            )}
          </div>

          <p className="text-sm text-text-secondary truncate">
            @{player.username}
          </p>

          {/* Character Info */}
          {player.character && (
            <div className="mt-1 text-xs text-text-tertiary">
              <span className="font-medium">{player.character.name}</span>
              <span className="mx-1">•</span>
              <span>Lv{player.character.level} {player.character.race} {player.character.class}</span>
            </div>
          )}
        </div>

        {/* Actions */}
        <div className="flex items-center gap-1">
          {player.isConnected ? (
            <Wifi className="h-4 w-4 text-success" />
          ) : (
            <WifiOff className="h-4 w-4 text-error" />
          )}

          {isGM && !isCurrentUser && (
            <button
              onClick={() => setShowOptions(!showOptions)}
              className="p-1 hover:bg-bg-secondary rounded transition-colors"
            >
              <MoreVertical className="h-4 w-4 text-text-secondary" />
            </button>
          )}
        </div>
      </div>

      {/* Character Health/Status */}
      {player.character && (
        <div className="mt-3 space-y-2">
          <div className="flex items-center gap-3">
            {/* Health Bar */}
            <div className="flex-1">
              <div className="flex items-center gap-2 mb-1">
                <Heart className="h-3 w-3 text-error" />
                <span className="text-xs text-text-secondary">
                  {player.character.hitPoints}/{player.character.maxHitPoints} HP
                </span>
              </div>
              <div className="w-full bg-bg-secondary rounded-full h-2">
                <div 
                  className="bg-error h-2 rounded-full transition-all duration-300"
                  style={{ 
                    width: `${Math.max(0, (player.character.hitPoints / player.character.maxHitPoints) * 100)}%` 
                  }}
                />
              </div>
            </div>
          </div>

          {/* Quick Actions for Current User */}
          {isCurrentUser && (
            <div className="flex gap-2">
              <Button variant="ghost" size="sm" className="text-xs h-6">
                <Shield className="h-3 w-3 mr-1" />
                AC {player.character.level + 10}
              </Button>
              <Button variant="ghost" size="sm" className="text-xs h-6">
                <Zap className="h-3 w-3 mr-1" />
                Initiative
              </Button>
            </div>
          )}
        </div>
      )}

      {/* GM Options Dropdown */}
      {showOptions && isGM && !isCurrentUser && (
        <div className="absolute top-full right-0 mt-1 w-48 bg-bg-secondary border border-border-primary rounded-lg shadow-lg z-10">
          <div className="p-2 space-y-1">
            <button
              onClick={() => setShowPermissions(!showPermissions)}
              className="w-full text-left px-2 py-1.5 text-sm hover:bg-bg-tertiary rounded transition-colors"
            >
              <Settings className="h-3 w-3 inline mr-2" />
              Permissions
            </button>

            <button
              onClick={() => onKick?.(player.id)}
              className="w-full text-left px-2 py-1.5 text-sm text-error hover:bg-error-light rounded transition-colors"
            >
              <UserX className="h-3 w-3 inline mr-2" />
              Kick Player
            </button>
          </div>

          {/* Permissions Panel */}
          {showPermissions && (
            <div className="border-t border-border-primary p-2">
              <h5 className="text-xs font-medium text-text-primary mb-2">Player Permissions</h5>
              <div className="space-y-1">
                {Object.entries(player.permissions).map([key, _value] => (
                  <label key={key} className="flex items-center gap-2 text-xs">
                    <input
                      type="checkbox"
                      checked={value}
                      onChange={() => togglePermission(key as keyof Player['permissions'])}
                      className="rounded"
                    />
                    <span className="capitalize">
                      {key.replace(/([A-Z])/g, ' $1').toLowerCase()}
                    </span>
                  </label>
                ))}
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export function PlayerPanel({_ className }: PlayerPanelProps) {
  const { user  } = useAuth();
  const { session,  isGM,  kickPlayer,  updatePlayerPermissions  } = useGame();
  const { isConnected,  latency  } = useWebSocket();
  const [showOfflinePlayers, setShowOfflinePlayers] = useState(true);

  if (!session) {
    return (
      <div className={cn('bg-bg-secondary rounded-lg border border-border-primary p-4', className)}>
        <p className="text-text-secondary text-center">
          Join a game session to see players
        </p>
      </div>
    );
  }

  const connectedPlayers = session.players.filter(p => p.isConnected);
  const offlinePlayers = session.players.filter(p => !p.isConnected);

  return (
    <div className={cn('bg-bg-secondary rounded-lg border border-border-primary', className)}>
      {/* Header */}
      <div className="p-4 border-b border-border-primary">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <Users className="h-5 w-5 text-accent-primary" />
            <h3 className="font-semibold text-text-primary">
              Players ({session.players.length}/{session.settings.maxPlayers})
            </h3>
          </div>

          {/* Session Status */}
          <div className="flex items-center gap-2">
            <div className={cn(
              'h-2 w-2 rounded-full',
              isConnected ? 'bg-success' : 'bg-error'
            )} />
            <span className="text-xs text-text-secondary">
              {isConnected ? `${latency}ms` : 'Disconnected'}
            </span>
          </div>
        </div>

        {/* Gamemaster Info */}
        <div className="mt-3 p-2 bg-gm-accent/10 border border-gm-accent/20 rounded-lg">
          <div className="flex items-center gap-2">
            <Crown className="h-4 w-4 text-gm-accent" />
            <span className="text-sm font-medium text-text-primary">
              {session.gamemaster.displayName}
            </span>
            <span className="text-xs text-text-secondary">
              Gamemaster
            </span>
          </div>
        </div>
      </div>

      {/* Players List */}
      <div className="p-4 space-y-3">
        {/* Connected Players */}
        {connectedPlayers.length > 0 && (
          <div>
            <h4 className="text-sm font-medium text-text-primary mb-2 flex items-center gap-2">
              <Wifi className="h-4 w-4 text-success" />
              Connected ({connectedPlayers.length})
            </h4>
            <div className="space-y-2">
              {connectedPlayers.map((player) => (
                <PlayerStatus
                  key={player.id}
                  player={player}
                  isCurrentUser={player.userId === user?.id}
                  isGM={isGM}
                  onKick={kickPlayer}
                  onUpdatePermissions={updatePlayerPermissions}
                />
              ))}
            </div>
          </div>
        )}

        {/* Offline Players */}
        {offlinePlayers.length > 0 && (
          <div>
            <button
              onClick={() => setShowOfflinePlayers(!showOfflinePlayers)}
              className="w-full text-left"
            >
              <h4 className="text-sm font-medium text-text-secondary mb-2 flex items-center gap-2 hover:text-text-primary transition-colors">
                <WifiOff className="h-4 w-4 text-error" />
                Offline ({offlinePlayers.length})
                {showOfflinePlayers ? (
                  <EyeOff className="h-3 w-3 ml-auto" />
                ) : (
                  <Eye className="h-3 w-3 ml-auto" />
                )}
              </h4>
            </button>

            {showOfflinePlayers && (
              <div className="space-y-2 opacity-60">
                {offlinePlayers.map((player) => (
                  <PlayerStatus
                    key={player.id}
                    player={player}
                    isCurrentUser={player.userId === user?.id}
                    isGM={isGM}
                    onKick={kickPlayer}
                    onUpdatePermissions={updatePlayerPermissions}
                  />
                ))}
              </div>
            )}
          </div>
        )}

        {/* Empty State */}
        {session.players.length === 0 && (
          <div className="text-center py-8">
            <Users className="h-8 w-8 text-text-tertiary mx-auto mb-2" />
            <p className="text-text-secondary">
              No players have joined yet
            </p>
            {isGM && (
              <p className="text-text-tertiary text-sm mt-1">
                Share the session link to invite players
              </p>
            )}
          </div>
        )}
      </div>

      {/* Session Controls */}
      {isGM && (
        <div className="p-4 border-t border-border-primary">
          <div className="flex gap-2">
            {session.status === 'active' ? (
              <Button
                variant="secondary"
                size="sm"
                onClick={() => {/* pause session */}}
              >
                Pause Session
              </Button>
            ) : (
              <Button
                variant="primary"
                size="sm"
                onClick={() => {/* resume session */}}
              >
                Resume Session
              </Button>
            )}

            <Button
              variant="destructive"
              size="sm"
              onClick={() => {/* end session */}}
            >
              End Session
            </Button>
          </div>
        </div>
      )}
    </div>
  );
}

