      id: 'tokens',
      name: 'Tokens',
      type: 'tokens',
      visible: true,
      opacity: 1,
      locked: false
    }
  ]);

  // Initialize with some demo tokens
  useEffect(() => {
    setTokens([
      {
        id: 'token1',
        name: 'Thorin',
        x: 5,
        y: 5,
        size: 1,
        color: '#3b82f6',
        isVisible: true,
        rotation: 0,
        conditions: [],
        hitPoints: { current: 28, max: 28 }
      },
      {
        id: 'token2',
        name: 'Goblin 1',
        x: 10,
        y: 8,
        size: 1,
        color: '#ef4444',
        isVisible: true,
        rotation: 0,
        conditions: ['poisoned'],
        hitPoints: { current: 7, max: 7 }
      },
      {
        id: 'token3',
        name: 'Dragon',
        x: 15,
        y: 12,
        size: 3,
        color: '#dc2626',
        isVisible: isGM,
        rotation: 45,
        conditions: [],
        hitPoints: { current: 184, max: 200 }
      }
    ]);
  }, [isGM]);

  const snapToGridCoords = useCallback((_x: number, _y: number) => {
    if (!snapToGrid) return { x, y };
    return {
      x: Math.round(x / GRID_SIZE) * GRID_SIZE,
      y: Math.round(y / GRID_SIZE) * GRID_SIZE
    };
  }, [snapToGrid]);

  const _handleTokenMove = useCallback((tokenId: string, _newX: number, _newY: number) => {
    const gridX = Math.round(newX / GRID_SIZE);
    const gridY = Math.round(newY / GRID_SIZE);

    setTokens(prev => prev.map(token => 
      token.id === tokenId 
        ? { ...token, x: gridX, y: gridY }
        : token
    ));

    // Send movement to server
    send({
      type: 'MOVE_TOKEN',
      entityId: parseInt(tokenId.replace('token', '')) || 0,
      x: gridX,
      y: gridY,
      animate: false
    });
  }, [send]);

  const handleTokenSelect = useCallback((tokenId: string, _multiSelect = false) => {
    setSelectedTokens(prev => {
      if (multiSelect) {
        return prev.includes(tokenId) 
          ? prev.filter(id => id !== tokenId)
          : [...prev, tokenId];
      }
      return [tokenId];
    });
  }, []);

  const handleTokenDoubleClick = useCallback((token: Token) => {
    setSelectedToken(token);
    setShowTokenProperties(true);
  }, []);

  const handleTokenUpdate = useCallback((tokenId: string, updates: Partial<Token>) => {
    setTokens(prev => prev.map(token => 
      token.id === tokenId ? { ...token, ...updates } : token
    ));

    // Send update to server
    send({
      type: 'MOVE_TOKEN',
      entityId: parseInt(tokenId.replace('token', '')) || 0,
      x: updates.x || 0,
      y: updates.y || 0,
      animate: false
    });
  }, [send]);

  const handleTokenDelete = useCallback((tokenId: string) => {
    setTokens(prev => prev.filter(token => token.id !== tokenId));
    setSelectedTokens(prev => prev.filter(id => id !== tokenId));
  }, []);

  const handleBackgroundUpload = useCallback((_file: File) => {
    const reader = new FileReader();
    reader.onload = (e) => {
      const result = e.target?.result as string;
      setBackgroundImage(result);
    };
    reader.readAsDataURL(file);
  }, []);

  const handleLayersUpdate = useCallback((_newLayers: MapLayer[]) => {
    setLayers(newLayers);
  }, []);

  const handleMapClick = useCallback((event: React.MouseEvent) => {
    if (!mapRef.current) return;

    const rect = mapRef.current.getBoundingClientRect();
    const x = (event.clientX - rect.left - pan.x) / zoom;
    const y = (event.clientY - rect.top - pan.y) / zoom;

    if (activeTool === 'add-token') {
      const gridPos = snapToGridCoords(x, y);
      const newToken: Token = {
        id: `token_${Date.now()}`,
        name: 'New Token',
        x: Math.round(gridPos.x / GRID_SIZE),
        y: Math.round(gridPos.y / GRID_SIZE),
        size: 1,
        color: '#6b7280',
        isVisible: true,
        rotation: 0,
        conditions: []
      };
      setTokens(prev => [...prev, newToken]);
    } else if (activeTool === 'select') {
      // Clear selection if clicking empty space
      setSelectedTokens([]);
    }
  }, [activeTool, pan, zoom, snapToGridCoords]);

  const _handleZoomIn = () => setZoom(prev => Math.min(prev * 1.2, 3));
  const _handleZoomOut = () => setZoom(prev => Math.max(prev / 1.2, 0.5));

  const deleteSelectedTokens = () => {
    setTokens(prev => prev.filter(token => !selectedTokens.includes(token.id)));
    setSelectedTokens([]);
  };

  const rotateSelectedTokens = (_degrees: number) => {
    setTokens(prev => prev.map(token => 
      selectedTokens.includes(token.id)
        ? { ...token, rotation: (token.rotation + degrees) % 360 }
        : token
    ));
  };

  const toggleTokenVisibility = (tokenId: string) => {
    setTokens(prev => prev.map(token => 
      token.id === tokenId
        ? { ...token, isVisible: !token.isVisible }
        : token
    ));
  };

  const getTokenStyle = (token: Token) => {
    const size = token.size * GRID_SIZE;
    return {
      position: 'absolute' as const,
      left: token.x * GRID_SIZE,
      top: token.y * GRID_SIZE,
      width: size,
      height: size,
      backgroundColor: token.color,
      border: selectedTokens.includes(token.id) ? '3px solid #fbbf24' : '2px solid #000',
      borderRadius: token.size === 1 ? '50%' : '8px',
      transform: `rotate(${token.rotation}deg)`,
      opacity: token.isVisible ? 1 : (isGM ? 0.5 : 0),
      cursor: activeTool === 'select' ? 'move' : 'pointer',
      zIndex: selectedTokens.includes(token.id) ? 10 : 5,
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      color: 'white',
      fontSize: `${Math.max(10, size / 4)}px`,
      fontWeight: 'bold',
      textShadow: '1px 1px 2px rgba(0,0,0,0.8)',
      transition: 'all 0.1s ease'
    };
  };

  const _renderGrid = () => {
    if (!showGrid) return null;

    const lines = [];
    const mapWidth = DEFAULT_MAP_SIZE.width * GRID_SIZE;
    const mapHeight = DEFAULT_MAP_SIZE.height * GRID_SIZE;

    // Vertical lines
    for (let i = 0; i <= DEFAULT_MAP_SIZE.width; i++) {
      lines.push(
        <line
          key={`v${i}`}
          x1={i * GRID_SIZE}
          y1={0}
          x2={i * GRID_SIZE}
          y2={mapHeight}
          stroke="rgba(0,0,0,0.2)"
          strokeWidth="1"
        />
      );
    }

    // Horizontal lines
    for (let i = 0; i <= DEFAULT_MAP_SIZE.height; i++) {
      lines.push(
        <line
          key={`h${i}`}
          x1={0}
          y1={i * GRID_SIZE}
          x2={mapWidth}
          y2={i * GRID_SIZE}
          stroke="rgba(0,0,0,0.2)"
          strokeWidth="1"
        />
      );
    }

    return (
      <svg
        className="absolute inset-0 pointer-events-none"
        width={mapWidth}
        height={mapHeight}
      >
        {lines}
      </svg>
    );
  };

  return (
    <div className={cn('flex flex-col h-full bg-bg-primary', className)}>
      {/* Toolbar */}
      <div className="flex items-center gap-2 p-2 bg-bg-secondary border-b border-border-primary">
        {/* Tools */}
        <div className="flex items-center gap-1">
          <Button
            variant={activeTool === 'select' ? 'primary' : 'ghost'}
            size="sm"
            onClick={() => setActiveTool('select')}
          >
            <MousePointer className="h-4 w-4" />
          </Button>
          <Button
            variant={activeTool === 'pan' ? 'primary' : 'ghost'}
            size="sm"
            onClick={() => setActiveTool('pan')}
          >
            <Hand className="h-4 w-4" />
          </Button>
          <Button
            variant={activeTool === 'measure' ? 'primary' : 'ghost'}
            size="sm"
            onClick={() => setActiveTool('measure')}
          >
            <Ruler className="h-4 w-4" />
          </Button>
          {isGM && (
            <Button
              variant={activeTool === 'add-token' ? 'primary' : 'ghost'}
              size="sm"
              onClick={() => setActiveTool('add-token')}
            >
              <Plus className="h-4 w-4" />
            </Button>
          )}
        </div>

        <div className="h-6 w-px bg-border-primary" />

        {/* Grid Controls */}
        <Button
          variant={showGrid ? 'primary' : 'ghost'}
          size="sm"
          onClick={() => setShowGrid(!showGrid)}
          title="Toggle Grid"
        >
          <Grid3X3 className="h-4 w-4" />
        </Button>

        {isGM && (
          <Button
            variant={showMapLayers ? 'primary' : 'ghost'}
            size="sm"
            onClick={() => setShowMapLayers(!showMapLayers)}
            title="Map Layers"
          >
            <Layers className="h-4 w-4" />
          </Button>
        )}

        <div className="border-l border-border-primary h-6" />

        <Button
          variant="ghost"
          size="sm"
          onClick={() => setZoom(Math.max(0.25, zoom - 0.25))}
          title="Zoom Out"
        >
          <ZoomOut className="h-4 w-4" />
        </Button>

        <span className="text-xs text-text-secondary px-2">
          {Math.round(zoom * 100)}%
        </span>

        <Button
          variant="ghost"
          size="sm"
          onClick={() => setZoom(Math.min(4, zoom + 0.25))}
          title="Zoom In"
        >
          <ZoomIn className="h-4 w-4" />
        </Button>

        <div className="h-6 w-px bg-border-primary" />

        {/* Token Controls (when tokens selected) */}
        {selectedTokens.length > 0 && (
          <div className="flex items-center gap-1">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => rotateSelectedTokens(-45)}
            >
              <RotateCcw className="h-4 w-4" />
            </Button>
            <Button
              variant="ghost"
              size="sm"
              onClick={() => rotateSelectedTokens(45)}
            >
              <RotateCw className="h-4 w-4" />
            </Button>
            {isGM && (
              <>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => selectedTokens.forEach(id => toggleTokenVisibility(id))}
                >
                  <Eye className="h-4 w-4" />
                </Button>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={deleteSelectedTokens}
                >
                  <Trash2 className="h-4 w-4" />
                </Button>
              </>
            )}
            <span className="text-sm text-text-secondary">
              {selectedTokens.length} selected
            </span>
          </div>
        )}

        {/* Spacer */}
        <div className="flex-1" />

        {/* Snap to Grid */}
        <label className="flex items-center gap-2 text-sm text-text-secondary">
          <input
            type="checkbox"
            checked={snapToGrid}
            onChange={(e) => setSnapToGrid(e.target.checked)}
            className="rounded border-border-primary"
          />
          Snap to Grid
        </label>
      </div>

      {/* Map Canvas */}
      <div className="flex-1 relative overflow-hidden bg-gray-100">
        <div
          ref={mapRef}
          className="absolute inset-0 cursor-crosshair"
          onClick={handleMapClick}
          style={{
            transform: `scale(${zoom}) translate(${pan.x}px, ${pan.y}px)`,
            transformOrigin: '0 0'
          }}
        >
          {/* Background */}
          <div 
            className="relative flex-1 overflow-hidden bg-bg-primary"
            style={{
              transform: `scale(${zoom}) translate(${pan.x}px, ${pan.y}px)`,
              transformOrigin: '0 0'
            }}
          >
            {/* Background Image Layer */}
            {backgroundImage && layers.find(l => l.id === 'background')?.visible && (
              <div
                className="absolute inset-0"
                style={{
                  opacity: layers.find(l => l.id === 'background')?.opacity || 1,
                  backgroundImage: `url(${backgroundImage})`,
                  backgroundSize: 'cover',
                  backgroundPosition: 'center',
                  backgroundRepeat: 'no-repeat'
                }}
              />
            )}

            {/* Grid Layer */}
            {showGrid && layers.find(l => l.id === 'grid')?.visible && (
              <svg 
                className="absolute inset-0 pointer-events-none"
                style={{ 
                  width: '100%', 
                  height: '100%',
                  opacity: layers.find(l => l.id === 'grid')?.opacity || 0.3
                }}
              >
                <defs>
                  <pattern
                    id="grid"
                    width={GRID_SIZE}
                    height={GRID_SIZE}
                    patternUnits="userSpaceOnUse"
                  >
                    <path
                      d={`M ${GRID_SIZE} 0 L 0 0 0 ${GRID_SIZE}`}
                      fill="none"
                      stroke="currentColor"
                      strokeWidth="1"
                      className="text-border-primary"
                    />
                  </pattern>
                </defs>
                <rect width="100%" height="100%" fill="url(#grid)" />
              </svg>
            )}

            {/* Tokens */}
            {tokens.map(token => (
              <div
                key={token.id}
                style={getTokenStyle(token)}
                onClick={(e) => {
                  e.stopPropagation();
                  handleTokenSelect(token.id, e.shiftKey);
                }}
                onDoubleClick={(e) => {
                  e.stopPropagation();
                  if (isGM || token.playerId === session?.id) {
                    handleTokenDoubleClick(token);
                  }
                }}
                onMouseDown={(e) => {
                  if (activeTool === 'select') {
                    setIsDragging(true);
                    setDragStart({ 
                      x: e.clientX - token.x * GRID_SIZE * zoom, 
                      y: e.clientY - token.y * GRID_SIZE * zoom 
                    });
                  }
                }}
                title={`${token.name}${token.hitPoints ? ` (${token.hitPoints.current}/${token.hitPoints.max} HP)` : ''}${token.conditions.length > 0 ? ` [${token.conditions.join(', ')}]` : ''}`}
              >
                {token.avatar ? (
                  <img 
                    src={token.avatar} 
                    alt={token.name}
                    className="w-full h-full object-cover rounded-inherit"
                  />
                ) : (
                  <span>{token.name.charAt(0).toUpperCase()}</span>
                )}

                {/* Conditions */}
                {token.conditions.length > 0 && (
                  <div className="absolute -top-1 -right-1 w-3 h-3 bg-warning rounded-full border border-white text-xs flex items-center justify-center text-white">
                    {token.conditions.length}
                  </div>
                )}

                {/* Health Bar (if damaged) */}
                {token.hitPoints && token.hitPoints.current < token.hitPoints.max && (
                  <div className="absolute bottom-0 left-0 right-0 h-1 bg-black bg-opacity-50">
                    <div 
                      className="h-full bg-red-500"
                      style={{ 
                        width: `${(token.hitPoints.current / token.hitPoints.max) * 100}%` 
                      }}
                    />
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Token Properties Panel */}
      {showTokenProperties && selectedToken && (
        <TokenPropertiesPanel
          token={selectedToken}
          onClose={() => {
            setShowTokenProperties(false);
            setSelectedToken(null);
          }}
          onUpdate={handleTokenUpdate}
          onDelete={handleTokenDelete}
          isGM={isGM}
        />
      )}

      {/* Map Layers Panel */}
      {showMapLayers && isGM && (
        <div className="absolute top-16 right-4 w-80 z-50">
          <MapLayersPanel
            layers={layers}
            onLayersUpdate={handleLayersUpdate}
            onBackgroundUpload={handleBackgroundUpload}
            isGM={isGM}
          />
        </div>
      )}
    </div>
  );
}

/**
 * Map Layers Panel - Manage map layers, backgrounds, and visibility
 */

import React, { useState } from 'react';
import { Button } from '../ui/Button';
import { Input } from '../ui/Input';
import { cn } from '../../lib/utils';
import { 
  Eye,
  EyeOff,
  Lock,
  Unlock,
  Upload,
  Trash2,
  Plus,
  Move,
  Layers,
  Image,
  Grid3X3,
  Paintbrush,
  MoreVertical
} from 'lucide-react';
import type { MapLayer } from './BattleMap';

interface MapLayersPanelProps {
  layers: MapLayer[];
  onLayersUpdate: (layers: MapLayer[]) => void;
  onBackgroundUpload: (file: File) => void;
  isGM?: boolean;
  className?: string;
}

const LAYER_TYPES = [
  { type: 'background', label: 'Background', icon: Image, description: 'Base map image or terrain' },
  { type: 'overlay', label: 'Overlay', icon: Grid3X3, description: 'Grid lines, fog, weather effects' },
  { type: 'tokens', label: 'Tokens', icon: Move, description: 'Character and creature tokens' },
  { type: 'effects', label: 'Effects', icon: Paintbrush, description: 'Spell effects, lighting, etc.' }
] as const;

export function MapLayersPanel({_ 
  layers, _onLayersUpdate, 
  _onBackgroundUpload, 
  _isGM = false, _className}: MapLayersPanelProps) {
  const [showAddLayer, setShowAddLayer] = useState(false);
  const [newLayerName, setNewLayerName] = useState('');
  const [newLayerType, setNewLayerType] = useState<MapLayer['type']>('overlay');

  const updateLayer = (_layerId: string, _updates: Partial<MapLayer>) => {
    const updatedLayers = layers.map(layer => 
      layer.id === layerId ? { ...layer, ...updates } : layer
    );
    onLayersUpdate(updatedLayers);
  };

  const deleteLayer = (_layerId: string) => {
    if (layerId === 'background' || layerId === 'tokens') {
      return; // Cannot delete essential layers
    }
    const updatedLayers = layers.filter(layer => layer.id !== layerId);
    onLayersUpdate(updatedLayers);
  };

  const addLayer = () => {
    if (!newLayerName.trim()) return;

    const newLayer: MapLayer = {
      id: `layer_${Date.now()}`,
      name: newLayerName.trim(),
      type: newLayerType,
      visible: true,
      opacity: 1,
      locked: false
    };

    onLayersUpdate([...layers, newLayer]);
    setNewLayerName('');
    setShowAddLayer(false);
  };

  const moveLayer = (_layerId: string, _direction: 'up' | 'down') => {
    const currentIndex = layers.findIndex(layer => layer.id === layerId);
    if (currentIndex === -1) return;

    const newIndex = direction === 'up' ? currentIndex - 1 : currentIndex + 1;
    if (newIndex < 0 || newIndex >= layers.length) return;

    const newLayers = [...layers];
    const currentLayer = newLayers[currentIndex];
    const targetLayer = newLayers[newIndex];
    if (currentLayer && targetLayer) {
      [newLayers[currentIndex], newLayers[newIndex]] = [targetLayer, currentLayer];
      onLayersUpdate(newLayers);
    }
  };

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file && file.type.startsWith('image/')) {
      onBackgroundUpload(file);
    }
    event.target.value = ''; // Reset input
  };

  const getLayerIcon = (type: MapLayer['type']) => {
    const layerType = LAYER_TYPES.find(t => t.type === type);
    return layerType?.icon || Layers;
  };

  return (
    <div className={cn('bg-bg-secondary border border-border-primary rounded-lg', className)}>
      {/* Header */}
      <div className="flex items-center justify-between p-3 border-b border-border-primary">
        <div className="flex items-center gap-2">
          <Layers className="h-4 w-4 text-accent-primary" />
          <h3 className="text-sm font-semibold text-text-primary">Map Layers</h3>
        </div>
        {isGM && (
          <div className="flex items-center gap-1">
            <input
              type="file"
              accept="image/*"
              onChange={handleFileUpload}
              className="hidden"
              id="background-upload"
            />
            <Button
              variant="ghost"
              size="sm"
              onClick={() => document.getElementById('background-upload')?.click()}
              title="Upload Background"
            >
              <Upload className="h-3 w-3" />
            </Button>
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setShowAddLayer(!showAddLayer)}
              title="Add Layer"
            >
              <Plus className="h-3 w-3" />
            </Button>
          </div>
        )}
      </div>

      {/* Add Layer Form */}
      {showAddLayer && isGM && (
        <div className="p-3 border-b border-border-primary bg-bg-tertiary">
          <div className="space-y-2">
            <Input
              placeholder="Layer name"
              value={newLayerName}
              onChange={(e) => setNewLayerName(e.target.value)}
              size="sm"
            />
            <select
              value={newLayerType}
              onChange={(e) => setNewLayerType(e.target.value as MapLayer['type'])}
              className="w-full px-2 py-1 bg-bg-secondary border border-border-primary rounded text-text-primary text-xs"
            >
              {LAYER_TYPES.filter(t => t.type !== 'background' && t.type !== 'tokens').map(type => (
                <option key={type.type} value={type.type}>
                  {type.label}
                </option>
              ))}
            </select>
            <div className="flex gap-1">
              <Button variant="primary" size="sm" onClick={addLayer}>
                Add
              </Button>
              <Button variant="ghost" size="sm" onClick={() => setShowAddLayer(false)}>
                Cancel
              </Button>
            </div>
          </div>
        </div>
      )}

      {/* Layers List */}
      <div className="p-2 space-y-1">
        {layers.map((layer, _index) => {
          const LayerIcon = getLayerIcon(layer.type);
          const isEssential = layer.id === 'background' || layer.id === 'tokens';

          return (
            <div
              key={layer.id}
              className={cn(
                'flex items-center gap-2 p-2 rounded transition-colors',
                layer.visible ? 'bg-bg-primary' : 'bg-bg-tertiary opacity-60'
              )}
            >
              {/* Layer Icon */}
              <LayerIcon className="h-3 w-3 text-text-secondary flex-shrink-0" />

              {/* Layer Info */}
              <div className="flex-1 min-w-0">
                <div className="text-xs font-medium text-text-primary truncate">
                  {layer.name}
                </div>
                <div className="text-xs text-text-tertiary">
                  {LAYER_TYPES.find(t => t.type === layer.type)?.label}
                </div>
              </div>

              {/* Opacity Slider */}
              <div className="flex items-center gap-1">
                <input
                  type="range"
                  min="0"
                  max="100"
                  value={layer.opacity * 100}
                  onChange={(e) => updateLayer(layer.id, { opacity: parseInt(e.target.value) / 100 })}
                  className="w-12 h-1 bg-bg-tertiary rounded-lg appearance-none cursor-pointer"
                  disabled={!isGM}
                />
                <span className="text-xs text-text-tertiary w-6 text-right">
                  {Math.round(layer.opacity * 100)}
                </span>
              </div>

              {/* Controls */}
              <div className="flex items-center gap-1">
                {/* Visibility Toggle */}
                <button
                  onClick={() => updateLayer(layer.id, { visible: !layer.visible })}
                  disabled={!isGM}
                  className={cn(
                    'p-1 rounded hover:bg-bg-tertiary transition-colors',
                    !isGM && 'cursor-default'
                  )}
                  title={layer.visible ? 'Hide Layer' : 'Show Layer'}
                >
                  {layer.visible ? (
                    <Eye className="h-3 w-3 text-text-secondary" />
                  ) : (
                    <EyeOff className="h-3 w-3 text-text-tertiary" />
                  )}
                </button>

                {/* Lock Toggle */}
                {isGM && (
                  <button
                    onClick={() => updateLayer(layer.id, { locked: !layer.locked })}
                    className="p-1 rounded hover:bg-bg-tertiary transition-colors"
                    title={layer.locked ? 'Unlock Layer' : 'Lock Layer'}
                  >
                    {layer.locked ? (
                      <Lock className="h-3 w-3 text-warning" />
                    ) : (
                      <Unlock className="h-3 w-3 text-text-secondary" />
                    )}
                  </button>
                )}

                {/* Move Controls */}
                {isGM && (
                  <div className="flex flex-col">
                    <button
                      onClick={() => moveLayer(layer.id, 'up')}
                      disabled={index === 0}
                      className="p-0.5 rounded hover:bg-bg-tertiary transition-colors disabled:opacity-30"
                      title="Move Up"
                    >
                      <div className="w-2 h-1 bg-text-secondary rounded-sm transform rotate-180" style={{ clipPath: 'polygon(50% 0%, 0% 100%, 100% 100%)' }} />
                    </button>
                    <button
                      onClick={() => moveLayer(layer.id, 'down')}
                      disabled={index === layers.length - 1}
                      className="p-0.5 rounded hover:bg-bg-tertiary transition-colors disabled:opacity-30"
                      title="Move Down"
                    >
                      <div className="w-2 h-1 bg-text-secondary rounded-sm" style={{ clipPath: 'polygon(50% 100%, 0% 0%, 100% 0%)' }} />
                    </button>
                  </div>
                )}

                {/* Delete Button */}
                {isGM && !isEssential && (
                  <button
                    onClick={() => deleteLayer(layer.id)}
                    className="p-1 rounded hover:bg-bg-tertiary transition-colors text-danger"
                    title="Delete Layer"
                  >
                    <Trash2 className="h-3 w-3" />
                  </button>
                )}
              </div>
            </div>
          );
        })}

        {layers.length === 0 && (
          <div className="text-center py-4 text-text-secondary text-xs">
            No layers found
          </div>
        )}
      </div>

      {/* Layer Info */}
      <div className="p-2 border-t border-border-primary bg-bg-tertiary">
        <div className="text-xs text-text-secondary">
          <div className="flex justify-between">
            <span>Visible Layers:</span>
            <span>{layers.filter(l => l.visible).length}/{layers.length}</span>
          </div>
          <div className="mt-1 text-xs text-text-tertiary">
            Layers are rendered from top to bottom. Higher layers appear above lower layers.
          </div>
        </div>
      </div>
    </div>
  );
}

/**
 * Map Upload Modal - Handle map image uploads with metadata
 */
import React, { useState, useRef } from 'react';
import { 
  Upload, 
  X, 
  Image, 
  FileImage, 
  Grid3X3,
  Ruler,
  AlertCircle,
  CheckCircle,
  Loader2
} from 'lucide-react';
import { Button } from '../ui/Button';
import { Input } from '../ui/Input';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/Card';
import { cn } from '../../lib/utils';

interface MapUploadModalProps {
  isOpen: boolean;
  onClose: () => void;
  onUploadComplete: (_map: any) => void;
  campaignId?: string;
}

interface UploadState {
  file: File | null;
  preview: string | null;
  uploading: boolean;
  error: string | null;
  progress: number;
}

interface MapMetadata {
  name: string;
  description: string;
  gridSize: number;
  gridUnits: string;
  realWorldScale: number; // pixels per unit
}

export function MapUploadModal({_ isOpen, _onClose, _onUploadComplete, _campaignId}: MapUploadModalProps) {
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [uploadState, setUploadState] = useState<UploadState>({
    file: null,
    preview: null,
    uploading: false,
    error: null,
    progress: 0
  });

  const [metadata, setMetadata] = useState<MapMetadata>({
    name: '',
    description: '',
    gridSize: 70,
    gridUnits: 'feet',
    realWorldScale: 70 // 70 pixels = 5 feet (default D&D scale)
  });

  const [dragOver, setDragOver] = useState(false);

  if (!isOpen) return null;

  const handleFileSelect = (file: File) => {
    if (!file.type.startsWith('image/')) {
      setUploadState(prev => ({ ...prev, error: 'Please select a valid image file' }));
      return;
    }

    if (file.size > 50 * 1024 * 1024) { // 50MB limit
      setUploadState(prev => ({ ...prev, error: 'File size must be less than 50MB' }));
      return;
    }

    const reader = new FileReader();
    reader.onload = (e) => {
      const preview = e.target?.result as string;
      setUploadState(prev => ({
        ...prev,
        file,
        preview,
        error: null
      }));

      // Auto-generate name from filename if not set
      if (!metadata.name) {
        const nameWithoutExt = file.name.replace(/\.[^/.]+$/, "");
        setMetadata(prev => ({ ...prev, name: nameWithoutExt }));
      }
    };
    reader.readAsDataURL(file);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setDragOver(false);

    const files = Array.from(e.dataTransfer.files);
    if (files.length > 0) {
      handleFileSelect(files[0]);
    }
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setDragOver(true);
  };

  const handleDragLeave = () => {
    setDragOver(false);
  };

  const handleUpload = async () => {
    if (!uploadState.file) return;

    setUploadState(prev => ({ ...prev, uploading: true, error: null, progress: 0 }));

    try {
      const formData = new FormData();
      formData.append('map', uploadState.file);
      formData.append('metadata', JSON.stringify({
        ...metadata,
        campaignId
      }));

      const response = await fetch('/api/maps/upload', {
        method: 'POST',
        body: formData,
        credentials: 'include'
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Upload failed');
      }

      const result = await response.json();

      setUploadState(prev => ({ ...prev, uploading: false, progress: 100 }));

      // Call success callback
      onUploadComplete(result.map);

      // Reset and close
      setTimeout(() => {
        handleClose();
      }, 1000);

    } catch (error) {
      console.error('Upload error:', error);
      setUploadState(prev => ({
        ...prev,
        uploading: false,
        error: error instanceof Error ? error.message : 'Upload failed'
      }));
    }
  };

  const handleClose = () => {
    setUploadState({
      file: null,
      preview: null,
      uploading: false,
      error: null,
      progress: 0
    });
    setMetadata({
      name: '',
      description: '',
      gridSize: 70,
      gridUnits: 'feet',
      realWorldScale: 70
    });
    onClose();
  };

  const canUpload = uploadState.file && metadata.name.trim() && !uploadState.uploading;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <Card className="w-full max-w-2xl max-h-[90vh] overflow-y-auto">
        <CardHeader className="flex flex-row items-center justify-between pb-4">
          <CardTitle className="flex items-center gap-2">
            <Upload className="h-5 w-5" />
            Upload Map
          </CardTitle>
          <Button variant="ghost" size="icon" onClick={handleClose}>
            <X className="h-4 w-4" />
          </Button>
        </CardHeader>

        <CardContent className="space-y-6">
          {/* File Upload Area */}
          <div
            className={cn(
              "border-2 border-dashed rounded-lg p-8 text-center transition-colors",
              dragOver ? "border-primary-500 bg-primary-50" : "border-neutral-300",
              uploadState.file ? "border-success-300 bg-success-50" : ""
            )}
            onDrop={handleDrop}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
          >
            {uploadState.preview ? (
              <div className="space-y-4">
                <img 
                  src={uploadState.preview} 
                  alt="Map preview" 
                  className="max-h-48 mx-auto rounded-lg shadow-md"
                />
                <div className="flex items-center justify-center gap-2 text-success-700">
                  <CheckCircle className="h-4 w-4" />
                  <span className="font-medium">{uploadState.file?.name}</span>
                </div>
                <Button 
                  variant="outline" 
                  size="sm"
                  onClick={() => fileInputRef.current?.click()}
                >
                  Choose Different File
                </Button>
              </div>
            ) : (
              <div className="space-y-4">
                <div className="mx-auto w-16 h-16 bg-neutral-100 rounded-full flex items-center justify-center">
                  <FileImage className="h-8 w-8 text-neutral-500" />
                </div>
                <div>
                  <p className="text-lg font-medium text-neutral-900">
                    Drop your map image here
                  </p>
                  <p className="text-neutral-600 mt-1">
                    or click to browse files
                  </p>
                </div>
                <Button 
                  variant="primary"
                  onClick={() => fileInputRef.current?.click()}
                >
                  <Upload className="h-4 w-4 mr-2" />
                  Choose File
                </Button>
                <p className="text-xs text-neutral-500">
                  Supports: JPG, PNG, WEBP, GIF (max 50MB)
                </p>
              </div>
            )}
          </div>

          <input
            ref={fileInputRef}
            type="file"
            accept="image/*"
            className="hidden"
            onChange={(e) => {
              const file = e.target.files?.[0];
              if (file) handleFileSelect(file);
            }}
          />

          {/* Error Display */}
          {uploadState.error && (
            <div className="flex items-center gap-2 p-3 bg-error-50 border border-error-200 rounded-lg text-error-800">
              <AlertCircle className="h-4 w-4" />
              <span className="text-sm">{uploadState.error}</span>
            </div>
          )}

          {/* Map Metadata */}
          <div className="space-y-4">
            <h4 className="font-medium text-neutral-900">Map Details</h4>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-neutral-700 mb-1">
                  Map Name *
                </label>
                <Input
                  value={metadata.name}
                  onChange={(e) => setMetadata(prev => ({ ...prev, name: e.target.value }))}
                  placeholder="Enter map name"
                  disabled={uploadState.uploading}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-neutral-700 mb-1">
                  Grid Size (pixels)
                </label>
                <Input
                  type="number"
                  value={metadata.gridSize}
                  onChange={(e) => setMetadata(prev => ({ ...prev, gridSize: parseInt(e.target.value) || 70 }))}
                  min="20"
                  max="200"
                  disabled={uploadState.uploading}
                />
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-neutral-700 mb-1">
                Description
              </label>
              <textarea
                value={metadata.description}
                onChange={(e) => setMetadata(prev => ({ ...prev, description: e.target.value }))}
                placeholder="Optional description for this map"
                className="w-full px-3 py-2 border border-neutral-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500 resize-none"
                rows={3}
                disabled={uploadState.uploading}
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-neutral-700 mb-1">
                  Grid Units
                </label>
                <select
                  value={metadata.gridUnits}
                  onChange={(e) => setMetadata(prev => ({ ...prev, gridUnits: e.target.value }))}
                  className="w-full px-3 py-2 border border-neutral-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
                  disabled={uploadState.uploading}
                >
                  <option value="feet">Feet</option>
                  <option value="meters">Meters</option>
                  <option value="squares">Squares</option>
                  <option value="hexes">Hexes</option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-neutral-700 mb-1">
                  Real World Scale
                </label>
                <Input
                  type="number"
                  value={metadata.realWorldScale}
                  onChange={(e) => setMetadata(prev => ({ ...prev, realWorldScale: parseInt(e.target.value) || 70 }))}
                  placeholder="Pixels per unit"
                  disabled={uploadState.uploading}
                />
              </div>
            </div>
          </div>

          {/* Upload Progress */}
          {uploadState.uploading && (
            <div className="space-y-2">
              <div className="flex items-center justify-between text-sm">
                <span>Uploading...</span>
                <span>{uploadState.progress}%</span>
              </div>
              <div className="w-full bg-neutral-200 rounded-full h-2">
                <div 
                  className="bg-primary-600 h-2 rounded-full transition-all duration-300"
                  style={{ width: `${uploadState.progress}%` }}
                />
              </div>
            </div>
          )}

          {/* Action Buttons */}
          <div className="flex justify-end gap-3 pt-4 border-t">
            <Button variant="outline" onClick={handleClose} disabled={uploadState.uploading}>
              Cancel
            </Button>
            <Button 
              variant="primary" 
              onClick={handleUpload}
              disabled={!canUpload}
            >
              {uploadState.uploading ? (
                <>
                  <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                  Uploading...
                </>
              ) : (
                <>
                  <Upload className="h-4 w-4 mr-2" />
                  Upload Map
                </>
              )}
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

/**
 * Interactive Map Viewer for VTT sessions
 * Handles grid rendering, token placement, and real-time collaboration
 */
import React, { useRef, useEffect, useState, useCallback } from 'react';
import { 
  ZoomIn, 
  ZoomOut, 
  RotateCcw, 
  Move, 
  Square,
  Circle,
  MousePointer,
  Grid3X3,
  Lightbulb,
  Eye,
  Ruler,
  Settings,
  Users
} from 'lucide-react';
import { Button } from '../ui/Button';
import { _cn } from '../../lib/utils';
import { useAuth } from '../../hooks/useAuth';
import { useWebSocket, _VTTWebSocketMessage} from '../../hooks/useWebSocket';
import { Card, CardContent, _CardHeader, _CardTitle } from '../ui/Card';

interface MapViewerProps {
  sceneId: string;
  campaignId: string;
  isGM: boolean;
  onTokenSelect?: (_tokenId: string) => void;
  onTokenMove?: (_tokenId: string, x: number, _y: number) => void;
}

interface Token {
  id: string;
  name: string;
  x: number;
  y: number;
  width: number;
  height: number;
  rotation: number;
  scale: number;
  disposition: 'FRIENDLY' | 'NEUTRAL' | 'HOSTILE' | 'UNKNOWN';
  isVisible: boolean;
  isLocked: boolean;
  layer: number;
  actorId?: string;
  assetId?: string;
}

interface SpellEffect {
  id: string;
  type: 'spell_cast' | 'area_effect' | 'spell_impact' | 'projectile_hit' | 'spell_effect_expired' | 'projectile';
  position: { x: number; y: number };
  spell?: string;
  school?: string;
  duration: number;
  animation: 'pulse' | 'expand' | 'flash' | 'projectile' | 'beam';
  color: string;
  opacity?: number;
  area?: {
    type: 'sphere' | 'cube' | 'cylinder' | 'line' | 'cone';
    size: number;
  };
  targetId?: string;
  startTime: number;
  // Projectile-specific properties
  targetPosition?: { x: number; y: number };
  speed?: number;
  trail?: boolean;
}

interface SpellProjectile {
  id: string;
  spell: string;
  startPosition: { x: number; y: number };
  targetPosition: { x: number; y: number };
  currentPosition: { x: number; y: number };
  speed: number;
  color: string;
  size: number;
  trail: { x: number; y: number }[];
  startTime: number;
  duration: number;
  active: boolean;
}

interface SpellTemplate {
  id: string;
  spell: string;
  area: {
    type: 'sphere' | 'cube' | 'cylinder' | 'line' | 'cone';
    size: number;
  };
  position: { x: number; y: number };
  color: string;
  opacity: number;
}

interface GridSettings {
  type: 'square' | 'hex' | 'none';
  size: number;
  offsetX: number;
  offsetY: number;
  visible: boolean;
  color: string;
  opacity: number;
}

interface MapScene {
  id: string;
  name: string;
  width: number;
  height: number;
  campaignId: string;
  mapId: string | null;
  grid: GridSettings;
  tokens?: Token[];
  map?: {
    id: string;
    name: string;
    widthPx: number;
    heightPx: number;
    assets: Array<{
      uri: string;
      mimeType: string;
      width: number;
      height: number;
    }>;
  } | null;
}

type Tool = 'select' | 'move' | 'measure' | 'draw' | 'fog';

export const MapViewer: React.FC<MapViewerProps> = ({_sceneId, _campaignId, _isGM, _onTokenSelect, _onTokenMove}) => {
  const { user  } = useAuth();
  const sessionId = `session_${campaignId}_${sceneId}`;

  // WebSocket integration for real-time collaboration
  const { _isConnected, _send,  lastMessage, _connectedUsers} = useWebSocket({
    sessionId,
    userId: user?.id || 'anonymous',
    campaignId,
    isGM,
    autoConnect: !!user
  });

  const canvasRef = useRef<HTMLCanvasElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);

  const [scene, setScene] = useState<MapScene | null>(null);
  const [selectedTool, setSelectedTool] = useState<Tool>('select');
  const [zoom, setZoom] = useState(1);
  const [pan, setPan] = useState({ x: 0, y: 0 });
  const [selectedToken, setSelectedToken] = useState<string | null>(null);
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [isGridVisible, setIsGridVisible] = useState(true);
  const [mapImage, setMapImage] = useState<HTMLImageElement | null>(null);
  const [loading, setLoading] = useState(true);
  const [spellEffects, setSpellEffects] = useState<SpellEffect[]>([]);
  const [spellProjectiles, setSpellProjectiles] = useState<SpellProjectile[]>([]);
  const [spellTemplate, setSpellTemplate] = useState<SpellTemplate | null>(null);
  const [castingMode, setCastingMode] = useState<{
    active: boolean;
    spell?: any;
    step: 'select_spell' | 'target' | 'position';
  }>({ active: false, step: 'select_spell' });

  // Load scene data
  useEffect(() => {
    const loadScene = async () => {
      try {
        const response = await fetch(`/api/maps/scenes/${sceneId}`);
        if (response.ok) {
          const data = await response.json();
          setScene(data.scene);
        }
      } catch (error) {
        console.error('Failed to load scene:', error);
      } finally {
        setLoading(false);
      }
    };

    loadScene();
  }, [sceneId]);

  // Load map image when scene changes
  useEffect(() => {
    if (scene?.map?.assets?.[0]?.uri) {
      const img = new Image();
      img.onload = () => {
        setMapImage(img);
      };
      img.onerror = () => {
        console.error('Failed to load map image');
        setMapImage(null);
      };
      img.src = scene.map.assets[0].uri;
    } else {
      setMapImage(null);
    }
  }, [scene?.map]);

  // Handle WebSocket messages for real-time collaboration
  useEffect(() => {
    if (!lastMessage) return;

    switch (lastMessage.type) {
      case 'token_move': {
        const { tokenId,  x,  y  } = lastMessage.payload;
        setScene(prevScene => {
          if (!prevScene || !prevScene.tokens) return prevScene;
          const updatedTokens = prevScene.tokens.map(token => 
            token.id === tokenId ? { ...token, x, y } : token
          );
          return { ...prevScene, tokens: updatedTokens };
        });
    }
        break;

      case 'token_add': {
        const { token  } = lastMessage.payload;
        setScene(prevScene => {
          if (!prevScene) return prevScene;
          const currentTokens = prevScene.tokens || [];
          return { ...prevScene, tokens: [...currentTokens, token] };
        });
    }
        break;

      case 'token_remove': {
        const { _tokenId: removeId  } = lastMessage.payload;
        setScene(prevScene => {
          if (!prevScene || !prevScene.tokens) return prevScene;
          return { 
            ...prevScene, 
            tokens: prevScene.tokens.filter(t => t.id !== removeId) 
          };
        });
    }
        break;

      case 'scene_update': {
        const { sceneData  } = lastMessage.payload;
        setScene(prevScene => ({ ...prevScene, ...sceneData }));
        // Handle spell effects and projectiles
        if (lastMessage.payload.type === 'spell_cast' && lastMessage.payload.visualEffects) {
          const newEffects = lastMessage.payload.visualEffects.map((effect: any) => ({
            ...effect,
            startTime: Date.now()
          }));
          setSpellEffects(prev => [...prev, ...newEffects]);
        }

        // Handle spell projectiles
        if (lastMessage.payload.type === 'spell_projectile_start') {
          const projectile: SpellProjectile = {
            id: lastMessage.payload.projectileId,
            spell: lastMessage.payload.spell,
            startPosition: lastMessage.payload.startPosition,
            targetPosition: lastMessage.payload.targetPosition,
            currentPosition: { ...lastMessage.payload.startPosition },
            speed: lastMessage.payload.speed || 300, // pixels per second
            color: lastMessage.payload.color || '#FF4500',
            size: lastMessage.payload.size || 8,
            trail: [],
            startTime: Date.now(),
            duration: lastMessage.payload.duration || 2000,
            active: true
          };
          setSpellProjectiles(prev => [...prev, projectile]);
        }

        if (lastMessage.payload.type === 'spell_projectile_hit') {
          setSpellProjectiles(prev => 
            prev.map(p => 
              p.id === lastMessage.payload.projectileId
                ? { ...p, active: false }
                : p
            )
          );
        }
    }
        break;
    }
  }, [lastMessage]);

  // Convert screen coordinates to world coordinates
  const screenToWorld = useCallback((_screenX: number, _screenY: number) => {
    if (!containerRef.current) return { x: 0, y: 0 };

    const rect = containerRef.current.getBoundingClientRect();
    const x = (screenX - rect.left - pan.x) / zoom;
    const y = (screenY - rect.top - pan.y) / zoom;

    return { x, y };
  }, [zoom, pan]);

  // Convert world coordinates to grid coordinates
  const worldToGrid = useCallback((_worldX: number, _worldY: number) => {
    if (!scene) return { gridX: 0, gridY: 0 };

    const { grid  } = scene;
    const adjustedX = worldX - grid.offsetX;
    const adjustedY = worldY - grid.offsetY;

    return {
      gridX: Math.floor(adjustedX / grid.size),
      gridY: Math.floor(adjustedY / grid.size)
    };
  }, [scene]);

  // Snap coordinates to grid
  const snapToGrid = useCallback((_worldX: number, _worldY: number) => {
    if (!scene || scene.grid.type === 'none') return { x: worldX, y: worldY };

    const { gridX,  gridY  } = worldToGrid(worldX, worldY);
    const { grid  } = scene;

    return {
      x: gridX * grid.size + grid.offsetX + grid.size / 2,
      y: gridY * grid.size + grid.offsetY + grid.size / 2
    };
  }, [scene, worldToGrid]);

  // Render grid
  const renderGrid = useCallback((_ctx: CanvasRenderingContext2D) => {
    if (!scene || !isGridVisible || scene.grid.type === 'none') return;

    const { grid,  width,  height  } = scene;

    ctx.save();
    ctx.strokeStyle = grid.color;
    ctx.globalAlpha = grid.opacity;
    ctx.lineWidth = 1 / zoom;

    // Draw vertical lines
    for (let x = grid.offsetX; x < width; x += grid.size) {
      ctx.beginPath();
      ctx.moveTo(x, 0);
      ctx.lineTo(x, height);
      ctx.stroke();
    }

    // Draw horizontal lines
    for (let y = grid.offsetY; y < height; y += grid.size) {
      ctx.beginPath();
      ctx.moveTo(0, y);
      ctx.lineTo(width, y);
      ctx.stroke();
    }

    ctx.restore();
  }, [scene, isGridVisible, zoom]);

  // Render tokens
  const renderTokens = useCallback((_ctx: CanvasRenderingContext2D) => {
    if (!scene?.tokens) return;

    scene.tokens.forEach(token => {
      if (!token.isVisible && !isGM) return;

      ctx.save();

      // Token appearance based on disposition
      const colors = {
        FRIENDLY: '#22c55e',
        NEUTRAL: '#6b7280',
        HOSTILE: '#ef4444',
        UNKNOWN: '#8b5cf6'
      };

      const size = scene.grid.size * token.scale;
      const _x = token.x - size / 2;
      const _y = token.y - size / 2;

      // Token background
      ctx.fillStyle = colors[token.disposition];
      ctx.globalAlpha = token.isVisible ? 1 : 0.5;

      if (scene.grid.type === 'hex') {
        // Draw hexagon
        ctx.beginPath();
        for (let i = 0; i < 6; i++) {
          const angle = (i * Math.PI) / 3;
          const px = token.x + (size / 2) * Math.cos(angle);
          const py = token.y + (size / 2) * Math.sin(angle);
          if (i === 0) ctx.moveTo(px, py);
          else ctx.lineTo(px, py);
        }
        ctx.closePath();
        ctx.fill();
      } else {
        // Draw circle
        ctx.beginPath();
        ctx.arc(token.x, token.y, size / 2, 0, 2 * Math.PI);
        ctx.fill();
      }

      // Token border if selected
      if (selectedToken === token.id) {
        ctx.strokeStyle = '#3b82f6';
        ctx.lineWidth = 3 / zoom;
        ctx.stroke();
      }

      // Token name
      if (isGM || token.isVisible) {
        ctx.fillStyle = '#ffffff';
        ctx.font = `${12 / zoom}px sans-serif`;
        ctx.textAlign = 'center';
        ctx.fillText(token.name, token.x, token.y + size / 2 + 15 / zoom);
      }

      ctx.restore();
    });
  }, [scene, selectedToken, isGM, zoom]);

  // Render spell effects
  const renderSpellEffects = useCallback((_ctx: CanvasRenderingContext2D) => {
    const now = Date.now();

    spellEffects.forEach(effect => {
      const elapsed = now - effect.startTime;
      if (elapsed > effect.duration) return; // Effect expired

      const progress = elapsed / effect.duration;
      const alpha = effect.opacity || (1 - progress); // Fade out over time

      ctx.save();
      ctx.globalAlpha = alpha;

      switch (effect.type) {
        case 'spell_cast': {
          // Pulsing circle at caster position
          const pulseRadius = 20 + Math.sin(elapsed / 100) * 5;
          ctx.strokeStyle = effect.color;
          ctx.lineWidth = 3;
          ctx.setLineDash([5, 5]);
          ctx.beginPath();
          ctx.arc(effect.position.x, effect.position.y, pulseRadius, 0, 2 * Math.PI);
          ctx.stroke();
          ctx.setLineDash([]);
    }
          break;

        case 'area_effect':
          // Expanding area effect
          if (effect.area) {
            const size = effect.area.size * progress;
            ctx.fillStyle = effect.color;
            ctx.globalAlpha = alpha * 0.3;

            switch (effect.area.type) {
              case 'sphere':
              case 'cylinder':
                ctx.beginPath();
                ctx.arc(effect.position.x, effect.position.y, size, 0, 2 * Math.PI);
                ctx.fill();
                break;
              case 'cube':
                ctx.fillRect(
                  effect.position.x - size/2,
                  effect.position.y - size/2,
                  size,
                  size
                );
                break;
              case 'cone':
                // Draw cone shape
                ctx.beginPath();
                ctx.moveTo(effect.position.x, effect.position.y);
                ctx.arc(effect.position.x, effect.position.y, size, -Math.PI/6, Math.PI/6);
                ctx.closePath();
                ctx.fill();
                break;
              case 'line':
                // Draw line
                ctx.fillRect(
                  effect.position.x,
                  effect.position.y - 2.5,
                  size,
                  5
                );
                break;
            }

            // Add border
            ctx.globalAlpha = alpha;
            ctx.strokeStyle = effect.color;
            ctx.lineWidth = 2;
            ctx.stroke();
          }
          break;

        case 'spell_impact': {
          // Flash effect at target
          const flashIntensity = Math.max(0, 1 - progress * 2);
          ctx.fillStyle = effect.color;
          ctx.globalAlpha = flashIntensity;
          ctx.beginPath();
          ctx.arc(effect.position.x, effect.position.y, 15, 0, 2 * Math.PI);
          ctx.fill();

          // Add sparks
          for (let i = 0; i < 8; i++) {
            const angle = (i / 8) * Math.PI * 2;
            const sparkDistance = 20 * progress;
            const sparkX = effect.position.x + Math.cos(angle) * sparkDistance;
            const sparkY = effect.position.y + Math.sin(angle) * sparkDistance;

            ctx.fillStyle = effect.color;
            ctx.globalAlpha = flashIntensity * 0.7;
            ctx.beginPath();
            ctx.arc(sparkX, sparkY, 2, 0, 2 * Math.PI);
            ctx.fill();
          }
    }
          break;

        case 'projectile':
          // Moving projectile effect
          if (effect.targetPosition) {
            const totalDistance = Math.sqrt(
              Math.pow(effect.targetPosition.x - effect.position.x, 2) + 
              Math.pow(effect.targetPosition.y - effect.position.y, 2)
            );
            const currentDistance = totalDistance * progress;
            const angle = Math.atan2(
              effect.targetPosition.y - effect.position.y,
              effect.targetPosition.x - effect.position.x
            );

            const currentX = effect.position.x + Math.cos(angle) * currentDistance;
            const currentY = effect.position.y + Math.sin(angle) * currentDistance;

            // Draw projectile
            ctx.fillStyle = effect.color;
            ctx.globalAlpha = alpha;
            ctx.beginPath();
            ctx.arc(currentX, currentY, 6, 0, 2 * Math.PI);
            ctx.fill();

            // Draw trail if enabled
            if (effect.trail) {
              ctx.strokeStyle = effect.color;
              ctx.lineWidth = 3;
              ctx.globalAlpha = alpha * 0.5;
              ctx.beginPath();
              ctx.moveTo(effect.position.x, effect.position.y);
              ctx.lineTo(currentX, currentY);
              ctx.stroke();
            }
          }
          break;
      }

      ctx.restore();
    });

    // Clean up expired effects
    setSpellEffects(prev => prev.filter(effect => 
      now - effect.startTime < effect.duration
    ));
  }, [spellEffects]);

  // Render spell projectiles
  const renderSpellProjectiles = useCallback((_ctx: CanvasRenderingContext2D) => {
    const now = Date.now();

    spellProjectiles.forEach(projectile => {
      if (!projectile.active) return;

      const elapsed = now - projectile.startTime;
      if (elapsed > projectile.duration) return;

      // Calculate current position
      const progress = Math.min(elapsed / projectile.duration, 1);
      const totalDistance = Math.sqrt(
        Math.pow(projectile.targetPosition.x - projectile.startPosition.x, 2) + 
        Math.pow(projectile.targetPosition.y - projectile.startPosition.y, 2)
      );

      const angle = Math.atan2(
        projectile.targetPosition.y - projectile.startPosition.y,
        projectile.targetPosition.x - projectile.startPosition.x
      );

      const currentDistance = totalDistance * progress;
      const currentX = projectile.startPosition.x + Math.cos(angle) * currentDistance;
      const currentY = projectile.startPosition.y + Math.sin(angle) * currentDistance;

      // Update projectile position for trail
      projectile.currentPosition = { x: currentX, y: currentY };
      projectile.trail.push({ x: currentX, y: currentY });

      // Keep trail length manageable
      if (projectile.trail.length > 10) {
        projectile.trail.shift();
      }

      ctx.save();

      // Draw trail
      if (projectile.trail.length > 1) {
        ctx.strokeStyle = projectile.color;
        ctx.lineWidth = projectile.size / 2;
        ctx.globalAlpha = 0.6;
        ctx.beginPath();

        for (let i = 0; i < projectile.trail.length - 1; i++) {
          const trailAlpha = (i / projectile.trail.length) * 0.6;
          ctx.globalAlpha = trailAlpha;

          const point = projectile.trail[i];
          if (point) {
            if (i === 0) {
              ctx.moveTo(point.x, point.y);
            } else {
              ctx.lineTo(point.x, point.y);
            }
          }
        }
        ctx.stroke();
      }

      // Draw projectile body
      ctx.fillStyle = projectile.color;
      ctx.globalAlpha = 1;
      ctx.beginPath();
      ctx.arc(currentX, currentY, projectile.size, 0, 2 * Math.PI);
      ctx.fill();

      // Add glow effect
      ctx.shadowColor = projectile.color;
      ctx.shadowBlur = 10;
      ctx.beginPath();
      ctx.arc(currentX, currentY, projectile.size * 0.7, 0, 2 * Math.PI);
      ctx.fill();

      ctx.restore();
    });

    // Clean up inactive projectiles
    setSpellProjectiles(prev => prev.filter(projectile => 
      projectile.active && (now - projectile.startTime < projectile.duration)
    ));
  }, [spellProjectiles]);

  // Render spell template preview
  const renderSpellTemplate = useCallback((_ctx: CanvasRenderingContext2D) => {
    if (!spellTemplate) return;

    ctx.save();
    ctx.globalAlpha = spellTemplate.opacity;
    ctx.fillStyle = spellTemplate.color;
    ctx.strokeStyle = spellTemplate.color;
    ctx.lineWidth = 2;
    ctx.setLineDash([5, 5]);

    const size = spellTemplate.area.size;

    switch (spellTemplate.area.type) {
      case 'sphere':
      case 'cylinder':
        ctx.beginPath();
        ctx.arc(spellTemplate.position.x, spellTemplate.position.y, size, 0, 2 * Math.PI);
        ctx.fill();
        ctx.stroke();
        break;
      case 'cube':
        ctx.fillRect(
          spellTemplate.position.x - size/2,
          spellTemplate.position.y - size/2,
          size,
          size
        );
        ctx.strokeRect(
          spellTemplate.position.x - size/2,
          spellTemplate.position.y - size/2,
          size,
          size
        );
        break;
      case 'cone':
        ctx.beginPath();
        ctx.moveTo(spellTemplate.position.x, spellTemplate.position.y);
        ctx.arc(spellTemplate.position.x, spellTemplate.position.y, size, -Math.PI/6, Math.PI/6);
        ctx.closePath();
        ctx.fill();
        ctx.stroke();
        break;
      case 'line':
        ctx.fillRect(
          spellTemplate.position.x,
          spellTemplate.position.y - 2.5,
          size,
          5
        );
        ctx.strokeRect(
          spellTemplate.position.x,
          spellTemplate.position.y - 2.5,
          size,
          5
        );
        break;
    }

    ctx.setLineDash([]);
    ctx.restore();
  }, [spellTemplate]);

  // Main render function
  const render = useCallback(() => {
    const canvas = canvasRef.current;
    if (!canvas || !scene) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Apply transform
    ctx.save();
    ctx.scale(zoom, zoom);
    ctx.translate(pan.x / zoom, pan.y / zoom);

    // Render background
    if (mapImage && scene.map) {
      // Draw the map image scaled to scene dimensions
      ctx.drawImage(
        mapImage, 
        0, 0, 
        scene.map.widthPx, 
        scene.map.heightPx,
        0, 0, 
        scene.width, 
        scene.height
      );
    } else {
      // Fallback background
      ctx.fillStyle = '#f3f4f6';
      ctx.fillRect(0, 0, scene.width, scene.height);
    }

    // Render grid
    renderGrid(ctx);

    // Render tokens
    renderTokens(ctx);

    // Render spell template preview
    renderSpellTemplate(ctx);

    // Render spell effects
    renderSpellEffects(ctx);

    // Render spell projectiles
    renderSpellProjectiles(ctx);

    ctx.restore();
  }, [scene, zoom, pan, renderGrid, renderTokens]);

  // Handle canvas resize
  useEffect(() => {
    const canvas = canvasRef.current;
    const container = containerRef.current;
    if (!canvas || !container) return;

    const resizeCanvas = () => {
      const rect = container.getBoundingClientRect();
      canvas.width = rect.width;
      canvas.height = rect.height;
      render();
    };

    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);
    return () => window.removeEventListener('resize', resizeCanvas);
  }, [render]);

  // Render when dependencies change
  useEffect(() => {
    render();
  }, [render]);

  // Handle spell casting
  const handleCastSpell = useCallback(async (spell: any, _targets?: string[], _position?: { x: number; y: number }) => {
    if (!scene || !selectedToken) return;

    try {
      const response = await fetch(`/api/maps/scenes/${scene.id}/cast-spell`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          casterId: selectedToken,
          spell,
          targets,
          position,
          spellLevel: spell.level
        })
      });

      if (response.ok) {
        const _result = await response.json();
        // Visual effects will be handled via WebSocket
        setCastingMode({ active: false, step: 'select_spell' });
        setSpellTemplate(null);
      }
    } catch (error) {
      console.error('Failed to cast spell:', error);
    }
  }, [scene, selectedToken]);

  // Handle spell template preview
  const handleSpellPreview = useCallback((_spell: any, position: { x: number; y: number }) => {
    if (!spell.area) {
      setSpellTemplate(null);
      return;
    }

    const schoolColors: Record<string, string> = {
      'abjuration': '#4169E1',
      'conjuration': '#FFD700',
      'divination': '#E6E6FA',
      'enchantment': '#FF69B4',
      'evocation': '#FF4500',
      'illusion': '#9932CC',
      'necromancy': '#2F4F2F',
      'transmutation': '#32CD32'
    };

    setSpellTemplate({
      id: `template_${spell.id}`,
      spell: spell.name,
      area: spell.area,
      position,
      color: schoolColors[spell.school] || '#FFFFFF',
      opacity: 0.3
    });
  }, []);

  // Mouse event handlers
  const handleMouseDown = (e: React.MouseEvent) => {
    const { x,  y  } = screenToWorld(e.clientX, e.clientY);
    setDragStart({ x: e.clientX, y: e.clientY });
    setIsDragging(true);

    if (selectedTool === 'select') {
      // Check for token selection
      const token = scene?.tokens?.find(t => {
        const distance = Math.sqrt((t.x - x) ** 2 + (t.y - y) ** 2);
        return distance < scene.grid.size / 2;
      });

      if (token) {
        setSelectedToken(token.id);
        onTokenSelect?.(token.id);
      } else {
        setSelectedToken(null);
      }
    }
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    const { x,  y  } = screenToWorld(e.clientX, e.clientY);

    // Handle spell template preview
    if (castingMode.active && castingMode.step === 'position' && castingMode.spell) {
      handleSpellPreview(castingMode.spell, { x, y });
    }

    if (!isDragging) return;

    if (selectedTool === 'move' || (selectedTool === 'select' && !selectedToken)) {
      // Pan the map
      const deltaX = e.clientX - dragStart.x;
      const deltaY = e.clientY - dragStart.y;
      setPan(prev => ({ x: prev.x + deltaX, y: prev.y + deltaY }));
      setDragStart({ x: e.clientX, y: e.clientY });
    } else if (selectedTool === 'select' && selectedToken) {
      // Move selected token
      const snapped = snapToGrid(x, y);

      if (scene?.tokens) {
        setScene(prev => {
          if (!prev) return prev;
          return {
            ...prev,
            tokens: prev.tokens?.map(t => 
              t.id === selectedToken 
                ? { ...t, x: snapped.x, y: snapped.y }
                : t
            ) || []
          };
        });
      }
    }
  };

  const handleMouseUp = (e: React.MouseEvent) => {
    const { x,  y  } = screenToWorld(e.clientX, e.clientY);

    // Handle spell casting
    if (castingMode.active && castingMode.step === 'position' && castingMode.spell) {
      handleCastSpell(castingMode.spell, [], { x, y });
      return;
    }

    if (selectedTool === 'select' && selectedToken && isDragging) {
      const snapped = snapToGrid(x, y);
      onTokenMove?.(selectedToken, snapped.x, snapped.y);
    }

    setIsDragging(false);
  };

  // Zoom controls
  const handleZoomIn = () => setZoom(prev => Math.min(prev * 1.2, 5));
  const handleZoomOut = () => setZoom(prev => Math.max(prev / 1.2, 0.1));

  if (loading) {
    return (
      <Card className="h-full">
        <CardContent className="flex items-center justify-center h-full">
          <div className="text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600 mx-auto mb-4"></div>
            <p className="text-neutral-600">Loading map...</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  if (!scene) {
    return (
      <Card className="h-full">
        <CardContent className="flex items-center justify-center h-full">
          <div className="text-center">
            <p className="text-neutral-600 mb-4">No scene found</p>
            <Button>Create Scene</Button>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="h-full flex flex-col bg-neutral-100">
      {/* Toolbar */}
      <div className="flex items-center justify-between p-4 bg-white border-b">
        <div className="flex items-center gap-2">
          <h3 className="font-medium">{scene.name}</h3>
          <span className="text-sm text-neutral-500">
            {scene.tokens?.length || 0} tokens
          </span>
        </div>

        {/* Tools */}
        <div className="flex items-center gap-2">
          <div className="flex border border-neutral-300 rounded-lg p-1">
            <Button
              variant={selectedTool === 'select' ? 'primary' : 'ghost'}
              size="sm"
              onClick={() => setSelectedTool('select')}
            >
              <MousePointer2 className="h-4 w-4" />
            </Button>
            <Button
              variant={selectedTool === 'move' ? 'primary' : 'ghost'}
              size="sm"
              onClick={() => setSelectedTool('move')}
            >
              <Move className="h-4 w-4" />
            </Button>
            <Button
              variant={selectedTool === 'measure' ? 'primary' : 'ghost'}
              size="sm"
              onClick={() => setSelectedTool('measure')}
            >
              <Ruler className="h-4 w-4" />
            </Button>
          </div>

          {/* Spell Casting UI */}
          {isGM && (
            <div className="flex items-center gap-2 ml-4 px-3 py-1 bg-purple-100 rounded-lg">
              <Button
                variant={castingMode.active ? 'primary' : 'ghost'}
                size="sm"
                onClick={() => setCastingMode(prev => ({
                  active: !prev.active,
                  step: 'select_spell'
                }))}
              >
                <Lightbulb className="h-4 w-4" />
                <span className="ml-1">Cast Spell</span>
              </Button>

              {castingMode.active && (
                <div className="flex items-center gap-2">
                  <select
                    className="text-sm border rounded px-2 py-1"
                    onChange={(e) => {
                      const spellData = JSON.parse(e.target.value || '{}');
                      setCastingMode({
                        active: true,
                        spell: spellData,
                        step: spellData.target === 'area' ? 'position' : 'target'
                      });
                    }}
                  >
                    <option value="">Select Spell...</option>
                    <option value={JSON.stringify({
                      id: 'fireball',
                      name: 'Fireball',
                      level: 3,
                      school: 'evocation',
                      target: 'area',
                      area: { type: 'sphere', size: 20 }
                    })}>Fireball</option>
                    <option value={JSON.stringify({
                      id: 'magic_missile',
                      name: 'Magic Missile',
                      level: 1,
                      school: 'evocation',
                      target: 'single'
                    })}>Magic Missile</option>
                    <option value={JSON.stringify({
                      id: 'cone_of_cold',
                      name: 'Cone of Cold',
                      level: 5,
                      school: 'evocation',
                      target: 'area',
                      area: { type: 'cone', size: 60 }
                    })}>Cone of Cold</option>
                  </select>

                  {castingMode.spell && castingMode.step === 'position' && (
                    <span className="text-sm text-purple-600">
                      Click to place {castingMode.spell.name}
                    </span>
                  )}

                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => {
                      setCastingMode({ active: false, step: 'select_spell' });
                      setSpellTemplate(null);
                    }}
                  >
                    Cancel
                  </Button>
                </div>
              )}
            </div>
          )}

          <Button
            variant={isGridVisible ? 'primary' : 'ghost'}
            size="sm"
            onClick={() => setIsGridVisible(!isGridVisible)}
          >
            <Grid3X3 className="h-4 w-4" />
          </Button>

          <div className="flex items-center gap-1">
            <Button variant="ghost" size="sm" onClick={handleZoomOut}>
              <ZoomOut className="h-4 w-4" />
            </Button>
            <span className="text-sm min-w-12 text-center">
              {Math.round(zoom * 100)}%
            </span>
            <Button variant="ghost" size="sm" onClick={handleZoomIn}>
              <ZoomIn className="h-4 w-4" />
            </Button>
          </div>

          {isGM && (
            <Button variant="ghost" size="sm">
              <Settings className="h-4 w-4" />
            </Button>
          )}
        </div>
      </div>

      {/* Map Canvas */}
      <div 
        ref={containerRef}
        className="flex-1 relative overflow-hidden cursor-crosshair"
      >
        <canvas
          ref={canvasRef}
          className="absolute inset-0"
          onMouseDown={handleMouseDown}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          onContextMenu={(e) => e.preventDefault()}
        />
      </div>

      {/* Status Bar */}
      <div className="flex items-center justify-between p-2 bg-white border-t text-sm text-neutral-600">
        <div className="flex items-center gap-4">
          <span>Grid: {scene.grid.size}px {scene.grid.type}</span>
          <span>Size: {scene.width}×{scene.height}</span>
          {(spellEffects.length > 0 || spellProjectiles.length > 0) && (
            <span className="text-purple-600">
              {spellEffects.length} effect{spellEffects.length !== 1 ? 's' : ''}
              {spellProjectiles.length > 0 && (
                <>, {spellProjectiles.length} projectile{spellProjectiles.length !== 1 ? 's' : ''}</>
              )}
            </span>
          )}
        </div>
        <div className="flex items-center gap-2">
          <Users className="h-4 w-4" />
          <span>Campaign: {campaignId}</span>
        </div>
      </div>
    </div>
  );
}

/**
 * Scene Settings Modal - Configure grid, lighting, and fog settings
 */
import React, { useState, useEffect } from 'react';
import { 
  Settings, 
  X, 
  Grid3X3,
  Sun,
  Eye,
  EyeOff,
  Palette,
  Ruler,
  RotateCcw,
  Save,
  Loader2
} from 'lucide-react';
import { Button } from '../ui/Button';
import { Input } from '../ui/Input';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/Card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '../ui/Tabs';
import { Switch } from '../ui/Switch';
import { Slider } from '../ui/Slider';
import { _cn } from '../../lib/utils';

interface SceneSettingsModalProps {
  isOpen: boolean;
  onClose: () => void;
  sceneId: string;
  initialSettings?: SceneSettings;
  onSettingsUpdate: (settings: SceneSettings) => void;
}

interface GridSettings {
  enabled: boolean;
  size: number;
  color: string;
  opacity: number;
  type: 'square' | 'hex';
  snapToGrid: boolean;
  showLabels: boolean;
  offsetX: number;
  offsetY: number;
}

interface LightingSettings {
  enabled: boolean;
  ambientLight: number;
  globalIllumination: boolean;
  shadowQuality: 'low' | 'medium' | 'high';
  colorTemperature: number;
  contrast: number;
}

interface FogSettings {
  enabled: boolean;
  type: 'static' | 'dynamic';
  color: string;
  opacity: number;
  revealOnMove: boolean;
  persistReveal: boolean;
  blurRadius: number;
}

interface SceneSettings {
  grid: GridSettings;
  lighting: LightingSettings;
  fog: FogSettings;
}

const DEFAULT_SETTINGS: SceneSettings = {
  grid: {
    enabled: true,
    size: 70,
    color: '#000000',
    opacity: 0.3,
    type: 'square',
    snapToGrid: true,
    showLabels: false,
    offsetX: 0,
    offsetY: 0,
  },
  lighting: {
    enabled: false,
    ambientLight: 0.3,
    globalIllumination: true,
    shadowQuality: 'medium',
    colorTemperature: 5500,
    contrast: 1.0,
  },
  fog: {
    enabled: false,
    type: 'static',
    color: '#000000',
    opacity: 0.8,
    revealOnMove: true,
    persistReveal: true,
    blurRadius: 3,
  },
};

export function SceneSettingsModal({ 
  isOpen, _onClose, 
  _sceneId, _initialSettings, 
  _onSettingsUpdate 
}: SceneSettingsModalProps) {
  const [settings, setSettings] = useState<SceneSettings>(initialSettings || DEFAULT_SETTINGS);
  const [activeTab, setActiveTab] = useState('grid');
  const [saving, setSaving] = useState(false);
  const [hasChanges, setHasChanges] = useState(false);

  useEffect(() => {
    if (initialSettings) {
      setSettings(initialSettings);
      setHasChanges(false);
    }
  }, [initialSettings]);

  if (!isOpen) return null;

  const updateGridSettings = (_updates: Partial<GridSettings>) => {
    setSettings(prev => ({
      ...prev,
      grid: { ...prev.grid, ...updates }
    }));
    setHasChanges(true);
  };

  const updateLightingSettings = (_updates: Partial<LightingSettings>) => {
    setSettings(prev => ({
      ...prev,
      lighting: { ...prev.lighting, ...updates }
    }));
    setHasChanges(true);
  };

  const updateFogSettings = (_updates: Partial<FogSettings>) => {
    setSettings(prev => ({
      ...prev,
      fog: { ...prev.fog, ...updates }
    }));
    setHasChanges(true);
  };

  const handleSave = async () => {
    setSaving(true);
    try {
      // Call API to update scene settings
      const response = await fetch(`/api/scenes/${sceneId}/settings`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify(settings),
      });

      if (!response.ok) {
        throw new Error('Failed to save settings');
      }

      onSettingsUpdate(settings);
      setHasChanges(false);
    } catch (error) {
      console.error('Error saving settings:', error);
    } finally {
      setSaving(false);
    }
  };

  const handleReset = () => {
    setSettings(initialSettings || DEFAULT_SETTINGS);
    setHasChanges(false);
  };

  const handleClose = () => {
    if (hasChanges) {
      const confirm = window.confirm('You have unsaved changes. Are you sure you want to close?');
      if (!confirm) return;
    }
    onClose();
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <Card className="w-full max-w-4xl max-h-[90vh] overflow-hidden">
        <CardHeader className="flex flex-row items-center justify-between pb-4">
          <CardTitle className="flex items-center gap-2">
            <Settings className="h-5 w-5" />
            Scene Settings
          </CardTitle>
          <Button variant="ghost" size="icon" onClick={handleClose}>
            <X className="h-4 w-4" />
          </Button>
        </CardHeader>

        <CardContent className="p-0">
          <Tabs value={activeTab} onValueChange={setActiveTab} className="h-full">
            <TabsList className="grid w-full grid-cols-3 mx-6 mb-4">
              <TabsTrigger value="grid" className="flex items-center gap-2">
                <Grid3X3 className="h-4 w-4" />
                Grid
              </TabsTrigger>
              <TabsTrigger value="lighting" className="flex items-center gap-2">
                <Sun className="h-4 w-4" />
                Lighting
              </TabsTrigger>
              <TabsTrigger value="fog" className="flex items-center gap-2">
                <Eye className="h-4 w-4" />
                Fog of War
              </TabsTrigger>
            </TabsList>

            <div className="px-6 pb-6 max-h-[60vh] overflow-y-auto">
              {/* Grid Settings */}
              <TabsContent value="grid" className="space-y-6 mt-0">
                <div className="flex items-center justify-between">
                  <h3 className="text-lg font-medium">Grid Configuration</h3>
                  <Switch
                    checked={settings.grid.enabled}
                    onCheckedChange={(enabled) => updateGridSettings({ enabled })}
                  />
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-neutral-700 mb-2">
                        Grid Size (pixels)
                      </label>
                      <Input
                        type="number"
                        value={settings.grid.size}
                        onChange={(e) => updateGridSettings({ size: parseInt(e.target.value) || 70 })}
                        min="20"
                        max="200"
                        disabled={!settings.grid.enabled}
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-neutral-700 mb-2">
                        Grid Type
                      </label>
                      <select
                        value={settings.grid.type}
                        onChange={(e) => updateGridSettings({ type: e.target.value as 'square' | 'hex' })}
                        className="w-full px-3 py-2 border border-neutral-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
                        disabled={!settings.grid.enabled}
                      >
                        <option value="square">Square</option>
                        <option value="hex">Hexagonal</option>
                      </select>
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-neutral-700 mb-2">
                        Grid Color
                      </label>
                      <div className="flex gap-2">
                        <input
                          type="color"
                          value={settings.grid.color}
                          onChange={(e) => updateGridSettings({ color: e.target.value })}
                          className="w-12 h-10 rounded border border-neutral-300"
                          disabled={!settings.grid.enabled}
                        />
                        <Input
                          value={settings.grid.color}
                          onChange={(e) => updateGridSettings({ color: e.target.value })}
                          placeholder="#000000"
                          disabled={!settings.grid.enabled}
                        />
                      </div>
                    </div>
                  </div>

                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-neutral-700 mb-2">
                        Opacity: {Math.round(settings.grid.opacity * 100)}%
                      </label>
                      <Slider
                        value={[settings.grid.opacity]}
                        onValueChange={([_opacity]) => updateGridSettings({ opacity })}
                        min={0}
                        max={1}
                        step={0.1}
                        disabled={!settings.grid.enabled}
                      />
                    </div>

                    <div className="space-y-3">
                      <div className="flex items-center justify-between">
                        <span className="text-sm font-medium text-neutral-700">Snap to Grid</span>
                        <Switch
                          checked={settings.grid.snapToGrid}
                          onCheckedChange={(_snapToGrid) => updateGridSettings({ snapToGrid })}
                          disabled={!settings.grid.enabled}
                        />
                      </div>

                      <div className="flex items-center justify-between">
                        <span className="text-sm font-medium text-neutral-700">Show Labels</span>
                        <Switch
                          checked={settings.grid.showLabels}
                          onCheckedChange={(_showLabels) => updateGridSettings({ showLabels })}
                          disabled={!settings.grid.enabled}
                        />
                      </div>
                    </div>

                    <div className="grid grid-cols-2 gap-2">
                      <div>
                        <label className="block text-sm font-medium text-neutral-700 mb-1">
                          X Offset
                        </label>
                        <Input
                          type="number"
                          value={settings.grid.offsetX}
                          onChange={(e) => updateGridSettings({ offsetX: parseInt(e.target.value) || 0 })}
                          disabled={!settings.grid.enabled}
                        />
                      </div>
                      <div>
                        <label className="block text-sm font-medium text-neutral-700 mb-1">
                          Y Offset
                        </label>
                        <Input
                          type="number"
                          value={settings.grid.offsetY}
                          onChange={(e) => updateGridSettings({ offsetY: parseInt(e.target.value) || 0 })}
                          disabled={!settings.grid.enabled}
                        />
                      </div>
                    </div>
                  </div>
                </div>
              </TabsContent>

              {/* Lighting Settings */}
              <TabsContent value="lighting" className="space-y-6 mt-0">
                <div className="flex items-center justify-between">
                  <h3 className="text-lg font-medium">Lighting System</h3>
                  <Switch
                    checked={settings.lighting.enabled}
                    onCheckedChange={(_enabled) => updateLightingSettings({ enabled })}
                  />
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-neutral-700 mb-2">
                        Ambient Light: {Math.round(settings.lighting.ambientLight * 100)}%
                      </label>
                      <Slider
                        value={[settings.lighting.ambientLight]}
                        onValueChange={([_ambientLight]) => updateLightingSettings({ ambientLight })}
                        min={0}
                        max={1}
                        step={0.1}
                        disabled={!settings.lighting.enabled}
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-neutral-700 mb-2">
                        Shadow Quality
                      </label>
                      <select
                        value={settings.lighting.shadowQuality}
                        onChange={(e) => updateLightingSettings({ shadowQuality: e.target.value as 'low' | 'medium' | 'high' })}
                        className="w-full px-3 py-2 border border-neutral-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
                        disabled={!settings.lighting.enabled}
                      >
                        <option value="low">Low</option>
                        <option value="medium">Medium</option>
                        <option value="high">High</option>
                      </select>
                    </div>

                    <div className="flex items-center justify-between">
                      <span className="text-sm font-medium text-neutral-700">Global Illumination</span>
                      <Switch
                        checked={settings.lighting.globalIllumination}
                        onCheckedChange={(_globalIllumination) => updateLightingSettings({ globalIllumination })}
                        disabled={!settings.lighting.enabled}
                      />
                    </div>
                  </div>

                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-neutral-700 mb-2">
                        Color Temperature: {settings.lighting.colorTemperature}K
                      </label>
                      <Slider
                        value={[settings.lighting.colorTemperature]}
                        onValueChange={([_colorTemperature]) => updateLightingSettings({ colorTemperature })}
                        min={2000}
                        max={8000}
                        step={100}
                        disabled={!settings.lighting.enabled}
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-neutral-700 mb-2">
                        Contrast: {settings.lighting.contrast.toFixed(1)}
                      </label>
                      <Slider
                        value={[settings.lighting.contrast]}
                        onValueChange={([_contrast]) => updateLightingSettings({ contrast })}
                        min={0.5}
                        max={2.0}
                        step={0.1}
                        disabled={!settings.lighting.enabled}
                      />
                    </div>
                  </div>
                </div>
              </TabsContent>

              {/* Fog of War Settings */}
              <TabsContent value="fog" className="space-y-6 mt-0">
                <div className="flex items-center justify-between">
                  <h3 className="text-lg font-medium">Fog of War</h3>
                  <Switch
                    checked={settings.fog.enabled}
                    onCheckedChange={(_enabled) => updateFogSettings({ enabled })}
                  />
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-neutral-700 mb-2">
                        Fog Type
                      </label>
                      <select
                        value={settings.fog.type}
                        onChange={(e) => updateFogSettings({ type: e.target.value as 'static' | 'dynamic' })}
                        className="w-full px-3 py-2 border border-neutral-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
                        disabled={!settings.fog.enabled}
                      >
                        <option value="static">Static (Manual)</option>
                        <option value="dynamic">Dynamic (Line of Sight)</option>
                      </select>
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-neutral-700 mb-2">
                        Fog Color
                      </label>
                      <div className="flex gap-2">
                        <input
                          type="color"
                          value={settings.fog.color}
                          onChange={(e) => updateFogSettings({ color: e.target.value })}
                          className="w-12 h-10 rounded border border-neutral-300"
                          disabled={!settings.fog.enabled}
                        />
                        <Input
                          value={settings.fog.color}
                          onChange={(e) => updateFogSettings({ color: e.target.value })}
                          placeholder="#000000"
                          disabled={!settings.fog.enabled}
                        />
                      </div>
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-neutral-700 mb-2">
                        Opacity: {Math.round(settings.fog.opacity * 100)}%
                      </label>
                      <Slider
                        value={[settings.fog.opacity]}
                        onValueChange={([_opacity]) => updateFogSettings({ opacity })}
                        min={0}
                        max={1}
                        step={0.1}
                        disabled={!settings.fog.enabled}
                      />
                    </div>
                  </div>

                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-neutral-700 mb-2">
                        Blur Radius: {settings.fog.blurRadius}px
                      </label>
                      <Slider
                        value={[settings.fog.blurRadius]}
                        onValueChange={([_blurRadius]) => updateFogSettings({ blurRadius })}
                        min={0}
                        max={10}
                        step={1}
                        disabled={!settings.fog.enabled}
                      />
                    </div>

                    <div className="space-y-3">
                      <div className="flex items-center justify-between">
                        <span className="text-sm font-medium text-neutral-700">Reveal on Move</span>
                        <Switch
                          checked={settings.fog.revealOnMove}
                          onCheckedChange={(_revealOnMove) => updateFogSettings({ revealOnMove })}
                          disabled={!settings.fog.enabled}
                        />
                      </div>

                      <div className="flex items-center justify-between">
                        <span className="text-sm font-medium text-neutral-700">Persist Reveal</span>
                        <Switch
                          checked={settings.fog.persistReveal}
                          onCheckedChange={(_persistReveal) => updateFogSettings({ persistReveal })}
                          disabled={!settings.fog.enabled}
                        />
                      </div>
                    </div>
                  </div>
                </div>
              </TabsContent>
            </div>

            {/* Action Buttons */}
            <div className="border-t px-6 py-4 flex justify-between">
              <Button variant="outline" onClick={handleReset} disabled={!hasChanges || saving}>
                <RotateCcw className="h-4 w-4 mr-2" />
                Reset
              </Button>

              <div className="flex gap-3">
                <Button variant="outline" onClick={handleClose} disabled={saving}>
                  Cancel
                </Button>
                <Button 
                  variant="primary" 
                  onClick={handleSave}
                  disabled={!hasChanges || saving}
                >
                  {saving ? (
                    <>
                      <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                      Saving...
                    </>
                  ) : (
                    <>
                      <Save className="h-4 w-4 mr-2" />
                      Save Settings
                    </>
                  )}
                </Button>
              </div>
            </div>
          </Tabs>
        </CardContent>
      </Card>
    </div>
  );
}

/**
 * Token Properties Panel - Manage token details, conditions, and stats
 */

import React, { useState } from 'react';
import { Button } from '../ui/Button';
import { Input } from '../ui/Input';
import { cn } from '../../lib/utils';
import { 
  X, 
  Save, 
  Trash2, 
  Plus,
  Eye,
  EyeOff,
  RotateCw,
  Palette,
  Heart,
  Shield,
  Zap,
  Clock,
  Skull,
  AlertTriangle,
  CheckCircle
} from 'lucide-react';
import type { Token } from './BattleMap';

interface TokenPropertiesPanelProps {
  token: Token | null;
  onClose: () => void;
  onUpdate: (_tokenId: string, _updates: Partial<Token>) => void;
  onDelete: (_tokenId: string) => void;
  isGM?: boolean;
}

const CONDITION_TYPES = [
  { id: 'blinded', name: 'Blinded', icon: Eye, color: '#6b7280', description: 'Cannot see and automatically fails sight-based checks' },
  { id: 'charmed', name: 'Charmed', icon: Heart, color: '#ec4899', description: 'Cannot attack the charmer or target them with harmful abilities' },
  { id: 'deafened', name: 'Deafened', icon: AlertTriangle, color: '#f59e0b', description: 'Cannot hear and automatically fails hearing-based checks' },
  { id: 'frightened', name: 'Frightened', icon: Skull, color: '#7c3aed', description: 'Disadvantage on ability checks and attack rolls while source of fear is in sight' },
  { id: 'grappled', name: 'Grappled', icon: Zap, color: '#059669', description: 'Speed becomes 0 and cannot benefit from bonuses to speed' },
  { id: 'incapacitated', name: 'Incapacitated', icon: Clock, color: '#dc2626', description: 'Cannot take actions or reactions' },
  { id: 'invisible', name: 'Invisible', icon: EyeOff, color: '#6366f1', description: 'Cannot be seen without magical means, heavily obscured for hiding' },
  { id: 'paralyzed', name: 'Paralyzed', icon: X, color: '#991b1b', description: 'Incapacitated and cannot move or speak, fails Strength and Dexterity saves' },
  { id: 'petrified', name: 'Petrified', icon: Shield, color: '#78716c', description: 'Transformed into solid inanimate substance, incapacitated' },
  { id: 'poisoned', name: 'Poisoned', icon: Skull, color: '#16a34a', description: 'Disadvantage on attack rolls and ability checks' },
  { id: 'prone', name: 'Prone', icon: RotateCw, color: '#ea580c', description: 'Disadvantage on attack rolls, attacks against have advantage if within 5 feet' },
  { id: 'restrained', name: 'Restrained', icon: Zap, color: '#b91c1c', description: 'Speed becomes 0, disadvantage on Dexterity saves, attacks against have advantage' },
  { id: 'stunned', name: 'Stunned', icon: AlertTriangle, color: '#fbbf24', description: 'Incapacitated, cannot move, and can speak only falteringly' },
  { id: 'unconscious', name: 'Unconscious', icon: Clock, color: '#374151', description: 'Incapacitated, cannot move or speak, and is unaware of surroundings' }
];

const TOKEN_SIZES = [
  { value: 0.5, label: 'Tiny' },
  { value: 1, label: 'Small/Medium' },
  { value: 2, label: 'Large' },
  { value: 3, label: 'Huge' },
  { value: 4, label: 'Gargantuan' }
];

const PRESET_COLORS = [
  '#3b82f6', '#ef4444', '#10b981', '#f59e0b', '#8b5cf6',
  '#ec4899', '#06b6d4', '#84cc16', '#f97316', '#6366f1'
];

export function TokenPropertiesPanel({_ 
  token, _onClose, 
  _onUpdate, _onDelete, 
  _isGM = false 
}: TokenPropertiesPanelProps) {
  const [editedToken, setEditedToken] = useState<Token | null>(token);
  const [showConditions, setShowConditions] = useState(false);

  if (!token || !editedToken) return null;

  const handleSave = () => {
    onUpdate(token.id, editedToken);
    onClose();
  };

  const handleDelete = () => {
    if (window.confirm(`Are you sure you want to delete "${token.name}"?`)) {
      onDelete(token.id);
      onClose();
    }
  };

  const updateField = (_field: keyof Token, _value: any) => {
    setEditedToken(prev => prev ? { ...prev, [field]: value } : null);
  };

  const addCondition = (_conditionId: string) => {
    if (!editedToken.conditions.includes(conditionId)) {
      updateField('conditions', [...editedToken.conditions, conditionId]);
    }
  };

  const removeCondition = (_conditionId: string) => {
    updateField('conditions', editedToken.conditions.filter(id => id !== conditionId));
  };

  const updateHitPoints = (_field: 'current' | 'max', _value: number) => {
    const hp = editedToken.hitPoints || { current: 1, max: 1 };
    updateField('hitPoints', { ...hp, [field]: Math.max(0, value) });
  };

  const getConditionInfo = (_conditionId: string) => {
    return CONDITION_TYPES.find(c => c.id === conditionId);
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-bg-secondary rounded-lg border border-border-primary w-full max-w-md max-h-[90vh] overflow-hidden flex flex-col">
        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b border-border-primary">
          <h2 className="text-lg font-semibold text-text-primary">Token Properties</h2>
          <Button variant="ghost" size="sm" onClick={onClose}>
            <X className="h-4 w-4" />
          </Button>
        </div>

        {/* Content */}
        <div className="flex-1 overflow-y-auto p-4 space-y-4">
          {/* Basic Info */}
          <div className="space-y-3">
            <div>
              <label className="block text-sm font-medium text-text-primary mb-1">Name</label>
              <Input
                value={editedToken.name}
                onChange={(e) => updateField('name', e.target.value)}
                placeholder="Token name"
              />
            </div>

            <div className="grid grid-cols-2 gap-3">
              <div>
                <label className="block text-sm font-medium text-text-primary mb-1">Size</label>
                <select
                  value={editedToken.size}
                  onChange={(e) => updateField('size', parseFloat(e.target.value))}
                  className="w-full px-3 py-2 bg-bg-primary border border-border-primary rounded-md text-text-primary text-sm"
                >
                  {TOKEN_SIZES.map(size => (
                    <option key={size.value} value={size.value}>
                      {size.label}
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-text-primary mb-1">Rotation</label>
                <Input
                  type="number"
                  min="0"
                  max="360"
                  step="15"
                  value={editedToken.rotation}
                  onChange={(e) => updateField('rotation', parseInt(e.target.value) || 0)}
                />
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-text-primary mb-1">Color</label>
              <div className="flex gap-2 mb-2">
                {PRESET_COLORS.map(_color => (
                  <button
                    key={color}
                    onClick={() => updateField('color', color)}
                    className={cn(
                      'w-8 h-8 rounded border-2 transition-all',
                      editedToken.color === color 
                        ? 'border-accent-primary scale-110' 
                        : 'border-border-primary hover:scale-105'
                    )}
                    style={{ backgroundColor: color }}
                  />
                ))}
              </div>
              <Input
                type="color"
                value={editedToken.color}
                onChange={(e) => updateField('color', e.target.value)}
                className="w-full h-10"
              />
            </div>

            {isGM && (
              <div className="flex items-center gap-2">
                <input
                  type="checkbox"
                  id="visible"
                  checked={editedToken.isVisible}
                  onChange={(e) => updateField('isVisible', e.target.checked)}
                  className="rounded border-border-primary"
                />
                <label htmlFor="visible" className="text-sm text-text-primary flex items-center gap-1">
                  {editedToken.isVisible ? <Eye className="h-4 w-4" /> : <EyeOff className="h-4 w-4" />}
                  Visible to Players
                </label>
              </div>
            )}
          </div>

          {/* Health */}
          <div className="space-y-3">
            <h3 className="text-md font-semibold text-text-primary">Health</h3>
            <div className="grid grid-cols-2 gap-3">
              <div>
                <label className="block text-sm font-medium text-text-primary mb-1">Current HP</label>
                <Input
                  type="number"
                  min="0"
                  value={editedToken.hitPoints?.current || 0}
                  onChange={(e) => updateHitPoints('current', parseInt(e.target.value) || 0)}
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-text-primary mb-1">Max HP</label>
                <Input
                  type="number"
                  min="1"
                  value={editedToken.hitPoints?.max || 1}
                  onChange={(e) => updateHitPoints('max', parseInt(e.target.value) || 1)}
                />
              </div>
            </div>

            {editedToken.hitPoints && (
              <div className="bg-bg-tertiary rounded p-2">
                <div className="flex justify-between text-sm text-text-secondary mb-1">
                  <span>Health</span>
                  <span>{editedToken.hitPoints.current}/{editedToken.hitPoints.max}</span>
                </div>
                <div className="w-full bg-bg-primary rounded-full h-2">
                  <div 
                    className={cn(
                      'h-2 rounded-full transition-all',
                      editedToken.hitPoints.current <= editedToken.hitPoints.max * 0.25 
                        ? 'bg-red-500'
                        : editedToken.hitPoints.current <= editedToken.hitPoints.max * 0.5
                        ? 'bg-yellow-500'
                        : 'bg-green-500'
                    )}
                    style={{ 
                      width: `${Math.max(0, (editedToken.hitPoints.current / editedToken.hitPoints.max) * 100)}%` 
                    }}
                  />
                </div>
              </div>
            )}
          </div>

          {/* Conditions */}
          <div className="space-y-3">
            <div className="flex items-center justify-between">
              <h3 className="text-md font-semibold text-text-primary">Conditions</h3>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setShowConditions(!showConditions)}
              >
                <Plus className="h-4 w-4" />
              </Button>
            </div>

            {/* Applied Conditions */}
            <div className="space-y-2">
              {editedToken.conditions.map(conditionId => {
                const condition = getConditionInfo(conditionId);
                if (!condition) return null;

                return (
                  <div 
                    key={conditionId}
                    className="flex items-center justify-between p-2 bg-bg-tertiary rounded border"
                  >
                    <div className="flex items-center gap-2">
                      <condition.icon 
                        className="h-4 w-4" 
                        style={{ color: condition.color }} 
                      />
                      <span className="text-sm font-medium text-text-primary">
                        {condition.name}
                      </span>
                    </div>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => removeCondition(conditionId)}
                    >
                      <X className="h-3 w-3" />
                    </Button>
                  </div>
                );
              })}

              {editedToken.conditions.length === 0 && (
                <div className="text-center py-4 text-text-secondary text-sm">
                  No conditions applied
                </div>
              )}
            </div>

            {/* Available Conditions */}
            {showConditions && (
              <div className="border border-border-primary rounded p-2 space-y-1 max-h-40 overflow-y-auto">
                {CONDITION_TYPES
                  .filter(condition => !editedToken.conditions.includes(condition.id))
                  .map(condition => (
                    <button
                      key={condition.id}
                      onClick={() => {
                        addCondition(condition.id);
                        setShowConditions(false);
                      }}
                      className="w-full flex items-center gap-2 p-2 text-left hover:bg-bg-secondary rounded transition-colors"
                    >
                      <condition.icon 
                        className="h-4 w-4" 
                        style={{ color: condition.color }} 
                      />
                      <div>
                        <div className="text-sm font-medium text-text-primary">
                          {condition.name}
                        </div>
                        <div className="text-xs text-text-secondary">
                          {condition.description}
                        </div>
                      </div>
                    </button>
                  ))}
              </div>
            )}
          </div>

          {/* Character Info */}
          <div className="space-y-3">
            <h3 className="text-md font-semibold text-text-primary">Character Info</h3>
            <div>
              <label className="block text-sm font-medium text-text-primary mb-1">Character ID</label>
              <Input
                value={editedToken.characterId || ''}
                onChange={(e) => updateField('characterId', e.target.value)}
                placeholder="Link to character sheet"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-text-primary mb-1">Player ID</label>
              <Input
                value={editedToken.playerId || ''}
                onChange={(e) => updateField('playerId', e.target.value)}
                placeholder="Player owner"
              />
            </div>
          </div>
        </div>

        {/* Footer */}
        <div className="flex items-center justify-between p-4 border-t border-border-primary">
          <div>
            {isGM && (
              <Button
                variant="ghost"
                onClick={handleDelete}
                className="text-danger hover:text-danger"
              >
                <Trash2 className="h-4 w-4" />
              </Button>
            )}
          </div>
          <div className="flex gap-2">
            <Button variant="ghost" onClick={onClose}>
              Cancel
            </Button>
            <Button variant="primary" onClick={handleSave}>
              <Save className="h-4 w-4 mr-2" />
              Save
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
}

/**
 * Button Component - Versatile, accessible button with multiple variants and sizes
 */
import React, { forwardRef } from 'react';
import { cva, type VariantProps } from 'class-variance-authority';
import { cn } from '../../lib/utils';
import { Loader2 } from 'lucide-react';

const buttonVariants = cva(
  // Base styles - consistent across all variants
  [
    'inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-lg text-sm font-medium',
    'transition-all duration-200 ease-in-out',
    'focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-offset-2',
    'disabled:pointer-events-none disabled:opacity-50',
    'relative overflow-hidden'
  ],
  {
    variants: {
      variant: {
        // Primary - Main call-to-action button
        primary: [
          'bg-gradient-to-r from-primary-600 to-primary-700 text-white shadow-lg',
          'hover:from-primary-700 hover:to-primary-800 hover:shadow-xl hover:-translate-y-0.5',
          'active:from-primary-800 active:to-primary-900 active:translate-y-0',
          'focus-visible:ring-primary-500'
        ],

        // Secondary - Less prominent actions
        secondary: [
          'bg-white text-neutral-900 border border-neutral-300 shadow-sm',
          'hover:bg-neutral-50 hover:border-neutral-400 hover:shadow-md',
          'active:bg-neutral-100',
          'focus-visible:ring-neutral-500'
        ],

        // Destructive - Delete, remove, dangerous actions
        destructive: [
          'bg-gradient-to-r from-error-600 to-error-700 text-white shadow-lg',
          'hover:from-error-700 hover:to-error-800 hover:shadow-xl hover:-translate-y-0.5',
          'active:from-error-800 active:to-error-900 active:translate-y-0',
          'focus-visible:ring-error-500'
        ],

        // Ghost - Minimal styling, text-like
        ghost: [
          'text-neutral-700 hover:bg-neutral-100 hover:text-neutral-900',
          'active:bg-neutral-200',
          'focus-visible:ring-neutral-500'
        ],

        // Link - Looks like a text link
        link: [
          'text-primary-600 underline-offset-4 hover:underline',
          'active:text-primary-700',
          'focus-visible:ring-primary-500'
        ],

        // Success - Positive actions
        success: [
          'bg-gradient-to-r from-success-600 to-success-700 text-white shadow-lg',
          'hover:from-success-700 hover:to-success-800 hover:shadow-xl hover:-translate-y-0.5',
          'active:from-success-800 active:to-success-900 active:translate-y-0',
          'focus-visible:ring-success-500'
        ]
      },
      size: {
        sm: 'h-8 px-3 text-xs',
        md: 'h-10 px-4 text-sm',
        lg: 'h-12 px-6 text-base',
        xl: 'h-14 px-8 text-lg',
        icon: 'h-10 w-10 rounded-full'
      },
      fullWidth: {
        true: 'w-full'
      }
    },
    defaultVariants: {
      variant: 'primary',
      size: 'md'
    }
  }
);

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  loading?: boolean;
  leftIcon?: React.ReactNode;
  rightIcon?: React.ReactNode;
  asChild?: boolean;
}

const Button = forwardRef<HTMLButtonElement, ButtonProps>(
  _({ 
    className, _variant, 
    _size, _fullWidth,
    _loading = false, _leftIcon,
    _rightIcon, _disabled,
    _children,
    _...props 
  }, _ref) => {
    const isDisabled = disabled || loading;

    return (
      <button
        className={cn(buttonVariants({ variant, size, fullWidth, className }))}
        ref={ref}
        disabled={isDisabled}
        {...props}
      >
        {/* Loading spinner */}
        {loading && (
          <Loader2 className="h-4 w-4 animate-spin" />
        )}

        {/* Left icon (hidden when loading) */}
        {!loading && leftIcon && (
          <span className="inline-flex shrink-0">
            {leftIcon}
          </span>
        )}

        {/* Button text */}
        <span className={cn(
          'inline-flex items-center',
          loading && 'opacity-70'
        )}>
          {children}
        </span>

        {/* Right icon (hidden when loading) */}
        {!loading && rightIcon && (
          <span className="inline-flex shrink-0">
            {rightIcon}
          </span>
        )}

        {/* Ripple effect overlay */}
        <span className="absolute inset-0 -z-10 rounded-lg bg-white/20 opacity-0 transition-opacity duration-200 hover:opacity-100" />
      </button>
    );
  }
);

Button.displayName = 'Button';

export { Button, buttonVariants };
export type { ButtonProps };

