/**
 * Input Component - Flexible input field with validation states and icons
 */
import React, { forwardRef } from 'react';
import { cva, type VariantProps } from 'class-variance-authority';
import { cn } from '../../lib/utils';
import { Eye, EyeOff, AlertCircle, CheckCircle } from 'lucide-react';

const inputVariants = cva(
  [
    'flex w-full rounded-lg border bg-white px-3 py-2 text-sm transition-all duration-200',
    'placeholder:text-neutral-400 focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-offset-2',
    'disabled:cursor-not-allowed disabled:opacity-50 disabled:bg-neutral-50'
  ],
  {
    variants: {
      variant: {
        default: [
          'border-neutral-300 text-neutral-900',
          'hover:border-neutral-400',
          'focus-visible:border-primary-500 focus-visible:ring-primary-500'
        ],
        error: [
          'border-error-300 text-error-900 bg-error-50',
          'focus-visible:border-error-500 focus-visible:ring-error-500'
        ],
        success: [
          'border-success-300 text-success-900 bg-success-50',
          'focus-visible:border-success-500 focus-visible:ring-success-500'
        ]
      },
      size: {
        sm: 'h-8 px-2.5 py-1.5 text-xs',
        md: 'h-10 px-3 py-2 text-sm',
        lg: 'h-12 px-4 py-3 text-base'
      }
    },
    defaultVariants: {
      variant: 'default',
      size: 'md'
    }
  }
);

export interface InputProps
  extends Omit<React.InputHTMLAttributes<HTMLInputElement>, 'size'>,
    VariantProps<typeof inputVariants> {
  label?: string;
  description?: string;
  error?: string | undefined;
  success?: string;
  leftIcon?: React.ReactNode;
  rightIcon?: React.ReactNode;
  showPasswordToggle?: boolean;
}

const Input = forwardRef<HTMLInputElement, InputProps>(
  _({
    className, _variant,
    _size,
    _type = 'text', _label,
    _description, _error,
    _success, _leftIcon,
    _rightIcon, _showPasswordToggle,
    _disabled, _id,
    _...props
  }, _ref) => {
    const [showPassword, setShowPassword] = React.useState(false);
    // TODO: Fix conditional hook call
    const inputId = id || React.useId();

    // Determine variant based on validation state
    const resolvedVariant = error ? 'error' : success ? 'success' : variant;

    // Determine input type (handle password toggle)
    const inputType = showPasswordToggle && type === 'password' && showPassword ? 'text' : type;

    // Show password toggle icon
    const passwordToggleIcon = showPasswordToggle && type === 'password' && (
      <button
        type="button"
        className="absolute right-3 top-1/2 -translate-y-1/2 text-neutral-400 hover:text-neutral-600 focus:outline-none"
        onClick={() => setShowPassword(!showPassword)}
        tabIndex={-1}
      >
        {showPassword ? (
          <EyeOff className="h-4 w-4" />
        ) : (
          <Eye className="h-4 w-4" />
        )}
      </button>
    );

    // Validation state icon
    const validationIcon = error ? (
      <AlertCircle className="h-4 w-4 text-error-500" />
    ) : success ? (
      <CheckCircle className="h-4 w-4 text-success-500" />
    ) : null;

    return (
      <div className="space-y-1.5">
        {/* Label */}
        {label && (
          <label
            htmlFor={inputId}
            className="block text-sm font-medium text-neutral-700"
          >
            {label}
          </label>
        )}

        {/* Input container */}
        <div className="relative">
          {/* Left icon */}
          {leftIcon && (
            <div className="absolute left-3 top-1/2 -translate-y-1/2 text-neutral-400">
              {leftIcon}
            </div>
          )}

          {/* Input field */}
          <input
            type={inputType}
            className={cn(
              inputVariants({ variant: resolvedVariant, size }),
              leftIcon && 'pl-10',
              (rightIcon || validationIcon || passwordToggleIcon) && 'pr-10',
              className
            )}
            ref={ref}
            id={inputId}
            disabled={disabled}
            {...props}
          />

          {/* Right icons container */}
          <div className="absolute right-3 top-1/2 -translate-y-1/2 flex items-center gap-1">
            {validationIcon}
            {!validationIcon && rightIcon && (
              <span className="text-neutral-400">{rightIcon}</span>
            )}
            {passwordToggleIcon}
          </div>
        </div>

        {/* Description */}
        {description && !error && !success && (
          <p className="text-xs text-neutral-500">
            {description}
          </p>
        )}

        {/* Error message */}
        {error && (
          <p className="text-xs text-error-600 flex items-center gap-1">
            <AlertCircle className="h-3 w-3 shrink-0" />
            {error}
          </p>
        )}

        {/* Success message */}
        {success && (
          <p className="text-xs text-success-600 flex items-center gap-1">
            <CheckCircle className="h-3 w-3 shrink-0" />
            {success}
          </p>
        )}
      </div>
    );
  }
);

Input.displayName = 'Input';

export { Input, inputVariants };
export type { InputProps };

/**
 * Authentication Hook - Manages user authentication state and operations
 */
import React, { createContext, useContext, useEffect, useState, useCallback } from 'react';
import { apiClient } from '../lib/api-client';

interface User {
  id: string;
  username: string;
  email: string;
  firstName: string;
  lastName: string;
  emailVerified: boolean;
  role: string;
  subscription?: {
    tier: string;
    status: string;
    currentPeriodEnd?: string;
  };
  createdAt: string;
  lastLoginAt?: string;
}

interface AuthContextType {
  user: User | null;
  loading: boolean;
  error: string | null;

  // Authentication methods
  login: (_identifier: string, _password: string, _rememberMe?: boolean) => Promise<void>;
  register: (data: RegisterData) => Promise<void>;
  logout: () => Promise<void>;
  refreshAuth: () => Promise<void>;

  // Password management
  requestPasswordReset: (_email: string) => Promise<void>;
  resetPassword: (_token: string, _newPassword: string) => Promise<void>;
  changePassword: (_currentPassword: string, _newPassword: string) => Promise<void>;

  // Email verification
  verifyEmail: (_token: string) => Promise<void>;
  resendVerification: () => Promise<void>;

  // Profile management
  updateProfile: (data: Partial<User>) => Promise<void>;

  // Utility methods
  clearError: () => void;
  isAuthenticated: boolean;
  hasRole: (_role: string) => boolean;
  hasSubscription: (_tier?: string) => boolean;
}

interface RegisterData {
  username: string;
  email: string;
  password: string;
  firstName: string;
  lastName: string;
  timezone?: string;
  acceptedTerms: boolean;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({_ children }: { children: React.ReactNode }) {
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Clear error helper
  const clearError = useCallback(() => {
    setError(null);
  }, []);

  // Check if user is authenticated
  const isAuthenticated = Boolean(user);

  // Check if user has specific role
  const hasRole = useCallback((_role: string) => {
    return user?.role === role;
  }, [user]);

  // Check if user has active subscription
  const hasSubscription = useCallback(_(tier?: string) => {
    if (!user?.subscription) return false;
    if (user.subscription.status !== 'active') return false;
    if (tier && user.subscription.tier !== tier) return false;
    return true;
  }, [user]);

  // Login function
  const login = useCallback(async (identifier: string, _password: string, _rememberMe = false) => {
    try {
      setLoading(true);
      setError(null);

      const response = await apiClient.post('/auth/login', {
        identifier,
        password,
        rememberMe,
      });

      if (response.data.success) {
        setUser(response.data.user);
        // Store session info if needed
        if (response.data.session) {
          localStorage.setItem('sessionExpiry', response.data.session.expiresAt);
        }
      } else {
        throw new Error(response.data.error || 'Login failed');
      }
    } catch (err: any) {
      const errorMessage = err.response?.data?.error || err.message || 'Login failed';
      setError(errorMessage);
      throw new Error(errorMessage);
    } finally {
      setLoading(false);
    }
  }, []);

  // Register function
  const register = useCallback(async (data: RegisterData) => {
    try {
      setLoading(true);
      setError(null);

      const response = await apiClient.post('/auth/register', data);

      if (response.data.success) {
        setUser(response.data.user);
      } else {
        throw new Error(response.data.error || 'Registration failed');
      }
    } catch (err: any) {
      const errorMessage = err.response?.data?.error || err.message || 'Registration failed';
      setError(errorMessage);
      throw new Error(errorMessage);
    } finally {
      setLoading(false);
    }
  }, []);

  // Logout function
  const logout = useCallback(async () => {
    try {
      setLoading(true);
      await apiClient.post('/auth/logout');
    } catch (err) {
      console.error('Logout error:', err);
    } finally {
      setUser(null);
      setError(null);
      setLoading(false);
      localStorage.removeItem('sessionExpiry');
    }
  }, []);

  // Refresh authentication state
  const refreshAuth = useCallback(async () => {
    try {
      setLoading(true);
      const response = await apiClient.get('/auth/me');

      if (response.data.success) {
        setUser(response.data.user);
      } else {
        setUser(null);
      }
    } catch (err) {
      setUser(null);
      console.error('Auth refresh error:', err);
    } finally {
      setLoading(false);
    }
  }, []);

  // Request password reset
  const requestPasswordReset = useCallback(async (email: string) => {
    try {
      setLoading(true);
      setError(null);

      await apiClient.post('/auth/reset-password', { email });
    } catch (err: any) {
      const errorMessage = err.response?.data?.error || 'Password reset request failed';
      setError(errorMessage);
      throw new Error(errorMessage);
    } finally {
      setLoading(false);
    }
  }, []);

  // Reset password with token
  const resetPassword = useCallback(async (token: string, _newPassword: string) => {
    try {
      setLoading(true);
      setError(null);

      await apiClient.post('/auth/reset-password/confirm', {
        token,
        newPassword,
      });
    } catch (err: any) {
      const errorMessage = err.response?.data?.error || 'Password reset failed';
      setError(errorMessage);
      throw new Error(errorMessage);
    } finally {
      setLoading(false);
    }
  }, []);

  // Change password (authenticated user)
  const changePassword = useCallback(async (currentPassword: string, _newPassword: string) => {
    try {
      setLoading(true);
      setError(null);

      await apiClient.put('/auth/me/password', {
        currentPassword,
        newPassword,
      });
    } catch (err: any) {
      const errorMessage = err.response?.data?.error || 'Password change failed';
      setError(errorMessage);
      throw new Error(errorMessage);
    } finally {
      setLoading(false);
    }
  }, []);

  // Verify email with token
  const verifyEmail = useCallback(async (token: string) => {
    try {
      setLoading(true);
      setError(null);

      await apiClient.post('/auth/verify-email', { token });

      // Refresh user data to get updated emailVerified status
      await refreshAuth();
    } catch (err: any) {
      const errorMessage = err.response?.data?.error || 'Email verification failed';
      setError(errorMessage);
      throw new Error(errorMessage);
    } finally {
      setLoading(false);
    }
  }, [refreshAuth]);

  // Resend email verification
  const resendVerification = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      await apiClient.post('/auth/verify-email/resend');
    } catch (err: any) {
      const errorMessage = err.response?.data?.error || 'Failed to resend verification email';
      setError(errorMessage);
      throw new Error(errorMessage);
    } finally {
      setLoading(false);
    }
  }, []);

  // Update user profile
  const updateProfile = useCallback(async (data: Partial<User>) => {
    try {
      setLoading(true);
      setError(null);

      const response = await apiClient.put('/auth/me', data);

      if (response.data.success) {
        setUser(response.data.user);
      }
    } catch (err: any) {
      const errorMessage = err.response?.data?.error || 'Profile update failed';
      setError(errorMessage);
      throw new Error(errorMessage);
    } finally {
      setLoading(false);
    }
  }, []);

  // Initialize auth state on mount
  useEffect(() => {
    refreshAuth();
  }, [refreshAuth]);

  // Set up axios interceptors for auth
  useEffect(() => {
    const responseInterceptor = apiClient.interceptors.response.use(
      (response) => response,
      async (error) => {
        if (error.response?.status === 401 && user) {
          // Token expired, try to refresh or logout
          setUser(null);
          setError('Session expired. Please log in again.');
        }
        return Promise.reject(error);
      }
    );

    return () => {
      apiClient.interceptors.response.eject(responseInterceptor);
    };
  }, [user]);

  const value: AuthContextType = {
    user,
    loading,
    error,
    login,
    register,
    logout,
    refreshAuth,
    requestPasswordReset,
    resetPassword,
    changePassword,
    verifyEmail,
    resendVerification,
    updateProfile,
    clearError,
    isAuthenticated,
    hasRole,
    hasSubscription,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

export type { User, RegisterData, AuthContextType };

/**
 * Character management hook
 */

import { useState, useCallback } from 'react';

interface Character {
  id: string;
  name: string;
  race: string;
  class: string;
  background: string;
  level: number;
  hitPoints: number;
  armorClass: number;
  abilities: {
    strength: number;
    dexterity: number;
    constitution: number;
    intelligence: number;
    wisdom: number;
    charisma: number;
  };
  [key: string]: any;
}

interface UseCharacterReturn {
  characters: Character[];
  loading: boolean;
  error: string | null;
  createCharacter: (_characterData: Partial<Character>) => Promise<Character>;
  getCharacter: (_id: string) => Promise<Character>;
  updateCharacter: (_id: string, _updates: Partial<Character>) => Promise<Character>;
  deleteCharacter: (_id: string) => Promise<void>;
  getUserCharacters: () => Promise<Character[]>;
}

export const _useCharacter = (): UseCharacterReturn => {
  const [characters, setCharacters] = useState<Character[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const apiCall = async (_endpoint: string, options: RequestInit = {}) => {
    const token = localStorage.getItem('token');
    const response = await fetch(`/api${endpoint}`, {
      ...options,
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
        ...options.headers,
      },
    });

    if (!response.ok) {
      const errorData = await response.json().catch(() => ({ error: 'Network error' }));
      throw new Error(errorData.error || `HTTP ${response.status}`);
    }

    return response.json();
  };

  const createCharacter = useCallback(async (characterData: Partial<Character>): Promise<Character> => {
    setLoading(true);
    setError(null);

    try {
      const result = await apiCall('/characters', {
        method: 'POST',
        body: JSON.stringify(characterData),
      });

      const newCharacter = result.data || result;
      setCharacters(prev => [...prev, newCharacter]);
      return newCharacter;
    } catch (err: any) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  }, []);

  const getCharacter = useCallback(async (id: string): Promise<Character> => {
    setLoading(true);
    setError(null);

    try {
      const result = await apiCall(`/characters/${id}`);
      return result.data || result;
    } catch (err: any) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  }, []);

  const updateCharacter = useCallback(async (id: string, updates: Partial<Character>): Promise<Character> => {
    setLoading(true);
    setError(null);

    try {
      const result = await apiCall(`/characters/${id}`, {
        method: 'PUT',
        body: JSON.stringify(updates),
      });

      const updatedCharacter = result.data || result;
      setCharacters(prev => 
        prev.map(char => char.id === id ? updatedCharacter : char)
      );
      return updatedCharacter;
    } catch (err: any) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  }, []);

  const deleteCharacter = useCallback(async (id: string): Promise<void> => {
    setLoading(true);
    setError(null);

    try {
      await apiCall(`/characters/${id}`, {
        method: 'DELETE',
      });

      setCharacters(prev => prev.filter(char => char.id !== id));
    } catch (err: any) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  }, []);

  const getUserCharacters = useCallback(async (): Promise<Character[]> => {
    setLoading(true);
    setError(null);

    try {
      const result = await apiCall('/characters');
      const userCharacters = result.data || result;
      setCharacters(userCharacters);
      return userCharacters;
    } catch (err: any) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  }, []);

  return {
    characters,
    loading,
    error,
    createCharacter,
    getCharacter,
    updateCharacter,
    deleteCharacter,
    getUserCharacters,
  };
};

/**
 * Hook for encounter and combat management
 */

import { useState, useCallback, _useEffect} from 'react';

export interface CombatActor {
  id: string;
  name: string;
  type: 'character' | 'monster';

  hitPoints: {
    current: number;
    max: number;
    temporary: number;
  };
  armorClass: number;
  initiative: number;
  speed: number;

  abilities: {
    STR: { value: number; modifier: number };
    DEX: { value: number; modifier: number };
    CON: { value: number; modifier: number };
    INT: { value: number; modifier: number };
    WIS: { value: number; modifier: number };
    CHA: { value: number; modifier: number };
  };

  conditions: Array<{
    type: string;
    duration: number;
    source?: string;
  }>;

  actions: Array<{
    id: string;
    name: string;
    type: 'action' | 'bonus_action' | 'reaction';
    description: string;
    attackBonus?: number;
    damage?: {
      diceExpression: string;
      damageType: string;
    };
    saveDC?: number;
    saveAbility?: string;
  }>;

  sourceId: string;
  isPlayer: boolean;
}

export interface EncounterData {
  id: string;
  name: string;
  campaignId: string;
  actors: CombatActor[];
  currentRound: number;
  currentTurn: number;
  isActive: boolean;
}

interface UseEncounterReturn {
  encounter: EncounterData | null;
  isLoading: boolean;
  error: string | null;
  createEncounter: (_data: CreateEncounterData) => Promise<EncounterData>;
  getEncounter: (_id: string) => Promise<EncounterData | null>;
  startEncounter: (_id: string) => Promise<void>;
  endEncounter: (_id: string) => Promise<void>;
  addCharacterToEncounter: (_encounterId: string, _characterId: string) => Promise<CombatActor>;
  addMonsterToEncounter: (_encounterId: string, _monsterId: string, _instanceName?: string) => Promise<CombatActor>;
  updateActorHealth: (_actorId: string, health: { current: number; max: number; temporary?: number }) => Promise<void>;
}

interface CreateEncounterData {
  name: string;
  campaignId: string;
  characterIds?: string[];
  monsters?: Array<{ monsterId: string; instanceName?: string }>;
}

export const _useEncounter = (): UseEncounterReturn => {
  const [encounter, setEncounter] = useState<EncounterData | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const apiCall = useCallback(async (url: string, options?: RequestInit) => {
    const response = await fetch(`/api${url}`, {
      headers: {
        'Content-Type': 'application/json',
        ...options?.headers,
      },
      ...options,
    });

    if (!response.ok) {
      const errorData = await response.json().catch(() => ({ error: 'Unknown error' }));
      throw new Error(errorData.error || `HTTP ${response.status}`);
    }

    return response.json();
  }, []);

  const createEncounter = useCallback(async (data: CreateEncounterData): Promise<EncounterData> => {
    setIsLoading(true);
    setError(null);

    try {
      const result = await apiCall('/encounters', {
        method: 'POST',
        body: JSON.stringify(data),
      });

      if (result.success) {
        setEncounter(result.encounter);
        return result.encounter;
      } else {
        throw new Error(result.error || 'Failed to create encounter');
      }
    } catch (err: any) {
      setError(err.message);
      throw err;
    } finally {
      setIsLoading(false);
    }
  }, [apiCall]);

  const getEncounter = useCallback(async (id: string): Promise<EncounterData | null> => {
    setIsLoading(true);
    setError(null);

    try {
      const result = await apiCall(`/encounters/${id}`);

      if (result.success) {
        setEncounter(result.encounter);
        return result.encounter;
      } else {
        throw new Error(result.error || 'Failed to get encounter');
      }
    } catch (err: any) {
      setError(err.message);
      return null;
    } finally {
      setIsLoading(false);
    }
  }, [apiCall]);

  const startEncounter = useCallback(async (id: string): Promise<void> => {
    setIsLoading(true);
    setError(null);

    try {
      const result = await apiCall(`/encounters/${id}/start`, {
        method: 'POST',
      });

      if (result.success) {
        setEncounter(result.encounter);
      } else {
        throw new Error(result.error || 'Failed to start encounter');
      }
    } catch (err: any) {
      setError(err.message);
      throw err;
    } finally {
      setIsLoading(false);
    }
  }, [apiCall]);

  const endEncounter = useCallback(async (id: string): Promise<void> => {
    setIsLoading(true);
    setError(null);

    try {
      const result = await apiCall(`/encounters/${id}/end`, {
        method: 'POST',
      });

      if (result.success) {
        setEncounter(result.encounter);
      } else {
        throw new Error(result.error || 'Failed to end encounter');
      }
    } catch (err: any) {
      setError(err.message);
      throw err;
    } finally {
      setIsLoading(false);
    }
  }, [apiCall]);

  const addCharacterToEncounter = useCallback(async (
    encounterId: string, 
    characterId: string
  ): Promise<CombatActor> => {
    setIsLoading(true);
    setError(null);

    try {
      const result = await apiCall(`/encounters/${encounterId}/actors/character`, {
        method: 'POST',
        body: JSON.stringify({ characterId }),
      });

      if (result.success) {
        // Refresh encounter data to get updated actors list
        await getEncounter(encounterId);
        return result.actor;
      } else {
        throw new Error(result.error || 'Failed to add character to encounter');
      }
    } catch (err: any) {
      setError(err.message);
      throw err;
    } finally {
      setIsLoading(false);
    }
  }, [apiCall, getEncounter]);

  const addMonsterToEncounter = useCallback(async (
    encounterId: string, 
    monsterId: string, 
    instanceName?: string
  ): Promise<CombatActor> => {
    setIsLoading(true);
    setError(null);

    try {
      const result = await apiCall(`/encounters/${encounterId}/actors/monster`, {
        method: 'POST',
        body: JSON.stringify({ monsterId, instanceName }),
      });

      if (result.success) {
        // Refresh encounter data to get updated actors list
        await getEncounter(encounterId);
        return result.actor;
      } else {
        throw new Error(result.error || 'Failed to add monster to encounter');
      }
    } catch (err: any) {
      setError(err.message);
      throw err;
    } finally {
      setIsLoading(false);
    }
  }, [apiCall, getEncounter]);

  const updateActorHealth = useCallback(async (
    actorId: string,
    health: { current: number; max: number; temporary?: number }
  ): Promise<void> => {
    setIsLoading(true);
    setError(null);

    try {
      const result = await apiCall(`/encounters/${encounter?.id}/actors/${actorId}/health`, {
        method: 'PUT',
        body: JSON.stringify(health),
      });

      if (result.success) {
        // Update local state
        if (encounter) {
          setEncounter(prev => {
            if (!prev) return prev;

            return {
              ...prev,
              actors: prev.actors.map(actor => 
                actor.id === actorId 
                  ? { 
                      ...actor, 
                      hitPoints: { 
                        current: health.current, 
                        max: health.max, 
                        temporary: health.temporary || 0 
                      } 
                    }
                  : actor
              )
            };
          });
        }
      } else {
        throw new Error(result.error || 'Failed to update actor health');
      }
    } catch (err: any) {
      setError(err.message);
      throw err;
    } finally {
      setIsLoading(false);
    }
  }, [apiCall, encounter?.id]);

  return {
    encounter,
    isLoading,
    error,
    createEncounter,
    getEncounter,
    startEncounter,
    endEncounter,
    addCharacterToEncounter,
    addMonsterToEncounter,
    updateActorHealth,
  };
};

import React, { useState, useContext, createContext, ReactNode } from 'react';

export interface GameState {
  id: string;
  name: string;
  mapId?: string;
  isActive: boolean;
  players: GamePlayer[];
  currentUserId?: string;
  isGM: boolean;
}

export interface GamePlayer {
  id: string;
  userId: string;
  characterId?: string;
  name: string;
  isOnline: boolean;
}

interface GameContextType {
  currentGame: GameState | null;
  isLoading: boolean;
  error: string | null;
  joinGame: (_gameId: string) => Promise<void>;
  leaveGame: () => Promise<void>;
  updateGameState: (_updates: Partial<GameState>) => void;
}

const GameContext = createContext<GameContextType | undefined>(undefined);

export const GameProvider: React.FC<{ children: ReactNode }> = ({_children}) => {
  const [currentGame, setCurrentGame] = useState<GameState | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const joinGame = async (_gameId: string) => {
    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch(`/api/games/${gameId}/join`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error(`Failed to join game: ${response.statusText}`);
      }

      const gameData = await response.json();
      setCurrentGame(gameData);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error');
      console.error('Failed to join game:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const leaveGame = async () => {
    if (!currentGame) return;

    setIsLoading(true);

    try {
      const response = await fetch(`/api/games/${currentGame.id}/leave`, {
        method: 'POST',
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error(`Failed to leave game: ${response.statusText}`);
      }

      setCurrentGame(null);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error');
      console.error('Failed to leave game:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const updateGameState = (_updates: Partial<GameState>) => {
    setCurrentGame(prev => prev ? { ...prev, ...updates } : null);
  };

  return (
    <GameContext.Provider value={{
      currentGame,
      isLoading,
      error,
      joinGame,
      leaveGame,
      updateGameState,
    }}>
      {children}
    </GameContext.Provider>
  );
};

export const useGame = () => {
  const context = useContext(GameContext);
  if (context === undefined) {
    throw new Error('useGame must be used within a GameProvider');
  }
  return context;
};

import { useEffect, useRef, useState } from 'react';
import { io, Socket } from 'socket.io-client';

export interface SocketUser {
  id: string;
  displayName: string;
  campaignId?: string;
  sceneId?: string;
}

export interface UseSocketReturn {
  socket: Socket | null;
  isConnected: boolean;
  user: SocketUser | null;
  authenticate: (_userId: string, _campaignId?: string) => void;
  joinScene: (_sceneId: string) => void;
  sendMessage: (_text: string, _channel?: string) => void;
}

export const _useSocket = (serverUrl: string = 'http://localhost:8080'): UseSocketReturn => {
  const [socket, setSocket] = useState<Socket | null>(null);
  const [isConnected, setIsConnected] = useState(false);
  const [user, setUser] = useState<SocketUser | null>(null);
  const reconnectTimeoutRef = useRef<number>();

  useEffect(() => {
    const newSocket = io(serverUrl, {
      autoConnect: false,
      transports: ['websocket', 'polling'],
      reconnection: true,
      reconnectionAttempts: 5,
      reconnectionDelay: 1000,
      reconnectionDelayMax: 5000,
      timeout: 10000
    });

    // Connection events
    newSocket.on('connect', () => {
      console.log('Connected to server:', newSocket.id);
      setIsConnected(true);

      // Clear any reconnection timeout
      if (reconnectTimeoutRef.current) {
        clearTimeout(reconnectTimeoutRef.current);
      }
    });

    newSocket.on('disconnect', (reason) => {
      console.log('Disconnected from server:', reason);
      setIsConnected(false);
      setUser(null);
    });

    newSocket.on('connect_error', (error) => {
      console.error('Connection error:', error);
      setIsConnected(false);
    });

    // Authentication events
    newSocket.on('authenticated', (data: { user: SocketUser }) => {
      console.log('Authenticated as:', data.user);
      setUser(data.user);
    });

    newSocket.on('auth_error', (data: { message: string }) => {
      console.error('Authentication failed:', data.message);
      setUser(null);
    });

    // Error handling
    newSocket.on('error', (data: { message: string }) => {
      console.error('Socket error:', data.message);
    });

    // Set up reconnection logic
    newSocket.on('reconnect', (attemptNumber) => {
      console.log(`Reconnected after ${attemptNumber} attempts`);

      // Re-authenticate if we had a user before
      const savedUser = localStorage.getItem('vtt_user');
      const savedCampaign = localStorage.getItem('vtt_campaign');

      if (savedUser && savedCampaign) {
        const userData = JSON.parse(savedUser);
        const campaignData = JSON.parse(savedCampaign);
        newSocket.emit('authenticate', {
          userId: userData.id,
          campaignId: campaignData.id
        });
      }
    });

    newSocket.on('reconnect_failed', () => {
      console.error('Failed to reconnect to server');
    });

    setSocket(newSocket);

    // Auto-connect
    newSocket.connect();

    return () => {
      if (reconnectTimeoutRef.current) {
        clearTimeout(reconnectTimeoutRef.current);
      }
      newSocket.close();
    };
  }, [serverUrl]);

  const authenticate = (_userId: string, _campaignId?: string) => {
    if (!socket) return;

    socket.emit('authenticate', { userId, campaignId });

    // Save to localStorage for reconnection
    localStorage.setItem('vtt_user', JSON.stringify({ id: userId }));
    if (campaignId) {
      localStorage.setItem('vtt_campaign', JSON.stringify({ id: campaignId }));
    }
  };

  const joinScene = (_sceneId: string) => {
    if (!socket) return;
    socket.emit('join_scene', { sceneId });
  };

  const sendMessage = (_text: string, _channel?: string) => {
    if (!socket) return;
    socket.emit('send_message', { text, channel });
  };

  return {
    socket,
    isConnected,
    user,
    authenticate,
    joinScene,
    sendMessage
  };
};

/**
 * WebSocket hook for real-time VTT communication
 */

import { useState, useEffect, useCallback, useRef } from 'react';

export interface VTTWebSocketMessage {
  type: 'token_move' | 'token_add' | 'token_remove' | 'scene_update' | 'combat_update' | 'user_join' | 'user_leave' | 'ping' | 'pong' | 'error';
  payload: any;
  sessionId: string;
  userId: string;
  timestamp: number;
}

interface UseWebSocketOptions {
  sessionId?: string;
  userId?: string;
  campaignId?: string;
  isGM?: boolean;
  autoConnect?: boolean;
  reconnectAttempts?: number;
  reconnectInterval?: number;
}

interface UseWebSocketReturn {
  socket: {
    emit: (event: string, _data: any) => void;
    on: (event: string, _callback: (data: any) => void) => void;
    off: (event: string, _callback?: (data: any) => void) => void;
  } | null;
  isConnected: boolean;
  isConnecting: boolean;
  send: (message: VTTWebSocketMessage) => void;
  lastMessage: VTTWebSocketMessage | null;
  error: string | null;
  connect: () => void;
  disconnect: () => void;
  connectedUsers: number;
}

export const _useWebSocket = (options: UseWebSocketOptions = {}): UseWebSocketReturn => {
  const { sessionId, 
    userId, 
    campaignId, 
    isGM = false, 
    autoConnect = true, 
    reconnectAttempts = 5, 
    reconnectInterval = 3000
   } = options;

  const [isConnected, setIsConnected] = useState(false);
  const [isConnecting, setIsConnecting] = useState(false);
  const [lastMessage, setLastMessage] = useState<VTTWebSocketMessage | null>(null);
  const [connectedUsers, setConnectedUsers] = useState(0);
  const [error, setError] = useState<string | null>(null);

  const wsRef = useRef<WebSocket | null>(null);
  const reconnectCountRef = useRef(0);
  const reconnectTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  const connect = useCallback(() => {
    if (wsRef.current?.readyState === WebSocket.OPEN) {
      return;
    }

    if (!sessionId || !userId || !campaignId) {
      setError('Missing required connection parameters');
      return;
    }

    setIsConnecting(true);
    setError(null);

    try {
      const wsUrl = `ws://localhost:8080?sessionId=${sessionId}&userId=${userId}&campaignId=${campaignId}&isGM=${isGM}`;
      const ws = new WebSocket(wsUrl);
      wsRef.current = ws;

      ws.onopen = () => {
        setIsConnected(true);
        setIsConnecting(false);
        setError(null);
        reconnectCountRef.current = 0;
        console.log('WebSocket connected');
      };

      ws.onmessage = (event) => {
        try {
          const message: VTTWebSocketMessage = JSON.parse(event.data);
          setLastMessage(message);

          // Update connected users count
          if (message.type === 'user_join' || message.type === 'user_leave') {
            setConnectedUsers(message.payload.userCount || 0);
          }
        } catch (err) {
          console.error('Failed to parse WebSocket message:', err);
        }
      };

      ws.onclose = (event) => {
        setIsConnected(false);
        setIsConnecting(false);
        console.log('WebSocket disconnected:', event.code, event.reason);

        // Attempt to reconnect if not manually closed
        if (event.code !== 1000 && reconnectCountRef.current < reconnectAttempts) {
          reconnectCountRef.current += 1;
          reconnectTimeoutRef.current = setTimeout(() => {
            console.log(`Reconnecting... (${reconnectCountRef.current}/${reconnectAttempts})`);
            connect();
          }, reconnectInterval);
        }
      };

      ws.onerror = (event) => {
        setError('WebSocket connection error');
        setIsConnecting(false);
        console.error('WebSocket error:', event);
      };

    } catch (err: any) {
      setError(err.message);
      setIsConnecting(false);
    }
  }, [sessionId, userId, campaignId, isGM, reconnectAttempts, reconnectInterval]);

  const disconnect = useCallback(() => {
    if (reconnectTimeoutRef.current) {
      clearTimeout(reconnectTimeoutRef.current);
      reconnectTimeoutRef.current = null;
    }

    if (wsRef.current) {
      wsRef.current.close(1000, 'Manual disconnect');
      wsRef.current = null;
    }

    setIsConnected(false);
    setIsConnecting(false);
    reconnectCountRef.current = 0;
  }, []);

  const send = useCallback((message: VTTWebSocketMessage) => {
    if (wsRef.current?.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify(message));
    } else {
      console.warn('WebSocket not connected, message not sent:', message);
    }
  }, []);

  useEffect(() => {
    if (autoConnect) {
      connect();
    }

    return () => {
      disconnect();
    };
  }, [autoConnect, connect, disconnect]);

  // Create socket-like interface for compatibility
  const socketInterface = wsRef.current ? {
    emit: (event: string, _data: any) => {
      const message: VTTWebSocketMessage = {
        type: event as any,
        payload: data,
        sessionId: sessionId || '',
        userId: userId || '',
        timestamp: Date.now()
      };
      send(message);
    },
    on: (event: string, _callback: (data: any) => void) => {
      // Simple event handling - in a real implementation this would be more sophisticated
      if (wsRef.current) {
        const handler = (e: MessageEvent) => {
          try {
            const message = JSON.parse(e.data);
            if (message.type === event) {
              callback(message.payload);
            }
          } catch (err) {
            console.error('Failed to parse WebSocket message:', err);
          }
        };
        wsRef.current.addEventListener('message', handler);
      }
    },
    off: (_event: string, _callback?: (data: any) => void) => {
      // Remove event listener - simplified implementation
      // In a real implementation, we'd track listeners properly
    }
  } : null;

  return {
    socket: socketInterface,
    isConnected,
    isConnecting,
    send,
    lastMessage,
    error,
    connect,
    disconnect,
    connectedUsers
  };
};

/**
 * API Client - Configured axios instance for VTT Platform API
 */
import axios, { AxiosInstance, AxiosRequestConfig, AxiosResponse } from 'axios';

interface ApiResponse<T = any> {
  success: boolean;
  data?: T;
  error?: string;
  message?: string;
}

class ApiClient {
  private client: AxiosInstance;

  constructor() {
    this.client = axios.create({
      baseURL: process.env.NEXT_PUBLIC_API_URL || 'http://localhost:3001/api/v1',
      timeout: 10000,
      withCredentials: true, // Include cookies for session management
      headers: {
        'Content-Type': 'application/json',
      },
    });

    this.setupInterceptors();
  }

  private setupInterceptors() {
    // Request interceptor for adding auth token
    this.client.interceptors.request.use((_config) => {
        // Add timestamp to prevent caching
        config.headers['X-Request-Time'] = new Date().toISOString();

        // Add CSRF token if available
        const csrfToken = document.querySelector('meta[name="csrf-token"]')?.getAttribute('content');
        if (csrfToken) {
          config.headers['X-CSRF-Token'] = csrfToken;
        }

        return config;
      },
      (error) => Promise.reject(error)
    );

    // Response interceptor for error handling
    this.client.interceptors.response.use(
      (_response: AxiosResponse) => response,
      (error) => {
        // Handle network errors
        if (!error.response) {
          console.error('Network error:', error.message);
          return Promise.reject(new Error('Network error. Please check your connection.'));
        }

        // Handle specific HTTP status codes
        const { status,  data  } = error.response;

        switch (status) {
          case 401:
            // Unauthorized - redirect to login or refresh token
            if (typeof window !== 'undefined' && !window.location.pathname.includes('/auth')) {
              window.location.href = '/auth/login';
            }
            break;
          case 403:
            // Forbidden - show permission error
            console.error('Permission denied:', data?.error || 'Access forbidden');
            break;
          case 429:
            // Rate limited
            console.error('Rate limit exceeded:', data?.error || 'Too many requests');
            break;
          case 500:
            // Server error
            console.error('Server error:', data?.error || 'Internal server error');
            break;
        }

        return Promise.reject(error);
      }
    );
  }

  // Generic request method
  async request<T>(config: AxiosRequestConfig): Promise<AxiosResponse<ApiResponse<T>>> {
    return this.client.request(config);
  }

  // GET request
  async get<T>(url: string, config?: AxiosRequestConfig): Promise<AxiosResponse<ApiResponse<T>>> {
    return this.client.get(url, config);
  }

  // POST request
  async post<T>(url: string, data?: any, config?: AxiosRequestConfig): Promise<AxiosResponse<ApiResponse<T>>> {
    return this.client.post(url, data, config);
  }

  // PUT request
  async put<T>(url: string, data?: any, config?: AxiosRequestConfig): Promise<AxiosResponse<ApiResponse<T>>> {
    return this.client.put(url, data, config);
  }

  // PATCH request
  async patch<T>(url: string, data?: any, config?: AxiosRequestConfig): Promise<AxiosResponse<ApiResponse<T>>> {
    return this.client.patch(url, data, config);
  }

  // DELETE request
  async delete<T>(url: string, config?: AxiosRequestConfig): Promise<AxiosResponse<ApiResponse<T>>> {
    return this.client.delete(url, config);
  }

  // Upload file
  async upload<T>(_url: string, _file: File, _onProgress?: (progress: number) => void): Promise<AxiosResponse<ApiResponse<T>>> {
    const formData = new FormData();
    formData.append('file', file);

    return this.client.post(_url, formData, _{
      headers: {
        'Content-Type': 'multipart/form-data',
      _},
      _onUploadProgress: (progressEvent) => {
        if (onProgress && progressEvent.total) {
          const progress = Math.round((progressEvent.loaded * 100) / progressEvent.total);
          onProgress(progress);
        }
      },
    });
  }

  // Download file
  async download(url: string, filename?: string): Promise<void> {
    const response = await this.client.get(url, {
      responseType: 'blob',
    });

    const blob = new Blob([response.data]);
    const downloadUrl = window.URL.createObjectURL(blob);

    const link = document.createElement('a');
    link.href = downloadUrl;
    link.download = filename || 'download';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);

    window.URL.revokeObjectURL(downloadUrl);
  }

  // Set authorization header
  setAuthToken(token: string) {
    this.client.defaults.headers.common['Authorization'] = `Bearer ${token}`;
  }

  // Remove authorization header
  clearAuthToken() {
    delete this.client.defaults.headers.common['Authorization'];
  }

  // Get base URL
  getBaseURL(): string {
    return this.client.defaults.baseURL || '';
  }
}

