                        <div className="flex items-center gap-1">
                          <Users className="h-4 w-4" />
                          <span>
                            {sessionInfo.players.length}/{sessionInfo.settings.maxPlayers} players
                          </span>
                        </div>
                        <div>
                          Last active: {new Date(sessionInfo.lastActivity).toLocaleDateString()}
                        </div>
                      </div>
                    </div>

                    <div className="flex items-center gap-2">
                      {sessionInfo.status === 'waiting' && !sessionInfo.settings.isPrivate && (
                        <Button
                          variant="primary"
                          size="sm"
                          onClick={() => handleJoinSession(sessionInfo.id)}
                          disabled={gameLoading}
                        >
                          Join Game
                        </Button>
                      )}

                      {sessionInfo.status === 'active' && sessionInfo.settings.allowSpectators && (
                        <Button
                          variant="secondary"
                          size="sm"
                          leftIcon={<Eye className="h-4 w-4" />}
                          onClick={() => handleJoinSession(sessionInfo.id)}
                        >
                          Spectate
                        </Button>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Create Session Modal (placeholder) */}
      {showCreateModal && (
        <div className="fixed inset-0 bg-bg-overlay flex items-center justify-center z-50 p-4">
          <div className="bg-bg-secondary border border-border-primary rounded-xl p-6 w-full max-w-md">
            <h3 className="text-lg font-semibold text-text-primary mb-4">
              Create New Session
            </h3>
            <p className="text-text-secondary mb-4">
              Session creation will be implemented in the next phase.
            </p>
            <div className="flex justify-end gap-3">
              <Button
                variant="secondary"
                onClick={() => setShowCreateModal(false)}
              >
                Cancel
              </Button>
              <Button variant="primary" disabled>
                Coming Soon
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

/**
 * Game Session Page - Main game interface with map, chat, and controls
 */

import React, { useState, useEffect } from 'react';
import { _useRouter } from '../components/Router';
import { useAuth } from '../providers/AuthProvider';
import { _useWebSocket } from '../providers/WebSocketProvider';
import { useGame } from '../providers/GameProvider';
import { ChatSystem } from '../components/game/ChatSystem';
import { DiceRoller } from '../components/game/DiceRoller';
import { PlayerPanel } from '../components/game/PlayerPanel';
import { BattleMap } from '../components/map/BattleMap';
import { Button } from '../components/ui/Button';
import { LoadingSpinner } from '../components/ui/LoadingSpinner';
import { cn } from '../lib/utils';
import { 
  MessageCircle, 
  Dice6, 
  Users, 
  Map,
  ChevronLeft,
  ChevronRight,
  Settings,
  Maximize,
  Minimize,
  LogOut,
  Wifi,
  WifiOff,
  Menu,
  X,
  Home
} from 'lucide-react';

interface GameSessionProps {
  sessionId: string;
  router: {
    navigate: (_path: string) => void;
    currentPath: string;
  };
}

type PanelType = 'chat' | 'dice' | 'players' | 'map' | null;

export function GameSession({ sessionId, router }: GameSessionProps) {
  const { user  } = useAuth();
  const { session,  joinSession,  leaveSession,  isLoading  } = useGame();
  const [activePanel, setActivePanel] = useState<PanelType>('chat');
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);

  // Join session on mount
  useEffect(() => {
    if (!session || session.id !== sessionId) {
      joinSession(sessionId);
    }
  }, [sessionId, session, joinSession]);

  // Handle session not found or loading
  if (isLoading) {
    return (
      <div className="min-h-screen bg-bg-primary flex items-center justify-center">
        <LoadingSpinner size="xl" showLabel label="Loading game session..." />
      </div>
    );
  }

  if (!session) {
    return (
      <div className="min-h-screen bg-bg-primary flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-error mb-4">
            Session Not Found
          </h1>
          <p className="text-text-secondary mb-6">
            The game session "{sessionId}" could not be found or you don't have permission to access it.
          </p>
          <Button onClick={() => router.navigate('/dashboard')}>
            Return to Dashboard
          </Button>
        </div>
      </div>
    );
  }

  const handleLeaveSession = async () => {
    await leaveSession();
    router.navigate('/dashboard');
  };

  const toggleFullscreen = () => {
    if (!document.fullscreenElement) {
      document.documentElement.requestFullscreen();
      setIsFullscreen(true);
    } else {
      document.exitFullscreen();
      setIsFullscreen(false);
    }
  };

  const renderActivePanel = () => {
    switch (activePanel) {
      case 'chat':
        return <ChatSystem className="h-full" />;
      case 'dice':
        return <DiceRoller className="h-full" />;
      case 'players':
        return <PlayerPanel className="h-full" />;
      case 'map':
        return (
          <div className="h-full bg-bg-secondary rounded-lg border border-border-primary flex items-center justify-center">
            <div className="text-center">
              <Map className="h-12 w-12 text-text-tertiary mx-auto mb-4" />
              <h3 className="text-lg font-medium text-text-primary mb-2">
                Tactical Map
              </h3>
              <p className="text-text-secondary">
                Map and grid system will be implemented in the next phase
              </p>
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="h-screen bg-bg-primary flex flex-col overflow-hidden">
      {/* Header */}
      <header className="bg-bg-secondary border-b border-border-primary px-4 py-3 flex items-center justify-between flex-shrink-0">
        <div className="flex items-center gap-4">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setSidebarCollapsed(!sidebarCollapsed)}
            leftIcon={sidebarCollapsed ? <Menu className="h-4 w-4" /> : <X className="h-4 w-4" />}
          >
            {sidebarCollapsed ? 'Show' : 'Hide'} Panels
          </Button>

          <div>
            <h1 className="font-semibold text-text-primary">{session.name}</h1>
            <p className="text-sm text-text-secondary">
              GM: {session.gamemaster.displayName}
            </p>
          </div>
        </div>

        <div className="flex items-center gap-2">
          <Button
            variant="ghost"
            size="sm"
            onClick={toggleFullscreen}
            leftIcon={isFullscreen ? <Minimize2 className="h-4 w-4" /> : <Maximize2 className="h-4 w-4" />}
          >
            {isFullscreen ? 'Exit' : 'Fullscreen'}
          </Button>

          <Button
            variant="ghost"
            size="sm"
            leftIcon={<Settings className="h-4 w-4" />}
          >
            Settings
          </Button>

          <Button
            variant="secondary"
            size="sm"
            onClick={handleLeaveSession}
            leftIcon={<Home className="h-4 w-4" />}
          >
            Leave Session
          </Button>
        </div>
      </header>

      <div className="flex-1 flex overflow-hidden">
        {/* Sidebar */}
        {!sidebarCollapsed && (
          <aside className="w-80 bg-bg-secondary border-r border-border-primary flex flex-col">
            {/* Panel Tabs */}
            <div className="border-b border-border-primary">
              <div className="flex">
                {[
                  { key: 'chat', _label: 'Chat', _icon: MessageSquare },
                  { key: 'dice', _label: 'Dice', _icon: Dice6 },
                  { key: 'players', _label: 'Players', _icon: Users },
                  { key: 'map', _label: 'Map', _icon: Map }
                ].map(({ key, _label, _icon: Icon }) => (
                  <button
                    key={key}
                    onClick={() => setActivePanel(key as PanelType)}
                    className={cn(
                      'flex-1 flex items-center justify-center gap-2 px-3 py-3 text-sm font-medium transition-colors border-b-2',
                      activePanel === key
                        ? 'text-accent-primary border-accent-primary bg-accent-light'
                        : 'text-text-secondary border-transparent hover:text-text-primary hover:bg-bg-tertiary'
                    )}
                  >
                    <Icon className="h-4 w-4" />
                    <span className="hidden sm:inline">{label}</span>
                  </button>
                ))}
              </div>
            </div>

            {/* Panel Content */}
            <div className="flex-1 overflow-hidden">
              {renderActivePanel()}
            </div>
          </aside>
        )}

        {/* Main Content Area */}
        <main className="flex-1 flex flex-col overflow-hidden">
          {/* Main Content Area - Map/Tactical View */}
          <div className="flex-1 bg-bg-primary relative">
            {activePanel === 'map' ? (
              <BattleMap 
                className="h-full" 
                isGM={session?.role === 'gamemaster'} 
              />
            ) : (
              <div className="h-full flex items-center justify-center">
                <div className="text-center">
                  <div className="w-32 h-32 bg-bg-tertiary rounded-lg flex items-center justify-center mb-4 mx-auto">
                    <Map className="h-16 w-16 text-text-tertiary" />
                  </div>
                  <h3 className="text-xl font-semibold text-text-primary mb-2">
                    Game Session Active
                  </h3>
                  <p className="text-text-secondary mb-4">
                    Use the sidebar panels to chat, _roll dice, _or manage your character.
                  </p>
                  <Button
                    variant="primary"
                    onClick={() => setActivePanel('map')}
                    leftIcon={<Map className="h-4 w-4" />}
                  >
                    Open Battle Map
                  </Button>
                </div>
              </div>
            )}
            {/* Quick Actions Overlay */}
            <div className="absolute bottom-4 right-4 flex flex-col gap-2">
              {sidebarCollapsed && (
                <>
                  <Button
                    variant="primary"
                    onClick={() => {
                      setSidebarCollapsed(false);
                      setActivePanel('chat');
                    }}
                    leftIcon={<MessageSquare className="h-4 w-4" />}
                  >
                    Chat
                  </Button>
                  <Button
                    variant="secondary"
                    onClick={() => {
                      setSidebarCollapsed(false);
                      setActivePanel('dice');
                    }}
                    leftIcon={<Dice6 className="h-4 w-4" />}
                  >
                    Dice
                  </Button>
                </>
              )}
            </div>
          </div>

          {/* Status Bar */}
          <div className="bg-bg-secondary border-t border-border-primary px-4 py-2">
            <div className="flex items-center justify-between text-sm">
              <div className="flex items-center gap-4">
                <span className="text-text-secondary">
                  Session: <span className="text-text-primary">{session.status}</span>
                </span>
                <span className="text-text-secondary">
                  Players: <span className="text-text-primary">
                    {session.players.filter(p => p.isConnected).length}/{session.players.length}
                  </span>
                </span>
              </div>

              <div className="flex items-center gap-4">
                {user && (
                  <span className="text-text-secondary">
                    Playing as: <span className="text-text-primary">{user.displayName}</span>
