/**
 * Authentication Provider - Manages user authentication state and flows
 */

import React, { createContext, useContext, useEffect, useState, useCallback } from 'react';

export interface User {
  id: string;
  email: string;
  username: string;
  displayName: string;
  avatar?: string;
  role: 'admin' | 'moderator' | 'gamemaster' | 'player' | 'guest';
  permissions: string[];
  subscription: 'free' | 'basic' | 'premium' | 'enterprise';
  isEmailVerified: boolean;
  lastLogin: string;
}

export interface AuthState {
  user: User | null;
  isAuthenticated: boolean;
  isLoading: boolean;
  error: string | null;
}

interface AuthContextType extends AuthState {
  login: (_email: string, _password: string) => Promise<void>;
  logout: () => Promise<void>;
  register: (data: RegisterData) => Promise<void>;
  loginWithProvider: (_provider: 'discord' | 'google') => void;
  refreshAuth: () => Promise<void>;
  clearError: () => void;
}

interface RegisterData {
  email: string;
  username: string;
  password: string;
  displayName: string;
}

const AuthContext = createContext<AuthContextType | null>(null);

export function useAuth() {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

interface AuthProviderProps {
  children: React.ReactNode;
  serverUrl: string;
}

export function AuthProvider({_ children, _serverUrl}: AuthProviderProps) {
  const [state, setState] = useState<AuthState>({
    user: null,
    isAuthenticated: false,
    isLoading: true,
    error: null
  });

  const setError = useCallback((_error: string) => {
    setState(prev => ({ ...prev, error, isLoading: false }));
  }, []);

  const clearError = useCallback(() => {
    setState(prev => ({ ...prev, error: null }));
  }, []);

  const setUser = useCallback((_user: User | null) => {
    setState(prev => ({
      ...prev,
      user,
      isAuthenticated: !!user,
      isLoading: false,
      error: null
    }));
  }, []);

  const setLoading = useCallback((_isLoading: boolean) => {
    setState(prev => ({ ...prev, isLoading }));
  }, []);

  // Check for existing session on mount
  useEffect(() => {
    const checkAuthStatus = async () => {
      try {
        const response = await fetch(`${serverUrl}/auth/me`, {
          credentials: 'include',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('accessToken') || ''}`
          }
        });

        if (response.ok) {
          const userData = await response.json();
          setUser(userData.user);
        } else {
          // Clear any stale tokens
          localStorage.removeItem('accessToken');
          localStorage.removeItem('refreshToken');
          setUser(null);
        }
      } catch (error) {
        console.error('Auth check failed:', error);
        setUser(null);
      }
    };

    checkAuthStatus();
  }, [serverUrl, setUser]);

  const login = useCallback(async (email: string, _password: string) => {
    setLoading(true);
    clearError();

    try {
      const response = await fetch(`${serverUrl}/auth/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({ email, password })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Login failed');
      }

      // Store tokens
      if (data.tokens) {
        localStorage.setItem('accessToken', data.tokens.accessToken);
        localStorage.setItem('refreshToken', data.tokens.refreshToken);
      }

      setUser(data.user);
    } catch (error) {
      setError(error instanceof Error ? error.message : 'Login failed');
    }
  }, [serverUrl, setLoading, clearError, setError, setUser]);

  const register = useCallback(async (data: RegisterData) => {
    setLoading(true);
    clearError();

    try {
      const response = await fetch(`${serverUrl}/auth/register`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ ...data, acceptTerms: true })
      });

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.error || 'Registration failed');
      }

      // Auto-login after successful registration
      if (result.tokens) {
        localStorage.setItem('accessToken', result.tokens.accessToken);
        localStorage.setItem('refreshToken', result.tokens.refreshToken);
        setUser(result.user);
      }
    } catch (error) {
      setError(error instanceof Error ? error.message : 'Registration failed');
    }
  }, [serverUrl, setLoading, clearError, setError, setUser]);

  const logout = useCallback(async () => {
    setLoading(true);

    try {
      await fetch(`${serverUrl}/auth/logout`, {
        method: 'POST',
        credentials: 'include',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('accessToken') || ''}`
        }
      });
    } catch (error) {
      console.error('Logout request failed:', error);
    } finally {
      // Clear local state regardless of server response
      localStorage.removeItem('accessToken');
      localStorage.removeItem('refreshToken');
      setUser(null);
    }
  }, [serverUrl, setLoading, setUser]);

  const loginWithProvider = useCallback((_provider: 'discord' | 'google') => {
    // Redirect to OAuth provider
    window.location.href = `${serverUrl}/auth/${provider}`;
  }, [serverUrl]);

  const refreshAuth = useCallback(async () => {
    const refreshToken = localStorage.getItem('refreshToken');
    if (!refreshToken) {
      setUser(null);
      return;
    }

    try {
      const response = await fetch(`${serverUrl}/auth/refresh`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ refreshToken })
      });

      const data = await response.json();

      if (response.ok && data.tokens) {
        localStorage.setItem('accessToken', data.tokens.accessToken);
        localStorage.setItem('refreshToken', data.tokens.refreshToken);

        // Fetch updated user data
        const userResponse = await fetch(`${serverUrl}/auth/me`, {
          headers: {
            'Authorization': `Bearer ${data.tokens.accessToken}`
          }
        });

        if (userResponse.ok) {
          const userData = await userResponse.json();
          setUser(userData.user);
        }
      } else {
        // Refresh failed, logout user
        localStorage.removeItem('accessToken');
        localStorage.removeItem('refreshToken');
        setUser(null);
      }
    } catch (error) {
      console.error('Token refresh failed:', error);
      setUser(null);
    }
  }, [serverUrl, setUser]);

  // Set up token refresh interval
  useEffect(() => {
    if (state.isAuthenticated) {
      const interval = setInterval(() => {
        refreshAuth();
      }, 5 * 60 * 1000); // Refresh every 5 minutes

      return () => clearInterval(interval);
    }
  }, [state.isAuthenticated, refreshAuth]);

  const contextValue: AuthContextType = {
    ...state,
    login,
    logout,
    register,
    loginWithProvider,
    refreshAuth,
    clearError
  };

  return (
    <AuthContext.Provider value={contextValue}>
      {children}
    </AuthContext.Provider>
  );
}

/**
 * Game Provider - Manages game session state and real-time synchronization
 */

import React, { createContext, useContext, useEffect, useState, useCallback } from 'react';
import { useWebSocket } from './WebSocketProvider';
import { useAuth } from './AuthProvider';

export interface GameSession {
  id: string;
  name: string;
  description: string;
  gamemaster: {
    id: string;
    username: string;
    displayName: string;
  };
  players: Player[];
  currentScene?: {
    id: string;
    name: string;
    mapUrl?: string;
  };
  status: 'waiting' | 'active' | 'paused' | 'ended';
  settings: {
    maxPlayers: number;
    isPrivate: boolean;
    allowSpectators: boolean;
    voiceChatEnabled: boolean;
  };
  createdAt: string;
  lastActivity: string;
}

export interface Player {
  id: string;
  userId: string;
  username: string;
  displayName: string;
  avatar?: string;
  character?: {
    id: string;
    name: string;
    level: number;
    class: string;
    race: string;
    hitPoints: number;
    maxHitPoints: number;
  };
  isConnected: boolean;
  permissions: {
    canMoveTokens: boolean;
    canEditCharacter: boolean;
    canRollDice: boolean;
    canUseChat: boolean;
  };
  joinedAt: string;
}

export interface GameState {
  session: GameSession | null;
  isGM: boolean;
  isPlayer: boolean;
  isSpectator: boolean;
  isLoading: boolean;
  error: string | null;
  connectionStatus: 'disconnected' | 'connecting' | 'connected' | 'error';
}

interface GameContextType extends GameState {
  joinSession: (_sessionId: string) => Promise<void>;
  leaveSession: () => Promise<void>;
  createSession: (_data: CreateSessionData) => Promise<void>;
  sendGameMessage: (message: any) => void;
  updatePlayerPermissions: (_playerId: string, _permissions: Partial<Player['permissions']>) => void;
  kickPlayer: (_playerId: string) => void;
  pauseSession: () => void;
  resumeSession: () => void;
  endSession: () => void;
}

interface CreateSessionData {
  name: string;
  description: string;
  maxPlayers: number;
  isPrivate: boolean;
  allowSpectators: boolean;
}

const GameContext = createContext<GameContextType | null>(null);

export function useGame() {
  const context = useContext(GameContext);
  if (!context) {
    throw new Error('useGame must be used within a GameProvider');
  }
  return context;
}

interface GameProviderProps {
  children: React.ReactNode;
}

export function GameProvider({_ children }: GameProviderProps) {
  const { user,  isAuthenticated  } = useAuth();
  const { send,  subscribe,  isConnected  } = useWebSocket();

  const [state, setState] = useState<GameState>({
    session: null,
    isGM: false,
    isPlayer: false,
    isSpectator: false,
    isLoading: false,
    error: null,
    connectionStatus: 'disconnected'
  });

  const setError = useCallback((_error: string) => {
    setState(prev => ({ ...prev, error, isLoading: false }));
  }, []);

  const clearError = useCallback(() => {
    setState(prev => ({ ...prev, error: null }));
  }, []);

  const setLoading = useCallback((_isLoading: boolean) => {
    setState(prev => ({ ...prev, isLoading }));
  }, []);

  // Update connection status based on WebSocket state
  useEffect(() => {
    setState(prev => ({
      ...prev,
      connectionStatus: isConnected ? 'connected' : 'disconnected'
    }));
  }, [isConnected]);

  // Subscribe to game-related WebSocket messages
  useEffect(() => {
    if (!isConnected) return;

    const unsubscribers = [
      subscribe('GAME_SESSION_UPDATED', (message) => {
        setState(prev => ({
          ...prev,
          session: message.session,
          _isGM: message.session.gamemaster.id === user?.id,
          _isPlayer: message.session.players.some((p: Player) => p.userId === user?.id),
          isSpectator: !message.session.players.some((_p: Player) => p.userId === user?.id) && 
                      message.session.gamemaster.id !== user?.id
        }));
      }),

      subscribe('PLAYER_JOINED', (message) => {
        setState(prev => {
          if (!prev.session) return prev;

          const updatedSession = {
            ...prev.session,
            players: [...prev.session.players, message.player]
          };

          return { ...prev, session: updatedSession };
        });
      }),

      subscribe('PLAYER_LEFT', (message) => {
        setState(prev => {
          if (!prev.session) return prev;

          const updatedSession = {
            ...prev.session,
            players: prev.session.players.filter(p => p.id !== message.playerId)
          };

          return { ...prev, session: updatedSession };
        });
      }),

      subscribe('PLAYER_UPDATED', (message) => {
        setState(prev => {
          if (!prev.session) return prev;

          const updatedSession = {
            ...prev.session,
            players: prev.session.players.map(p => 
              p.id === message.player.id ? message.player : p
            )
          };

          return { ...prev, session: updatedSession };
        });
      }),

      subscribe('SESSION_PAUSED', () => {
        setState(prev => {
          if (!prev.session) return prev;
          return {
            ...prev,
            session: { ...prev.session, status: 'paused' }
          };
        });
      }),

      subscribe('SESSION_RESUMED', () => {
        setState(prev => {
          if (!prev.session) return prev;
          return {
            ...prev,
            session: { ...prev.session, status: 'active' }
          };
        });
      }),

      subscribe('SESSION_ENDED', () => {
        setState(prev => ({
          ...prev,
          session: null,
          isGM: false,
          isPlayer: false,
          isSpectator: false
        }));
      }),

      subscribe('GAME_ERROR', (message) => {
        setError(message.error || 'An unknown game error occurred');
      })
    ];

    return () => {
      unsubscribers.forEach(unsub => unsub());
    };
  }, [isConnected, subscribe, user?.id, setError]);

  const joinSession = useCallback(async (sessionId: string) => {
    if (!isAuthenticated || !user) {
      setError('You must be logged in to join a session');
      return;
    }

    setLoading(true);
    clearError();

    try {
      send({
        type: 'JOIN_SESSION',
        sessionId,
        userId: user.id
      });
    } catch (_error) {
      setError('Failed to join session');
      setLoading(false);
    }
  }, [isAuthenticated, user, setLoading, clearError, setError, send]);

  const leaveSession = useCallback(async () => {
    if (!state.session || !user) return;

    try {
      send({
        type: 'LEAVE_SESSION',
        sessionId: state.session.id,
        userId: user.id
      });

      setState(prev => ({
        ...prev,
        session: null,
        isGM: false,
        isPlayer: false,
        isSpectator: false
      }));
    } catch (_error) {
      setError('Failed to leave session');
    }
  }, [state.session, user, send, setError]);

  const createSession = useCallback(async (data: CreateSessionData) => {
    if (!isAuthenticated || !user) {
      setError('You must be logged in to create a session');
      return;
    }

    setLoading(true);
    clearError();

    try {
      send({
        type: 'CREATE_SESSION',
        sessionData: {
          ...data,
          gamemasterId: user.id
        }
      });
    } catch (_error) {
      setError('Failed to create session');
      setLoading(false);
    }
  }, [isAuthenticated, user, setLoading, clearError, setError, send]);

  const sendGameMessage = useCallback((message: any) => {
    if (!state.session) {
      console.warn('Cannot send game message: not in a session');
      return;
    }

    send({
      ...message,
      sessionId: state.session.id,
      userId: user?.id
    });
  }, [state.session, user?.id, send]);

  const updatePlayerPermissions = useCallback((_playerId: string, _permissions: Partial<Player['permissions']>) => {
    if (!state.isGM) {
      setError('Only the game master can update player permissions');
      return;
    }

    sendGameMessage({
      type: 'UPDATE_PLAYER_PERMISSIONS',
      playerId,
      permissions
    });
  }, [state.isGM, sendGameMessage, setError]);

  const kickPlayer = useCallback((_playerId: string) => {
    if (!state.isGM) {
      setError('Only the game master can kick players');
      return;
    }

    sendGameMessage({
      type: 'KICK_PLAYER',
      playerId
    });
  }, [state.isGM, sendGameMessage, setError]);

  const pauseSession = useCallback(() => {
    if (!state.isGM) {
      setError('Only the game master can pause the session');
      return;
    }

    sendGameMessage({
      type: 'PAUSE_SESSION'
    });
  }, [state.isGM, sendGameMessage, setError]);

  const resumeSession = useCallback(() => {
    if (!state.isGM) {
      setError('Only the game master can resume the session');
      return;
    }

    sendGameMessage({
      type: 'RESUME_SESSION'
    });
  }, [state.isGM, sendGameMessage, setError]);

  const endSession = useCallback(() => {
    if (!state.isGM) {
      setError('Only the game master can end the session');
      return;
    }

    sendGameMessage({
      type: 'END_SESSION'
    });
  }, [state.isGM, sendGameMessage, setError]);

  const contextValue: GameContextType = {
    ...state,
    joinSession,
    leaveSession,
    createSession,
    sendGameMessage,
    updatePlayerPermissions,
    kickPlayer,
    pauseSession,
    resumeSession,
    endSession
  };

  return (
    <GameContext.Provider value={contextValue}>
      {children}
    </GameContext.Provider>
  );
}

/**
 * WebSocket Provider - Manages real-time communication with game server
 */

import React, { createContext, useContext, useEffect, useState, useCallback, useRef } from 'react';
import { WSClient, WSState } from '../net/ws';
import type { AnyServerMessage, AnyClientMessage } from '@vtt/core-schemas';

interface WebSocketContextType {
  state: WSState;
  isConnected: boolean;
  latency: number;
  send: (message: AnyClientMessage) => void;
  subscribe: (_type: string, _handler: (message: any) => void) => () => void;
  connect: () => void;
  disconnect: () => void;
  reconnect: () => void;
}

const WebSocketContext = createContext<WebSocketContextType | null>(null);

export function useWebSocket() {
  const context = useContext(WebSocketContext);
  if (!context) {
    throw new Error('useWebSocket must be used within a WebSocketProvider');
  }
  return context;
}

interface WebSocketProviderProps {
  children: React.ReactNode;
  wsUrl: string;
}

interface MessageHandler {
  type: string;
  handler: (message: any) => void;
}

export function WebSocketProvider({_ children, _wsUrl}: WebSocketProviderProps) {
  const [state, setState] = useState<WSState>('disconnected');
  const [latency, setLatency] = useState<number>(0);
  const clientRef = useRef<WSClient | null>(null);
  const handlersRef = useRef<MessageHandler[]>([]);
  const pingIntervalRef = useRef<NodeJS.Timeout | null>(null);
  const reconnectTimeoutRef = useRef<NodeJS.Timeout | null>(null);
  const reconnectAttemptsRef = useRef<number>(0);
  const lastPingRef = useRef<number>(0);

  const isConnected = state === 'open';

  const startLatencyTracking = useCallback(() => {
    if (pingIntervalRef.current) return;

    pingIntervalRef.current = setInterval(() => {
      if (clientRef.current && isConnected) {
        lastPingRef.current = Date.now();
        clientRef.current.send({
          type: 'PING',
          timestamp: lastPingRef.current
        });
      }
    }, 30000); // Ping every 30 seconds
  }, [isConnected]);

  const stopLatencyTracking = useCallback(() => {
    if (pingIntervalRef.current) {
      clearInterval(pingIntervalRef.current);
      pingIntervalRef.current = null;
    }
  }, []);

  const scheduleReconnect = useCallback(() => {
    if (reconnectTimeoutRef.current) return;

    const delay = Math.min(1000 * Math.pow(2, reconnectAttemptsRef.current), 30000);
    reconnectAttemptsRef.current++;

    reconnectTimeoutRef.current = setTimeout(() => {
      reconnectTimeoutRef.current = null;
      connect();
    }, delay);
  }, []);

  const cancelReconnect = useCallback(() => {
    if (reconnectTimeoutRef.current) {
      clearTimeout(reconnectTimeoutRef.current);
      reconnectTimeoutRef.current = null;
    }
    reconnectAttemptsRef.current = 0;
  }, []);

  const connect = useCallback(() => {
    if (clientRef.current) {
      clientRef.current.close();
    }

    const client = new WSClient(wsUrl);
    clientRef.current = client;

    client.onState((_newState) => {
      setState(newState);

      if (newState === 'open') {
        console.log('WebSocket connected');
        cancelReconnect();
        startLatencyTracking();
      } else if (newState === 'disconnected') {
        console.log('WebSocket disconnected');
        stopLatencyTracking();

        // Auto-reconnect unless explicitly disconnected
        if (reconnectAttemptsRef.current < 10) {
          scheduleReconnect();
        }
      }
    });

    client.onMessage((message: AnyServerMessage) => {
      // Handle system messages
      if (message.type === 'PONG') {
        const now = Date.now();
        const roundTripTime = now - lastPingRef.current;
        setLatency(roundTripTime);
        return;
      }

      if (message.type === 'ERROR') {
        console.error('WebSocket error:', message);
        return;
      }

      // Distribute message to registered handlers
      handlersRef.current.forEach(_({ type, _handler}) => {
        if (type === message.type || type === '*') {
          try {
            handler(message);
          } catch (error) {
            console.error(`Error in message handler for ${message.type}:`, error);
          }
        }
      });
    });

    client.connect();
  }, [wsUrl, cancelReconnect, startLatencyTracking, stopLatencyTracking, scheduleReconnect]);

  const disconnect = useCallback(() => {
    cancelReconnect();
    stopLatencyTracking();

    if (clientRef.current) {
      clientRef.current.close();
      clientRef.current = null;
    }

    setState('disconnected');
  }, [cancelReconnect, stopLatencyTracking]);

  const reconnect = useCallback(() => {
    disconnect();
    setTimeout(connect, 100);
  }, [disconnect, connect]);

  const send = useCallback((message: AnyClientMessage) => {
    if (clientRef.current && isConnected) {
      clientRef.current.send(message);
    } else {
      console.warn('Cannot send message: WebSocket not connected', message);
    }
  }, [isConnected]);

  const subscribe = useCallback((_type: string, _handler: (message: any) => void) => {
    const messageHandler: MessageHandler = { type, handler };
    handlersRef.current.push(messageHandler);

    return () => {
      const index = handlersRef.current.indexOf(messageHandler);
      if (index > -1) {
        handlersRef.current.splice(index, 1);
      }
    };
  }, []);

  // Auto-connect on mount
  useEffect(() => {
    connect();

    return () => {
      disconnect();
    };
  }, [connect, disconnect]);

  // Handle page visibility changes
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (document.hidden) {
        // Page hidden, reduce activity
        stopLatencyTracking();
      } else {
        // Page visible, resume activity
        if (isConnected) {
          startLatencyTracking();
        } else {
          // Try to reconnect if disconnected while hidden
          connect();
        }
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);
    return () => document.removeEventListener('visibilitychange', handleVisibilityChange);
  }, [isConnected, startLatencyTracking, stopLatencyTracking, connect]);

  // Handle online/offline events
  useEffect(() => {
    const handleOnline = () => {
      if (!isConnected) {
        connect();
      }
    };

    const handleOffline = () => {
      // Don't disconnect immediately, but stop trying to reconnect
      cancelReconnect();
    };

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, [isConnected, connect, cancelReconnect]);

  const contextValue: WebSocketContextType = {
    state,
    isConnected,
    latency,
    send,
    subscribe,
    connect,
    disconnect,
    reconnect
  };

  return (
    <WebSocketContext.Provider value={contextValue}>
      {children}
    </WebSocketContext.Provider>
  );
}

import React from "react";
import SRDMonsters from "@vtt/content-5e-srd";
import { compileMonsters } from "@vtt/rules-5e";

export function MonsterBrowser() {
  const monsters = React.useMemo(() => compileMonsters(SRDMonsters), []);

  return (
    <div style={{ padding: 16, _fontFamily: "system-ui, _sans-serif" }}>
      <h2 style={{ margin: "8px 0" }}>SRD Monsters</h2>
      <div style={{ fontSize: 12, _color: "#555", _marginBottom: 12 }}>{monsters.length} entries</div>
      <div style={{ overflowX: "auto", _border: "1px solid #eee", _borderRadius: 8 }}>
        <table style={{ width: "100%", _borderCollapse: "collapse" }}>
          <thead>
            <tr style={{ background: "#fafafa", _textAlign: "left" }}>
              <th style={th}>Name</th>
              <th style={th}>CR</th>
              <th style={th}>XP</th>
              <th style={th}>AC</th>
              <th style={th}>HP</th>
              <th style={th}>Speed</th>
              <th style={th}>Type</th>
              <th style={th}>Tags</th>
            </tr>
          </thead>
          <tbody>
            {monsters.map((m) => (
              <tr key={m.id} style={{ borderTop: "1px solid #f0f0f0" }}>
                <td style={td}>{m.name}</td>
                <td style={td}>{m.challengeRating}</td>
                <td style={td}>{m.xp}</td>
                <td style={td}>
                  {m.ac.value}
                  {m.ac.type ? ` (${m.ac.type})` : ""}
                </td>
                <td style={td}>
                  {m.hp.average}
                  {m.hp.formula ? ` (${m.hp.formula})` : ""}
                </td>
                <td style={td}>{formatSpeed(m.speed)}</td>
                <td style={td}>{m.type}</td>
                <td style={td}>{(m.tags ?? []).join(", ")}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

const th: React.CSSProperties = { padding: "8px 10px", borderBottom: "1px solid #eee" };
const td: React.CSSProperties = { padding: "8px 10px", verticalAlign: "top" };

function formatSpeed(_speed: Record<string, _unknown> | undefined) {
  const entries = Object.entries(speed || {}).filter(_([, _v]) => typeof v === "number") as [
    string,
    number,
  ][];
  if (!entries.length) return "-";
  return entries
    .map([k, _v] => `${k.replace(_/^[a-z]/, (c) => c.toUpperCase())} ${v} ft.`)
    .join(", ");
}

export default MonsterBrowser;

/**
 * AI Assistant service for natural language rule queries and game assistance
 */

import { PrismaClient } from "@prisma/client";
import { OpenAIProvider, AnthropicProvider } from "@vtt/ai";

export interface AssistantQuery {
  question: string;
  context?: {
    gameSystem?: string;
    campaignId?: string;
    sceneId?: string;
    playerLevel?: number;
    characterClass?: string;
  };
}

export interface AssistantResponse {
  answer: string;
  sources?: string[];
  confidence: number;
  metadata: {
    provider: string;
    model: string;
    costUSD: number;
    latencyMs: number;
    timestamp: Date;
  };
}

export function createAIAssistantService(prisma: PrismaClient) {
  const openaiKey = process.env.OPENAI_API_KEY;
  const anthropicKey = process.env.ANTHROPIC_API_KEY;

  let openaiProvider: OpenAIProvider | null = null;
  let anthropicProvider: AnthropicProvider | null = null;

  if (openaiKey && openaiKey.trim().length > 0) {
    openaiProvider = new OpenAIProvider({ apiKey: openaiKey });
  }

  if (anthropicKey && anthropicKey.trim().length > 0) {
    anthropicProvider = new AnthropicProvider({ apiKey: anthropicKey });
  }

  const D5E_SYSTEM_PROMPT = `You are an expert D&D 5th Edition rules assistant and game master. You have comprehensive knowledge of:
- Player's Handbook, Dungeon Master's Guide, Monster Manual
- All official D&D 5e rules, spells, classes, races, and mechanics
- Combat mechanics, spell interactions, and edge cases
- Character creation and advancement
- Game balance and encounter design

Provide accurate, helpful answers with specific rule references when possible. If a rule is ambiguous or has multiple interpretations, explain the options. Always prioritize official rules over homebrew unless specifically asked about homebrew content.

Format your responses clearly with:
1. Direct answer to the question
2. Relevant rule references (book and page if known)
3. Any important caveats or edge cases
4. Practical gameplay advice when appropriate`;

  async function queryRules(query: AssistantQuery): Promise<AssistantResponse> {
    const provider = anthropicProvider || openaiProvider;
    if (!provider) {
      throw new Error('No AI provider configured. Set OPENAI_API_KEY or ANTHROPIC_API_KEY environment variable.');
    }

    const _started = Date.now();
    const gameSystem = query.context?.gameSystem || 'D&D 5e';

    const contextualPrompt = `${query.question}

Context:
- Game System: ${gameSystem}
- Player Level: ${query.context?.playerLevel || 'Unknown'}
- Character Class: ${query.context?.characterClass || 'Unknown'}
${query.context?.campaignId ? `- Campaign ID: ${query.context.campaignId}` : ''}`;

    try {
      let result;

      if (provider instanceof AnthropicProvider) {
        result = await provider.generateText(contextualPrompt, {
          systemPrompt: D5E_SYSTEM_PROMPT,
          maxTokens: 1000,
          temperature: 0.3, // Lower temperature for more consistent rule answers
        });
      } else if (provider instanceof OpenAIProvider) {
        result = await provider.generateText(contextualPrompt, {
          systemPrompt: D5E_SYSTEM_PROMPT,
          maxTokens: 1000,
          temperature: 0.3,
        });
      } else {
        throw new Error('Unsupported provider type');
      }

      // Log the query for analytics
      await prisma.generationJob.create({
        data: {
          type: 'TEXT_TO_IMAGE', // We'd need RULE_QUERY type in schema
          status: 'SUCCEEDED',
          input: { query: query.question, context: query.context } as any,
          output: { answer: result.text } as any,
        },
      });

      return {
        answer: result.text,
        confidence: 0.85, // Could be calculated based on response patterns
        metadata: {
          provider: provider instanceof AnthropicProvider ? 'anthropic' : 'openai',
          model: result.model,
          costUSD: result.costUSD,
          latencyMs: result.latencyMs,
          timestamp: new Date(),
        },
      };
    } catch (error: any) {
      await prisma.generationJob.create({
        data: {
          type: 'TEXT_TO_IMAGE',
          status: 'FAILED',
          input: { query: query.question } as any,
          error: error.message,
        },
      });

      throw error;
    }
  }

  async function explainSpell(_spellName: string, context?: AssistantQuery['context']) {
    const payload: AssistantQuery = context
      ? {
          question: `Explain the D&D 5e spell "${spellName}" including its mechanics, components, duration, and common use cases.`,
          context,
        }
      : {
          question: `Explain the D&D 5e spell "${spellName}" including its mechanics, components, duration, and common use cases.`,
        };
    return queryRules(payload);
  }

  async function explainRule(_ruleTopic: string, context?: AssistantQuery['context']) {
    const payload: AssistantQuery = context
      ? {
          question: `Explain the D&D 5e rule about "${ruleTopic}" with specific mechanics and examples.`,
          context,
        }
      : {
          question: `Explain the D&D 5e rule about "${ruleTopic}" with specific mechanics and examples.`,
        };
    return queryRules(payload);
  }

  async function suggestActions(_situation: string, context?: AssistantQuery['context']) {
    const payload: AssistantQuery = context
      ? {
          question: `In D&D 5e, what are the best action options for: ${situation}? Consider tactical advantages and rule interactions.`,
          context,
        }
      : {
          question: `In D&D 5e, what are the best action options for: ${situation}? Consider tactical advantages and rule interactions.`,
        };
    return queryRules(payload);
  }

  async function clarifyInteraction(_interaction: string, context?: AssistantQuery['context']) {
    const payload: AssistantQuery = context
      ? {
          question: `How do these D&D 5e mechanics interact: ${interaction}? Explain any special rules or edge cases.`,
          context,
        }
      : {
          question: `How do these D&D 5e mechanics interact: ${interaction}? Explain any special rules or edge cases.`,
        };
    return queryRules(payload);
  }

  async function generateRuling(_scenario: string, context?: AssistantQuery['context']) {
    const payload: AssistantQuery = context
      ? {
          question: `As a D&D 5e DM, how would you rule on this scenario: ${scenario}? Provide reasoning based on official rules and game balance.`,
          context,
        }
      : {
          question: `As a D&D 5e DM, how would you rule on this scenario: ${scenario}? Provide reasoning based on official rules and game balance.`,
        };
    return queryRules(payload);
  }

  return {
    queryRules,
    explainSpell,
    explainRule,
    suggestActions,
    clarifyInteraction,
    generateRuling,
  };
}

/**
 * AI Character Generation Service - Genesis System
 * Handles intelligent character creation from natural language prompts
 */

import { PrismaClient } from "@prisma/client";
import { OpenAIProvider, AnthropicProvider } from "@vtt/ai";
import { CharacterService } from "../character/CharacterService";

export interface CharacterConcept {
  prompt: string;
  preferences?: {
    system?: string; // D&D 5e, Pathfinder, etc.
    powerLevel?: 'low' | 'standard' | 'high' | 'epic';
    complexity?: 'simple' | 'moderate' | 'complex';
    playstyle?: 'combat' | 'roleplay' | 'exploration' | 'balanced';
  };
}

export interface GenerationStep {
  step: 'concept' | 'race' | 'class' | 'background' | 'abilities' | 'equipment' | 'spells' | 'personality' | 'optimization';
  status: 'pending' | 'processing' | 'completed' | 'error';
  result?: any;
  reasoning?: string;
  alternatives?: any[];
  confidence?: number;
}

export interface CharacterGeneration {
  id: string;
  concept: CharacterConcept;
  steps: GenerationStep[];
  currentStep: string;
  character?: any;
  isComplete: boolean;
  error?: string;
  metadata: {
    provider: string;
    totalCostUSD: number;
    totalLatencyMs: number;
    generatedAt: Date;
  };
}

export class GenesisService {
  private prisma: PrismaClient;
  private characterService: CharacterService;
  private openaiProvider?: OpenAIProvider;
  private anthropicProvider?: AnthropicProvider;
  private activeGenerations = new Map<string, CharacterGeneration>();

  constructor(prisma: PrismaClient) {
    this.prisma = prisma;
    this.characterService = new CharacterService();

    // Initialize AI providers
    const openaiKey = process.env.OPENAI_API_KEY;
    if (openaiKey?.trim()) {
      this.openaiProvider = new OpenAIProvider({ apiKey: openaiKey });
    }

    const anthropicKey = process.env.ANTHROPIC_API_KEY;
    if (anthropicKey?.trim()) {
      this.anthropicProvider = new AnthropicProvider({ apiKey: anthropicKey });
    }
  }

  /**
   * Start character generation from concept
   */
  async startGeneration(concept: CharacterConcept, userId: string): Promise<CharacterGeneration> {
    const generationId = this.generateId();

    const generation: CharacterGeneration = {
      id: generationId,
      concept,
      steps: [
        { step: 'concept', status: 'processing' },
        { step: 'race', status: 'pending' },
        { step: 'class', status: 'pending' },
        { step: 'background', status: 'pending' },
        { step: 'abilities', status: 'pending' },
        { step: 'equipment', status: 'pending' },
        { step: 'spells', status: 'pending' },
        { step: 'personality', status: 'pending' },
        { step: 'optimization', status: 'pending' }
      ],
      currentStep: 'concept',
      isComplete: false,
      metadata: {
        provider: 'genesis',
        totalCostUSD: 0,
        totalLatencyMs: 0,
        generatedAt: new Date()
      }
    };

    this.activeGenerations.set(generationId, generation);

    // Start async generation process
    this.processGeneration(generation, userId).catch(error => {
      generation.error = error.message;
      generation.steps.forEach(step => {
        if (step.status === 'processing') step.status = 'error';
      });
    });

    return generation;
  }

  /**
   * Get generation status
   */
  getGeneration(generationId: string): CharacterGeneration | null {
    return this.activeGenerations.get(generationId) || null;
  }

  /**
   * Process character generation pipeline
   */
  private async processGeneration(generation: CharacterGeneration, userId: string): Promise<void> {
    try {
      // Step 1: Analyze concept
      await this.analyzeConceptStep(generation);

      // Step 2: Determine race
      await this.determineRaceStep(generation);

      // Step 3: Determine class
      await this.determineClassStep(generation);

      // Step 4: Determine background
      await this.determineBackgroundStep(generation);

      // Step 5: Generate ability scores
      await this.generateAbilitiesStep(generation);

      // Step 6: Generate equipment
      await this.generateEquipmentStep(generation);

      // Step 7: Generate spells (if applicable)
      await this.generateSpellsStep(generation);

      // Step 8: Generate personality
      await this.generatePersonalityStep(generation);

      // Step 9: Optimize build
      await this.optimizeCharacterStep(generation);

      // Create final character
      const character = await this.createFinalCharacter(generation, userId);
      generation.character = character;
      generation.isComplete = true;

    } catch (error: any) {
      generation.error = error.message;
      throw error;
    }
  }

  private async analyzeConceptStep(generation: CharacterGeneration): Promise<void> {
    const step = this.getStep(generation, 'concept');
    step.status = 'processing';

    const prompt = `Analyze this D&D 5e character concept and extract key elements:

Concept: "${generation.concept.prompt}"

Preferences: ${JSON.stringify(generation.concept.preferences || {}, null, 2)}

Please analyze and extract:
1. Core character archetype/role
2. Suggested race options (3-5 best fits)
3. Suggested class options (3-5 best fits)  
4. Suggested background options (3-5 best fits)
5. Key personality traits implied
6. Combat vs roleplay focus
7. Power level assessment
8. Complexity requirements

Format as JSON with clear reasoning for each suggestion.`;

    const result = await this.callAI(prompt, generation);

    step.result = result;
    step.status = 'completed';
    step.confidence = 0.9;

    this.updateCurrentStep(generation, 'race');
  }

  private async determineRaceStep(generation: CharacterGeneration): Promise<void> {
    const step = this.getStep(generation, 'race');
    step.status = 'processing';

    const conceptAnalysis = this.getStep(generation, 'concept').result;

    const prompt = `Based on this character concept analysis, choose the BEST race for this character:

Concept Analysis: ${JSON.stringify(conceptAnalysis, null, 2)}

Available D&D 5e races:
- Human (Variant/Standard)
- Elf (High/Wood/Dark/Eladrin)
- Dwarf (Mountain/Hill)
- Halfling (Lightfoot/Stout)
- Dragonborn
- Gnome (Forest/Rock)
- Half-Elf
- Half-Orc
- Tiefling
- Aarakocra
- Genasi
- Goliath
- Tabaxi
- Triton
- Aasimar
- Firbolg
- Kenku
- Lizardfolk
- Tortle
- Bugbear
- Goblin
- Hobgoblin
- Kobold
- Orc
- Yuan-Ti Pureblood

Choose ONE race and provide:
1. Selected race and subrace
2. Reasoning for choice
3. How racial traits support the concept
4. Alternative races considered
5. Ability score improvements gained

Format as JSON.`;

    const result = await this.callAI(prompt, generation);

    step.result = result;
    step.status = 'completed';
    step.confidence = 0.85;

    this.updateCurrentStep(generation, 'class');
  }

  private async determineClassStep(generation: CharacterGeneration): Promise<void> {
    const step = this.getStep(generation, 'class');
    step.status = 'processing';

    const conceptAnalysis = this.getStep(generation, 'concept').result;
    const raceChoice = this.getStep(generation, 'race').result;

    const prompt = `Choose the BEST class for this character:

Concept: ${JSON.stringify(conceptAnalysis, null, 2)}
Selected Race: ${JSON.stringify(raceChoice, null, 2)}

Available D&D 5e classes:
- Artificer
- Barbarian  
- Bard
- Cleric
- Druid
- Fighter
- Monk
- Paladin
- Ranger
- Rogue
- Sorcerer
- Warlock
- Wizard

Consider:
1. How class synergizes with chosen race
2. Concept fulfillment
3. Mechanical optimization
4. Roleplay opportunities

Choose ONE class and provide:
1. Selected class
2. Reasoning for choice
3. Suggested subclass at level 3
4. Key abilities and features
5. How it fulfills the character concept
6. Starting level recommendation (1-3)

Format as JSON.`;

    const result = await this.callAI(prompt, generation);

    step.result = result;
    step.status = 'completed';
    step.confidence = 0.8;

    this.updateCurrentStep(generation, 'background');
  }

  private async determineBackgroundStep(generation: CharacterGeneration): Promise<void> {
    const step = this.getStep(generation, 'background');
    step.status = 'processing';

    const conceptAnalysis = this.getStep(generation, 'concept').result;
    const raceChoice = this.getStep(generation, 'race').result;
    const classChoice = this.getStep(generation, 'class').result;

    const prompt = `Choose the BEST background for this character:

Concept: ${JSON.stringify(conceptAnalysis, null, 2)}
Race: ${JSON.stringify(raceChoice, null, 2)}
Class: ${JSON.stringify(classChoice, null, 2)}

Available D&D 5e backgrounds:
- Acolyte, Criminal, Folk Hero, Noble, Sage, Soldier
- Charlatan, Entertainer, Guild Artisan, Hermit, Outlander, Sailor
- Anthropologist, Archaeologist, City Watch, Clan Crafter, Cloistered Scholar
- Courtier, Faction Agent, Far Traveler, Inheritor, Knight of the Order
- Mercenary Veteran, Urban Bounty Hunter, Uthgardt Tribe Member, Waterdhavian Noble

Consider:
1. Story integration with concept
2. Skill proficiencies that complement class
3. Background features and benefits
4. Roleplay opportunities
5. Equipment and starting wealth

Choose ONE background and provide:
1. Selected background
2. Reasoning for choice  
3. Skill proficiencies gained
4. Background feature description
5. How it enhances the character story
6. Starting equipment from background

Format as JSON.`;

    const result = await this.callAI(prompt, generation);

    step.result = result;
    step.status = 'completed';
    step.confidence = 0.75;

    this.updateCurrentStep(generation, 'abilities');
  }

  private async generateAbilitiesStep(generation: CharacterGeneration): Promise<void> {
    const step = this.getStep(generation, 'abilities');
    step.status = 'processing';

    const raceChoice = this.getStep(generation, 'race').result;
    const classChoice = this.getStep(generation, 'class').result;

    const prompt = `Generate optimal ability scores for this character using point buy (27 points):

Race: ${JSON.stringify(raceChoice, null, 2)}
Class: ${JSON.stringify(classChoice, null, 2)}

Point Buy Rules:
- Start with 8 in each ability
- Costs: 9(1), 10(2), 11(3), 12(4), 13(5), 14(7), 15(9)
- 27 points total to spend
- Apply racial bonuses AFTER point buy

Prioritize:
1. Class primary abilities (highest priority)
2. Class secondary abilities  
3. Constitution for survivability
4. Abilities that support concept

Provide:
1. Base scores before racial bonuses (point buy)
2. Final scores after racial bonuses
3. Ability modifiers
4. Points spent breakdown
5. Reasoning for each score
6. How scores support class features

Format as JSON with clear STR/DEX/CON/INT/WIS/CHA breakdown.`;

    const result = await this.callAI(prompt, generation);

    step.result = result;
    step.status = 'completed';
    step.confidence = 0.9;

    this.updateCurrentStep(generation, 'equipment');
  }

  private async generateEquipmentStep(generation: CharacterGeneration): Promise<void> {
    const step = this.getStep(generation, 'equipment');
    step.status = 'processing';

    const classChoice = this.getStep(generation, 'class').result;
    const backgroundChoice = this.getStep(generation, 'background').result;
    const abilities = this.getStep(generation, 'abilities').result;

    const prompt = `Generate starting equipment for this character:

Class: ${JSON.stringify(classChoice, null, 2)}
Background: ${JSON.stringify(backgroundChoice, null, 2)}
Abilities: ${JSON.stringify(abilities, null, 2)}

Consider:
1. Class starting equipment options
2. Background equipment
3. Ability score optimizations (e.g., finesse weapons for high DEX)
4. Character concept alignment
5. Practical adventuring gear

Provide:
1. Weapons (with attack bonuses and damage)
2. Armor (with AC calculation)
3. Tools and equipment from background
4. Adventuring gear essentials
5. Starting currency
6. Equipment alternatives based on concept

Format as JSON with detailed equipment list and reasoning.`;

    const result = await this.callAI(prompt, generation);

    step.result = result;
    step.status = 'completed';
    step.confidence = 0.8;

    this.updateCurrentStep(generation, 'spells');
  }

  private async generateSpellsStep(generation: CharacterGeneration): Promise<void> {
    const step = this.getStep(generation, 'spells');
    step.status = 'processing';

    const classChoice = this.getStep(generation, 'class').result;
    const abilities = this.getStep(generation, 'abilities').result;

    // Check if character is a spellcaster
    const spellcastingClasses = ['wizard', 'sorcerer', 'warlock', 'cleric', 'druid', 'bard', 'paladin', 'ranger', 'artificer'];
    const isSpellcaster = spellcastingClasses.some(cls => 
      classChoice.selectedClass?.toLowerCase().includes(cls)
    );

    if (!isSpellcaster) {
      step.result = { spells: [], cantrips: [], message: 'Non-spellcasting class' };
      step.status = 'completed';
      step.confidence = 1.0;
      this.updateCurrentStep(generation, 'personality');
      return;
    }

    const prompt = `Generate optimal starting spells for this spellcaster:

Class: ${JSON.stringify(classChoice, null, 2)}
Abilities: ${JSON.stringify(abilities, null, 2)}

Consider:
1. Spellcasting ability modifier
2. Spells known vs prepared mechanics
3. Cantrip selections
4. Level 1 spell choices  
5. Concept alignment
6. Utility vs combat balance
7. Ritual spells if applicable

Provide:
1. Cantrips known (list with reasoning)
2. 1st level spells (known/prepared with reasoning)
3. Spell attack bonus calculation
4. Spell save DC calculation
5. Spell slots available
6. Spellcasting focus/component pouch

Format as JSON with complete spell list and mechanics.`;

    const result = await this.callAI(prompt, generation);

    step.result = result;
    step.status = 'completed';
    step.confidence = 0.85;

    this.updateCurrentStep(generation, 'personality');
  }

  private async generatePersonalityStep(generation: CharacterGeneration): Promise<void> {
    const step = this.getStep(generation, 'personality');
    step.status = 'processing';

    const conceptAnalysis = this.getStep(generation, 'concept').result;
    const backgroundChoice = this.getStep(generation, 'background').result;

    const prompt = `Generate rich personality details for this character:

Original Concept: "${generation.concept.prompt}"
Concept Analysis: ${JSON.stringify(conceptAnalysis, null, 2)}
Background: ${JSON.stringify(backgroundChoice, null, 2)}

Create:
1. Personality Traits (2 distinct traits)
2. Ideals (1 core driving principle)
3. Bonds (1 important connection)
4. Flaws (1 meaningful weakness)
5. Physical description
6. Mannerisms and speech patterns
7. Backstory hooks for the DM
8. Character motivations
9. Fears and aspirations
10. Relationships and connections

Make it:
- Consistent with the original concept
- Mechanically integrated with background
- Rich and playable
- Memorable and distinct

Format as JSON with detailed explanations.`;

    const result = await this.callAI(prompt, generation);

    step.result = result;
    step.status = 'completed';
    step.confidence = 0.7;

    this.updateCurrentStep(generation, 'optimization');
  }

  private async optimizeCharacterStep(generation: CharacterGeneration): Promise<void> {
    const step = this.getStep(generation, 'optimization');
    step.status = 'processing';

    // Gather all previous results
    const allResults = {
      concept: this.getStep(generation, 'concept').result,
      race: this.getStep(generation, 'race').result,
      class: this.getStep(generation, 'class').result,
      background: this.getStep(generation, 'background').result,
      abilities: this.getStep(generation, 'abilities').result,
      equipment: this.getStep(generation, 'equipment').result,
      spells: this.getStep(generation, 'spells').result,
      personality: this.getStep(generation, 'personality').result
    };

    const prompt = `Optimize and validate this complete character build:

Character Build: ${JSON.stringify(allResults, null, 2)}

Perform:
1. Rules validation (check for errors)
2. Mechanical optimization suggestions
3. Synergy analysis between race/class/background
4. Missing elements identification
5. Alternative choices recommendations
6. Power level assessment
7. Roleplay integration check
8. Future advancement suggestions (levels 2-5)

Provide:
1. Validation results (any rule violations)
2. Optimization score (1-10)
3. Specific improvements
4. Alternative builds
5. Advancement path recommendations
6. Character sheet summary
7. Final assessment

Format as JSON with detailed analysis.`;

    const result = await this.callAI(prompt, generation);

    step.result = result;
    step.status = 'completed';
    step.confidence = 0.95;

    // Mark generation as ready for final character creation
    generation.currentStep = 'completed';
  }

  private async createFinalCharacter(generation: CharacterGeneration, userId: string): Promise<any> {
    // Extract all the generated data
    const raceResult = this.getStep(generation, 'race').result;
    const classResult = this.getStep(generation, 'class').result;
    const backgroundResult = this.getStep(generation, 'background').result;
    const abilitiesResult = this.getStep(generation, 'abilities').result;
    const _equipmentResult = this.getStep(generation, 'equipment').result;
    const _spellsResult = this.getStep(generation, 'spells').result;
    const personalityResult = this.getStep(generation, 'personality').result;

    // Create character using existing CharacterService
    const characterRequest = {
      name: personalityResult.name || this.generateCharacterName(),
      race: raceResult.selectedRace,
      class: classResult.selectedClass,
      background: backgroundResult.selectedBackground,
      level: classResult.startingLevel || 1,
      abilities: abilitiesResult.finalScores,
      // Add other fields as needed
    };

    const character = await this.characterService.createCharacter(userId, characterRequest);

    // Log generation job
    await this.logGenerationJob(generation, character);

    return character;
  }

  private async callAI(prompt: string, generation: CharacterGeneration): Promise<any> {
    const startTime = Date.now();

    try {
      // Try Anthropic first, fallback to OpenAI
      let result;
      let provider;

      if (this.anthropicProvider) {
        provider = 'anthropic';
        const response = await this.anthropicProvider.generateGameContent('character', {
          additionalContext: prompt
        });
        result = response.content;
        generation.metadata.totalCostUSD += response.costUSD;
      } else if (this.openaiProvider) {
        provider = 'openai';
        const response = await this.openaiProvider.generateText(prompt, {
          model: 'gpt-4',
          temperature: 0.7,
          maxTokens: 2000
        });
        result = JSON.parse(response.text);
        generation.metadata.totalCostUSD += response.costUSD;
      } else {
        throw new Error('No AI provider available');
      }

      const latency = Date.now() - startTime;
      generation.metadata.totalLatencyMs += latency;
      generation.metadata.provider = provider;

      return result;
    } catch (error: any) {
      throw new Error(`AI generation failed: ${error.message}`);
    }
  }

  private async logGenerationJob(generation: CharacterGeneration, character: any): Promise<void> {
    await this.prisma.generationJob.create({
      data: {
        type: 'CHARACTER_GENERATION',
        status: 'SUCCEEDED',
        input: generation.concept as any,
        output: character as any,
      },
    });
  }

  private getStep(generation: CharacterGeneration, stepName: string): GenerationStep {
    const step = generation.steps.find(s => s.step === stepName);
    if (!step) throw new Error(`Step not found: ${stepName}`);
    return step;
  }

  private updateCurrentStep(generation: CharacterGeneration, nextStep: string): void {
    generation.currentStep = nextStep;
  }

  private generateId(): string {
    return `gen_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  private generateCharacterName(): string {
    const names = [
      'Aelindra', 'Thorin', 'Zara', 'Gareth', 'Luna', 'Kael', 'Rina', 'Daven',
      'Lyra', 'Magnus', 'Vera', 'Finn', 'Nyx', 'Orion', 'Sage', 'Raven'
    ];
    return names[Math.floor(Math.random() * names.length)];
  }

  // Public methods for external access
  async retryStep(generationId: string, stepName: string): Promise<void> {
    const generation = this.activeGenerations.get(generationId);
    if (!generation) throw new Error('Generation not found');

    const step = this.getStep(generation, stepName);
    step.status = 'processing';
    step.error = undefined;

    // Re-run the specific step
    switch (stepName) {
      case 'concept':
        await this.analyzeConceptStep(generation);
        break;
      case 'race':
        await this.determineRaceStep(generation);
        break;
      case 'class':
        await this.determineClassStep(generation);
        break;
      // Add other cases as needed
    }
  }

  async getGenerationHistory(_userId: string): Promise<CharacterGeneration[]> {
    // In a real implementation, this would query the database
    // For now, return active generations
    return Array.from(this.activeGenerations.values());
  }
}

/**
 * OAuth Configuration and Strategies
 */

import passport from 'passport';
import { Strategy as DiscordStrategy } from 'passport-discord';
import { Strategy as GoogleStrategy } from 'passport-google-oauth20';
import { AuthManager } from '@vtt/auth';

export interface OAuthProfile {
  id: string;
  provider: 'discord' | 'google';
  email: string;
  username: string;
  displayName: string;
  avatar?: string | undefined;
  accessToken: string;
  refreshToken?: string;
}

export interface OAuthConfig {
  discord: {
    clientId: string;
    clientSecret: string;
    callbackURL: string;
    scope: string[];
  };
  google: {
    clientId: string;
    clientSecret: string;
    callbackURL: string;
    scope: string[];
  };
}

export class OAuthManager {
  private authManager: AuthManager;
  private config: OAuthConfig;

  constructor(authManager: AuthManager, config: OAuthConfig) {
    this.authManager = authManager;
    this.config = config;
    this.initializeStrategies();
  }

  private initializeStrategies(): void {
    // Discord OAuth Strategy - only initialize if credentials are provided
    if (this.config.discord.clientId && this.config.discord.clientSecret) {
      passport.use('discord', _new DiscordStrategy({
        clientID: this.config.discord.clientId,
        _clientSecret: this.config.discord.clientSecret,
        _callbackURL: this.config.discord.callbackURL,
        _scope: this.config.discord.scope
      }, async (accessToken: string, _refreshToken: string, profile: any, _done: any) => {
      try {
        const oauthProfile: OAuthProfile = {
          id: profile.id,
          provider: 'discord',
          email: profile.email,
          username: profile.username,
          displayName: profile.global_name || profile.username,
          avatar: profile.avatar ? `https://cdn.discordapp.com/avatars/${profile.id}/${profile.avatar}.png` : undefined,
          accessToken,
          refreshToken
        };

        const user = await this.handleOAuthLogin(oauthProfile);
        return done(null, user);
      } catch (error) {
        return done(error, null);
      }
    }));
    }

    // Google OAuth Strategy - only initialize if credentials are provided
    if (this.config.google.clientId && this.config.google.clientSecret) {
      passport.use('google', _new GoogleStrategy({
      clientID: this.config.google.clientId,
      _clientSecret: this.config.google.clientSecret,
      _callbackURL: this.config.google.callbackURL,
      _scope: this.config.google.scope
    }, async (accessToken: string, _refreshToken: string, profile: any, _done: any) => {
      try {
        const oauthProfile: OAuthProfile = {
          id: profile.id,
          provider: 'google',
          email: profile.emails?.[0]?.value || '',
          username: profile.displayName.replace(/\s+/g, '').toLowerCase(),
          displayName: profile.displayName,
          avatar: profile.photos?.[0]?.value,
          accessToken,
          refreshToken
        };

        const user = await this.handleOAuthLogin(oauthProfile);
        return done(null, user);
      } catch (error) {
        return done(error, null);
      }
    }));
    }

    // Serialize/deserialize user for session
    passport.serializeUser((_user: any, _done) => {
      done(null, user.id);
    });

    passport.deserializeUser(async (id: string, _done: any) => {
      try {
        const user = await this.authManager.findUserById(id);
        done(null, user);
      } catch (error) {
        done(error, null);
      }
    });
  }

  private async handleOAuthLogin(oauthProfile: OAuthProfile): Promise<any> {
    // Check if user exists by OAuth provider ID
    let user = await this.findUserByOAuthId(oauthProfile.provider, oauthProfile.id);

    if (user) {
      // Update user info if needed
      await this.updateOAuthUser(user, oauthProfile);
      return user;
    }

    // Check if user exists by email
    user = await this.authManager.findUserByEmail(oauthProfile.email);

    if (user) {
      // Link OAuth account to existing user
      await this.linkOAuthAccount(user, oauthProfile);
      return user;
    }

    // Create new user
    return await this.createOAuthUser(oauthProfile);
  }

  private async findUserByOAuthId(provider: string, providerId: string): Promise<any> {
    try {
      // Note: This assumes you have Prisma client available - you may need to adjust based on your DB setup
      const oauthAccount = await this.authManager.findOAuthAccount(provider, providerId);
      if (oauthAccount) {
        return await this.authManager.findUserById(oauthAccount.userId);
      }
      return null;
    } catch (error) {
      console.error(`Failed to find user by OAuth ID ${provider}:${providerId}:`, error);
      return null;
    }
  }

  private async updateOAuthUser(user: any, profile: OAuthProfile): Promise<void> {
    // TODO: Update user's OAuth token and profile info in database
    console.log(`Updating OAuth user ${user.id} from ${profile.provider}`);
  }

  private async linkOAuthAccount(user: any, profile: OAuthProfile): Promise<void> {
    // TODO: Create OAuth account link in database
    console.log(`Linking ${profile.provider} account to user ${user.id}`);
  }

  private async createOAuthUser(profile: OAuthProfile): Promise<any> {
    // TODO: Create new user from OAuth profile
    const newUser = {
      id: profile.id,
      email: profile.email,
      username: profile.username,
      displayName: profile.displayName,
      avatar: profile.avatar,
      provider: profile.provider,
      createdAt: new Date(),
      emailVerified: true // OAuth providers handle email verification
    };

    console.log(`Creating new user from ${profile.provider}:`, newUser);
    return newUser;
  }
}

export function createOAuthConfig(): OAuthConfig {
  return {
    discord: {
      clientId: process.env.DISCORD_CLIENT_ID || '',
      clientSecret: process.env.DISCORD_CLIENT_SECRET || '',
      callbackURL: process.env.DISCORD_CALLBACK_URL || '/auth/discord/callback',
      scope: ['identify', 'email']
    },
    google: {
      clientId: process.env.GOOGLE_CLIENT_ID || '',
      clientSecret: process.env.GOOGLE_CLIENT_SECRET || '',
      callbackURL: process.env.GOOGLE_CALLBACK_URL || '/auth/google/callback',
      scope: ['profile', 'email']
    }
  };
}

/**
 * Campaign management service with database persistence and map integration
 */

import { Campaign } from '../character/types';
import { PrismaClient } from '@prisma/client';
import { MapService } from '../map/MapService';
import { v4 as _uuidv4 } from 'uuid';

export interface CreateCampaignRequest {
  name: string;
  description: string;
  gameSystem?: string;
  isActive?: boolean;
}

export interface UpdateCampaignRequest {
  name?: string;
  description?: string;
  gameSystem?: string;
  isActive?: boolean;
  players?: string[];
  characters?: string[];
}

export interface Scene {
  id: string;
  name: string;
  mapId: string | null;
  isActive: boolean;
}

export interface CampaignWithScenes extends Campaign {
  scenes: Scene[];
  activeSceneId?: string | undefined;
}

export interface GameSession {
  sessionId: string;
  campaignId: string;
  sceneId: string;
  gameMasterId: string;
  connectedUsers: string[];
  status: 'active' | 'paused' | 'ended';
  startedAt: Date;
  endedAt?: Date;
}

export class CampaignService {
  private campaigns = new Map<string, Campaign>();

  // Active scene tracking (TODO: move to database)
  private activeScenes: Map<string, string> = new Map();

  // Session management
  private activeSessions: Map<string, GameSession> = new Map();

  constructor(
    private prisma: PrismaClient,
    private mapService?: MapService
  ) {}

  /**
   * Create a new campaign with database persistence
   */
  async createCampaign(gameMasterId: string, request: CreateCampaignRequest): Promise<Campaign> {
    // Create campaign in database
    const dbCampaign = await this.prisma.campaign.create({
      data: {
        name: request.name,
        description: request.description || '',
        gameSystem: request.gameSystem || 'dnd5e',
        isActive: request.isActive !== false,
        members: {
          create: {
            userId: gameMasterId,
            role: 'GM'
          }
        }
      },
      include: {
        members: {
          include: {
            user: true
          }
        },
        scenes: true,
        activeScene: true
      }
    });

    const campaign: Campaign = {
      id: dbCampaign.id,
      name: dbCampaign.name,
      description: dbCampaign.description,
      gameSystem: dbCampaign.gameSystem,
      gameMasterId,
      players: [gameMasterId],
      characters: [],
      isActive: dbCampaign.isActive,
      createdAt: dbCampaign.createdAt,
      updatedAt: dbCampaign.updatedAt
    };

    this.campaigns.set(dbCampaign.id, campaign);
    return campaign;
  }

  /**
   * Get campaign by ID with database lookup
   */
  async getCampaign(campaignId: string): Promise<Campaign | null> {
    // Check cache first
    const cached = this.campaigns.get(campaignId);
    if (cached) return cached;

    // Load from database
    const dbCampaign = await this.prisma.campaign.findUnique({
      where: { id: campaignId },
      include: {
        members: {
          include: {
            user: true
          }
        },
        scenes: true
      }
    });

    if (!dbCampaign) return null;

    const gameMaster = dbCampaign.members.find(m => m.role === 'GM');
    const players = dbCampaign.members.map(m => m.userId);

    const campaign: Campaign = {
      id: dbCampaign.id,
      name: dbCampaign.name,
      description: (dbCampaign as any).description || '',
      gameSystem: (dbCampaign as any).gameSystem || 'dnd5e',
      gameMasterId: gameMaster?.userId || '',
      players,
      characters: [],
      isActive: (dbCampaign as any).isActive !== false,
      createdAt: dbCampaign.createdAt,
      updatedAt: (dbCampaign as any).updatedAt || new Date()
    };

    this.campaigns.set(campaignId, campaign);
    return campaign;
  }

  /**
   * Get campaign with scenes and map data
   */
  async getCampaignWithScenes(campaignId: string): Promise<CampaignWithScenes | null> {
    const campaign = await this.getCampaign(campaignId);
    if (!campaign) return null;

    const scenes = await this.prisma.scene.findMany({
      where: { campaignId },
      include: {
        map: true
      }
    });

    return {
      ...campaign,
      scenes: scenes.map(scene => ({
        id: scene.id,
        name: scene.name,
        mapId: scene.mapId,
        isActive: false // TODO: Track active scene
      })),
      activeSceneId: undefined // TODO: Implement active scene tracking
    };
  }

  /**
   * Create a scene for a campaign
   */
  async createSceneForCampaign(
    campaignId: string, 
    userId: string, 
    sceneName: string,
    mapId?: string
  ): Promise<any> {
    const campaign = await this.getCampaign(campaignId);
    if (!campaign || campaign.gameMasterId !== userId) {
      throw new Error('Unauthorized or campaign not found');
    }

    if (this.mapService) {
      return await this.mapService.createScene(
        sceneName,
        1920, // Default width
        1080, // Default height
        campaignId,
        mapId
      );
    }

    throw new Error('MapService not available');
  }

  /**
   * Set active scene for campaign
   */
  async setActiveScene(campaignId: string, sceneId: string, userId: string): Promise<boolean> {
    const campaign = await this.getCampaign(campaignId);
    if (!campaign || campaign.gameMasterId !== userId) {
      return false;
    }

    // Verify scene belongs to campaign
    const scene = await this.prisma.scene.findFirst({
      where: { id: sceneId, campaignId }
    });

    if (!scene) return false;

    // Update active scene in database
    await this.prisma.campaign.update({
      where: { id: campaignId },
      data: { activeSceneId: sceneId } as any
    });

    // Update in-memory cache
    this.activeScenes.set(campaignId, sceneId);

    return true;
  }

  /**
   * Get campaigns where user is GM or player
   */
  async getCampaignsForUser(userId: string): Promise<Campaign[]> {
    return Array.from(this.campaigns.values()).filter(campaign => 
      campaign.gameMasterId === userId || campaign.players.includes(userId)
    );
  }

  /**
   * Get campaigns where user is the game master
   */
  async getCampaignsAsMaster(userId: string): Promise<Campaign[]> {
    return Array.from(this.campaigns.values()).filter(campaign => 
      campaign.gameMasterId === userId
    );
  }

  /**
   * Update campaign
   */
  async updateCampaign(campaignId: string, userId: string, update: UpdateCampaignRequest): Promise<Campaign | null> {
    const campaign = this.campaigns.get(campaignId);

    if (!campaign || campaign.gameMasterId !== userId) {
      return null; // Only GM can update campaign
    }

    // Apply updates
    if (update.name) campaign.name = update.name;
    if (update.description !== undefined) campaign.description = update.description;
    if (update.gameSystem) campaign.gameSystem = update.gameSystem;
    if (update.isActive !== undefined) campaign.isActive = update.isActive;
    if (update.players) campaign.players = update.players;
    if (update.characters) campaign.characters = update.characters;

    campaign.updatedAt = new Date();
    return campaign;
  }

  /**
   * Delete campaign
   */
  async deleteCampaign(campaignId: string, userId: string): Promise<boolean> {
    const campaign = this.campaigns.get(campaignId);

    if (!campaign || campaign.gameMasterId !== userId) {
      return false; // Only GM can delete campaign
    }

    return this.campaigns.delete(campaignId);
  }

  /**
   * Add player to campaign
   */
  async addPlayer(campaignId: string, userId: string, playerId: string): Promise<boolean> {
    const campaign = this.campaigns.get(campaignId);

    if (!campaign || campaign.gameMasterId !== userId) {
      return false; // Only GM can add players
    }

    if (!campaign.players.includes(playerId)) {
      campaign.players.push(playerId);
      campaign.updatedAt = new Date();
      return true;
    }

    return false; // Player already in campaign
  }

  /**
   * Remove player from campaign
   */
  async removePlayer(campaignId: string, userId: string, playerId: string): Promise<boolean> {
    const campaign = this.campaigns.get(campaignId);

    if (!campaign || campaign.gameMasterId !== userId) {
      return false; // Only GM can remove players
    }

    const playerIndex = campaign.players.indexOf(playerId);
    if (playerIndex > -1) {
      campaign.players.splice(playerIndex, 1);
      campaign.updatedAt = new Date();
      return true;
    }

    return false; // Player not in campaign
  }

  /**
   * Add character to campaign
   */
  async addCharacter(campaignId: string, userId: string, characterId: string): Promise<boolean> {
    const campaign = this.campaigns.get(campaignId);

    if (!campaign) {
      return false;
    }

    // Players can add their own characters, GM can add any character
    const isGM = campaign.gameMasterId === userId;
    const isPlayer = campaign.players.includes(userId);

    if (!isGM && !isPlayer) {
      return false;
    }

    if (!campaign.characters.includes(characterId)) {
      campaign.characters.push(characterId);
      campaign.updatedAt = new Date();
      return true;
    }

    return false; // Character already in campaign
  }

  /**
   * Remove character from campaign
   */
  async removeCharacter(campaignId: string, userId: string, characterId: string): Promise<boolean> {
    const campaign = this.campaigns.get(campaignId);

    if (!campaign) {
      return false;
    }

    // Players can remove their own characters, GM can remove any character
    const isGM = campaign.gameMasterId === userId;

    if (!isGM) {
      // TODO: Check if character belongs to user
      // For now, allow any player to remove characters
      const isPlayer = campaign.players.includes(userId);
      if (!isPlayer) {
        return false;
      }
    }

    const characterIndex = campaign.characters.indexOf(characterId);
    if (characterIndex > -1) {
      campaign.characters.splice(characterIndex, 1);
      campaign.updatedAt = new Date();
      return true;
    }

    return false; // Character not in campaign
  }

  /**
   * Get active campaigns
   */
  async getActiveCampaigns(): Promise<Campaign[]> {
    return Array.from(this.campaigns.values()).filter(campaign => campaign.isActive);
  }

  /**
   * Search campaigns by name or description
   */
  async searchCampaigns(query: string): Promise<Campaign[]> {
    const lowercaseQuery = query.toLowerCase();
    return Array.from(this.campaigns.values()).filter(campaign => 
      campaign.name.toLowerCase().includes(lowercaseQuery) ||
      campaign.description.toLowerCase().includes(lowercaseQuery)
    );
  }

  /**
   * Get campaign statistics
   */
  async getCampaignStats(campaignId: string): Promise<any> {
    const campaign = this.campaigns.get(campaignId);

    if (!campaign) {
      return null;
    }

    return {
      id: campaign.id,
      name: campaign.name,
      playerCount: campaign.players.length,
      characterCount: campaign.characters.length,
      isActive: campaign.isActive,
      gameSystem: campaign.gameSystem,
      createdAt: campaign.createdAt,
      updatedAt: campaign.updatedAt
    };
  }

  /**
   * Get all campaigns (admin function)
   */
  async getAllCampaigns(): Promise<Campaign[]> {
    return Array.from(this.campaigns.values());
  }

  /**
   * Archive campaign (set inactive)
   */
  async archiveCampaign(campaignId: string, userId: string): Promise<boolean> {
    const campaign = this.campaigns.get(campaignId);

    if (!campaign || campaign.gameMasterId !== userId) {
      return false;
    }

    campaign.isActive = false;
    campaign.updatedAt = new Date();
    return true;
  }

  /**
   * Reactivate campaign
   */
  async reactivateCampaign(campaignId: string, userId: string): Promise<boolean> {
    const campaign = this.campaigns.get(campaignId);

    if (!campaign || campaign.gameMasterId !== userId) {
      return false;
    }

    campaign.isActive = true;
    campaign.updatedAt = new Date();
    return true;
  }

  /**
   * Start a game session for a campaign
   */
  async startSession(campaignId: string, sceneId: string, gameMasterId: string): Promise<GameSession> {
    // Verify user is GM
    const campaign = await this.getCampaign(campaignId);
    if (!campaign || campaign.gameMasterId !== gameMasterId) {
      throw new Error('Unauthorized: Only campaign GM can start sessions');
    }

    // Verify scene exists and belongs to campaign
    const scene = await this.prisma.scene.findFirst({
      where: { id: sceneId, campaignId }
    });
    if (!scene) {
      throw new Error('Scene not found or does not belong to campaign');
    }

    // End any existing session for this campaign
    const existingSessionId = Array.from(this.activeSessions.entries())
      .find(_([_, _session]) => session.campaignId === campaignId)?.[0];
    if (existingSessionId) {
      await this.endSession(existingSessionId, gameMasterId);
    }

    // Create new session
    const sessionId = `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
    const session: GameSession = {
      sessionId,
      campaignId,
      sceneId,
      gameMasterId,
      connectedUsers: [gameMasterId],
      status: 'active',
      startedAt: new Date()
    };

    this.activeSessions.set(sessionId, session);
    this.activeScenes.set(campaignId, sceneId);

    return session;
  }

  /**
   * End a game session
   */
  async endSession(sessionId: string, userId: string): Promise<boolean> {
    const session = this.activeSessions.get(sessionId);
    if (!session) return false;

    // Only GM can end session
    if (session.gameMasterId !== userId) {
      throw new Error('Unauthorized: Only session GM can end sessions');
    }

    session.status = 'ended';
    session.endedAt = new Date();
    this.activeSessions.delete(sessionId);

    return true;
  }

  /**
   * Join a game session
   */
  async joinSession(sessionId: string, userId: string): Promise<boolean> {
    const session = this.activeSessions.get(sessionId);
    if (!session || session.status !== 'active') return false;

    // Verify user is member of campaign
    const campaign = await this.getCampaign(session.campaignId);
    if (!campaign) return false;

    const isMember = campaign.gameMasterId === userId || 
                    campaign.players.includes(userId);
    if (!isMember) return false;

    // Add user to session if not already connected
    if (!session.connectedUsers.includes(userId)) {
      session.connectedUsers.push(userId);
    }

    return true;
  }

  /**
   * Leave a game session
   */
  async leaveSession(sessionId: string, userId: string): Promise<boolean> {
    const session = this.activeSessions.get(sessionId);
    if (!session) return false;

    // Remove user from connected users
    session.connectedUsers = session.connectedUsers.filter(id => id !== userId);

    // If GM leaves, pause the session
    if (userId === session.gameMasterId) {
      session.status = 'paused';
    }

    return true;
  }

  /**
   * Get active session for campaign
   */
  getActiveSession(campaignId: string): GameSession | null {
    return Array.from(this.activeSessions.values())
      .find(session => session.campaignId === campaignId && session.status === 'active') || null;
  }

  /**
   * Get session by ID
   */
  getSession(sessionId: string): GameSession | null {
    return this.activeSessions.get(sessionId) || null;
  }

  /**
   * Get all active sessions
   */
  getActiveSessions(): GameSession[] {
    return Array.from(this.activeSessions.values())
      .filter(session => session.status === 'active');
  }
}

