      ctx.res.end(JSON.stringify({ error: "Logout failed" }));
      return;
    }

    ctx.res.setHeader("Set-Cookie", [
      "sessionToken=; HttpOnly; Expires=Thu, 01 Jan 1970 00:00:00 GMT",
