// HTTP server
const server = createServer(async (req, res) => {
  const url = new URL(req.url ?? "/", `http://${req.headers.host}`);
  // requestId will be set by requestIdMiddleware; initialize to empty for type safety
  const ctx = { req, res, prisma, url, requestId: "" } as any;

  const handled = await router.handle(ctx);
  if (!handled) {
    res.writeHead(404, { "Content-Type": "application/json" });
    res.end(JSON.stringify({ error: "Not found" }));
  }
});

// WebSocket setup
const wss = new WebSocketServer({ server });
const wsManager = new WebSocketManager(wss);

// Graceful shutdown
async function shutdown(_signal: string) {
  console.log(`[server] ${signal} received, shutting down gracefully`);
  try {
    await Promise.allSettled([DatabaseManager.disconnect(), wsManager.shutdown?.()]);
  } catch (e) {
    console.error("[server] Error during shutdown", e);
  }
  // Close HTTP server
  server.close(() => {
    process.exit(0);
  });
  // Fallback exit after timeout
  setTimeout(() => process.exit(0), 5000).unref();
}

process.on("SIGTERM", () => void shutdown("SIGTERM"));

process.on("SIGINT", () => void shutdown("SIGINT"));

// Start server
async function startServer() {
  try {
    await DatabaseManager.connect();
    server.listen(_PORT, () => {
      console.log(`[server] HTTP+WS listening on http://localhost:${PORT}`);
      console.log(
        `[server] Database: ${DatabaseManager.getConnectionStatus() ? "connected" : "disconnected"}`);
    });
  } catch (error) {
    console.error("[server] Failed to start:", error);
    process.exit(1);
  }
}

startServer();

