  /**
   * Create measurement tool
   */
  async createMeasurement(
    sceneId: string,
    type: 'distance' | 'area',
    points: Array<{ x: number; y: number }>,
    ownerId: string
  ): Promise<any> {
    const scene = await this.getScene(sceneId);
    if (!scene) {
      throw new Error('Scene not found');
    }

    let distance = 0;
    let area = 0;

    // Calculate measurements
    if (points.length >= 2) {
      for (let i = 1; i < points.length; i++) {
        const prevPoint = points[i-1];
        const currentPoint = points[i];
        if (prevPoint && currentPoint) {
          distance += this.calculateDistance(
            prevPoint.x, prevPoint.y,
            currentPoint.x, currentPoint.y,
            scene.grid,
            'feet'
          );
        }
      }
    }

    if (type === 'area' && points.length >= 3) {
      // Calculate polygon area using shoelace formula
      let sum = 0;
      for (let i = 0; i < points.length; i++) {
        const j = (i + 1) % points.length;
        const currentPoint = points[i];
        const nextPoint = points[j];
        if (currentPoint && nextPoint) {
          sum += currentPoint.x * nextPoint.y;
          sum -= nextPoint.x * currentPoint.y;
        }
      }
      area = Math.abs(sum) / 2;
      // Convert to grid squares
      const gridSize = scene.grid?.size || 50;
      area = area / (gridSize * gridSize);
    }

    const measurement = {
      id: uuidv4(),
      type,
      points,
      color: '#ff0000',
      visible: true,
      ownerId,
      measurements: {
        distance,
        ...(area > 0 && { area }),
        units: 'feet'
      }
    };

    return measurement;
  }

/**
 * Add a token to a scene with database persistence
 */
async addToken(
  sceneId: string, 
  tokenData: {
    name: string;
    x: number;
    y: number;
    width?: number;
    height?: number;
    disposition?: 'FRIENDLY' | 'NEUTRAL' | 'HOSTILE' | 'UNKNOWN';
    actorId?: string;
    assetId?: string;
  }
): Promise<string | null> {
  try {
    const scene = await this.getScene(sceneId);
    if (!scene) return null;

    // Snap token position to grid
    const alignedPosition = this.snapToGrid(tokenData.x, tokenData.y, scene.grid);
    const gridBounds = this.getGridBounds(scene.grid, scene.width, scene.height);

    const tokenId = uuidv4();
    const token = await this.prisma.token.create({
      data: {
        ...tokenData,
        id: tokenId,
        sceneId,
        x: alignedPosition.x,
        y: alignedPosition.y,
        width: tokenData.width || 1,
        height: tokenData.height || 1,
        rotation: 0,
        scale: 1,
        disposition: tokenData.disposition || 'UNKNOWN',
        isVisible: true,
        isLocked: false,
        layer: 0
      }
    });

    const bodySize = Math.min(gridBounds.cellWidth, gridBounds.cellHeight) * 1 * 0.8;
    try {
      const numericId = this.generateNumericId(tokenId);
      const rigidBody = new RigidBody(
        numericId,
        alignedPosition.x,
        alignedPosition.y,
        bodySize,
        bodySize,
        {
          mass: 1,
          friction: 0.8,
          restitution: 0.3,
          isStatic: false,
          isTrigger: false,
          layer: 1,
          mask: 0xFFFFFFFF
        }
      );
      this.physicsWorld.addBody(rigidBody);
    } catch (error) {
      console.warn('Failed to create physics body for token:', tokenId, error);
    }

    // Update spatial index for new token
    const tokenWidth = tokenData.width || 1;
    const tokenHeight = tokenData.height || 1;
    this.updateSpatialIndex(sceneId, tokenId, alignedPosition.x, alignedPosition.y, tokenWidth * 50, tokenHeight * 50);

    // Emit real-time update
    this.emitMapUpdate(sceneId, {
      type: 'token_add',
      token,
      timestamp: Date.now()
    });

    return token.id;
  } catch (error) {
    console.error('Failed to add token:', error);
    return null;
  }
}

