/**
 * WebSocket events for real-time combat updates
 */
export const _handleCombatWebSocket = (ws: any, message: any, _userId: string) => {
  switch (message.type) {
    case 'COMBAT_SUBSCRIBE': {
      const { simulationId  } = message.payload;

      // Subscribe to combat updates
      ws.combatSubscription = simulationId;

      // Send current status
      crucibleService.getSimulation(simulationId).then(simulation => {
        if (simulation) {
          ws.send(JSON.stringify({
            type: 'COMBAT_UPDATE',
            payload: {
              simulationId,
              currentRound: simulation.currentRound,
              participants: simulation.participants,
              winner: simulation.winner,
              isComplete: simulation.isComplete
            }
          }));
        }
      });
    }
      break;

    case 'COMBAT_UNSUBSCRIBE':
      ws.combatSubscription = null;
      break;

    case 'REQUEST_TACTICAL_DECISION': {
      // Real-time tactical decision request
      const { context  } = message.payload;

      crucibleService.makeTacticalDecision(context).then(decision => {
        ws.send(JSON.stringify({
          type: 'TACTICAL_DECISION',
          payload: {
            requestId: message.payload.requestId,
            decision
          }
        }));
      }).catch(error => {
        ws.send(JSON.stringify({
          type: 'TACTICAL_DECISION_ERROR',
          payload: {
            requestId: message.payload.requestId,
            error: error.message
          }
        }));
      });
    }
      break;
  }
};

