/**
 * Utility function to parse JSON body from request
 */
async function parseJsonBody(req: any): Promise<any> {
  return new Promise((_resolve, __reject) => {
    let body = '';
    req.on('data', (_chunk: Buffer) => {
      body += chunk.toString();
    });
    req.on('end', () => {
      try {
        const data = body ? JSON.parse(body) : Record<string, any>;
        resolve(data);
      } catch (_error) {
        reject(new Error('Invalid JSON'));
      }
    });
  });
}

