      if (data.isVisible !== undefined) updateData.isVisible = data.isVisible;
      if (data.isLocked !== undefined) updateData.isLocked = data.isLocked;
      if (data.layer !== undefined) updateData.layer = data.layer;

      const token = await ctx.prisma.token.update({
        where: { id: tokenId },
        data: updateData,
        include: {
          actor: true,
          asset: true
        }
      });

      ctx.res.writeHead(200, { 'Content-Type': 'application/json' });
      ctx.res.end(JSON.stringify(token));
    } catch (error) {
      console.error('Error updating token:', error);
      ctx.res.writeHead(500, { 'Content-Type': 'application/json' });
      ctx.res.end(JSON.stringify({ error: 'Internal server error' }));
    }
  });
}

export async function deleteTokenHandler(ctx: Context) {
  if (ctx.req.method !== 'DELETE') {
    ctx.res.writeHead(405, { 'Content-Type': 'application/json' });
    ctx.res.end(JSON.stringify({ error: 'Method not allowed' }));
    return;
  }

  const tokenId = ctx.url.pathname.split('/').pop();
  if (!tokenId) {
    ctx.res.writeHead(400, { 'Content-Type': 'application/json' });
    ctx.res.end(JSON.stringify({ error: 'Token ID required' }));
    return;
  }

  try {
    await ctx.prisma.token.delete({
      where: { id: tokenId }
    });

    ctx.res.writeHead(204);
    ctx.res.end();
  } catch (error) {
    console.error('Error deleting token:', error);
    ctx.res.writeHead(500, { 'Content-Type': 'application/json' });
    ctx.res.end(JSON.stringify({ error: 'Internal server error' }));
  }
}

export async function getCampaignScenesHandler(ctx: Context) {
  const campaignId = ctx.url.pathname.split('/')[3]; // /api/campaigns/:id/scenes
  if (!campaignId) {
    ctx.res.writeHead(400, { 'Content-Type': 'application/json' });
    ctx.res.end(JSON.stringify({ error: 'Campaign ID required' }));
    return;
  }

  try {
    const scenes = await ctx.prisma.scene.findMany({
      where: { campaignId },
      include: {
        _count: {
          select: { tokens: true }
        }
      },
      orderBy: { createdAt: 'asc' }
    });

    ctx.res.writeHead(200, { 'Content-Type': 'application/json' });
    ctx.res.end(JSON.stringify(scenes));
  } catch (error) {
    console.error('Error fetching campaign scenes:', error);
    ctx.res.writeHead(500, { 'Content-Type': 'application/json' });
    ctx.res.end(JSON.stringify({ error: 'Internal server error' }));
  }
}

/**
 * Content Injection Service for live procedural content integration
 * Handles dynamic content injection into active scenes and manages content lifecycle
 */

import { MapService } from '../map/MapService';
import { GameEventBridge } from '../integration/GameEventBridge';
import { PrismaClient } from '@prisma/client';
import { WebSocketManager } from '../websocket/WebSocketManager';

export interface ContentInjectionRequest {
  sceneId: string;
  contentType: 'encounter' | 'npc' | 'treasure' | 'hazard' | 'room' | 'dungeon';
  trigger: 'manual' | 'rule_based' | 'ai_decision' | 'player_action';
  parameters?: Record<string, any>;
  position?: { x: number; y: number };
  userId?: string;
}

export interface InjectedContent {
  id: string;
  sceneId: string;
  contentType: string;
  data: any;
  position?: { x: number; y: number };
  injectedAt: number;
  expiresAt?: number;
  active: boolean;
}

export class ContentInjectionService {
  private injectedContent = new Map<string, InjectedContent>();
  private contentGenerationQueue: ContentInjectionRequest[] = [];
  private processing = false;

  constructor(
    private mapService: MapService,
    private eventBridge: GameEventBridge,
    private prisma: PrismaClient,
    private webSocketManager: WebSocketManager
  ) {}

  /**
   * Initialize the content injection service
   */
  async initialize(): Promise<void> {
    console.log('Initializing Content Injection Service...');

    // Set up periodic content processing
    setInterval(() => {
      this.processContentQueue();
      this.cleanupExpiredContent();
    }, 5000); // Process every 5 seconds

    console.log('Content Injection Service initialized');
  }

  /**
   * Request procedural content injection into a scene
   */
  async requestContentInjection(request: ContentInjectionRequest): Promise<string> {
    console.log(`Content injection requested: ${request.contentType} for scene ${request.sceneId}`);

    // Add to processing queue
    this.contentGenerationQueue.push(request);

    // Generate unique request ID
    const requestId = `content_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

    // Process immediately if not already processing
    if (!this.processing) {
      this.processContentQueue();
    }

    return requestId;
  }

  /**
   * Process the content generation queue
   */
  private async processContentQueue(): Promise<void> {
    if (this.processing || this.contentGenerationQueue.length === 0) {
      return;
    }

    this.processing = true;

    try {
      while (this.contentGenerationQueue.length > 0) {
        const request = this.contentGenerationQueue.shift();
        if (request) {
          await this.processContentRequest(request);
        }
      }
    } catch (error) {
      console.error('Error processing content queue:', error);
    } finally {
      this.processing = false;
    }
  }

  /**
   * Process a single content injection request
   */
  private async processContentRequest(request: ContentInjectionRequest): Promise<void> {
    try {
      console.log(`Processing content request: ${request.contentType}`);

      // Generate content based on type
      const generatedContent = await this.generateContent(request);

      if (!generatedContent) {
        console.warn(`Failed to generate content for request: ${request.contentType}`);
        return;
      }

      // Inject content into scene
      const injectedId = await this.injectContentIntoScene(request.sceneId, generatedContent, request);

      if (injectedId) {
        // Notify clients of new content
        await this.notifyContentInjection(request.sceneId, injectedId, generatedContent);
      }

    } catch (error) {
      console.error(`Error processing content request:`, error);
    }
  }

  /**
   * Generate content based on request type
   */
  private async generateContent(request: ContentInjectionRequest): Promise<any> {
    const { contentType,  parameters = { } } = request;

    // Basic procedural generation - in a real implementation, this would use
    // the ContentGenerationWorkflows from the rules package
    switch (contentType) {
      case 'encounter':
        return this.generateEncounter(parameters);
      case 'npc':
        return this.generateNPC(parameters);
      case 'treasure':
        return this.generateTreasure(parameters);
      case 'hazard':
        return this.generateHazard(parameters);
      case 'room':
        return this.generateRoom(parameters);
      default:
        console.warn(`Unknown content type: ${contentType}`);
        return null;
    }
  }

  /**
   * Generate a random encounter
   */
  private generateEncounter(params: any): any {
    const encounterTypes = ['combat', 'puzzle', 'social', 'exploration'];
    const difficulties = ['easy', 'medium', 'hard', 'deadly'];

    return {
      type: 'encounter',
      subtype: encounterTypes[Math.floor(Math.random() * encounterTypes.length)],
      difficulty: difficulties[Math.floor(Math.random() * difficulties.length)],
      description: `A ${params.theme || 'mysterious'} encounter appears`,
      creatures: this.generateCreatures(params.partyLevel || 1),
      rewards: this.generateRewards(params.partyLevel || 1),
      position: params.position
    };
  }

  /**
   * Generate an NPC
   */
  private generateNPC(params: any): any {
    const names = ['Aldric', 'Brenna', 'Cedric', 'Dara', 'Elara', 'Finn'];
    const roles = ['merchant', 'guard', 'scholar', 'noble', 'commoner'];

    return {
      type: 'npc',
      name: names[Math.floor(Math.random() * names.length)],
      role: roles[Math.floor(Math.random() * roles.length)],
      level: params.level || Math.floor(Math.random() * 10) + 1,
      description: `A ${params.disposition || 'neutral'} NPC`,
      position: params.position,
      stats: this.generateNPCStats(params.level || 1)
    };
  }

  /**
   * Generate treasure
   */
  private generateTreasure(params: any): any {
    const treasureTypes = ['gold', 'gems', 'magic_item', 'artifact'];

    return {
      type: 'treasure',
      subtype: treasureTypes[Math.floor(Math.random() * treasureTypes.length)],
      value: (params.value || Math.floor(Math.random() * 1000) + 100),
      description: `Valuable treasure worth exploring`,
      position: params.position,
      hidden: params.hidden || Math.random() > 0.5
    };
  }

  /**
   * Generate a hazard
   */
  private generateHazard(params: any): any {
    const hazardTypes = ['trap', 'environmental', 'magical', 'structural'];

    return {
      type: 'hazard',
      subtype: hazardTypes[Math.floor(Math.random() * hazardTypes.length)],
      danger: params.danger || 'medium',
      description: `A dangerous hazard blocks the way`,
      position: params.position,
      radius: params.radius || 5,
      damage: params.damage || '2d6'
    };
  }

  /**
   * Generate a room
   */
  private generateRoom(params: any): any {
    const roomTypes = ['chamber', 'corridor', 'vault', 'shrine', 'library'];

    return {
      type: 'room',
      subtype: roomTypes[Math.floor(Math.random() * roomTypes.length)],
      dimensions: params.dimensions || { width: 20, height: 20 },
      description: `A ${params.theme || 'stone'} room`,
      features: this.generateRoomFeatures(),
      position: params.position
    };
  }

  /**
   * Generate creatures for encounters
   */
  private generateCreatures(partyLevel: number): any[] {
    const creatures = ['goblin', 'orc', 'skeleton', 'wolf', 'bear'];
    const count = Math.floor(Math.random() * 4) + 1;

    return Array.from({_ length: count }, () => ({
      type: creatures[Math.floor(Math.random() * creatures.length)],
      level: Math.max(1, partyLevel + Math.floor(Math.random() * 3) - 1),
      hp: Math.floor(Math.random() * 20) + 10
    }));
  }

  /**
   * Generate rewards for encounters
   */
  private generateRewards(partyLevel: number): any {
    return {
      experience: partyLevel * 100 + Math.floor(Math.random() * 100),
      gold: Math.floor(Math.random() * 100) + partyLevel * 10,
      items: Math.random() > 0.7 ? ['minor_potion'] : []
    };
  }

  /**
   * Generate NPC stats
   */
  private generateNPCStats(level: number): any {
    const base = 10;
    return {
      level,
      hp: base + level * 5,
      ac: base + Math.floor(level / 2),
      str: base + Math.floor(Math.random() * 6),
      dex: base + Math.floor(Math.random() * 6),
      con: base + Math.floor(Math.random() * 6),
      int: base + Math.floor(Math.random() * 6),
      wis: base + Math.floor(Math.random() * 6),
      cha: base + Math.floor(Math.random() * 6)
    };
  }

  /**
   * Generate room features
   */
  private generateRoomFeatures(): string[] {
    const features = ['altar', 'statue', 'fountain', 'chest', 'bookshelf', 'fireplace'];
    const count = Math.floor(Math.random() * 3);

    return features
      .sort(() => Math.random() - 0.5)
      .slice(0, count);
  }

  /**
   * Inject generated content into the scene
   */
  private async injectContentIntoScene(
    sceneId: string, 
    content: any, 
    request: ContentInjectionRequest
  ): Promise<string | null> {
    try {
      const contentId = `injected_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

      // Store injected content
      const injectedContent: InjectedContent = {
        id: contentId,
        sceneId,
        contentType: request.contentType,
        data: content,
        position: request.position,
        injectedAt: Date.now(),
        active: true
      };

      this.injectedContent.set(contentId, injectedContent);

      // Add content to scene via MapService
      switch (request.contentType) {
        case 'encounter':
          await this.mapService.addEncounter(sceneId, content);
          break;
        case 'npc':
          await this.mapService.addNPC(sceneId, content);
          break;
        case 'treasure':
          await this.mapService.addTreasure(sceneId, content);
          break;
        case 'hazard':
          await this.mapService.addHazard(sceneId, content);
          break;
      }

      console.log(`Content injected successfully: ${contentId}`);
      return contentId;

    } catch (error) {
      console.error('Error injecting content into scene:', error);
      return null;
    }
  }

  /**
   * Notify clients of content injection
   */
  private async notifyContentInjection(
    sceneId: string, 
    contentId: string, 
    content: any
  ): Promise<void> {
    try {
      // Broadcast via WebSocket
      const message = {
        type: 'content_injected' as any,
        data: {
          sceneId,
          contentId,
          contentType: content.type,
          content,
          timestamp: Date.now()
        }
      };

      // Send to all users in the scene session
      this.webSocketManager.broadcastToSession(sceneId, message);

    } catch (error) {
      console.error('Error notifying content injection:', error);
    }
  }

  /**
   * Remove injected content from scene
   */
  async removeInjectedContent(contentId: string): Promise<boolean> {
    const content = this.injectedContent.get(contentId);
    if (!content) return false;

    try {
      // Mark as inactive
      content.active = false;

      // TODO: Remove from MapService/scene
      // This would need specific removal methods based on content type

      this.injectedContent.delete(contentId);

      // Notify clients
      const message = {
        type: 'content_removed' as any,
        data: {
          sceneId: content.sceneId,
          contentId,
          timestamp: Date.now()
        }
      };

      this.webSocketManager.broadcastToSession(content.sceneId, message);

      return true;
    } catch (error) {
      console.error('Error removing injected content:', error);
      return false;
    }
  }

  /**
   * Get all injected content for a scene
   */
  getInjectedContentForScene(sceneId: string): InjectedContent[] {
    return Array.from(this.injectedContent.values())
      .filter(content => content.sceneId === sceneId && content.active);
  }

  /**
   * Clean up expired content
   */
  private cleanupExpiredContent(): void {
    const now = Date.now();

    for (const [id, content] of this.injectedContent.entries()) {
      if (content.expiresAt && now > content.expiresAt) {
        this.removeInjectedContent(id);
      }
    }
  }

  /**
   * Get service statistics
   */
  getStats(): {
    totalInjected: number;
    activeContent: number;
    queueSize: number;
    processing: boolean;
  } {
    return {
      totalInjected: this.injectedContent.size,
      activeContent: Array.from(this.injectedContent.values()).filter(c => c.active).length,
      queueSize: this.contentGenerationQueue.length,
      processing: this.processing
    };
  }
}

/**
 * Monster service for business logic and data operations
 */

import { PrismaClient } from "@prisma/client";
// import { monsters } from '@vtt/content-5e-srd'; // Temporarily disabled for e2e tests

export interface CreateMonsterRequest {
  name: string;
  stableId?: string;
  statblock: any;
  tags?: string[];
}

export interface UpdateMonsterRequest {
  name?: string;
  statblock?: any;
  tags?: string[];
}

export interface MonsterSearchOptions {
  query?: string | undefined;
  tags?: string[] | undefined;
  limit?: number;
  offset?: number;
}

export class MonsterService {
  constructor(private prisma: PrismaClient) {}

  async seedSRDMonsters(): Promise<{ created: number; updated: number; total: number }> {
    let created = 0;
    let updated = 0;

    for (const monster of SRDMonsters) {
      const stableId = monster.id;
      const existedBefore = await this.prisma.monster.findUnique({
        where: { stableId },
      });

      await this.prisma.monster.upsert({
        where: { stableId },
        create: {
          stableId,
          name: monster.name,
          statblock: monster as any,
          tags: Array.isArray((monster as any).tags) ? (monster as any).tags : [],
        },
        update: {
          name: monster.name,
          statblock: monster as any,
          tags: Array.isArray((monster as any).tags) ? (monster as any).tags : [],
        },
      });

      if (existedBefore) {
        updated += 1;
      } else {
        created += 1;
      }
    }

    return { created, updated, total: SRDMonsters.length };
  }

  async searchMonsters(options: MonsterSearchOptions = {}) {
    const { query,  tags,  limit = 50,  offset = 0  } = options;

    const where: any = {};

    if (query || tags?.length) {
      where.OR = [];

      if (query) {
        where.OR.push(
          { name: { contains: query, mode: "insensitive" } },
          { stableId: { contains: query, mode: "insensitive" } }
        );
      }

      if (tags?.length) {
        where.OR.push({ tags: { hasSome: tags } });
      }
    }

    const [items, total] = await Promise.all([
      this.prisma.monster.findMany({
        where,
        skip: offset,
        take: Math.min(limit, 200),
        orderBy: { name: "asc" },
      }),
      this.prisma.monster.count({ where }),
    ]);

    return { items, total, limit, offset };
  }

  async getMonster(idOrStableId: string) {
    return this.prisma.monster.findFirst({
      where: {
        OR: [{ id: idOrStableId }, { stableId: idOrStableId }],
      },
      include: {
        actors: {
          include: {
            tokens: true,
          },
        },
      },
    });
  }

  async createMonster(request: CreateMonsterRequest) {
    const stableId = request.stableId || this.generateStableId(request.name);

    return this.prisma.monster.create({
      data: {
        name: request.name,
        stableId,
        statblock: request.statblock,
        tags: request.tags || [],
      },
    });
  }

  async updateMonster(idOrStableId: string, request: UpdateMonsterRequest) {
    const monster = await this.getMonster(idOrStableId);
    if (!monster) {
      throw new Error("Monster not found");
    }

    const data: any = {};
    if (request.name !== undefined) data.name = request.name;
    if (request.statblock !== undefined) data.statblock = request.statblock;
    if (request.tags !== undefined) data.tags = request.tags;

    return this.prisma.monster.update({
      where: { id: monster.id },
      data,
    });
  }

  async deleteMonster(idOrStableId: string) {
    const monster = await this.getMonster(idOrStableId);
    if (!monster) {
      throw new Error("Monster not found");
    }

    return this.prisma.monster.delete({
      where: { id: monster.id },
    });
  }

  private generateStableId(name: string): string {
    const base = name
      .toLowerCase()
      .replace(/[^a-z0-9]+/g, "-")
      .replace(/(^-|-$)+/g, "");

    const suffix = Math.random().toString(36).slice(2, 6);
    return base ? `${base}-${suffix}` : `monster-${Date.now()}`;
  }

  async getMonsterStats() {
    const [total, byType] = await Promise.all([
      this.prisma.monster.count(),
      this.prisma.monster.groupBy({
        by: ["tags"],
        _count: true,
      }),
    ]);

    // Flatten tags for statistics
    const tagCounts: Record<string, number> = {};
    byType.forEach((_group) => {
      group.tags.forEach((_tag) => {
        tagCounts[tag] = (tagCounts[tag] || 0) + group._count;
      });
    });

    return {
      total,
      tagCounts,
      topTags: Object.entries(tagCounts)
        .sort(_([, _a], _[, _b]) => b - a)
        .slice(0, 10)
        .map([tag, _count] => ({ tag, count })),
    };
  }
}

import http from 'http';
import fs from 'fs';
import path from 'path';
import url from 'url';

const base = path.resolve('apps/client/spike');
const mime: Record<string,string> = {
  '.html':'text/html; charset=utf-8',
  '.js':'text/javascript; charset=utf-8',
  '.css':'text/css; charset=utf-8',
  '.map':'application/json; charset=utf-8'
};

const server = http.createServer((req, res) => {
  try {
    const parsed = url.parse(req.url || '/');
    let p = decodeURIComponent(parsed.pathname || '/');
    if (p === '/') p = '/index.html';
    const file = path.join(base, p);
    if (!file.startsWith(base)) { res.writeHead(403); return res.end('forbidden'); }
    if (!fs.existsSync(file)) { res.writeHead(404); return res.end('not found'); }
    const ext = path.extname(file);
    res.writeHead(200, { 'content-type': mime[ext] || 'application/octet-stream' });
    fs.createReadStream(file).pipe(res);
  } catch (_e) {
    res.writeHead(500); res.end('error');
  }
});

const PORT = 8081;
server.listen(_PORT, () => {
  console.log(`[spike] serving ${base} at http://localhost:${PORT}`);
  console.log(`[spike] open that URL and watch the console for PICK logs.`);
});

import { IncomingMessage } from "http";

export class HttpError extends Error {
  status: number;
  details?: unknown;
  constructor(status: number, message: string, details?: unknown) {
    super(message);
    this.status = status;
    this.details = details;
  }
}

export function parseJsonBody<T = any>(
  req: IncomingMessage,
  opts?: { limitBytes?: number }): Promise<T> {
  const envLimit = Number(process.env.JSON_LIMIT_BYTES ?? "");
  const defaultLimit = Number.isFinite(envLimit) && envLimit > 0 ? envLimit : 1_000_000; // 1MB default
  const limit = opts?.limitBytes ?? defaultLimit;
  return new Promise((_resolve, __reject) => {
    let body = "";
    let size = 0;

    req.on("data", (_chunk: Buffer | string) => {
      const len = typeof chunk === "string" ? Buffer.byteLength(chunk) : chunk.length;
      size += len;
      if (size > limit) {
        // terminate early to avoid memory pressure
        try {
          req.destroy();
        } catch {}
        return reject(new HttpError(413, "Payload too large"));
      }
      body += chunk as any;
    });

    req.on("end", () => {
      if (!body) return resolve({} as T);
      try {
        const data = JSON.parse(body);
        resolve(data);
      } catch (_error) {
        reject(new HttpError(400, "Invalid JSON in request body"));
      }
    });

    req.on("error", (err) => reject(err));
  });
}

export function sendJson(res: any, _data: any, _status = 200): void {
  res.writeHead(status, { "Content-Type": "application/json" });
  res.end(JSON.stringify(data));
}

/**
 * WebSocket Manager for real-time VTT collaboration
 */
import { WebSocketServer, WebSocket } from 'ws';
import { IncomingMessage } from 'http';
import { v4 as _uuidv4 } from 'uuid';
import { EventEmitter } from 'events';

export interface VTTWebSocketMessage {
  type: 'token_move' | 'token_add' | 'token_remove' | 'scene_update' | 'combat_update' | 
        'spell_cast' | 'spell_effect' | 'physics_collision' | 'projectile_launch' | 
        'barrier_created' | 'constraint_applied' | 'force_applied' | 'teleport_effect' |
        'concentration_check' | 'effect_expired' | 'user_join' | 'user_leave' | 'ping' | 'pong';
  payload: any;
  sessionId: string;
  userId: string;
  timestamp: number;
}

export interface ConnectedUser {
  id: string;
  socket: WebSocket;
  sessionId: string;
  campaignId: string;
  isGM: boolean;
  lastPing: number;
}

export class WebSocketManager extends EventEmitter {
  private wss: WebSocketServer;
  private sessions: Map<string, Set<ConnectedUser>> = new Map();
  private userSockets: Map<string, ConnectedUser> = new Map();
  private pingInterval!: NodeJS.Timeout;

  constructor(wss: WebSocketServer) {
    super();
    this.wss = wss;
    this.setupWebSocketServer();
    this.startPingInterval();
  }

  private setupWebSocketServer() {
    this.wss.on('connection', (_ws: WebSocket, req: IncomingMessage) => {
      const url = new URL(req.url!, `http://${req.headers.host}`);
      const sessionId = url.searchParams.get('sessionId');
      const userId = url.searchParams.get('userId');
      const campaignId = url.searchParams.get('campaignId');
      const isGM = url.searchParams.get('isGM') === 'true';

      if (!sessionId || !userId || !campaignId) {
        ws.close(1008, 'Missing required parameters');
        return;
      }

      const user: ConnectedUser = {
        id: userId,
        socket: ws,
        sessionId,
        campaignId,
        isGM,
        lastPing: Date.now()
      };

      this.addUserToSession(user);

      ws.on('message', (data: Buffer) => {
        try {
          const message: VTTWebSocketMessage = JSON.parse(data.toString());
          this.handleMessage(user, message);
        } catch (error) {
          console.error('Invalid WebSocket message:', error);
        }
      });

      ws.on('close', () => {
        this.removeUserFromSession(user);
      });

      ws.on('error', (error) => {
        console.error('WebSocket error:', error);
        this.removeUserFromSession(user);
      });

      // Send welcome message
      this.sendToUser(user, {
        type: 'user_join',
        payload: { userId, sessionId, campaignId },
        sessionId,
        userId: 'system',
        timestamp: Date.now()
      });
    });
  }

  private addUserToSession(user: ConnectedUser) {
    // Add to session
    if (!this.sessions.has(user.sessionId)) {
      this.sessions.set(user.sessionId, new Set());
    }
    this.sessions.get(user.sessionId)!.add(user);

    // Add to user lookup
    this.userSockets.set(user.id, user);

    // Notify other users
    this.broadcastToSession(user.sessionId, {
      type: 'user_join',
      payload: { 
        userId: user.id, 
        isGM: user.isGM,
        userCount: this.sessions.get(user.sessionId)!.size
      },
      sessionId: user.sessionId,
      userId: 'system',
      timestamp: Date.now()
    }, user.id);

    console.log(`User ${user.id} joined session ${user.sessionId}`);
  }

  private removeUserFromSession(user: ConnectedUser) {
    const session = this.sessions.get(user.sessionId);
    if (session) {
      session.delete(user);
      if (session.size === 0) {
        this.sessions.delete(user.sessionId);
      }
    }

    this.userSockets.delete(user.id);

    // Notify other users
    this.broadcastToSession(user.sessionId, {
      type: 'user_leave',
      payload: { 
        userId: user.id,
        userCount: session?.size || 0
      },
      sessionId: user.sessionId,
      userId: 'system',
      timestamp: Date.now()
    });

    console.log(`User ${user.id} left session ${user.sessionId}`);
  }

  private handleMessage(user: ConnectedUser, message: VTTWebSocketMessage) {
    // Update last ping
    user.lastPing = Date.now();

    switch (message.type) {
      case 'ping':
        this.sendToUser(user, {
          type: 'pong',
          payload: Record<string, any>,
          sessionId: user.sessionId,
          userId: 'system',
          timestamp: Date.now()
        });
        break;

      case 'token_move':
        // Validate GM permissions for certain actions
        if (this.requiresGMPermission(message.type) && !user.isGM) {
          this.sendError(user, 'Insufficient permissions');
          return;
        }
        this.broadcastToSession(user.sessionId, message, user.id);
        break;

      case 'token_add':
      case 'token_remove':
      case 'scene_update':
        if (!user.isGM) {
          this.sendError(user, 'GM permissions required');
          return;
        }
        this.broadcastToSession(user.sessionId, message, user.id);
        break;

      case 'combat_update':
        if (!user.isGM) {
          this.sendError(user, 'GM permissions required');
          return;
        }
        this.broadcastToSession(user.sessionId, message, user.id);
        break;

      case 'spell_cast':
      case 'spell_effect':
      case 'physics_collision':
      case 'projectile_launch':
      case 'barrier_created':
      case 'constraint_applied':
      case 'force_applied':
      case 'teleport_effect':
      case 'concentration_check':
      case 'effect_expired':
        this.broadcastToSession(user.sessionId, message, user.id);
        this.emit('gameEvent', message);
        break;

      default:
        this.broadcastToSession(user.sessionId, message, user.id);
    }
  }

  private requiresGMPermission(messageType: string): boolean {
    const gmOnlyActions = ['token_add', 'token_remove', 'scene_update', 'combat_update'];
    return gmOnlyActions.includes(messageType);
  }

  private sendToUser(user: ConnectedUser, message: VTTWebSocketMessage) {
    if (user.socket.readyState === WebSocket.OPEN) {
      user.socket.send(JSON.stringify(message));
    }
  }

  private sendError(user: ConnectedUser, error: string) {
    this.sendToUser(user, {
      type: 'error' as any,
      payload: { error },
      sessionId: user.sessionId,
      userId: 'system',
      timestamp: Date.now()
    });
  }

  private broadcastToSession(sessionId: string, message: VTTWebSocketMessage, excludeUserId?: string) {
    const session = this.sessions.get(sessionId);
    if (!session) return;

    for (const user of session) {
      if (excludeUserId && user.id === excludeUserId) continue;
      this.sendToUser(user, message);
    }
  }

  private startPingInterval() {
    this.pingInterval = setInterval(() => {
      const now = Date.now();
      for (const user of this.userSockets.values()) {
        // Remove stale connections (no ping for 30 seconds)
        if (now - user.lastPing > 30000) {
          user.socket.terminate();
          this.removeUserFromSession(user);
        }
      }
    }, 10000); // Check every 10 seconds
  }

  // Public API methods
  public broadcastTokenMove(sessionId: string, tokenId: string, x: number, y: number, userId: string) {
    this.broadcastToSession(sessionId, {
      type: 'token_move',
      payload: { tokenId, x, y },
      sessionId,
      userId,
      timestamp: Date.now()
    });
  }

  public broadcastTokenAdd(sessionId: string, token: any, userId: string) {
    this.broadcastToSession(sessionId, {
      type: 'token_add',
      payload: { token },
      sessionId,
      userId,
      timestamp: Date.now()
    });
  }

  public broadcastTokenRemove(sessionId: string, tokenId: string, userId: string) {
    this.broadcastToSession(sessionId, {
      type: 'token_remove',
      payload: { tokenId },
      sessionId,
      userId,
      timestamp: Date.now()
    });
  }

  public broadcastSceneUpdate(sessionId: string, sceneData: any, userId: string) {
    this.broadcastToSession(sessionId, {
      type: 'scene_update',
      payload: { sceneData },
      sessionId,
      userId,
      timestamp: Date.now()
    });
  }

  public broadcastCombatUpdate(sessionId: string, combatData: any, userId: string) {
    this.broadcastToSession(sessionId, {
      type: 'combat_update',
      payload: { combatData },
      sessionId,
      userId,
      timestamp: Date.now()
    });
  }

  public getSessionUsers(sessionId: string): ConnectedUser[] {
    const session = this.sessions.get(sessionId);
    return session ? Array.from(session) : [];
  }

  public getSessionCount(sessionId: string): number {
    const session = this.sessions.get(sessionId);
    return session ? session.size : 0;
  }

  public shutdown() {
    if (this.pingInterval) {
      clearInterval(this.pingInterval);
    }
    // Close all connections
    for (const session of this.sessions.values()) {
      for (const user of session) {
        user.socket.close();
      }
    }
    this.sessions.clear();
    this.userSockets.clear();
    this.wss.close();
  }
}

/**
 * Real-time WebSocket integration for combat state synchronization
 */

import { WebSocket } from 'ws';
import { ActorIntegrationService, _EncounterData, CombatActor } from '../services/ActorIntegrationService';

export interface CombatWebSocketMessage {
  type: string;
  payload: any;
  requestId?: string;
}

export interface CombatSubscription {
  ws: WebSocket;
  encounterId: string;
  userId: string;
}

export class CombatWebSocketManager {
  private subscriptions = new Map<string, CombatSubscription[]>(); // encounterId -> subscriptions
  private userSockets = new Map<string, WebSocket[]>(); // userId -> websockets

  constructor(private actorService: ActorIntegrationService) {}

  /**
   * Handle new WebSocket connection
   */
  handleConnection(ws: WebSocket, userId: string): void {
    // Add to user sockets
    if (!this.userSockets.has(userId)) {
      this.userSockets.set(userId, []);
    }
    this.userSockets.get(userId)!.push(ws);

    // Handle disconnection
    ws.on('close', () => {
      this.handleDisconnection(ws, userId);
    });

    // Handle messages
    ws.on('message', (data) => {
      try {
        const message: CombatWebSocketMessage = JSON.parse(data.toString());
        this.handleMessage(ws, userId, message);
      } catch (error) {
        console.error('Invalid WebSocket message:', error);
        this.sendError(ws, 'Invalid message format');
      }
    });

    // Send welcome message
    this.sendMessage(ws, {
      type: 'CONNECTED',
      payload: { userId, timestamp: Date.now() }
    });
  }

  /**
   * Handle WebSocket disconnection
   */
  private handleDisconnection(ws: WebSocket, userId: string): void {
    // Remove from user sockets
    const userSockets = this.userSockets.get(userId);
    if (userSockets) {
      const index = userSockets.indexOf(ws);
      if (index > -1) {
        userSockets.splice(index, 1);
      }
      if (userSockets.length === 0) {
        this.userSockets.delete(userId);
      }
    }

    // Remove from encounter subscriptions
    for (const [encounterId, subscriptions] of this.subscriptions.entries()) {
      const index = subscriptions.findIndex(sub => sub.ws === ws);
      if (index > -1) {
        subscriptions.splice(index, 1);
        if (subscriptions.length === 0) {
          this.subscriptions.delete(encounterId);
        }
      }
    }
  }

  /**
   * Handle incoming WebSocket messages
   */
  private async handleMessage(ws: WebSocket, userId: string, message: CombatWebSocketMessage): Promise<void> {
    switch (message.type) {
      case 'SUBSCRIBE_ENCOUNTER':
        await this.handleSubscribeEncounter(ws, userId, message);
        break;

      case 'UNSUBSCRIBE_ENCOUNTER':
        await this.handleUnsubscribeEncounter(ws, userId, message);
        break;

      case 'UPDATE_ACTOR_HEALTH':
        await this.handleUpdateActorHealth(ws, userId, message);
        break;

      case 'START_ENCOUNTER':
        await this.handleStartEncounter(ws, userId, message);
        break;

      case 'END_ENCOUNTER':
        await this.handleEndEncounter(ws, userId, message);
        break;

      case 'ADD_ACTOR':
        await this.handleAddActor(ws, userId, message);
        break;

      case 'REMOVE_ACTOR':
        await this.handleRemoveActor(ws, userId, message);
        break;

      case 'UPDATE_INITIATIVE':
        await this.handleUpdateInitiative(ws, userId, message);
        break;

      case 'NEXT_TURN':
        await this.handleNextTurn(ws, userId, message);
        break;

      case 'APPLY_CONDITION':
        await this.handleApplyCondition(ws, userId, message);
        break;

      case 'REMOVE_CONDITION':
        await this.handleRemoveCondition(ws, userId, message);
        break;

      default:
        this.sendError(ws, `Unknown message type: ${message.type}`, message.requestId);
    }
  }

  /**
   * Subscribe to encounter updates
   */
  private async handleSubscribeEncounter(ws: WebSocket, userId: string, message: CombatWebSocketMessage): Promise<void> {
    const { encounterId  } = message.payload;

    if (!encounterId) {
      this.sendError(ws, 'Missing encounterId', message.requestId);
      return;
    }

    try {
      // Verify encounter exists and user has access
      const encounter = await this.actorService.getEncounter(encounterId);
      if (!encounter) {
        this.sendError(ws, 'Encounter not found', message.requestId);
        return;
      }

      // Add subscription
      if (!this.subscriptions.has(encounterId)) {
        this.subscriptions.set(encounterId, []);
      }

      const subscription: CombatSubscription = { ws, encounterId, userId };
      this.subscriptions.get(encounterId)!.push(subscription);

      // Send current encounter state
      this.sendMessage(ws, {
        type: 'ENCOUNTER_SUBSCRIBED',
        payload: { encounter },
        requestId: message.requestId
      });

    } catch (error) {
      console.error('Subscribe encounter error:', error);
      this.sendError(ws, 'Failed to subscribe to encounter', message.requestId);
    }
  }

  /**
   * Unsubscribe from encounter updates
   */
  private async handleUnsubscribeEncounter(ws: WebSocket, userId: string, message: CombatWebSocketMessage): Promise<void> {
    const { encounterId  } = message.payload;

    const subscriptions = this.subscriptions.get(encounterId);
    if (subscriptions) {
      const index = subscriptions.findIndex(sub => sub.ws === ws && sub.userId === userId);
      if (index > -1) {
        subscriptions.splice(index, 1);
        if (subscriptions.length === 0) {
          this.subscriptions.delete(encounterId);
        }
      }
    }

    this.sendMessage(ws, {
      type: 'ENCOUNTER_UNSUBSCRIBED',
      payload: { encounterId },
      requestId: message.requestId
    });
  }

  /**
   * Handle actor health updates
   */
  private async handleUpdateActorHealth(ws: WebSocket, userId: string, message: CombatWebSocketMessage): Promise<void> {
    const { encounterId,  actorId,  health  } = message.payload;

    try {
      await this.actorService.updateActorHealth(actorId, health);

      // Broadcast to all subscribers
      this.broadcastToEncounter(encounterId, {
        type: 'ACTOR_HEALTH_UPDATED',
        payload: { actorId, health, updatedBy: userId }
      });

      this.sendMessage(ws, {
        type: 'HEALTH_UPDATE_SUCCESS',
        payload: { actorId },
        requestId: message.requestId
      });

    } catch (error) {
      console.error('Update actor health error:', error);
      this.sendError(ws, 'Failed to update actor health', message.requestId);
    }
  }

  /**
   * Handle start encounter
   */
  private async handleStartEncounter(ws: WebSocket, userId: string, message: CombatWebSocketMessage): Promise<void> {
    const { encounterId  } = message.payload;

    try {
      await this.actorService.startEncounter(encounterId);
      const encounter = await this.actorService.getEncounter(encounterId);

      // Broadcast to all subscribers
      this.broadcastToEncounter(encounterId, {
        type: 'ENCOUNTER_STARTED',
        payload: { encounter, startedBy: userId }
      });

    } catch (error) {
      console.error('Start encounter error:', error);
      this.sendError(ws, 'Failed to start encounter', message.requestId);
    }
  }

  /**
   * Handle end encounter
   */
  private async handleEndEncounter(ws: WebSocket, userId: string, message: CombatWebSocketMessage): Promise<void> {
    const { encounterId  } = message.payload;

    try {
      await this.actorService.endEncounter(encounterId);
      const encounter = await this.actorService.getEncounter(encounterId);

      // Broadcast to all subscribers
      this.broadcastToEncounter(encounterId, {
        type: 'ENCOUNTER_ENDED',
        payload: { encounter, endedBy: userId }
      });

    } catch (error) {
      console.error('End encounter error:', error);
      this.sendError(ws, 'Failed to end encounter', message.requestId);
    }
  }

  /**
   * Handle add actor to encounter
   */
  private async handleAddActor(ws: WebSocket, userId: string, message: CombatWebSocketMessage): Promise<void> {
    const { encounterId,  actorType,  actorId,  instanceName  } = message.payload;

    try {
      let actor: CombatActor;

      if (actorType === 'character') {
        actor = await this.actorService.addCharacterToEncounter(encounterId, actorId);
      } else if (actorType === 'monster') {
        actor = await this.actorService.addMonsterToEncounter(encounterId, actorId, instanceName);
      } else {
        this.sendError(ws, 'Invalid actor type', message.requestId);
        return;
      }

      // Broadcast to all subscribers
      this.broadcastToEncounter(encounterId, {
        type: 'ACTOR_ADDED',
        payload: { actor, addedBy: userId }
      });

    } catch (error) {
      console.error('Add actor error:', error);
      this.sendError(ws, 'Failed to add actor', message.requestId);
    }
  }

  /**
   * Handle remove actor from encounter
   */
  private async handleRemoveActor(ws: WebSocket, userId: string, message: CombatWebSocketMessage): Promise<void> {
    const { encounterId,  actorId  } = message.payload;

    try {
      // TODO: Implement remove actor method in ActorIntegrationService

      // Broadcast to all subscribers
      this.broadcastToEncounter(encounterId, {
        type: 'ACTOR_REMOVED',
        payload: { actorId, removedBy: userId }
      });

    } catch (error) {
      console.error('Remove actor error:', error);
      this.sendError(ws, 'Failed to remove actor', message.requestId);
    }
  }

  /**
   * Handle initiative updates
   */
  private async handleUpdateInitiative(ws: WebSocket, userId: string, message: CombatWebSocketMessage): Promise<void> {
    const { encounterId,  actorId,  initiative  } = message.payload;

    try {
      // TODO: Implement initiative update in ActorIntegrationService

      // Broadcast to all subscribers
      this.broadcastToEncounter(encounterId, {
        type: 'INITIATIVE_UPDATED',
        payload: { actorId, initiative, updatedBy: userId }
      });

    } catch (error) {
      console.error('Update initiative error:', error);
      this.sendError(ws, 'Failed to update initiative', message.requestId);
    }
  }

  /**
   * Handle next turn
   */
  private async handleNextTurn(ws: WebSocket, userId: string, message: CombatWebSocketMessage): Promise<void> {
    const { encounterId  } = message.payload;

    try {
      // TODO: Implement next turn logic in ActorIntegrationService

      const encounter = await this.actorService.getEncounter(encounterId);

      // Broadcast to all subscribers
      this.broadcastToEncounter(encounterId, {
        type: 'TURN_ADVANCED',
        payload: { encounter, advancedBy: userId }
      });

    } catch (error) {
      console.error('Next turn error:', error);
      this.sendError(ws, 'Failed to advance turn', message.requestId);
    }
  }

  /**
   * Handle apply condition
   */
  private async handleApplyCondition(ws: WebSocket, userId: string, message: CombatWebSocketMessage): Promise<void> {
    const { encounterId,  actorId,  condition  } = message.payload;

    try {
      // TODO: Implement condition application

      // Broadcast to all subscribers
      this.broadcastToEncounter(encounterId, {
        type: 'CONDITION_APPLIED',
        payload: { actorId, condition, appliedBy: userId }
      });

    } catch (error) {
      console.error('Apply condition error:', error);
      this.sendError(ws, 'Failed to apply condition', message.requestId);
    }
  }

  /**
   * Handle remove condition
   */
  private async handleRemoveCondition(ws: WebSocket, userId: string, message: CombatWebSocketMessage): Promise<void> {
    const { encounterId,  actorId,  conditionId  } = message.payload;

    try {
      // TODO: Implement condition removal

      // Broadcast to all subscribers
      this.broadcastToEncounter(encounterId, {
        type: 'CONDITION_REMOVED',
        payload: { actorId, conditionId, removedBy: userId }
      });

    } catch (error) {
      console.error('Remove condition error:', error);
      this.sendError(ws, 'Failed to remove condition', message.requestId);
    }
  }

  /**
   * Broadcast message to all encounter subscribers
   */
  private broadcastToEncounter(encounterId: string, message: CombatWebSocketMessage): void {
    const subscriptions = this.subscriptions.get(encounterId);
    if (!subscriptions) return;

    subscriptions.forEach(subscription => {
      if (subscription.ws.readyState === WebSocket.OPEN) {
        this.sendMessage(subscription.ws, message);
      }
    });
  }

  /**
   * Send message to specific WebSocket
   */
  private sendMessage(ws: WebSocket, message: CombatWebSocketMessage): void {
    if (ws.readyState === WebSocket.OPEN) {
      ws.send(JSON.stringify(message));
    }
  }

  /**
   * Send error message
   */
  private sendError(ws: WebSocket, error: string, requestId?: string): void {
    this.sendMessage(ws, {
      type: 'ERROR',
      payload: { error },
      requestId
    });
  }

  /**
   * Get subscription statistics
   */
  getStats(): {
    activeEncounters: number;
    totalSubscriptions: number;
    connectedUsers: number;
  } {
    const totalSubscriptions = Array.from(this.subscriptions.values())
      .reduce((_sum, _subs) => sum + subs.length, 0);

    return {
      activeEncounters: this.subscriptions.size,
      totalSubscriptions,
      connectedUsers: this.userSockets.size
    };
  }
}

import { WebSocket, WebSocketServer, RawData } from "ws";
import { IncomingMessage } from "http";
import { AnyClientMessageSchema, AnyServerMessage } from "@vtt/core-schemas";
import { _World, _MovementSystem, _NetworkSyncSystem } from "@vtt/core-ecs";
import { GameManager } from "../game/GameManager";
import { _GameSession } from "../game/GameSession";

interface Client {
  id: string;
  ws: WebSocket;
  ip: string;
  userId?: string;
  displayName?: string;
  gameId?: string;
}

export class WebSocketManager {
  private clients = new Map<string, Client>();
  private wss: WebSocketServer;
  private gameManager: GameManager;

  constructor(wss: WebSocketServer) {
    this.wss = wss;
    this.gameManager = new GameManager();
    this.setupConnectionHandler();
    this.startGameStateSyncing();
  }

  private setupConnectionHandler(): void {
    this.wss.on("connection", (_ws: WebSocket, req: IncomingMessage) => {
      const id = this.generateClientId();
      const ip = req.socket.remoteAddress || "unknown";

      const client: Client = { id, ws, ip };
      this.clients.set(id, client);

      console.log(`[ws] client connected ${id} from ${ip}`);

      this.sendToClient(id, { type: "HELLO", tickRate: 20, snapshotVersion: 0 });

      ws.on("message", (_data: RawData) => this.handleMessage(id, data));
      ws.on("close", () => this.handleDisconnection(id));
      ws.on("error", (_err: Error) => this.handleError(id, err));
    });
  }

  private startGameStateSyncing(): void {
    // Broadcast network deltas from all active games every 50ms (20hz)
    setInterval(() => {
      this.broadcastGameStateDeltas();
    }, 50);
  }

  private broadcastGameStateDeltas(): void {
    const activeGames = this.gameManager.getGames();

    for (const game of activeGames) {
      const delta = game.getNetworkDelta();
      if (!delta || (!delta.created.length && !delta.updated.length && !delta.removed.length)) {
        continue;
      }

      // Broadcast delta to all clients in this game
      this.broadcastToGame(game.gameId, {
        type: "DELTA",
        seq: delta.seq,
        baseSeq: delta.baseSeq,
        created: delta.created,
        updated: delta.updated,
        removed: delta.removed,
      });
    }
  }

  private handleMessage(clientId: string, data: RawData): void {
    const raw = typeof data === "string" ? data : data.toString("utf-8");
    let parsed: unknown;
    try {
      parsed = JSON.parse(raw);
    } catch (_err) {
      console.warn(`[ws] non-json from ${clientId}`);
      this.sendToClient(clientId, {
        type: "ERROR",
        code: "INVALID_JSON",
        message: "Payload must be JSON",
      });
      return;
    }

    const result = AnyClientMessageSchema.safeParse(parsed);
    if (!result.success) {
      this.sendToClient(clientId, {
        type: "ERROR",
        code: "SCHEMA_VALIDATION_FAILED",
        message: result.error.message,
      });
      return;
    }

    const msg = result.data;
    console.log(`[ws] message from ${clientId}:`, msg.type);

    try {
      switch (msg.type) {
        case "PING":
          this.sendToClient(clientId, { type: "PONG", t: msg.t });
          break;

        case "ECHO":
          this.sendToClient(clientId, { type: "ECHO", payload: msg.payload });
          break;

        case "JOIN_GAME":
          this.handleJoinGame(clientId, msg);
          break;

        case "LEAVE_GAME":
          this.handleLeaveGame(clientId, msg);
          break;

        case "MOVE_TOKEN":
          this.handleMoveToken(clientId, msg);
          break;

        case "ROLL_DICE": {
          const rollDiceParams: { dice: string; label?: string; private: boolean } = {
            dice: msg.dice,
            private: msg.private
          };
          if (msg.label !== undefined) {
            rollDiceParams.label = msg.label;
          }
          this.handleRollDice(clientId, rollDiceParams);
    }
          break;

        case "CHAT_MESSAGE":
          this.handleChatMessage(clientId, msg);
          break;

        case "SET_ACTIVE_SCENE":
          this.handleSetActiveScene(clientId, msg);
          break;

        case "UPDATE_SCENE_SETTINGS":
          this.handleUpdateSceneSettings(clientId, msg);
          break;

        case "SYNC_MAP_VIEW":
          this.handleSyncMapView(clientId, msg);
          break;

        default: {
          // Handle combat messages or other unknown types
          const msgType = (msg as any).type;
          if (msgType === "COMBAT_SUBSCRIBE" || msgType === "COMBAT_UNSUBSCRIBE" || msgType === "REQUEST_TACTICAL_DECISION") {
            this.handleCombatMessage(clientId, msg);
          } else {
            this.sendToClient(clientId, {
              type: "ERROR",
              code: "UNHANDLED_TYPE",
              message: `Unhandled type ${msgType}`,
            });
          }
    }
          break;
      }
    } catch (error) {
      console.error(`[ws] Error handling message from ${clientId}:`, error);
      this.sendToClient(clientId, {
        type: "ERROR",
        code: "INTERNAL_ERROR",
        message: "Internal server error",
      });
    }
  }

  // Game message handlers
  private handleJoinGame(clientId: string, msg: { gameId: string; userId: string; displayName: string }): void {
    const client = this.clients.get(clientId);
    if (!client) return;

    // Update client info
    client.userId = msg.userId;
    client.displayName = msg.displayName;
    client.gameId = msg.gameId;

    // Get or create game
    const game = this.gameManager.findOrCreateGame(msg.gameId);

    // Add player to game
    const success = game.addPlayer(msg.userId, msg.displayName);
    if (success) {
      console.log(`[ws] Player ${msg.displayName} joined game ${msg.gameId}`);

      // Send game state to client
      this.sendToClient(clientId, {
        type: "GAME_STATE",
        ...game.getGameState(),
      });

      // Send snapshot if game has entities
      const snapshot = game.getSnapshot();
      if (snapshot.entities.length > 0) {
        this.sendToClient(clientId, {
          type: "SNAPSHOT",
          seq: snapshot.seq,
          entities: snapshot.entities,
        });
      }

      // Broadcast player joined to other clients in the game
      this.broadcastToGame(msg.gameId, {
        type: "PLAYER_JOINED",
        userId: msg.userId,
        displayName: msg.displayName,
        gameId: msg.gameId,
      }, clientId);
    } else {
      this.sendToClient(clientId, {
        type: "ERROR",
        code: "JOIN_FAILED",
        message: "Failed to join game",
      });
    }
  }

  private handleLeaveGame(clientId: string, msg: { gameId: string }): void {
    const client = this.clients.get(clientId);
    if (!client || !client.userId) return;

    const game = this.gameManager.getGame(msg.gameId);
    if (game) {
      game.removePlayer(client.userId);

      this.broadcastToGame(msg.gameId, {
        type: "PLAYER_LEFT",
        userId: client.userId,
        gameId: msg.gameId,
      });

      console.log(`[ws] Player ${client.displayName} left game ${msg.gameId}`);
    }

    // Clear client game info
    delete client.gameId;
  }

  private handleMoveToken(clientId: string, msg: { entityId: number; x: number; y: number; animate: boolean }): void {
    const client = this.clients.get(clientId);
    if (!client || !client.gameId) return;

    const game = this.gameManager.getGame(client.gameId);
    if (!game) return;

    // TODO: Add authorization check - only allow moving own tokens or if GM
    const success = game.moveToken(msg.entityId, msg.x, msg.y, msg.animate);

    if (!success) {
      this.sendToClient(clientId, {
        type: "ERROR",
        code: "MOVE_FAILED",
        message: "Failed to move token",
      });
    }
    // Movement will be broadcast automatically via game's network sync
  }

  private handleRollDice(clientId: string, msg: { dice: string; label?: string; private: boolean }): void {
    const client = this.clients.get(clientId);
    if (!client || !client.gameId || !client.userId || !client.displayName) return;

    const game = this.gameManager.getGame(client.gameId);
    if (!game) return;

    const result = game.rollDice(msg.dice, client.userId, msg.label);
    if (!result) {
      this.sendToClient(clientId, {
        type: "ERROR",
        code: "INVALID_DICE",
        message: "Invalid dice notation",
      });
      return;
    }

    const rollMessage = {
      type: "DICE_ROLL_RESULT" as const,
      rollId: result.rollId,
      userId: client.userId,
      displayName: client.displayName,
      dice: result.dice,
      label: result.label,
      total: result.total,
      rolls: result.rolls,
      modifier: result.modifier,
      timestamp: result.timestamp,
      private: msg.private,
    };

    if (msg.private) {
      // Send only to the rolling player
      this.sendToClient(clientId, rollMessage);
    } else {
      // Broadcast to all players in the game
      this.broadcastToGame(client.gameId, rollMessage);
    }
  }

  private handleChatMessage(clientId: string, msg: { message: string; channel: string }): void {
    const client = this.clients.get(clientId);
    if (!client || !client.gameId || !client.userId || !client.displayName) return;

    const messageId = `msg_${Date.now()}_${Math.random().toString(36).slice(2, 8)}`;

    this.broadcastToGame(client.gameId, {
      type: "CHAT_BROADCAST",
      messageId,
      userId: client.userId,
      displayName: client.displayName,
      message: msg.message,
      channel: msg.channel,
      timestamp: Date.now(),
    });
  }

  private async handleCombatMessage(clientId: string, msg: any): Promise<void> {
    const client = this.clients.get(clientId);
    if (!client || !client.userId) return;

    try {
      // Import combat WebSocket handler
      const { handleCombatWebSocket  } = await import("../routes/combat");

      // Create a mock WebSocket object with send method for the handler
      const mockWs = {
        send: (data: string) => {
          try {
            const response = JSON.parse(data);
            this.sendToClient(clientId, response);
          } catch (error) {
            console.error('Error sending combat response:', error);
          }
        },
        combatSubscription: null
      };

      // Handle the combat message
      handleCombatWebSocket(mockWs, msg, client.userId);
    } catch (error) {
      console.error(`[ws] Error handling combat message from ${clientId}:`, error);
      this.sendToClient(clientId, {
        type: "ERROR",
        code: "COMBAT_ERROR",
        message: "Failed to process combat message",
      });
    }
  }

  // Map synchronization handlers
  private async handleSetActiveScene(clientId: string, msg: any): Promise<void> {
    const client = this.clients.get(clientId);
    if (!client || !client.userId || !client.gameId) return;

    try {
      const { CampaignService  } = await import("../campaign/CampaignService");
      const campaignService = new CampaignService({} as any);

      // Set active scene (assuming gameId corresponds to campaignId)
      const success = await campaignService.setActiveScene(client.gameId, msg.sceneId, client.userId);

      if (success) {
        // Broadcast scene change to all clients in the campaign
        this.broadcastToGame(client.gameId, {
          type: "ACTIVE_SCENE_CHANGED",
          campaignId: client.gameId,
          sceneId: msg.sceneId,
          timestamp: Date.now(),
        });
      } else {
        this.sendToClient(clientId, {
          type: "ERROR",
          code: "SET_SCENE_FAILED",
          message: "Failed to set active scene",
        });
      }
    } catch (error) {
      console.error(`[ws] Error setting active scene from ${clientId}:`, error);
      this.sendToClient(clientId, {
        type: "ERROR",
        code: "SET_SCENE_ERROR",
        message: "Error setting active scene",
      });
    }
  }

  private async handleUpdateSceneSettings(clientId: string, msg: any): Promise<void> {
    const client = this.clients.get(clientId);
    if (!client || !client.userId || !client.gameId) return;

    try {
      // Broadcast scene settings update to all clients in the campaign
      this.broadcastToGame(client.gameId, {
        type: "SCENE_SETTINGS_UPDATED",
        campaignId: client.gameId,
        sceneId: msg.sceneId,
        settings: {
          gridSettings: msg.gridSettings,
          lightingSettings: msg.lightingSettings,
          fogSettings: msg.fogSettings,
        },
        updatedBy: client.userId,
        timestamp: Date.now(),
      });
    } catch (error) {
      console.error(`[ws] Error updating scene settings from ${clientId}:`, error);
      this.sendToClient(clientId, {
        type: "ERROR",
        code: "UPDATE_SETTINGS_ERROR",
        message: "Error updating scene settings",
      });
    }
  }

  private handleSyncMapView(clientId: string, msg: any): void {
    const client = this.clients.get(clientId);
    if (!client || !client.userId || !client.gameId) return;

    try {
      // Broadcast map view synchronization to other clients in the campaign
      this.broadcastToGame(client.gameId, {
        type: "MAP_VIEW_SYNCED",
        campaignId: client.gameId,
        sceneId: msg.sceneId,
        viewState: {
          zoom: msg.zoom,
          centerX: msg.centerX,
          centerY: msg.centerY,
          viewport: msg.viewport,
        },
        syncedBy: client.userId,
        timestamp: Date.now(),
      }, clientId); // Exclude the sender
    } catch (error) {
      console.error(`[ws] Error syncing map view from ${clientId}:`, error);
      this.sendToClient(clientId, {
        type: "ERROR",
        code: "SYNC_VIEW_ERROR",
        message: "Error syncing map view",
      });
    }
  }

  private handleDisconnection(clientId: string): void {
    const client = this.clients.get(clientId);

    // Clean up game state if client was in a game
    if (client?.gameId && client.userId) {
      const game = this.gameManager.getGame(client.gameId);
      if (game) {
        game.setPlayerConnected(client.userId, false);

        this.broadcastToGame(client.gameId, {
          type: "PLAYER_LEFT",
          userId: client.userId,
          gameId: client.gameId,
        });
      }
    }

    this.clients.delete(clientId);
    console.log(`[ws] client ${clientId} disconnected`);
  }

  private handleError(clientId: string, error: Error): void {
    console.error(`[ws] client ${clientId} error:`, error);
    this.clients.delete(clientId);
  }

  private sendToClient(clientId: string, message: AnyServerMessage): void {
    const client = this.clients.get(clientId);
    if (client && client.ws.readyState === WebSocket.OPEN) {
      client.ws.send(JSON.stringify(message));
    }
  }

  broadcast(message: AnyServerMessage, excludeClientId?: string): void {
    const payload = JSON.stringify(message);
    for (const [id, client] of this.clients) {
      if (id !== excludeClientId && client.ws.readyState === WebSocket.OPEN) {
        client.ws.send(payload);
      }
    }
  }

  private broadcastToGame(gameId: string, message: AnyServerMessage, excludeClientId?: string): void {
    const payload = JSON.stringify(message);
    for (const [id, client] of this.clients) {
      if (client.gameId === gameId && id !== excludeClientId && client.ws.readyState === WebSocket.OPEN) {
        client.ws.send(payload);
      }
    }
  }

  getClientCount(): number {
    return this.clients.size;
  }

  private generateClientId(): string {
    return Math.random().toString(36).slice(2, 8);
  }

  async shutdown(): Promise<void> {
    // Shutdown game manager
    await this.gameManager.shutdown();
    // close client connections
    for (const [, client] of this.clients) {
      try {
        client.ws.close(1001, "Server shutting down");
      } catch {}
    }
    this.clients.clear();
    // close the server
    await new Promise<void>((_resolve) => {
      try {
        this.wss.close(() => resolve());
      } catch {
        resolve();
      }
    });
  }
}

import { test, expect } from '@playwright/test';
import type { AIProvider, AITextOptions, AITextResponse, AICapability } from '../packages/core/dist/index.js';

/**
 * Integration tests for AI provider failover and multi-provider support
 * Tests the robustness of the provider-agnostic AI system
 */

// Mock providers for testing failover scenarios
class MockSuccessProvider implements AIProvider {
  public readonly name = 'mock-success';
