  test('should handle rate limiting appropriately', async () => {
    class MockRateLimitedProvider implements AIProvider {
      public readonly name = 'mock-rate-limited';
      public readonly type = 'chat' as const;
      public readonly capabilities: AICapability[] = ['text_generation'];
      private callCount = 0;

      async generateText(prompt: string, options?: AITextOptions): Promise<AITextResponse> {
        this.callCount++;
        if (this.callCount <= 2) {
          throw new Error('Rate limit exceeded');
        }
        return {
          text: `Rate limited response: ${prompt}`,
          usage: { promptTokens: 10, completionTokens: 20, totalTokens: 30 },
          model: 'rate-limited-model',
          finishReason: 'stop' as const
        };
      }
    }

    aiProviderRegistry.registerProvider(
      new MockRateLimitedProvider(),
      { apiKey: 'test', timeout: 30000, maxRetries: 3, rateLimitPerMinute: 60, priority: 5, enabled: true }
    );

    // Should eventually succeed after retries
    const response = await aiProviderRegistry.generateText('Test prompt');
    expect(response.text).toBe('Rate limited response: Test prompt');
  });
});

import { test, expect } from '@playwright/test';
import { factory } from './utils/factories';
import { authUtils } from './utils/auth';
import { testDb } from './utils/database';

test.describe('Real-time Collaboration Scenarios', () => {
  test.beforeEach(async () => {
    await testDb.reset();
  });

  test('Concurrent token editing with conflict resolution', async ({ browser }) => {
    const gameSession = await factory.createCompleteGameSession();
    const { gm,  player1,  player2  } = gameSession.users;

    const contexts = await Promise.all([
      browser.newContext(),
      browser.newContext(),
      browser.newContext(),
    ]);

    const [gmPage, player1Page, player2Page] = await Promise.all([
      contexts[0].newPage(),
      contexts[1].newPage(),
      contexts[2].newPage(),
    ]);

    try {
      await Promise.all([
        authUtils.mockAuthentication(gmPage, gm),
        authUtils.mockAuthentication(player1Page, player1),
        authUtils.mockAuthentication(player2Page, player2),
      ]);

      const sceneUrl = `/scenes/${gameSession.scene.id}`;
      await Promise.all([
        gmPage.goto(sceneUrl),
        player1Page.goto(sceneUrl),
        player2Page.goto(sceneUrl),
      ]);

      await Promise.all([
        authUtils.waitForAuthReady(gmPage),
        authUtils.waitForAuthReady(player1Page),
        authUtils.waitForAuthReady(player2Page),
      ]);

      await gmPage.waitForTimeout(2000);

      // Both GM and Player1 try to move the same token simultaneously
      const gmToken = gmPage.locator('[data-testid="token"]').first();
      const player1Token = player1Page.locator('[data-testid="token"]').first();

      // Start concurrent moves
      await Promise.all([_
        (async () => {
          await gmToken.hover();
          await gmPage.mouse.down();
