  test.beforeAll(async () => {
    baseURL = process.env.TEST_SERVER_URL || 'http://localhost:8080';

    securitySystem = new SecuritySystem({
      auth: {
        jwtSecret: 'test-secret-key-for-e2e',
        jwtRefreshSecret: 'test-refresh-secret-for-e2e',
