    test('should refresh expired tokens', async ({ request }) => {
      // Register user
      await request.post(`${baseURL}/api/auth/register`, {
        data: {
          email: 'refresh-test@example.com',
          username: 'refreshuser',
          password: 'RefreshPass123!',
          acceptTerms: true,
        },
      });

      // Login to get initial tokens
