/**
 * Procedural Content Generator
 * Handles AI-driven generation of encounters, NPCs, loot, and environmental content
 */

export interface GenerationTemplate {
  id: string;
  name: string;
  type: 'encounter' | 'npc' | 'loot' | 'location' | 'quest' | 'dialogue';
  description: string;
  parameters: GenerationParameter[];
  generateContent: (params: Record<string, _any>) => Promise<GeneratedContent>;
  tags: string[];
  difficulty?: 'easy' | 'medium' | 'hard';
}

export interface GenerationParameter {
  name: string;
  type: 'string' | 'number' | 'boolean' | 'select' | 'multi-select';
  label: string;
  description?: string;
  required: boolean;
  defaultValue?: any;
  options?: Array<{ label: string; value: any }>;
  min?: number;
  max?: number;
}

export interface GeneratedContent {
  id: string;
  type: string;
  name: string;
  description: string;
  data: any;
  metadata: {
    generatedAt: number;
    templateId: string;
    parameters: Record<string, any>;
    version: string;
  };
}

export interface EncounterData {
  name: string;
  description: string;
  difficulty: number;
  enemies: Array<{
    name: string;
    count: number;
    level: number;
    type: string;
    stats: Record<string, number>;
  }>;
  environment: {
    terrain: string;
    weather: string;
    lighting: string;
    hazards: string[];
  };
  objectives: string[];
  rewards: {
    experience: number;
    gold: number;
    items: string[];
  };
  tactics: string[];
}

export interface NPCData {
  name: string;
  race: string;
  class: string;
  level: number;
  personality: {
    traits: string[];
    ideals: string[];
    bonds: string[];
    flaws: string[];
