  appearance: {
    age: string;
    height: string;
    weight: string;
    eyes: string;
    hair: string;
    skin: string;
    distinguishing_features: string[];
  };
  background: {
    occupation: string;
    history: string;
    motivations: string[];
    secrets: string[];
  };
  stats: Record<string, number>;
  skills: string[];
  equipment: string[];
  spells?: string[];
}

export interface LocationData {
  name: string;
  type: string;
  size: string;
  description: string;
  features: Array<{
    name: string;
    description: string;
    interactive: boolean;
    hidden?: boolean;
