  public startTiming(name: string): () => void {
    return this.telemetry.startTiming(name);
  }

  // Dashboard methods
  public subscribeToDashboard(_callback: (data: any) => void): () => void {
    this.subscribers.push(callback);

    // Send initial data
    callback(this.getDashboardData());

    return () => {
      const index = this.subscribers.indexOf(callback);
      if (index > -1) {
        this.subscribers.splice(index, 1);
      }
    };
  }

  public getDashboardData(): any {
    const metrics = this.telemetry.getMetrics();
    const currentTime = Date.now();

    return {
      timestamp: currentTime,
      metrics,
      charts: this.generateChartData(),
      alerts: this.activeAlerts,
      summary: this.generateSummary(metrics)
    };
  }

  private generateChartData(): any {
    const chartData: Record<string, any> = {};

    for (const chart of this.config.dashboard.charts) {
      chartData[chart.id] = this.getChartData(chart);
    }

    return chartData;
  }

  private getChartData(chart: ChartConfig): any {
    const timeRange = chart.timeRange * 60 * 1000; // Convert minutes to ms
    const cutoff = Date.now() - timeRange;

    const relevantData = this.metricsHistory.filter(entry => entry.timestamp > cutoff);

    switch (chart.type) {
      case 'line':
        return {
          labels: relevantData.map(entry => new Date(entry.timestamp).toLocaleTimeString()),
          datasets: chart.metrics.map(metric => ({
            label: metric,
            data: relevantData.map(entry => this.extractMetricValue(entry.metrics, metric))
          }))
        };

      case 'bar':
        return {
          labels: chart.metrics,
          data: chart.metrics.map(metric => {
            const latest = relevantData[relevantData.length - 1];
            return latest ? this.extractMetricValue(latest.metrics, metric) : 0;
          })
        };

      case 'pie': {
        const total = relevantData.length;
        return {
          labels: chart.metrics,
          data: chart.metrics.map(metric => {
            const count = relevantData.filter(entry => 
              this.extractMetricValue(entry.metrics, metric) > 0
            ).length;
            return total > 0 ? (count / total) * 100 : 0;
          })
        };

    }
      case 'gauge': {
        const latest = relevantData[relevantData.length - 1];
        return {
          value: latest ? this.extractMetricValue(latest.metrics, chart.metrics[0]) : 0,
          max: 100
        };

    }
      default:
        return {};
    }
  }

  private extractMetricValue(metrics: any, path: string): number {
    const parts = path.split('.');
    let value = metrics;

    for (const part of parts) {
      value = value?.[part];
    }

    return typeof value === 'number' ? value : 0;
  }

  private generateSummary(metrics: any): AnalyticsSummary {
    const { _performance,  user,  system  } = metrics;

    return {
      totalEvents: this.eventHistory.reduce((_sum, _entry) => sum + entry.events.length, 0),
      totalUsers: 1, // Would track unique users
      totalSessions: 1, // Would track session count
      avgSessionDuration: user.sessionDuration,
      errorRate: system.errorRate,
      uptime: system.uptime
    };
  }

  // Alert methods
  public addAlertRule(rule: AlertRule): void {
    this.config.alerts.rules.push(rule);
    this.alertState.set(rule.id, { triggered: false, lastCheck: Date.now() });
  }

  public removeAlertRule(ruleId: string): void {
    this.config.alerts.rules = this.config.alerts.rules.filter(rule => rule.id !== ruleId);
    this.alertState.delete(ruleId);
  }

  public getActiveAlerts(): Array<{ rule: AlertRule; triggeredAt: number; severity: string }> {
    return [...this.activeAlerts];
  }

  private startAlertProcessing(): void {
    this.alertCheckTimer = window.setInterval(() => {
      this.processAlerts();
    }, 5000); // Check every 5 seconds
  }

  private processAlerts(): void {
    const currentMetrics = this.telemetry.getMetrics();
    const currentTime = Date.now();

    for (const rule of this.config.alerts.rules) {
      if (!rule.enabled) continue;

      const state = this.alertState.get(rule.id)!;
      const metricValue = this.extractMetricValue(currentMetrics, rule.metric);
      const shouldTrigger = this.evaluateAlertCondition(rule, metricValue);

      if (shouldTrigger && !state.triggered) {
        // Check if condition has been true for required duration
        if (currentTime - state.lastCheck >= rule.duration) {
          this.triggerAlert(rule);
          state.triggered = true;
        }
      } else if (!shouldTrigger && state.triggered) {
        this.resolveAlert(rule);
        state.triggered = false;
      }

      state.lastCheck = currentTime;
    }
  }

  private evaluateAlertCondition(rule: AlertRule, value: number): boolean {
    switch (rule.condition) {
      case 'above':
        return value > rule.threshold;
      case 'below':
        return value < rule.threshold;
      case 'equals':
        return Math.abs(value - rule.threshold) < 0.001;
      case 'change':
        // Would need historical data for change detection
        return false;
      default:
        return false;
    }
  }

  private triggerAlert(rule: AlertRule): void {
    const alert = {
      rule,
      triggeredAt: Date.now(),
      severity: rule.severity
    };

    this.activeAlerts.push(alert);

    // Send alerts through configured channels
    for (const channel of this.config.alerts.channels) {
      if (channel.enabled) {
        this.sendAlert(channel, rule);
      }
    }

    console.warn(`Alert triggered: ${rule.name}`);
  }

  private resolveAlert(rule: AlertRule): void {
    const index = this.activeAlerts.findIndex(alert => alert.rule.id === rule.id);
    if (index > -1) {
      this.activeAlerts.splice(index, 1);
      console.log(`Alert resolved: ${rule.name}`);
    }
  }

  private sendAlert(channel: AlertChannel, rule: AlertRule): void {
    switch (channel.type) {
      case 'console':
        console.error(`ALERT [${rule.severity.toUpperCase()}]: ${rule.name}`);
        break;

      case 'notification':
        if ('Notification' in window && Notification.permission === 'granted') {
          new Notification(`Alert: ${rule.name}`, {
            body: `${rule.metric} ${rule.condition} ${rule.threshold}`,
            icon: '/alert-icon.png'
          });
        }
        break;

      case 'webhook':
        fetch(channel.config.url, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            alert: rule.name,
            severity: rule.severity,
            metric: rule.metric,
            threshold: rule.threshold,
            timestamp: Date.now()
          })
        }).catch(error => console.error('Webhook alert failed:', error));
        break;

      default:
        console.log(`Alert channel ${channel.type} not implemented`);
    }
  }

  // Reporting methods
  public async generateReport(period?: { start: number; end: number }): Promise<AnalyticsReport> {
    const defaultPeriod = {
      start: Date.now() - 24 * 60 * 60 * 1000, // Last 24 hours
      end: Date.now()
    };

    const reportPeriod = period || defaultPeriod;
    const metrics = this.telemetry.getMetrics();

    const report: AnalyticsReport = {
      period: reportPeriod,
      summary: this.generateSummary(metrics),
      performance: this.generatePerformanceReport(metrics.performance),
      user: this.generateUserReport(metrics.user),
      system: this.generateSystemReport(metrics.system),
      insights: this.generateInsights(metrics),
      recommendations: this.generateRecommendations(metrics)
    };

    return report;
  }

  private generatePerformanceReport(metrics: PerformanceMetrics): PerformanceReport {
    return {
      avgFPS: metrics.fps,
      avgFrameTime: metrics.frameTime,
      avgMemoryUsage: metrics.memoryUsage,
      renderTimeP95: metrics.renderTime * 1.2, // Estimate
      loadTimeP95: metrics.loadTime * 1.1, // Estimate
      slowestOperations: [] // Would be populated from detailed metrics
    };
  }

  private generateUserReport(metrics: UserMetrics): UserReport {
    return {
      activeUsers: 1, // Would track from session data
      newUsers: 1,
      returningUsers: 0,
      avgActionsPerSession: metrics.actionsPerSession,
      topFeatures: metrics.featuresUsed.map(feature => ({ feature, usage: 1 })),
      conversionRate: metrics.conversionEvents.length > 0 ? 1 : 0,
      abandonmentRate: 0
    };
  }

  private generateSystemReport(metrics: SystemMetrics): SystemReport {
    return {
      errorsByType: { general: metrics.errorRate },
      crashRate: metrics.crashRate,
      resourceUtilization: metrics.resourceUsage,
      networkStats: { latency: 0, bandwidth: 0, errors: 0 },
      featureAdoption: metrics.featureUsage
    };
  }

  private generateInsights(metrics: any): AnalyticsInsight[] {
    const insights: AnalyticsInsight[] = [];

    // Performance insights
    if (metrics.performance.fps < 30) {
      insights.push({
        type: 'performance',
        severity: 'warning',
        title: 'Low FPS Detected',
        description: `Average FPS is ${metrics.performance.fps}, below optimal threshold of 30`,
        data: { fps: metrics.performance.fps },
        timestamp: Date.now()
      });
    }

    // Memory insights
    if (metrics.performance.memoryUsage > 500) {
      insights.push({
        type: 'performance',
        severity: 'warning',
        title: 'High Memory Usage',
        description: `Memory usage is ${metrics.performance.memoryUsage}MB, approaching limits`,
        data: { memoryUsage: metrics.performance.memoryUsage },
        timestamp: Date.now()
      });
    }

    // Error rate insights
    if (metrics.system.errorRate > 0.05) {
      insights.push({
        type: 'system',
        severity: 'critical',
        title: 'High Error Rate',
        description: `Error rate is ${(metrics.system.errorRate * 100).toFixed(1)}%, exceeding 5% threshold`,
        data: { errorRate: metrics.system.errorRate },
        timestamp: Date.now()
      });
    }

    return insights;
  }

  private generateRecommendations(metrics: any): AnalyticsRecommendation[] {
    const recommendations: AnalyticsRecommendation[] = [];

    if (metrics.performance.fps < 60) {
      recommendations.push({
        type: 'optimization',
        priority: 'high',
        title: 'Optimize Rendering Performance',
        description: 'Frame rate is below optimal 60 FPS target',
        impact: 'Improved user experience and smoother gameplay',
        effort: 'medium',
        actionItems: [
          'Profile rendering bottlenecks',
          'Optimize shader complexity',
          'Implement LOD system',
          'Enable occlusion culling'
        ]
      });
    }

    if (metrics.user.featuresUsed.length < 5) {
      recommendations.push({
        type: 'feature',
        priority: 'medium',
        title: 'Improve Feature Discovery',
        description: 'Users are utilizing fewer features than expected',
        impact: 'Increased user engagement and retention',
        effort: 'low',
        actionItems: [
          'Add feature tooltips',
          'Implement guided tours',
          'Improve UI/UX design',
          'Add feature highlights'
        ]
      });
    }

    return recommendations;
  }

  private setupDefaultAlerts(): void {
    const defaultRules: AlertRule[] = [
      {
        id: 'low_fps',
        name: 'Low FPS',
        metric: 'performance.fps',
        condition: 'below',
        threshold: 30,
        duration: 10000,
        severity: 'medium',
        enabled: true
      },
      {
        id: 'high_memory',
        name: 'High Memory Usage',
        metric: 'performance.memoryUsage',
        condition: 'above',
        threshold: 1000,
        duration: 30000,
        severity: 'high',
        enabled: true
      },
      {
        id: 'high_error_rate',
        name: 'High Error Rate',
        metric: 'system.errorRate',
        condition: 'above',
        threshold: 0.1,
        duration: 5000,
        severity: 'critical',
        enabled: true
      }
    ];

    this.config.alerts.rules.push(...defaultRules);

    for (const rule of defaultRules) {
      this.alertState.set(rule.id, { triggered: false, lastCheck: Date.now() });
    }
  }

  private setupDefaultCharts(): void {
    const defaultCharts: ChartConfig[] = [
      {
        id: 'fps_chart',
        type: 'line',
        title: 'FPS Over Time',
        metrics: ['performance.fps'],
        timeRange: 15,
        refreshRate: 5000
      },
      {
        id: 'memory_chart',
        type: 'line',
        title: 'Memory Usage',
        metrics: ['performance.memoryUsage'],
        timeRange: 30,
        refreshRate: 5000
      },
      {
        id: 'error_rate_gauge',
        type: 'gauge',
        title: 'Error Rate',
        metrics: ['system.errorRate'],
        timeRange: 5,
        refreshRate: 5000
      }
    ];

    this.config.dashboard.charts.push(...defaultCharts);
  }

  private setupReporting(): void {
    // Setup periodic reporting based on interval
    // This would typically integrate with a reporting service
    console.log('Reporting system setup (placeholder)');
  }

  private startDashboardUpdates(): void {
    this.dashboardUpdateTimer = window.setInterval(() => {
      const metrics = this.telemetry.getMetrics();
      const timestamp = Date.now();

      // Store metrics history
      this.metricsHistory.push({ timestamp, metrics });

      // Trim history to max data points
      if (this.metricsHistory.length > this.config.dashboard.maxDataPoints) {
        this.metricsHistory.shift();
      }

      // Notify subscribers
      const dashboardData = this.getDashboardData();
      for (const subscriber of this.subscribers) {
        subscriber(dashboardData);
      }

    }, this.config.dashboard.updateInterval);
  }

  public async dispose(): Promise<void> {
    // Clean up timers
    if (this.dashboardUpdateTimer) {
      clearInterval(this.dashboardUpdateTimer);
    }

    if (this.alertCheckTimer) {
      clearInterval(this.alertCheckTimer);
    }

    // Dispose telemetry
    await this.telemetry.dispose();

    this.isInitialized = false;
    console.log('AnalyticsManager disposed');
  }
}

export interface TelemetryEvent {
  id: string;
  type: string;
  category: 'performance' | 'user' | 'system' | 'error' | 'business';
  timestamp: number;
  sessionId: string;
  userId?: string;
  data: Record<string, any>;
  metadata: TelemetryMetadata;
}

export interface TelemetryMetadata {
  platform: string;
  version: string;
  build: string;
  userAgent: string;
  screen: { width: number; height: number };
  viewport: { width: number; height: number };
  timezone: string;
  language: string;
  referrer?: string;
}

export interface TelemetryConfig {
  endpoint: string;
  batchSize: number;
  flushInterval: number; // ms
  maxRetries: number;
  retryDelay: number; // ms
  enabledCategories: string[];
  samplingRate: number; // 0-1
  enableLocalStorage: boolean;
  enableErrorTracking: boolean;
  enablePerformanceTracking: boolean;
  enableUserTracking: boolean;
  privacyMode: boolean;
}

export interface PerformanceMetrics {
  fps: number;
  frameTime: number;
  renderTime: number;
  memoryUsage: number;
  drawCalls: number;
  triangles: number;
  loadTime: number;
  networkLatency: number;
  errorRate: number;
}

export interface UserMetrics {
  sessionDuration: number;
  actionsPerSession: number;
  featuresUsed: string[];
  conversionEvents: string[];
  abandonmentPoints: string[];
  satisfactionScore?: number;
}

export interface SystemMetrics {
  crashRate: number;
  errorRate: number;
  uptime: number;
  resourceUsage: {
    cpu: number;
    memory: number;
    gpu: number;
    network: number;
  };
  featureUsage: Record<string, number>;
}

export class TelemetrySystem {
  private config: TelemetryConfig;
  private eventQueue: TelemetryEvent[] = [];
  private sessionId: string;
  private userId?: string;
  private isInitialized = false;
  private flushTimer?: number;
  private metadata: TelemetryMetadata;

  // Performance tracking
  private performanceMetrics: PerformanceMetrics = {
    fps: 0,
    frameTime: 0,
    renderTime: 0,
    memoryUsage: 0,
    drawCalls: 0,
    triangles: 0,
    loadTime: 0,
    networkLatency: 0,
    errorRate: 0
  };

  // User tracking
  private userMetrics: UserMetrics = {
    sessionDuration: 0,
    actionsPerSession: 0,
    featuresUsed: [],
    conversionEvents: [],
    abandonmentPoints: [],
  };

  // System tracking
  private systemMetrics: SystemMetrics = {
    crashRate: 0,
    errorRate: 0,
    uptime: 0,
    resourceUsage: { cpu: 0, memory: 0, gpu: 0, network: 0 },
    featureUsage: Record<string, any>
  };

  private sessionStartTime = Date.now();
  private eventCount = 0;
  private errorCount = 0;
  private lastFlushTime = Date.now();

  constructor(config: Partial<TelemetryConfig> = {}) {
    this.config = {
      endpoint: '',
      batchSize: 50,
      flushInterval: 30000, // 30 seconds
      maxRetries: 3,
      retryDelay: 1000,
      enabledCategories: ['performance', 'user', 'system', 'error'],
      samplingRate: 1.0,
      enableLocalStorage: true,
      enableErrorTracking: true,
      enablePerformanceTracking: true,
      enableUserTracking: true,
      privacyMode: false,
      ...config
    };

    this.sessionId = this.generateSessionId();
    this.metadata = this.collectMetadata();
  }

  public async initialize(userId?: string): Promise<void> {
    if (this.isInitialized) return;

    this.userId = userId;
    this.isInitialized = true;

    // Load persisted events from local storage
    if (this.config.enableLocalStorage) {
      await this.loadPersistedEvents();
    }

    // Setup automatic flushing
    this.setupAutoFlush();

    // Setup error tracking
    if (this.config.enableErrorTracking) {
      this.setupErrorTracking();
    }

    // Setup performance tracking
    if (this.config.enablePerformanceTracking) {
      this.setupPerformanceTracking();
    }

    // Setup page visibility tracking
    this.setupVisibilityTracking();

    // Track initialization
    this.track('system.initialized', {
      config: this.sanitizeConfig(),
      metadata: this.metadata
    });

    console.log('TelemetrySystem initialized');
  }

  public track(type: string, data: Record<string, any> = {}, category: TelemetryEvent['category'] = 'user'): void {
    if (!this.isInitialized || !this.shouldSample()) return;

    if (!this.config.enabledCategories.includes(category)) return;

    const event: TelemetryEvent = {
      id: this.generateEventId(),
      type,
      category,
      timestamp: Date.now(),
      sessionId: this.sessionId,
      userId: this.userId,
      data: this.config.privacyMode ? this.sanitizeData(data) : data,
      metadata: this.metadata
    };

    this.eventQueue.push(event);
    this.eventCount++;

    if (category === 'user') {
      this.userMetrics.actionsPerSession++;
      if (!this.userMetrics.featuresUsed.includes(type)) {
        this.userMetrics.featuresUsed.push(type);
      }
    }

    if (category === 'system') {
      this.systemMetrics.featureUsage[type] = (this.systemMetrics.featureUsage[type] || 0) + 1;
    }

    // Flush if batch size reached
    if (this.eventQueue.length >= this.config.batchSize) {
      this.flush();
    }

    // Persist to local storage
    if (this.config.enableLocalStorage) {
      this.persistEvents();
    }
  }

  public trackError(error: Error, context: Record<string, any> = {}): void {
    this.errorCount++;

    const errorData = {
      message: error.message,
      stack: error.stack,
      name: error.name,
      context,
      url: window.location.href,
      userAgent: navigator.userAgent,
      timestamp: Date.now()
    };

    this.track('error.caught', errorData, 'error');

    // Update error rate
    this.systemMetrics.errorRate = this.errorCount / this.eventCount;
  }

  public trackPerformance(metrics: Partial<PerformanceMetrics>): void {
    this.performanceMetrics = { ...this.performanceMetrics, ...metrics };

    this.track('performance.metrics', this.performanceMetrics, 'performance');
  }

  public trackUserAction(action: string, target?: string, value?: any): void {
    this.track('user.action', {
      action,
      target,
      value,
      timestamp: Date.now()
    }, 'user');
  }

  public trackPageView(page: string, referrer?: string): void {
    this.track('user.pageview', {
      page,
      referrer: referrer || document.referrer,
      timestamp: Date.now()
    }, 'user');
  }

  public trackConversion(event: string, value?: number): void {
    this.userMetrics.conversionEvents.push(event);

    this.track('user.conversion', {
      event,
      value,
      timestamp: Date.now()
    }, 'business');
  }

  public trackCustomEvent(name: string, properties: Record<string, any> = {}): void {
    this.track(`custom.${name}`, properties, 'user');
  }

  public startTiming(name: string): () => void {
    const startTime = performance.now();

    return () => {
      const duration = performance.now() - startTime;
      this.track('performance.timing', {
        name,
        duration,
        startTime
      }, 'performance');
    };
  }

  public async flush(): Promise<void> {
    if (this.eventQueue.length === 0) return;

    const events = [...this.eventQueue];
    this.eventQueue = [];

    try {
      await this.sendEvents(events);
      this.lastFlushTime = Date.now();

      // Clear persisted events on successful flush
      if (this.config.enableLocalStorage) {
        localStorage.removeItem('telemetry_events');
      }

    } catch (error) {
      console.error('Failed to flush telemetry events:', error);

      // Put events back in queue for retry
      this.eventQueue.unshift(...events);
    }
  }

  public setUserId(userId: string): void {
    this.userId = userId;
    this.track('user.identified', { userId }, 'user');
  }

  public updateConfig(updates: Partial<TelemetryConfig>): void {
    this.config = { ...this.config, ...updates };
    this.track('system.config_updated', { updates }, 'system');
  }

  public getMetrics(): {
    performance: PerformanceMetrics;
    user: UserMetrics;
    system: SystemMetrics;
  } {
    // Update session duration
    this.userMetrics.sessionDuration = Date.now() - this.sessionStartTime;
    this.systemMetrics.uptime = Date.now() - this.sessionStartTime;

    return {
      performance: { ...this.performanceMetrics },
      user: { ...this.userMetrics },
      system: { ...this.systemMetrics }
    };
  }

  public async dispose(): Promise<void> {
    // Track session end
    this.track('user.session_end', {
      duration: Date.now() - this.sessionStartTime,
      eventCount: this.eventCount,
      errorCount: this.errorCount
    }, 'user');

    // Final flush
    await this.flush();

    // Clear timers
    if (this.flushTimer) {
      clearInterval(this.flushTimer);
    }

    this.isInitialized = false;
    console.log('TelemetrySystem disposed');
  }

  private generateSessionId(): string {
    return `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  private generateEventId(): string {
    return `event_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  private collectMetadata(): TelemetryMetadata {
    return {
      platform: navigator.platform,
      version: '1.0.0', // Would come from build config
      build: 'development', // Would come from build config
      userAgent: navigator.userAgent,
      screen: {
        width: screen.width,
        height: screen.height
      },
      viewport: {
        width: window.innerWidth,
        height: window.innerHeight
      },
      timezone: Intl.DateTimeFormat().resolvedOptions().timeZone,
      language: navigator.language,
      referrer: document.referrer
    };
  }

  private shouldSample(): boolean {
    return Math.random() < this.config.samplingRate;
  }

  private sanitizeConfig(): Partial<TelemetryConfig> {
    const { _endpoint,  _...safeConfig  } = this.config;
    return safeConfig;
  }

  private sanitizeData(data: Record<string, any>): Record<string, any> {
    // Remove potentially sensitive data in privacy mode
    const sanitized = { ...data };

    const sensitiveKeys = ['password', 'token', 'key', 'secret', 'email', 'phone'];

    for (const key of Object.keys(sanitized)) {
      if (sensitiveKeys.some(sk => key.toLowerCase().includes(sk))) {
        sanitized[key] = '[REDACTED]';
      }
    }

    return sanitized;
  }

  private async sendEvents(events: TelemetryEvent[]): Promise<void> {
    if (!this.config.endpoint) {
      console.warn('No telemetry endpoint configured');
      return;
    }

    const payload = {
      events,
      sessionId: this.sessionId,
      userId: this.userId,
      timestamp: Date.now()
    };

    let retries = 0;

    while (retries <= this.config.maxRetries) {
      try {
        const response = await fetch(this.config.endpoint, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(payload)
        });

        if (response.ok) {
          return;
        }

        throw new Error(`HTTP ${response.status}: ${response.statusText}`);

      } catch (error) {
        retries++;

        if (retries > this.config.maxRetries) {
          throw error;
        }

        // Wait before retry
        await new Promise(resolve => setTimeout(resolve, this.config.retryDelay * retries));
      }
    }
  }

  private setupAutoFlush(): void {
    this.flushTimer = window.setInterval(() => {
      if (this.eventQueue.length > 0) {
        this.flush();
      }
    }, this.config.flushInterval);
  }

  private setupErrorTracking(): void {
    // Global error handler
    window.addEventListener('error', (event) => {
      this.trackError(new Error(event.message), {
        filename: event.filename,
        lineno: event.lineno,
        colno: event.colno
      });
    });

    // Unhandled promise rejection handler
    window.addEventListener('unhandledrejection', (event) => {
      this.trackError(new Error(event.reason), {
        type: 'unhandled_rejection'
      });
    });
  }

  private setupPerformanceTracking(): void {
    // Track page load performance
    window.addEventListener('load', () => {
      const perfData = performance.getEntriesByType('navigation')[0] as PerformanceNavigationTiming;

      if (perfData) {
        this.trackPerformance({
          loadTime: perfData.loadEventEnd - perfData.fetchStart,
          networkLatency: perfData.responseStart - perfData.requestStart
        });
      }
    });

    // Track FPS and frame time
    let frameCount = 0;
    let lastTime = performance.now();

    const trackFrame = (_currentTime: number) => {
      frameCount++;

      if (currentTime - lastTime >= 1000) {
        this.performanceMetrics.fps = frameCount;
        this.performanceMetrics.frameTime = 1000 / frameCount;

        frameCount = 0;
        lastTime = currentTime;

        // Track performance every 5 seconds
        if (Math.random() < 0.2) {
          this.trackPerformance({
            fps: this.performanceMetrics.fps,
            frameTime: this.performanceMetrics.frameTime
          });
        }
      }

      requestAnimationFrame(trackFrame);
    };

    requestAnimationFrame(trackFrame);

    // Track memory usage if available
    if ('memory' in performance) {
      setInterval(() => {
        const memory = (performance as any).memory;
        this.performanceMetrics.memoryUsage = memory.usedJSHeapSize / (1024 * 1024);
      }, 5000);
    }
  }

  private setupVisibilityTracking(): void {
    let visibleTime = Date.now();
    let totalVisibleTime = 0;

    document.addEventListener('visibilitychange', () => {
      if (document.hidden) {
        totalVisibleTime += Date.now() - visibleTime;
        this.track('user.page_hidden', {
          visibleDuration: Date.now() - visibleTime,
          totalVisibleTime
        }, 'user');
      } else {
        visibleTime = Date.now();
        this.track('user.page_visible', {
          timestamp: Date.now()
        }, 'user');
      }
    });

    // Track on page unload
    window.addEventListener('beforeunload', () => {
      if (!document.hidden) {
        totalVisibleTime += Date.now() - visibleTime;
      }

      this.track('user.page_unload', {
        totalVisibleTime,
        sessionDuration: Date.now() - this.sessionStartTime
      }, 'user');

      // Synchronous final flush
      navigator.sendBeacon(
        this.config.endpoint,
        JSON.stringify({
          events: this.eventQueue,
          sessionId: this.sessionId,
          userId: this.userId,
          timestamp: Date.now()
        })
      );
    });
  }

  private async loadPersistedEvents(): Promise<void> {
    try {
      const stored = localStorage.getItem('telemetry_events');
      if (stored) {
        const events = JSON.parse(stored) as TelemetryEvent[];
        this.eventQueue.push(...events);
      }
    } catch (error) {
      console.warn('Failed to load persisted telemetry events:', error);
    }
  }

  private persistEvents(): void {
    try {
      localStorage.setItem('telemetry_events', JSON.stringify(this.eventQueue));
    } catch (error) {
      console.warn('Failed to persist telemetry events:', error);
    }
  }
}

