/**
 * Authorization Manager - Role-based access control and permissions
 */

import { EventEmitter } from 'events';
import {
  User,
  Permission,
  SecurityContext,
  SessionPermissions,
  AuditLogEntry
} from './types';

export class AuthorizationManager extends EventEmitter {
  private rolePermissions: Map<string, Set<Permission>> = new Map();
  private resourcePolicies: Map<string, ResourcePolicy> = new Map();
  private auditLog: AuditLogEntry[] = [];

  constructor() {
    super();
    this.initializeRolePermissions();
    this.initializeResourcePolicies();
  }

  /**
   * Check if user has specific permission
   */
  checkPermission(context: SecurityContext, permission: Permission, resource?: string, resourceId?: string): boolean {
    const hasPermission = this.hasDirectPermission(context, permission);

    if (!hasPermission) {
      this.logPermissionDenied(context, permission, resource, resourceId);
      return false;
    }

    // Check resource-specific policies
    if (resource) {
      const policy = this.resourcePolicies.get(resource);
      if (policy && !policy.evaluate(context, permission, resourceId)) {
        this.logPermissionDenied(context, permission, resource, resourceId);
        return false;
      }
    }

    this.logPermissionGranted(context, permission, resource, resourceId);
    return true;
  }

  /**
   * Check multiple permissions (all must pass)
   */
  checkPermissions(context: SecurityContext, permissions: Permission[], resource?: string, resourceId?: string): boolean {
    return permissions.every(permission => 
      this.checkPermission(context, permission, resource, resourceId)
    );
  }

  /**
   * Check if user has any of the specified permissions
   */
  checkAnyPermission(context: SecurityContext, permissions: Permission[], resource?: string, resourceId?: string): boolean {
    return permissions.some(permission => 
      this.checkPermission(context, permission, resource, resourceId)
    );
  }

  /**
   * Get session-specific permissions for VTT gameplay
   */
  getSessionPermissions(context: SecurityContext, sessionId: string, isGameMaster: boolean = false): SessionPermissions {
    const basePermissions = {
      canCreateTokens: false,
      canMoveTokens: false,
      canEditMap: false,
      canManagePlayers: false,
      canControlCombat: false,
      canUseFogOfWar: false,
      canPlayAudio: false,
      canManageAssets: false,
      canViewGMNotes: false,
      canRollDice: true,
      canUseChat: true
    };

    // Game Master gets all permissions
    if (isGameMaster || context.user.role === 'gamemaster' || context.user.role === 'admin') {
      return {
        canCreateTokens: true,
        canMoveTokens: true,
        canEditMap: true,
        canManagePlayers: true,
        canControlCombat: true,
        canUseFogOfWar: true,
        canPlayAudio: true,
        canManageAssets: true,
        canViewGMNotes: true,
        canRollDice: true,
        canUseChat: true
      };
    }

    // Players get limited permissions
    if (context.user.role === 'player') {
      basePermissions.canMoveTokens = this.checkPermission(context, 'session.manage', 'session', sessionId);
      basePermissions.canCreateTokens = false; // Usually GM only
      basePermissions.canRollDice = true;
      basePermissions.canUseChat = true;
    }

    return basePermissions;
  }

  /**
   * Grant temporary permission for specific action
   */
  grantTemporaryPermission(userId: string, permission: Permission, resource: string, resourceId: string, durationMs: number): void {
    const tempPermissionKey = `${userId}:${permission}:${resource}:${resourceId}`;

    setTimeout(() => {
      this.revokeTemporaryPermission(tempPermissionKey);
    }, durationMs);

    this.emit('temporaryPermissionGranted', {
      userId,
      permission,
      resource,
      resourceId,
      duration: durationMs
    });
  }

  /**
   * Check resource ownership
   */
  checkOwnership(context: SecurityContext, resourceType: string, resourceId: string): boolean {
    // Check if user owns the resource
    switch (resourceType) {
      case 'session':
        return this.isSessionOwner(context.user.id, resourceId);
      case 'character':
        return this.isCharacterOwner(context.user.id, resourceId);
      case 'campaign':
        return this.isCampaignOwner(context.user.id, resourceId);
      default:
        return false;
    }
  }

  /**
   * Elevate user permissions (admin action)
   */
  elevatePermissions(adminContext: SecurityContext, targetUserId: string, permissions: Permission[]): boolean {
    if (!this.checkPermission(adminContext, 'user.manage')) {
      throw new Error('Insufficient permissions to elevate user permissions');
    }

    // Implementation would update user permissions in database
    this.logAuditEvent({
      id: this.generateId(),
      userId: adminContext.user.id,
      action: 'elevate_permissions',
      resource: 'user',
      resourceId: targetUserId,
      details: { permissions },
      ipAddress: adminContext.ipAddress,
      userAgent: adminContext.userAgent,
      timestamp: new Date(),
      sessionId: adminContext.session.id
    });

    return true;
  }

  /**
   * Audit log management
   */
  getAuditLog(context: SecurityContext, filters?: AuditFilters): AuditLogEntry[] {
    if (!this.checkPermission(context, 'system.admin')) {
      throw new Error('Insufficient permissions to view audit log');
    }

    let log = [...this.auditLog];

    if (filters) {
      if (filters.userId) {
        log = log.filter(entry => entry.userId === filters.userId);
      }
      if (filters.action) {
        log = log.filter(entry => entry.action === filters.action);
      }
      if (filters.resource) {
        log = log.filter(entry => entry.resource === filters.resource);
      }
      if (filters.startDate) {
        log = log.filter(entry => entry.timestamp >= filters.startDate!);
      }
      if (filters.endDate) {
        log = log.filter(entry => entry.timestamp <= filters.endDate!);
      }
    }

    return log.sort((_a, _b) => b.timestamp.getTime() - a.timestamp.getTime());
  }

  /**
   * Create authorization middleware for Express
   */
  createMiddleware(requiredPermission: Permission, resource?: string) {
    return async (req: any, res: any, _next: any) => {
      try {
        const context = req.securityContext as SecurityContext;
        if (!context) {
          return res.status(401).json({ error: 'Authentication required' });
        }

        const resourceId = req.params.id || req.params.resourceId;

        if (!this.checkPermission(context, requiredPermission, resource, resourceId)) {
          return res.status(403).json({ 
            error: 'Insufficient permissions',
            required: requiredPermission,
            resource,
            resourceId
          });
        }

        next();
      } catch (_error) {
        res.status(500).json({ error: 'Authorization check failed' });
      }
    };
  }

  /**
   * Bulk permission check for UI state
   */
  getUserPermissionSet(context: SecurityContext, resources: string[] = []): Record<string, boolean> {
    const permissions: Record<string, boolean> = {};

    // Check all standard permissions
    const allPermissions: Permission[] = [
      'session.create', 'session.join', 'session.manage', 'session.delete',
      'content.create', 'content.edit', 'content.delete', 'content.publish',
      'user.manage', 'user.moderate', 'system.admin', 'billing.manage'
    ];

    allPermissions.forEach(permission => {
      permissions[permission] = this.hasDirectPermission(context, permission);
    });

    // Check resource-specific permissions
    resources.forEach(resource => {
      allPermissions.forEach(permission => {
        const key = `${resource}.${permission}`;
        permissions[key] = this.checkPermission(context, permission, resource);
      });
    });

    return permissions;
  }

  // Private helper methods

  private initializeRolePermissions(): void {
    // Guest permissions
    this.rolePermissions.set('guest', new Set(['session.join']));

    // Player permissions
    this.rolePermissions.set('player', new Set([
      'session.join',
      'session.create'
    ]));

    // GameMaster permissions
    this.rolePermissions.set('gamemaster', new Set([
      'session.join',
      'session.create',
      'session.manage',
      'content.create',
      'content.edit'
    ]));

    // Moderator permissions
    this.rolePermissions.set('moderator', new Set([
      'session.join',
      'session.create',
      'session.manage',
      'content.create',
      'content.edit',
      'content.delete',
      'user.moderate'
    ]));

    // Admin permissions (all)
    this.rolePermissions.set('admin', new Set([
      'session.create', 'session.join', 'session.manage', 'session.delete',
      'content.create', 'content.edit', 'content.delete', 'content.publish',
      'user.manage', 'user.moderate', 'system.admin', 'billing.manage'
    ]));
  }

  private initializeResourcePolicies(): void {
    // Session access policy
    this.resourcePolicies.set('session', _{
      evaluate: (context: SecurityContext, _permission: Permission, _resourceId?: string) => {
        if (permission === 'session.manage' || permission === 'session.delete') {
          return resourceId ? this.isSessionOwner(context.user.id, resourceId) : false;
        }
        return true;
      }
    });

    // Content access policy
    this.resourcePolicies.set('content', _{
      evaluate: (context: SecurityContext, _permission: Permission, _resourceId?: string) => {
        if (permission === 'content.edit' || permission === 'content.delete') {
          return resourceId ? this.isContentOwner(context.user.id, resourceId) : false;
        }
        return true;
      }
    });

    // User management policy
    this.resourcePolicies.set('user', _{
      evaluate: (context: SecurityContext, _permission: Permission, _resourceId?: string) => {
        if (permission === 'user.manage') {
          // Users can manage their own profile, admins can manage anyone
          return resourceId === context.user.id || context.user.role === 'admin';
        }
        return true;
      }
    });
  }

  private hasDirectPermission(context: SecurityContext, permission: Permission): boolean {
    // Check explicit permissions
    if (context.permissions.includes(permission)) {
      return true;
    }

    // Check role-based permissions
    const rolePermissions = this.rolePermissions.get(context.user.role);
    return rolePermissions ? rolePermissions.has(permission) : false;
  }

  private logPermissionGranted(context: SecurityContext, permission: Permission, resource?: string, resourceId?: string): void {
    this.logAuditEvent({
      id: this.generateId(),
      userId: context.user.id,
      action: 'permission_granted',
      resource: resource || 'system',
      resourceId: resourceId || undefined,
      details: { permission },
      ipAddress: context.ipAddress,
      userAgent: context.userAgent,
      timestamp: new Date(),
      sessionId: context.session.id
    });
  }

  private logPermissionDenied(context: SecurityContext, permission: Permission, resource?: string, resourceId?: string): void {
    this.logAuditEvent({
      id: this.generateId(),
      userId: context.user.id,
      action: 'permission_denied',
      resource: resource || 'system',
      resourceId: resourceId || undefined,
      details: { permission, reason: 'insufficient_permissions' },
      ipAddress: context.ipAddress,
      userAgent: context.userAgent,
      timestamp: new Date(),
      sessionId: context.session.id
    });
  }

  private logAuditEvent(entry: AuditLogEntry): void {
    this.auditLog.push(entry);

    // Keep only last 10000 entries in memory
    if (this.auditLog.length > 10000) {
      this.auditLog = this.auditLog.slice(-10000);
    }

    this.emit('auditLog', entry);
  }

  private revokeTemporaryPermission(key: string): void {
    // Implementation would remove temporary permission
    this.emit('temporaryPermissionRevoked', { key });
  }

  private generateId(): string {
    return Date.now().toString(36) + Math.random().toString(36).substr(2);
  }

  // Resource ownership checks (would query database in real implementation)
  private isSessionOwner(_userId: string, _sessionId: string): boolean {
    // Placeholder - would check database
    return true;
  }

  private isCharacterOwner(_userId: string, _characterId: string): boolean {
    // Placeholder - would check database
    return true;
  }

  private isCampaignOwner(_userId: string, _campaignId: string): boolean {
    // Placeholder - would check database
    return true;
  }

  private isContentOwner(_userId: string, _contentId: string): boolean {
    // Placeholder - would check database
    return true;
  }
}

interface ResourcePolicy {
  evaluate(context: SecurityContext, permission: Permission, resourceId?: string): boolean;
}

interface AuditFilters {
  userId?: string;
  action?: string;
  resource?: string;
  startDate?: Date;
  endDate?: Date;
}

/**
 * Security Middleware and Rate Limiting
 */

import { EventEmitter } from 'events';
import { RateLimiterMemory } from 'rate-limiter-flexible';
import helmet from 'helmet';
import { AuthManager } from './AuthManager';
import { AuthorizationManager } from './AuthorizationManager';
import {
  SecurityContext,
  RateLimitConfig,
  SecuritySettings,
  AuditLogEntry
} from './types';

export class SecurityMiddleware extends EventEmitter {
  private authManager: AuthManager;
  private authzManager: AuthorizationManager;
  private rateLimiters: Map<string, RateLimiterMemory> = new Map();
  private securitySettings: SecuritySettings;
  private blockedIPs = new Set<string>();
  private suspiciousActivity = new Map<string, SuspiciousActivityTracker>();

  constructor(
    authManager: AuthManager,
    authzManager: AuthorizationManager,
    settings: SecuritySettings
  ) {
    super();
    this.authManager = authManager;
    this.authzManager = authzManager;
    this.securitySettings = settings;
    this.initializeRateLimiters();
    this.startSecurityMonitoring();
  }

  /**
   * Authentication middleware
   */
  authenticate() {
    return async (req: any, res: any, _next: any) => {
      try {
        const token = this.extractToken(req);
        if (!token) {
          return this.respondUnauthorized(res, 'No token provided');
        }

        const ipAddress = this.getClientIP(req);
        const userAgent = req.get('User-Agent') || '';

        // Check if IP is blocked
        if (this.blockedIPs.has(ipAddress)) {
          this.logSecurityEvent('blocked_ip_access', '', ipAddress, userAgent);
          return res.status(403).json({ error: 'Access denied' });
        }

        // Validate token and get security context
        const context = await this.authManager.validateToken(token, ipAddress, userAgent);

        // Check for suspicious activity
        await this.checkSuspiciousActivity(context, req);

        // Attach security context to request
        req.securityContext = context;
        req.user = context.user;

        this.logSecurityEvent('authenticated', context.user.id, ipAddress, userAgent);
        next();
      } catch (error) {
        this.logSecurityEvent('authentication_failed', '', this.getClientIP(req), req.get('User-Agent') || '');
        return this.respondUnauthorized(res, error instanceof Error ? error.message : 'Authentication failed');
      }
    };
  }

  /**
   * Authorization middleware
   */
  authorize(permission: string, resource?: string) {
    return (req: any, res: any, _next: any) => {
      try {
        const context = req.securityContext as SecurityContext;
        if (!context) {
          return res.status(401).json({ error: 'Authentication required' });
        }

        const resourceId = req.params.id || req.params.resourceId;

        if (!this.authzManager.checkPermission(context, permission as any, resource, resourceId)) {
          this.logSecurityEvent('authorization_failed', context.user.id, context.ipAddress, context.userAgent, {
            permission,
            resource,
            resourceId
          });
          return res.status(403).json({ 
            error: 'Insufficient permissions',
            required: permission
          });
        }

        next();
      } catch (error) {
        return res.status(500).json({ error: 'Authorization check failed' });
      }
    };
  }

  /**
   * Rate limiting middleware
   */
  rateLimit(type: string = 'general') {
    return async (req: any, res: any, _next: any) => {
      try {
        const limiter = this.rateLimiters.get(type);
        if (!limiter) {
          return next();
        }

        const key = this.getRateLimitKey(req, type);
        const resRateLimit = await limiter.consume(key);

        // Set rate limit headers
        res.set({
          'X-RateLimit-Limit': limiter.points,
          'X-RateLimit-Remaining': resRateLimit.remainingPoints,
          'X-RateLimit-Reset': new Date(Date.now() + resRateLimit.msBeforeNext).toISOString()
        });

        next();
      } catch (rejRes: any) {
        const secs = Math.round(rejRes.msBeforeNext / 1000) || 1;

        this.logSecurityEvent('rate_limit_exceeded', this.getUserId(req), this.getClientIP(req), req.get('User-Agent') || '', {
          type,
          retryAfter: secs
        });

        res.set('Retry-After', String(secs));
        return res.status(429).json({
          error: 'Too many requests',
          retryAfter: secs
        });
      }
    };
  }

  /**
   * Security headers middleware
   */
  securityHeaders(): any {
    return helmet({
      contentSecurityPolicy: {
        directives: {
          defaultSrc: ["'self'"],
          scriptSrc: ["'self'", "'unsafe-inline'", "'unsafe-eval'"],
          styleSrc: ["'self'", "'unsafe-inline'", "https://fonts.googleapis.com"],
          fontSrc: ["'self'", "https://fonts.gstatic.com"],
          imgSrc: ["'self'", "data:", "https:", "blob:"],
          connectSrc: ["'self'", "ws:", "wss:"],
          mediaSrc: ["'self'", "blob:"],
          objectSrc: ["'none'"],
          baseUri: ["'self'"],
          formAction: ["'self'"],
          frameAncestors: ["'none'"]
        }
      },
      crossOriginEmbedderPolicy: false, // Allow WebGL contexts
      hsts: {
        maxAge: 31536000,
        includeSubDomains: true,
        preload: true
      }
    });
  }

  /**
   * Input validation middleware
   */
  validateInput(schema: ValidationSchema) {
    return (req: any, res: any, _next: any) => {
      const errors = this.validateRequestData(req, schema);
      if (errors.length > 0) {
        this.logSecurityEvent('input_validation_failed', this.getUserId(req), this.getClientIP(req), req.get('User-Agent') || '', {
          errors
        });
        return res.status(400).json({ 
          error: 'Invalid input',
          details: errors
        });
      }
      next();
    };
  }

  /**
   * CORS middleware with security considerations
   */
  cors(allowedOrigins: string[] = []) {
    return (req: any, res: any, _next: any) => {
      const origin = req.get('Origin');

      if (origin && (allowedOrigins.includes(origin) || this.isDevelopment())) {
        res.set('Access-Control-Allow-Origin', origin);
        res.set('Access-Control-Allow-Credentials', 'true');
      }

      res.set('Access-Control-Allow-Methods', 'GET, POST, PUT, DELETE, OPTIONS, PATCH');
      res.set('Access-Control-Allow-Headers', 'Origin, X-Requested-With, Content-Type, Accept, Authorization, X-API-Key');

      if (req.method === 'OPTIONS') {
        return res.status(200).end();
      }

      next();
    };
  }

  /**
   * WebSocket security middleware
   */
  secureWebSocket() {
    return async (ws: any, req: any) => {
      try {
        const token = this.extractTokenFromWS(req);
        if (!token) {
          ws.close(1008, 'No token provided');
          return;
        }

        const ipAddress = this.getClientIP(req);
        const userAgent = req.headers['user-agent'] || '';

        if (this.blockedIPs.has(ipAddress)) {
          ws.close(1008, 'Access denied');
          return;
        }

        const context = await this.authManager.validateToken(token, ipAddress, userAgent);

        // Attach security context to WebSocket
        ws.securityContext = context;
        ws.isAuthenticated = true;

        this.logSecurityEvent('websocket_authenticated', context.user.id, ipAddress, userAgent);

        // Set up periodic token validation
        const validationInterval = setInterval(async () => {
          try {
            await this.authManager.validateToken(token, ipAddress, userAgent);
          } catch (error) {
            clearInterval(validationInterval);
            ws.close(1008, 'Token expired');
          }
        }, 5 * 60 * 1000); // Every 5 minutes

        ws.on('close', () => {
          clearInterval(validationInterval);
        });

      } catch (error) {
        this.logSecurityEvent('websocket_auth_failed', '', this.getClientIP(req), req.headers['user-agent'] || '');
        ws.close(1008, 'Authentication failed');
      }
    };
  }

  /**
   * Intrusion detection system
   */
  async detectIntrusion(req: any, res: any, next: any) {
    const ipAddress = this.getClientIP(req);
    const userAgent = req.get('User-Agent') || '';
    const userId = this.getUserId(req);

    // Check for SQL injection patterns
    if (this.detectSQLInjection(req)) {
      await this.handleSecurityThreat('sql_injection', userId, ipAddress, userAgent, req);
      return res.status(400).json({ error: 'Invalid request' });
    }

    // Check for XSS patterns
    if (this.detectXSS(req)) {
      await this.handleSecurityThreat('xss_attempt', userId, ipAddress, userAgent, req);
      return res.status(400).json({ error: 'Invalid request' });
    }

    // Check for path traversal
    if (this.detectPathTraversal(req)) {
      await this.handleSecurityThreat('path_traversal', userId, ipAddress, userAgent, req);
      return res.status(400).json({ error: 'Invalid request' });
    }

    // Check for unusual patterns
    if (await this.detectAnomalousActivity(req)) {
      await this.handleSecurityThreat('anomalous_activity', userId, ipAddress, userAgent, req);
    }

    next();
  }

  /**
   * Block IP address
   */
  blockIP(ipAddress: string, reason: string = 'Security violation'): void {
    this.blockedIPs.add(ipAddress);
    this.logSecurityEvent('ip_blocked', '', ipAddress, '', { reason });

    // Auto-unblock after 24 hours
    setTimeout(() => {
      this.unblockIP(ipAddress);
    }, 24 * 60 * 60 * 1000);
  }

  /**
   * Unblock IP address
   */
  unblockIP(ipAddress: string): void {
    this.blockedIPs.delete(ipAddress);
    this.logSecurityEvent('ip_unblocked', '', ipAddress, '');
  }

  /**
   * Get security metrics
   */
  getSecurityMetrics(): SecurityMetrics {
    return {
      blockedIPs: this.blockedIPs.size,
      suspiciousActivities: this.suspiciousActivity.size,
      rateLimiterStats: this.getRateLimiterStats(),
      lastSecurityEvents: this.getRecentSecurityEvents()
    };
  }

  // Private helper methods

  private initializeRateLimiters(): void {
    const configs = {
      login: { points: 5, duration: 60 * 15 }, // 5 attempts per 15 minutes
      register: { points: 3, duration: 60 * 60 }, // 3 attempts per hour
      passwordReset: { points: 3, duration: 60 * 60 }, // 3 attempts per hour
      general: { points: 100, duration: 60 * 15 }, // 100 requests per 15 minutes
      websocket: { points: 10, duration: 60 } // 10 connections per minute
    };

    Object.entries(configs).forEach(_([type, _config]) => {
      this.rateLimiters.set(type, new RateLimiterMemory({
        points: config.points,
        duration: config.duration,
        blockDuration: config.duration
      }));
    });
  }

  private startSecurityMonitoring(): void {
    // Clean up old suspicious activity records every hour
    setInterval(() => {
      const cutoff = Date.now() - (24 * 60 * 60 * 1000); // 24 hours ago

      for (const [key, tracker] of this.suspiciousActivity) {
        if (tracker.lastActivity < cutoff) {
          this.suspiciousActivity.delete(key);
        }
      }
    }, 60 * 60 * 1000);
  }

  private extractToken(req: any): string | null {
    const authHeader = req.get('Authorization');
    if (authHeader && authHeader.startsWith('Bearer ')) {
      return authHeader.substring(7);
    }

    // Check for token in cookies
    const cookieToken = req.cookies?.access_token;
    if (cookieToken) {
      return cookieToken;
    }

    // Check for token in query parameters (less secure, for WebSocket upgrades)
    const queryToken = req.query?.token;
    if (queryToken && typeof queryToken === 'string') {
      return queryToken;
    }

    return null;
  }

  private extractTokenFromWS(req: any): string | null {
    // Check query parameters
    const url = new URL(req.url, 'http://localhost');
    const token = url.searchParams.get('token');
    if (token) {
      return token;
    }

    // Check headers
    const authHeader = req.headers.authorization;
    if (authHeader && authHeader.startsWith('Bearer ')) {
      return authHeader.substring(7);
    }

    return null;
  }

  private getClientIP(req: any): string {
    return req.ip || 
           req.connection?.remoteAddress || 
           req.socket?.remoteAddress || 
           req.headers['x-forwarded-for']?.split(',')[0] || 
           'unknown';
  }

  private getUserId(req: any): string {
    return req.securityContext?.user?.id || req.user?.id || '';
  }

  private getRateLimitKey(req: any, type: string): string {
    const baseKey = this.getClientIP(req);
    const userId = this.getUserId(req);

    if (userId && type !== 'login') {
      return `${type}:${userId}`;
    }

    return `${type}:${baseKey}`;
  }

  private respondUnauthorized(res: any, message: string): any {
    return res.status(401).json({ 
      error: 'Authentication failed',
      message 
    });
  }

  private async checkSuspiciousActivity(context: SecurityContext, req: any): Promise<void> {
    const key = `${context.user.id}:${context.ipAddress}`;
    const tracker = this.suspiciousActivity.get(key) || {
      userId: context.user.id,
      ipAddress: context.ipAddress,
      lastActivity: Date.now(),
      events: []
    };

    // Update activity
    tracker.lastActivity = Date.now();

    // Check for rapid session switching
    if (this.detectRapidSessionSwitching(tracker, context)) {
      await this.handleSecurityThreat('rapid_session_switching', context.user.id, context.ipAddress, context.userAgent, req);
    }

    this.suspiciousActivity.set(key, tracker);
  }

  private detectSQLInjection(req: any): boolean {
    const sqlPatterns = [
      /(\bUNION\b.*\bSELECT\b)/i,
      /(\bSELECT\b.*\bFROM\b)/i,
      /(\bINSERT\b.*\bINTO\b)/i,
      /(\bDELETE\b.*\bFROM\b)/i,
      /(\bDROP\b.*\bTABLE\b)/i,
      /(;.*--)/,
      /('.*OR.*'.*=.*')/i
    ];

    const checkString = JSON.stringify(req.body) + JSON.stringify(req.query) + JSON.stringify(req.params);
    return sqlPatterns.some(pattern => pattern.test(checkString));
  }

  private detectXSS(req: any): boolean {
    const xssPatterns = [
      /<script\b[^<]*(?:(?!</script>)<[^<]*)*</script>/gi,
      /<iframe\b[^<]*(?:(?!</iframe>)<[^<]*)*</iframe>/gi,
      /javascript:/gi,
      /vbscript:/gi,
      /onload\s*=/gi,
      /onerror\s*=/gi
    ];

    const checkString = JSON.stringify(req.body) + JSON.stringify(req.query);
    return xssPatterns.some(pattern => pattern.test(checkString));
  }

  private detectPathTraversal(req: any): boolean {
    const pathTraversalPatterns = [
      /\.\.//g,
      /\.\./g,
      /%2e%2e%2f/gi,
      /%2e%2e%5c/gi
    ];

    const checkString = req.url + JSON.stringify(req.query) + JSON.stringify(req.params);
    return pathTraversalPatterns.some(pattern => pattern.test(checkString));
  }

  private async detectAnomalousActivity(req: any): Promise<boolean> {
    // Placeholder for machine learning-based anomaly detection
    // Would analyze request patterns, timing, etc.
    return false;
  }

  private detectRapidSessionSwitching(tracker: SuspiciousActivityTracker, context: SecurityContext): boolean {
    // Check if user is switching sessions rapidly (potential account takeover)
    const recentEvents = tracker.events.filter(e => 
      e.timestamp > Date.now() - (5 * 60 * 1000) && // Last 5 minutes
      e.type === 'session_switch'
    );

    return recentEvents.length > 3; // More than 3 session switches in 5 minutes
  }

  private async handleSecurityThreat(
    type: string, 
    userId: string, 
    ipAddress: string, 
    userAgent: string, 
    req: any
  ): Promise<void> {
    this.logSecurityEvent('security_threat_detected', userId, ipAddress, userAgent, {
      type,
      url: req.url,
      method: req.method,
      body: req.body,
      headers: req.headers
    });

    // Auto-block IP for severe threats
    const severeThreats = ['sql_injection', 'xss_attempt', 'path_traversal'];
    if (severeThreats.includes(type)) {
      this.blockIP(ipAddress, `Security threat: ${type}`);
    }

    this.emit('securityThreat', {
      type,
      userId,
      ipAddress,
      userAgent,
      severity: severeThreats.includes(type) ? 'high' : 'medium'
    });
  }

  private validateRequestData(req: any, schema: ValidationSchema): string[] {
    const errors: string[] = [];
    // Placeholder for comprehensive input validation
    // Would use libraries like Joi or express-validator
    return errors;
  }

  private isDevelopment(): boolean {
    return process.env.NODE_ENV === 'development';
  }

  private logSecurityEvent(
    action: string, 
    userId: string, 
    ipAddress: string, 
    userAgent: string, 
    details: Record<string, any> = {}
  ): void {
    const event = {
      action,
      userId,
      ipAddress,
      userAgent,
      details,
      timestamp: new Date()
    };

    this.emit('securityEvent', event);
  }

  private getRateLimiterStats(): Record<string, any> {
    const stats: Record<string, any> = {};

    for (const [type, limiter] of this.rateLimiters) {
      stats[type] = {
        points: limiter.points,
        duration: limiter.duration
      };
    }

    return stats;
  }

  private getRecentSecurityEvents(): any[] {
    // Placeholder - would return recent security events from log
    return [];
  }
}

interface SuspiciousActivityTracker {
  userId: string;
  ipAddress: string;
  lastActivity: number;
  events: Array<{
    type: string;
    timestamp: number;
    details: Record<string, any>;
  }>;
}

interface ValidationSchema {
  body?: Record<string, any>;
  query?: Record<string, any>;
  params?: Record<string, any>;
}

interface SecurityMetrics {
  blockedIPs: number;
  suspiciousActivities: number;
  rateLimiterStats: Record<string, any>;
  lastSecurityEvents: any[];
}

/**
 * Security Utilities and Helpers
 */

import * as crypto from 'crypto';
import { _SecurityContext, _User} from './types';

export class SecurityUtils {
  /**
   * Generate cryptographically secure random string
   */
  static generateSecureToken(length: number = 32): string {
    return crypto.randomBytes(length).toString('hex');
  }

  /**
   * Generate secure random password
   */
  static generateSecurePassword(length: number = 16): string {
    const chars = 'ABCDEFGHJKMNPQRSTUVWXYZabcdefghijkmnpqrstuvwxyz23456789!@#$%^&*';
    let password = '';

    for (let i = 0; i < length; i++) {
      password += chars.charAt(crypto.randomInt(0, chars.length));
    }

    return password;
  }

  /**
   * Hash sensitive data using SHA-256
   */
  static hashData(data: string, salt: string = ''): string {
    const hash = crypto.createHash('sha256');
    hash.update(data + salt);
    return hash.digest('hex');
  }

  /**
   * Generate HMAC signature
   */
  static generateHMAC(data: string, secret: string): string {
    const hmac = crypto.createHmac('sha256', secret);
    hmac.update(data);
    return hmac.digest('hex');
  }

  /**
   * Verify HMAC signature
   */
  static verifyHMAC(data: string, signature: string, secret: string): boolean {
    const expectedSignature = this.generateHMAC(data, secret);
    return crypto.timingSafeEqual(
      Buffer.from(signature, 'hex'),
      Buffer.from(expectedSignature, 'hex')
    );
  }

  /**
   * Encrypt sensitive data using AES-256-GCM
   */
  static encrypt(text: string, key: string): EncryptedData {
    const algorithm = 'aes-256-gcm';
    const iv = crypto.randomBytes(16);
    const keyBuffer = crypto.scryptSync(key, 'salt', 32);

    const cipher = crypto.createCipher(algorithm, keyBuffer);
    cipher.setAAD(Buffer.from('vtt-auth'));

    let encrypted = cipher.update(text, 'utf8', 'hex');
    encrypted += cipher.final('hex');

    const authTag = cipher.getAuthTag();

    return {
      encrypted,
      iv: iv.toString('hex'),
      authTag: authTag.toString('hex')
    };
  }

  /**
   * Decrypt sensitive data
   */
  static decrypt(encryptedData: EncryptedData, key: string): string {
    const algorithm = 'aes-256-gcm';
    const keyBuffer = crypto.scryptSync(key, 'salt', 32);

    const decipher = crypto.createDecipher(algorithm, keyBuffer);
    decipher.setAAD(Buffer.from('vtt-auth'));
    decipher.setAuthTag(Buffer.from(encryptedData.authTag, 'hex'));

    let decrypted = decipher.update(encryptedData.encrypted, 'hex', 'utf8');
    decrypted += decipher.final('utf8');

    return decrypted;
  }

  /**
   * Sanitize user input to prevent XSS
   */
  static sanitizeInput(input: string): string {
    return input
      .replace(/[<>]/g, '') // Remove angle brackets
      .replace(/javascript:/gi, '') // Remove javascript: protocol
      .replace(/vbscript:/gi, '') // Remove vbscript: protocol
      .replace(/on\w+\s*=/gi, '') // Remove event handlers
      .trim();
  }

  /**
   * Validate email format
   */
  static isValidEmail(email: string): boolean {
    const emailRegex = /^[a-zA-Z0-9.!#$%&'*+/=?^`{|}~-]+@[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?(?:\.[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?)*$/;
    return emailRegex.test(email);
  }

  /**
   * Check password strength
   */
  static checkPasswordStrength(password: string): PasswordStrength {
    const checks = {
      length: password.length >= 12,
      uppercase: /[A-Z]/.test(password),
      lowercase: /[a-z]/.test(password),
      numbers: /[0-9]/.test(password),
      symbols: /[^A-Za-z0-9]/.test(password),
      noCommonWords: !this.containsCommonWords(password),
      noPersonalInfo: true // Would check against user data in real implementation
    };

    const score = Object.values(checks).filter(Boolean).length;
    let strength: 'weak' | 'fair' | 'good' | 'strong';

    if (score < 4) strength = 'weak';
    else if (score < 6) strength = 'fair';
    else if (score < 7) strength = 'good';
    else strength = 'strong';

    return {
      score,
      strength,
      checks,
      suggestions: this.getPasswordSuggestions(checks)
    };
  }

  /**
   * Generate secure session token
   */
  static generateSessionToken(): string {
    const timestamp = Date.now().toString(36);
    const randomBytes = crypto.randomBytes(16).toString('hex');
    return `${timestamp}.${randomBytes}`;
  }

  /**
   * Create secure cookie options
   */
  static getSecureCookieOptions(isProduction: boolean = false): CookieOptions {
    const options: CookieOptions = {
      httpOnly: true,
      secure: isProduction,
      sameSite: 'strict',
      maxAge: 24 * 60 * 60 * 1000, // 24 hours
      path: '/'
    };

    if (isProduction) {
      options.domain = '.yourdomain.com';
    }

    return options;
  }

  /**
   * Rate limit key generator
   */
  static generateRateLimitKey(identifier: string, action: string): string {
    const hash = this.hashData(`${identifier}:${action}`);
    return `rate_limit:${hash}`;
  }

  /**
   * Obfuscate sensitive data for logging
   */
  static obfuscateForLogging(data: any): any {
    if (typeof data !== 'object' || data === null) {
      return data;
    }

    const sensitiveFields = [
      'password', 'token', 'secret', 'key', 'authorization',
      'credit_card', 'ssn', 'social_security', 'passport'
    ];

    const obfuscated = { ...data };

    for (const [key, value] of Object.entries(obfuscated)) {
      const lowercaseKey = key.toLowerCase();

      if (sensitiveFields.some(field => lowercaseKey.includes(field))) {
        if (typeof value === 'string') {
          obfuscated[key] = '*'.repeat(Math.min(value.length, 8));
        } else {
          obfuscated[key] = '[REDACTED]';
        }
      } else if (typeof value === 'object') {
        obfuscated[key] = this.obfuscateForLogging(value);
      }
    }

    return obfuscated;
  }

  /**
   * Generate device fingerprint
   */
  static generateDeviceFingerprint(userAgent: string, acceptLanguage: string = '', timezone: string = ''): string {
    const fingerprint = `${userAgent}|${acceptLanguage}|${timezone}`;
    return this.hashData(fingerprint);
  }

  /**
   * Check if request comes from suspicious source
   */
  static isSuspiciousRequest(req: any): boolean {
    const userAgent = req.get('User-Agent') || '';
    const suspiciousPatterns = [
      /bot/i, /crawler/i, /spider/i, /scraper/i,
      /curl/i, /wget/i, /python/i, /php/i
    ];

    // Check user agent
    if (suspiciousPatterns.some(pattern => pattern.test(userAgent))) {
      return true;
    }

    // Check for missing common headers
    const commonHeaders = ['accept', 'accept-language', 'accept-encoding'];
    const missingHeaders = commonHeaders.filter(header => !req.get(header));

    if (missingHeaders.length > 1) {
      return true;
    }

    return false;
  }

  /**
   * Generate API key
   */
  static generateAPIKey(prefix: string = 'vtt'): string {
    const keyPart = crypto.randomBytes(20).toString('hex');
    const checksum = this.hashData(keyPart).substring(0, 8);
    return `${prefix}_${keyPart}${checksum}`;
  }

  /**
   * Validate API key format
   */
  static validateAPIKey(apiKey: string, expectedPrefix: string = 'vtt'): boolean {
    if (!apiKey.startsWith(`${expectedPrefix}`)) {
      return false;
    }

    const keyPart = apiKey.substring(expectedPrefix.length + 1, apiKey.length - 8);
    const providedChecksum = apiKey.slice(-8);
    const expectedChecksum = this.hashData(keyPart).substring(0, 8);

    return providedChecksum === expectedChecksum;
  }

  /**
   * Secure comparison to prevent timing attacks
   */
  static secureCompare(a: string, b: string): boolean {
    if (a.length !== b.length) {
      return false;
    }

    return crypto.timingSafeEqual(
      Buffer.from(a, 'utf8'),
      Buffer.from(b, 'utf8')
    );
  }

  /**
   * Generate CSRF token
   */
  static generateCSRFToken(sessionId: string, secret: string): string {
    const timestamp = Date.now().toString();
    const data = `${sessionId}:${timestamp}`;
    const signature = this.generateHMAC(data, secret);
    return Buffer.from(`${data}:${signature}`).toString('base64');
  }

  /**
   * Validate CSRF token
   */
  static validateCSRFToken(token: string, sessionId: string, secret: string, maxAge: number = 3600000): boolean {
    try {
      const decoded = Buffer.from(token, 'base64').toString('utf8');
      const [receivedSessionId, timestamp, signature] = decoded.split(':');

      if (receivedSessionId !== sessionId || !timestamp || !signature) {
        return false;
      }

      const tokenAge = Date.now() - parseInt(timestamp);
      if (tokenAge > maxAge) {
        return false;
      }

      const data = `${receivedSessionId}:${timestamp}`;
      const expectedSignature = this.generateHMAC(data, secret);

      return this.secureCompare(signature, expectedSignature);
    } catch (_error) {
      return false;
    }
  }

  // Private helper methods

  private static containsCommonWords(password: string): boolean {
    const commonWords = [
      'password', '123456', 'qwerty', 'abc123', 'admin',
      'login', 'welcome', 'monkey', 'dragon', 'master'
    ];

    const lowercasePassword = password.toLowerCase();
    return commonWords.some(word => lowercasePassword.includes(word));
  }

  private static getPasswordSuggestions(checks: Record<string, boolean>): string[] {
    const suggestions: string[] = [];

    if (!checks.length) suggestions.push('Use at least 12 characters');
    if (!checks.uppercase) suggestions.push('Include uppercase letters');
    if (!checks.lowercase) suggestions.push('Include lowercase letters');
    if (!checks.numbers) suggestions.push('Include numbers');
    if (!checks.symbols) suggestions.push('Include special characters');
    if (!checks.noCommonWords) suggestions.push('Avoid common words');

    return suggestions;
  }
}

export interface EncryptedData {
  encrypted: string;
  iv: string;
  authTag: string;
}

export interface PasswordStrength {
  score: number;
  strength: 'weak' | 'fair' | 'good' | 'strong';
  checks: Record<string, boolean>;
  suggestions: string[];
}

export interface CookieOptions {
  httpOnly: boolean;
  secure: boolean;
  sameSite: 'strict' | 'lax' | 'none';
  maxAge: number;
  path: string;
  domain?: string | undefined;
}

/**
 * Synchronization Service
 * Handles real-time synchronization between clients and server
 */

import { StateManager, Operation, StateSnapshot } from './StateManager';

export interface SyncConfig {
  syncInterval: number; // ms
  batchSize: number;
  retryAttempts: number;
  retryDelay: number;
  heartbeatInterval: number;
}

export interface ConnectionState {
  connected: boolean;
  lastSync: number;
  lastHeartbeat: number;
  reconnectAttempts: number;
  latency: number;
}

export interface SyncMessage {
  type: 'operations' | 'snapshot' | 'heartbeat' | 'ack';
  timestamp: number;
  userId: string;
  data: any;
  messageId?: string;
}

export class SynchronizationService {
  private stateManager: StateManager;
  private config: SyncConfig;
  private connectionState: ConnectionState;
  private sendQueue: SyncMessage[] = [];
  private ackQueue: Map<string, { resolve: (_...args: any[]) => any; reject: (_...args: any[]) => any; timeout: NodeJS.Timeout }> = new Map();
  private syncInterval: NodeJS.Timeout | null = null;
  private heartbeatInterval: NodeJS.Timeout | null = null;
  private eventListeners: Array<(_event: SyncEvent) => void> = [];

  // Abstract transport layer - implement with Socket.IO, WebSocket, etc.
  private transport: SyncTransport;

  constructor(
    stateManager: StateManager,
    transport: SyncTransport,
    config: Partial<SyncConfig> = {}
  ) {
    this.stateManager = stateManager;
    this.transport = transport;
    this.config = {
      syncInterval: 100,
      batchSize: 50,
      retryAttempts: 3,
      retryDelay: 1000,
      heartbeatInterval: 30000,
      ...config
    };

    this.connectionState = {
      connected: false,
      lastSync: 0,
      lastHeartbeat: 0,
      reconnectAttempts: 0,
      latency: 0
    };

    this.setupTransportListeners();
    this.setupStateManagerListeners();
  }

  private setupTransportListeners(): void {
    this.transport.onConnect(() => {
      this.connectionState.connected = true;
      this.connectionState.reconnectAttempts = 0;
      this.startSyncLoop();
      this.startHeartbeat();
      this.emit({ type: 'connected', data: Record<string, unknown>});
    });

    this.transport.onDisconnect(() => {
      this.connectionState.connected = false;
      this.stopSyncLoop();
      this.stopHeartbeat();
      this.emit({ type: 'disconnected', data: Record<string, unknown>});
      this.attemptReconnect();
    });

    this.transport.onMessage((message: SyncMessage) => {
      this.handleMessage(message);
    });

    this.transport.onError((_error: Error) => {
      this.emit({ type: 'error', data: { error } });
    });
  }

  private setupStateManagerListeners(): void {
    this.stateManager.addChangeListener((change) => {
      if (change.type === 'operation-applied') {
        this.queueOperation(change.data.operation);
      }
    });
  }

  private queueOperation(operation: Operation): void {
    const message: SyncMessage = {
      type: 'operations',
      timestamp: Date.now(),
      userId: operation.userId,
      data: { operations: [operation] },
      messageId: this.generateMessageId()
    };

    this.sendQueue.push(message);
  }

  private startSyncLoop(): void {
    if (this.syncInterval) return;

    this.syncInterval = setInterval(() => {
      this.processSendQueue();
    }, this.config.syncInterval);
  }

  private stopSyncLoop(): void {
    if (this.syncInterval) {
      clearInterval(this.syncInterval);
      this.syncInterval = null;
    }
  }

  private startHeartbeat(): void {
    if (this.heartbeatInterval) return;

    this.heartbeatInterval = setInterval(() => {
      this.sendHeartbeat();
    }, this.config.heartbeatInterval);
  }

  private stopHeartbeat(): void {
    if (this.heartbeatInterval) {
      clearInterval(this.heartbeatInterval);
      this.heartbeatInterval = null;
    }
  }

  private async processSendQueue(): Promise<void> {
    if (!this.connectionState.connected || this.sendQueue.length === 0) {
      return;
    }

    // Batch operations
    const batch = this.sendQueue.splice(0, this.config.batchSize);

    if (batch.length === 1) {
      const message = batch[0];
      if (message) {
        await this.sendMessage(message);
      }
    } else if (batch.length > 1) {
      // Combine multiple operations into one message
      const operations = batch.flatMap(msg => msg.data.operations || []);
      const firstMessage = batch[0];
      if (!firstMessage) return;

      const combinedMessage: SyncMessage = {
        type: 'operations',
        timestamp: Date.now(),
        userId: firstMessage.userId,
        data: { operations },
        messageId: this.generateMessageId()
      };

      await this.sendMessage(combinedMessage);
    }
  }

  private async sendMessage(message: SyncMessage): Promise<void> {
    if (!this.connectionState.connected) {
      // Re-queue message for later
      this.sendQueue.unshift(message);
      return;
    }

    try {
      const startTime = Date.now();
      await this.transport.send(message);

      if (message.messageId) {
        // Wait for acknowledgment
        await this.waitForAck(message.messageId);
      }

      this.connectionState.latency = Date.now() - startTime;
      this.connectionState.lastSync = Date.now();

    } catch (error) {
      console.error('Failed to send message:', error);
      const errorObj = error instanceof Error ? error : new Error(String(error));
      this.emit({ type: 'send-error', data: { message, error: errorObj } });

      // Re-queue for retry
      this.sendQueue.unshift(message);
    }
  }

  private waitForAck(messageId: string): Promise<void> {
    return new Promise((_resolve, __reject) => {
      const timeout = setTimeout(() => {
        this.ackQueue.delete(messageId);
        reject(new Error(`Message ${messageId} acknowledgment timeout`));
      }, 5000);

      this.ackQueue.set(messageId, { resolve, reject, timeout });
    });
  }

  private sendHeartbeat(): void {
    const heartbeatMessage: SyncMessage = {
      type: 'heartbeat',
      timestamp: Date.now(),
      userId: this.stateManager['userId'], // Access private field
      data: {
        latency: this.connectionState.latency,
        lastSync: this.connectionState.lastSync
      }
    };

    this.transport.send(heartbeatMessage).catch(error => {
      console.error('Heartbeat failed:', error);
    });
  }

  private async handleMessage(message: SyncMessage): Promise<void> {
    switch (message.type) {
      case 'operations':
        await this.handleOperationsMessage(message);
        break;

      case 'snapshot':
        await this.handleSnapshotMessage(message);
        break;

      case 'heartbeat':
        await this.handleHeartbeatMessage(message);
        break;

      case 'ack':
        this.handleAckMessage(message);
        break;
    }

    // Send acknowledgment for messages that request it
    if (message.messageId && message.type !== 'ack') {
      const ackMessage: SyncMessage = {
        type: 'ack',
        timestamp: Date.now(),
        userId: this.stateManager['userId'],
        data: { messageId: message.messageId }
      };

      await this.transport.send(ackMessage);
    }
  }

  private async handleOperationsMessage(message: SyncMessage): Promise<void> {
    const operations: Operation[] = message.data.operations || [];

    for (const operation of operations) {
      this.stateManager.applyRemoteOperation(operation);
    }

    this.emit({ 
      type: 'operations-received', 
      data: { operations, fromUser: message.userId } 
    });
  }

  private async handleSnapshotMessage(message: SyncMessage): Promise<void> {
    const snapshot: StateSnapshot = message.data.snapshot;
    this.stateManager.loadSnapshot(snapshot);

    this.emit({ 
      type: 'snapshot-received', 
      data: { snapshot, fromUser: message.userId } 
    });
  }

  private async handleHeartbeatMessage(message: SyncMessage): Promise<void> {
    this.connectionState.lastHeartbeat = message.timestamp;

    // Respond with our own heartbeat
    const responseMessage: SyncMessage = {
      type: 'heartbeat',
      timestamp: Date.now(),
      userId: this.stateManager['userId'],
      data: {
        latency: this.connectionState.latency,
        lastSync: this.connectionState.lastSync,
        responseToHeartbeat: message.timestamp
      }
    };

    await this.transport.send(responseMessage);
  }

  private handleAckMessage(message: SyncMessage): void {
    const messageId = message.data.messageId;
    const ackEntry = this.ackQueue.get(messageId);

    if (ackEntry) {
      clearTimeout(ackEntry.timeout);
      ackEntry.resolve();
      this.ackQueue.delete(messageId);
    }
  }

  private async attemptReconnect(): Promise<void> {
    if (this.connectionState.reconnectAttempts >= this.config.retryAttempts) {
      this.emit({ type: 'max-reconnect-attempts', data: Record<string, unknown>});
      return;
    }

    this.connectionState.reconnectAttempts++;

    await new Promise(resolve => 
      setTimeout(resolve, this.config.retryDelay * this.connectionState.reconnectAttempts)
    );

    try {
      await this.transport.connect();
    } catch (error) {
      console.error('Reconnection failed:', error);
      this.attemptReconnect();
    }
  }

  private generateMessageId(): string {
    return `msg-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
  }

  /**
   * Connect to remote sync service
   */
  async connect(): Promise<void> {
    await this.transport.connect();
  }

  /**
   * Disconnect from remote sync service
   */
  async disconnect(): Promise<void> {
    this.stopSyncLoop();
    this.stopHeartbeat();
    await this.transport.disconnect();
  }

  /**
   * Force synchronization
   */
  async forceSyncAll(): Promise<void> {
    // Send current state snapshot
    const snapshot = this.stateManager.createSnapshot();
    const message: SyncMessage = {
      type: 'snapshot',
      timestamp: Date.now(),
      userId: this.stateManager['userId'],
      data: { snapshot }
    };

    await this.sendMessage(message);
  }

  /**
   * Request full state from server
   */
  async requestFullSync(): Promise<void> {
    const message: SyncMessage = {
      type: 'operations',
      timestamp: Date.now(),
      userId: this.stateManager['userId'],
      data: { requestFullSync: true },
      messageId: this.generateMessageId()
    };

    await this.sendMessage(message);
  }

  /**
   * Get connection status
   */
  getConnectionState(): ConnectionState {
    return { ...this.connectionState };
  }

  /**
   * Get sync statistics
   */
  getSyncStats(): {
    queueSize: number;
    pendingAcks: number;
    averageLatency: number;
    syncRate: number;
  } {
    return {
      queueSize: this.sendQueue.length,
      pendingAcks: this.ackQueue.size,
      averageLatency: this.connectionState.latency,
      syncRate: 1000 / this.config.syncInterval
    };
  }

  // Event System
  addEventListener(_listener: (event: SyncEvent) => void): void {
    this.eventListeners.push(_listener);
  }

  removeEventListener(_listener: (event: SyncEvent) => void): void {
    const index = this.eventListeners.indexOf(_listener);
    if (index > -1) {
      this.eventListeners.splice(index, 1);
    }
  }

  private emit(event: SyncEvent): void {
    this.eventListeners.forEach(listener => {
      try {
        listener(event);
      } catch (error) {
        console.error('Sync event listener error:', error);
      }
    });
  }
}

// Transport Interface (implement with Socket.IO, WebSocket, etc.)
export interface SyncTransport {
  connect(): Promise<void>;
  disconnect(): Promise<void>;
  send(message: SyncMessage): Promise<void>;
  onConnect(_callback: () => void): void;
  onDisconnect(_callback: () => void): void;
  onMessage(_callback: (message: SyncMessage) => void): void;
  onError(_callback: (error: Error) => void): void;
}

// Event Types
export type SyncEvent =
  | { type: 'connected'; data: Record<string, unknown>}
  | { type: 'disconnected'; data: Record<string, unknown>}
  | { type: 'operations-received'; data: { operations: Operation[]; fromUser: string } }
  | { type: 'snapshot-received'; data: { snapshot: StateSnapshot; fromUser: string } }
  | { type: 'send-error'; data: { message: SyncMessage; error: Error } }
  | { type: 'error'; data: { error: Error } }
  | { type: 'max-reconnect-attempts'; data: Record<string, unknown>};

/**
 * Initiative Tracker
 * Visual and functional component for managing combat initiative order
 */

import { _Combatant, CombatState } from './CombatManager';

export interface InitiativeEntry {
  id: string;
  combatantId: string;
  name: string;
  initiative: number;
  isActive: boolean;
  isVisible: boolean;
  isPlayer: boolean;
  avatar?: string;
  conditions: string[];
  hitPoints: {
    current: number;
    max: number;
    temp: number;
  };
  actions: {
    action: boolean;
    bonusAction: boolean;
    reaction: boolean;
    movement: number;
    maxMovement: number;
  };
}

export interface InitiativeTrackerSettings {
  showHealthBars: boolean;
  showConditions: boolean;
  showActions: boolean;
  showMovement: boolean;
  allowReordering: boolean;
  showHiddenCombatants: boolean;
  colorCoding: boolean;
}

export class InitiativeTracker {
  private entries: InitiativeEntry[] = [];
  private currentTurn = 0;
  private settings: InitiativeTrackerSettings;
  private changeListeners: Array<(_event: InitiativeTrackerEvent) => void> = [];

  constructor(settings: Partial<InitiativeTrackerSettings> = {}) {
    this.settings = {
      showHealthBars: true,
      showConditions: true,
      showActions: true,
      showMovement: true,
      allowReordering: true,
      showHiddenCombatants: false,
      colorCoding: true,
      ...settings
    };
  }

  /**
   * Update tracker from combat state
   */
  updateFromCombatState(combatState: CombatState): void {
    this.entries = combatState.combatants.map((_combatant, __index) => ({
      id: `entry-${combatant.id}`,
      combatantId: combatant.id,
      name: combatant.name,
      initiative: combatant.initiative,
      isActive: combatant.isActive,
      isVisible: combatant.isVisible,
      isPlayer: combatant.type === 'pc',
      conditions: [...combatant.conditions],
      hitPoints: {
        current: combatant.currentHitPoints,
        max: combatant.maxHitPoints,
        temp: combatant.temporaryHitPoints
      },
      actions: { ...combatant.actions }
    }));

    this.currentTurn = combatState.currentTurn;

    this.emitChange({
      type: 'tracker-updated',
      data: { entries: this.getVisibleEntries() }
    });
  }

  /**
   * Get entries visible to current user
   */
  getVisibleEntries(): InitiativeEntry[] {
    return this.entries.filter(entry => 
      entry.isVisible || this.settings.showHiddenCombatants
    );
  }

  /**
   * Get current active entry
   */
  getCurrentEntry(): InitiativeEntry | null {
    return this.entries.find(entry => entry.isActive) || null;
  }

  /**
   * Reorder initiative entries
   */
  reorderEntry(entryId: string, newPosition: number): boolean {
    if (!this.settings.allowReordering) return false;

    const entryIndex = this.entries.findIndex(e => e.id === entryId);
    if (entryIndex === -1 || newPosition < 0 || newPosition >= this.entries.length) {
      return false;
    }

    const [entry] = this.entries.splice(entryIndex, 1);
    if (entry) {
      this.entries.splice(newPosition, 0, entry);
    }

    this.emitChange({
      type: 'order-changed',
      data: { entryId, oldPosition: entryIndex, newPosition }
    });

    return true;
  }

  /**
   * Toggle entry visibility
   */
  toggleVisibility(entryId: string): boolean {
    const entry = this.entries.find(e => e.id === entryId);
    if (!entry) return false;

    entry.isVisible = !entry.isVisible;

    this.emitChange({
      type: 'visibility-changed',
      data: { entryId, visible: entry.isVisible }
    });

    return true;
  }

  /**
   * Update entry health display
   */
  updateHealth(entryId: string, current: number, max: number, temp: number = 0): void {
    const entry = this.entries.find(e => e.id === entryId);
    if (!entry) return;

    entry.hitPoints = { current, max, temp };

    this.emitChange({
      type: 'health-updated',
      data: { entryId, hitPoints: entry.hitPoints }
    });
  }

  /**
   * Update entry conditions
   */
  updateConditions(entryId: string, conditions: string[]): void {
    const entry = this.entries.find(e => e.id === entryId);
    if (!entry) return;

    entry.conditions = [...conditions];

    this.emitChange({
      type: 'conditions-updated',
      data: { entryId, conditions: entry.conditions }
    });
  }

  /**
   * Update entry actions
   */
  updateActions(entryId: string, actions: InitiativeEntry['actions']): void {
    const entry = this.entries.find(e => e.id === entryId);
    if (!entry) return;

    entry.actions = { ...actions };

    this.emitChange({
      type: 'actions-updated',
      data: { entryId, actions: entry.actions }
    });
  }

  /**
   * Get entry health percentage
   */
  getHealthPercentage(entryId: string): number {
    const entry = this.entries.find(e => e.id === entryId);
    if (!entry || entry.hitPoints.max === 0) return 0;

    return Math.max(0, (entry.hitPoints.current / entry.hitPoints.max) * 100);
  }

  /**
   * Get entry health status
   */
  getHealthStatus(entryId: string): 'healthy' | 'injured' | 'bloodied' | 'critical' | 'unconscious' | 'dead' {
    const percentage = this.getHealthPercentage(entryId);
    const entry = this.entries.find(e => e.id === entryId);

    if (!entry) return 'dead';
    if (entry.hitPoints.current <= 0) return entry.hitPoints.current < 0 ? 'dead' : 'unconscious';
    if (percentage <= 10) return 'critical';
    if (percentage <= 50) return 'bloodied';
    if (percentage < 100) return 'injured';
    return 'healthy';
  }

  /**
   * Get condition severity
   */
  getConditionSeverity(entryId: string): 'none' | 'minor' | 'major' | 'severe' {
    const entry = this.entries.find(e => e.id === entryId);
    if (!entry || entry.conditions.length === 0) return 'none';

    const severeConditions = ['unconscious', 'paralyzed', 'petrified', 'stunned'];
    const majorConditions = ['blinded', 'charmed', 'frightened', 'incapacitated', 'restrained'];

    if (entry.conditions.some(c => severeConditions.includes(c.toLowerCase()))) return 'severe';
    if (entry.conditions.some(c => majorConditions.includes(c.toLowerCase()))) return 'major';
    return 'minor';
  }

  /**
   * Calculate initiative statistics
   */
  getInitiativeStats(): {
    highest: number;
    lowest: number;
    average: number;
    playerAverage: number;
    npcAverage: number;
  } {
    if (this.entries.length === 0) {
      return { highest: 0, lowest: 0, average: 0, playerAverage: 0, npcAverage: 0 };
    }

    const initiatives = this.entries.map(e => e.initiative);
    const playerInitiatives = this.entries.filter(e => e.isPlayer).map(e => e.initiative);
    const npcInitiatives = this.entries.filter(e => !e.isPlayer).map(e => e.initiative);

    return {
      highest: Math.max(...initiatives),
      lowest: Math.min(...initiatives),
      average: initiatives.reduce((_sum, __init) => sum + init, 0) / initiatives.length,
      playerAverage: playerInitiatives.length > 0 
        ? playerInitiatives.reduce((_sum, __init) => sum + init, 0) / playerInitiatives.length 
        : 0,
      npcAverage: npcInitiatives.length > 0 
        ? npcInitiatives.reduce((_sum, __init) => sum + init, 0) / npcInitiatives.length 
        : 0
    };
  }

  /**
   * Generate turn summary
   */
  getTurnSummary(): {
    currentEntry: InitiativeEntry | null;
    nextEntry: InitiativeEntry | null;
    roundNumber: number;
    totalCombatants: number;
    activeCombatants: number;
    defeatedCombatants: number;
  } {
    const activeEntries = this.entries.filter(e => this.getHealthStatus(e.id) !== 'dead');
    const defeatedEntries = this.entries.filter(e => this.getHealthStatus(e.id) === 'dead');

    const currentEntry = this.getCurrentEntry();
    let nextEntry: InitiativeEntry | null = null;

    if (currentEntry) {
      const currentIndex = this.entries.findIndex(e => e.isActive);
      const nextIndex = (currentIndex + 1) % this.entries.length;
      nextEntry = this.entries[nextIndex] || null;
    }

    return {
      currentEntry,
      nextEntry,
      roundNumber: Math.floor(this.currentTurn / this.entries.length) + 1,
      totalCombatants: this.entries.length,
      activeCombatants: activeEntries.length,
      defeatedCombatants: defeatedEntries.length
    };
  }

  /**
   * Export initiative order
   */
  exportInitiativeOrder(): {
    entries: InitiativeEntry[];
    settings: InitiativeTrackerSettings;
    timestamp: number;
  } {
    return {
      entries: [...this.entries],
      settings: { ...this.settings },
      timestamp: Date.now()
    };
  }

  /**
   * Import initiative order
   */
  importInitiativeOrder(data: {
    entries: InitiativeEntry[];
    settings?: Partial<InitiativeTrackerSettings>;
  }): void {
    this.entries = [...data.entries];

    if (data.settings) {
      this.settings = { ...this.settings, ...data.settings };
    }

    this.emitChange({
      type: 'tracker-imported',
      data: { entries: this.getVisibleEntries() }
    });
  }

  /**
   * Clear all entries
   */
  clear(): void {
    this.entries = [];
    this.currentTurn = 0;

    this.emitChange({
      type: 'tracker-cleared',
      data: Record<string, any>
    });
  }

  /**
   * Update settings
   */
  updateSettings(newSettings: Partial<InitiativeTrackerSettings>): void {
    this.settings = { ...this.settings, ...newSettings };

    this.emitChange({
      type: 'settings-updated',
      data: { settings: this.settings }
    });
  }

  /**
   * Get settings
   */
  getSettings(): InitiativeTrackerSettings {
    return { ...this.settings };
  }

  // Event system
  addEventListener(_listener: (event: InitiativeTrackerEvent) => void): void {
    this.changeListeners.push(listener);
  }

  removeEventListener(_listener: (event: InitiativeTrackerEvent) => void): void {
    const index = this.changeListeners.indexOf(listener);
    if (index > -1) {
      this.changeListeners.splice(index, 1);
    }
  }

  private emitChange(event: InitiativeTrackerEvent): void {
    this.changeListeners.forEach(listener => {
      try {
        listener(event);
      } catch (error) {
        console.error('Initiative tracker event listener error:', error);
      }
    });
  }
}

// Event types
export type InitiativeTrackerEvent =
  | { type: 'tracker-updated'; data: { entries: InitiativeEntry[] } }
  | { type: 'order-changed'; data: { entryId: string; oldPosition: number; newPosition: number } }
  | { type: 'visibility-changed'; data: { entryId: string; visible: boolean } }
  | { type: 'health-updated'; data: { entryId: string; hitPoints: InitiativeEntry['hitPoints'] } }
  | { type: 'conditions-updated'; data: { entryId: string; conditions: string[] } }
  | { type: 'actions-updated'; data: { entryId: string; actions: InitiativeEntry['actions'] } }
  | { type: 'settings-updated'; data: { settings: InitiativeTrackerSettings } }
  | { type: 'tracker-cleared'; data: Record<string, unknown>}
  | { type: 'tracker-imported'; data: { entries: InitiativeEntry[] } };

