   */
  createCollection(data: Omit<SpellCollection, 'id' | 'createdAt' | 'updatedAt' | 'version'>): SpellCollection {
    const collection: SpellCollection = {
      ...data,
      id: this.generateId(),
      createdAt: new Date(),
      updatedAt: new Date(),
      version: '1.0.0'
    };

    this.collections.set(collection.id, collection);
    return collection;
  }

