   * Update a collection
   */
  updateCollection(id: string, updates: Partial<Omit<SpellCollection, 'id' | 'createdAt' | 'version'>>): SpellCollection | undefined {
    const collection = this.collections.get(id);
    if (!collection) return undefined;

    const updated = {
      ...collection,
      ...updates,
      updatedAt: new Date()
    };

    this.collections.set(id, updated);
    return updated;
  }

  /**
   * Delete a collection
   */
  deleteCollection(id: string): boolean {
    return this.collections.delete(id);
  }

  /**
   * Add spells to a collection
   */
  addSpellsToCollection(collectionId: string, spellIds: string[]): boolean {
    const collection = this.collections.get(collectionId);
    if (!collection) return false;

    const uniqueSpells = new Set([...collection.spellIds, ...spellIds]);
    collection.spellIds = Array.from(uniqueSpells);
    collection.updatedAt = new Date();

    return true;
  }

  /**
   * Remove spells from a collection
   */
  removeSpellsFromCollection(collectionId: string, spellIds: string[]): boolean {
    const collection = this.collections.get(collectionId);
    if (!collection) return false;

    collection.spellIds = collection.spellIds.filter(id => !spellIds.includes(id));
    collection.updatedAt = new Date();

    return true;
  }

  /**
   * Export collection to various formats
   */
  exportCollection(collectionId: string, options: ExportOptions): string {
    const collection = this.collections.get(collectionId);
    if (!collection) throw new Error('Collection not found');

    const spells = this.getSpellsForCollection(collection);
    const exportData: ExportedSpellCollection = {
      ...collection,
      spells: options.includeSpellData ? spells : []
    };

    switch (options.format) {
      case 'json':
        return this.exportAsJson(exportData, options.minify);
      case 'csv':
        return this.exportAsCsv(spells);
      case 'txt':
        return this.exportAsText(collection, spells);
      case 'foundry':
        return this.exportAsFoundryVTT(collection, spells);
      case 'roll20':
        return this.exportAsRoll20(collection, spells);
      case 'dndbeyond':
        return this.exportAsDnDBeyond(collection, spells);
      default:
        throw new Error(`Unsupported export format: ${options.format}`);
    }
  }

  /**
   * Import collection from various formats
   */
  importCollection(data: string, format: ExportOptions['format'], options: ImportOptions = {}): SpellCollection {
    let parsedData: ExportedSpellCollection;

    try {
      switch (format) {
        case 'json':
          parsedData = this.parseJsonImport(data);
          break;
        case 'csv':
          parsedData = this.parseCsvImport(data);
          break;
        case 'foundry':
          parsedData = this.parseFoundryImport(data);
          break;
        default:
          throw new Error(`Unsupported import format: ${format}`);
      }
    } catch (error) {
      throw new Error(`Failed to parse import data: ${error}`);
    }

    // Validate spells if requested
    if (options.validateSpells) {
      const validSpellIds = this.validateSpellIds(parsedData.spellIds);
      if (validSpellIds.length !== parsedData.spellIds.length) {
        const invalidIds = parsedData.spellIds.filter(id => !validSpellIds.includes(id));
        console.warn(`Invalid spell IDs found: ${invalidIds.join(', ')}`);
        parsedData.spellIds = validSpellIds;
      }
    }

    // Check if collection already exists
    if (options.updateExisting && this.collections.has(parsedData.id)) {
      if (options.merge) {
        // Merge spell lists
        const existing = this.collections.get(parsedData.id)!;
        const mergedSpellIds = new Set([...existing.spellIds, ...parsedData.spellIds]);
        parsedData.spellIds = Array.from(mergedSpellIds);
      }

      return this.updateCollection(parsedData.id, {
        name: parsedData.name,
        description: parsedData.description || undefined,
        spellIds: parsedData.spellIds,
        characterClass: parsedData.characterClass || undefined,
        characterLevel: parsedData.characterLevel || undefined,
        tags: parsedData.tags
      })!;
    }

    // Create new collection
    const { _spells,  ...collectionData  } = parsedData;
    return this.createCollection({
      ...collectionData,
      id: options.updateExisting ? parsedData.id : undefined
    } as any);
  }

  /**
   * Create collection from search results
   */
  createCollectionFromSearch(
    name: string,
    searchCriteria: any,
    options?: { description?: string; tags?: string[] }
  ): SpellCollection {
    const results = spellSearchEngine.search(searchCriteria);
    const spellIds = results.spells.map(spell => spell.id);

    return this.createCollection({
      name,
      description: options?.description || `Collection created from search: ${JSON.stringify(searchCriteria)}`,
      spellIds,
      tags: options?.tags || ['search-generated'],
      characterClass: searchCriteria.classes?.[0],
      characterLevel: searchCriteria.level?.[0]
    });
  }

  /**
   * Create collection for character class and level
   */
  createClassCollection(className: string, level: number): SpellCollection {
    const spells = spellSearchEngine.getSpellsForClass(className, level);
    const spellIds = spells.map(spell => spell.id);

    return this.createCollection({
      name: `${className} Spells (Level ${level})`,
      description: `All spells available to a level ${level} ${className}`,
      spellIds,
      characterClass: className,
      characterLevel: level,
      tags: ['class-generated', className.toLowerCase()]
    });
  }

  /**
   * Duplicate a collection
   */
  duplicateCollection(collectionId: string, newName?: string): SpellCollection | undefined {
    const original = this.collections.get(collectionId);
    if (!original) return undefined;

    const duplicate = this.createCollection({
      ...original,
      name: newName || `${original.name} (Copy)`,
      tags: [...original.tags, 'duplicate']
    });

    return duplicate;
  }

  /**
   * Get spell statistics for a collection
   */
  getCollectionStats(collectionId: string): {
    totalSpells: number;
    byLevel: Record<number, number>;
    bySchool: Record<string, number>;
    byClass: Record<string, number>;
    concentrationSpells: number;
    ritualSpells: number;
    averageLevel: number;
  } {
    const collection = this.collections.get(collectionId);
    if (!collection) throw new Error('Collection not found');

    const spells = this.getSpellsForCollection(collection);

    const stats = {
      totalSpells: spells.length,
      byLevel: {} as Record<number, number>,
      bySchool: {} as Record<string, number>,
      byClass: {} as Record<string, number>,
      concentrationSpells: 0,
      ritualSpells: 0,
      averageLevel: 0
    };

    let totalLevels = 0;

    spells.forEach(spell => {
      // Level distribution
      stats.byLevel[spell.level] = (stats.byLevel[spell.level] || 0) + 1;
      totalLevels += spell.level;

      // School distribution
      stats.bySchool[spell.school] = (stats.bySchool[spell.school] || 0) + 1;

      // Class distribution
      spell.classes.forEach(cls => {
        stats.byClass[cls] = (stats.byClass[cls] || 0) + 1;
      });

      // Special properties
      if (spell.concentration) stats.concentrationSpells++;
    });

    spells.forEach(spell => {
      if (spell.ritual) stats.ritualSpells++;
    });

    stats.averageLevel = spells.length > 0 ? totalLevels / spells.length : 0;

    return stats;
  }

  private getSpellsForCollection(collection: SpellCollection): SRDSpell[] {
    return spellSearchEngine.search({_customFilter: (spell) => collection.spellIds.includes(spell.id)}).spells;
  }

  private generateId(): string {
    return `collection_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  private validateSpellIds(spellIds: string[]): string[] {
    const allSpells = spellSearchEngine.search({});
    const validIds = new Set(allSpells.spells.map(s => s.id));
    return spellIds.filter(id => validIds.has(id));
  }

  private exportAsJson(data: ExportedSpellCollection, minify?: boolean): string {
    return JSON.stringify(data, null, minify ? 0 : 2);
  }

  private exportAsCsv(spells: SRDSpell[]): string {
    const headers = ['Name', 'Level', 'School', 'Casting Time', 'Range', 'Duration', 'Components', 'Classes', 'Description'];
    const rows = spells.map(spell => [
      spell.name,
      spell.level.toString(),
      spell.school,
      spell.castingTime,
      spell.range,
      spell.duration,
      spell.components.join(', '),
      spell.classes.join(', '),
      spell.description.replace(/"/g, '""')
    ]);

    return [headers, ...rows]
      .map(row => row.map(cell => `"${cell}"`).join(','))
      .join('\n');
  }

  private exportAsText(collection: SpellCollection, spells: SRDSpell[]): string {
    let output = `# ${collection.name}\n\n`;

    if (collection.description) {
      output += `${collection.description}\n\n`;
    }

    output += `**Total Spells:** ${spells.length}\n`;
    output += `**Created:** ${collection.createdAt.toLocaleDateString()}\n\n`;

    // Group by level
    const byLevel = spells.reduce((_acc, _spell) => {
      const level = spell.level === 0 ? 'Cantrips' : `Level ${spell.level}`;
      if (!acc[level]) acc[level] = [];
      acc[level].push(spell);
      return acc;
    }, {} as Record<string, SRDSpell[]>);

    for (const [level, levelSpells] of Object.entries(byLevel)) {
      output += `## ${level}\n\n`;
      levelSpells.forEach(spell => {
        output += `**${spell.name}** (${spell.school})\n`;
        output += `*${spell.castingTime} • ${spell.range} • ${spell.duration}*\n`;
        output += `${spell.description}\n\n`;
      });
    }

    return output;
  }

  private exportAsFoundryVTT(collection: SpellCollection, spells: SRDSpell[]): string {
    const foundrySpells = spells.map(spell => ({
      name: spell.name,
      type: 'spell',
      data: {
        level: spell.level,
        school: spell.school,
        components: {
          vocal: spell.components?.includes('V') || false,
          somatic: spell.components?.includes('S') || false,
          material: spell.components?.includes('M') || false,
          materialData: spell.materialComponent || ''
        },
        activation: { type: 'action', cost: 1 },
        duration: { value: null, units: 'inst' },
        target: { value: null, units: '', type: '' },
        range: { value: null, units: 'ft' },
        uses: { value: null, max: '', per: null },
        consume: { type: '', target: null, amount: null },
        ability: null,
        actionType: spell.attackRoll ? 'rsak' : (spell.savingThrow ? 'save' : null),
        chatFlavor: '',
        critical: { threshold: null, damage: null },
        damage: spell.damage ? {
          parts: [[spell.damage.diceExpression, spell.damage.damageType]],
          versatile: ''
        } : { parts: [], versatile: '' },
        formula: '',
        save: spell.savingThrow ? {
          ability: spell.savingThrow.ability.toLowerCase(),
          dc: null,
          scaling: 'spell'
        } : { ability: '', dc: null, scaling: 'spell' },
        description: { value: spell.description }
      }
    }));

    return JSON.stringify({ spells: foundrySpells }, null, 2);
  }

  private exportAsRoll20(collection: SpellCollection, spells: SRDSpell[]): string {
    // Roll20 macro format
    let output = `!spell-import --name "${collection.name}"\n\n`;

    spells.forEach(spell => {
      output += `/w gm &{template:spell} {{name=${spell.name}}} {{level=${spell.level}}} `;
      output += `{{school=${spell.school}}} {{casttime=${spell.castingTime}}} `;
      output += `{{range=${spell.range}}} {{duration=${spell.duration}}} `;
      output += `{{components=${spell.components.join(', ')}}} `;
      output += `{{description=${spell.description}}}\n`;
    });

    return output;
  }

  private exportAsDnDBeyond(collection: SpellCollection, spells: SRDSpell[]): string {
    // D&D Beyond homebrew format (simplified)
    const dndbSpells = spells.map(spell => ({
      name: spell.name,
      level: spell.level,
      school: spell.school,
      castingTime: spell.castingTime,
      range: spell.range,
      duration: spell.duration,
      components: spell.components.join(', '),
      classes: spell.classes,
      description: spell.description,
      source: spell.source || 'Homebrew Collection'
    }));

    return JSON.stringify({
      name: collection.name,
      description: collection.description,
      spells: dndbSpells
    }, null, 2);
  }

  private parseJsonImport(data: string): ExportedSpellCollection {
    return JSON.parse(data);
  }

  private parseCsvImport(data: string): ExportedSpellCollection {
    const lines = data.split('\n');
    const headers = lines[0].split(',').map(h => h.replace(/"/g, ''));

    const spells: SRDSpell[] = [];
    for (let i = 1; i < lines.length; i++) {
      const values = lines[i].split(',').map(v => v.replace(/"/g, ''));
      if (values.length >= headers.length) {
        // Basic spell creation from CSV (simplified)
        spells.push({
          id: values[0].toLowerCase().replace(/\s+/g, ''),
          name: values[0],
          level: parseInt(values[1]) || 0,
          school: values[2] as any,
          castingTime: values[3],
          range: values[4],
          duration: values[5],
          components: values[6].split(', ') as any,
          description: values[8] || '',
          classes: values[7].split(', '),
          concentration: false,
          ritual: false,
          source: 'Imported',
          tags: ['imported']
        } as SRDSpell);
      }
    }

    return {
      id: this.generateId(),
      name: 'Imported Collection',
      description: 'Collection imported from CSV',
      spellIds: spells.map(s => s.id),
      tags: ['imported'],
      createdAt: new Date(),
      updatedAt: new Date(),
      version: '1.0.0',
      spells
    };
  }

  private parseFoundryImport(data: string): ExportedSpellCollection {
    const foundryData = JSON.parse(data);

    const spells: SRDSpell[] = foundryData.spells?.map((spell: any) => ({
      id: spell.name.toLowerCase().replace(/\s+/g, ''),
      name: spell.name,
      level: spell.data.level || 0,
      school: spell.data.school || 'evocation',
      castingTime: '1 action', // Default
      range: '30 feet', // Default
      duration: 'Instantaneous', // Default
      components: [
        spell.data.components?.vocal ? 'V' : '',
        spell.data.components?.somatic ? 'S' : '',
        spell.data.components?.material ? 'M' : ''
      ].filter(Boolean) as any,
      description: spell.data.description?.value || '',
      concentration: false,
      ritual: false,
      classes: [], // Would need to be inferred or provided
      source: 'Foundry VTT Import',
      tags: ['foundry-import']
    } as SRDSpell)) || [];

    return {
      id: this.generateId(),
      name: foundryData.name || 'Foundry Import',
      description: 'Collection imported from Foundry VTT',
      spellIds: spells.map(s => s.id),
      tags: ['foundry-import'],
      createdAt: new Date(),
      updatedAt: new Date(),
      version: '1.0.0',
      spells
    };
  }
}

// Export singleton instance
export const _spellCollectionManager = new SpellCollectionManager();

/**
 * Spell Physics Integration - Computational Physics for Spell Effects
 * Integrates computational spell system with 3D physics simulation
 */
// 3D Geometry calculations
export class GeometryCalculator {
    static getEntitiesInSphere(center, radius, entities) {
        return entities.filter(entity => {
            const distance = this.distance(center, entity.position);
            return distance <= radius;
        });
    }
    static getEntitiesInCube(center, size, entities) {
        const halfSize = size / 2;
        return entities.filter(entity => {
            return Math.abs(entity.position.x - center.x) <= halfSize &&
                Math.abs(entity.position.y - center.y) <= halfSize &&
                Math.abs(entity.position.z - center.z) <= halfSize;
        });
    }
    static getEntitiesInCone(apex, direction, angle, length, entities) {
        const normalizedDirection = this.normalize(direction);
        return entities.filter(entity => {
            const toEntity = this.subtract(entity.position, apex);
            const distance = this.magnitude(toEntity);
            if (distance > length)
                return false;
            const normalizedToEntity = this.normalize(toEntity);
            const dot = this.dot(normalizedDirection, normalizedToEntity);
            const entityAngle = Math.acos(Math.max(-1, Math.min(1, dot)));
            return entityAngle <= angle / 2;
        });
    }
    static getEntitiesInLine(start, end, width, entities) {
        return entities.filter(entity => {
            const distance = this.distancePointToLine(entity.position, start, end);
            return distance <= width / 2;
        });
    }
    static distancePointToLine(point, lineStart, lineEnd) {
        const lineVec = this.subtract(lineEnd, lineStart);
        const pointVec = this.subtract(point, lineStart);
        const lineLength = this.magnitude(lineVec);
        if (lineLength === 0)
            return this.distance(point, lineStart);
        const t = Math.max(0, Math.min(1, this.dot(pointVec, lineVec) / (lineLength * lineLength)));
        const projection = this.add(lineStart, this.multiply(lineVec, t));
        return this.distance(point, projection);
    }
    static distance(a, b) {
        return Math.sqrt(Math.pow(b.x - a.x, 2) +
            Math.pow(b.y - a.y, 2) +
            Math.pow(b.z - a.z, 2));
    }
    static add(a, b) {
        return { x: a.x + b.x, y: a.y + b.y, z: a.z + b.z };
    }
    static subtract(a, b) {
        return { x: a.x - b.x, y: a.y - b.y, z: a.z - b.z };
    }
    static multiply(v, scalar) {
        return { x: v.x * scalar, y: v.y * scalar, z: v.z * scalar };
    }
    static normalize(v) {
        const mag = this.magnitude(v);
        return mag > 0 ? { x: v.x / mag, y: v.y / mag, z: v.z / mag } : { x: 0, y: 0, z: 0 };
    }
    static magnitude(v) {
        return Math.sqrt(v.x * v.x + v.y * v.y + v.z * v.z);
    }
    static dot(a, b) {
        return a.x * b.x + a.y * b.y + a.z * b.z;
    }
    static cross(a, b) {
        return {
            x: a.y * b.z - a.z * b.y,
            y: a.z * b.x - a.x * b.z,
            z: a.x * b.y - a.y * b.x
        };
    }
}
// Physics-integrated spell execution engine
export class PhysicsSpellExecutor {
    constructor(physicsWorld) {
        this.activeEffects = new Map();
        this.physicsWorld = physicsWorld;
    }
    executeSpellWithPhysics(spell, ctx) {
        const physicsEffects = [];
        for (const effect of spell.effects) {
            const physicsEffect = this.convertToPhysicsEffect(effect, spell, ctx);
            if (physicsEffect) {
                const activeEffect = this.createActiveEffect(physicsEffect, ctx);
                this.activeEffects.set(activeEffect.id, activeEffect);
                physicsEffects.push(activeEffect);
            }
        }
        return { success: true, physicsEffects };
    }
    convertToPhysicsEffect(effect, spell, ctx) {
        switch (effect.type) {
            case 'damage':
                if (effect.areaOfEffect) {
                    return this.createAreaDamagePhysics(effect, spell, ctx);
                }
                else {
                    return this.createProjectilePhysics(effect, spell, ctx);
                }
            case 'movement':
                return this.createForcePhysics(effect, spell, ctx);
            case 'geometry':
                return this.createGeometryPhysics(effect, spell, ctx);
            default:
                return null;
        }
    }
    createProjectilePhysics(effect, spell, ctx) {
        return {
            type: 'projectile',
            duration: () => 5000, // 5 second max flight time
            physics: {
                velocity: (ctx) => {
                    // Calculate initial velocity toward target
                    const target = ctx.targets[0];
                    if (!target)
                        return { x: 0, y: 0, z: 0 };
                    const direction = GeometryCalculator.subtract(target.position, ctx.caster.position);
                    const distance = GeometryCalculator.magnitude(direction);
                    const normalized = GeometryCalculator.normalize(direction);
                    // Calculate velocity for projectile to reach target (simple ballistic)
                    const speed = Math.max(30, distance * 2); // Adaptive speed based on distance
                    return GeometryCalculator.multiply(normalized, speed);
                },
                acceleration: (ctx) => ctx.environment.gravity || { x: 0, y: -9.8, z: 0 },
                mass: () => 0.1, // Light magical projectile
                drag: 0.1,
                bounce: 0,
                onCollision: (collision, ctx) => {
                    // Execute spell effect on collision
                    const targetEntity = this.findEntityAtPosition(collision.point);
                    if (targetEntity && effect.targetFilter(targetEntity, ctx)) {
                        this.applyDamageEffect(effect, targetEntity, ctx);
                    }
                }
            }
        };
    }
    createAreaDamagePhysics(effect, spell, ctx) {
        return {
            type: 'area_field',
            duration: () => 100, // Instantaneous explosion
            physics: {
                fieldStrength: (position, time) => {
                    // Field strength diminishes over time and distance from center
                    const center = ctx.targets[0]?.position || ctx.caster.position;
                    const distance = GeometryCalculator.distance(position, center);
                    const radius = effect.areaOfEffect.size(ctx);
                    if (distance > radius)
                        return 0;
                    const timeDecay = Math.max(0, 1 - time / 100);
                    const distanceDecay = Math.max(0, 1 - distance / radius);
                    return timeDecay * distanceDecay;
                },
                fieldGradient: (position, time) => {
                    // Push outward from center
                    const center = ctx.targets[0]?.position || ctx.caster.position;
                    const direction = GeometryCalculator.subtract(position, center);
                    const normalized = GeometryCalculator.normalize(direction);
                    const strength = this.physics.fieldStrength(position, time);
                    return GeometryCalculator.multiply(normalized, strength * 1000);
                }
            }
        };
    }
    createForcePhysics(effect, spell, ctx) {
        return {
            type: 'force_application',
            duration: (ctx) => effect.duration ? effect.duration(ctx) : 1000,
            physics: {
                velocity: (ctx) => {
                    if (effect.mode === 'teleport') {
                        // Instant displacement
                        return { x: 0, y: 0, z: 0 };
                    }
                    const magnitude = effect.magnitude(ctx);
                    const direction = effect.direction ? effect.direction(ctx) : { x: 1, y: 0, z: 0 };
                    const normalized = GeometryCalculator.normalize(direction);
                    return GeometryCalculator.multiply(normalized, magnitude);
                }
            }
        };
    }
    createGeometryPhysics(effect, spell, ctx) {
        return {
            type: 'transformation',
            duration: (ctx) => effect.duration ? effect.duration(ctx) : 60000,
            physics: {
                // Create physical barriers, walls, etc.
                mass: () => Infinity, // Immovable
                velocity: () => ({ x: 0, y: 0, z: 0 })
            }
        };
    }
    // Physics simulation update loop
    updatePhysics(deltaTime) {
        const currentTime = Date.now();
        // Update all active effects
        for (const [id, effect] of this.activeEffects.entries()) {
            const elapsed = currentTime - effect.startTime;
            const duration = effect.physicsEffect.duration(effect.context);
            if (elapsed >= duration) {
                this.removeEffect(id);
                continue;
            }
            this.updateEffect(effect, deltaTime, elapsed);
        }
        // Update physics world
        this.updatePhysicsWorld(deltaTime);
    }
    updateEffect(effect, deltaTime, elapsed) {
        switch (effect.physicsEffect.type) {
            case 'projectile':
                this.updateProjectile(effect, deltaTime);
                break;
            case 'area_field':
                this.updateAreaField(effect, elapsed);
                break;
            case 'force_application':
                this.updateForceApplication(effect, deltaTime);
                break;
        }
    }
    updateProjectile(effect, deltaTime) {
        const physics = effect.physicsEffect.physics;
        const entity = effect.entity;
        if (!entity)
            return;
        // Apply acceleration
        if (physics.acceleration) {
            const accel = physics.acceleration(effect.context);
            entity.velocity = GeometryCalculator.add(entity.velocity, GeometryCalculator.multiply(accel, deltaTime));
        }
        // Apply drag
        if (physics.drag) {
            const dragForce = GeometryCalculator.multiply(entity.velocity, -physics.drag);
            entity.velocity = GeometryCalculator.add(entity.velocity, dragForce);
        }
        // Update position
        const displacement = GeometryCalculator.multiply(entity.velocity, deltaTime);
        entity.position = GeometryCalculator.add(entity.position, displacement);
        // Check for collisions
        const collision = this.checkCollisions(entity);
        if (collision && physics.onCollision) {
            physics.onCollision(collision, effect.context);
            this.removeEffect(effect.id);
        }
    }
    updateAreaField(effect, elapsed) {
        const physics = effect.physicsEffect.physics;
        if (!physics.fieldStrength || !physics.fieldGradient)
            return;
        // Apply field forces to all entities in range
        for (const entity of this.physicsWorld.entities.values()) {
            const strength = physics.fieldStrength(entity.position, elapsed);
            if (strength > 0) {
                const force = physics.fieldGradient(entity.position, elapsed);
                entity.forces.push(force);
            }
        }
    }
    updateForceApplication(effect, deltaTime) {
        const physics = effect.physicsEffect.physics;
        const targets = effect.context.targets;
        for (const target of targets) {
            if (physics.velocity) {
                const velocity = physics.velocity(effect.context);
                target.velocity = GeometryCalculator.add(target.velocity, GeometryCalculator.multiply(velocity, deltaTime));
            }
        }
    }
    updatePhysicsWorld(deltaTime) {
        // Update all entities in the physics world
