        id,
        position: { x: 0, y: 0, z: 0 },
        hitPoints: { current: 100, maximum: 100 },
        armorClass: 15,
        savingThrows: Record<string, any>,
        conditions: new Set(),
        resistances: new Set(),
        immunities: new Set(),
        vulnerabilities: new Set()
      })),
      environment: {
        entities: new Map(),
        obstacles: [],
        lighting: 1.0,
        temperature: 20
      },
      dice: (_sides: number, _count: number = 1) => {
        const results = [];
        for (let i = 0; i < count; i++) {
          results.push(Math.floor(Math.random() * sides) + 1);
        }
        return results;
      },
      time: Date.now()
    };
  }

  private convertComputationalToEffects(results: EffectResult[]): any[] {
    return results.map(result => ({
      type: `computational_effect_${result.effectIndex}`,
      target: result.targets.join(','),
      result: {
        success: result.success,
        values: result.values,
        modifications: result.modifications
      }
    }));
  }

  private async generateVisualEffects(
    spell: UnifiedSpell,
    context: UnifiedCastingContext,
    results: UnifiedCastingResult
  ): Promise<any[]> {
    const visualEffects = [];

    // Generate based on spell school and type
    switch (spell.school) {
      case 'evocation':
        if (spell.tags.includes('fire')) {
          visualEffects.push({
            type: 'particle_system',
            effect: 'fire_explosion',
            position: context.position,
            duration: 2000
          });
        }
        if (spell.tags.includes('lightning')) {
          visualEffects.push({
            type: 'lightning_bolt',
            start: context.caster.position || context.position,
            end: context.position,
            duration: 500
          });
        }
        break;

      case 'conjuration':
        if (spell.tags.includes('teleport')) {
          visualEffects.push({
            type: 'teleport_effect',
            start: context.caster.position,
            end: context.position,
            duration: 1000
          });
        }
        break;

      case 'enchantment':
        visualEffects.push({
          type: 'mind_effect',
          targets: context.targets,
          duration: 3000
        });
        break;
    }

    // Add physics-based visual effects
    if (results.physicsEffects.length > 0) {
      for (const physicsEffect of results.physicsEffects) {
        switch (physicsEffect.type) {
          case 'projectile_created':
            visualEffects.push({
              type: 'projectile_trail',
              projectileId: physicsEffect.projectileId,
              duration: 5000
            });
            break;
          case 'force_applied':
            visualEffects.push({
              type: 'force_impact',
              target: physicsEffect.targetId,
              force: physicsEffect.force,
              duration: 1000
            });
            break;
        }
      }
    }

    return visualEffects;
  }

  private async updateMapService(mapService: any, sceneId: string, spell: UnifiedSpell, results: UnifiedCastingResult): Promise<void> {
    // Update tokens with spell effects
    for (const effect of results.effects) {
      if (effect.type === 'damage' && effect.result.total) {
        await mapService.applyDamage(sceneId, effect.target, effect.result.total, spell.damage?.damageType || 'magical');
      }
      if (effect.type === 'healing' && effect.result.amount) {
        await mapService.applyHealing(sceneId, effect.target, effect.result.amount);
      }
    }

    // Handle conditions
    if (results.conditions) {
      for (const condition of results.conditions) {
        // This would integrate with a conditions system
        mapService.emit('conditionApplied', {
          target: condition.target,
          condition: condition.condition,
          duration: condition.duration,
          source: spell.id
        });
      }
    }

    // Emit spell cast event to map
    mapService.emitMapUpdate(sceneId, {
      type: 'spell_cast',
      spell: spell.id,
      caster: results.effects[0]?.target,
      effects: results.effects.length,
      timestamp: Date.now()
    });
  }

  // Helper methods for format conversions
  private formatComponents(components: any): string[] {
    const result = [];
    if (components.verbal) result.push('V');
    if (components.somatic) result.push('S');
    if (components.material) result.push('M');
    return result;
  }

  private formatComputationalComponents(components: any): string[] {
    const result = [];
    if (components.verbal) result.push('V');
    if (components.somatic) result.push('S');
    if (components.material?.required) result.push('M');
    return result;
  }

  private formatCastingTime(ms: number): string {
    if (ms === 0) return '1 reaction';
    if (ms === 1000) return '1 action';
    if (ms === 6000) return '1 bonus action';
    return `${ms / 1000} seconds`;
  }

  private formatRange(gameUnits: number): string {
    const feet = gameUnits / 5;
    if (feet === 0) return 'Self';
    if (feet === 1) return 'Touch';
    return `${feet} feet`;
  }

  private extractTagsFromEffects(effects: any[]): string[] {
    const tags = new Set<string>();
    for (const effect of effects) {
      tags.add(effect.type);
      if (effect.damage?.type) tags.add(effect.damage.type);
      if (effect.area?.type) tags.add(effect.area.type);
    }
    return Array.from(tags);
  }

  private extractComputationalTags(effects: any[]): string[] {
    const tags = new Set<string>();
    for (const effect of effects) {
      tags.add(effect.type);
    }
    return Array.from(tags);
  }

  private extractPhysicsTags(physics: PhysicsSpellEffect): string[] {
    const tags = new Set<string>();
    if (physics.physics?.type) tags.add(physics.physics.type);
    if (physics.effects) {
      for (const effect of physics.effects) {
        if (effect.damage?.type) tags.add(effect.damage.type);
      }
    }
    return Array.from(tags);
  }

  /**
   * Get system statistics
   */
  getStats(): {
    totalSpells: number;
    basicSpells: number;
    computationalSpells: number;
    physicsSpells: number;
    unifiedSpells: number;
  } {
    const spells = Array.from(this.spellRegistry.values());
    return {
      totalSpells: spells.length,
      basicSpells: spells.filter(s => s.basicSpell).length,
      computationalSpells: spells.filter(s => s.computationalSpell).length,
      physicsSpells: spells.filter(s => s.physicsSpell).length,
      unifiedSpells: spells.filter(s => s.basicSpell && s.computationalSpell && s.physicsSpell).length
    };
  }

  /**
   * Export unified spell registry
   */
  exportSpells(): Record<string, UnifiedSpell> {
    const result: Record<string, UnifiedSpell> = {};
    for (const [id, spell] of this.spellRegistry.entries()) {
      result[id] = spell;
    }
    return result;
  }
}

// Singleton instance
export const _unifiedSpellSystem = new UnifiedSpellSystem();

/**
 * D&D 5e Consumable Items System
 * Handles potions, scrolls, ammunition, and other consumable items
 */

import { Consumable, _ConsumableEffect} from './index.js';

export interface PotionEffect {
  type: 'healing' | 'buff' | 'utility';
  dice?: string;
  fixedValue?: number;
  duration?: number; // in seconds
  description: string;
}

export interface ScrollData {
  spellId: string;
  spellLevel: number;
  castingTime: string;
  spellAttackBonus?: number;
  spellSaveDC?: number;
  casterLevel: number;
}

export interface AmmunitionData {
  weaponTypes: string[]; // weapon IDs this ammo works with
  recoveryRate: number; // percentage chance to recover after combat (0-1)
  specialProperties?: string[];
}

export interface ConsumableUseResult {
  success: boolean;
  effects: string[];
  diceRolls?: { dice: string; result: number }[];
  remainingCharges?: number;
  consumed: boolean;
  error?: string;
}

export class ConsumableSystem {
  private potions: Map<string, Consumable & { potionData: PotionEffect }> = new Map();
  private scrolls: Map<string, Consumable & { scrollData: ScrollData }> = new Map();
  private ammunition: Map<string, Consumable & { ammoData: AmmunitionData }> = new Map();
  private usageHistory: ConsumableUsage[] = [];

  constructor() {
    this.initializePotions();
    this.initializeScrolls();
    this.initializeAmmunition();
  }

  /**
   * Use a consumable item
   */
  useConsumable(
    itemId: string,
    userId: string,
    targetId?: string,
    quantity: number = 1
  ): ConsumableUseResult {
    // Check potions first
    const potion = this.potions.get(itemId);
    if (potion) {
      return this.usePotion(potion, userId, targetId, quantity);
    }

    // Check scrolls
    const scroll = this.scrolls.get(itemId);
    if (scroll) {
      return this.useScroll(scroll, userId, targetId);
    }

    // Check ammunition
    const ammo = this.ammunition.get(itemId);
    if (ammo) {
      return this.useAmmunition(ammo, userId, quantity);
    }

    return {
      success: false,
      effects: [],
      consumed: false,
      error: `Unknown consumable: ${itemId}`
    };
  }

  /**
   * Use a potion
   */
  private usePotion(
    potion: Consumable & { potionData: PotionEffect },
    userId: string,
    targetId?: string,
    quantity: number = 1
  ): ConsumableUseResult {
    const effects: string[] = [];
    const diceRolls: { dice: string; result: number }[] = [];

    for (let i = 0; i < quantity; i++) {
      const effect = potion.potionData;

      if (effect.type === 'healing' && effect.dice) {
        const roll = this.rollDice(effect.dice);
        diceRolls.push({ dice: effect.dice, result: roll });
        effects.push(`Healed ${roll} hit points`);
      } else if (effect.fixedValue) {
        effects.push(`${effect.description}: ${effect.fixedValue}`);
      } else {
        effects.push(effect.description);
      }
    }

    this.recordUsage(potion.id, userId, 'potion', quantity, effects);
    this.recordUsage(potion.id, userId, 'scroll', 1, effects);

    return {
      success: true,
      effects,
      diceRolls,
      consumed: true
    };
  }

  /**
   * Use a scroll
   */
  private useScroll(
    scroll: Consumable & { scrollData: ScrollData },
    userId: string,
    targetId?: string
  ): ConsumableUseResult {
    const scrollData = scroll.scrollData;

    // This would integrate with the spell system
    const effects = [
      `Cast ${scrollData.spellId} at level ${scrollData.spellLevel}`,
      `Casting time: ${scrollData.castingTime}`,
      `Caster level: ${scrollData.casterLevel}`
    ];

    if (scrollData.spellAttackBonus) {
      effects.push(`Spell attack bonus: +${scrollData.spellAttackBonus}`);
    }

    if (scrollData.spellSaveDC) {
      effects.push(`Spell save DC: ${scrollData.spellSaveDC}`);
    }

    this.recordUsage(scroll.id, userId, 'scroll', 1, effects);

    return {
      success: true,
      effects,
      consumed: true
    };
  }

  /**
   * Use ammunition
   */
  private useAmmunition(
    ammo: Consumable & { ammoData: AmmunitionData },
    userId: string,
    quantity: number
  ): ConsumableUseResult {
    const effects = [`Used ${quantity}x ${ammo.name}`];

    if (ammo.ammoData.specialProperties) {
      effects.push(`Special: ${ammo.ammoData.specialProperties.join(', ')}`);
    }

    this.recordUsage(ammo.id, userId, 'ammunition', quantity, effects);

    return {
      success: true,
      effects,
      consumed: true
    };
  }

  /**
   * Recover ammunition after combat
   */
  recoverAmmunition(
    ammoId: string,
    quantityUsed: number
  ): { recovered: number; lost: number } {
    const ammo = this.ammunition.get(ammoId);
    if (!ammo) {
      return { recovered: 0, lost: quantityUsed };
    }

    let recovered = 0;
    const recoveryRate = ammo.ammoData.recoveryRate;

    for (let i = 0; i < quantityUsed; i++) {
      if (Math.random() <= recoveryRate) {
        recovered++;
      }
    }

    const lost = quantityUsed - recovered;

    return { recovered, lost };
  }

  /**
   * Get compatible ammunition for a weapon
   */
  getCompatibleAmmunition(weaponId: string): (Consumable & { ammoData: AmmunitionData })[] {
    const compatible: (Consumable & { ammoData: AmmunitionData })[] = [];

    for (const ammo of this.ammunition.values()) {
      if (ammo.ammoData.weaponTypes.includes(weaponId)) {
        compatible.push(ammo);
      }
    }

    return compatible;
  }

  /**
   * Get all potions
   */
  getAllPotions(): (Consumable & { potionData: PotionEffect })[] {
    return Array.from(this.potions.values());
  }

  /**
   * Get all scrolls
   */
  getAllScrolls(): (Consumable & { scrollData: ScrollData })[] {
    return Array.from(this.scrolls.values());
  }

  /**
   * Get all ammunition
   */
  getAllAmmunition(): (Consumable & { ammoData: AmmunitionData })[] {
    return Array.from(this.ammunition.values());
  }

  /**
   * Get usage history
   */
  getUsageHistory(userId?: string, limit?: number): ConsumableUsage[] {
    let history = userId ? 
      this.usageHistory.filter(usage => usage.userId === userId) :
      this.usageHistory;

    history = history.sort((_a, _b) => b.timestamp.getTime() - a.timestamp.getTime());

    return limit ? history.slice(0, limit) : history;
  }

  /**
   * Add custom consumable
   */
  addPotion(potion: Consumable & { potionData: PotionEffect }): void {
    this.potions.set(potion.id, potion);
  }

  addScroll(scroll: Consumable & { scrollData: ScrollData }): void {
    this.scrolls.set(scroll.id, scroll);
  }

  addAmmunition(ammo: Consumable & { ammoData: AmmunitionData }): void {
    this.ammunition.set(ammo.id, ammo);
  }

  private rollDice(diceString: string): number {
    // Simple dice roller - supports formats like "2d4+2", "1d8", etc.
    const match = diceString.match(/(\d+)d(\d+)(?:\+(\d+))?/);
    if (!match) return 0;

    const numDice = parseInt(match[1]!, 10);
    const dieSize = parseInt(match[2]!, 10);
    const bonus = parseInt(match[3] || '0', 10);

    let total = bonus;
    for (let i = 0; i < numDice; i++) {
      total += Math.floor(Math.random() * dieSize) + 1;
    }

    return total;
  }

  private recordUsage(
    itemId: string,
    userId: string,
    type: 'potion' | 'scroll' | 'ammunition',
    quantity: number,
    effects: string[]
  ): void {
    const usage: ConsumableUsage = {
      id: `usage_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      itemId,
      userId,
      type,
      quantity,
      effects,
      timestamp: new Date()
    };

    this.usageHistory.push(usage);

    // Keep only last 1000 usage records
    if (this.usageHistory.length > 1000) {
      this.usageHistory = this.usageHistory.slice(-1000);
    }
  }

  private initializePotions(): void {
    const potions = [
      {
