/**
 * Content Export System
 * Handles exporting content to various formats and packages
 */

import { EventEmitter } from 'events';
import JSZip from 'jszip';
import { v4 as _uuidv4 } from 'uuid';
import { AssetManager, AssetMetadata, AssetType, AssetCategory } from './AssetManager';
import { PackageManifest, PackageAssetEntry } from './ContentImporter';

export interface ExportOptions {
  format: ExportFormat;
  includeMetadata?: boolean;
  compressAssets?: boolean;
  includePreview?: boolean;
  filterByType?: AssetType[];
  filterByCategory?: AssetCategory[];
  filterByTags?: string[];
  customManifest?: Partial<PackageManifest>;
}

export type ExportFormat = 'zip' | 'content-package' | 'json' | 'individual';

export interface ExportResult {
  success: boolean;
  data?: ArrayBuffer;
  files?: Map<string, ArrayBuffer>; // For individual export
  manifest?: PackageManifest;
  exportedAssets: string[];
  skippedAssets: string[];
  totalSize: number;
  compressedSize?: number;
  warnings: string[];
}

export interface ExportProgress {
  stage: 'preparing' | 'collecting' | 'processing' | 'compressing' | 'complete';
  current: number;
  total: number;
  currentAsset?: string;
  bytes?: number;
  totalBytes?: number;
}

export interface ExportPreset {
  name: string;
  description: string;
  options: ExportOptions;
}

export class ContentExporter extends EventEmitter {
  private assetManager: AssetManager;
  private presets = new Map<string, ExportPreset>();

  constructor(assetManager: AssetManager) {
    super();
    this.assetManager = assetManager;
    this.setupDefaultPresets();
  }

  /**
   * Export assets based on provided options
   */
  async exportContent(assetIds: string[], options: ExportOptions): Promise<ExportResult> {
    const result: ExportResult = {
      success: false,
      exportedAssets: [],
      skippedAssets: [],
      totalSize: 0,
      warnings: [],
    };

    this.emitProgress('preparing', 0, assetIds.length);

    // Filter and validate assets
    const assetsToExport = await this.prepareAssets(assetIds, options);

    if (assetsToExport.length === 0) {
      throw new Error('No valid assets to export');
    }

    this.emitProgress('collecting', 0, assetsToExport.length);

    // Collect asset data
    const assetData = new Map<string, { metadata: AssetMetadata; data: ArrayBuffer }>();
    let totalBytes = 0;

    for (let i = 0; i < assetsToExport.length; i++) {
      const asset = assetsToExport[i];
      this.emitProgress('collecting', i + 1, assetsToExport.length, asset.name);

      try {
        const data = await this.assetManager.getAssetData(asset.id);
        assetData.set(asset.id, { metadata: asset, data });
        totalBytes += data.byteLength;
        result.exportedAssets.push(asset.id);
      } catch (error) {
        result.skippedAssets.push(asset.id);
        result.warnings.push(`Failed to export ${asset.name}: ${error instanceof Error ? error.message : 'Unknown error'}`);
      }
    }

    result.totalSize = totalBytes;

    // Export based on format
    switch (options.format) {
      case 'zip':
        result.data = await this.exportAsZip(assetData, options);
        break;

      case 'content-package': {
        const packageResult = await this.exportAsContentPackage(assetData, options);
        result.data = packageResult.data;
        result.manifest = packageResult.manifest;
    }
        break;

      case 'json':
        result.data = await this.exportAsJSON(assetData, options);
        break;

      case 'individual':
        result.files = await this.exportAsIndividualFiles(assetData, options);
        break;
    }

    result.success = true;
    this.emitProgress('complete', assetsToExport.length, assetsToExport.length);

    return result;
  }

  /**
   * Export using a preset configuration
   */
  async exportWithPreset(assetIds: string[], presetName: string, overrides?: Partial<ExportOptions>): Promise<ExportResult> {
    const preset = this.presets.get(presetName);
    if (!preset) {
      throw new Error(`Export preset not found: ${presetName}`);
    }

    const options = { ...preset.options, ...overrides };
    return this.exportContent(assetIds, options);
  }

  /**
   * Export all assets of specific types/categories
   */
  async exportByFilter(filter: {
    types?: AssetType[];
    categories?: AssetCategory[];
    tags?: string[];
    uploadedBy?: string;
  }, options: ExportOptions): Promise<ExportResult> {
    const searchResult = this.assetManager.searchAssets({
      type: filter.types,
      category: filter.categories,
      tags: filter.tags,
      uploadedBy: filter.uploadedBy,
    }, 1, 1000); // Get first 1000 assets

    const assetIds = searchResult.assets.map(a => a.id);
    return this.exportContent(assetIds, options);
  }

  /**
   * Create a content package with custom structure
   */
  async createContentPackage(
    name: string,
    version: string,
    assetIds: string[],
    packageOptions: {
      description?: string;
      author?: string;
      license?: string;
      tags?: string[];
      dependencies?: string[];
      customStructure?: Map<string, string>; // assetId -> custom path
    }
  ): Promise<ExportResult> {
    const manifest: PackageManifest = {
      name,
      version,
      description: packageOptions.description,
      author: packageOptions.author,
      license: packageOptions.license,
      created: new Date().toISOString(),
      assets: [],
      dependencies: packageOptions.dependencies,
      tags: packageOptions.tags,
    };

    const options: ExportOptions = {
      format: 'content-package',
      includeMetadata: true,
      customManifest: manifest,
    };

    return this.exportContent(assetIds, options);
  }

  /**
   * Register a custom export preset
   */
  registerPreset(preset: ExportPreset): void {
    this.presets.set(preset.name, preset);
  }

  /**
   * Get available export presets
   */
  getPresets(): ExportPreset[] {
    return Array.from(this.presets.values());
  }

  /**
   * Estimate export size
   */
  async estimateExportSize(assetIds: string[]): Promise<{
    totalSize: number;
    compressedEstimate: number;
    assetCount: number;
  }> {
    let totalSize = 0;
    let validAssets = 0;

    for (const assetId of assetIds) {
      const asset = this.assetManager.getAsset(assetId);
      if (asset) {
        totalSize += asset.size;
        validAssets++;
      }
    }

    // Rough compression estimate (varies by content type)
    const compressionRatio = 0.7; // 30% compression on average
    const compressedEstimate = Math.floor(totalSize * compressionRatio);

    return {
      totalSize,
      compressedEstimate,
      assetCount: validAssets,
    };
  }

  private async prepareAssets(assetIds: string[], options: ExportOptions): Promise<AssetMetadata[]> {
    const assets: AssetMetadata[] = [];

    for (const assetId of assetIds) {
      const asset = this.assetManager.getAsset(assetId);
      if (!asset) {
        continue;
      }

      // Apply filters
      if (options.filterByType && !options.filterByType.includes(asset.type)) {
        continue;
      }

      if (options.filterByCategory && !options.filterByCategory.includes(asset.category)) {
        continue;
      }

      if (options.filterByTags && !options.filterByTags.some(tag => asset.tags.includes(tag))) {
        continue;
      }

      assets.push(asset);
    }

    return assets;
  }

  private async exportAsZip(
    assetData: Map<string, { metadata: AssetMetadata; data: ArrayBuffer }>, 
    options: ExportOptions
  ): Promise<ArrayBuffer> {
    const zip = new JSZip();

    this.emitProgress('processing', 0, assetData.size);

    let current = 0;
    for (const [_assetId, { metadata, data }] of assetData) {
      current++;
      this.emitProgress('processing', current, assetData.size, metadata.name);

      const filename = this.generateSafeFilename(metadata.name, metadata.type);
      zip.file(filename, data);

      if (options.includeMetadata) {
        zip.file(`${filename}.meta.json`, JSON.stringify(metadata, null, 2));
      }
    }

    this.emitProgress('compressing', assetData.size, assetData.size);

    return await zip.generateAsync({
      type: 'arraybuffer',
      compression: options.compressAssets ? 'DEFLATE' : 'STORE',
      compressionOptions: { level: 6 },
    });
  }

  private async exportAsContentPackage(
    assetData: Map<string, { metadata: AssetMetadata; data: ArrayBuffer }>,
    options: ExportOptions
  ): Promise<{ data: ArrayBuffer; manifest: PackageManifest }> {
    const zip = new JSZip();

    // Create manifest
    const manifest: PackageManifest = {
      name: options.customManifest?.name || 'Exported Content',
      version: options.customManifest?.version || '1.0.0',
      description: options.customManifest?.description || 'Exported from VTT',
      author: options.customManifest?.author || 'VTT Export',
      license: options.customManifest?.license,
      created: new Date().toISOString(),
      assets: [],
      dependencies: options.customManifest?.dependencies,
      tags: options.customManifest?.tags,
    };

    // Create assets folder and manifest entries
    this.emitProgress('processing', 0, assetData.size);

    let current = 0;
    for (const [assetId, { metadata, data }] of assetData) {
      current++;
      this.emitProgress('processing', current, assetData.size, metadata.name);

      const filename = this.generateSafeFilename(metadata.name, metadata.type);
      const assetPath = `assets/${filename}`;

      // Add asset to zip
      zip.file(assetPath, data);

      // Calculate checksum
      const checksum = await this.calculateChecksum(data);

      // Add to manifest
      const manifestEntry: PackageAssetEntry = {
        id: assetId,
        path: assetPath,
        name: metadata.name,
        type: metadata.type,
        category: metadata.category,
        size: metadata.size,
        checksum,
        tags: metadata.tags,
        metadata: metadata.customProperties,
      };

      manifest.assets.push(manifestEntry);
    }

    // Add manifest to zip
    zip.file('manifest.json', JSON.stringify(manifest, null, 2));

    // Add metadata if requested
    if (options.includeMetadata) {
      const metadataObj: Record<string, any> = {};
      for (const [assetId, { metadata }] of assetData) {
        metadataObj[assetId] = metadata;
      }
      zip.file('metadata.json', JSON.stringify(metadataObj, null, 2));
    }

    this.emitProgress('compressing', assetData.size, assetData.size);

    const data = await zip.generateAsync({
      type: 'arraybuffer',
      compression: options.compressAssets ? 'DEFLATE' : 'STORE',
      compressionOptions: { level: 6 },
    });

    return { data, manifest };
  }

  private async exportAsJSON(
    assetData: Map<string, { metadata: AssetMetadata; data: ArrayBuffer }>,
    options: ExportOptions
  ): Promise<ArrayBuffer> {
    const exportData: {
      metadata: AssetMetadata[];
      assets: Record<string, string>; // assetId -> base64 data
    } = {
      metadata: [],
      assets: Record<string, any>,
    };

    this.emitProgress('processing', 0, assetData.size);

    let current = 0;
    for (const [assetId, { metadata, data }] of assetData) {
      current++;
      this.emitProgress('processing', current, assetData.size, metadata.name);

      exportData.metadata.push(metadata);

      // Convert to base64
      const base64 = Buffer.from(data).toString('base64');
      exportData.assets[assetId] = base64;
    }

    const json = JSON.stringify(exportData, null, 2);
    return new TextEncoder().encode(json);
  }

  private async exportAsIndividualFiles(
    assetData: Map<string, { metadata: AssetMetadata; data: ArrayBuffer }>,
    options: ExportOptions
  ): Promise<Map<string, ArrayBuffer>> {
    const files = new Map<string, ArrayBuffer>();

    this.emitProgress('processing', 0, assetData.size);

    let current = 0;
    for (const [_assetId, { metadata, data }] of assetData) {
      current++;
      this.emitProgress('processing', current, assetData.size, metadata.name);

      const filename = this.generateSafeFilename(metadata.name, metadata.type);
      files.set(filename, data);

      if (options.includeMetadata) {
        const metadataJson = JSON.stringify(metadata, null, 2);
        files.set(`${filename}.meta.json`, new TextEncoder().encode(metadataJson));
      }
    }

    return files;
  }

  private generateSafeFilename(name: string, type: AssetType): string {
    // Remove unsafe characters and ensure valid filename
    let safeName = name.replace(/[<>:"/\\|?*]/g, '');

    // Ensure it has an extension based on type
    if (!safeName.includes('.')) {
      const extension = this.getDefaultExtension(type);
      safeName += `.${extension}`;
    }

    return safeName;
  }

  private getDefaultExtension(type: AssetType): string {
    switch (type) {
      case 'image': return 'png';
      case 'audio': return 'wav';
      case 'model': return 'obj';
      case 'shader': return 'glsl';
      case 'font': return 'ttf';
      case 'data': return 'json';
      default: return 'bin';
    }
  }

  private async calculateChecksum(data: ArrayBuffer): Promise<string> {
    const crypto = globalThis.crypto || require('crypto');
    const hashBuffer = await crypto.subtle.digest('SHA-256', data);
    return Array.from(new Uint8Array(hashBuffer))
      .map(b => b.toString(16).padStart(2, '0'))
      .join('');
  }

  private setupDefaultPresets(): void {
    // Complete export preset
    this.presets.set('complete', {
      name: 'Complete Export',
      description: 'Export all assets with full metadata',
      options: {
        format: 'content-package',
        includeMetadata: true,
        compressAssets: true,
        includePreview: true,
      },
    });

    // Images only preset
    this.presets.set('images', {
      name: 'Images Only',
      description: 'Export only image assets',
      options: {
        format: 'zip',
        filterByType: ['image'],
        compressAssets: false,
        includeMetadata: false,
      },
    });

    // Audio only preset
    this.presets.set('audio', {
      name: 'Audio Only',
      description: 'Export only audio assets',
      options: {
        format: 'zip',
        filterByType: ['audio'],
        compressAssets: true,
        includeMetadata: true,
      },
    });

    // Campaign export preset
    this.presets.set('campaign', {
      name: 'Campaign Export',
      description: 'Export complete campaign with scenes, tokens, and maps',
      options: {
        format: 'content-package',
        filterByType: ['scene', 'token', 'map', 'image'],
        includeMetadata: true,
        compressAssets: true,
        includePreview: true,
      },
    });

    // Minimal export preset
    this.presets.set('minimal', {
      name: 'Minimal Export',
      description: 'Export assets without metadata or compression',
      options: {
        format: 'individual',
        includeMetadata: false,
        compressAssets: false,
      },
    });
  }

  private emitProgress(stage: ExportProgress['stage'], current: number, total: number, currentAsset?: string): void {
    this.emit('progress', {
      stage,
      current,
      total,
      currentAsset,
    } as ExportProgress);
  }
}

/**
 * Content Import System
 * Handles importing content from various formats and sources
 */

import { EventEmitter } from 'events';
import JSZip from 'jszip';
import * as mime from 'mime-types';
import { v4 as _uuidv4 } from 'uuid';
import { AssetManager, AssetMetadata, AssetType, AssetCategory } from './AssetManager';

export interface ImportOptions {
  overwriteExisting?: boolean;
  validateContent?: boolean;
  generateThumbnails?: boolean;
  extractMetadata?: boolean;
  maxFileSize?: number; // bytes
  allowedTypes?: string[]; // MIME types
  defaultCategory?: AssetCategory;
  defaultTags?: string[];
}

export interface ImportResult {
  success: boolean;
  imported: AssetMetadata[];
  failed: ImportFailure[];
  warnings: string[];
  totalFiles: number;
  totalSize: number;
}

export interface ImportFailure {
  filename: string;
  reason: string;
  error?: Error;
}

export interface ContentPackage {
  manifest: PackageManifest;
  assets: Map<string, ArrayBuffer>;
  metadata: Map<string, any>;
}

export interface PackageManifest {
  name: string;
  version: string;
  description?: string;
  author?: string;
  license?: string;
  created: string;
  assets: PackageAssetEntry[];
  dependencies?: string[];
  tags?: string[];
}

export interface PackageAssetEntry {
  id: string;
  path: string;
  name: string;
  type: AssetType;
  category: AssetCategory;
  size: number;
  checksum: string;
  tags?: string[];
  metadata?: Record<string, any>;
}

export interface ImportProgress {
  stage: 'reading' | 'validating' | 'processing' | 'storing' | 'complete';
  current: number;
  total: number;
  currentFile?: string;
  errors: string[];
}

export class ContentImporter extends EventEmitter {
  private assetManager: AssetManager;
  private processors = new Map<string, ContentProcessor>();

  constructor(assetManager: AssetManager) {
    super();
    this.assetManager = assetManager;
    this.setupDefaultProcessors();
  }

  /**
   * Import files from a file list (e.g., drag & drop)
   */
  async importFiles(files: FileList, options: ImportOptions = {}): Promise<ImportResult> {
    const result: ImportResult = {
      success: true,
      imported: [],
      failed: [],
      warnings: [],
      totalFiles: files.length,
      totalSize: 0,
    };

    this.emitProgress('reading', 0, files.length);

    for (let i = 0; i < files.length; i++) {
      const file = files[i];
      this.emitProgress('reading', i + 1, files.length, file.name);

      try {
        // Validate file
        const validation = this.validateFile(file, options);
        if (!validation.valid) {
          result.failed.push({
            filename: file.name,
            reason: validation.reason!,
          });
          continue;
        }

        // Read file data
        const data = await this.readFile(file);
        result.totalSize += data.byteLength;

        // Determine asset type and category
        const assetType = this.determineAssetType(file);
        const category = options.defaultCategory || this.determineCategory(assetType);

        // Process the file
        const processed = await this.processFile(file, data, assetType, options);

        // Create asset metadata
        const metadata: Omit<AssetMetadata, 'id' | 'createdAt' | 'updatedAt' | 'version' | 'size' | 'checksum'> = {
          name: processed.name || file.name,
          type: assetType,
          category,
          mimeType: file.type || mime.lookup(file.name) || 'application/octet-stream',
          tags: [...(options.defaultTags || []), ...(processed.tags || [])],
          uploadedBy: 'import',
          customProperties: processed.metadata || {},
        };

        // Add to asset manager
        const asset = await this.assetManager.addAsset(processed.data, metadata);
        result.imported.push(asset);

      } catch (error) {
        result.failed.push({
          filename: file.name,
          reason: error instanceof Error ? error.message : 'Unknown error',
          error: error instanceof Error ? error : undefined,
        });
      }
    }

    result.success = result.failed.length === 0;
    this.emitProgress('complete', result.totalFiles, result.totalFiles);

    return result;
  }

  /**
   * Import from a ZIP archive
   */
  async importZip(zipData: ArrayBuffer, options: ImportOptions = {}): Promise<ImportResult> {
    const zip = await JSZip.loadAsync(zipData);
    const files: { name: string; data: ArrayBuffer }[] = [];

    // Extract all files
    for (const [path, zipEntry] of Object.entries(zip.files)) {
      if (!zipEntry.dir) {
        const data = await zipEntry.async('arraybuffer');
        files.push({ name: path, data });
      }
    }

    const result: ImportResult = {
      success: true,
      imported: [],
      failed: [],
      warnings: [],
      totalFiles: files.length,
      totalSize: 0,
    };

    this.emitProgress('processing', 0, files.length);

    for (let i = 0; i < files.length; i++) {
      const file = files[i];
      this.emitProgress('processing', i + 1, files.length, file.name);

      try {
        // Create a File-like object for validation
        const fileObj = {
          name: file.name,
          size: file.data.byteLength,
          type: mime.lookup(file.name) || 'application/octet-stream',
        } as File;

        // Validate
        const validation = this.validateFile(fileObj, options);
        if (!validation.valid) {
          result.failed.push({
            filename: file.name,
            reason: validation.reason!,
          });
          continue;
        }

        result.totalSize += file.data.byteLength;

        // Determine types
        const assetType = this.determineAssetTypeFromPath(file.name);
        const category = options.defaultCategory || this.determineCategory(assetType);

        // Process
        const processed = await this.processFile(fileObj, file.data, assetType, options);

        // Create metadata
        const metadata: Omit<AssetMetadata, 'id' | 'createdAt' | 'updatedAt' | 'version' | 'size' | 'checksum'> = {
          name: processed.name || file.name,
          type: assetType,
          category,
          mimeType: fileObj.type,
          tags: [...(options.defaultTags || []), ...(processed.tags || [])],
          uploadedBy: 'import',
          customProperties: processed.metadata || {},
        };

        const asset = await this.assetManager.addAsset(processed.data, metadata);
        result.imported.push(asset);

      } catch (error) {
        result.failed.push({
          filename: file.name,
          reason: error instanceof Error ? error.message : 'Unknown error',
          error: error instanceof Error ? error : undefined,
        });
      }
    }

    result.success = result.failed.length === 0;
    this.emitProgress('complete', result.totalFiles, result.totalFiles);

    return result;
  }

  /**
   * Import a content package
   */
  async importContentPackage(packageData: ArrayBuffer, options: ImportOptions = {}): Promise<ImportResult> {
    const contentPackage = await this.parseContentPackage(packageData);

    const result: ImportResult = {
      success: true,
      imported: [],
      failed: [],
      warnings: [],
      totalFiles: contentPackage.assets.size,
      totalSize: 0,
    };

    this.emitProgress('validating', 0, contentPackage.assets.size);

    // Validate manifest
    if (!this.validateManifest(contentPackage.manifest)) {
      throw new Error('Invalid content package manifest');
    }

    let current = 0;
    for (const [assetId, data] of contentPackage.assets) {
      current++;
      this.emitProgress('storing', current, contentPackage.assets.size, assetId);

      try {
        const manifestEntry = contentPackage.manifest.assets.find(a => a.id === assetId);
        if (!manifestEntry) {
          result.failed.push({
            filename: assetId,
            reason: 'Asset not found in manifest',
          });
          continue;
        }

        result.totalSize += data.byteLength;

        // Verify checksum
        const checksum = await this.calculateChecksum(data);
        if (checksum !== manifestEntry.checksum) {
          result.warnings.push(`Checksum mismatch for ${manifestEntry.name}`);
        }

        // Create metadata from manifest
        const metadata: Omit<AssetMetadata, 'id' | 'createdAt' | 'updatedAt' | 'version' | 'size' | 'checksum'> = {
          name: manifestEntry.name,
          type: manifestEntry.type,
          category: manifestEntry.category,
          mimeType: mime.lookup(manifestEntry.path) || 'application/octet-stream',
          tags: [...(options.defaultTags || []), ...(manifestEntry.tags || [])],
          uploadedBy: 'package-import',
          customProperties: manifestEntry.metadata || {},
        };

        const asset = await this.assetManager.addAsset(data, metadata);
        result.imported.push(asset);

      } catch (error) {
        result.failed.push({
          filename: assetId,
          reason: error instanceof Error ? error.message : 'Unknown error',
          error: error instanceof Error ? error : undefined,
        });
      }
    }

    result.success = result.failed.length === 0;
    this.emitProgress('complete', result.totalFiles, result.totalFiles);

    return result;
  }

  /**
   * Register a custom content processor
   */
  registerProcessor(mimeType: string, processor: ContentProcessor): void {
    this.processors.set(mimeType, processor);
  }

  private validateFile(file: File, options: ImportOptions): { valid: boolean; reason?: string } {
    // Check file size
    if (options.maxFileSize && file.size > options.maxFileSize) {
      return { valid: false, reason: `File too large: ${file.size} bytes` };
    }

    // Check allowed types
    if (options.allowedTypes && !options.allowedTypes.includes(file.type)) {
      return { valid: false, reason: `File type not allowed: ${file.type}` };
    }

    // Check for empty files
    if (file.size === 0) {
      return { valid: false, reason: 'Empty file' };
    }

    return { valid: true };
  }

  private async readFile(file: File): Promise<ArrayBuffer> {
    return new Promise((_resolve, __reject) => {
      const reader = new FileReader();
      reader.onload = () => resolve(reader.result as ArrayBuffer);
      reader.onerror = () => reject(reader.error);
      reader.readAsArrayBuffer(file);
    });
  }

  private determineAssetType(file: File): AssetType {
    return this.determineAssetTypeFromPath(file.name);
  }

  private determineAssetTypeFromPath(path: string): AssetType {
    const ext = path.split('.').pop()?.toLowerCase();

    switch (ext) {
      case 'jpg':
      case 'jpeg':
      case 'png':
      case 'gif':
      case 'webp':
      case 'svg':
        return 'image';

      case 'mp3':
      case 'wav':
      case 'ogg':
      case 'flac':
        return 'audio';

      case 'obj':
      case 'fbx':
      case 'gltf':
      case 'glb':
        return 'model';

      case 'glsl':
      case 'vert':
      case 'frag':
        return 'shader';

      case 'ttf':
      case 'otf':
      case 'woff':
      case 'woff2':
        return 'font';

      case 'json':
      case 'xml':
      case 'yaml':
      case 'yml':
        return 'data';

      default:
        return 'data';
    }
  }

  private determineCategory(type: AssetType): AssetCategory {
    switch (type) {
      case 'image':
      case 'model':
        return 'characters';
      case 'audio':
        return 'environments';
      case 'shader':
      case 'font':
        return 'ui';
      default:
        return 'user';
    }
  }

  private async processFile(
    file: File, 
    data: ArrayBuffer, 
    type: AssetType, 
    options: ImportOptions
  ): Promise<{ name?: string; data: ArrayBuffer; tags?: string[]; metadata?: any }> {
    const processor = this.processors.get(file.type);
    if (processor) {
      return await processor.process(file, data, type, options);
    }

    // Default processing - just return as-is
    return { data };
  }

  private async parseContentPackage(data: ArrayBuffer): Promise<ContentPackage> {
    const zip = await JSZip.loadAsync(data);

    // Read manifest
    const manifestFile = zip.file('manifest.json');
    if (!manifestFile) {
      throw new Error('Content package missing manifest.json');
    }

    const manifestData = await manifestFile.async('text');
    const manifest: PackageManifest = JSON.parse(manifestData);

    // Read assets
    const assets = new Map<string, ArrayBuffer>();
    for (const assetEntry of manifest.assets) {
      const assetFile = zip.file(assetEntry.path);
      if (assetFile) {
        const assetData = await assetFile.async('arraybuffer');
        assets.set(assetEntry.id, assetData);
      }
    }

    // Read metadata
    const metadata = new Map<string, any>();
    const metadataFile = zip.file('metadata.json');
    if (metadataFile) {
      const metadataData = await metadataFile.async('text');
      const metadataObj = JSON.parse(metadataData);
      for (const [key, value] of Object.entries(metadataObj)) {
        metadata.set(key, value);
      }
    }

    return { manifest, assets, metadata };
  }

  private validateManifest(manifest: PackageManifest): boolean {
    return !!(manifest.name && manifest.version && manifest.assets && Array.isArray(manifest.assets));
  }

  private async calculateChecksum(data: ArrayBuffer): Promise<string> {
    const crypto = globalThis.crypto || require('crypto');
    const hashBuffer = await crypto.subtle.digest('SHA-256', data);
    return Array.from(new Uint8Array(hashBuffer))
      .map(b => b.toString(16).padStart(2, '0'))
      .join('');
  }

  private setupDefaultProcessors(): void {
    // Image processor
    this.processors.set('image/jpeg', new ImageProcessor());
    this.processors.set('image/png', new ImageProcessor());
    this.processors.set('image/gif', new ImageProcessor());
    this.processors.set('image/webp', new ImageProcessor());

    // Audio processor
    this.processors.set('audio/mpeg', new AudioProcessor());
    this.processors.set('audio/wav', new AudioProcessor());
    this.processors.set('audio/ogg', new AudioProcessor());

    // JSON data processor
    this.processors.set('application/json', new JSONProcessor());
  }

  private emitProgress(stage: ImportProgress['stage'], current: number, total: number, currentFile?: string): void {
    this.emit('progress', {
      stage,
      current,
      total,
      currentFile,
      errors: [],
    } as ImportProgress);
  }
}

export interface ContentProcessor {
  process(file: File, data: ArrayBuffer, type: AssetType, options: ImportOptions): Promise<{
    name?: string;
    data: ArrayBuffer;
    tags?: string[];
    metadata?: any;
  }>;
}

/**
 * Image content processor
 */
class ImageProcessor implements ContentProcessor {
  async process(file: File, data: ArrayBuffer): Promise<{ data: ArrayBuffer; tags?: string[]; metadata?: any }> {
    // In a real implementation, you might:
    // - Extract EXIF data
    // - Generate thumbnails
    // - Optimize image size
    // - Extract color palette

    const metadata = {
      originalName: file.name,
      originalSize: data.byteLength,
      lastModified: file.lastModified,
    };

    const tags = ['image'];

    return { data, tags, metadata };
  }
}

/**
 * Audio content processor
 */
class AudioProcessor implements ContentProcessor {
  async process(file: File, data: ArrayBuffer): Promise<{ data: ArrayBuffer; tags?: string[]; metadata?: any }> {
    // In a real implementation, you might:
    // - Extract audio metadata (duration, bitrate, etc.)
    // - Generate waveform preview
    // - Normalize audio levels

    const metadata = {
      originalName: file.name,
      originalSize: data.byteLength,
      lastModified: file.lastModified,
    };

    const tags = ['audio'];

    return { data, tags, metadata };
  }
}

/**
 * JSON data processor
 */
class JSONProcessor implements ContentProcessor {
  async process(file: File, data: ArrayBuffer): Promise<{ data: ArrayBuffer; tags?: string[]; metadata?: any }> {
    try {
      const text = new TextDecoder().decode(data);
      const json = JSON.parse(text);

      const metadata = {
        originalName: file.name,
        jsonSchema: this.inferSchema(json),
        keys: Object.keys(json).length,
      };

      const tags = ['data', 'json'];

      return { data, tags, metadata };
    } catch (error) {
      throw new Error(`Invalid JSON: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  private inferSchema(obj: any): string {
    if (Array.isArray(obj)) {
      return 'array';
    } else if (typeof obj === 'object' && obj !== null) {
      return 'object';
    } else {
      return typeof obj;
    }
  }
}

/**
 * Content Validation System
 * Validates assets, content packages, and enforces content policies
 */

import { AssetMetadata, AssetType, AssetCategory } from './AssetManager';
import { PackageManifest } from './ContentImporter';

export interface ValidationRule {
  name: string;
  description: string;
  type: 'warning' | 'error';
  validator: (context: ValidationContext) => ValidationResult;
}

export interface ValidationContext {
  asset?: AssetMetadata;
  data?: ArrayBuffer;
  manifest?: PackageManifest;
  filename?: string;
  mimeType?: string;
}

export interface ValidationResult {
  valid: boolean;
  issues: ValidationIssue[];
}

export interface ValidationIssue {
  type: 'error' | 'warning' | 'info';
  code: string;
  message: string;
  field?: string;
  suggestion?: string;
}

export interface ContentPolicy {
  maxFileSize: number; // bytes
  maxTotalSize: number; // bytes for packages
  allowedTypes: AssetType[];
  allowedCategories: AssetCategory[];
  allowedMimeTypes: string[];
  blockedMimeTypes: string[];
  requireMetadata: string[]; // required metadata fields
  namePattern?: RegExp;
  tagRestrictions?: {
    required: string[];
    forbidden: string[];
    maxCount: number;
  };
  customRules: ValidationRule[];
}

export class ContentValidator {
  private rules = new Map<string, ValidationRule>();
  private policy: ContentPolicy;

  constructor(policy: ContentPolicy) {
    this.policy = policy;
    this.setupDefaultRules();
    this.setupPolicyRules();
  }

  /**
   * Validate an asset
   */
  validateAsset(asset: AssetMetadata, data?: ArrayBuffer): ValidationResult {
    const context: ValidationContext = { asset, data };
    return this.runValidation(context);
  }

  /**
   * Validate a file before import
   */
  validateFile(filename: string, mimeType: string, data: ArrayBuffer): ValidationResult {
    const context: ValidationContext = { filename, mimeType, data };
    return this.runValidation(context);
  }

  /**
   * Validate a content package
   */
  validatePackage(manifest: PackageManifest, totalSize: number): ValidationResult {
    const context: ValidationContext = { manifest };
    const result = this.runValidation(context);

    // Additional package-specific validation
    if (totalSize > this.policy.maxTotalSize) {
      result.issues.push({
        type: 'error',
        code: 'PACKAGE_TOO_LARGE',
        message: `Package size ${this.formatBytes(totalSize)} exceeds maximum ${this.formatBytes(this.policy.maxTotalSize)}`,
        suggestion: 'Reduce package size by removing or compressing assets',
      });
      result.valid = false;
    }

    return result;
  }

  /**
   * Validate content integrity
   */
  async validateIntegrity(asset: AssetMetadata, data: ArrayBuffer): Promise<ValidationResult> {
    const result: ValidationResult = { valid: true, issues: [] };

    // Check size matches
    if (data.byteLength !== asset.size) {
      result.issues.push({
        type: 'error',
        code: 'SIZE_MISMATCH',
        message: `Asset size mismatch: expected ${asset.size}, got ${data.byteLength}`,
      });
      result.valid = false;
    }

    // Check checksum
    try {
      const checksum = await this.calculateChecksum(data);
      if (checksum !== asset.checksum) {
        result.issues.push({
          type: 'error',
          code: 'CHECKSUM_MISMATCH',
          message: 'Asset checksum does not match expected value',
          suggestion: 'Asset may be corrupted or modified',
        });
        result.valid = false;
      }
    } catch (error) {
      result.issues.push({
        type: 'error',
        code: 'CHECKSUM_ERROR',
        message: `Failed to calculate checksum: ${error instanceof Error ? error.message : 'Unknown error'}`,
      });
      result.valid = false;
    }

    return result;
  }

  /**
   * Register a custom validation rule
   */
  registerRule(rule: ValidationRule): void {
    this.rules.set(rule.name, rule);
  }

  /**
   * Remove a validation rule
   */
  removeRule(name: string): void {
    this.rules.delete(name);
  }

  /**
   * Get all registered rules
   */
  getRules(): ValidationRule[] {
    return Array.from(this.rules.values());
  }

  /**
   * Update content policy
   */
  updatePolicy(policy: Partial<ContentPolicy>): void {
    this.policy = { ...this.policy, ...policy };
    this.setupPolicyRules(); // Recreate policy-based rules
  }

  private runValidation(context: ValidationContext): ValidationResult {
    const result: ValidationResult = { valid: true, issues: [] };

    for (const rule of this.rules.values()) {
      try {
        const ruleResult = rule.validator(context);
        result.issues.push(...ruleResult.issues);

        if (!ruleResult.valid && rule.type === 'error') {
          result.valid = false;
        }
      } catch (error) {
        result.issues.push({
          type: 'error',
          code: 'RULE_ERROR',
          message: `Validation rule "${rule.name}" failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
        });
        result.valid = false;
      }
    }

    return result;
  }

  private setupDefaultRules(): void {
    // File name validation
    this.rules.set('filename', _{
      name: 'filename',
      _description: 'Validates file names',
      _type: 'error',
      _validator: (_context) => {
        const result: ValidationResult = { valid: true, issues: [] };
        const filename = context.filename || context.asset?.name;

        if (!filename) {
          return result;
        }

        // Check for dangerous characters
        if (/[<>:"/\\|?*\x00-\x1f]/.test(filename)) {
          result.issues.push({
            type: 'error',
            code: 'INVALID_FILENAME',
            message: 'Filename contains invalid characters',
            suggestion: 'Remove special characters from filename',
          });
          result.valid = false;
        }

        // Check length
        if (filename.length > 255) {
          result.issues.push({
            type: 'error',
            code: 'FILENAME_TOO_LONG',
            message: 'Filename exceeds 255 characters',
            suggestion: 'Shorten the filename',
          });
          result.valid = false;
        }

        // Check for reserved names (Windows)
        const reservedNames = ['CON', 'PRN', 'AUX', 'NUL', 'COM1', 'COM2', 'COM3', 'COM4', 'COM5', 'COM6', 'COM7', 'COM8', 'COM9', 'LPT1', 'LPT2', 'LPT3', 'LPT4', 'LPT5', 'LPT6', 'LPT7', 'LPT8', 'LPT9'];
        const baseName = filename.split('.')[0].toUpperCase();
        if (reservedNames.includes(baseName)) {
          result.issues.push({
            type: 'warning',
            code: 'RESERVED_FILENAME',
            message: 'Filename may conflict with system reserved names',
            suggestion: 'Consider using a different name',
          });
        }

        return result;
      },
    });

    // Asset metadata validation
    this.rules.set('metadata', _{
      name: 'metadata',
      _description: 'Validates asset metadata completeness',
      _type: 'warning',
      _validator: (_context) => {
        const result: ValidationResult = { valid: true, issues: [] };
        const asset = context.asset;

        if (!asset) {
          return result;
        }

        // Check required fields
        if (!asset.name || asset.name.trim().length === 0) {
          result.issues.push({
            type: 'error',
            code: 'MISSING_NAME',
            message: 'Asset name is required',
            field: 'name',
          });
          result.valid = false;
        }

        if (!asset.uploadedBy) {
          result.issues.push({
            type: 'warning',
            code: 'MISSING_UPLOADER',
            message: 'Asset uploader not specified',
            field: 'uploadedBy',
          });
        }

        if (!asset.tags || asset.tags.length === 0) {
          result.issues.push({
            type: 'info',
            code: 'NO_TAGS',
            message: 'Asset has no tags for organization',
            field: 'tags',
            suggestion: 'Add descriptive tags to improve searchability',
          });
        }

        return result;
      },
    });

    // Content type validation
    this.rules.set('content-type', _{
      name: 'content-type',
      _description: 'Validates content type and MIME type consistency',
      _type: 'error',
      _validator: (_context) => {
        const result: ValidationResult = { valid: true, issues: [] };
        const mimeType = context.mimeType || context.asset?.mimeType;
        const assetType = context.asset?.type;

        if (!mimeType) {
          return result;
        }

        // Check MIME type format
        if (!/^[a-zA-Z][a-zA-Z0-9]*/[a-zA-Z0-9][a-zA-Z0-9\-\._]*$/.test(mimeType)) {
          result.issues.push({
            type: 'error',
            code: 'INVALID_MIME_TYPE',
            message: 'Invalid MIME type format',
            field: 'mimeType',
          });
          result.valid = false;
        }

        // Check consistency with asset type
        if (assetType) {
          const expectedMimeTypes = this.getExpectedMimeTypes(assetType);
          if (expectedMimeTypes.length > 0 && !expectedMimeTypes.some(expected => mimeType.startsWith(expected))) {
            result.issues.push({
              type: 'warning',
              code: 'MIME_TYPE_MISMATCH',
              message: `MIME type "${mimeType}" may not match asset type "${assetType}"`,
              suggestion: `Expected MIME types: ${expectedMimeTypes.join(', ')}`,
            });
          }
        }

        return result;
      },
    });

    // Security validation
    this.rules.set('security', _{
      name: 'security',
      _description: 'Performs basic security checks',
      _type: 'error',
      _validator: (_context) => {
        const result: ValidationResult = { valid: true, issues: [] };
        const data = context.data;
        const mimeType = context.mimeType || context.asset?.mimeType;

        if (!data || !mimeType) {
          return result;
        }

        // Check for executable file signatures
        const executableSignatures = [
          [0x4D, 0x5A], // PE executable
          [0x7F, 0x45, 0x4C, 0x46], // ELF executable
          [0xCF, 0xFA, 0xED, 0xFE], // Mach-O binary
          [0x23, 0x21], // Script with shebang
        ];

        const bytes = new Uint8Array(data.slice(0, 10));
        for (const signature of executableSignatures) {
          if (this.matchesSignature(bytes, signature)) {
            result.issues.push({
              type: 'error',
              code: 'EXECUTABLE_CONTENT',
              message: 'File appears to contain executable code',
              suggestion: 'Executable files are not allowed for security reasons',
            });
            result.valid = false;
            break;
          }
        }

        // Check for suspicious script content
        if (mimeType.includes('text') || mimeType.includes('json')) {
          const text = new TextDecoder('utf-8', { fatal: false }).decode(data.slice(0, 1024));
          const suspiciousPatterns = [
            /<script[^>]*>.*</script>/i,
            /javascript:/i,
            /eval\s*\(/i,
            /document\.write/i,
            /innerHTML/i,
          ];

          for (const pattern of suspiciousPatterns) {
            if (pattern.test(text)) {
              result.issues.push({
                type: 'warning',
                code: 'SUSPICIOUS_CONTENT',
                message: 'File contains potentially unsafe script content',
                suggestion: 'Review file content for security risks',
              });
              break;
            }
          }
        }

        return result;
      },
    });
  }

  private setupPolicyRules(): void {
    // Remove existing policy rules
    for (const [name, rule] of this.rules) {
      if (name.startsWith('policy')) {
        this.rules.delete(name);
      }
    }

    // File size policy
    this.rules.set('policy_size', _{
      name: 'policy_size',
      _description: 'Enforces file size policy',
      _type: 'error',
      _validator: (_context) => {
        const result: ValidationResult = { valid: true, issues: [] };
        const size = context.data?.byteLength || context.asset?.size;

        if (size && size > this.policy.maxFileSize) {
          result.issues.push({
            type: 'error',
            code: 'FILE_TOO_LARGE',
            message: `File size ${this.formatBytes(size)} exceeds maximum ${this.formatBytes(this.policy.maxFileSize)}`,
            suggestion: 'Compress or reduce file size',
          });
          result.valid = false;
        }

        return result;
      },
    });

    // Asset type policy
    this.rules.set('policy_type', _{
      name: 'policy_type',
      _description: 'Enforces allowed asset types',
      _type: 'error',
      _validator: (_context) => {
        const result: ValidationResult = { valid: true, issues: [] };
        const assetType = context.asset?.type;

        if (assetType && !this.policy.allowedTypes.includes(assetType)) {
          result.issues.push({
            type: 'error',
            code: 'TYPE_NOT_ALLOWED',
            message: `Asset type "${assetType}" is not allowed`,
            suggestion: `Allowed types: ${this.policy.allowedTypes.join(', ')}`,
          });
          result.valid = false;
        }

        return result;
      },
    });

    // MIME type policy
    this.rules.set('policy_mime', _{
      name: 'policy_mime',
      _description: 'Enforces MIME type restrictions',
      _type: 'error',
      _validator: (_context) => {
        const result: ValidationResult = { valid: true, issues: [] };
        const mimeType = context.mimeType || context.asset?.mimeType;

        if (!mimeType) {
          return result;
        }

        // Check blocked MIME types
        if (this.policy.blockedMimeTypes.some(blocked => mimeType.startsWith(blocked))) {
          result.issues.push({
            type: 'error',
            code: 'MIME_TYPE_BLOCKED',
            message: `MIME type "${mimeType}" is blocked`,
          });
          result.valid = false;
        }

        // Check allowed MIME types (if specified)
        if (this.policy.allowedMimeTypes.length > 0 && 
            !this.policy.allowedMimeTypes.some(allowed => mimeType.startsWith(allowed))) {
          result.issues.push({
            type: 'error',
            code: 'MIME_TYPE_NOT_ALLOWED',
            message: `MIME type "${mimeType}" is not allowed`,
            suggestion: `Allowed MIME types: ${this.policy.allowedMimeTypes.join(', ')}`,
          });
          result.valid = false;
        }

        return result;
      },
    });
  }

  private getExpectedMimeTypes(assetType: AssetType): string[] {
    switch (assetType) {
      case 'image':
        return ['image/'];
      case 'audio':
        return ['audio/'];
      case 'model':
        return ['model/', 'application/'];
      case 'font':
        return ['font/', 'application/font'];
      case 'data':
        return ['application/json', 'text/', 'application/xml'];
      default:
        return [];
    }
  }

  private matchesSignature(data: Uint8Array, signature: number[]): boolean {
    if (data.length < signature.length) {
      return false;
    }

    for (let i = 0; i < signature.length; i++) {
      if (data[i] !== signature[i]) {
        return false;
      }
    }

    return true;
  }

  private formatBytes(bytes: number): string {
    if (bytes === 0) return '0 B';

    const k = 1024;
    const sizes = ['B', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));

    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  }

  private async calculateChecksum(data: ArrayBuffer): Promise<string> {
    const crypto = globalThis.crypto || require('crypto');
    const hashBuffer = await crypto.subtle.digest('SHA-256', data);
    return Array.from(new Uint8Array(hashBuffer))
      .map(b => b.toString(16).padStart(2, '0'))
      .join('');
  }
}

/**
 * Default content policy for VTT systems
 */
export const DEFAULT_CONTENT_POLICY: ContentPolicy = {
  maxFileSize: 50 * 1024 * 1024, // 50MB per file
  maxTotalSize: 500 * 1024 * 1024, // 500MB per package
  allowedTypes: ['image', 'audio', 'model', 'map', 'token', 'scene', 'campaign', 'data', 'template'],
  allowedCategories: ['characters', 'environments', 'items', 'effects', 'ui', 'system', 'user'],
  allowedMimeTypes: [
    'image/',
    'audio/',
    'application/json',
    'text/',
    'model/',
    'font/',
  ],
  blockedMimeTypes: [
    'application/x-executable',
    'application/x-msdownload',
    'application/x-dosexec',
    'application/vnd.microsoft.portable-executable',
  ],
  requireMetadata: ['name', 'type', 'category'],
  namePattern: /^[a-zA-Z0-9\s\-_\.\(\)]+$/,
  tagRestrictions: {
    required: [],
    forbidden: ['admin', 'system', 'hidden'],
    maxCount: 20,
  },
  customRules: [],
};

/**
 * Asset Manager Tests
 * Comprehensive test suite for asset management functionality
 */

import { AssetManager, MemoryStorageProvider } from '../AssetManager';

describe('AssetManager', () => {
  let assetManager: AssetManager;
  let storageProvider: MemoryStorageProvider;

  beforeEach(() => {
    storageProvider = new MemoryStorageProvider();
    assetManager = new AssetManager({
      storageProvider,
      cacheSize: 100,
      enableVersioning: true,
    });
  });

  afterEach(async () => {
    await assetManager.clear();
  });

  describe('Asset Storage and Retrieval', () => {
    test('should store and retrieve assets', async () => {
      const assetData = {
        name: 'Test Image',
        type: 'image',
        tags: ['test', 'sample'],
        metadata: { format: 'png', width: 100, height: 100 },
      };

      const asset = await assetManager.addAsset('test-image.png', assetData);
      expect(asset.id).toBeDefined();
      expect(asset.name).toBe('Test Image');
      expect(asset.type).toBe('image');

      const retrieved = await assetManager.getAsset(asset.id);
      expect(retrieved).toBeDefined();
      expect(retrieved?.name).toBe('Test Image');
    });

    test('should generate unique IDs for assets', async () => {
      const asset1 = await assetManager.addAsset('file1.png', { name: 'Asset 1', type: 'image' });
      const asset2 = await assetManager.addAsset('file2.png', { name: 'Asset 2', type: 'image' });

      expect(asset1.id).not.toBe(asset2.id);
    });

    test('should handle asset not found', async () => {
      const asset = await assetManager.getAsset('non-existent-id');
      expect(asset).toBeNull();
    });

    test('should list all assets', async () => {
      await assetManager.addAsset('asset1.png', { name: 'Asset 1', type: 'image' });
      await assetManager.addAsset('asset2.jpg', { name: 'Asset 2', type: 'image' });
      await assetManager.addAsset('sound1.mp3', { name: 'Sound 1', type: 'audio' });

      const assets = await assetManager.listAssets();
      expect(assets).toHaveLength(3);
      expect(assets.map(a => a.name)).toContain('Asset 1');
      expect(assets.map(a => a.name)).toContain('Asset 2');
      expect(assets.map(a => a.name)).toContain('Sound 1');
    });
  });

  describe('Asset Filtering and Search', () => {
    beforeEach(async () => {
      await assetManager.addAsset('image1.png', {
        name: 'Forest Scene',
        type: 'image',
        tags: ['forest', 'nature', 'outdoor'],
        metadata: { format: 'png', size: 1024 },
      });

      await assetManager.addAsset('image2.jpg', {
        name: 'Dungeon Map',
        type: 'image',
        tags: ['dungeon', 'indoor', 'map'],
        metadata: { format: 'jpg', size: 2048 },
      });

      await assetManager.addAsset('audio1.mp3', {
        name: 'Forest Sounds',
        type: 'audio',
        tags: ['forest', 'ambient', 'nature'],
        metadata: { format: 'mp3', duration: 120 },
      });
    });

    test('should filter assets by type', async () => {
      const imageAssets = await assetManager.searchAssets({ type: 'image' });
      expect(imageAssets).toHaveLength(2);
      expect(imageAssets.every(a => a.type === 'image')).toBe(true);

      const audioAssets = await assetManager.searchAssets({ type: 'audio' });
      expect(audioAssets).toHaveLength(1);
      expect(audioAssets[0].type).toBe('audio');
    });

    test('should filter assets by tags', async () => {
      const forestAssets = await assetManager.searchAssets({ tags: ['forest'] });
      expect(forestAssets).toHaveLength(2);
      expect(forestAssets.every(a => a.tags.includes('forest'))).toBe(true);

      const dungeonAssets = await assetManager.searchAssets({ tags: ['dungeon'] });
      expect(dungeonAssets).toHaveLength(1);
      expect(dungeonAssets[0].name).toBe('Dungeon Map');
    });

    test('should filter assets by multiple criteria', async () => {
      const forestImages = await assetManager.searchAssets({
        type: 'image',
        tags: ['forest'],
      });
      expect(forestImages).toHaveLength(1);
      expect(forestImages[0].name).toBe('Forest Scene');
    });

    test('should search assets by name', async () => {
      const results = await assetManager.searchAssets({ name: 'Forest' });
      expect(results).toHaveLength(2);
      expect(results.map(a => a.name)).toContain('Forest Scene');
      expect(results.map(a => a.name)).toContain('Forest Sounds');
    });

    test('should search assets by metadata', async () => {
      const pngAssets = await assetManager.searchAssets({
        metadata: { format: 'png' },
      });
      expect(pngAssets).toHaveLength(1);
      expect(pngAssets[0].name).toBe('Forest Scene');
    });
  });

  describe('Asset Updates and Deletion', () => {
    test('should update asset metadata', async () => {
      const asset = await assetManager.addAsset('test.png', {
        name: 'Original Name',
        type: 'image',
        tags: ['old'],
      });

      const updated = await assetManager.updateAsset(asset.id, {
        name: 'Updated Name',
        tags: ['new', 'updated'],
        metadata: { edited: true },
      });

      expect(updated.name).toBe('Updated Name');
      expect(updated.tags).toEqual(['new', 'updated']);
      expect(updated.metadata.edited).toBe(true);
      expect(updated.updatedAt.getTime()).toBeGreaterThan(asset.updatedAt.getTime());
    });

    test('should delete assets', async () => {
      const asset = await assetManager.addAsset('delete-me.png', {
        name: 'To Delete',
        type: 'image',
      });

      expect(await assetManager.getAsset(asset.id)).not.toBeNull();

      await assetManager.deleteAsset(asset.id);

      expect(await assetManager.getAsset(asset.id)).toBeNull();
    });

    test('should handle update of non-existent asset', async () => {
      await expect(assetManager.updateAsset('non-existent', { name: 'New Name' }))
        .rejects.toThrow('Asset not found');
    });

    test('should handle deletion of non-existent asset', async () => {
      await expect(assetManager.deleteAsset('non-existent'))
        .rejects.toThrow('Asset not found');
    });
  });

  describe('Asset Dependencies', () => {
    test('should track asset dependencies', async () => {
      const texture = await assetManager.addAsset('texture.png', {
        name: 'Wall Texture',
        type: 'image',
      });

      const scene = await assetManager.addAsset('scene.json', {
        name: 'Dungeon Scene',
        type: 'scene',
        dependencies: [texture.id],
      });

      const dependencies = await assetManager.getDependencies(scene.id);
      expect(dependencies).toHaveLength(1);
      expect(dependencies[0].id).toBe(texture.id);

      const dependents = await assetManager.getDependents(texture.id);
      expect(dependents).toHaveLength(1);
      expect(dependents[0].id).toBe(scene.id);
    });

    test('should prevent deletion of assets with dependents', async () => {
      const texture = await assetManager.addAsset('texture.png', {
        name: 'Wall Texture',
        type: 'image',
      });

      await assetManager.addAsset('scene.json', {
        name: 'Scene',
        type: 'scene',
        dependencies: [texture.id],
      });

      await expect(assetManager.deleteAsset(texture.id))
        .rejects.toThrow('Cannot delete asset with dependents');
    });

    test('should allow forced deletion of assets with dependents', async () => {
      const texture = await assetManager.addAsset('texture.png', {
        name: 'Wall Texture',
        type: 'image',
      });

      const scene = await assetManager.addAsset('scene.json', {
        name: 'Scene',
        type: 'scene',
        dependencies: [texture.id],
      });

      await assetManager.deleteAsset(texture.id, { force: true });

      // Texture should be deleted
      expect(await assetManager.getAsset(texture.id)).toBeNull();

      // Scene should still exist but dependency should be removed
      const updatedScene = await assetManager.getAsset(scene.id);
      expect(updatedScene?.dependencies).toHaveLength(0);
    });
  });

  describe('Asset Versioning', () => {
    test('should create versions when assets are updated', async () => {
      const asset = await assetManager.addAsset('versioned.png', {
        name: 'Versioned Asset',
        type: 'image',
        metadata: { version: 1 },
      });

      // Update to create new version
      await assetManager.updateAsset(asset.id, {
        metadata: { version: 2 },
      });

      const versions = await assetManager.getAssetVersions(asset.id);
      expect(versions).toHaveLength(2);
      expect(versions[0].metadata.version).toBe(1); // Original
      expect(versions[1].metadata.version).toBe(2); // Updated
    });

    test('should restore asset to previous version', async () => {
      const asset = await assetManager.addAsset('restore-test.png', {
        name: 'Original Name',
        type: 'image',
      });

      // Update asset
      await assetManager.updateAsset(asset.id, {
        name: 'Modified Name',
      });

      const versions = await assetManager.getAssetVersions(asset.id);
      const originalVersion = versions[0];

      // Restore to original version
      const restored = await assetManager.restoreAssetVersion(asset.id, originalVersion.version);
      expect(restored.name).toBe('Original Name');
    });

    test('should limit number of versions stored', async () => {
      // Create asset manager with version limit
      const limitedVersionManager = new AssetManager({
        storageProvider: new MemoryStorageProvider(),
        enableVersioning: true,
        maxVersions: 3,
      });

      const asset = await limitedVersionManager.addAsset('limited.png', {
        name: 'Asset',
        type: 'image',
      });

      // Make multiple updates
      for (let i = 1; i <= 5; i++) {
        await limitedVersionManager.updateAsset(asset.id, {
          metadata: { iteration: i },
        });
      }

      const versions = await limitedVersionManager.getAssetVersions(asset.id);
      expect(versions.length).toBeLessThanOrEqual(3);
    });
  });

  describe('Asset Caching', () => {
    test('should cache frequently accessed assets', async () => {
      const asset = await assetManager.addAsset('cached.png', {
        name: 'Cached Asset',
        type: 'image',
      });

      // Access asset multiple times
      await assetManager.getAsset(asset.id);
      await assetManager.getAsset(asset.id);
      await assetManager.getAsset(asset.id);

      const stats = assetManager.getCacheStats();
      expect(stats.hits).toBeGreaterThan(0);
    });

    test('should evict assets from cache when limit reached', async () => {
      // Create manager with small cache
      const smallCacheManager = new AssetManager({
        storageProvider: new MemoryStorageProvider(),
        cacheSize: 2,
      });

      // Add more assets than cache size
      const asset1 = await smallCacheManager.addAsset('1.png', { name: 'Asset 1', type: 'image' });
      const asset2 = await smallCacheManager.addAsset('2.png', { name: 'Asset 2', type: 'image' });
      const asset3 = await smallCacheManager.addAsset('3.png', { name: 'Asset 3', type: 'image' });

      // Access all assets to populate cache
      await smallCacheManager.getAsset(asset1.id);
      await smallCacheManager.getAsset(asset2.id);
      await smallCacheManager.getAsset(asset3.id);

      const stats = smallCacheManager.getCacheStats();
      expect(stats.size).toBeLessThanOrEqual(2);
    });
  });

  describe('Bulk Operations', () => {
    test('should handle bulk asset import', async () => {
      const assetsData = [
        { filename: 'bulk1.png', data: { name: 'Bulk Asset 1', type: 'image' } },
        { filename: 'bulk2.png', data: { name: 'Bulk Asset 2', type: 'image' } },
        { filename: 'bulk3.mp3', data: { name: 'Bulk Asset 3', type: 'audio' } },
      ];

      const results = await assetManager.bulkAddAssets(assetsData);
      expect(results).toHaveLength(3);
      expect(results.every(r => r.success)).toBe(true);

      const assets = await assetManager.listAssets();
      expect(assets).toHaveLength(3);
    });

    test('should handle bulk asset deletion', async () => {
      const asset1 = await assetManager.addAsset('del1.png', { name: 'Delete 1', type: 'image' });
