  totalSize: number;
  byType: Record<string, number>;
  byCategory: Record<string, number>;
  topTags: Array<{ tag: string; count: number }>;
}

export interface AssetMetadata {
  id: string;
  name: string;
  type: AssetType;
  version: string;
  description: string;
  tags: string[];
  author: string;
  created: Date;
  modified: Date;
  size: number;
  checksum: string;
  filePath: string;

  // Technical metadata
  dimensions?: { width: number; height: number };
  duration?: number; // for audio/video
  format: string;
  compression?: string;
  colorSpace?: string;
  bitrate?: number;
  sampleRate?: number;

  // Content metadata  
  category: string;
  subcategory?: string;
  rarity?: 'common' | 'uncommon' | 'rare' | 'epic' | 'legendary';
  license: string;
  dependencies: string[];
  compatibleSystems: string[];

  // Usage metadata
