export class CampaignBuilder extends EventEmitter {
  private assetManager: AssetManager;
  private campaign!: Campaign;
  private templates = new Map<string, CampaignTemplate>();

  constructor(assetManager: AssetManager) {
    super();
    this.assetManager = assetManager;
  }

  // Campaign management
  createCampaign(
    name: string,
    system: string,
    description: string = '',
    templateId?: string
  ): Campaign {
    this.campaign = {
      id: this.generateId(),
      name,
      description,
      system,
      theme: '',
      genre: '',
      setting: '',
      startDate: new Date(),
      status: 'planning',

      characters: [],
      quests: [],
      locations: [],
      factions: [],
      scenes: [],
      sessions: [],
      players: [],
      timeline: [],

      settings: {
        experienceType: 'milestone',
        restingRules: 'standard',
        magicAvailability: 'standard',
        deathRules: 'standard',
        startingLevel: 1,
        maxLevel: 20,
        allowedBooks: [],
        bannedContent: [],
        houseRules: []
      },

      assetCollections: [],
      handouts: [],
      notes: [],

      created: new Date(),
      modified: new Date(),
      version: '1.0.0',
      author: '',
      collaborators: []
    };

    // Apply template if provided
    if (templateId) {
      const template = this.templates.get(templateId);
      if (template) {
        this.applyTemplate(template);
      }
    }

    this.emit('campaignCreated', this.campaign);
    return this.campaign;
  }

  loadCampaign(campaign: Campaign): void {
    this.campaign = { ...campaign };
    this.emit('campaignLoaded', this.campaign);
  }

  updateCampaign(updates: Partial<Campaign>): void {
    Object.assign(this.campaign, updates, { modified: new Date() });
    this.emit('campaignUpdated', this.campaign);
  }

  // Character management
  addCharacter(character: Omit<Character, 'id' | 'created' | 'modified'>): Character {
    const newCharacter: Character = {
      ...character,
      id: this.generateId(),
      created: new Date(),
      modified: new Date()
    };

    this.campaign.characters.push(newCharacter);
    this.updateCampaign({});
    this.emit('characterAdded', newCharacter);
    return newCharacter;
  }

  updateCharacter(characterId: string, updates: Partial<Character>): void {
    const character = this.campaign.characters.find(c => c.id === characterId);
    if (!character) return;

    Object.assign(character, updates, { modified: new Date() });
    this.updateCampaign({});
    this.emit('characterUpdated', character);
  }

  removeCharacter(characterId: string): void {
    const index = this.campaign.characters.findIndex(c => c.id === characterId);
    if (index === -1) return;

    const character = this.campaign.characters[index];
    this.campaign.characters.splice(index, 1);
    this.updateCampaign({});
    this.emit('characterRemoved', character);
  }

  // Quest management
  addQuest(quest: Omit<Quest, 'id' | 'created' | 'modified'>): Quest {
    const newQuest: Quest = {
      ...quest,
      id: this.generateId(),
      created: new Date(),
      modified: new Date()
    };

    this.campaign.quests.push(newQuest);
    this.updateCampaign({});
    this.emit('questAdded', newQuest);
    return newQuest;
  }

  updateQuestStatus(questId: string, status: Quest['status']): void {
    const quest = this.campaign.quests.find(q => q.id === questId);
    if (!quest) return;

    quest.status = status;
    quest.modified = new Date();
    this.updateCampaign({});
    this.emit('questStatusChanged', quest);
  }

  completeQuestObjective(questId: string, objectiveId: string): void {
    const quest = this.campaign.quests.find(q => q.id === questId);
    if (!quest) return;

    const objective = quest.objectives.find(o => o.id === objectiveId);
    if (!objective) return;

    objective.completed = true;
    quest.modified = new Date();

    // Check if all required objectives are complete
    const requiredObjectives = quest.objectives.filter(o => !o.optional);
    const completedRequired = requiredObjectives.filter(o => o.completed);

    if (completedRequired.length === requiredObjectives.length && quest.status === 'in_progress') {
      quest.status = 'completed';
    }

    this.updateCampaign({});
    this.emit('questObjectiveCompleted', quest, objective);
  }

  // Location management
  addLocation(location: Omit<Location, 'id' | 'created' | 'modified'>): Location {
    const newLocation: Location = {
      ...location,
      id: this.generateId(),
      created: new Date(),
      modified: new Date()
    };

    this.campaign.locations.push(newLocation);
    this.updateCampaign({});
    this.emit('locationAdded', newLocation);
    return newLocation;
  }

  connectLocations(
    locationId1: string,
    locationId2: string,
    distance: number,
    travelTime: string,
    method: string = 'road',
    difficulty: string = 'easy'
  ): void {
    const location1 = this.campaign.locations.find(l => l.id === locationId1);
    const location2 = this.campaign.locations.find(l => l.id === locationId2);

    if (!location1 || !location2) return;

    // Add connection from location1 to location2
    location1.connections.push({
      locationId: locationId2,
      distance,
      travelTime,
      method,
      difficulty
    });

    // Add reverse connection
    location2.connections.push({
      locationId: locationId1,
      distance,
      travelTime,
      method,
      difficulty
    });

    this.updateCampaign({});
    this.emit('locationsConnected', location1, location2);
  }

  // Faction management
  addFaction(faction: Omit<Faction, 'id' | 'created' | 'modified'>): Faction {
    const newFaction: Faction = {
      ...faction,
      id: this.generateId(),
      created: new Date(),
      modified: new Date()
    };

    this.campaign.factions.push(newFaction);
    this.updateCampaign({});
    this.emit('factionAdded', newFaction);
    return newFaction;
  }

  setFactionRelationship(
    factionId1: string,
    factionId2: string,
    relationship: 'ally' | 'enemy' | 'neutral'
  ): void {
    const faction1 = this.campaign.factions.find(f => f.id === factionId1);
    const faction2 = this.campaign.factions.find(f => f.id === factionId2);

    if (!faction1 || !faction2) return;

    // Remove existing relationships
    faction1.allies = faction1.allies.filter(id => id !== factionId2);
    faction1.enemies = faction1.enemies.filter(id => id !== factionId2);
    faction1.neutrals = faction1.neutrals.filter(id => id !== factionId2);

    faction2.allies = faction2.allies.filter(id => id !== factionId1);
    faction2.enemies = faction2.enemies.filter(id => id !== factionId1);
    faction2.neutrals = faction2.neutrals.filter(id => id !== factionId1);

    // Add new relationship
    if (relationship === 'ally') {
      faction1.allies.push(factionId2);
      faction2.allies.push(factionId1);
    } else if (relationship === 'enemy') {
      faction1.enemies.push(factionId2);
      faction2.enemies.push(factionId1);
    } else {
      faction1.neutrals.push(factionId2);
      faction2.neutrals.push(factionId1);
    }

    this.updateCampaign({});
    this.emit('factionRelationshipChanged', faction1, faction2, relationship);
  }

  // Session management
  addSession(
    title: string,
    date: Date,
    summary: string = '',
    duration: number = 0
  ): void {
    const session = {
      id: this.generateId(),
      number: this.campaign.sessions.length + 1,
      title,
      date,
      duration,
      summary,
      events: [],
      experience: 0,
      treasure: [],
      notes: ''
    };

    this.campaign.sessions.push(session);
    this.updateCampaign({});
    this.emit('sessionAdded', session);
  }

  // Timeline management
  addTimelineEvent(
    date: string,
    title: string,
    description: string,
    type: 'historical' | 'current' | 'future' | 'personal' = 'current',
    importance: number = 1
  ): void {
    const event = {
      id: this.generateId(),
      date,
      title,
      description,
      type,
      importance,
      secret: false,
      consequences: []
    };

    this.campaign.timeline.push(event);
    this.campaign.timeline.sort((_a, _b) => a.date.localeCompare(b.date));
    this.updateCampaign({});
    this.emit('timelineEventAdded', event);
  }

  // Note management
  addNote(
    title: string,
    content: string,
    type: 'general' | 'plot' | 'character' | 'session' | 'rules' = 'general',
    tags: string[] = []
  ): void {
    const note = {
      id: this.generateId(),
      title,
      content,
      type,
      tags,
      secret: false,
      created: new Date(),
      modified: new Date()
    };

    this.campaign.notes.push(note);
    this.updateCampaign({});
    this.emit('noteAdded', note);
  }

  // Asset integration
  async addHandout(
    name: string,
    description: string,
    assetId: string,
    isPublic: boolean = true,
    recipients: string[] = []
  ): Promise<void> {
    const asset = this.assetManager.getAsset(assetId);
    if (!asset) {
      throw new Error(`Asset not found: ${assetId}`);
    }

    const handout = {
      id: this.generateId(),
      name,
      description,
      assetId,
      public: isPublic,
      recipients
    };

    this.campaign.handouts.push(handout);
    this.updateCampaign({});
    this.emit('handoutAdded', handout);
  }

  // Template system
  saveAsTemplate(
    name: string,
    description: string,
    includeCharacters: boolean = true,
    includeQuests: boolean = true,
    includeLocations: boolean = true,
    includeFactions: boolean = true
  ): CampaignTemplate {
    const template: CampaignTemplate = {
      id: this.generateId(),
      name,
      description,
      system: this.campaign.system,
      theme: this.campaign.theme,
      estimatedSessions: this.campaign.sessions.length || 10,
      difficulty: 'intermediate',

      characters: includeCharacters ? this.campaign.characters.map(this.sanitizeForTemplate) : [],
      quests: includeQuests ? this.campaign.quests.map(this.sanitizeForTemplate) : [],
      locations: includeLocations ? this.campaign.locations.map(this.sanitizeForTemplate) : [],
      factions: includeFactions ? this.campaign.factions.map(this.sanitizeForTemplate) : [],
      scenes: [],

      setupInstructions: [],
      requiredAssets: [],
      optionalAssets: [],

      created: new Date(),
      author: this.campaign.author,
      tags: []
    };

    this.templates.set(template.id, template);
    this.emit('templateCreated', template);
    return template;
  }

  private applyTemplate(template: CampaignTemplate): void {
    this.campaign.theme = template.theme;
    this.campaign.system = template.system;

    // Apply template content
    template.characters.forEach(char => {
      if (char.name) {
        this.addCharacter(char as any);
      }
    });

    template.quests.forEach(quest => {
      if (quest.title) {
        this.addQuest(quest as any);
      }
    });

    template.locations.forEach(location => {
      if (location.name) {
        this.addLocation(location as any);
      }
    });

    template.factions.forEach(faction => {
      if (faction.name) {
        this.addFaction(faction as any);
      }
    });
  }

  private sanitizeForTemplate(item: any): any {
    const sanitized = { ...item };
    delete sanitized.id;
    delete sanitized.created;
    delete sanitized.modified;
    return sanitized;
  }

  // Export and analysis
  exportCampaign(): Campaign {
    return { ...this.campaign };
  }

  generateCampaignReport(): {
    overview: any;
    characters: any;
    quests: any;
    locations: any;
    factions: any;
    sessions: any;
    timeline: any;
  } {
    return {
      overview: {
        name: this.campaign.name,
        system: this.campaign.system,
        status: this.campaign.status,
        totalSessions: this.campaign.sessions.length,
        totalCharacters: this.campaign.characters.length,
        totalQuests: this.campaign.quests.length,
        totalLocations: this.campaign.locations.length,
        totalFactions: this.campaign.factions.length,
        created: this.campaign.created,
        lastModified: this.campaign.modified
      },
      characters: {
        total: this.campaign.characters.length,
        byType: this.groupBy(this.campaign.characters, 'type'),
        byLevel: this.groupBy(this.campaign.characters, 'level'),
        byClass: this.groupBy(this.campaign.characters, 'class')
      },
      quests: {
        total: this.campaign.quests.length,
        byStatus: this.groupBy(this.campaign.quests, 'status'),
        byType: this.groupBy(this.campaign.quests, 'type'),
        completionRate: this.campaign.quests.length > 0 ? 
          this.campaign.quests.filter(q => q.status === 'completed').length / this.campaign.quests.length : 0
      },
      locations: {
        total: this.campaign.locations.length,
        byType: this.groupBy(this.campaign.locations, 'type'),
        connections: this.campaign.locations.reduce((_sum, _loc) => sum + loc.connections.length, 0) / 2
      },
      factions: {
        total: this.campaign.factions.length,
        byType: this.groupBy(this.campaign.factions, 'type'),
        relationships: {
          allies: this.campaign.factions.reduce((_sum, _f) => sum + f.allies.length, 0) / 2,
          enemies: this.campaign.factions.reduce((_sum, _f) => sum + f.enemies.length, 0) / 2,
          neutral: this.campaign.factions.reduce((_sum, _f) => sum + f.neutrals.length, 0) / 2
        }
      },
      sessions: {
        total: this.campaign.sessions.length,
        totalDuration: this.campaign.sessions.reduce((_sum, _s) => sum + s.duration, 0),
        averageDuration: this.campaign.sessions.length > 0 ? 
          this.campaign.sessions.reduce((_sum, _s) => sum + s.duration, 0) / this.campaign.sessions.length : 0
      },
      timeline: {
        total: this.campaign.timeline.length,
        byType: this.groupBy(this.campaign.timeline, 'type'),
        byImportance: this.groupBy(this.campaign.timeline, 'importance')
      }
    };
  }

  private groupBy(array: any[], key: string): Record<string, number> {
    return array.reduce((acc, item) => {
      const value = item[key]?.toString() || 'unknown';
      acc[value] = (acc[value] || 0) + 1;
      return acc;
    }, {});
  }

  private generateId(): string {
    return Math.random().toString(36).substr(2, 9);
  }

  // Getters
  getCampaign(): Campaign {
    return this.campaign;
  }

  getTemplates(): CampaignTemplate[] {
    return Array.from(this.templates.values());
  }
}

/**
 * Visual content editor and authoring tools for VTT
 */

import { EventEmitter } from 'events';
import { AssetManager, _AssetMetadata} from './AssetManager';

export interface EditorTool {
  id: string;
  name: string;
  icon: string;
  category: 'drawing' | 'selection' | 'measurement' | 'annotation' | 'terrain' | 'lighting';
  shortcut?: string;
  cursor?: string;
}

export interface Layer {
  id: string;
  name: string;
  type: 'background' | 'terrain' | 'objects' | 'tokens' | 'effects' | 'ui' | 'fog';
  visible: boolean;
  locked: boolean;
  opacity: number;
  blendMode: string;
  order: number;
}

export interface DrawingElement {
  id: string;
  type: 'line' | 'rectangle' | 'circle' | 'polygon' | 'text' | 'image' | 'token';
  layerId: string;
  position: { x: number; y: number };
  size: { width: number; height: number };
  rotation: number;
  style: {
    fill?: string;
    stroke?: string;
    strokeWidth?: number;
    opacity?: number;
    fontSize?: number;
    fontFamily?: string;
    textAlign?: string;
  };
  data?: any;
  locked: boolean;
  visible: boolean;
  created: Date;
  modified: Date;
}

export interface Scene {
  id: string;
  name: string;
  description: string;
  dimensions: { width: number; height: number };
  gridSize: number;
  gridType: 'square' | 'hex' | 'none';
  gridColor: string;
  backgroundColor: string;
  layers: Layer[];
  elements: DrawingElement[];
  lighting: {
    ambient: string;
    sources: Array<{
      id: string;
      position: { x: number; y: number };
      radius: number;
      color: string;
      intensity: number;
      shadows: boolean;
    }>;
  };
  fogOfWar: {
    enabled: boolean;
    revealedAreas: Array<{
      id: string;
      shape: 'circle' | 'polygon';
      points: Array<{ x: number; y: number }>;
    }>;
  };
  created: Date;
  modified: Date;
  version: string;
}

export interface EditorState {
  activeTool: string;
  activeLayer: string;
  selectedElements: string[];
  clipboard: DrawingElement[];
  history: Array<{
    action: string;
    timestamp: Date;
    data: any;
  }>;
  historyIndex: number;
  zoom: number;
  pan: { x: number; y: number };
  grid: {
    visible: boolean;
    snap: boolean;
    size: number;
  };
  rulers: {
    visible: boolean;
    units: 'pixels' | 'feet' | 'meters';
  };
}

export class ContentEditor extends EventEmitter {
  private assetManager: AssetManager;
  private scene!: Scene;
  private state!: EditorState;
  private tools: Map<string, EditorTool> = new Map();
  private maxHistorySize = 100;

  constructor(assetManager: AssetManager) {
    super();
    this.assetManager = assetManager;
    this.initializeDefaultTools();
    this.initializeDefaultState();
  }

  // Scene management
  createScene(name: string, width: number = 4096, height: number = 4096): Scene {
    this.scene = {
      id: this.generateId(),
      name,
      description: '',
      dimensions: { width, height },
      gridSize: 50,
      gridType: 'square',
      gridColor: '#cccccc',
      backgroundColor: '#ffffff',
      layers: this.createDefaultLayers(),
      elements: [],
      lighting: {
        ambient: '#404040',
        sources: []
      },
      fogOfWar: {
        enabled: false,
        revealedAreas: []
      },
      created: new Date(),
      modified: new Date(),
      version: '1.0.0'
    };

    this.emit('sceneCreated', this.scene);
    return this.scene;
  }

  loadScene(scene: Scene): void {
    this.scene = { ...scene };
    this.state.activeLayer = this.scene.layers[0]?.id || '';
    this.state.selectedElements = [];
    this.clearHistory();
    this.emit('sceneLoaded', this.scene);
  }

  // Layer management
  addLayer(name: string, type: Layer['type'], order?: number): Layer {
    const layer: Layer = {
      id: this.generateId(),
      name,
      type,
      visible: true,
      locked: false,
      opacity: 1,
      blendMode: 'normal',
      order: order ?? this.scene.layers.length
    };

    if (order !== undefined) {
      this.scene.layers.splice(order, 0, layer);
      this.reorderLayers();
    } else {
      this.scene.layers.push(layer);
    }

    this.recordHistory('addLayer', { layer });
    this.emit('layerAdded', layer);
    return layer;
  }

  removeLayer(layerId: string): void {
    const layerIndex = this.scene.layers.findIndex(l => l.id === layerId);
    if (layerIndex === -1) return;

    const layer = this.scene.layers[layerIndex];
    this.scene.layers.splice(layerIndex, 1);

    // Remove all elements on this layer
    this.scene.elements = this.scene.elements.filter(e => e.layerId !== layerId);

    this.recordHistory('removeLayer', { layer, elements: this.scene.elements });
    this.emit('layerRemoved', layer);
  }

  // Drawing tools
  setActiveTool(toolId: string): void {
    if (!this.tools.has(toolId)) return;

    this.state.activeTool = toolId;
    this.emit('toolChanged', toolId);
  }

  // Element creation
  addElement(element: Omit<DrawingElement, 'id' | 'created' | 'modified'>): DrawingElement {
    const newElement: DrawingElement = {
      ...element,
      id: this.generateId(),
      created: new Date(),
      modified: new Date()
    };

    this.scene.elements.push(newElement);
    this.recordHistory('addElement', { element: newElement });
    this.emit('elementAdded', newElement);
    return newElement;
  }

  updateElement(elementId: string, updates: Partial<DrawingElement>): void {
    const element = this.scene.elements.find(e => e.id === elementId);
    if (!element) return;

    const oldElement = { ...element };
    Object.assign(element, updates, { modified: new Date() });

    this.recordHistory('updateElement', { elementId, oldElement, newElement: element });
    this.emit('elementUpdated', element);
  }

  removeElement(elementId: string): void {
    const elementIndex = this.scene.elements.findIndex(e => e.id === elementId);
    if (elementIndex === -1) return;

    const element = this.scene.elements[elementIndex];
    this.scene.elements.splice(elementIndex, 1);

    this.recordHistory('removeElement', { element });
    this.emit('elementRemoved', element);
  }

  // Selection management
  selectElement(elementId: string, addToSelection = false): void {
    if (!addToSelection) {
      this.state.selectedElements = [elementId];
    } else if (!this.state.selectedElements.includes(elementId)) {
      this.state.selectedElements.push(elementId);
    }

    this.emit('selectionChanged', this.state.selectedElements);
  }

  selectElements(elementIds: string[]): void {
    this.state.selectedElements = [...elementIds];
    this.emit('selectionChanged', this.state.selectedElements);
  }

  clearSelection(): void {
    this.state.selectedElements = [];
    this.emit('selectionChanged', this.state.selectedElements);
  }

  // Transform operations
  moveElements(elementIds: string[], deltaX: number, deltaY: number): void {
    const elements = this.scene.elements.filter(e => elementIds.includes(e.id));

    elements.forEach(element => {
      element.position.x += deltaX;
      element.position.y += deltaY;
      element.modified = new Date();
    });

    this.recordHistory('moveElements', { elementIds, deltaX, deltaY });
    this.emit('elementsTransformed', elements);
  }

  rotateElements(elementIds: string[], angle: number): void {
    const elements = this.scene.elements.filter(e => elementIds.includes(e.id));

    elements.forEach(element => {
      element.rotation += angle;
      element.modified = new Date();
    });

    this.recordHistory('rotateElements', { elementIds, angle });
    this.emit('elementsTransformed', elements);
  }

  scaleElements(elementIds: string[], scaleX: number, scaleY: number): void {
    const elements = this.scene.elements.filter(e => elementIds.includes(e.id));

    elements.forEach(element => {
      element.size.width *= scaleX;
      element.size.height *= scaleY;
      element.modified = new Date();
    });

    this.recordHistory('scaleElements', { elementIds, scaleX, scaleY });
    this.emit('elementsTransformed', elements);
  }

  // History management
  undo(): void {
    if (this.state.historyIndex > 0) {
      this.state.historyIndex--;
      const action = this.state.history[this.state.historyIndex];
      this.revertAction(action);
      this.emit('undone', action);
    }
  }

  redo(): void {
    if (this.state.historyIndex < this.state.history.length) {
      const action = this.state.history[this.state.historyIndex];
      this.applyAction(action);
      this.state.historyIndex++;
      this.emit('redone', action);
    }
  }

  // Grid and snapping
  snapToGrid(point: { x: number; y: number }): { x: number; y: number } {
    if (!this.state.grid.snap) return point;

    const gridSize = this.state.grid.size;
    return {
      x: Math.round(point.x / gridSize) * gridSize,
      y: Math.round(point.y / gridSize) * gridSize
    };
  }

  // Asset integration
  async placeAsset(assetId: string, position: { x: number; y: number }): Promise<DrawingElement> {
    const asset = this.assetManager.getAsset(assetId);
    if (!asset) {
      throw new Error(`Asset not found: ${assetId}`);
    }

    const element = this.addElement({
      type: 'image',
      layerId: this.state.activeLayer,
      position: this.snapToGrid(position),
      size: asset.dimensions || { width: 100, height: 100 },
      rotation: 0,
      style: { opacity: 1 },
      data: { assetId },
      locked: false,
      visible: true
    });

    return element;
  }

  // Export and serialization
  exportScene(): Scene {
    return {
      ...this.scene,
      modified: new Date()
    };
  }

  exportAsImage(_format: 'png' | 'jpg' | 'webp' = 'png'): Promise<Buffer> {
    // Would render scene to image format
    throw new Error('Not implemented');
  }

  // Private methods
  private initializeDefaultTools(): void {
    const tools: EditorTool[] = [
      { id: 'select', name: 'Select', icon: 'cursor', category: 'selection', shortcut: 'v' },
      { id: 'pan', name: 'Pan', icon: 'hand', category: 'selection', shortcut: 'space' },
      { id: 'brush', name: 'Brush', icon: 'brush', category: 'drawing', shortcut: 'b' },
      { id: 'eraser', name: 'Eraser', icon: 'eraser', category: 'drawing', shortcut: 'e' },
      { id: 'line', name: 'Line', icon: 'line', category: 'drawing', shortcut: 'l' },
      { id: 'rectangle', name: 'Rectangle', icon: 'square', category: 'drawing', shortcut: 'r' },
      { id: 'circle', name: 'Circle', icon: 'circle', category: 'drawing', shortcut: 'c' },
      { id: 'polygon', name: 'Polygon', icon: 'polygon', category: 'drawing', shortcut: 'p' },
      { id: 'text', name: 'Text', icon: 'text', category: 'annotation', shortcut: 't' },
      { id: 'measure', name: 'Measure', icon: 'ruler', category: 'measurement', shortcut: 'm' },
      { id: 'light', name: 'Light', icon: 'lightbulb', category: 'lighting', shortcut: 'shift+l' },
      { id: 'fog', name: 'Fog of War', icon: 'eye-off', category: 'lighting', shortcut: 'f' },
      { id: 'terrain', name: 'Terrain', icon: 'mountain', category: 'terrain', shortcut: 'shift+t' }
    ];

    tools.forEach(tool => this.tools.set(tool.id, tool));
  }

  private initializeDefaultState(): void {
    this.state = {
      activeTool: 'select',
      activeLayer: '',
      selectedElements: [],
      clipboard: [],
      history: [],
      historyIndex: 0,
      zoom: 1,
      pan: { x: 0, y: 0 },
      grid: {
        visible: true,
        snap: true,
        size: 50
      },
      rulers: {
        visible: true,
        units: 'feet'
      }
    };
  }

  private createDefaultLayers(): Layer[] {
    return [
      {
        id: this.generateId(),
        name: 'Background',
        type: 'background',
        visible: true,
        locked: false,
        opacity: 1,
        blendMode: 'normal',
        order: 0
      },
      {
        id: this.generateId(),
        name: 'Terrain',
        type: 'terrain',
        visible: true,
        locked: false,
        opacity: 1,
        blendMode: 'normal',
        order: 1
      },
      {
        id: this.generateId(),
        name: 'Objects',
        type: 'objects',
        visible: true,
        locked: false,
        opacity: 1,
        blendMode: 'normal',
        order: 2
      },
      {
        id: this.generateId(),
        name: 'Tokens',
        type: 'tokens',
        visible: true,
        locked: false,
        opacity: 1,
        blendMode: 'normal',
        order: 3
      },
      {
        id: this.generateId(),
        name: 'Effects',
        type: 'effects',
        visible: true,
        locked: false,
        opacity: 1,
        blendMode: 'normal',
        order: 4
      }
    ];
  }

  private recordHistory(action: string, data: any): void {
    // Remove any redo history when adding new action
    this.state.history = this.state.history.slice(0, this.state.historyIndex);

    this.state.history.push({
      action,
      timestamp: new Date(),
      data
    });

    this.state.historyIndex = this.state.history.length;

    // Limit history size
    if (this.state.history.length > this.maxHistorySize) {
      this.state.history.shift();
      this.state.historyIndex--;
    }
  }

  private clearHistory(): void {
    this.state.history = [];
    this.state.historyIndex = 0;
  }

  private revertAction(action: any): void {
    // Implement action reversal logic
    switch (action.action) {
      case 'addElement':
        this.scene.elements = this.scene.elements.filter(e => e.id !== action.data.element.id);
        break;
      case 'removeElement':
        this.scene.elements.push(action.data.element);
        break;
      case 'updateElement': {
        const element = this.scene.elements.find(e => e.id === action.data.elementId);
        if (element) {
          Object.assign(element, action.data.oldElement);
        }
    }
        break;
      // Add more action reversals as needed
    }
  }

  private applyAction(_action: any): void {
    // Implement action application logic for redo
    // Similar to revertAction but applies the action forward
  }

  private reorderLayers(): void {
    this.scene.layers.forEach((_layer, __index) => {
      layer.order = index;
    });
  }

  private generateId(): string {
    return Math.random().toString(36).substr(2, 9);
  }

  // Getters
  getScene(): Scene {
    return this.scene;
  }

  getState(): EditorState {
    return this.state;
  }

  getTools(): EditorTool[] {
    return Array.from(this.tools.values());
  }

  getSelectedElements(): DrawingElement[] {
    return this.scene.elements.filter(e => this.state.selectedElements.includes(e.id));
  }
}

/**
 * Content publishing and distribution system
 */

import { EventEmitter } from 'events';
import { promises as fs } from 'fs';
import * as path from 'path';
import * as crypto from 'crypto';
import JSZip from 'jszip';
import { AssetManager, _AssetMetadata} from './AssetManager';
import { Campaign } from './CampaignBuilder';
import { Scene } from './ContentEditor';

export interface ContentPackage {
  id: string;
  name: string;
  version: string;
  description: string;
  author: string;
  category: 'campaign' | 'adventure' | 'asset_pack' | 'ruleset' | 'supplement';
  system: string;
  tags: string[];

  // Content
  assets: string[];
  scenes: Scene[];
  campaigns: Campaign[];

  // Metadata
  license: string;
  requiredVersion: string;
  dependencies: Array<{
    packageId: string;
    version: string;
    optional: boolean;
  }>;

  // Publishing info
  publishedDate?: Date;
  downloads: number;
  rating: number;
  reviewCount: number;

  // File info
  size: number;
  checksum: string;
  filePath?: string;

  created: Date;
  modified: Date;
}

export interface PublishingConfig {
  exportPath: string;
  compressionLevel: number;
  includeAssets: boolean;
  includeThumbnails: boolean;
  validateContent: boolean;
  generateManifest: boolean;

  // Distribution
  platforms: Array<{
    name: string;
    endpoint: string;
    apiKey?: string;
    enabled: boolean;
  }>;

  // Metadata
  defaultLicense: string;
  authorInfo: {
    name: string;
    email: string;
    website?: string;
  };
}

export interface ValidationResult {
  valid: boolean;
  errors: Array<{
    type: 'error' | 'warning' | 'info';
    code: string;
    message: string;
    path?: string;
    severity: number;
  }>;
  warnings: Array<{
    type: 'error' | 'warning' | 'info';
    code: string;
    message: string;
    path?: string;
    severity: number;
  }>;
  stats: {
    totalAssets: number;
    totalSize: number;
    missingAssets: number;
    brokenReferences: number;
  };
}

export class PublishingManager extends EventEmitter {
  private assetManager: AssetManager;
  private config: PublishingConfig;
  private packages = new Map<string, ContentPackage>();

  constructor(assetManager: AssetManager, config: PublishingConfig) {
    super();
    this.assetManager = assetManager;
    this.config = config;
  }

  // Package creation
  async createPackage(
    name: string,
    version: string,
    description: string,
    category: ContentPackage['category'],
    system: string,
    content: {
      assets?: string[];
      scenes?: Scene[];
      campaigns?: Campaign[];
    }
  ): Promise<ContentPackage> {
    const pkg: ContentPackage = {
      id: this.generateId(),
      name,
      version,
      description,
      author: this.config.authorInfo.name,
      category,
      system,
      tags: [],

      assets: content.assets || [],
      scenes: content.scenes || [],
      campaigns: content.campaigns || [],

      license: this.config.defaultLicense,
      requiredVersion: '1.0.0',
      dependencies: [],

      downloads: 0,
      rating: 0,
      reviewCount: 0,

      size: 0,
      checksum: '',

      created: new Date(),
      modified: new Date()
    };

    this.packages.set(pkg.id, pkg);
    this.emit('packageCreated', pkg);
    return pkg;
  }

  // Content validation
  async validatePackage(packageId: string): Promise<ValidationResult> {
    const pkg = this.packages.get(packageId);
    if (!pkg) {
      throw new Error(`Package not found: ${packageId}`);
    }

    const result: ValidationResult = {
      valid: true,
      errors: [],
      warnings: [],
      stats: {
        totalAssets: pkg.assets.length,
        totalSize: 0,
        missingAssets: 0,
        brokenReferences: 0
      }
    };

    // Validate basic package info
    if (!pkg.name.trim()) {
      result.errors.push({
        type: 'error',
        code: 'MISSING_NAME',
        message: 'Package name is required',
        severity: 10
      });
    }

    if (!pkg.version.match(/^\d+\.\d+\.\d+$/)) {
      result.errors.push({
        type: 'error',
        code: 'INVALID_VERSION',
        message: 'Version must follow semantic versioning (x.y.z)',
        severity: 8
      });
    }

    if (!pkg.description.trim()) {
      result.warnings.push({
        type: 'warning',
        code: 'MISSING_DESCRIPTION',
        message: 'Package description is recommended',
        severity: 3
      });
    }

    // Validate assets
    for (const assetId of pkg.assets) {
      const asset = this.assetManager.getAsset(assetId);
      if (!asset) {
        result.errors.push({
          type: 'error',
          code: 'MISSING_ASSET',
          message: `Referenced asset not found: ${assetId}`,
          path: `assets/${assetId}`,
          severity: 9
        });
        result.stats.missingAssets++;
      } else {
        result.stats.totalSize += asset.size;
      }
    }

    // Validate scenes
    for (let i = 0; i < pkg.scenes.length; i++) {
      const scene = pkg.scenes[i];
      if (!scene) continue;
      const sceneErrors = await this.validateScene(scene);

      sceneErrors.forEach(error => {
        error.path = `scenes/${i}/${error.path || ''}`;
        if (error.type === 'error') {
          result.errors.push(error);
        } else {
          result.warnings.push(error);
        }
      });
    }

    // Validate campaigns
    for (let i = 0; i < pkg.campaigns.length; i++) {
      const campaign = pkg.campaigns[i];
      if (!campaign) continue;
      const campaignErrors = await this.validateCampaign(campaign);

      campaignErrors.forEach(error => {
        error.path = `campaigns/${i}/${error.path || ''}`;
        if (error.type === 'error') {
          result.errors.push(error);
        } else {
          result.warnings.push(error);
        }
      });
    }

    // Check dependencies
    for (const dep of pkg.dependencies) {
      if (!this.packages.has(dep.packageId) && !dep.optional) {
        result.warnings.push({
          type: 'warning',
          code: 'MISSING_DEPENDENCY',
          message: `Required dependency not available: ${dep.packageId}`,
          severity: 6
        });
      }
    }

    result.valid = result.errors.length === 0;
    this.emit('packageValidated', pkg, result);
    return result;
  }

  // Export and packaging
  async exportPackage(packageId: string, outputPath?: string): Promise<string> {
    const pkg = this.packages.get(packageId);
    if (!pkg) {
      throw new Error(`Package not found: ${packageId}`);
    }

    // Validate before export if configured
    if (this.config.validateContent) {
      const validation = await this.validatePackage(packageId);
      if (!validation.valid) {
        throw new Error(`Package validation failed: ${validation.errors.length} errors`);
      }
    }

    const zip = new JSZip();
    const exportPath = outputPath || path.join(this.config.exportPath, `${pkg.name}-${pkg.version}.vttp`);

    // Add manifest
    if (this.config.generateManifest) {
      const manifest = this.generateManifest(pkg);
      zip.file('manifest.json', JSON.stringify(manifest, null, 2));
    }

    // Add package metadata
    zip.file('package.json', JSON.stringify({
      id: pkg.id,
      name: pkg.name,
      version: pkg.version,
      description: pkg.description,
      author: pkg.author,
      category: pkg.category,
      system: pkg.system,
      tags: pkg.tags,
      license: pkg.license,
      requiredVersion: pkg.requiredVersion,
      dependencies: pkg.dependencies,
      created: pkg.created,
      modified: pkg.modified
    }, null, 2));

    // Add content
    if (pkg.scenes.length > 0) {
      zip.file('scenes.json', JSON.stringify(pkg.scenes, null, 2));
    }

    if (pkg.campaigns.length > 0) {
      zip.file('campaigns.json', JSON.stringify(pkg.campaigns, null, 2));
    }

    // Add assets if configured
    if (this.config.includeAssets) {
      const assetsFolder = zip.folder('assets');

      for (const assetId of pkg.assets) {
        const asset = this.assetManager.getAsset(assetId);
        if (asset && asset.filePath) {
          try {
            const assetData = await fs.readFile(asset.filePath);
            const assetPath = path.basename(asset.filePath);
            assetsFolder?.file(assetPath, assetData);

            // Add thumbnails if configured
            if (this.config.includeThumbnails && asset.thumbnailPath) {
              const thumbnailData = await fs.readFile(asset.thumbnailPath);
              const thumbnailPath = `thumbnails/${path.basename(asset.thumbnailPath)}`;
              zip.file(thumbnailPath, thumbnailData);
            }
          } catch (error) {
            console.warn(`Failed to include asset ${assetId}:`, error);
          }
        }
      }
    }

    // Generate and save package
    const zipBuffer = await zip.generateAsync({
      type: 'nodebuffer',
      compression: 'DEFLATE',
      compressionOptions: {
        level: this.config.compressionLevel
      }
    });

    await fs.writeFile(exportPath, zipBuffer);

    // Update package info
    pkg.size = zipBuffer.length;
    pkg.checksum = this.calculateChecksum(zipBuffer);
    pkg.filePath = exportPath;
    pkg.modified = new Date();

    this.emit('packageExported', pkg, exportPath);
    return exportPath;
  }

  // Import and installation
  async importPackage(filePath: string): Promise<ContentPackage> {
    const zipData = await fs.readFile(filePath);
    const zip = await JSZip.loadAsync(zipData);

    // Read package metadata
    const packageFile = zip.file('package.json');
    if (!packageFile) {
      throw new Error('Invalid package: missing package.json');
    }

    const packageData = JSON.parse(await packageFile.async('text'));

    // Verify checksum
    const expectedChecksum = this.calculateChecksum(zipData);

    const pkg: ContentPackage = {
      ...packageData,
      size: zipData.length,
      checksum: expectedChecksum,
      filePath,
      downloads: packageData.downloads || 0,
      rating: packageData.rating || 0,
      reviewCount: packageData.reviewCount || 0
    };

    // Import scenes
    const scenesFile = zip.file('scenes.json');
    if (scenesFile) {
      pkg.scenes = JSON.parse(await scenesFile.async('text'));
    }

    // Import campaigns
    const campaignsFile = zip.file('campaigns.json');
    if (campaignsFile) {
      pkg.campaigns = JSON.parse(await campaignsFile.async('text'));
    }

    // Import assets
    const assetsFolder = zip.folder('assets');
    if (assetsFolder) {
      for (const [_filename, file] of Object.entries(assetsFolder.files)) {
        if (file && typeof file === 'object' && 'dir' in file && !file.dir && 'async' in file) {
          const _assetData = await (file as any).async('nodebuffer');
          // Import asset to asset manager
          // This would integrate with AssetManager.importAsset()
        }
      }
    }

    this.packages.set(pkg.id, pkg);
    this.emit('packageImported', pkg);
    return pkg;
  }

  // Publishing to platforms
  async publishToMarketplace(packageId: string, platformName?: string): Promise<void> {
    const pkg = this.packages.get(packageId);
    if (!pkg) {
      throw new Error(`Package not found: ${packageId}`);
    }

    const platforms = platformName 
      ? this.config.platforms.filter(p => p.name === platformName && p.enabled)
      : this.config.platforms.filter(p => p.enabled);

    if (platforms.length === 0) {
      throw new Error('No enabled publishing platforms configured');
    }

    // Export package first
    const exportPath = await this.exportPackage(packageId);

    for (const platform of platforms) {
      try {
        await this.publishToPlatform(pkg, exportPath, platform);
        this.emit('packagePublished', pkg, platform.name);
      } catch (error) {
        this.emit('publishFailed', pkg, platform.name, error);
        throw error;
      }
    }

    pkg.publishedDate = new Date();
    pkg.modified = new Date();
  }

  // Package management
  getPackage(packageId: string): ContentPackage | undefined {
    return this.packages.get(packageId);
  }

  getAllPackages(): ContentPackage[] {
    return Array.from(this.packages.values());
  }

  removePackage(packageId: string): void {
    const pkg = this.packages.get(packageId);
    if (pkg) {
      this.packages.delete(packageId);

      // Clean up exported file
      if (pkg.filePath) {
        fs.unlink(pkg.filePath).catch(console.warn);
      }

      this.emit('packageRemoved', pkg);
    }
  }

  updatePackage(packageId: string, updates: Partial<ContentPackage>): void {
    const pkg = this.packages.get(packageId);
    if (pkg) {
      Object.assign(pkg, updates, { modified: new Date() });
      this.emit('packageUpdated', pkg);
    }
  }

  // Private methods
  private async validateScene(scene: Scene): Promise<Array<{ type: 'error' | 'warning'; code: string; message: string; path?: string; severity: number }>> {
    const errors: Array<{ type: 'error' | 'warning'; code: string; message: string; path?: string; severity: number }> = [];

    if (!scene.name.trim()) {
      errors.push({
        type: 'error',
        code: 'MISSING_SCENE_NAME',
        message: 'Scene name is required',
        severity: 8
      });
    }

    if (scene.dimensions.width <= 0 || scene.dimensions.height <= 0) {
      errors.push({
        type: 'error',
        code: 'INVALID_DIMENSIONS',
        message: 'Scene dimensions must be positive',
        severity: 7
      });
    }

    // Validate elements reference valid assets
    for (const element of scene.elements) {
      if (element.type === 'image' && element.data?.assetId) {
        const asset = this.assetManager.getAsset(element.data.assetId);
        if (!asset) {
          errors.push({
            type: 'error',
            code: 'MISSING_ELEMENT_ASSET',
            message: `Element references missing asset: ${element.data.assetId}`,
            path: `elements/${element.id}`,
            severity: 6
          });
        }
      }
    }

    return errors;
  }

  private async validateCampaign(campaign: Campaign): Promise<Array<{ type: 'error' | 'warning'; code: string; message: string; path?: string; severity: number }>> {
    const errors: Array<{ type: 'error' | 'warning'; code: string; message: string; path?: string; severity: number }> = [];

    if (!campaign.name.trim()) {
      errors.push({
        type: 'error',
        code: 'MISSING_CAMPAIGN_NAME',
        message: 'Campaign name is required',
        severity: 8
      });
    }

    if (!campaign.system.trim()) {
      errors.push({
        type: 'warning',
        code: 'MISSING_SYSTEM',
        message: 'Game system should be specified',
        severity: 4
      });
    }

    // Validate character references
    for (const character of campaign.characters) {
      if (character.portraitAssetId) {
        const asset = this.assetManager.getAsset(character.portraitAssetId);
        if (!asset) {
          errors.push({
            type: 'warning',
            code: 'MISSING_PORTRAIT',
            message: `Character portrait asset not found: ${character.portraitAssetId}`,
            path: `characters/${character.id}/portrait`,
            severity: 3
          });
        }
      }
    }

    return errors;
  }

  private generateManifest(pkg: ContentPackage): any {
    return {
      formatVersion: '1.0',
      packageId: pkg.id,
      name: pkg.name,
      version: pkg.version,
      description: pkg.description,
      author: pkg.author,
      category: pkg.category,
      system: pkg.system,
      license: pkg.license,
      created: pkg.created,
      contents: {
        assets: pkg.assets.length,
        scenes: pkg.scenes.length,
        campaigns: pkg.campaigns.length
      },
      requirements: {
        minVersion: pkg.requiredVersion,
        dependencies: pkg.dependencies
      }
    };
  }

  private async publishToPlatform(
    pkg: ContentPackage,
    filePath: string,
    platform: { name: string; endpoint: string; apiKey?: string }
  ): Promise<void> {
    // Mock implementation - would integrate with actual marketplace APIs
    console.log(`Publishing ${pkg.name} to ${platform.name}`);

    // This would typically involve:
    // 1. Uploading the package file
    // 2. Submitting metadata
    // 3. Handling API responses
    // 4. Updating package status

    await new Promise(resolve => setTimeout(resolve, 1000)); // Simulate API call
  }

  private calculateChecksum(data: Buffer): string {
    return crypto.createHash('sha256').update(data).digest('hex');
  }

  private generateId(): string {
    return Math.random().toString(36).substr(2, 9);
  }

  // Configuration management
  updateConfig(updates: Partial<PublishingConfig>): void {
    Object.assign(this.config, updates);
    this.emit('configUpdated', this.config);
  }

  getConfig(): PublishingConfig {
    return { ...this.config };
  }

  // Statistics and analytics
  getPackageStats(packageId: string): {
    downloads: number;
    rating: number;
    reviews: number;
    size: number;
    created: Date;
    lastModified: Date;
  } | null {
    const pkg = this.packages.get(packageId);
    if (!pkg) return null;

    return {
      downloads: pkg.downloads,
      rating: pkg.rating,
      reviews: pkg.reviewCount,
      size: pkg.size,
      created: pkg.created,
      lastModified: pkg.modified
    };
  }

  getAllStats(): {
    totalPackages: number;
    totalDownloads: number;
    averageRating: number;
    totalSize: number;
    byCategory: Record<string, number>;
    bySystem: Record<string, number>;
  } {
    const packages = Array.from(this.packages.values());

    return {
      totalPackages: packages.length,
      totalDownloads: packages.reduce((_sum, _pkg) => sum + pkg.downloads, 0),
      averageRating: packages.length > 0 
        ? packages.reduce((_sum, _pkg) => sum + pkg.rating, 0) / packages.length 
        : 0,
      totalSize: packages.reduce((_sum, _pkg) => sum + pkg.size, 0),
      byCategory: this.groupBy(packages, 'category'),
      bySystem: this.groupBy(packages, 'system')
    };
  }

  private groupBy(array: any[], key: string): Record<string, number> {
    return array.reduce((acc, item) => {
      const value = item[key] || 'unknown';
      acc[value] = (acc[value] || 0) + 1;
      return acc;
    }, {});
  }
}

/**
 * Monster Statblock Adapter - Converts D&D 5e monster statblocks to ECS components
 */

import { _Condition, ConditionType } from '../components/Conditions';

export interface D5eStatblock {
  name: string;
  size: string;
  type: string;
  alignment: string;
  armorClass: number | { value: number; type?: string };
  hitPoints: number | { value: number; formula?: string };
  speed: Record<string, number>;
  abilities: {
    STR: number;
    DEX: number;
    CON: number;
    INT: number;
    WIS: number;
    CHA: number;
  };
  skills?: Record<string, number>;
  savingThrows?: Record<string, number>;
  damageResistances?: string[];
  damageImmunities?: string[];
  damageVulnerabilities?: string[];
  conditionImmunities?: string[];
  senses?: Record<string, number>;
  languages?: string[];
  challengeRating: number | string;
  proficiencyBonus?: number;
  actions?: Action[];
  bonusActions?: Action[];
  reactions?: Action[];
  legendaryActions?: LegendaryAction[];
  spellcasting?: Spellcasting;
}

export interface Action {
  name: string;
  description: string;
  attackBonus?: number;
  damage?: {
    diceExpression: string;
    damageType: string;
    versatile?: string;
  };
  saveDC?: number;
  saveAbility?: string;
  range?: number;
  targets?: number;
  recharge?: number | string;
  uses?: number;
}

export interface LegendaryAction {
  name: string;
  description: string;
  cost: number;
}

export interface Spellcasting {
  level: number;
  ability: string;
  saveDC: number;
  attackBonus: number;
  slots?: Record<string, number>;
  spells: Record<string, string[]>;
}

export interface ECSMonsterData {
  name: string;
  health: {
    current: number;
    max: number;
    temporary: number;
  };
  stats: {
    STR: { value: number; modifier: number };
    DEX: { value: number; modifier: number };
    CON: { value: number; modifier: number };
    INT: { value: number; modifier: number };
    WIS: { value: number; modifier: number };
    CHA: { value: number; modifier: number };
  };
  armorClass: number;
  speed: number;
  proficiencyBonus: number;
  challengeRating: number;
  conditionImmunities: ConditionType[];
  actions: Action[];
  bonusActions: Action[];
  reactions: Action[];
  legendaryActions?: LegendaryAction[];
  spellcasting?: Spellcasting;
  traits: {
    damageResistances: string[];
    damageImmunities: string[];
    damageVulnerabilities: string[];
    senses: Record<string, number>;
    languages: string[];
  };
}

export class MonsterStatblockAdapter {
  /**
   * Convert a D&D 5e statblock to ECS-compatible data
   */
  static convertToECS(statblock: D5eStatblock): ECSMonsterData {
    const hitPoints = this.extractHitPoints(statblock.hitPoints);
    const armorClass = this.extractArmorClass(statblock.armorClass);
    const challengeRating = this.extractChallengeRating(statblock.challengeRating);
    const proficiencyBonus = statblock.proficiencyBonus || this.calculateProficiencyBonus(challengeRating);

    return {
      name: statblock.name,
      health: {
        current: hitPoints,
        max: hitPoints,
        temporary: 0
      },
      stats: {
        STR: { 
          value: statblock.abilities.STR, 
          modifier: Math.floor((statblock.abilities.STR - 10) / 2) 
        },
        DEX: { 
          value: statblock.abilities.DEX, 
          modifier: Math.floor((statblock.abilities.DEX - 10) / 2) 
        },
        CON: { 
          value: statblock.abilities.CON, 
          modifier: Math.floor((statblock.abilities.CON - 10) / 2) 
        },
        INT: { 
          value: statblock.abilities.INT, 
          modifier: Math.floor((statblock.abilities.INT - 10) / 2) 
        },
        WIS: { 
          value: statblock.abilities.WIS, 
          modifier: Math.floor((statblock.abilities.WIS - 10) / 2) 
        },
        CHA: { 
          value: statblock.abilities.CHA, 
          modifier: Math.floor((statblock.abilities.CHA - 10) / 2) 
        }
      },
      armorClass,
      speed: statblock.speed.walk || 30,
      proficiencyBonus,
      challengeRating,
      conditionImmunities: this.convertConditionImmunities(statblock.conditionImmunities || []),
      actions: statblock.actions || [],
      bonusActions: statblock.bonusActions || [],
      reactions: statblock.reactions || [],
      legendaryActions: statblock.legendaryActions,
      spellcasting: statblock.spellcasting,
      traits: {
        damageResistances: statblock.damageResistances || [],
        damageImmunities: statblock.damageImmunities || [],
        damageVulnerabilities: statblock.damageVulnerabilities || [],
        senses: statblock.senses || {},
        languages: statblock.languages || []
      }
    };
  }

  /**
   * Calculate initiative modifier for monster
   */
  static calculateInitiativeModifier(statblock: D5eStatblock): number {
    return Math.floor((statblock.abilities.DEX - 10) / 2);
  }

  /**
   * Generate available actions for ActionSystem
   */
  static generateActionSystemActions(monsterData: ECSMonsterData): Array<{
    id: string;
    name: string;
    actionType: 'action' | 'bonus_action' | 'reaction';
    attackBonus?: number;
    damage?: {
      diceExpression: string;
      damageType: string;
    };
    saveDC?: number;
    saveAbility?: string;
    range?: { normal: number; long?: number };
    description: string;
  }> {
    const actions = [];

    // Convert main actions
    for (const action of monsterData.actions) {
      actions.push({
        id: `${monsterData.name.toLowerCase().replace(/\s+/g, '')}_${action.name.toLowerCase().replace(/\s+/g, '')}`,
        name: action.name,
        actionType: 'action' as const,
        attackBonus: action.attackBonus,
        damage: action.damage ? {
          diceExpression: action.damage.diceExpression,
          damageType: action.damage.damageType
        } : undefined,
        saveDC: action.saveDC,
        saveAbility: action.saveAbility,
        range: action.range ? { normal: action.range } : undefined,
        description: action.description
      });
    }

    // Convert bonus actions
    for (const action of monsterData.bonusActions) {
      actions.push({
        id: `${monsterData.name.toLowerCase().replace(/\s+/g, '')}_${action.name.toLowerCase().replace(/\s+/g, '')}_bonus`,
        name: action.name,
        actionType: 'bonus_action' as const,
        attackBonus: action.attackBonus,
        damage: action.damage ? {
          diceExpression: action.damage.diceExpression,
          damageType: action.damage.damageType
        } : undefined,
        saveDC: action.saveDC,
        saveAbility: action.saveAbility,
        range: action.range ? { normal: action.range } : undefined,
        description: action.description
      });
    }

    // Convert reactions
    for (const action of monsterData.reactions) {
      actions.push({
        id: `${monsterData.name.toLowerCase().replace(/\s+/g, '')}_${action.name.toLowerCase().replace(/\s+/g, '')}_reaction`,
        name: action.name,
        actionType: 'reaction' as const,
        attackBonus: action.attackBonus,
        damage: action.damage ? {
          diceExpression: action.damage.diceExpression,
          damageType: action.damage.damageType
        } : undefined,
        saveDC: action.saveDC,
        saveAbility: action.saveAbility,
        range: action.range ? { normal: action.range } : undefined,
        description: action.description
      });
    }

    return actions;
  }

  /**
   * Extract hit points from various formats
   */
  private static extractHitPoints(hitPoints: number | { value: number; formula?: string }): number {
    if (typeof hitPoints === 'number') {
      return hitPoints;
    }
    return hitPoints.value;
  }

  /**
   * Extract armor class from various formats
   */
  private static extractArmorClass(armorClass: number | { value: number; type?: string }): number {
    if (typeof armorClass === 'number') {
      return armorClass;
    }
    return armorClass.value;
  }

  /**
   * Extract challenge rating as number
   */
  private static extractChallengeRating(cr: number | string): number {
    if (typeof cr === 'number') {
      return cr;
    }

    // Handle fractional CRs like "1/2", "1/4", "1/8"
    if (cr.includes('/')) {
      const [numerator, denominator] = cr.split('/').map(Number);
      return numerator / denominator;
    }

    return parseFloat(cr) || 0;
  }

  /**
   * Calculate proficiency bonus from challenge rating
   */
  private static calculateProficiencyBonus(challengeRating: number): number {
    if (challengeRating < 1) return 2;
    if (challengeRating < 5) return 2;
    if (challengeRating < 9) return 3;
    if (challengeRating < 13) return 4;
    if (challengeRating < 17) return 5;
    if (challengeRating < 21) return 6;
    if (challengeRating < 25) return 7;
    if (challengeRating < 29) return 8;
    return 9;
  }

  /**
   * Convert condition immunities to typed array
   */
  private static convertConditionImmunities(immunities: string[]): ConditionType[] {
    const conditionMap: Record<string, ConditionType> = {
      'blinded': 'blinded',
      'charmed': 'charmed',
      'deafened': 'deafened',
      'frightened': 'frightened',
      'grappled': 'grappled',
      'incapacitated': 'incapacitated',
      'invisible': 'invisible',
      'paralyzed': 'paralyzed',
      'petrified': 'petrified',
      'poisoned': 'poisoned',
      'prone': 'prone',
      'restrained': 'restrained',
      'stunned': 'stunned',
      'unconscious': 'unconscious',
      'exhaustion': 'exhaustion'
    };

    return immunities
      .map(immunity => conditionMap[immunity.toLowerCase()])
      .filter(Boolean) as ConditionType[];
  }

  /**
   * Parse damage dice expressions like "2d6+3" or "1d8+2"
   */
  static parseDamageExpression(expression: string): {
    dice: number;
    sides: number;
    modifier: number;
    average: number;
  } {
    const match = expression.match(/(\d+)d(\d+)(?:\s*([+-])\s*(\d+))?/);
    if (!match) {
      return { dice: 1, sides: 4, modifier: 0, average: 2.5 };
    }

    const dice = parseInt(match[1]);
    const sides = parseInt(match[2]);
    const modifierSign = match[3] === '-' ? -1 : 1;
    const modifier = match[4] ? parseInt(match[4]) * modifierSign : 0;
    const average = (dice * (sides + 1) / 2) + modifier;

    return { dice, sides, modifier, average };
  }

  /**
   * Convert SRD monster format to standardized format
   */
  static normalizeSRDMonster(srdMonster: any): D5eStatblock {
    return {
      name: srdMonster.name,
      size: srdMonster.size || 'Medium',
      type: srdMonster.type || 'humanoid',
      alignment: srdMonster.alignment || 'neutral',
      armorClass: srdMonster.armor_class || 10,
      hitPoints: srdMonster.hit_points || 10,
      speed: srdMonster.speed || { walk: 30 },
      abilities: {
        STR: srdMonster.strength || 10,
        DEX: srdMonster.dexterity || 10,
        CON: srdMonster.constitution || 10,
        INT: srdMonster.intelligence || 10,
        WIS: srdMonster.wisdom || 10,
        CHA: srdMonster.charisma || 10
      },
      skills: srdMonster.skills,
      savingThrows: srdMonster.saving_throws,
      damageResistances: srdMonster.damage_resistances,
      damageImmunities: srdMonster.damage_immunities,
      damageVulnerabilities: srdMonster.damage_vulnerabilities,
      conditionImmunities: srdMonster.condition_immunities,
      senses: srdMonster.senses,
      languages: srdMonster.languages,
      challengeRating: srdMonster.challenge_rating || 0,
      proficiencyBonus: srdMonster.proficiency_bonus,
      actions: srdMonster.actions,
      bonusActions: srdMonster.bonus_actions,
      reactions: srdMonster.reactions,
      legendaryActions: srdMonster.legendary_actions,
      spellcasting: srdMonster.spellcasting
    };
  }
}

/**
 * Condition Synchronization Service - Keeps ECS conditions in sync with database applied conditions
 */

import { EntityId } from '../World';
import { Condition, ConditionType } from '../components/Conditions';

export interface DatabaseCondition {
  id: string;
  actorId: string;
  conditionId: string;
  duration: number;
  remainingDuration: number;
  source?: string;
  metadata?: Record<string, any>;
  appliedAt: Date;
  expiresAt?: Date;
  condition: {
    id: string;
    name: string;
    description: string;
    category: string;
    effects: Record<string, any>;
  };
}

export interface IConditionDatabase {
  getAppliedConditions(actorId: string): Promise<DatabaseCondition[]>;
  applyCondition(actorId: string, conditionId: string, duration: number, source?: string): Promise<string>;
  updateConditionDuration(appliedConditionId: string, remainingDuration: number): Promise<void>;
  removeCondition(appliedConditionId: string): Promise<void>;
}

export class ConditionSyncService {
  private entityToActorMap = new Map<EntityId, string>(); // ECS Entity -> Database Actor ID
  private actorToEntityMap = new Map<string, EntityId>(); // Database Actor ID -> ECS Entity
  private appliedConditionMap = new Map<string, string>(); // ECS Condition ID -> Database Applied Condition ID

  constructor(
    private conditionsStore: any, // ECS Conditions Store
    private database?: IConditionDatabase
  ) {}

  /**
   * Register entity-actor mapping for condition sync
   */
  registerEntityActor(entityId: EntityId, actorId: string): void {
    this.entityToActorMap.set(entityId, actorId);
    this.actorToEntityMap.set(actorId, entityId);
  }

  /**
   * Unregister entity-actor mapping
   */
  unregisterEntity(entityId: EntityId): void {
    const actorId = this.entityToActorMap.get(entityId);
    if (actorId) {
      this.actorToEntityMap.delete(actorId);
      this.entityToActorMap.delete(entityId);
    }
  }

  /**
   * Sync conditions from database to ECS for a specific entity
   */
  async syncFromDatabase(entityId: EntityId): Promise<void> {
    if (!this.database) return;

    const actorId = this.entityToActorMap.get(entityId);
    if (!actorId) return;

    try {
      const dbConditions = await this.database.getAppliedConditions(actorId);

      // Clear existing ECS conditions for this entity
      this.conditionsStore.remove(entityId);

      // Apply database conditions to ECS
      for (const dbCondition of dbConditions) {
        const ecsCondition: Condition = {
          id: `db_${dbCondition.id}`,
          type: this.mapDbConditionToECSType(dbCondition.condition.name),
          duration: dbCondition.remainingDuration,
          source: dbCondition.source || 'database',
          appliedAt: dbCondition.appliedAt.getTime(),
          metadata: {
            dbId: dbCondition.id,
            dbConditionId: dbCondition.conditionId,
            ...dbCondition.metadata
          }
        };

        this.conditionsStore.add(entityId, ecsCondition);
        this.appliedConditionMap.set(ecsCondition.id, dbCondition.id);
      }
    } catch (error) {
      console.error(`Failed to sync conditions from database for entity ${entityId}:`, error);
    }
  }

  /**
   * Sync conditions from ECS to database for a specific entity
   */
  async syncToDatabase(entityId: EntityId): Promise<void> {
    if (!this.database) return;

    const actorId = this.entityToActorMap.get(entityId);
    if (!actorId) return;

    try {
      const ecsConditions = this.conditionsStore.getAll(entityId) || [];
      const dbConditions = await this.database.getAppliedConditions(actorId);

      // Create maps for efficient lookup
      const _ecsConditionMap = new Map(_ecsConditions.map((c: Condition) => [c.id, c]));
      const dbConditionMap = new Map(dbConditions.map(c => [c.id, c]));

      // Update existing conditions in database
      for (const ecsCondition of ecsConditions) {
        const dbId = this.appliedConditionMap.get(ecsCondition.id);

        if (dbId && dbConditionMap.has(dbId)) {
          // Update duration in database
          const dbCondition = dbConditionMap.get(dbId)!;
          if (dbCondition.remainingDuration !== ecsCondition.duration) {
            await this.database.updateConditionDuration(dbId, ecsCondition.duration);
          }
        } else if (!ecsCondition.metadata?.dbId) {
          // New ECS condition that needs to be added to database
          const conditionName = this.mapECSTypeToDbCondition(ecsCondition.type);
          if (conditionName) {
            const newDbId = await this.database.applyCondition(
              actorId,
              conditionName,
              ecsCondition.duration,
              ecsCondition.source
            );
            this.appliedConditionMap.set(ecsCondition.id, newDbId);
          }
        }
      }

      // Remove conditions from database that no longer exist in ECS
      for (const dbCondition of dbConditions) {
        const correspondingECSCondition = ecsConditions.find((c: Condition) => 
          this.appliedConditionMap.get(c.id) === dbCondition.id
        );

        if (!correspondingECSCondition) {
          await this.database.removeCondition(dbCondition.id);
          // Remove from our mapping
          const ecsId = [...this.appliedConditionMap.entries()]
            .find(_([, _dbId]) => dbId === dbCondition.id)?.[0];
          if (ecsId) {
            this.appliedConditionMap.delete(ecsId);
          }
        }
      }
    } catch (error) {
      console.error(`Failed to sync conditions to database for entity ${entityId}:`, error);
    }
  }

  /**
   * Apply a condition to ECS and sync to database
   */
  async applyCondition(
    entityId: EntityId,
    conditionType: ConditionType,
    duration: number,
    source?: string
  ): Promise<void> {
    // Apply to ECS first
    const condition: Condition = {
      id: `sync_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      type: conditionType,
      duration,
      source: source || 'system',
      appliedAt: Date.now(),
      metadata: Record<string, any>
    };

    this.conditionsStore.add(entityId, condition);

    // Sync to database
    if (this.database) {
      const actorId = this.entityToActorMap.get(entityId);
      if (actorId) {
        try {
          const conditionName = this.mapECSTypeToDbCondition(conditionType);
          if (conditionName) {
            const dbId = await this.database.applyCondition(
              actorId,
              conditionName,
              duration,
              source
            );
            this.appliedConditionMap.set(condition.id, dbId);

            // Update condition metadata
            condition.metadata.dbId = dbId;
          }
        } catch (error) {
          console.error(`Failed to sync new condition to database:`, error);
        }
      }
    }
  }

  /**
   * Remove a condition from ECS and sync to database
   */
  async removeCondition(entityId: EntityId, conditionId: string): Promise<void> {
    // Remove from ECS
    this.conditionsStore.remove(entityId, conditionId);

    // Sync to database
    const dbId = this.appliedConditionMap.get(conditionId);
    if (dbId && this.database) {
      try {
        await this.database.removeCondition(dbId);
        this.appliedConditionMap.delete(conditionId);
      } catch (error) {
        console.error(`Failed to remove condition from database:`, error);
      }
    }
  }

  /**
   * Update condition duration in ECS and sync to database
   */
  async updateConditionDuration(
    entityId: EntityId,
    conditionId: string,
    newDuration: number
  ): Promise<void> {
    // Update in ECS
    const conditions = this.conditionsStore.getAll(entityId) || [];
    const condition = conditions.find((c: Condition) => c.id === conditionId);

    if (condition) {
      condition.duration = newDuration;

      // Sync to database
      const dbId = this.appliedConditionMap.get(conditionId);
      if (dbId && this.database) {
        try {
          await this.database.updateConditionDuration(dbId, newDuration);
        } catch (error) {
          console.error(`Failed to update condition duration in database:`, error);
        }
      }
    }
  }

  /**
   * Periodic sync - should be called regularly to keep ECS and database in sync
   */
  async periodicSync(): Promise<void> {
    for (const entityId of this.entityToActorMap.keys()) {
      await this.syncFromDatabase(entityId);
      await this.syncToDatabase(entityId);
    }
  }

  /**
   * Map database condition name to ECS condition type
   */
  private mapDbConditionToECSType(conditionName: string): ConditionType {
    // Common D&D 5e conditions
    const mapping: Record<string, ConditionType> = {
      'blinded': 'blinded',
      'charmed': 'charmed',
      'deafened': 'deafened',
      'frightened': 'frightened',
      'grappled': 'grappled',
      'incapacitated': 'incapacitated',
      'invisible': 'invisible',
      'paralyzed': 'paralyzed',
      'petrified': 'petrified',
      'poisoned': 'poisoned',
      'prone': 'prone',
      'restrained': 'restrained',
      'stunned': 'stunned',
      'unconscious': 'unconscious',
      'exhaustion': 'exhaustion'
    };

    return mapping[conditionName.toLowerCase()] || 'custom';
  }

  /**
   * Map ECS condition type to database condition name
   */
  private mapECSTypeToDbCondition(conditionType: ConditionType): string | null {
    // Reverse mapping
    const mapping: Record<ConditionType, string> = {
      'blinded': 'blinded',
      'charmed': 'charmed',
      'deafened': 'deafened',
      'frightened': 'frightened',
      'grappled': 'grappled',
      'incapacitated': 'incapacitated',
      'invisible': 'invisible',
      'paralyzed': 'paralyzed',
      'petrified': 'petrified',
      'poisoned': 'poisoned',
      'prone': 'prone',
      'restrained': 'restrained',
      'stunned': 'stunned',
      'unconscious': 'unconscious',
      'exhaustion': 'exhaustion',
      'custom': 'custom'
    };

    return mapping[conditionType] || null;
  }

  /**
   * Get sync statistics
   */
  getSyncStats(): {
    entitiesTracked: number;
    conditionsMapped: number;
  } {
    return {
      entitiesTracked: this.entityToActorMap.size,
      conditionsMapped: this.appliedConditionMap.size
    };
  }
}

/**
 * ECS Bridge Service - Connects CharacterService and MonsterService to ECS components
 */

import { World, EntityId } from '../World';
import { _HealthStore } from '../components/Health';
import { _StatsStore } from '../components/Stats';
import { _ConditionsStore, Condition } from '../components/Conditions';
import { _CombatStore } from '../components/Combat';
// Service interfaces for dependency injection
export interface ICharacterService {
  getCharacter(id: string): Promise<any>;
  updateCharacter(id: string, userId: string, updates: any): Promise<any>;
}

export interface IMonsterService {
  getMonster(id: string): Promise<any>;
}

export interface EntityData {
  id: string;
  name: string;
  type: 'character' | 'monster';
  hitPoints: {
    current: number;
    max: number;
    temporary: number;
  };
  armorClass: number;
  abilities: Record<string, { value: number; modifier: number }>;
  conditions?: Condition[];
  initiative?: number;
}

export interface MonsterStatblock {
  name: string;
  hitPoints: number;
  armorClass: number;
  abilities: {
    STR: number;
    DEX: number;
    CON: number;
    INT: number;
    WIS: number;
    CHA: number;
  };
  skills?: Record<string, number>;
  savingThrows?: Record<string, number>;
  conditionImmunities?: string[];
  damageResistances?: string[];
  damageImmunities?: string[];
}

export class ECSBridgeService {
  // Entity mapping
  private entityMap = new Map<string, EntityId>(); // External ID -> ECS EntityId
  private reverseEntityMap = new Map<EntityId, string>(); // ECS EntityId -> External ID

  constructor(
    private world: World,
    private characterService?: ICharacterService,
    private monsterService?: IMonsterService
  ) {}

  /**
   * Initialize with service dependencies
   */
  initialize(characterService?: ICharacterService, monsterService?: IMonsterService): void {
    this.characterService = characterService;
    this.monsterService = monsterService;
  }

  /**
   * Create ECS entity from character data
   */
  async createCharacterEntity(characterId: string): Promise<EntityId> {
    if (!this.characterService) {
      throw new Error('CharacterService not initialized');
    }

    const character = await this.characterService.getCharacter(characterId);
    if (!character) {
      throw new Error(`Character ${characterId} not found`);
    }

    const entityId = this.world.createEntity();

    // Map the entity
    this.entityMap.set(characterId, entityId);
    this.reverseEntityMap.set(entityId, characterId);

    // Create health component
    this.healthStore.add(entityId, {
      current: character.hitPoints.current,
      max: character.hitPoints.max,
      temporary: character.hitPoints.temporary || 0
    });

    // Set stats - convert character abilities format to ECS format
    this.world.stats.add(entityId, {
      abilities: {
        strength: character.abilities.STR?.value || 10,
        dexterity: character.abilities.DEX?.value || 10,
        constitution: character.abilities.CON?.value || 10,
        intelligence: character.abilities.INT?.value || 10,
        wisdom: character.abilities.WIS?.value || 10,
        charisma: character.abilities.CHA?.value || 10
      },
      proficiencyBonus: character.proficiencyBonus || 2,
      armorClass: character.armorClass || 10,
      speed: character.speed || 30,
      level: character.level || 1,
      hitDie: character.hitDie || 'd8'
    });
    this.statsStore.add(entityId, {
      STR: { value: character.abilities.STR?.value || 10, modifier: character.abilities.STR?.modifier || 0 },
      DEX: { value: character.abilities.DEX?.value || 10, modifier: character.abilities.DEX?.modifier || 0 },
      CON: { value: character.abilities.CON?.value || 10, modifier: character.abilities.CON?.modifier || 0 },
      INT: { value: character.abilities.INT?.value || 10, modifier: character.abilities.INT?.modifier || 0 },
      WIS: { value: character.abilities.WIS?.value || 10, modifier: character.abilities.WIS?.modifier || 0 },
      CHA: { value: character.abilities.CHA?.value || 10, modifier: character.abilities.CHA?.modifier || 0 }
    });

    // Initialize combat component
    this.world.combat.add(entityId, {
      initiative: character.initiative || 0,
      isActive: false,
      hasActed: false,
      hasMovedThisTurn: false,
      actionPoints: 1,
      maxActionPoints: 1,
      reactionUsed: false,
      concentrating: false
    });
    this.combatStore.add(entityId, {
      initiative: character.initiative || 0,
      isActive: false,
      actionPoints: 1,
      bonusActionUsed: false,
      reactionUsed: false,
      hasMovedThisTurn: false,
      turnOrder: -1
    });

    return entityId;
  }

  /**
   * Create ECS entity from monster data
   */
  async createMonsterEntity(monsterId: string, instanceName?: string): Promise<EntityId> {
    if (!this.monsterService) {
      throw new Error('MonsterService not initialized');
    }

    const monster = await this.monsterService.getMonster(monsterId);
    if (!monster) {
      throw new Error(`Monster ${monsterId} not found`);
    }

    const entityId = this.world.createEntity();
    const instanceId = instanceName ? `${monsterId}_${instanceName}` : monsterId;

    // Map the entity
    this.entityMap.set(instanceId, entityId);
    this.reverseEntityMap.set(entityId, instanceId);

    // Extract statblock data
    const statblock = monster.statblock as MonsterStatblock;
    const maxHP = typeof statblock.hitPoints === 'number' ? statblock.hitPoints : this.calculateHPFromString(statblock.hitPoints as any);

    // Create health component
    this.healthStore.add(entityId, {
      current: maxHP,
      max: maxHP,
      temporary: 0
    });

    // Set stats from statblock - convert to ECS format
    this.world.stats.add(entityId, {
      abilities: {
        strength: statblock.abilities.STR || 10,
        dexterity: statblock.abilities.DEX || 10,
        constitution: statblock.abilities.CON || 10,
        intelligence: statblock.abilities.INT || 10,
        wisdom: statblock.abilities.WIS || 10,
        charisma: statblock.abilities.CHA || 10
      },
      proficiencyBonus: Math.max(2, Math.floor((statblock.hitPoints + 7) / 4)), // CR-based proficiency
      armorClass: statblock.armorClass,
      speed: statblock.speed || 30,
      level: Math.max(1, Math.floor(statblock.hitPoints / 2)) || 1,
      hitDie: 'd8' // Default for monsters
    });
    this.statsStore.add(entityId, {
      STR: { value: statblock.abilities.STR, modifier: Math.floor((statblock.abilities.STR - 10) / 2) },
      DEX: { value: statblock.abilities.DEX, modifier: Math.floor((statblock.abilities.DEX - 10) / 2) },
      CON: { value: statblock.abilities.CON, modifier: Math.floor((statblock.abilities.CON - 10) / 2) },
      INT: { value: statblock.abilities.INT, modifier: Math.floor((statblock.abilities.INT - 10) / 2) },
      WIS: { value: statblock.abilities.WIS, modifier: Math.floor((statblock.abilities.WIS - 10) / 2) },
      CHA: { value: statblock.abilities.CHA, modifier: Math.floor((statblock.abilities.CHA - 10) / 2) }
    });

    // Initialize combat component with monster initiative bonus
    const _dexModifier = Math.floor((statblock.abilities.DEX - 10) / 2);
    this.world.combat.add(entityId, {
      initiative: 0,
      isActive: false,
      hasActed: false,
      hasMovedThisTurn: false,
      actionPoints: 1,
      maxActionPoints: 1,
      reactionUsed: false,
      concentrating: false
    });
    this.combatStore.add(entityId, {
      initiative: 0,
      isActive: false,
      actionPoints: 1,
      bonusActionUsed: false,
      reactionUsed: false,
      hasMovedThisTurn: false,
      turnOrder: -1
    });

    return entityId;
  }

  /**
   * Sync ECS stats back to character service
   */
  async syncStatsToCharacter(entityId: number): Promise<void> {
    if (!this.characterService) return;

    const externalId = this.reverseEntityMap.get(entityId);
    if (!externalId || !externalId.startsWith('char')) return;

    const characterId = externalId.substring(5);
    const stats = this.statsStore.get(entityId);
    if (!stats) return;

    // Convert ECS stats format back to character format
    const characterUpdates = {
      abilities: {
        STR: { value: stats.STR.value, modifier: stats.STR.modifier },
        DEX: { value: stats.DEX.value, modifier: stats.DEX.modifier },
        CON: { value: stats.CON.value, modifier: stats.CON.modifier },
        INT: { value: stats.INT.value, modifier: stats.INT.modifier },
        WIS: { value: stats.WIS.value, modifier: stats.WIS.modifier },
        CHA: { value: stats.CHA.value, modifier: stats.CHA.modifier }
      }
    };

    await this.characterService.updateCharacter(characterId, 'system', characterUpdates);
  }

  /**
   * Sync character changes back to CharacterService
   */
  async syncCharacterToService(characterId: string): Promise<void> {
    if (!this.characterService) return;

    const entityId = this.entityMap.get(characterId);
    if (!entityId) return;

    const health = this.healthStore.get(entityId);
    const _conditions = this.conditionsStore.get(entityId);

    if (health) {
      await this.characterService.updateCharacter(characterId, 'system', {
        hitPoints: {
          current: health.current,
          max: health.max,
          temporary: health.temporary
        }
      });
    }

    // Sync conditions if needed
    // This would require extending the character service to handle conditions
  }

  /**
   * Apply damage to entity and sync back to services
   */
  async applyDamage(externalId: string, damage: number, _damageType: string = 'untyped'): Promise<boolean> {
    const entityId = this.entityMap.get(externalId);
    if (!entityId) return false;

    const success = this.healthStore.takeDamage(entityId, damage);

    if (success) {
      // Sync back to appropriate service
      if (externalId.includes('')) {
        // Monster instance - no sync needed typically
      } else {
        await this.syncCharacterToService(externalId);
      }
    }

    return success;
  }

  /**
   * Apply healing to entity and sync back to services
   */
  async applyHealing(externalId: string, healing: number): Promise<boolean> {
    const entityId = this.entityMap.get(externalId);
    if (!entityId) return false;

    const success = this.healthStore.heal(entityId, healing);

    if (success) {
      if (!externalId.includes('')) {
        await this.syncCharacterToService(externalId);
      }
    }

    return success;
  }

  /**
   * Apply condition to entity
   */
  applyCondition(externalId: string, condition: Condition): boolean {
    const entityId = this.entityMap.get(externalId);
    if (!entityId) return false;

    this.conditionsStore.add(entityId, condition);
    return true;
  }

  /**
   * Remove condition from entity
   */
  removeCondition(externalId: string, conditionType: string): boolean {
    const entityId = this.entityMap.get(externalId);
    if (!entityId) return false;

    this.conditionsStore.remove(entityId, conditionType as any);
    return true;
  }

  /**
   * Get entity data for external use
   */
  getEntityData(externalId: string): EntityData | null {
    const entityId = this.entityMap.get(externalId);
    if (!entityId) return null;

    const health = this.healthStore.get(entityId);
    const stats = this.statsStore.get(entityId);
    const conditions = this.conditionsStore.get(entityId);
    const combat = this.combatStore.get(entityId);

    if (!health || !stats) return null;

    return {
      id: externalId,
      name: externalId, // Would need to store name separately
      type: externalId.includes('') ? 'monster' : 'character',
      hitPoints: {
        current: health.current,
        max: health.max,
        temporary: health.temporary
      },
      armorClass: 10, // Would need to calculate from stats and equipment
      abilities: stats,
      conditions,
      initiative: combat?.initiative
    };
  }

  /**
   * Remove entity from ECS
   */
  removeEntity(externalId: string): boolean {
    const entityId = this.entityMap.get(externalId);
    if (!entityId) return false;

    // Remove from all component stores
    this.healthStore.remove(entityId);
    this.statsStore.remove(entityId);
    this.conditionsStore.remove(entityId);
    this.combatStore.remove(entityId);

    // Clean up mappings
    this.entityMap.delete(externalId);
    this.reverseEntityMap.delete(entityId);

    // Destroy entity in world
    this.world.destroyEntity(entityId);

    return true;
  }

  /**
   * Get all managed entities
   */
  getAllEntities(): string[] {
    return Array.from(this.entityMap.keys());
  }

  /**
   * Get ECS EntityId from external ID
   */
  getECSEntityId(externalId: string): EntityId | undefined {
    return this.entityMap.get(externalId);
  }

  /**
   * Get external ID from ECS EntityId
   */
  getExternalId(entityId: EntityId): string | undefined {
    return this.reverseEntityMap.get(entityId);
  }

  /**
   * Calculate HP from D&D hit dice string (e.g., "4d8+4")
   */
  private calculateHPFromString(hpString: string | number): number {
    if (typeof hpString === 'number') return hpString;

    // Simple parsing for formats like "4d8+4" or "58 (9d8 + 18)"
    const match = hpString.toString().match(/(\d+)(?:\s*\([^)]+\))?/);
    return match ? parseInt(match[1]) : 10;
  }

  /**
   * Batch sync all entities back to services
   */
  async syncAllToServices(): Promise<void> {
    const promises = [];

    for (const [externalId, _entityId] of this.entityMap) {
      if (!externalId.includes('')) { // Only sync characters, not monster instances
        promises.push(this.syncCharacterToService(externalId));
      }
    }

    await Promise.all(promises);
  }
}

export type EntityId = number;

export interface CombatData {
  initiative: number;
  turnOrder: number;
  isActive: boolean;
  hasActed: boolean;
  hasMovedThisTurn: boolean;
  actionPoints: number;
  maxActionPoints: number;
  reactionUsed: boolean;
  concentrating: boolean;
  concentrationTarget?: EntityId | undefined;
  legendaryActions?: number | undefined;
  maxLegendaryActions?: number | undefined;
  usedLegendaryActions?: number | undefined;
}

export class CombatStore {
  private data: Map<EntityId, CombatData> = new Map();
  private capacity: number;

  // Object pooling for performance optimization
  private static readonly POOL_SIZE = 1000;
  private dataPool: CombatData[] = [];
  private recycledEntities: Set<EntityId> = new Set();
  private poolIndex = 0;

  // Initiative order caching
  private initiativeOrderCache: EntityId[] = [];
  private initiativeCacheValid = false;
  private lastInitiativeUpdate = 0;

  constructor(capacity: number) {
    this.capacity = capacity;
    this.initializePool();
  }

  private initializePool(): void {
    for (let i = 0; i < CombatStore.POOL_SIZE; i++) {
      this.dataPool.push({
        initiative: 0,
        turnOrder: 0,
        isActive: false,
        hasActed: false,
        hasMovedThisTurn: false,
        actionPoints: 1,
        maxActionPoints: 1,
        reactionUsed: false,
        concentrating: false,
        concentrationTarget: undefined,
        legendaryActions: undefined,
        maxLegendaryActions: undefined,
        usedLegendaryActions: 0
      });
    }
  }

  private getPooledData(): CombatData {
    // Use pool if available
    if (this.poolIndex < this.dataPool.length) {
      const pooledData = this.dataPool[this.poolIndex++];
      if (pooledData) {
        return pooledData;
      }
    }

    // Fallback to new object if pool exhausted
    return {
      initiative: 0,
      turnOrder: 0,
      isActive: false,
      hasActed: false,
      hasMovedThisTurn: false,
      actionPoints: 1,
      maxActionPoints: 1,
      reactionUsed: false,
      concentrating: false,
      concentrationTarget: undefined,
      legendaryActions: undefined,
      maxLegendaryActions: undefined,
      usedLegendaryActions: 0
    };
  }

  private invalidateInitiativeCache(): void {
    this.initiativeCacheValid = false;
    this.lastInitiativeUpdate = performance.now();
  }

  add(id: EntityId, data: Partial<CombatData> = {}): void {
    const pooledData = this.getPooledData();

    // Assign values to pooled object
    pooledData.initiative = data.initiative ?? 0;
    pooledData.turnOrder = data.turnOrder ?? 0;
    pooledData.isActive = data.isActive ?? false;
    pooledData.hasActed = data.hasActed ?? false;
    pooledData.hasMovedThisTurn = data.hasMovedThisTurn ?? false;
    pooledData.actionPoints = data.actionPoints ?? 1;
    pooledData.maxActionPoints = data.maxActionPoints ?? 1;
    pooledData.reactionUsed = data.reactionUsed ?? false;
    pooledData.concentrating = data.concentrating ?? false;
    pooledData.concentrationTarget = data.concentrationTarget;
    pooledData.legendaryActions = data.legendaryActions;
    pooledData.maxLegendaryActions = data.maxLegendaryActions;
    pooledData.usedLegendaryActions = data.usedLegendaryActions ?? 0;

    this.data.set(id, pooledData);
    this.invalidateInitiativeCache();
  }

  get(id: EntityId): CombatData | undefined {
    return this.data.get(id);
  }

  has(id: EntityId): boolean {
    return this.data.has(id);
  }

  remove(id: EntityId): void {
    const data = this.data.get(id);
    if (data) {
      // Return to pool for reuse
      this.recycledEntities.add(id);
      this.invalidateInitiativeCache();
    }
    this.data.delete(id);
  }

  setInitiative(id: EntityId, initiative: number): void {
    const combat = this.data.get(id);
    if (combat) {
      combat.initiative = initiative;
      this.invalidateInitiativeCache();
    }
  }

  setTurnOrder(id: EntityId, order: number): void {
    const combat = this.data.get(id);
    if (combat) {
      combat.turnOrder = order;
    }
  }

  setActive(id: EntityId, active: boolean): void {
    const combat = this.data.get(id);
    if (combat) {
      combat.isActive = active;
    }
  }

  startTurn(id: EntityId): void {
    const combat = this.data.get(id);
    if (combat) {
      combat.hasActed = false;
      combat.hasMovedThisTurn = false;
      combat.actionPoints = combat.maxActionPoints;
      combat.reactionUsed = false;
    }
  }

  endTurn(id: EntityId): void {
    const combat = this.data.get(id);
    if (combat) {
      combat.hasActed = true;
      combat.actionPoints = 0;
    }
  }

  useAction(id: EntityId, cost: number = 1): boolean {
    const combat = this.data.get(id);
    if (combat && combat.actionPoints >= cost) {
      combat.actionPoints -= cost;
      combat.hasActed = true;
      return true;
    }
    return false;
  }

  useReaction(id: EntityId): boolean {
    const combat = this.data.get(id);
    if (combat && !combat.reactionUsed) {
      combat.reactionUsed = true;
      return true;
    }
    return false;
  }

  setConcentration(id: EntityId, target?: EntityId): void {
    const combat = this.data.get(id);
    if (combat) {
      combat.concentrating = target !== undefined;
      combat.concentrationTarget = target;
    }
  }

  breakConcentration(id: EntityId): void {
    const combat = this.data.get(id);
    if (combat) {
      combat.concentrating = false;
      combat.concentrationTarget = undefined;
    }
  }

  useLegendaryAction(id: EntityId, cost: number = 1): boolean {
    const combat = this.data.get(id);
    if (combat && combat.legendaryActions && combat.usedLegendaryActions !== undefined) {
      const remaining = combat.legendaryActions - combat.usedLegendaryActions;
      if (remaining >= cost) {
        combat.usedLegendaryActions += cost;
        return true;
      }
    }
    return false;
  }

  resetLegendaryActions(id: EntityId): void {
    const combat = this.data.get(id);
    if (combat) {
      combat.usedLegendaryActions = 0;
    }
  }

  hasLegendaryActions(id: EntityId): boolean {
    const combat = this.data.get(id);
    return combat?.legendaryActions !== undefined && combat.legendaryActions > 0;
  }

  getLegendaryActionsRemaining(id: EntityId): number {
    const combat = this.data.get(id);
    if (combat && combat.legendaryActions && combat.usedLegendaryActions !== undefined) {
      return combat.legendaryActions - combat.usedLegendaryActions;
    }
    return 0;
  }

  getAllInCombat(): EntityId[] {
    return Array.from(this.data.keys());
  }

  getInitiativeOrder(): EntityId[] {
    // Return cached result if valid
    if (this.initiativeCacheValid && this.initiativeOrderCache.length > 0) {
      return [...this.initiativeOrderCache];
    }

    // Rebuild cache
    this.initiativeOrderCache = Array.from(this.data.entries())
      .sort(_([, _a], [, _b]) => b.initiative - a.initiative || a.turnOrder - b.turnOrder)
      .map([id] => id);

    this.initiativeCacheValid = true;
    return [...this.initiativeOrderCache];
  }

  // Performance monitoring methods
  getPerformanceStats() {
    return {
      poolUtilization: this.poolIndex / CombatStore.POOL_SIZE,
      recycledEntities: this.recycledEntities.size,
      totalEntities: this.data.size,
      lastInitiativeUpdate: this.lastInitiativeUpdate,
      cacheValid: this.initiativeCacheValid
    };
  }

  // Reset pool for testing/cleanup
  resetPool(): void {
    this.poolIndex = 0;
    this.recycledEntities.clear();
    this.invalidateInitiativeCache();
  }
}

import { _SparseSet, MultiSparseSet } from './SparseSet';

export interface ComponentChange {
  entityId: number;
  componentType: string;
  changeType: 'added' | 'modified' | 'removed';
  timestamp: number;
  version: number;
}

export interface ComponentQuery {
  required: string[];
  optional?: string[];
  excluded?: string[];
}

/**
 * High-performance component manager using sparse sets and dirty tracking
 */
export class ComponentManager {
  private componentStorage = new MultiSparseSet();
  private changeLog: ComponentChange[] = [];
  private dirtyEntities = new Set<number>();
  private systemQueries = new Map<string, ComponentQuery>();
  private queryResultCache = new Map<string, { entities: number[]; version: number }>();
  private globalVersion = 0;
  private maxChangeLogSize = 1000;

  /**
   * Register a component type with optional initialization
   */
  registerComponent<T>(_componentType: string, _initializer?: () => T): void {
    this.componentStorage.getSet<T>(componentType);
  }

  /**
   * Add or update a component for an entity
   */
  setComponent<T>(entityId: number, componentType: string, component: T): void {
    const hadComponent = this.componentStorage.hasComponent(entityId, componentType);

    this.componentStorage.setComponent(entityId, componentType, component);

    // Track changes
    this.recordChange(entityId, componentType, hadComponent ? 'modified' : 'added');
    this.markEntityDirty(entityId);
    this.invalidateQueriesForComponent(componentType);
  }

  /**
   * Get a component for an entity
   */
  getComponent<T>(entityId: number, componentType: string): T | undefined {
    return this.componentStorage.getComponent<T>(entityId, componentType);
  }

  /**
   * Check if an entity has a component
   */
  hasComponent(entityId: number, componentType: string): boolean {
    return this.componentStorage.hasComponent(entityId, componentType);
  }

  /**
   * Remove a component from an entity
   */
  removeComponent(entityId: number, componentType: string): boolean {
    if (this.componentStorage.removeComponent(entityId, componentType)) {
      this.recordChange(entityId, componentType, 'removed');
      this.markEntityDirty(entityId);
      this.invalidateQueriesForComponent(componentType);
      return true;
    }
    return false;
  }

  /**
   * Remove all components for an entity
   */
  removeEntity(entityId: number): void {
    // Get all component types for this entity before removal
    const componentTypes = this.getEntityComponentTypes(entityId);

    this.componentStorage.removeEntity(entityId);

    // Record changes for all removed components
    for (const componentType of componentTypes) {
      this.recordChange(entityId, componentType, 'removed');
    }

    this.markEntityDirty(entityId);
    this.invalidateAllQueries();
  }

  /**
   * Query entities based on component requirements
   */
  queryEntities(query: ComponentQuery): number[] {
    const queryKey = this.getQueryKey(query);
    const cached = this.queryResultCache.get(queryKey);

    // Return cached result if still valid
    if (cached && cached.version === this.globalVersion) {
      return [...cached.entities];
    }

    // Execute query
    const entities = this.executeQuery(query);

    // Cache result
    this.queryResultCache.set(queryKey, {
      entities: [...entities],
      version: this.globalVersion
    });

    return entities;
  }

  /**
   * Register a system's query for optimization
   */
  registerSystemQuery(systemName: string, query: ComponentQuery): void {
    this.systemQueries.set(systemName, query);
  }

  /**
   * Get entities that match a registered system query
   */
  getSystemEntities(systemName: string): number[] {
    const query = this.systemQueries.get(systemName);
    if (!query) {
      throw new Error(`System query not registered: ${systemName}`);
    }
    return this.queryEntities(query);
  }

  /**
   * Get entities that have been modified since last update
   */
  getDirtyEntities(): Set<number> {
    return new Set(this.dirtyEntities);
  }

  /**
   * Clear dirty flags (call after processing updates)
   */
  clearDirtyFlags(): void {
    this.dirtyEntities.clear();
  }

  /**
   * Get recent component changes
   */
  getRecentChanges(sinceVersion?: number): ComponentChange[] {
    if (sinceVersion === undefined) {
      return [...this.changeLog];
    }
    return this.changeLog.filter(change => change.version > sinceVersion);
  }

  /**
   * Get all component types for an entity
   */
  getEntityComponentTypes(entityId: number): string[] {
    const types: string[] = [];
    const stats = this.componentStorage.getMemoryStats();

    for (const componentType in stats.componentStats) {
      if (this.componentStorage.hasComponent(entityId, componentType)) {
        types.push(componentType);
      }
    }

    return types;
  }

  /**
   * Get performance and memory statistics
   */
  getStats() {
    const memoryStats = this.componentStorage.getMemoryStats();
    const queryCacheSize = this.queryResultCache.size;
    const changeLogSize = this.changeLog.length;

    return {
      ...memoryStats,
      dirtyEntities: this.dirtyEntities.size,
      changeLogSize,
      queryCacheSize,
      registeredSystems: this.systemQueries.size,
      globalVersion: this.globalVersion
    };
  }

  /**
   * Compact storage to save memory
   */
  compact(): void {
    this.componentStorage.compact();

    // Trim change log if too large
    if (this.changeLog.length > this.maxChangeLogSize) {
      this.changeLog = this.changeLog.slice(-this.maxChangeLogSize / 2);
    }

    // Clear old query cache
    this.queryResultCache.clear();
  }

  /**
   * Batch operations for better performance
   */
  batch(_operations: (() => void)[]): void {
    const startVersion = this.globalVersion;

    // Execute all operations without triggering individual updates
    for (const operation of operations) {
      operation();
    }

    // Single version increment for the entire batch
    if (this.globalVersion === startVersion) {
      this.globalVersion++;
    }

    // Clear query cache once for the entire batch
    this.queryResultCache.clear();
  }

  /**
   * Execute a component query
   */
  private executeQuery(query: ComponentQuery): number[] {
    if (query.required.length === 0) {
      return [];
    }

    // Start with entities that have the first required component
    let entities = this.componentStorage.getEntitiesWithComponent(query.required[0]!);

    // Filter by remaining required components
    for (let i = 1; i < query.required.length; i++) {
      const req = query.required[i]!;
      entities = entities.filter(entityId => 
        this.componentStorage.hasComponent(entityId, req)
      );
    }

    // Filter out excluded components
    if (query.excluded) {
      entities = entities.filter(entityId =>
        !query.excluded!.some(excludedType => 
          this.componentStorage.hasComponent(entityId, excludedType)
        )
      );
    }

    return entities;
  }

  /**
   * Generate a unique key for a query
   */
  private getQueryKey(query: ComponentQuery): string {
    const required = query.required.sort().join(',');
    const optional = query.optional ? query.optional.sort().join(',') : '';
    const excluded = query.excluded ? query.excluded.sort().join(',') : '';
    return `req:${required}|opt:${optional}|exc:${excluded}`;
  }

  /**
   * Record a component change
   */
  private recordChange(entityId: number, componentType: string, changeType: ComponentChange['changeType']): void {
    this.globalVersion++;

    const change: ComponentChange = {
      entityId,
      componentType,
      changeType,
      timestamp: Date.now(),
      version: this.globalVersion
    };

    this.changeLog.push(change);

    // Limit change log size
    if (this.changeLog.length > this.maxChangeLogSize) {
      this.changeLog.shift();
    }
  }

  /**
   * Mark an entity as dirty
   */
  private markEntityDirty(entityId: number): void {
    this.dirtyEntities.add(entityId);
  }

  /**
   * Invalidate queries that depend on a component type
   */
  private invalidateQueriesForComponent(componentType: string): void {
    for (const [queryKey, _cached] of this.queryResultCache) {
      if (queryKey.includes(componentType)) {
        this.queryResultCache.delete(queryKey);
      }
    }
  }

  /**
   * Invalidate all cached queries
   */
  private invalidateAllQueries(): void {
    this.queryResultCache.clear();
  }

  /**
   * Clear all data
   */
  clear(): void {
    this.componentStorage.clear();
    this.changeLog = [];
    this.dirtyEntities.clear();
    this.queryResultCache.clear();
    this.globalVersion = 0;
  }
}

/**
 * Equipment Integration Service
 * Connects equipment effects to ECS components and combat systems
 */

import { EntityId } from '../components/Combat';
import { StatsStore } from '../components/Stats';
import { HealthStore } from '../components/Health';
import { CombatStore } from '../components/Combat';
import { equipmentEffectsEngine, type Equipment } from '@vtt/equipment-effects';

export interface CharacterEquipmentState {
  characterId: string;
  entityId: EntityId;
  equipment: Equipment[];
  lastUpdated: number;
}

export class EquipmentIntegrationService {
  private characterStates = new Map<string, CharacterEquipmentState>();
  private statsStore: StatsStore;
  private healthStore: HealthStore;
  private combatStore: CombatStore;

  constructor(
    statsStore: StatsStore,
    healthStore: HealthStore,
    combatStore: CombatStore
  ) {
    this.statsStore = statsStore;
    this.healthStore = healthStore;
    this.combatStore = combatStore;
  }

  /**
   * Initialize equipment for a character
   */
  initializeCharacterEquipment(
    characterId: string,
    entityId: EntityId,
    equipment: Equipment[]
  ): void {
    const state: CharacterEquipmentState = {
      characterId,
      entityId,
      equipment,
      lastUpdated: Date.now()
    };

    this.characterStates.set(characterId, state);
    equipmentEffectsEngine.initializeCharacterEquipment(characterId, equipment);

    // Apply initial equipment bonuses
    this.updateCharacterBonuses(characterId);
  }

  /**
   * Update equipment for a character
   */
  updateEquipment(characterId: string, equipment: Equipment[]): void {
    const state = this.characterStates.get(characterId);
    if (!state) return;

    state.equipment = equipment;
    state.lastUpdated = Date.now();

    equipmentEffectsEngine.initializeCharacterEquipment(characterId, equipment);
    this.updateCharacterBonuses(characterId);
  }

  /**
   * Equip/unequip an item
   */
  toggleEquipment(characterId: string, itemId: string, equipped: boolean): boolean {
    const state = this.characterStates.get(characterId);
    if (!state) return false;

    const item = state.equipment.find(e => e.id === itemId);
    if (!item) return false;

    item.equipped = equipped;
    state.lastUpdated = Date.now();

    this.updateCharacterBonuses(characterId);
    return true;
  }

  /**
   * Attune to a magical item
   */
  attuneToItem(characterId: string, itemId: string): boolean {
    const state = this.characterStates.get(characterId);
    if (!state) return false;

    const item = state.equipment.find(e => e.id === itemId);
    if (!item || !item.requiresAttunement) return false;

    // Check attunement limits (typically 3 items)
    const currentlyAttuned = state.equipment.filter(e => e.attuned).length;
    if (currentlyAttuned >= 3) return false;

    item.attuned = true;
    state.lastUpdated = Date.now();

    this.updateCharacterBonuses(characterId);
    return true;
  }

  /**
   * Process equipment triggers during combat events
   */
  processCombatTriggers(
    characterId: string,
    event: string,
    context?: any
  ): any[] {
    const character = this.getCharacterData(characterId);
    if (!character) return [];

    return equipmentEffectsEngine.processTriggers(characterId, event, character, context);
  }

  /**
   * Handle weapon attack with equipment bonuses
   */
  processWeaponAttack(
    characterId: string,
    weaponId: string,
    targetId: string,
    context: any
  ): {
    attackBonus: number;
    damageBonus: string;
    effects: any[];
  } {
    const character = this.getCharacterData(characterId);
    if (!character) {
      return { attackBonus: 0, damageBonus: '', effects: [] };
    }

    return equipmentEffectsEngine.processWeaponAttack(
      characterId,
      weaponId,
      character,
      targetId,
      context
    );
  }

  /**
   * Apply equipment bonuses to character stats
   */
  private updateCharacterBonuses(characterId: string): void {
    const state = this.characterStates.get(characterId);
    if (!state) return;

    const character = this.getCharacterData(characterId);
    if (!character) return;

    // Calculate equipment bonuses
    const bonuses = equipmentEffectsEngine.calculateEquipmentBonuses(characterId, character);

    // Apply bonuses to ECS components
    const statsData = this.statsStore.get(state.entityId);
    if (statsData) {
      // Update ability scores with equipment bonuses
      Object.entries(bonuses.abilityScores).forEach(_([ability, _bonus]) => {
        if (bonus !== 0) {
          statsData.abilities[ability as keyof typeof statsData.abilities] += bonus;
        }
      });

      // Update other stats
      statsData.armorClass += bonuses.ac;
      statsData.proficiencyBonus += bonuses.attackBonus; // Simplified for now
    }

    // Apply passive equipment effects
    equipmentEffectsEngine.applyPassiveEffects(characterId, character);
  }

  /**
   * Get character data for equipment calculations
   */
  private getCharacterData(characterId: string): any {
    const state = this.characterStates.get(characterId);
    if (!state) return null;

    const statsData = this.statsStore.get(state.entityId);
    const healthData = this.healthStore.get(state.entityId);
    const combatData = this.combatStore.get(state.entityId);

    if (!statsData || !healthData) return null;

    return {
      id: characterId,
      entityId: state.entityId,
      hitPoints: {
        current: healthData.currentHitPoints,
        max: healthData.maxHitPoints,
        temporary: healthData.temporaryHitPoints
      },
      abilities: statsData.abilities,
      armorClass: statsData.armorClass,
      proficiencyBonus: statsData.proficiencyBonus,
      level: statsData.level || 1,
      class: statsData.characterClass,
      alignment: statsData.alignment,
      initiative: combatData?.initiative || 0,
      isInCombat: combatData?.isActive || false
    };
  }

  /**
   * Recharge magic items at specified times
   */
  rechargeItems(characterId: string, timeOfDay: 'dawn' | 'dusk' | 'midnight'): void {
    equipmentEffectsEngine.rechargeItems(characterId, timeOfDay);

    const state = this.characterStates.get(characterId);
    if (state) {
      state.lastUpdated = Date.now();
    }
  }

  /**
   * Get equipped items for a character
   */
  getEquippedItems(characterId: string): Equipment[] {
    return equipmentEffectsEngine.getEquippedItems(characterId);
  }

  /**
   * Get all equipment for a character
   */
  getAllEquipment(characterId: string): Equipment[] {
    const state = this.characterStates.get(characterId);
    return state ? [...state.equipment] : [];
  }
}

import { EntityId } from '../components/Combat';
import { AIDecision, AIBehavior } from './MonsterAISystem';

export interface ActionOutcome {
  actionType: string;
  success: boolean;
  damageDealt: number;
  damageTaken: number;
  healthPercentageBefore: number;
  healthPercentageAfter: number;
  enemiesDefeated: number;
  timestamp: number;
  context: {
    enemyCount: number;
    allyCount: number;
    turnNumber: number;
    roundNumber: number;
  };
}

export interface LearningParameters {
  learningRate: number;
  discountFactor: number;
  explorationRate: number;
  explorationDecay: number;
  minExplorationRate: number;
  memorySize: number;
  batchSize: number;
}

export interface QValueState {
  healthPercentage: number;
  enemyCount: number;
  allyCount: number;
  actionPoints: number;
  hasAdvantage: boolean;
}

export interface QValue {
  state: QValueState;
  action: string;
  value: number;
  confidence: number;
  sampleCount: number;
}

export class AILearningSystem {
  private qValues: Map<string, QValue> = new Map();
  private experienceReplay: ActionOutcome[] = [];
  private parameters: LearningParameters;
  private entityLearningData: Map<EntityId, {
    totalReward: number;
    actionCount: number;
    lastUpdate: number;
    personalityAdjustments: Partial<AIBehavior>;
  }> = new Map();

  constructor(parameters?: Partial<LearningParameters>) {
    this.parameters = {
      learningRate: 0.1,
      discountFactor: 0.95,
      explorationRate: 0.3,
      explorationDecay: 0.995,
      minExplorationRate: 0.05,
      memorySize: 10000,
      batchSize: 32,
      ...parameters
    };
  }

  /**
   * Record an action outcome for learning
   */
  recordOutcome(entityId: EntityId, decision: AIDecision, outcome: ActionOutcome): void {
    // Add to experience replay buffer
    this.experienceReplay.push(outcome);

    // Limit memory size
    if (this.experienceReplay.length > this.parameters.memorySize) {
      this.experienceReplay.shift();
    }

    // Update entity learning data
    const learningData = this.entityLearningData.get(entityId) || {
      totalReward: 0,
      actionCount: 0,
      lastUpdate: Date.now(),
      personalityAdjustments: Record<string, any>
    };

    const reward = this.calculateReward(outcome);
    learningData.totalReward += reward;
    learningData.actionCount++;
    learningData.lastUpdate = Date.now();

    this.entityLearningData.set(entityId, learningData);

    // Update Q-values
    this.updateQValue(outcome, reward);

    // Trigger batch learning periodically
    if (this.experienceReplay.length % this.parameters.batchSize === 0) {
      this.performBatchLearning();
    }
  }

  /**
   * Get the best action based on learned Q-values with exploration
   */
  getBestAction(entityId: EntityId, state: QValueState, availableActions: string[]): string {
    if (availableActions.length === 0) {
      return 'wait';
    }

    // Exploration vs exploitation
    if (Math.random() < this.parameters.explorationRate) {
      return availableActions[Math.floor(Math.random() * availableActions.length)]!;
    }

    // Find best action based on Q-values
    let bestAction = availableActions[0]!;
    let bestValue = -Infinity;

    for (const action of availableActions) {
      const qValue = this.getQValue(state, action);
      if (qValue > bestValue) {
        bestValue = qValue;
        bestAction = action;
      }
    }

    return bestAction;
  }

  /**
   * Get adaptive behavior adjustments based on learning
   */
  getAdaptiveBehavior(entityId: EntityId, baseBehavior: AIBehavior): AIBehavior {
    const learningData = this.entityLearningData.get(entityId);
    if (!learningData) {
      return baseBehavior;
    }

    // Apply learned personality adjustments
    const adaptedBehavior = { ...baseBehavior };

    // Adjust based on success rate
    const averageReward = learningData.totalReward / Math.max(learningData.actionCount, 1);

    if (averageReward < -0.5) {
      // Poor performance - become more defensive
      adaptedBehavior.selfPreservation = Math.min(1, adaptedBehavior.selfPreservation + 0.2);
      adaptedBehavior.aggressiveness = Math.max(0, adaptedBehavior.aggressiveness - 0.1);
    } else if (averageReward > 0.5) {
      // Good performance - become more confident
      adaptedBehavior.aggressiveness = Math.min(1, adaptedBehavior.aggressiveness + 0.1);
      adaptedBehavior.spellPreference = Math.min(1, adaptedBehavior.spellPreference + 0.05);
    }

    // Apply stored personality adjustments
    Object.assign(adaptedBehavior, learningData.personalityAdjustments);

    return adaptedBehavior;
  }

  /**
   * Perform batch learning from experience replay
   */
  private performBatchLearning(): void {
    if (this.experienceReplay.length < this.parameters.batchSize) {
      return;
    }

    // Sample random batch from experience
    const batch = this.sampleExperience(this.parameters.batchSize);

    for (const experience of batch) {
      const reward = this.calculateReward(experience);
      this.updateQValue(experience, reward);
    }

    // Decay exploration rate
    this.parameters.explorationRate = Math.max(
      this.parameters.minExplorationRate,
      this.parameters.explorationRate * this.parameters.explorationDecay
    );
  }

  /**
   * Sample random experiences from replay buffer
   */
  private sampleExperience(batchSize: number): ActionOutcome[] {
    const batch: ActionOutcome[] = [];
    const indices = new Set<number>();

    while (batch.length < batchSize && indices.size < this.experienceReplay.length) {
      const index = Math.floor(Math.random() * this.experienceReplay.length);
      if (!indices.has(index)) {
        indices.add(index);
        batch.push(this.experienceReplay[index]!);
      }
    }

    return batch;
  }

  /**
   * Calculate reward based on action outcome
   */
  private calculateReward(outcome: ActionOutcome): number {
    let reward = 0;

    // Reward for success
    if (outcome.success) {
      reward += 1.0;
    }

    // Reward for damage dealt
    reward += outcome.damageDealt * 0.1;

    // Penalty for damage taken
    reward -= outcome.damageTaken * 0.15;

    // Bonus for defeating enemies
    reward += outcome.enemiesDefeated * 2.0;

    // Penalty for dying (health goes to 0)
    if (outcome.healthPercentageAfter <= 0) {
      reward -= 5.0;
    }

    // Bonus for maintaining health
    const healthChange = outcome.healthPercentageAfter - outcome.healthPercentageBefore;
    if (healthChange > 0) {
      reward += healthChange * 0.5;
    }

    // Context-based rewards
    if (outcome.context.enemyCount > outcome.context.allyCount) {
      // Bonus for performing well when outnumbered
      reward *= 1.2;
    }

    return reward;
  }

  /**
   * Update Q-value for a state-action pair
   */
  private updateQValue(outcome: ActionOutcome, reward: number): void {
    const state = this.outcomeToState(outcome);
    const stateKey = this.stateToKey(state);
    const actionKey = `${stateKey}:${outcome.actionType}`;

    const existingQValue = this.qValues.get(actionKey);

    if (existingQValue) {
      // Update existing Q-value using Q-learning formula
      const oldValue = existingQValue.value;
      const newValue = oldValue + this.parameters.learningRate * (
        reward + this.parameters.discountFactor * this.getMaxQValue(state) - oldValue
      );

      existingQValue.value = newValue;
      existingQValue.sampleCount++;
      existingQValue.confidence = Math.min(1, existingQValue.sampleCount / 100);
    } else {
      // Create new Q-value
      this.qValues.set(actionKey, {
        state,
        action: outcome.actionType,
        value: reward,
        confidence: 0.1,
        sampleCount: 1
      });
    }
  }

  /**
   * Get Q-value for state-action pair
   */
  private getQValue(state: QValueState, action: string): number {
    const stateKey = this.stateToKey(state);
    const actionKey = `${stateKey}:${action}`;
    const qValue = this.qValues.get(actionKey);
    return qValue ? qValue.value : 0;
  }

  /**
   * Get maximum Q-value for a state across all actions
   */
  private getMaxQValue(state: QValueState): number {
    const stateKey = this.stateToKey(state);
    let maxValue = 0;

    for (const [key, qValue] of this.qValues) {
      if (key.startsWith(stateKey + ':')) {
        maxValue = Math.max(maxValue, qValue.value);
      }
    }

    return maxValue;
  }

  /**
   * Convert outcome to state representation
   */
  private outcomeToState(outcome: ActionOutcome): QValueState {
    return {
      healthPercentage: Math.floor(outcome.healthPercentageBefore * 4) / 4, // Quantize to quarters
      enemyCount: Math.min(outcome.context.enemyCount, 10), // Cap at 10
      allyCount: Math.min(outcome.context.allyCount, 10), // Cap at 10
      actionPoints: Math.min(3, Math.max(0, 1)), // Assume 1 action point for simplicity
      hasAdvantage: outcome.context.allyCount > outcome.context.enemyCount
    };
  }

  /**
   * Convert state to string key for storage
   */
  private stateToKey(state: QValueState): string {
    return `h${state.healthPercentage}_e${state.enemyCount}_a${state.allyCount}_ap${state.actionPoints}_adv${state.hasAdvantage ? 1 : 0}`;
  }

  /**
   * Get learning statistics
   */
  getStats() {
    const entities = Array.from(this.entityLearningData.entries());
    const totalEntities = entities.length;
    const avgReward = entities.reduce(_(sum, _[, _data]) => 
      sum + (data.totalReward / Math.max(data.actionCount, 1)), 0) / Math.max(totalEntities, 1);

    return {
      totalEntities,
      totalQValues: this.qValues.size,
      experienceBufferSize: this.experienceReplay.length,
      explorationRate: this.parameters.explorationRate,
      averageReward: avgReward,
      highConfidenceQValues: Array.from(this.qValues.values()).filter(q => q.confidence > 0.8).length
    };
  }

  /**
   * Save learning data (for persistence)
   */
  exportLearningData() {
    return {
      qValues: Array.from(this.qValues.entries()),
      entityData: Array.from(this.entityLearningData.entries()),
      parameters: this.parameters,
      timestamp: Date.now()
    };
  }

  /**
   * Load learning data (for persistence)
   */
  importLearningData(data: any): void {
    if (data.qValues) {
      this.qValues = new Map(data.qValues);
    }
    if (data.entityData) {
      this.entityLearningData = new Map(data.entityData);
    }
    if (data.parameters) {
      this.parameters = { ...this.parameters, ...data.parameters };
    }
  }

  /**
   * Reset learning data for an entity
   */
  resetEntityLearning(entityId: EntityId): void {
    this.entityLearningData.delete(entityId);
  }

  /**
   * Clear all learning data
   */
  clearAllLearning(): void {
    this.qValues.clear();
    this.entityLearningData.clear();
    this.experienceReplay = [];
    this.parameters.explorationRate = 0.3;
  }
}

import { World, EntityId } from '../World';
import { CombatStore, _CombatData} from '../components/Combat';
import { SpellcastingSystem, SpellEffect } from './SpellcastingSystem';

export interface CombatAction {
  actorId: EntityId;
  entityId: EntityId;
  type: 'attack' | 'spell' | 'movement' | 'dash' | 'dodge' | 'help' | 'hide' | 'ready' | 'search';
  targetId?: EntityId;
  targets?: EntityId[];
  data?: any;
  spellId?: string;
  casterLevel?: number;
  spellSlotLevel?: number;
  spellEffects?: SpellEffect[];
  requiresConcentration?: boolean;
  duration?: number;
}

export interface CombatEvent {
  type: 'combat_start' | 'combat_end' | 'turn_start' | 'turn_end' | 'initiative_rolled' | 'action_taken';
  entityId: EntityId;
  data?: any;
  timestamp: number;
}

export class CombatSystem {
  private world: World;
  private combat: CombatStore;
  private spellcastingSystem?: SpellcastingSystem;
  private isActive: boolean = false;
  private currentTurn: number = 0;
  private turnOrder: EntityId[] = [];
  private round: number = 1;
  private eventHandlers: Map<string, Function[]> = new Map();

  constructor(world: World, combat: CombatStore, spellcastingSystem?: SpellcastingSystem) {
    this.world = world;
    this.combat = combat;
    this.spellcastingSystem = spellcastingSystem;
  }

  startCombat(participants: EntityId[]): void {
    if (this.isActive) {
      this.endCombat();
    }

    this.isActive = true;
    this.round = 1;
    this.currentTurn = 0;

    // Roll initiative for all participants
    for (const entityId of participants) {
      const initiative = this.rollInitiative(entityId);
      this.combat.add(entityId, { initiative, isActive: true });
      this.emitEvent('initiative_rolled', entityId, { initiative });
    }

    // Set turn order
    this.turnOrder = this.combat.getInitiativeOrder();

    // Set turn order numbers
    this.turnOrder.forEach((_entityId, __index) => {
      this.combat.setTurnOrder(entityId, index);
    });

    if (this.turnOrder.length > 0) {
      const firstEntity = this.turnOrder[0];
      if (firstEntity !== undefined) {
        this.emitEvent('combat_start', firstEntity);
      }
    }
    this.startTurn();
  }

  endCombat(): void {
    if (!this.isActive) return;

    const allCombatants = this.combat.getAllInCombat();

    // Clean up combat state
    for (const entityId of allCombatants) {
      this.combat.remove(entityId);
    }

    this.isActive = false;
    this.currentTurn = 0;
    this.turnOrder = [];
    this.round = 1;

    this.emitEvent('combat_end', 0, { participants: allCombatants });
  }

  nextTurn(): void {
    if (!this.isActive) return;

    // End current turn
    if (this.turnOrder.length > 0) {
      const currentEntity = this.turnOrder[this.currentTurn];
      if (currentEntity !== undefined) {
        this.combat.endTurn(currentEntity);
      }
      if (currentEntity !== undefined) {
      this.emitEvent('turn_end', currentEntity, { round: this.round });
    }
    }

    // Advance to next turn
    this.currentTurn++;

    // Check if we've completed a round
    if (this.currentTurn >= this.turnOrder.length) {
      this.currentTurn = 0;
      this.round++;
    }

    this.startTurn();
  }

  private startTurn(): void {
    if (!this.isActive || this.turnOrder.length === 0) return;

    const currentEntity = this.turnOrder[this.currentTurn];
    if (currentEntity !== undefined) {
      this.combat.startTurn(currentEntity);
      this.emitEvent('turn_start', currentEntity, { 
      round: this.round,
      turnInRound: this.currentTurn + 1
      });
    }
  }

  getCurrentActor(): EntityId | null {
    if (!this.isActive || this.turnOrder.length === 0) return null;
    const actor = this.turnOrder[this.currentTurn];
    return actor !== undefined ? actor : null;
  }

  canTakeAction(entityId: EntityId, actionCost: number = 1): boolean {
    if (!this.isActive) return false;

    const currentActor = this.getCurrentActor();
    if (currentActor !== entityId) return false;

    const combatData = this.combat.get(entityId);
    return combatData ? combatData.actionPoints >= actionCost : false;
  }

  takeAction(action: CombatAction): boolean {
    if (!this.canTakeAction(action.actorId)) return false;

    const actionCost = this.getActionCost(action.type);
    if (!this.combat.useAction(action.actorId, actionCost)) return false;

    // Process the action
    this.processAction(action);

    this.emitEvent('action_taken', action.actorId, action);
    return true;
  }

  canTakeReaction(entityId: EntityId): boolean {
    if (!this.isActive) return false;

    const combatData = this.combat.get(entityId);
    return combatData ? !combatData.reactionUsed : false;
  }

  takeReaction(entityId: EntityId, reactionData: any): boolean {
    if (!this.canTakeReaction(entityId)) return false;

    this.combat.useReaction(entityId);
    this.emitEvent('action_taken', entityId, { 
      type: 'reaction', 
      data: reactionData 
    });
    return true;
  }

  private rollInitiative(entityId: EntityId): number {
    // Basic initiative roll: d20 + dex modifier
    const baseRoll = Math.floor(Math.random() * 20) + 1;
    const dexModifier = this.getDexterityModifier(entityId);
    const tiebreaker = Math.random(); // For consistent tie-breaking

    return baseRoll + dexModifier + tiebreaker;
  }

  private getDexterityModifier(_entityId: EntityId): number {
    // This would integrate with the Stats component
    // For now, return a random modifier between -2 and +5
    return Math.floor(Math.random() * 8) - 2;
  }

  private getActionCost(actionType: string): number {
    switch (actionType) {
      case 'attack':
      case 'spell':
      case 'dodge':
      case 'help':
      case 'hide':
      case 'ready':
      case 'search':
        return 1; // Full action
      case 'dash':
        return 1; // Full action
      case 'movement':
        return 0; // Movement is free on your turn
      default:
        return 1;
    }
  }

  private processAction(action: CombatAction): void {
    switch (action.type) {
      case 'movement':
        this.processMovement(action);
        break;
      case 'attack':
        this.processAttack(action);
        break;
      case 'spell':
        this.processSpell(action);
        break;
      case 'dash':
        this.processDash(action);
        break;
      case 'dodge':
        this.processDodge(action);
        break;
      // Add more action types as needed
    }
  }

  private processMovement(action: CombatAction): void {
    const combatData = this.combat.get(action.actorId);
    if (combatData) {
      combatData.hasMovedThisTurn = true;
    }
  }

  private processAttack(action: CombatAction): void {
    if (!action.targetId) return;

    const attackerStats = this.world.getComponent('stats', action.entityId);
    const targetHealth = this.world.getComponent('health', action.targetId);

    if (!attackerStats || !targetHealth) return;

    // Simple attack resolution
    const attackRoll = Math.floor(Math.random() * 20) + 1;
    const attackModifier = attackerStats.abilityModifiers?.strength || 0;
    const proficiencyBonus = attackerStats.proficiencyBonus || 2;

    const totalAttack = attackRoll + attackModifier + proficiencyBonus;
    const targetAC = this.world.getComponent('stats', action.targetId)?.armorClass || 10;

    if (totalAttack >= targetAC) {
      // Hit - roll damage
      const damage = Math.floor(Math.random() * 8) + 1 + attackModifier; // 1d8 + modifier
      this.world.getStore('health').takeDamage(action.targetId, damage);

      // Check for concentration break
      if (this.spellcastingSystem && this.combatStore.get(action.targetId)?.concentrating) {
        this.spellcastingSystem.concentrationCheck(action.targetId, damage);
      }

      this.emitEvent('attack_hit', action.entityId, { 
        targetId: action.targetId, 
        damage,
        attackRoll,
        totalAttack
      });
    } else {
      this.emitEvent('attack_miss', action.entityId, { 
        targetId: action.targetId,
        attackRoll,
        totalAttack,
        targetAC
      });
    }
  }

  private processSpell(action: CombatAction): void {
    if (!this.spellcastingSystem) {
      console.warn('SpellcastingSystem not available');
      return;
    }

    // Extract spell data from action
    const spellInstance = {
      spellId: action.spellId || '',
      casterLevel: action.casterLevel || 1,
      spellSlotLevel: action.spellSlotLevel || 1,
      casterId: action.entityId,
      targets: action.targets || [],
      effects: action.spellEffects || [],
      concentration: action.requiresConcentration || false,
      duration: action.duration
    };

    const success = this.spellcastingSystem.castSpell(spellInstance);

    this.emit('spell_cast', {
      entityId: action.entityId,
      spellId: spellInstance.spellId,
      targets: spellInstance.targets,
      success
    });
  }

  private processDash(_action: CombatAction): void {
    // Double movement speed for the turn
  }

  private processDodge(_action: CombatAction): void {
    // Add dodge bonus until start of next turn
  }

  // Event system
  on(_eventType: string, _handler: (...args: any[]) => any): void {
    if (!this.eventHandlers.has(eventType)) {
      this.eventHandlers.set(eventType, []);
    }
    this.eventHandlers.get(eventType)!.push(handler);
  }

  off(_eventType: string, _handler: (...args: any[]) => any): void {
    const handlers = this.eventHandlers.get(eventType);
    if (handlers) {
      const index = handlers.indexOf(handler);
      if (index > -1) {
        handlers.splice(index, 1);
      }
    }
  }

  private emitEvent(type: string, entityId: EntityId, data?: any): void {
    const event: CombatEvent = {
      type: type as any,
      entityId,
      data,
      timestamp: Date.now()
    };

    const handlers = this.eventHandlers.get(type);
    if (handlers) {
      handlers.forEach(handler => {
        try {
          handler(event);
        } catch (error) {
          console.error('Combat event handler error:', error);
        }
      });
    }
  }

  // Public accessors
  isInCombat(): boolean {
    return this.isActive;
  }

  getCurrentRound(): number {
    return this.round;
  }

  getTurnOrder(): EntityId[] {
    return [...this.turnOrder];
  }

  getCombatants(): EntityId[] {
    return this.combat.getAllInCombat();
  }

  update(_deltaTime: number): void {
    if (!this.isActive) return;

    // Update combat-related logic
    // Handle ongoing effects, concentration checks, etc.
  }
}

/**
 * Condition system for managing status effects and their interactions
 */
export class ConditionSystem {
    constructor(conditionsStore, statsStore) {
        this.eventHandlers = new Map();
        this.conditionsStore = conditionsStore;
        this.statsStore = statsStore;
    }
    update(deltaTime) {
        // Process condition durations at end of each turn
        this.processConditionDurations();
        // Handle saving throws for conditions
        this.processSavingThrows();
    }
    processConditionDurations() {
        this.conditionsStore.updateDurations();
    }
    processSavingThrows() {
        this.conditionsStore.forEach((entity, conditions) => {
            for (const condition of conditions) {
                if (condition.saveEndOfTurn) {
                    const { ability, dc } = condition.saveEndOfTurn;
                    const modifier = this.statsStore.getSavingThrowModifier(entity, ability, false);
                    const roll = this.rollD20() + modifier;
                    if (roll >= dc) {
                        this.conditionsStore.remove(entity, condition.type);
                        this.emit('conditionSaved', {
                            entity,
                            condition: condition.type,
                            roll,
                            dc,
                        });
                    }
                }
            }
        });
    }
    // Apply condition effects to rolls and calculations
    getAttackModifiers(entity) {
        const conditions = this.conditionsStore.get(entity);
        let advantage = false;
        let disadvantage = false;
        let modifier = 0;
        for (const condition of conditions) {
            switch (condition.type) {
                case 'blessed':
                    advantage = true;
                    break;
                case 'blinded':
                case 'frightened':
                case 'poisoned':
                case 'prone':
                    disadvantage = true;
                    break;
                case 'invisible':
                    advantage = true;
                    break;
                case 'paralyzed':
                case 'stunned':
                case 'unconscious':
                    // These prevent attacks entirely
                    return { advantage: false, disadvantage: true, modifier: -Infinity };
            }
        }
        return { advantage, disadvantage, modifier };
    }
    getSavingThrowModifiers(entity, ability) {
        const conditions = this.conditionsStore.get(entity);
        let advantage = false;
        let disadvantage = false;
        let modifier = 0;
        for (const condition of conditions) {
            switch (condition.type) {
                case 'blessed':
                    if (['wisdom', 'charisma'].includes(ability)) {
                        advantage = true;
                    }
                    break;
                case 'frightened':
                    if (['wisdom'].includes(ability)) {
                        disadvantage = true;
                    }
                    break;
                case 'poisoned':
