/**
 * Condition system for managing status effects and their interactions
 */

import { ConditionsStore } from '../components/Conditions';
import { StatsStore } from '../components/Stats';

export interface ConditionEffect {
  entity: number;
  conditionType: string;
  effect: 'advantage' | 'disadvantage' | 'modifier' | 'immunity' | 'resistance';
  target: 'attack' | 'save' | 'skill' | 'damage' | 'speed';
  value?: number;
}

export class ConditionSystem {
  private conditionsStore: ConditionsStore;
  private statsStore: StatsStore;
  private eventHandlers: Map<string, Function[]> = new Map();

  constructor(conditionsStore: ConditionsStore, statsStore: StatsStore) {
    this.conditionsStore = conditionsStore;
    this.statsStore = statsStore;
  }

  update(_deltaTime: number): void {
    // Process condition durations at end of each turn
    this.processConditionDurations();

    // Handle saving throws for conditions
    this.processSavingThrows();
  }

  private processConditionDurations(): void {
    this.conditionsStore.updateDurations();
  }

  private processSavingThrows(): void {
    this.conditionsStore.forEach((_entity, __conditions) => {
      for (const condition of conditions) {
        if (condition.saveEndOfTurn) {
          const { ability,  dc  } = condition.saveEndOfTurn;
          const modifier = this.statsStore.getSavingThrowModifier(entity, ability, false);
          const roll = this.rollD20() + modifier;

          if (roll >= dc) {
            this.conditionsStore.remove(entity, condition.type);
            this.emit('conditionSaved', {
              entity,
              condition: condition.type,
              roll,
              dc,
            });
          }
        }
      }
    });
  }

  // Apply condition effects to rolls and calculations
  getAttackModifiers(entity: number): { advantage: boolean; disadvantage: boolean; modifier: number } {
    const conditions = this.conditionsStore.get(entity);
    let advantage = false;
    let disadvantage = false;
    const modifier = 0;

    for (const condition of conditions) {
      switch (condition.type) {
        case 'blessed':
          advantage = true;
          break;
        case 'blinded':
        case 'frightened':
        case 'poisoned':
        case 'prone':
          disadvantage = true;
          break;
        case 'invisible':
          advantage = true;
          break;
        case 'paralyzed':
        case 'stunned':
        case 'unconscious':
          // These prevent attacks entirely
          return { advantage: false, disadvantage: true, modifier: -Infinity };
      }
    }

    return { advantage, disadvantage, modifier };
  }

  getSavingThrowModifiers(entity: number, ability: string): { advantage: boolean; disadvantage: boolean; modifier: number } {
    const conditions = this.conditionsStore.get(entity);
    let advantage = false;
    let disadvantage = false;
    const modifier = 0;

    for (const condition of conditions) {
      switch (condition.type) {
        case 'blessed':
          if (['wisdom', 'charisma'].includes(ability)) {
            advantage = true;
          }
          break;
        case 'frightened':
          if (['wisdom'].includes(ability)) {
            disadvantage = true;
          }
          break;
        case 'poisoned':
          if (['constitution'].includes(ability)) {
            disadvantage = true;
          }
          break;
        case 'paralyzed':
        case 'stunned':
        case 'unconscious':
          if (['strength', 'dexterity'].includes(ability)) {
            return { advantage: false, disadvantage: true, modifier: -Infinity };
          }
          break;
      }
    }

    return { advantage, disadvantage, modifier };
  }

  getSpeedModifier(entity: number): number {
    const conditions = this.conditionsStore.get(entity);
    let speedMultiplier = 1;

    for (const condition of conditions) {
      switch (condition.type) {
        case 'hasted':
          speedMultiplier *= 2;
          break;
        case 'slowed':
          speedMultiplier *= 0.5;
          break;
        case 'grappled':
        case 'restrained':
          speedMultiplier = 0;
          break;
        case 'prone':
          speedMultiplier *= 0.5;
          break;
        case 'exhaustion': {
          const exhaustionLevel = this.conditionsStore.getExhaustionLevel(entity);
          if (exhaustionLevel >= 2) speedMultiplier *= 0.5;
          if (exhaustionLevel >= 5) speedMultiplier = 0;
    }
          break;
      }
    }

    return speedMultiplier;
  }

  getDamageResistances(entity: number): string[] {
    const conditions = this.conditionsStore.get(entity);
    const resistances: string[] = [];

    for (const condition of conditions) {
      // Add condition-based resistances
      if (condition.type === 'blessed') {
        resistances.push('necrotic');
      }
    }

    return resistances;
  }

  getDamageImmunities(entity: number): string[] {
    const conditions = this.conditionsStore.get(entity);
    const immunities: string[] = [];

    for (const condition of conditions) {
      // Add condition-based immunities
      if (condition.type === 'petrified') {
        immunities.push('poison', 'disease');
      }
    }

    return immunities;
  }

  // Check if entity can perform specific actions
  canTakeActions(entity: number): boolean {
    return this.conditionsStore.canAct(entity);
  }

  canMove(entity: number): boolean {
    return this.conditionsStore.canMove(entity);
  }

  canSee(entity: number): boolean {
    return this.conditionsStore.canSee(entity);
  }

  canHear(entity: number): boolean {
    return this.conditionsStore.canHear(entity);
  }

  canConcentrate(entity: number): boolean {
    return !this.conditionsStore.isIncapacitated(entity) &&
           !this.conditionsStore.has(entity, 'unconscious');
  }

  // Concentration checks
  makeConcentrationCheck(entity: number, damage: number): boolean {
    if (!this.conditionsStore.has(entity, 'concentration')) {
      return true; // Not concentrating
    }

    const dc = Math.max(10, Math.floor(damage / 2));
    const modifier = this.statsStore.getSavingThrowModifier(entity, 'constitution', false);
    const roll = this.rollD20() + modifier;

    const success = roll >= dc;
    if (!success) {
      this.conditionsStore.remove(entity, 'concentration');
      this.emit('concentrationBroken', { entity, roll, dc, damage });
    }

    return success;
  }

  // Utility methods
  private rollD20(): number {
    return Math.floor(Math.random() * 20) + 1;
  }

  // Event system
  on(_event: string, _handler: (...args: any[]) => any): void {
    if (!this.eventHandlers.has(event)) {
      this.eventHandlers.set(event, []);
    }
    this.eventHandlers.get(event)!.push(handler);
  }

  off(_event: string, _handler: (...args: any[]) => any): void {
    const handlers = this.eventHandlers.get(event);
    if (handlers) {
      const index = handlers.indexOf(handler);
      if (index > -1) {
        handlers.splice(index, 1);
      }
    }
  }

  private emit(event: string, data?: any): void {
    const handlers = this.eventHandlers.get(event);
    if (handlers) {
      handlers.forEach(handler => handler(data));
    }
  }
}

/**
 * Health system for managing damage, healing, and death
 */

import { HealthStore } from '../components/Health';
import { ConditionsStore } from '../components/Conditions';

export interface DamageEvent {
  entity: number;
  damage: number;
  damageType: string;
  source?: number;
}

export interface HealingEvent {
  entity: number;
  healing: number;
  source?: number;
}

export class HealthSystem {
  private healthStore: HealthStore;
  private conditionsStore: ConditionsStore;
  private eventHandlers: Map<string, Function[]> = new Map();

  constructor(healthStore: HealthStore, conditionsStore: ConditionsStore) {
    this.healthStore = healthStore;
    this.conditionsStore = conditionsStore;
  }

  update(deltaTime: number): void {
    // Process regeneration and damage over time
    this.healthStore.forEach((_entity, __health) => {
      this.processRegeneration(entity, deltaTime);
      this.processDamageOverTime(entity, deltaTime);
    });
  }

  applyDamage(event: DamageEvent): void {
    const { entity,  damage,  damageType,  source  } = event;

    if (!this.healthStore.has(entity)) return;

    const health = this.healthStore.get(entity);
    if (!health) return;

    const originalHP = health.current;
    const success = this.healthStore.takeDamage(entity, damage);

    if (success) {
      const newHealth = this.healthStore.get(entity);
      const actualDamage = originalHP - (newHealth?.current || 0);

      this.emit('damageApplied', {
        entity,
        damage: actualDamage,
        damageType,
        source,
        newHealth: newHealth?.current || 0,
      });

      // Check for death or unconsciousness
      if (this.healthStore.isDead(entity)) {
        this.handleDeath(entity);
      }
    }
  }

  applyHealing(event: HealingEvent): void {
    const { entity,  healing,  source  } = event;

    if (!this.healthStore.has(entity)) return;

    const health = this.healthStore.get(entity);
    if (!health) return;

    const originalHP = health.current;
    const success = this.healthStore.heal(entity, healing);

    if (success) {
      const newHealth = this.healthStore.get(entity);
      const actualHealing = (newHealth?.current || 0) - originalHP;

      this.emit('healingApplied', {
        entity,
        healing: actualHealing,
        source,
        newHealth: newHealth?.current || 0,
      });

      // Remove unconscious condition if healed above 0
      if (originalHP <= 0 && (newHealth?.current || 0) > 0) {
        this.conditionsStore.remove(entity, 'unconscious');
        this.emit('entityRevived', { entity });
      }
    }
  }

  setTemporaryHitPoints(entity: number, tempHP: number): void {
    if (!this.healthStore.has(entity)) return;

    const health = this.healthStore.get(entity);
    if (!health) return;

    // Temporary hit points don't stack, take the higher value
    const newTempHP = Math.max(health.temporary, tempHP);
    this.healthStore.set(entity, { temporary: newTempHP });

    this.emit('temporaryHitPointsChanged', {
      entity,
      tempHP: newTempHP,
    });
  }

  private handleDeath(entity: number): void {
    // Add unconscious condition
    this.conditionsStore.add(entity, {
      type: 'unconscious',
      duration: -1, // Permanent until healed
      source: 'death',
    });

    this.emit('entityDied', { entity });
  }

  private processRegeneration(entity: number, deltaTime: number): void {
    // Check for regeneration conditions or abilities
    const conditions = this.conditionsStore.get(entity);
    const regenCondition = conditions.find(c => c.type === 'blessed'); // Example

    if (regenCondition) {
      // Apply small amount of healing over time
      const regenAmount = Math.floor(deltaTime * 0.1); // 0.1 HP per second
      if (regenAmount > 0) {
        this.applyHealing({ entity, healing: regenAmount });
      }
    }
  }

  private processDamageOverTime(entity: number, deltaTime: number): void {
    const conditions = this.conditionsStore.get(entity);

    for (const condition of conditions) {
      if (condition.type === 'poisoned') {
        // Apply poison damage over time
        const poisonDamage = Math.floor(deltaTime * 0.2); // 0.2 damage per second
        if (poisonDamage > 0) {
          this.applyDamage({
            entity,
            damage: poisonDamage,
            damageType: 'poison',
          });
        }
      }
    }
  }

  // Utility methods
  isAlive(entity: number): boolean {
    return this.healthStore.has(entity) && !this.healthStore.isDead(entity);
  }

  isUnconscious(entity: number): boolean {
    return this.conditionsStore.has(entity, 'unconscious');
  }

  getHealthPercentage(entity: number): number {
    return this.healthStore.getHealthPercentage(entity);
  }

  getHealthStatus(entity: number): 'healthy' | 'injured' | 'bloodied' | 'critical' | 'unconscious' | 'dead' {
    if (!this.healthStore.has(entity)) return 'dead';

    if (this.isUnconscious(entity)) return 'unconscious';
    if (this.healthStore.isDead(entity)) return 'dead';

    const percentage = this.getHealthPercentage(entity);
    if (percentage >= 0.75) return 'healthy';
    if (percentage >= 0.5) return 'injured';
    if (percentage >= 0.25) return 'bloodied';
    return 'critical';
  }

  // Event system
  on(_event: string, _handler: (...args: any[]) => any): void {
    if (!this.eventHandlers.has(event)) {
      this.eventHandlers.set(event, []);
    }
    this.eventHandlers.get(event)!.push(handler);
  }

  off(_event: string, _handler: (...args: any[]) => any): void {
    const handlers = this.eventHandlers.get(event);
    if (handlers) {
      const index = handlers.indexOf(handler);
      if (index > -1) {
        handlers.splice(index, 1);
      }
    }
  }

  private emit(event: string, data?: any): void {
    const handlers = this.eventHandlers.get(event);
    if (handlers) {
      handlers.forEach(handler => handler(data));
    }
  }
}

import { describe, test, expect, beforeEach } from 'vitest';
import { CombatStore } from '../components/Combat';
import { CombatSystem } from '../systems/CombatSystem';
import { World } from '../World';

describe('CombatSystem Performance Benchmarks', () => {
  let combatStore: CombatStore;
  let combatSystem: CombatSystem;
  let world: World;

  beforeEach(() => {
    combatStore = new CombatStore(1000);
    world = new World();
    combatSystem = new CombatSystem(world, combatStore);
  });

  test('CombatStore object pooling performance', () => {
    const iterations = 1000;
    const startTime = performance.now();

    // Add and remove entities rapidly to test pooling
    for (let i = 0; i < iterations; i++) {
      combatStore.add(i, { initiative: Math.random() * 20 });
    }

    for (let i = 0; i < iterations; i++) {
      combatStore.remove(i);
    }

    // Add them again to test pool reuse
    for (let i = 0; i < iterations; i++) {
      combatStore.add(i + iterations, { initiative: Math.random() * 20 });
    }

    const endTime = performance.now();
    const duration = endTime - startTime;

    console.log(`Pool performance: ${iterations * 2} operations in ${duration.toFixed(2)}ms`);

    // Should complete within reasonable time (adjust threshold as needed)
    expect(duration).toBeLessThan(100);

    // Verify pool utilization
    const stats = combatStore.getPerformanceStats();
    expect(stats.poolUtilization).toBeGreaterThan(0);
    expect(stats.totalEntities).toBe(iterations);
  });

  test('Initiative order caching performance', () => {
    const entityCount = 100;
    const iterations = 1000;

    // Add entities with random initiative
    for (let i = 0; i < entityCount; i++) {
      combatStore.add(i, { initiative: Math.random() * 20 });
    }

    const startTime = performance.now();

    // Call getInitiativeOrder multiple times to test caching
    for (let i = 0; i < iterations; i++) {
      const order = combatStore.getInitiativeOrder();
      expect(order).toHaveLength(entityCount);
    }

    const endTime = performance.now();
    const duration = endTime - startTime;

    console.log(`Initiative caching: ${iterations} calls with ${entityCount} entities in ${duration.toFixed(2)}ms`);

    // Cached calls should be very fast
    expect(duration).toBeLessThan(50);

    // Verify cache is working
    const stats = combatStore.getPerformanceStats();
    expect(stats.cacheValid).toBe(true);
  });

  test('Large scale combat simulation', () => {
    const entityCount = 500;
    const participants: number[] = [];

    // Create a large number of entities
    for (let i = 0; i < entityCount; i++) {
      participants.push(i);
    }

    const startTime = performance.now();

    // Start combat with many participants
    combatSystem.startCombat(participants);

    // Simulate several rounds of combat
    for (let round = 0; round < 10; round++) {
      for (let turn = 0; turn < entityCount; turn++) {
        const currentActor = combatSystem.getCurrentActor();
        if (currentActor !== null) {
          // Simulate a basic action
          const success = combatSystem.takeAction({
            actorId: currentActor,
            entityId: currentActor,
            type: 'attack',
            targetId: (currentActor + 1) % entityCount
          });

          if (success || Math.random() > 0.5) {
            combatSystem.nextTurn();
          }
        } else {
          break;
        }
      }
    }

    const endTime = performance.now();
    const duration = endTime - startTime;

    console.log(`Large combat simulation: ${entityCount} entities, 10 rounds in ${duration.toFixed(2)}ms`);

    // Should handle large combat efficiently
    expect(duration).toBeLessThan(1000); // 1 second max
    expect(combatSystem.isInCombat()).toBe(true);
  });

  test('Memory usage stability test', () => {
    const iterations = 100;
    const entitiesPerIteration = 50;

    for (let i = 0; i < iterations; i++) {
      // Add entities
      const entities: number[] = [];
      for (let j = 0; j < entitiesPerIteration; j++) {
        const entityId = i * entitiesPerIteration + j;
        combatStore.add(entityId, { 
          initiative: Math.random() * 20,
          actionPoints: Math.floor(Math.random() * 3) + 1
        });
        entities.push(entityId);
      }

      // Perform operations
      combatStore.getInitiativeOrder();

      // Remove entities
      entities.forEach(id => combatStore.remove(id));
    }

    const stats = combatStore.getPerformanceStats();

    // Pool should be efficiently reused
    expect(stats.poolUtilization).toBeLessThan(1.0); // Not exhausted
    expect(stats.recycledEntities).toBeGreaterThan(0); // Some recycling occurred

    console.log('Memory stability stats:', stats);
  });

  test('Concurrent operations performance', async () => {
    const entityCount = 200;
    const operationsPerBatch = 10;

    // Add initial entities
    for (let i = 0; i < entityCount; i++) {
      combatStore.add(i, { initiative: Math.random() * 20 });
    }

    const startTime = performance.now();

    // Simulate concurrent operations
    const promises = [];
    for (let batch = 0; batch < 50; batch++) {
      promises.push(
        _new Promise<void>((_resolve) => {
          setTimeout(() => {
            for (let op = 0; op < operationsPerBatch; op++) {
              const entityId = Math.floor(Math.random() * entityCount);
              const combat = combatStore.get(entityId);
              if (combat) {
                combatStore.setInitiative(entityId, Math.random() * 20);
                combatStore.getInitiativeOrder();
              }
            }
            resolve();
          }, Math.random() * 10);
        })
      );
    }

    await Promise.all(promises);

    const endTime = performance.now();
    const duration = endTime - startTime;

    console.log(`Concurrent operations: 50 batches in ${duration.toFixed(2)}ms`);

    // Should handle concurrent operations reasonably well
    expect(duration).toBeLessThan(500);
  });

  test('Performance regression detection', () => {
    const baselineMs = 50; // Acceptable baseline for 1000 operations
    const operations = 1000;

    const startTime = performance.now();

    for (let i = 0; i < operations; i++) {
      combatStore.add(i, { 
        initiative: Math.random() * 20,
        actionPoints: Math.floor(Math.random() * 3) + 1,
        concentrating: Math.random() > 0.8
      });

      if (i % 10 === 0) {
        combatStore.getInitiativeOrder();
      }

      if (i % 5 === 0) {
        combatStore.setInitiative(i, Math.random() * 20);
      }
    }

    const endTime = performance.now();
    const duration = endTime - startTime;

    console.log(`Regression test: ${operations} mixed operations in ${duration.toFixed(2)}ms`);

    // Alert if performance degrades significantly
    if (duration > baselineMs * 2) {
      console.warn(`Performance regression detected: ${duration}ms > ${baselineMs * 2}ms baseline`);
    }

    expect(duration).toBeLessThan(baselineMs * 3); // Hard limit
  });
});

// Helper function for detailed performance profiling
export function profileCombatOperations(combatStore: CombatStore, _operations: number = 1000) {
  const results = {
    addEntity: 0,
    removeEntity: 0,
    getInitiative: 0,
    setInitiative: 0,
    getOrder: 0
  };

  // Profile add operations
  let start = performance.now();
  for (let i = 0; i < operations; i++) {
    combatStore.add(i, { initiative: Math.random() * 20 });
  }
  results.addEntity = performance.now() - start;

  // Profile initiative order operations
  start = performance.now();
  for (let i = 0; i < 100; i++) {
    combatStore.getInitiativeOrder();
  }
  results.getOrder = performance.now() - start;

  // Profile set initiative operations
  start = performance.now();
  for (let i = 0; i < operations / 2; i++) {
    combatStore.setInitiative(i, Math.random() * 20);
  }
  results.setInitiative = performance.now() - start;

  // Profile remove operations
  start = performance.now();
  for (let i = 0; i < operations; i++) {
    combatStore.remove(i);
  }
  results.removeEntity = performance.now() - start;

  return results;
}

