export class AIProviderRegistry extends EventEmitter<SystemEvents> implements Disposable {
  private providers = new Map<string, AIProvider>();
  private configs = new Map<string, AIProviderConfig>();
  private usageStats = new Map<string, AIUsageStats>();
  private requestHistory: AIRequest[] = [];
  private responseHistory: AIResponse[] = [];

  constructor() {
    super();
    this.initializeDefaultProviders();
  }

  /**
   * Register an AI provider
   */
  registerProvider(provider: AIProvider, config: AIProviderConfig): void {
    this.providers.set(provider.name, provider);
    this.configs.set(provider.name, config);

    // Reset stats on (re)registration to ensure clean slate per test/use
    this.usageStats.set(provider.name, {
      totalRequests: 0,
      successfulRequests: 0,
      failedRequests: 0,
      totalTokensUsed: 0,
      averageResponseTime: 0,
      costEstimate: 0
    });
  }

  /**
   * Get the best provider for a specific capability
   */
  getBestProvider(capability: AICapability): AIProvider | null {
    const availableProviders = Array.from(this.providers.entries())
      .filter(_([name, _provider]) => {
        const config = this.configs.get(name);
        return config?.enabled && provider.capabilities.includes(capability);
      })
      .sort(_([nameA], _[nameB]) => {
        const configA = this.configs.get(nameA);
        const configB = this.configs.get(nameB);
        if (!configA || !configB) return 0;
        return configB.priority - configA.priority;
      });

    return availableProviders.length > 0 ? availableProviders[0]![1] : null;
  }

  /**
   * Get all enabled providers supporting a capability, sorted by priority (desc)
   */
  getProvidersForCapability(capability: AICapability): Array<{ name: string; provider: AIProvider; config: AIProviderConfig }> {
    const candidates = Array.from(this.providers.entries())
      .map([name, _provider] => ({ name, provider, config: this.configs.get(name) }))
      .filter((p): p is { name: string; provider: AIProvider; config: AIProviderConfig } => !!p.config && p.config.enabled && p.provider.capabilities.includes(capability))
      .sort((_a, _b) => b.config.priority - a.config.priority);

    return candidates;
  }

  /**
   * Generate text using the best available provider
   */
  async generateText(prompt: string, options: AITextOptions = {}): Promise<AITextResponse> {
    const candidates = this.getProvidersForCapability('text_generation');
    if (candidates.length === 0) {
      throw new Error('No text generation provider available');
    }

    return this.executeWithFailover(candidates, 'text', prompt, options);
  }

  /**
   * Generate code using the best available provider
   */
  async generateCode(prompt: string, options: AICodeOptions = {}): Promise<AICodeResponse> {
    const candidates = this.getProvidersForCapability('code_generation');
    if (!candidates.length) {
      throw new Error('No code generation provider available');
    }

    return this.executeWithFailover(candidates, 'code', prompt, options);
  }

  /**
   * Create embeddings using the best available provider
   */
  async createEmbeddings(text: string): Promise<number[]> {
    const provider = this.getBestProvider('embeddings');
    if (!provider) {
      throw new Error('No embeddings provider available');
    }

    if (!provider.createEmbeddings) {
      throw new Error('Provider does not support embeddings');
    }

    return provider.createEmbeddings(text);
  }

  /**
   * Smart content generation with context awareness for VTT
   */
  async generateVTTContent(type: 'character' | 'item' | 'spell' | 'encounter' | 'map_description', context: any): Promise<string> {
    const prompts = {
      character: this.buildCharacterPrompt(context),
      item: this.buildItemPrompt(context),
      spell: this.buildSpellPrompt(context),
      encounter: this.buildEncounterPrompt(context),
      map_description: this.buildMapPrompt(context)
    };

    const prompt = prompts[type];
    const response = await this.generateText(prompt, {
      maxTokens: 1000,
      temperature: 0.8
    });

    return response.text;
  }

  /**
   * Generate rule-based content
   */
  async generateRuleContent(ruleSystem: string, contentType: string, parameters: Record<string, any>): Promise<any> {
    const prompt = `Generate ${contentType} for ${ruleSystem} with the following parameters:
${JSON.stringify(parameters, null, 2)}

Please provide detailed, mechanically accurate content that follows the rules and conventions of ${ruleSystem}.`;

    const response = await this.generateText(prompt, {
      maxTokens: 1500,
      temperature: 0.6
    });

    try {
      // Try to parse as JSON first, fallback to text
      return JSON.parse(response.text);
    } catch {
      return response.text;
    }
  }

  /**
   * Get provider usage statistics
   */
  getUsageStats(providerId?: string): Map<string, AIUsageStats> {
    if (providerId) {
      const stats = this.usageStats.get(providerId);
      return stats ? new Map([[providerId, stats]]) : new Map();
    }
    return new Map(this.usageStats);
  }

  /**
   * Get available providers
   */
  getAvailableProviders(): Array<{ name: string; type: AIProvider['type']; capabilities: AICapability[]; enabled: boolean; priority: number }> {
    return Array.from(this.providers.entries()).map([name, _provider] => ({
      name,
      type: provider.type,
      capabilities: provider.capabilities,
      enabled: this.configs.get(name)?.enabled || false,
      priority: this.configs.get(name)?.priority || 0
    }));
  }

  /**
   * Enable/disable a provider
   */
  toggleProvider(providerId: string, enabled: boolean): void {
    const config = this.configs.get(providerId);
    if (config) {
      config.enabled = enabled;
    }
  }

  /**
   * Dispose of the registry
   */
  dispose(): void {
    this.providers.clear();
    this.configs.clear();
    this.usageStats.clear();
    this.requestHistory = [];
    this.responseHistory = [];
    this.removeAllListeners();
  }

  // Private helper methods

  private async executeRequest(provider: AIProvider, type: string, prompt: string, options: any): Promise<any> {
    const requestId = this.generateRequestId();
    const request: AIRequest = {
      id: requestId,
      type: type as any,
      prompt,
      options,
      timestamp: new Date(),
      providerId: provider.name
    };

    this.requestHistory.push(request);

    const startTime = performance.now();
    let response: AIResponse;

    try {
      let result: any;

      switch (type) {
        case 'text':
          result = await provider.generateText!(prompt, options);
          break;
        case 'code':
          result = await provider.generateCode!(prompt, options);
          break;
        default:
          throw new Error(`Unsupported request type: ${type}`);
      }

      const processingTime = performance.now() - startTime;

      response = {
        requestId,
        success: true,
        data: result,
        usage: (result as any).usage,
        processingTime,
        providerId: provider.name
      };

      // Update stats
      this.updateUsageStats(provider.name, true, processingTime, (result as any).usage?.totalTokens || 0);

    } catch (error) {
      const processingTime = performance.now() - startTime;

      response = {
        requestId,
        success: false,
        error: error instanceof Error ? error.message : String(error),
        processingTime,
        providerId: provider.name
      };

      // Update stats
      this.updateUsageStats(provider.name, false, processingTime, 0);

      throw error;
    }

    this.responseHistory.push(response);
    return response.data;
  }

  /**
   * Execute a request with retries and provider failover
   */
  private async executeWithFailover(
    candidates: Array<{ name: string; provider: AIProvider; config: AIProviderConfig }>,
    type: 'text' | 'code',
    prompt: string,
    options: any
  ): Promise<any> {
    let lastError: unknown = null;

    for (const { _name,  provider,  config  } of candidates) {
      const attempts = Math.max(1, config.maxRetries || 1);

      for (let attempt = 1; attempt <= attempts; attempt++) {
        try {
          const timeoutMs = Math.max(1, config.timeout || 30000);
          // Reuse executeRequest for consistent stats and history tracking
          const result = await this.withTimeout(
            this.executeRequest(provider, type, prompt, options),
            timeoutMs
          );
          return result;
        } catch (err) {
          lastError = err;
          // If there are more attempts for this provider, continue retrying
          if (attempt < attempts) {
            continue;
          }
          // Otherwise, break to try next provider
          break;
        }
      }
    }

    throw new Error(
      `All providers failed${lastError instanceof Error ? `: ${lastError.message}` : ''}`
    );
  }

  private withTimeout<T>(promise: Promise<T>, ms: number): Promise<T> {
    return new Promise<T>((_resolve, __reject) => {
      const timer = setTimeout(() => reject(new Error('Request timed out')), ms);
      promise
        .then((_val) => {
          clearTimeout(timer);
          resolve(val);
        })
        .catch((_err) => {
          clearTimeout(timer);
          reject(err);
        });
    });
  }

  private updateUsageStats(providerId: string, success: boolean, processingTime: number, tokensUsed: number): void {
    const stats = this.usageStats.get(providerId);
    if (!stats) return;

    stats.totalRequests++;
    if (success) {
      stats.successfulRequests++;
    } else {
      stats.failedRequests++;
    }

    stats.totalTokensUsed += tokensUsed;
    stats.averageResponseTime = (stats.averageResponseTime * (stats.totalRequests - 1) + processingTime) / stats.totalRequests;
    stats.costEstimate += this.estimateCost(providerId, tokensUsed);
  }

  private estimateCost(providerId: string, tokensUsed: number): number {
    // Simplified cost estimation per 1K tokens
    const costs: Record<string, number> = {
      'openai': 0.002, // GPT-4 approximate
      'openrouter': 0.001, // Variable, using conservative estimate
      'anthropic': 0.003, // Claude Sonnet approximate
      'gemini': 0.0005, // Gemini Pro approximate
      'replicate': 0.0005, // Open source models, generally cheaper
      'huggingface': 0.0002, // Inference API pricing
      'local': 0
    };

    const costPer1K = costs[providerId] || 0.001; // Default fallback
    return (tokensUsed / 1000) * costPer1K;
  }

  private buildCharacterPrompt(context: any): string {
    return `Generate a detailed D&D 5e character description including:
- Name and basic appearance
- Race and class
- Background and personality traits
- Brief backstory
- Notable equipment

Context: ${JSON.stringify(context)}`;
  }

  private buildItemPrompt(context: any): string {
    return `Generate a magical item for D&D 5e including:
- Name and description
- Rarity and type
- Magical properties
- Lore and origin

Context: ${JSON.stringify(context)}`;
  }

  private buildSpellPrompt(context: any): string {
    return `Generate a custom D&D 5e spell including:
- Name and school of magic
- Level and casting requirements
- Duration and range
- Effects and flavor text

Context: ${JSON.stringify(context)}`;
  }

  private buildEncounterPrompt(context: any): string {
    return `Generate a D&D 5e encounter including:
- Setting and atmosphere
- Enemies or challenges
- Tactical considerations
- Potential rewards

Context: ${JSON.stringify(context)}`;
  }

  private buildMapPrompt(context: any): string {
    return `Generate a detailed map description including:
- Overall layout and geography
- Key locations and landmarks
- Atmosphere and mood
- Potential points of interest

Context: ${JSON.stringify(context)}`;
  }

  private initializeDefaultProviders(): void {
    const defaultConfig: AIProviderConfig = {
      apiKey: '',
      timeout: 30000,
      maxRetries: 3,
      rateLimitPerMinute: 60,
      priority: 1,
      enabled: false
    };

    // Initialize providers from environment if available
    const autoRegister = process.env.AI_ENABLE_AUTO_PROVIDERS !== 'false';
    const openaiKey = process.env.OPENAI_API_KEY;
    const openrouterKey = process.env.OPENROUTER_API_KEY;
    const anthropicKey = process.env.ANTHROPIC_API_KEY;
    const geminiKey = process.env.GOOGLE_API_KEY || process.env.GEMINI_API_KEY;
    const replicateKey = process.env.REPLICATE_API_TOKEN;
    const huggingfaceKey = process.env.HUGGINGFACE_API_KEY;

    // OpenAI Provider
    if (autoRegister && openaiKey && openaiKey.trim()) {
      this.registerProvider(
        new OpenAIProvider({ ...defaultConfig, apiKey: openaiKey, priority: 5, enabled: true }),
        { ...defaultConfig, apiKey: openaiKey, priority: 5, enabled: true }
      );
    }

    // OpenRouter Provider (highest priority for flexibility)
    if (autoRegister && openrouterKey && openrouterKey.trim()) {
      this.registerProvider(
        new OpenRouterProvider({ ...defaultConfig, apiKey: openrouterKey, priority: 4, enabled: true }),
        { ...defaultConfig, apiKey: openrouterKey, priority: 4, enabled: true }
      );
    }

    // Anthropic Provider
    if (autoRegister && anthropicKey && anthropicKey.trim()) {
      this.registerProvider(
        new AnthropicChatProvider({ ...defaultConfig, apiKey: anthropicKey, priority: 5, enabled: true }),
        { ...defaultConfig, apiKey: anthropicKey, priority: 5, enabled: true }
      );
    }

    // Gemini Provider
    if (autoRegister && geminiKey && geminiKey.trim()) {
      this.registerProvider(
        new GeminiProvider({ ...defaultConfig, apiKey: geminiKey, priority: 3, enabled: true }),
        { ...defaultConfig, apiKey: geminiKey, priority: 3, enabled: true }
      );
    }

    // Replicate Provider
    if (autoRegister && replicateKey && replicateKey.trim()) {
      this.registerProvider(
        new ReplicateProvider({ ...defaultConfig, apiKey: replicateKey, priority: 2, enabled: true, rateLimitPerMinute: 30 }),
        { ...defaultConfig, apiKey: replicateKey, priority: 2, enabled: true, rateLimitPerMinute: 30 }
      );
    }

    // HuggingFace Provider (inference API)
    if (autoRegister && huggingfaceKey && huggingfaceKey.trim()) {
      this.registerProvider(
        new HuggingFaceProvider({ ...defaultConfig, apiKey: huggingfaceKey, priority: 2, enabled: true, rateLimitPerMinute: 20 }),
        { ...defaultConfig, apiKey: huggingfaceKey, priority: 2, enabled: true, rateLimitPerMinute: 20 }
      );
    }

    // Optionally register local provider as fallback when explicitly enabled
    if (process.env.AI_ENABLE_LOCAL_PROVIDER === 'true') {
      this.registerProvider(
        new LocalAIProvider({ ...defaultConfig, priority: 1, enabled: true }),
        { ...defaultConfig, priority: 1, enabled: true }
      );
    }
  }

  private generateRequestId(): string {
    return `ai_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }
}

// Export singleton instance
export const _aiProviderRegistry = new AIProviderRegistry();

/**
 * Unified GPU Resource Manager - Consolidates GPU resource management
 * across renderer, physics, and audio systems
 */

import { EventEmitter, SystemEvents } from './EventEmitter';
import { 
  GPUResourceManager as IGPUResourceManager, 
  GPUResourceInfo, 
  Disposable 
} from './SharedInterfaces';

export interface GPUResourceConfig {
  maxBufferSize: number;
  maxTextureSize: number;
  enableResourceTracking: boolean;
  enableMemoryProfiling: boolean;
  warningThresholds: {
    memoryUsage: number;
    resourceCount: number;
  };
}

export interface BufferDescriptor {
  size: number;
  usage: number;
  label?: string;
  mappedAtCreation?: boolean;
}

export interface TextureDescriptor {
  width: number;
  height: number;
  depth?: number;
  format: string;
  usage: number;
  mipLevelCount?: number;
  sampleCount?: number;
  label?: string;
}

export class UnifiedGPUResourceManager extends EventEmitter<SystemEvents> implements IGPUResourceManager {
  private config: GPUResourceConfig;
  private resources = new Map<string, GPUResourceInfo>();
  private buffers = new Map<string, any>(); // GPUBuffer placeholder
  private textures = new Map<string, any>(); // GPUTexture placeholder
  private pipelines = new Map<string, any>(); // GPUPipeline placeholder
  private samplers = new Map<string, any>(); // GPUSampler placeholder

  private resourceCounter = 0;
  private memoryUsage = { used: 0, total: 0 };
  private cleanupScheduled = false;

  constructor(config: Partial<GPUResourceConfig> = {}) {
    super();

    this.config = {
      maxBufferSize: 256 * 1024 * 1024, // 256MB
      maxTextureSize: 4096,
      enableResourceTracking: true,
      enableMemoryProfiling: true,
      warningThresholds: {
        memoryUsage: 0.8, // 80%
        resourceCount: 1000
      },
      ...config
    };
  }

  /**
   * Initialize the GPU resource manager
   */
  async initialize(): Promise<void> {
    try {
      // Initialize GPU context and device
      // This would normally initialize WebGPU, but for now it's a placeholder
      this.memoryUsage.total = this.config.maxBufferSize;

      if (this.config.enableMemoryProfiling) {
        this.startMemoryMonitoring();
      }

      this.emit('ready', undefined);
    } catch (error) {
      console.error('Failed to initialize GPU resource manager:', error);
      throw error;
    }
  }

  /**
   * Create a GPU buffer
   */
  createBuffer(size: number, usage: number, label?: string): string {
    if (size > this.config.maxBufferSize) {
      throw new Error(`Buffer size ${size} exceeds maximum ${this.config.maxBufferSize}`);
    }

    const id = this.generateResourceId();

    // Placeholder buffer creation - would create actual GPUBuffer
    const buffer = {
      size,
      usage,
      label,
      destroy: () => this.destroyBuffer(id)
    };

    const resourceInfo: GPUResourceInfo = {
      id,
      type: 'buffer',
      size,
      usage,
      ...(label !== undefined && { label }),
      createdAt: new Date(),
      lastUsed: new Date()
    };

    this.buffers.set(id, buffer);
    this.resources.set(id, resourceInfo);
    this.memoryUsage.used += size;

    this.checkMemoryThresholds();

    return id;
  }

  /**
   * Create a GPU texture
   */
  createTexture(width: number, height: number, format: string, usage: number, label?: string): string {
    if (width > this.config.maxTextureSize || height > this.config.maxTextureSize) {
      throw new Error(`Texture size ${width}x${height} exceeds maximum ${this.config.maxTextureSize}`);
    }

    const id = this.generateResourceId();
    const bytesPerPixel = this.getBytesPerPixel(format);
    const size = width * height * bytesPerPixel;

    // Placeholder texture creation - would create actual GPUTexture
    const texture = {
      width,
      height,
      format,
      usage,
      label,
      destroy: () => this.destroyTexture(id)
    };

    const resourceInfo: GPUResourceInfo = {
      id,
      type: 'texture',
      size,
      usage,
      ...(label !== undefined && { label }),
      createdAt: new Date(),
      lastUsed: new Date()
    };

    this.textures.set(id, texture);
    this.resources.set(id, resourceInfo);
    this.memoryUsage.used += size;

    this.checkMemoryThresholds();

    return id;
  }

  /**
   * Get resource information
   */
  getResource(id: string): GPUResourceInfo | null {
    const resource = this.resources.get(id);
    if (resource) {
      resource.lastUsed = new Date();
      return resource;
    }
    return null;
  }

  /**
   * Destroy a resource
   */
  destroyResource(id: string): void {
    const resource = this.resources.get(id);
    if (!resource) return;

    switch (resource.type) {
      case 'buffer':
        this.destroyBuffer(id);
        break;
      case 'texture':
        this.destroyTexture(id);
        break;
      case 'pipeline':
        this.destroyPipeline(id);
        break;
      case 'sampler':
        this.destroySampler(id);
        break;
    }
  }

  /**
   * Get memory usage statistics
   */
  getMemoryUsage(): { used: number; total: number } {
    return { ...this.memoryUsage };
  }

  /**
   * Get all resources of a specific type
   */
  getResourcesByType(type: GPUResourceInfo['type']): GPUResourceInfo[] {
    return Array.from(this.resources.values()).filter(r => r.type === type);
  }

  /**
   * Schedule cleanup of unused resources
   */
  scheduleCleanup(): void {
    if (this.cleanupScheduled) return;

    this.cleanupScheduled = true;
    setTimeout(() => {
      this.performCleanup();
      this.cleanupScheduled = false;
    }, 5000); // Cleanup after 5 seconds
  }

  /**
   * Get buffer by ID
   */
  getBuffer(id: string): any | null {
    return this.buffers.get(id) || null;
  }

  /**
   * Get texture by ID
   */
  getTexture(id: string): any | null {
    return this.textures.get(id) || null;
  }

  /**
   * Get pipeline by ID
   */
  getPipeline(id: string): any | null {
    return this.pipelines.get(id) || null;
  }

  /**
   * Get sampler by ID
   */
  getSampler(id: string): any | null {
    return this.samplers.get(id) || null;
  }

  /**
   * Create a render pipeline
   */
  createRenderPipeline(descriptor: any, label?: string): string {
    const id = this.generateResourceId();

    // Placeholder pipeline creation
    const pipeline = {
      ...descriptor,
      label,
      destroy: () => this.destroyPipeline(id)
    };

    const size = 1024; // Estimated size
    const usage = 0;

    const resourceInfo: GPUResourceInfo = {
      id,
      type: 'pipeline',
      size,
      usage,
      ...(label !== undefined && { label }),
      createdAt: new Date(),
      lastUsed: new Date()
    };

    this.pipelines.set(id, pipeline);
    this.resources.set(id, resourceInfo);

    return id;
  }

  /**
   * Create a compute pipeline
   */
  createComputePipeline(descriptor: any, label?: string): string {
    return this.createRenderPipeline(descriptor, label); // Same implementation for now
  }

  /**
   * Create a sampler
   */
  createSampler(descriptor: any, label?: string): string {
    const id = this.generateResourceId();

    // Placeholder sampler creation
    const sampler = {
      ...descriptor,
      label,
      destroy: () => this.destroySampler(id)
    };

    const size = 64; // Estimated size
    const usage = 0;

    const resourceInfo: GPUResourceInfo = {
      id,
      type: 'sampler',
      size,
      usage,
      ...(label !== undefined && { label }),
      createdAt: new Date(),
      lastUsed: new Date()
    };

    this.samplers.set(id, sampler);
    this.resources.set(id, resourceInfo);

    return id;
  }

  /**
   * Get resource statistics
   */
  getStats(): {
    totalResources: number;
    memoryUsage: { used: number; total: number; percentage: number };
    resourcesByType: Record<string, number>;
    oldestResource: Date | null;
    newestResource: Date | null;
  } {
    const resourcesByType: Record<string, number> = {};
    let oldestResource: Date | null = null;
    let newestResource: Date | null = null;

    for (const resource of this.resources.values()) {
      resourcesByType[resource.type] = (resourcesByType[resource.type] || 0) + 1;

      if (!oldestResource || resource.createdAt < oldestResource) {
        oldestResource = resource.createdAt;
      }

      if (!newestResource || resource.createdAt > newestResource) {
        newestResource = resource.createdAt;
      }
    }

    return {
      totalResources: this.resources.size,
      memoryUsage: {
        used: this.memoryUsage.used,
        total: this.memoryUsage.total,
        percentage: this.memoryUsage.total > 0 ? this.memoryUsage.used / this.memoryUsage.total : 0
      },
      resourcesByType,
      oldestResource,
      newestResource
    };
  }

  /**
   * Dispose of all resources
   */
  dispose(): void {
    // Destroy all resources
    for (const id of this.resources.keys()) {
      this.destroyResource(id);
    }

    this.resources.clear();
    this.buffers.clear();
    this.textures.clear();
    this.pipelines.clear();
    this.samplers.clear();

    this.memoryUsage = { used: 0, total: 0 };
    this.removeAllListeners();
  }

  // Private helper methods

  private destroyBuffer(id: string): void {
    const buffer = this.buffers.get(id);
    const resource = this.resources.get(id);

    if (buffer && resource) {
      // Would call buffer.destroy() for real GPUBuffer
      this.buffers.delete(id);
      this.resources.delete(id);
      this.memoryUsage.used -= resource.size;
    }
  }

  private destroyTexture(id: string): void {
    const texture = this.textures.get(id);
    const resource = this.resources.get(id);

    if (texture && resource) {
      // Would call texture.destroy() for real GPUTexture
      this.textures.delete(id);
      this.resources.delete(id);
      this.memoryUsage.used -= resource.size;
    }
  }

  private destroyPipeline(id: string): void {
    const pipeline = this.pipelines.get(id);
    const resource = this.resources.get(id);

    if (pipeline && resource) {
      this.pipelines.delete(id);
      this.resources.delete(id);
    }
  }

  private destroySampler(id: string): void {
    const sampler = this.samplers.get(id);
    const resource = this.resources.get(id);

    if (sampler && resource) {
      this.samplers.delete(id);
      this.resources.delete(id);
    }
  }

  private performCleanup(): void {
    const now = new Date();
    const maxAge = 60000; // 1 minute
    const resourcesToCleanup: string[] = [];

    for (const [id, resource] of this.resources) {
      if (now.getTime() - resource.lastUsed.getTime() > maxAge) {
        resourcesToCleanup.push(id);
      }
    }

    for (const id of resourcesToCleanup) {
      this.destroyResource(id);
    }

    if (resourcesToCleanup.length > 0) {
      console.log(`Cleaned up ${resourcesToCleanup.length} unused GPU resources`);
    }
  }

  private checkMemoryThresholds(): void {
    const usage = this.memoryUsage.used / this.memoryUsage.total;

    if (usage > this.config.warningThresholds.memoryUsage) {
      console.warn(`GPU memory usage high: ${(usage * 100).toFixed(1)}%`);
      this.scheduleCleanup();
    }

    if (this.resources.size > this.config.warningThresholds.resourceCount) {
      console.warn(`GPU resource count high: ${this.resources.size}`);
      this.scheduleCleanup();
    }
  }

  private startMemoryMonitoring(): void {
    setInterval(() => {
      const stats = this.getStats();
      if (stats.memoryUsage.percentage > 0.9) {
        console.warn('GPU memory usage critical:', stats.memoryUsage);
        this.performCleanup();
      }
    }, 10000); // Check every 10 seconds
  }

  private generateResourceId(): string {
    return `gpu_${++this.resourceCounter}_${Date.now()}`;
  }

  private getBytesPerPixel(format: string): number {
    // Simplified format to bytes mapping
    const formatSizes: Record<string, number> = {
      'rgba8unorm': 4,
      'rgba8unorm-srgb': 4,
      'bgra8unorm': 4,
      'bgra8unorm-srgb': 4,
      'rgba16float': 8,
      'rgba32float': 16,
      'depth24plus': 4,
      'depth32float': 4,
      'r8unorm': 1,
      'rg8unorm': 2,
      'rgb8unorm': 3
    };

    return formatSizes[format] || 4;
  }
}

/**
