/**
 * Unified Network Manager - Consolidates networking and state synchronization
 * Provides real-time communication, message routing, and state sync across all VTT systems
 */

import { EventEmitter, SystemEvents } from './EventEmitter';
import { 
  NetworkManager as INetworkManager,
  NetworkMessage,
  MessageHandler,
  ConnectionState,
  ConnectionOptions,
  Disposable 
} from './SharedInterfaces';

export interface NetworkConfig {
  reconnectInterval: number;
  maxReconnectAttempts: number;
  heartbeatInterval: number;
  messageTimeout: number;
  bufferSize: number;
  compressionEnabled: boolean;
  encryptionEnabled: boolean;
}

export interface MessageRoute {
  pattern: string | RegExp;
  handler: MessageHandler;
  priority: number;
  once?: boolean;
}

export interface NetworkStats {
  totalMessages: number;
  messagesPerSecond: number;
  bytesTransmitted: number;
  bytesReceived: number;
  averageLatency: number;
  connectionUptime: number;
  reconnectCount: number;
  errorCount: number;
}

export interface QueuedMessage {
  message: NetworkMessage;
  timestamp: number;
  attempts: number;
  priority: number;
}

export class UnifiedNetworkManager extends EventEmitter<SystemEvents> implements INetworkManager {
  private config: NetworkConfig;
  private ws: WebSocket | undefined;
  private connectionState: ConnectionState = 'disconnected';
  private messageRoutes = new Map<string, MessageRoute[]>();
  private messageQueue: QueuedMessage[] = [];
  private pendingMessages = new Map<string, { resolve: (_...args: any[]) => any; reject: (_...args: any[]) => any; timeout: NodeJS.Timeout }>();

  private reconnectAttempts = 0;
  private reconnectTimer: NodeJS.Timeout | undefined;
  private heartbeatTimer: NodeJS.Timeout | undefined;
  private lastHeartbeat = 0;
  private latencyMeasurements: number[] = [];
  private connectionStartTime = 0;

  private stats: NetworkStats = {
    totalMessages: 0,
    messagesPerSecond: 0,
    bytesTransmitted: 0,
    bytesReceived: 0,
    averageLatency: 0,
    connectionUptime: 0,
    reconnectCount: 0,
    errorCount: 0
  };

  constructor(config: Partial<NetworkConfig> = {}) {
    super();

    this.config = {
      reconnectInterval: 5000,
      maxReconnectAttempts: 10,
      heartbeatInterval: 30000,
      messageTimeout: 10000,
      bufferSize: 64 * 1024, // 64KB
      compressionEnabled: false,
      encryptionEnabled: false,
      ...config
    };
  }

  /**
   * Connect to the server
   */
  async connect(url: string, options: ConnectionOptions = {}): Promise<void> {
    if (this.connectionState === 'connected' || this.connectionState === 'connecting') {
      return;
    }

    this.setConnectionState('connecting');
    this.connectionStartTime = Date.now();

    try {
      await this.establishConnection(url, options);
      this.reconnectAttempts = 0;
      this.startHeartbeat();
      this.processMessageQueue();
    } catch (error) {
      this.handleConnectionError(error as Error, options);
    }
  }

  /**
   * Disconnect from the server
   */
  disconnect(): void {
    this.clearTimers();

    if (this.ws) {
      this.ws.close(1000, 'Client disconnect');
      this.ws = undefined as WebSocket | undefined;
    }

    this.setConnectionState('disconnected');
    this.messageQueue = [];
    this.pendingMessages.clear();
  }

  /**
   * Send a message
   */
  send(message: NetworkMessage): void {
    if (!message.id) {
      message.id = this.generateMessageId();
    }

    if (!message.timestamp) {
      message.timestamp = Date.now();
    }

    if (this.connectionState !== 'connected') {
      this.queueMessage(message);
      return;
    }

    this.sendMessage(message);
  }

  /**
   * Send message and wait for response
   */
  async sendAndWait<T = any>(message: NetworkMessage, timeout?: number): Promise<T> {
    return new Promise((_resolve, __reject) => {
      if (!message.id) {
        message.id = this.generateMessageId();
      }

      const timeoutMs = timeout || this.config.messageTimeout;
      const timeoutHandle = setTimeout(() => {
        this.pendingMessages.delete(message.id!);
        reject(new Error(`Message timeout: ${message.id}`));
      }, timeoutMs);

      this.pendingMessages.set(message.id, { resolve, reject, timeout: timeoutHandle });
      this.send(message);
    });
  }

  /**
   * Subscribe to message type
   */
  subscribe(type: string, handler: MessageHandler): () => void {
    if (!this.messageRoutes.has(type)) {
      this.messageRoutes.set(type, []);
    }

    const route: MessageRoute = {
      pattern: type,
      handler,
      priority: 0
    };

    const routes = this.messageRoutes.get(type)!;
    routes.push(route);
    routes.sort((_a, _b) => b.priority - a.priority);

    return () => {
      const index = routes.indexOf(route);
      if (index >= 0) {
        routes.splice(index, 1);
      }
    };
  }

  /**
   * Subscribe once to message type
   */
  subscribeOnce(type: string, handler: MessageHandler): () => void {
    const unsubscribe = this.subscribe(_type, (message) => {
      handler(message);
      unsubscribe();
    });
    return unsubscribe;
  }

  /**
   * Register message route with pattern
   */
  registerRoute(pattern: string | RegExp, handler: MessageHandler, priority: number = 0): () => void {
    const routeKey = pattern.toString();

    if (!this.messageRoutes.has(routeKey)) {
      this.messageRoutes.set(routeKey, []);
    }

    const route: MessageRoute = {
      pattern,
      handler,
      priority
    };

    const routes = this.messageRoutes.get(routeKey)!;
    routes.push(route);
    routes.sort((_a, _b) => b.priority - a.priority);

    return () => {
      const index = routes.indexOf(route);
      if (index >= 0) {
        routes.splice(index, 1);
      }
    };
  }

  /**
   * Broadcast message to all subscribers
   */
  broadcast(message: NetworkMessage): void {
    this.routeMessage(message);
  }

  /**
   * Get connection state
   */
  getConnectionState(): ConnectionState {
    return this.connectionState;
  }

  /**
   * Get current latency
   */
  getLatency(): number {
    return this.latencyMeasurements.length > 0 
      ? this.latencyMeasurements.reduce((_a, _b) => a + b) / this.latencyMeasurements.length 
      : 0;
  }

  /**
   * Get network statistics
   */
  getStats(): NetworkStats {
    const now = Date.now();
    this.stats.connectionUptime = this.connectionStartTime > 0 ? now - this.connectionStartTime : 0;
    this.stats.averageLatency = this.getLatency();
    return { ...this.stats };
  }

  /**
   * Enable message compression
   */
  setCompression(enabled: boolean): void {
    this.config.compressionEnabled = enabled;
  }

  /**
   * Enable message encryption
   */
  setEncryption(enabled: boolean): void {
    this.config.encryptionEnabled = enabled;
  }

  /**
   * Get queued messages count
   */
  getQueueSize(): number {
    return this.messageQueue.length;
  }

  /**
   * Clear message queue
   */
  clearQueue(): void {
    this.messageQueue = [];
  }

  /**
   * Dispose of the network manager
   */
  dispose(): void {
    this.disconnect();
    this.messageRoutes.clear();
    this.removeAllListeners();
  }

  // Private helper methods

  private async establishConnection(url: string, options: ConnectionOptions): Promise<void> {
    return new Promise((_resolve, __reject) => {
      try {
        this.ws = new WebSocket(url);

        const timeout = setTimeout(() => {
          reject(new Error('Connection timeout'));
        }, options.timeout || 10000);

        this.ws.onopen = () => {
          clearTimeout(timeout);
          this.setConnectionState('connected');
          resolve();
        };

        this.ws.onclose = (event) => {
          clearTimeout(timeout);
          this.handleDisconnection(event);
        };

        this.ws.onerror = (_error) => {
          clearTimeout(timeout);
          reject(error);
        };

        this.ws.onmessage = (event) => {
          this.handleIncomingMessage(event);
        };

      } catch (error) {
        reject(error);
      }
    });
  }

  private handleConnectionError(error: Error, options: ConnectionOptions): void {
    this.stats.errorCount++;
    console.error('Connection error:', error);

    if (options.reconnect !== false && this.reconnectAttempts < this.config.maxReconnectAttempts) {
      this.scheduleReconnect();
    } else {
      this.setConnectionState('error');
    }
  }

  private handleDisconnection(event: CloseEvent): void {
    if (event.wasClean) {
      this.setConnectionState('disconnected');
    } else {
      this.setConnectionState('error');
      this.scheduleReconnect();
    }
  }

  private scheduleReconnect(): void {
    if (this.reconnectTimer) return;

    this.reconnectAttempts++;
    this.stats.reconnectCount++;
    this.setConnectionState('reconnecting');

    const delay = this.config.reconnectInterval * Math.pow(1.5, this.reconnectAttempts - 1);

    this.reconnectTimer = setTimeout(() => {
      this.reconnectTimer = undefined as NodeJS.Timeout | undefined;
      // Would reconnect with stored URL and options
    }, delay);
  }

  private handleIncomingMessage(event: MessageEvent): void {
    try {
      let data = event.data;

      if (typeof data === 'string') {
        data = JSON.parse(data);
      }

      const message: NetworkMessage = data;
      this.stats.totalMessages++;
      this.stats.bytesReceived += event.data.length;

      // Handle response to pending request
      if (message.id && this.pendingMessages.has(message.id)) {
        const pending = this.pendingMessages.get(message.id)!;
        clearTimeout(pending.timeout);
        this.pendingMessages.delete(message.id);
        pending.resolve(message.data);
        return;
      }

      // Handle heartbeat response
      if (message.type === 'heartbeat_response') {
        this.handleHeartbeatResponse(message);
        return;
      }

      // Route message to handlers
      this.routeMessage(message);

    } catch (error) {
      console.error('Failed to parse incoming message:', error);
      this.stats.errorCount++;
    }
  }

  private routeMessage(message: NetworkMessage): void {
    let handled = false;

    // Check exact type matches first
    const exactRoutes = this.messageRoutes.get(message.type);
    if (exactRoutes) {
      for (const route of exactRoutes) {
        try {
          route.handler(message);
          handled = true;

          if (route.once) {
            const routes = this.messageRoutes.get(message.type)!;
            const index = routes.indexOf(route);
            if (index >= 0) {
              routes.splice(index, 1);
            }
          }
        } catch (error) {
          console.error(`Error in message handler for ${message.type}:`, error);
        }
      }
    }

    // Check pattern matches
    for (const [key, routes] of this.messageRoutes) {
      if (key === message.type) continue; // Already handled above

      for (const route of routes) {
        if (route.pattern instanceof RegExp && route.pattern.test(message.type)) {
          try {
            route.handler(message);
            handled = true;
          } catch (error) {
            console.error(`Error in pattern handler ${route.pattern}:`, error);
          }
        }
      }
    }

    if (!handled) {
      console.warn(`Unhandled message type: ${message.type}`);
    }
  }

  private sendMessage(message: NetworkMessage): void {
    if (!this.ws || this.ws.readyState !== WebSocket.OPEN) {
      this.queueMessage(message);
      return;
    }

    try {
      let data = JSON.stringify(message);

      if (this.config.compressionEnabled) {
        data = this.compressData(data);
      }

      if (this.config.encryptionEnabled) {
        data = this.encryptData(data);
      }

      this.ws.send(data);
      this.stats.bytesTransmitted += data.length;

    } catch (error) {
      console.error('Failed to send message:', error);
      this.queueMessage(message);
      this.stats.errorCount++;
    }
  }

  private queueMessage(message: NetworkMessage, priority: number = 0): void {
    const queuedMessage: QueuedMessage = {
      message,
      timestamp: Date.now(),
      attempts: 0,
      priority
    };

    this.messageQueue.push(queuedMessage);
    this.messageQueue.sort((_a, _b) => b.priority - a.priority);

    // Limit queue size
    if (this.messageQueue.length > 1000) {
      this.messageQueue = this.messageQueue.slice(0, 1000);
    }
  }

  private processMessageQueue(): void {
    if (this.connectionState !== 'connected' || this.messageQueue.length === 0) {
      return;
    }

    const messagesToSend = this.messageQueue.splice(0, 10); // Send up to 10 at once

    for (const queuedMessage of messagesToSend) {
      queuedMessage.attempts++;
      this.sendMessage(queuedMessage.message);
    }

    // Schedule next batch if queue not empty
    if (this.messageQueue.length > 0) {
      setTimeout(() => this.processMessageQueue(), 100);
    }
  }

  private setConnectionState(state: ConnectionState): void {
    if (this.connectionState !== state) {
      const _previousState = this.connectionState;
      this.connectionState = state;
      this.emit('ready', undefined); // Generic event for connection state changes
    }
  }

  private startHeartbeat(): void {
    this.heartbeatTimer = setInterval(() => {
      if (this.connectionState === 'connected') {
        this.sendHeartbeat();
      }
    }, this.config.heartbeatInterval);
  }

  private sendHeartbeat(): void {
    const heartbeat: NetworkMessage = {
      type: 'heartbeat',
      data: { timestamp: Date.now() },
      timestamp: Date.now()
    };

    this.lastHeartbeat = Date.now();
    this.send(heartbeat);
  }

  private handleHeartbeatResponse(message: NetworkMessage): void {
    if (message.data?.timestamp) {
      const latency = Date.now() - message.data.timestamp;
      this.latencyMeasurements.push(latency);

      // Keep only last 10 measurements
      if (this.latencyMeasurements.length > 10) {
        this.latencyMeasurements.shift();
      }
    }
  }

  private clearTimers(): void {
    if (this.reconnectTimer) {
      clearTimeout(this.reconnectTimer);
      this.reconnectTimer = undefined;
    }

    if (this.heartbeatTimer) {
      clearInterval(this.heartbeatTimer);
      this.heartbeatTimer = undefined as NodeJS.Timeout | undefined;
    }
  }

  private compressData(data: string): string {
    // Placeholder for compression - would use actual compression library
    return data;
  }

  private encryptData(data: string): string {
    // Placeholder for encryption - would use actual encryption
    return btoa(data);
  }

  private generateMessageId(): string {
    return `msg_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }
}

// Export singleton instance
export const _networkManager = new UnifiedNetworkManager();

/**
 * Performance Monitor - Unified performance tracking across all VTT systems
 * Provides comprehensive metrics, profiling, and optimization insights
 */

import { EventEmitter, PerformanceEvents } from './EventEmitter';
import { 
  PerformanceMonitor as IPerformanceMonitor, 
  PerformanceProfiler, 
  PerformanceMeasurement, 
  PerformanceMetrics, 
  PerformanceStats,
  MemoryInfo,
  MemoryMeasurement,
  Disposable 
} from './SharedInterfaces';

export interface PerformanceConfig {
  maxMeasurements: number;
  enableMemoryTracking: boolean;
  enableGCTracking: boolean;
  profilerTimeout: number;
  warningThresholds: {
    frameTime: number;
    memoryUsage: number;
    operationDuration: number;
  };
}

export class PerformanceProfilerImpl implements PerformanceProfiler {
  public readonly name: string;
  public readonly startTime: number;
  private tags: Record<string, string> = {};
  private memoryStart?: MemoryMeasurement;

  constructor(name: string, trackMemory: boolean = false) {
    this.name = name;
    this.startTime = performance.now();

    if (trackMemory && (performance as any).memory) {
      const memory = (performance as any).memory;
      this.memoryStart = {
        used: memory.usedJSHeapSize,
        total: memory.totalJSHeapSize,
        delta: 0
      };
    }
  }

  addTag(key: string, value: string): void {
    this.tags[key] = value;
  }

  getTags(): Record<string, string> {
    return { ...this.tags };
  }

  getElapsedTime(): number {
    return performance.now() - this.startTime;
  }

  getMemoryDelta(): MemoryMeasurement | null {
    if (!this.memoryStart || !(performance as any).memory) return null;

    const current = (performance as any).memory;
    return {
      used: current.usedJSHeapSize,
      total: current.totalJSHeapSize,
      delta: current.usedJSHeapSize - this.memoryStart.used
    };
  }

  end(): PerformanceMeasurement {
    const duration = this.getElapsedTime();
    const memory = this.getMemoryDelta();
    return {
      name: this.name,
      duration,
      timestamp: this.startTime,
      tags: this.getTags(),
      ...(memory && { memory })
    };
  }
}

export class PerformanceMonitorImpl extends EventEmitter<PerformanceEvents> implements IPerformanceMonitor, Disposable {
  private measurements: PerformanceMeasurement[] = [];
  private counters = new Map<string, number>();
  private activeProfilers = new Map<string, PerformanceProfilerImpl>();

  private config: PerformanceConfig;
  private frameCount = 0;
  private lastFrameTime = 0;
  private frameStartTime = 0;
  private frameTimes: number[] = [];
  private isMonitoringMemory = false;
  private memoryInterval: ReturnType<typeof setInterval> | undefined;
  private gcObserver?: PerformanceObserver;

  // FPS tracking
  private fpsCounter = 0;
  private fpsStartTime = 0;
  private currentFPS = 0;

  constructor(config: Partial<PerformanceConfig> = {}) {
    super();

    this.config = {
      maxMeasurements: 1000,
      enableMemoryTracking: true,
      enableGCTracking: true,
      profilerTimeout: 30000, // 30 seconds
      warningThresholds: {
        frameTime: 16.67, // 60 FPS threshold
        memoryUsage: 0.8, // 80% of available memory
        operationDuration: 100 // 100ms for operations
      },
      ...config
    };

    this.initializePerformanceObservers();
  }

  /**
   * Start performance monitoring
   */
  start(): void {
    if (this.config.enableMemoryTracking) {
      this.startMemoryTracking();
    }

    if (this.config.enableGCTracking) {
      this.startGCTracking();
    }

    this.frameStartTime = performance.now();
    this.fpsStartTime = performance.now();
  }

  private startMemoryTracking(): void {
    this.isMonitoringMemory = true;
    this.memoryInterval = setInterval(() => {
      const memoryInfo = this.getMemoryInfo();
      const usage = memoryInfo.used / memoryInfo.total;
      if (usage > this.config.warningThresholds.memoryUsage) {
        this.emit('memory_warning', { usage, threshold: this.config.warningThresholds.memoryUsage });
      }
    }, 5000);
  }

  private startGCTracking(): void {
    try {
      this.gcObserver = new PerformanceObserver((_list) => {
        for (const entry of list.getEntries()) {
          if (entry.entryType === 'gc') {
            this.emit('gc_detected', {
              type: (entry as any).kind || 'unknown',
              duration: entry.duration
            });
          }
        }
      });
      this.gcObserver.observe({ entryTypes: ['gc'] });
    } catch (_error) {
      // GC observation not supported
    }
  }

  /**
   * Start a performance profiler
   */
  startProfiling(name: string, trackMemory: boolean = false): PerformanceProfiler {
    return this.startProfiler(name, trackMemory);
  }

  startProfiler(name: string, trackMemory: boolean = false): PerformanceProfiler {
    const profiler = new PerformanceProfilerImpl(name, trackMemory || this.config.enableMemoryTracking);
    this.activeProfilers.set(name, profiler);

    // Set timeout to auto-end long-running profilers
    setTimeout(() => {
      if (this.activeProfilers.has(name)) {
        console.warn(`Profiler '${name}' exceeded timeout, auto-ending`);
        this.endProfiling(profiler);
      }
    }, this.config.profilerTimeout);

    return profiler;
  }

  /**
   * End a performance profiler
   */
  endProfiling(profiler: PerformanceProfiler): PerformanceMeasurement {
    const measurement = (profiler as PerformanceProfilerImpl).end();
    this.measurements.push(measurement);
    this.activeProfilers.delete(profiler.name);

    this.emit('measurement_added', {
      name: measurement.name,
      duration: measurement.duration
    });

    return measurement;
  }

  endProfiler(name: string): PerformanceMeasurement | null {
    const profiler = this.activeProfilers.get(name);
    if (!profiler) {
      return null;
    }

    return this.endProfiling(profiler);
  }

  /**
   * Measure a synchronous operation
   */
  measure<T>(name: string, _fn: () => T): T {
    const profiler = this.startProfiler(name);

    try {
      const result = fn();
      this.endProfiling(profiler);
      return result;
    } catch (error) {
      this.endProfiling(profiler);
      throw error;
    }
  }

  /**
   * Measure an asynchronous operation
   */
  async measureAsync<T>(name: string, _fn: () => Promise<T>): Promise<T> {
    const profiler = this.startProfiler(name);

    try {
      const result = await fn();
      this.endProfiling(profiler);
      return result;
    } catch (error) {
      this.endProfiling(profiler);
      throw error;
    }
  }

  /**
   * Add a measurement manually
   */
  addMeasurement(name: string, duration: number, tags?: Record<string, string>): void {
    const measurement: PerformanceMeasurement = {
      name,
      duration,
      timestamp: performance.now(),
      tags: tags || {}
    };
    this.recordMeasurement(measurement);
  }

  /**
   * Get measurements by name
   */
  getMeasurements(name?: string): PerformanceMeasurement[] {
    if (name) {
      return this.measurements.filter(m => m.name === name);
    }
    return [...this.measurements];
  }

  /**
   * Clear all measurements
   */
  clearMeasurements(): void {
    this.measurements = [];
  }

  /**
   * Track FPS
   */
  trackFPS(_fps: number, _frameTime: number): void {
    this.endFrame();
  }

  /**
   * Get performance stats
   */
  getStats(): PerformanceStats {
    const durations = this.measurements.map(m => m.duration);
    const sortedDurations = durations.sort((_a, __b) => a - b);
    const frameTimes = this.frameTimes.length > 0 ? this.frameTimes : [0];

    return {
      totalMeasurements: this.measurements.length,
      averageDuration: durations.length > 0 ? durations.reduce((_a, __b) => a + b, 0) / durations.length : 0,
      averageFrameTime: frameTimes.reduce((_a, __b) => a + b, 0) / frameTimes.length,
      minDuration: durations.length > 0 ? Math.min(...durations) : 0,
      maxDuration: durations.length > 0 ? Math.max(...durations) : 0,
      p50: sortedDurations[Math.floor(sortedDurations.length * 0.5)] || 0,
      p95: sortedDurations[Math.floor(sortedDurations.length * 0.95)] || 0,
      p99: sortedDurations[Math.floor(sortedDurations.length * 0.99)] || 0,
      counters: Object.fromEntries(this.counters),
      gauges: Record<string, any>
    };
  }

  /**
   * Increment counter
   */
  increment(counter: string, value: number = 1): void {
    const current = this.counters.get(counter) || 0;
    this.counters.set(counter, current + value);
  }

  /**
   * Decrement counter
   */
  decrement(counter: string, value: number = 1): void {
    const current = this.counters.get(counter) || 0;
    this.counters.set(counter, current - value);
  }

  /**
   * Set gauge value
   */
  gauge(metric: string, value: number): void {
    this.counters.set(metric, value);
  }

  /**
   * Increment a counter
   */
  incrementCounter(name: string, tags?: Record<string, string>): void {
    const key = tags ? `${name}:${Object.entries(tags).map([k, _v] => `${k}=${v}`).join(',')}` : name;
    const current = this.counters.get(key) || 0;
    this.counters.set(key, current + 1);
  }

  /**
   * Start frame timing
   */
  startFrame(): void {
    this.frameStartTime = performance.now();
  }

  /**
   * End frame timing and track FPS
   */
  endFrame(): void {
    if (this.frameStartTime === 0) return;

    const frameTime = performance.now() - this.frameStartTime;
    this.frameTimes.push(frameTime);

    // Keep only last 60 frame times
    if (this.frameTimes.length > 60) {
      this.frameTimes.shift();
    }

    // Calculate FPS
    this.fpsCounter++;
    const currentTime = performance.now();

    if (this.fpsStartTime === 0) {
      this.fpsStartTime = currentTime;
    } else if (currentTime - this.fpsStartTime >= 1000) {
      this.currentFPS = this.fpsCounter;
      this.fpsCounter = 0;
      this.fpsStartTime = currentTime;

      this.emit('fps', { fps: this.currentFPS, frameTime });
    }

    // Check for performance issues
    if (frameTime > this.config.warningThresholds.frameTime) {
      this.emit('fps_drop', {
        fps: this.currentFPS,
        previousFps: this.currentFPS
      });
    }

    this.frameCount++;
    this.lastFrameTime = frameTime;
    this.frameStartTime = 0;
  }

  /**
   * Get current performance metrics
   */
  getMetrics(): PerformanceMetrics {
    const stats = this.getStats();
    const memory = this.getMemoryInfo();
    return {
      ...stats,
      fps: this.currentFPS,
      frameCount: this.frameCount,
      memoryUsage: memory
    };
  }

  /**
   * Record a custom metric
   */
  recordMetric(name: string, value: number, tags?: Record<string, string>): void {
    const measurement: PerformanceMeasurement = {
      name,
      duration: value,
      timestamp: performance.now(),
      tags: tags || {}
    };
    this.recordMeasurement(measurement);
  }

  /**
   * Start memory monitoring
   */
  startMemoryMonitoring(): void {
    if (this.isMonitoringMemory) return;

    this.isMonitoringMemory = true;

    this.memoryInterval = setInterval(() => {
      const memory = this.getMemoryInfo();

      const used = memory.used;
      const total = memory.total;
      const usage = used / total;

      this.emit('memory', { used, total, usage, threshold: this.config.warningThresholds.memoryUsage });

      // Check memory warnings
      if (memory.total > 0 && memory.used / memory.total > this.config.warningThresholds.memoryUsage) {
        this.emit('memory_warning', {
          usage,
          threshold: this.config.warningThresholds.memoryUsage
        });
      }
    }, 5000); // Check every 5 seconds
  }

  /**
   * Stop memory monitoring
   */
  stopMemoryMonitoring(): void {
    this.isMonitoringMemory = false;

    if (this.memoryInterval) {
      clearInterval(this.memoryInterval);
      this.memoryInterval = undefined;
    }
  }

  /**
   * Clear all performance data
   */
  reset(): void {
    this.measurements = [];
    this.counters.clear();
    this.frameTimes = [];
    this.frameCount = 0;
    this.fpsCounter = 0;
    this.currentFPS = 0;
    this.fpsStartTime = 0;
  }

  /**
   * Dispose of the performance monitor
   */
  dispose(): void {
    this.stopMemoryMonitoring();

    if (this.gcObserver) {
      this.gcObserver.disconnect();
    }

    // End any active profilers
    for (const [_name, profiler] of this.activeProfilers.entries()) {
      const measurement = profiler.end();
      this.recordMeasurement(measurement);
    }
    this.activeProfilers.clear();

    this.reset();
    this.removeAllListeners();
  }

  // Private helper methods

  private recordMeasurement(measurement: PerformanceMeasurement): void {
    this.measurements.push(measurement);

    // Keep only the most recent measurements
    if (this.measurements.length > this.config.maxMeasurements) {
      this.measurements.shift();
    }

    // Emit performance event
    this.emit('measurement', measurement);

    // Check for performance issues
    if (measurement.duration > this.config.warningThresholds.operationDuration) {
      this.emit('performance_issue', {
        type: 'high_duration',
        severity: 'warning' as const,
        details: { measurement, threshold: this.config.warningThresholds.operationDuration }
      });
    }
  }

  getMemoryInfo(): MemoryInfo {
    if ((performance as any).memory) {
      const memory = (performance as any).memory;
      return {
        used: memory?.usedJSHeapSize || 0,
        total: memory?.totalJSHeapSize || 0,
        limit: memory?.jsHeapSizeLimit || 0,
        available: memory ? memory.totalJSHeapSize - memory.usedJSHeapSize : 0
      };
    }

    // Fallback for environments without memory API
    return {
      used: 0,
      total: 0,
      limit: 0,
      available: 0
    };
  }

  private initializePerformanceObservers(): void {
    // Set up GC observer if available
    if (this.config.enableGCTracking && typeof PerformanceObserver !== 'undefined') {
      try {
        this.gcObserver = new PerformanceObserver((_list) => {
          const entries = list.getEntries();
          for (const entry of entries) {
            if (entry.entryType === 'measure' && entry.name.includes('gc')) {
              this.recordMetric('garbage_collection', entry.duration, {
                type: entry.name
              });
            }
          }
        });

        this.gcObserver.observe({ entryTypes: ['measure', 'navigation', 'paint'] });
      } catch (error) {
        console.warn('Failed to initialize performance observer:', error);
      }
    }
  }
}

// Export singleton instance
export const _performanceMonitor = new PerformanceMonitorImpl();

/**
 * Shared Interfaces and Types for the VTT Platform
 * Centralizes all common data structures and type definitions
 */

// ==================== BASIC TYPES ====================

export interface Vector2 {
  x: number;
  y: number;
}

export interface Vector3 extends Vector2 {
  z: number;
}

export interface Vector4 extends Vector3 {
  w: number;
}

export interface Matrix4 {
  elements: Float32Array;
}

export interface Transform {
  position: Vector3;
  rotation: Vector3;
  scale: Vector3;
}

export interface AABB {
  min: Vector3;
  max: Vector3;
}

export interface Color {
  r: number;
  g: number;
  b: number;
  a?: number;
}

// ==================== DISPOSABLE PATTERN ====================

export interface Disposable {
  dispose(): void;
}

// ==================== GPU RESOURCE MANAGEMENT ====================

export interface GPUResourceInfo {
  id: string;
  type: 'buffer' | 'texture' | 'pipeline' | 'sampler';
  size: number;
  usage: number;
  label?: string;
  createdAt: Date;
  lastUsed: Date;
}

export interface GPUResourceManager extends Disposable {
  initialize(): Promise<void>;
  createBuffer(size: number, usage: number, label?: string): string;
  createTexture(width: number, height: number, format: string, usage: number, label?: string): string;
  getResource(id: string): GPUResourceInfo | null;
  destroyResource(id: string): void;
  getMemoryUsage(): { used: number; total: number };
}

// ==================== ASSET MANAGEMENT ====================

export interface AssetInfo {
  id: string;
  path: string;
  type: AssetType;
  format: string;
  size: number;
  checksum: string;
  metadata: Record<string, any>;
  dependencies: string[];
  loadedAt?: Date;
}

export type AssetType = 'texture' | 'model' | 'audio' | 'data' | 'shader' | 'material' | 'animation';

export interface AssetLoadOptions {
  priority?: number;
  cache?: boolean;
  timeout?: number;
  transform?: (_data: any) => any;
}

export interface AssetManager extends Disposable {
  initialize(): Promise<void>;
  register(path: string, type: AssetType, metadata?: Record<string, any>): Promise<string>;
  load<T = any>(id: string, options?: AssetLoadOptions): Promise<T>;
  unload(id: string): void;
  get<T = any>(id: string): T | null;
  getInfo(id: string): AssetInfo | null;
  query(filter: Partial<AssetInfo>): AssetInfo[];
}

// ==================== AI PROVIDER INTERFACES ====================

export interface AIProvider {
  readonly name: string;
  readonly type: 'chat' | 'completion' | 'embedding' | 'image' | 'audio';
  readonly capabilities: AICapability[];
  generateText?(prompt: string, options?: AITextOptions): Promise<AITextResponse>;
  generateImage?(prompt: string, options?: AIImageOptions): Promise<AIImageResponse>;
  generateAudio?(prompt: string, options?: AIAudioOptions): Promise<AIAudioResponse>;
  generateCode?(prompt: string, options?: AICodeOptions): Promise<AICodeResponse>;
  createEmbeddings?(text: string): Promise<number[]>;
}

export type AICapability = 
  | 'text_generation'
  | 'code_generation'
  | 'image_generation'
  | 'audio_generation'
  | 'embeddings'
  | 'vision'
  | 'function_calling';

export interface AITextOptions {
  model?: string;
  maxTokens?: number;
  temperature?: number;
