/**
 * Unified Asset Manager - Consolidates asset handling across all VTT systems
 * Provides comprehensive asset loading, caching, processing, and management
 */

import { EventEmitter, SystemEvents } from './EventEmitter';
import { 
  AssetManager as IAssetManager, 
  AssetInfo, 
  AssetType, 
  AssetLoadOptions,
  Disposable 
} from './SharedInterfaces';

export interface AssetManagerConfig {
  maxCacheSize: number;
  maxConcurrentLoads: number;
  enableCompression: boolean;
  enableCaching: boolean;
  defaultTimeout: number;
  retryAttempts: number;
  cachePersistence: boolean;
}

export interface ProcessingPipeline {
  name: string;
  process: (data: any, asset: AssetInfo) => Promise<any>;
  priority: number;
  enabled: boolean;
}

export interface AssetBundle {
  id: string;
  name: string;
  assets: string[];
  metadata: Record<string, any>;
  loadedAt?: Date;
}

export class UnifiedAssetManager extends EventEmitter<SystemEvents> implements IAssetManager {
  private config: AssetManagerConfig;
  private assets = new Map<string, AssetInfo>();
  private cache = new Map<string, any>();
  private loadingPromises = new Map<string, Promise<any>>();
  private bundles = new Map<string, AssetBundle>();
  private processingPipelines = new Map<string, ProcessingPipeline[]>();

  private currentLoads = 0;
  private totalCacheSize = 0;
  private assetCounter = 0;

  constructor(config: Partial<AssetManagerConfig> = {}) {
    super();

    this.config = {
      maxCacheSize: 512 * 1024 * 1024, // 512MB
      maxConcurrentLoads: 10,
      enableCompression: false,
      enableCaching: true,
      defaultTimeout: 30000,
      retryAttempts: 3,
      cachePersistence: true,
      ...config
    };

    this.initializeProcessingPipelines();
  }

  /**
   * Initialize the asset manager
   */
  async initialize(): Promise<void> {
    try {
      if (this.config.cachePersistence) {
        await this.loadCacheFromStorage();
      }

      this.emit('ready', undefined);
    } catch (error) {
      console.error('Failed to initialize asset manager:', error);
      throw error;
    }
  }

  /**
   * Register an asset
   */
  async register(path: string, type: AssetType, metadata: Record<string, any> = {}): Promise<string> {
    const id = this.generateAssetId();
    const checksum = await this.calculateChecksum(path);

    const assetInfo: AssetInfo = {
      id,
      path,
      type,
      format: this.detectFormat(path),
      size: 0, // Will be updated on load
      checksum,
      metadata,
      dependencies: metadata.dependencies || []
    };

    this.assets.set(id, assetInfo);
    return id;
  }

  /**
   * Load an asset
   */
  async load<T = any>(id: string, options: AssetLoadOptions = {}): Promise<T> {
    const asset = this.assets.get(id);
    if (!asset) {
      throw new Error(`Asset with id '${id}' not found`);
    }

    // Check cache first
    if (this.config.enableCaching && this.cache.has(id)) {
      return this.cache.get(id);
    }

    // Check if already loading
    if (this.loadingPromises.has(id)) {
      return this.loadingPromises.get(id)!;
    }

    // Check concurrent load limit
    if (this.currentLoads >= this.config.maxConcurrentLoads) {
      await this.waitForLoadSlot();
    }

    const loadPromise = this.performLoad<T>(asset, options);
    this.loadingPromises.set(id, loadPromise);

    try {
      const result = await loadPromise;

      // Cache the result
      if (this.config.enableCaching && (options.cache !== false)) {
        this.addToCache(id, result, asset);
      }

      asset.loadedAt = new Date();
      return result;
    } finally {
      this.loadingPromises.delete(id);
      this.currentLoads--;
    }
  }

  /**
   * Unload an asset from cache
   */
  unload(id: string): void {
    if (this.cache.has(id)) {
      const asset = this.assets.get(id);
      if (asset) {
        this.totalCacheSize -= asset.size;
      }
      this.cache.delete(id);
    }
  }

  /**
   * Get cached asset data
   */
  get<T = any>(id: string): T | null {
    return this.cache.get(id) || null;
  }

  /**
   * Get asset information
   */
  getInfo(id: string): AssetInfo | null {
    return this.assets.get(id) || null;
  }

  /**
   * Query assets by filter
   */
  query(filter: Partial<AssetInfo>): AssetInfo[] {
    const results: AssetInfo[] = [];

    for (const asset of this.assets.values()) {
      let matches = true;

      for (const [key, value] of Object.entries(filter)) {
        if (key === 'metadata') {
          // Handle metadata filtering
          for (const [metaKey, metaValue] of Object.entries(value as Record<string, any>)) {
            if (asset.metadata[metaKey] !== metaValue) {
              matches = false;
              break;
            }
          }
        } else if ((asset as any)[key] !== value) {
          matches = false;
          break;
        }
      }

      if (matches) {
        results.push(asset);
      }
    }

    return results;
  }

  /**
   * Create asset bundle
   */
  createBundle(name: string, assetIds: string[], metadata: Record<string, any> = {}): string {
    const id = this.generateBundleId();
    const bundle: AssetBundle = {
      id,
      name,
      assets: assetIds,
      metadata
    };

    this.bundles.set(id, bundle);
    return id;
  }

  /**
   * Load asset bundle
   */
  async loadBundle(bundleId: string, options: AssetLoadOptions = {}): Promise<Record<string, any>> {
    const bundle = this.bundles.get(bundleId);
    if (!bundle) {
      throw new Error(`Bundle with id '${bundleId}' not found`);
    }

    const results: Record<string, any> = {};
    const loadPromises = bundle.assets.map(async (assetId) => {
      const asset = await this.load(assetId, options);
      results[assetId] = asset;
    });

    await Promise.all(loadPromises);
    bundle.loadedAt = new Date();

    return results;
  }

  /**
   * Add processing pipeline for asset type
   */
  addProcessingPipeline(assetType: AssetType, pipeline: ProcessingPipeline): void {
    if (!this.processingPipelines.has(assetType)) {
      this.processingPipelines.set(assetType, []);
    }

    const pipelines = this.processingPipelines.get(assetType)!;
    pipelines.push(pipeline);
    pipelines.sort((_a, _b) => b.priority - a.priority);
  }

  /**
   * Get cache statistics
   */
  getCacheStats(): { 
    size: number; 
    maxSize: number; 
    items: number; 
    hitRate: number;
    totalLoads: number;
  } {
    return {
      size: this.totalCacheSize,
      maxSize: this.config.maxCacheSize,
      items: this.cache.size,
      hitRate: 0, // Would track in real implementation
      totalLoads: this.assetCounter
    };
  }

  /**
   * Clear cache
   */
  clearCache(): void {
    this.cache.clear();
    this.totalCacheSize = 0;
  }

  /**
   * Preload assets
   */
  async preload(assetIds: string[], options: AssetLoadOptions = {}): Promise<void> {
    const loadPromises = assetIds.map(id => this.load(id, options));
    await Promise.all(loadPromises);
  }

  /**
   * Get all asset IDs by type
   */
  getAssetIdsByType(type: AssetType): string[] {
    const results: string[] = [];
    for (const [id, asset] of this.assets) {
      if (asset.type === type) {
        results.push(id);
      }
    }
    return results;
  }

  /**
   * Dispose of the asset manager
   */
  dispose(): void {
    this.clearCache();
    this.assets.clear();
    this.bundles.clear();
    this.loadingPromises.clear();
    this.processingPipelines.clear();
    this.removeAllListeners();
  }

  // Private helper methods

  private async performLoad<T>(asset: AssetInfo, options: AssetLoadOptions): Promise<T> {
    this.currentLoads++;

    try {
      let data = await this.fetchAsset(asset, options);

      // Apply processing pipelines
      const pipelines = this.processingPipelines.get(asset.type) || [];
      for (const pipeline of pipelines) {
        if (pipeline.enabled) {
          data = await pipeline.process(data, asset);
        }
      }

      // Apply custom transform
      if (options.transform) {
        data = options.transform(data);
      }

      // Update asset size
      asset.size = this.estimateDataSize(data);

      return data;
    } catch (error) {
      console.error(`Failed to load asset ${asset.id}:`, error);
      throw error;
    }
  }

  private async fetchAsset(asset: AssetInfo, options: AssetLoadOptions): Promise<any> {
    const timeout = options.timeout || this.config.defaultTimeout;
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), timeout);

    try {
      const response = await fetch(asset.path, {
        signal: controller.signal
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      // Determine how to parse based on asset type
      switch (asset.type) {
        case 'texture':
          return await this.loadImageData(response);
        case 'audio':
          return await response.arrayBuffer();
        case 'model':
          return await response.arrayBuffer();
        case 'data':
          return await response.json();
        case 'shader':
          return await response.text();
        default:
          return await response.arrayBuffer();
      }
    } finally {
      clearTimeout(timeoutId);
    }
  }

  private async loadImageData(response: Response): Promise<ImageBitmap> {
    const blob = await response.blob();
    return createImageBitmap(blob);
  }

  private addToCache(id: string, data: any, asset: AssetInfo): void {
    // Check if adding would exceed cache size
    if (this.totalCacheSize + asset.size > this.config.maxCacheSize) {
      this.evictCacheItems(asset.size);
    }

    this.cache.set(id, data);
    this.totalCacheSize += asset.size;
  }

  private evictCacheItems(requiredSpace: number): void {
    // Simple LRU-style eviction based on loadedAt timestamp
    const sortedAssets = Array.from(this.assets.entries())
      .filter(_([id]) => this.cache.has(id))
      .sort(_([, _a], _[, _b]) => {
        const aTime = a.loadedAt?.getTime() || 0;
        const bTime = b.loadedAt?.getTime() || 0;
        return aTime - bTime;
      });

    let freedSpace = 0;
    for (const [id, asset] of sortedAssets) {
      if (freedSpace >= requiredSpace) break;

      this.cache.delete(id);
      freedSpace += asset.size;
      this.totalCacheSize -= asset.size;
    }
  }

  private async waitForLoadSlot(): Promise<void> {
    return new Promise(_resolve => {
      const checkSlot = () => {
        if (this.currentLoads < this.config.maxConcurrentLoads) {
          resolve();
        } else {
          setTimeout(checkSlot, 100);
        }
      };
      checkSlot();
    });
  }

  private async calculateChecksum(path: string): Promise<string> {
    // Simple checksum based on path - would use actual file content in production
    const encoder = new TextEncoder();
    const data = encoder.encode(path);
    const hashBuffer = await crypto.subtle.digest('SHA-256', data);
    const hashArray = Array.from(new Uint8Array(hashBuffer));
    return hashArray.map(b => b.toString(16).padStart(2, '0')).join('');
  }

  private detectFormat(path: string): string {
    const extension = path.split('.').pop()?.toLowerCase() || '';
    const formatMap: Record<string, string> = {
      'png': 'image/png',
      'jpg': 'image/jpeg',
      'jpeg': 'image/jpeg',
      'webp': 'image/webp',
      'mp3': 'audio/mpeg',
      'wav': 'audio/wav',
      'ogg': 'audio/ogg',
      'gltf': 'model/gltf+json',
      'glb': 'model/gltf-binary',
      'obj': 'model/obj',
      'json': 'application/json',
      'wgsl': 'text/wgsl',
      'glsl': 'text/glsl'
    };
    return formatMap[extension] || 'application/octet-stream';
  }

  private estimateDataSize(data: any): number {
    if (data instanceof ArrayBuffer) {
      return data.byteLength;
    } else if (data instanceof ImageBitmap) {
      return data.width * data.height * 4; // Assume RGBA
    } else if (typeof data === 'string') {
      return new TextEncoder().encode(data).length;
    } else if (typeof data === 'object') {
      return new TextEncoder().encode(JSON.stringify(data)).length;
    }
    return 1024; // Default estimate
  }

  private generateAssetId(): string {
    return `asset_${++this.assetCounter}_${Date.now()}`;
  }

  private generateBundleId(): string {
    return `bundle_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  private async loadCacheFromStorage(): Promise<void> {
    // Placeholder for cache persistence - would implement with IndexedDB or similar
    try {
      const savedCache = localStorage.getItem('vtt_asset_cache');
      if (savedCache) {
        const _cacheData = JSON.parse(savedCache);
        // Would restore cache data here
      }
    } catch (error) {
      console.warn('Failed to load asset cache from storage:', error);
    }
  }

  private initializeProcessingPipelines(): void {
    // Texture processing pipeline
    this.addProcessingPipeline('texture', _{
      name: 'texture_optimizer',
      _process: async (data: ImageBitmap) => {
        // Placeholder for texture optimization
        return data;
      },
      priority: 10,
      enabled: true
    });

    // Audio processing pipeline
    this.addProcessingPipeline('audio', _{
      name: 'audio_decoder',
      _process: async (data: ArrayBuffer) => {
        // Placeholder for audio decoding
        return data;
      },
      priority: 10,
      enabled: true
    });

    // Model processing pipeline
    this.addProcessingPipeline('model', _{
      name: 'model_parser',
      _process: async (data: ArrayBuffer) => {
        // Placeholder for model parsing
        return data;
      },
      priority: 10,
      enabled: true
    });

    // Data processing pipeline
    this.addProcessingPipeline('data', _{
      name: 'data_validator',
      _process: async (data: any) => {
        // Placeholder for data validation
        return data;
      },
      priority: 10,
      enabled: true
    });
  }
}

/**
 * WebGPU Context Manager - Shared WebGPU context across renderer and physics systems
 * Provides centralized GPU device management and resource sharing
 */

import { EventEmitter } from './EventEmitter';
import { 
  GPUResourceManager, 
  SystemEvent, 
  Disposable 
} from './SharedInterfaces';

export interface WebGPUContextConfig {
  powerPreference: 'low-power' | 'high-performance';
  forceFallbackAdapter: boolean;
  requiredFeatures: GPUFeatureName[];
  requiredLimits: Record<string, number>;
  enableDebug: boolean;
}

export interface WebGPUContextInfo {
  adapter: GPUAdapter;
  device: GPUDevice;
  queue: GPUCommandQueue;
  canvas?: HTMLCanvasElement;
  context?: GPUCanvasContext;
  limits: GPUSupportedLimits;
  features: GPUSupportedFeatures;
}

export interface SharedRenderTarget {
  id: string;
  texture: GPUTexture;
  view: GPUTextureView;
  format: GPUTextureFormat;
  width: number;
  height: number;
  usage: GPUTextureUsageFlags;
  label?: string;
}

export interface SharedBuffer {
  id: string;
  buffer: GPUBuffer;
  size: number;
  usage: GPUBufferUsageFlags;
  label?: string;
  mappedRange?: ArrayBuffer;
}

export interface ComputeJob {
  id: string;
  pipeline: GPUComputePipeline;
  bindGroups: GPUBindGroup[];
  workgroupSize: [number, number, number];
  label?: string;
  priority: number;
}

interface WebGPUContextEvents {
  ready: undefined;
  error: GPUError;
  devicelost: GPUDeviceLostInfo;
}

export class WebGPUContextManager extends EventEmitter<WebGPUContextEvents> implements Disposable {
  private config: WebGPUContextConfig;
  private contextInfo?: WebGPUContextInfo;
  private sharedBuffers = new Map<string, SharedBuffer>();
  private sharedTextures = new Map<string, SharedRenderTarget>();
  private computeJobs = new Map<string, ComputeJob>();
  private frameCallbacks = new Set<(_deltaTime: number) => void>();

  private isInitialized = false;
  private animationFrameId?: number;
  private lastFrameTime = 0;

  constructor(config: Partial<WebGPUContextConfig> = {}) {
    super();

    this.config = {
      powerPreference: 'high-performance',
      forceFallbackAdapter: false,
      requiredFeatures: [],
      requiredLimits: Record<string, any>,
      enableDebug: false,
      ...config
    };
  }

  /**
   * Initialize WebGPU context
   */
  async initialize(): Promise<void> {
    if (this.isInitialized) {
      return;
    }

    try {
      // Check WebGPU support
      if (!navigator.gpu) {
        throw new Error('WebGPU is not supported in this browser');
      }

      // Request adapter
      const adapter = await navigator.gpu.requestAdapter({
        powerPreference: this.config.powerPreference,
        forceFallbackAdapter: this.config.forceFallbackAdapter
      });

      if (!adapter) {
        throw new Error('Failed to get WebGPU adapter');
      }

      // Check required features
      for (const feature of this.config.requiredFeatures) {
        if (!adapter.features.has(feature)) {
          throw new Error(`Required feature '${feature}' is not supported`);
        }
      }

      // Request device
      const device = await adapter.requestDevice({
        requiredFeatures: this.config.requiredFeatures,
        requiredLimits: this.config.requiredLimits
      });

      // Set up error handling
      device.onuncapturederror = (event: GPUUncapturedErrorEvent) => {
        console.error('WebGPU uncaptured error:', event.error);
        this.emit('error', event.error);
      };

      if (this.config.enableDebug) {
        device.pushErrorScope('validation');
        device.pushErrorScope('out-of-memory');
        device.pushErrorScope('internal');
      }

      this.contextInfo = {
        adapter,
        device,
        queue: device.queue,
        limits: adapter.limits,
        features: adapter.features
      };

      this.isInitialized = true;
      this.startRenderLoop();

      this.emit('ready', undefined);

    } catch (error) {
      console.error('Failed to initialize WebGPU context:', error);
      throw error;
    }
  }

  /**
   * Get the WebGPU device
   */
  getDevice(): GPUDevice {
    if (!this.contextInfo) {
      throw new Error('WebGPU context not initialized');
    }
    return this.contextInfo.device;
  }

  /**
   * Get the WebGPU queue
   */
  getQueue(): GPUCommandQueue {
    if (!this.contextInfo) {
      throw new Error('WebGPU context not initialized');
    }
    return this.contextInfo.queue;
  }

  /**
   * Get adapter info
   */
  getAdapterInfo(): { limits: GPUSupportedLimits; features: GPUSupportedFeatures } {
    if (!this.contextInfo) {
      throw new Error('WebGPU context not initialized');
    }
    return {
      limits: this.contextInfo.limits,
      features: this.contextInfo.features
    };
  }

  /**
   * Create canvas context
   */
  createCanvasContext(canvas: HTMLCanvasElement): GPUCanvasContext {
    const device = this.getDevice();
    const context = canvas.getContext('webgpu');

    if (!context) {
      throw new Error('Failed to get WebGPU canvas context');
    }

    const format = navigator.gpu?.getPreferredCanvasFormat?.() || 'bgra8unorm';
    context.configure({
      device,
      format,
      usage: GPUTextureUsage.RENDER_ATTACHMENT | GPUTextureUsage.COPY_SRC,
      alphaMode: 'premultiplied'
    });

    // Store context info
    if (this.contextInfo) {
      this.contextInfo.canvas = canvas;
      this.contextInfo.context = context;
    }

    return context;
  }

  /**
   * Create shared buffer that can be used by multiple systems
   */
  createSharedBuffer(id: string, size: number, usage: GPUBufferUsageFlags, label?: string): SharedBuffer {
    if (this.sharedBuffers.has(id)) {
      throw new Error(`Shared buffer with id '${id}' already exists`);
    }

    const device = this.getDevice();
    const buffer = device.createBuffer({
      size,
      usage,
      label: label || `SharedBuffer_${id}`
    });

    const sharedBuffer: SharedBuffer = {
      id,
      buffer,
      size,
      usage,
      ...(label !== undefined && { label })
    };

    this.sharedBuffers.set(id, sharedBuffer);
    return sharedBuffer;
  }

  /**
   * Get shared buffer by ID
   */
  getSharedBuffer(id: string): SharedBuffer | null {
    return this.sharedBuffers.get(id) || null;
  }

  /**
   * Create shared render target
   */
  createSharedRenderTarget(
    id: string, 
    width: number, 
    height: number, 
    format: GPUTextureFormat = 'rgba8unorm',
    usage: GPUTextureUsageFlags = GPUTextureUsage.RENDER_ATTACHMENT | GPUTextureUsage.TEXTURE_BINDING,
    label?: string
  ): SharedRenderTarget {
    if (this.sharedTextures.has(id)) {
      throw new Error(`Shared render target with id '${id}' already exists`);
    }

    const device = this.getDevice();
    const texture = device.createTexture({
      size: { width, height },
      format,
      usage,
      label: label || `SharedRenderTarget_${id}`
    });

    const view = texture.createView();

    const renderTarget: SharedRenderTarget = {
      id,
      texture,
      view,
      format,
      width,
      height,
      usage,
      ...(label !== undefined && { label })
    };

    this.sharedTextures.set(id, renderTarget);
    return renderTarget;
  }

  /**
   * Get shared render target by ID
   */
  getSharedRenderTarget(id: string): SharedRenderTarget | null {
    return this.sharedTextures.get(id) || null;
  }

  /**
   * Register compute job for scheduling
   */
  registerComputeJob(job: ComputeJob): void {
    this.computeJobs.set(job.id, job);
  }

  /**
   * Execute compute job
   */
  executeComputeJob(jobId: string): void {
    const job = this.computeJobs.get(jobId);
    if (!job) {
      throw new Error(`Compute job '${jobId}' not found`);
    }

    const device = this.getDevice();
    const commandEncoder = device.createCommandEncoder({
      label: `ComputeJob_${jobId}`
    });

    const computePass = commandEncoder.beginComputePass({
      label: job.label || `ComputePass_${jobId}`
    });

    computePass.setPipeline(job.pipeline);

    for (let i = 0; i < job.bindGroups.length; i++) {
      const bindGroup = job.bindGroups[i];
      if (bindGroup) {
        computePass.setBindGroup(i, bindGroup);
      }
    }

    computePass.dispatchWorkgroups(
      job.workgroupSize[0],
      job.workgroupSize[1],
      job.workgroupSize[2]
    );

    computePass.end();

    const commandBuffer = commandEncoder.finish();
    this.getQueue().submit([commandBuffer]);
  }

  /**
   * Add frame callback for systems that need per-frame updates
   */
  addFrameCallback(_callback: (deltaTime: number) => void): () => void {
    this.frameCallbacks.add(callback);

    return () => {
      this.frameCallbacks.delete(callback);
    };
  }

  /**
   * Create command encoder with proper labeling
   */
  createCommandEncoder(label?: string): GPUCommandEncoder {
    const device = this.getDevice();
    return device.createCommandEncoder(label ? { label } : Record<string, any>);
  }

  /**
   * Submit command buffers to queue
   */
  submitCommands(commandBuffers: GPUCommandBuffer[]): void {
    this.getQueue().submit(commandBuffers);
  }

  /**
   * Copy data between shared buffers
   */
  copyBuffer(srcId: string, dstId: string, srcOffset: number = 0, dstOffset: number = 0, size?: number): void {
    const srcBuffer = this.getSharedBuffer(srcId);
    const dstBuffer = this.getSharedBuffer(dstId);

    if (!srcBuffer || !dstBuffer) {
      throw new Error('Source or destination buffer not found');
    }

    const copySize = size || Math.min(srcBuffer.size - srcOffset, dstBuffer.size - dstOffset);

    const commandEncoder = this.createCommandEncoder('BufferCopy');
    commandEncoder.copyBufferToBuffer(
      srcBuffer.buffer, srcOffset,
      dstBuffer.buffer, dstOffset,
      copySize
    );

    this.submitCommands([commandEncoder.finish()]);
  }

  /**
   * Get memory usage statistics
   */
  getMemoryUsage(): { buffers: number; textures: number; total: number } {
    let bufferMemory = 0;
    let textureMemory = 0;

    for (const buffer of this.sharedBuffers.values()) {
      bufferMemory += buffer.size;
    }

    for (const texture of this.sharedTextures.values()) {
      // Rough estimation of texture memory usage
      const bytesPerPixel = this.getBytesPerPixel(texture.format);
      textureMemory += texture.width * texture.height * bytesPerPixel;
    }

    return {
      buffers: bufferMemory,
      textures: textureMemory,
      total: bufferMemory + textureMemory
    };
  }

  /**
   * Cleanup and dispose of resources
   */
  dispose(): void {
    if (this.animationFrameId !== undefined) {
      cancelAnimationFrame(this.animationFrameId);
    }

    // Destroy shared buffers
    for (const buffer of this.sharedBuffers.values()) {
      buffer.buffer.destroy();
    }
    this.sharedBuffers.clear();

    // Destroy shared textures
    for (const texture of this.sharedTextures.values()) {
      texture.texture.destroy();
    }
    this.sharedTextures.clear();

    this.computeJobs.clear();
    this.frameCallbacks.clear();

    // Destroy device
    if (this.contextInfo) {
      this.contextInfo.device.destroy();
    }

    this.isInitialized = false;
    this.removeAllListeners();
  }

  // Private helper methods

  private startRenderLoop(): void {
    const renderFrame = (_currentTime: number) => {
      const deltaTime = currentTime - this.lastFrameTime;
      this.lastFrameTime = currentTime;

      // Execute frame callbacks
      for (const callback of this.frameCallbacks) {
        try {
          callback(deltaTime);
        } catch (error) {
          console.error('Error in frame callback:', error);
        }
      }

      this.animationFrameId = requestAnimationFrame(renderFrame);
    };

    this.animationFrameId = requestAnimationFrame(renderFrame);
  }

  private getBytesPerPixel(format: GPUTextureFormat): number {
    // Simplified calculation - would be more comprehensive in production
    const formatSizes: Record<string, number> = {
      'rgba8unorm': 4,
      'rgba8unorm-srgb': 4,
      'bgra8unorm': 4,
      'bgra8unorm-srgb': 4,
      'rgba16float': 8,
      'rgba32float': 16,
      'depth24plus': 4,
      'depth32float': 4
    };

    return formatSizes[format] || 4;
  }
}

