export class MechanicalEffectsIntegrator {
  private activeIntegrations = new Map<string, Set<string>>(); // entityId -> active effect sources

  /**
   * Apply all mechanical effects to an action/roll
   */
  calculateMechanicalEffects(context: MechanicalEffectContext): MechanicalResult {
    const result: MechanicalResult = {
      finalValue: context.baseModifier || 0,
      advantage: false,
      disadvantage: false,
      modifiers: [],
      resistances: [],
      immunities: [],
      vulnerabilities: [],
      prevented: false,
      additionalEffects: []
    };

    // Apply condition effects
    this.applyConditionEffects(context, result);

    // Apply equipment effects
    this.applyEquipmentEffects(context, result);

    // Apply spell effects
    this.applySpellEffects(context, result);

    // Apply ability effects (for monsters)
    this.applyAbilityEffects(context, result);

    // Calculate final result
    this.calculateFinalResult(context, result);

    return result;
  }

  private applyConditionEffects(context: MechanicalEffectContext, result: MechanicalResult): void {
    const conditionEffects = conditionsEngine.applyConditionEffects(
      context.entityId, 
      context.actionType
    );

    if (conditionEffects.advantage) result.advantage = true;
    if (conditionEffects.disadvantage) result.disadvantage = true;
    if (conditionEffects.prevented) result.prevented = true;

    // Add condition modifiers
    conditionEffects.modifiers.forEach(modifier => {
      result.modifiers.push({
        source: 'condition',
        type: 'modifier',
        value: modifier
      });
    });

    // Get damage modifications from conditions
    const damageModifications = conditionsEngine.getDamageModifications(context.entityId);
    result.resistances.push(...damageModifications.resistances);
    result.immunities.push(...damageModifications.immunities);
    result.vulnerabilities.push(...damageModifications.vulnerabilities);
  }

  private applyEquipmentEffects(context: MechanicalEffectContext, result: MechanicalResult): void {
    // Get equipment bonuses
    const characterData = this.getCharacterData(context.entityId);
    if (!characterData) return;

    const equipmentBonuses = equipmentEffectsEngine.calculateEquipmentBonuses(
      context.entityId, 
      characterData
    );

    // Apply relevant bonuses based on action type
    switch (context.actionType) {
      case 'attack':
        result.modifiers.push({
          source: 'equipment',
          type: 'attack_bonus',
          value: equipmentBonuses.attackBonus
        });
        break;

      case 'damage':
        result.modifiers.push({
          source: 'equipment',
          type: 'damage_bonus',
          value: equipmentBonuses.damageBonus
        });
        break;

      case 'save':
        if (context.subtype && equipmentBonuses.savingThrows[context.subtype]) {
          result.modifiers.push({
            source: 'equipment',
            type: 'save_bonus',
            value: equipmentBonuses.savingThrows[context.subtype]
          });
        }
        break;
    }

    // Apply equipment-triggered effects
    if (context.actionType === 'attack' && context.metadata?.weaponId) {
      const weaponEffects = equipmentEffectsEngine.processWeaponAttack(
        context.entityId,
        context.metadata.weaponId,
        characterData,
        context.target || '',
        context.metadata
      );

      result.modifiers.push({
        source: 'weapon',
        type: 'attack_bonus',
        value: weaponEffects.attackBonus
      });

      weaponEffects.effects.forEach(effect => {
        result.additionalEffects.push({
          type: effect.type,
          value: effect.result,
          source: 'weapon_property'
        });
      });
    }
  }

  private applySpellEffects(context: MechanicalEffectContext, result: MechanicalResult): void {
    const characterData = this.getCharacterData(context.entityId);
    if (!characterData?.concentrationSpell) return;

    const concentrationSpell = characterData.concentrationSpell;

    // Apply ongoing spell effects that might affect rolls
    if (concentrationSpell.spell === 'bless' && context.actionType === 'attack') {
      result.modifiers.push({
        source: 'spell_bless',
        type: 'divine_bonus',
        value: 4 // 1d4 average
      });
    }

    if (concentrationSpell.spell === 'guidance' && context.actionType === 'check') {
      result.modifiers.push({
        source: 'spell_guidance',
        type: 'divine_bonus',
        value: 2 // 1d4 average
      });
    }
  }

  private applyAbilityEffects(context: MechanicalEffectContext, result: MechanicalResult): void {
    // Apply monster ability effects
    const availableAbilities = monsterAbilitiesEngine.getAvailableAbilities(context.entityId);

    // Check for passive abilities that affect actions
    for (const trait of availableAbilities.traits) {
      if (trait.type === 'trait' && this.traitAffectsAction(trait, context)) {
        this.applyTraitEffect(trait, context, result);
      }
    }
  }

  private traitAffectsAction(trait: any, context: MechanicalEffectContext): boolean {
    // Examples of traits that affect actions
    switch (trait.id) {
      case 'pack_tactics':
        return context.actionType === 'attack' && this.hasNearbyAllies(context.entityId);
      case 'keen_senses':
        return context.actionType === 'check' && context.subtype?.includes('perception');
      case 'magic_resistance':
        return context.actionType === 'save' && context.metadata?.isMagical;
      default:
        return false;
    }
  }

  private applyTraitEffect(trait: any, context: MechanicalEffectContext, result: MechanicalResult): void {
    switch (trait.id) {
      case 'pack_tactics':
        result.advantage = true;
        result.additionalEffects.push({
          type: 'pack_tactics',
          value: 'advantage_on_attack',
          source: trait.name
        });
        break;

      case 'keen_senses':
        result.advantage = true;
        result.additionalEffects.push({
          type: 'keen_senses',
          value: 'advantage_on_perception',
          source: trait.name
        });
        break;

      case 'magic_resistance':
        result.advantage = true;
        result.additionalEffects.push({
          type: 'magic_resistance',
          value: 'advantage_on_magic_saves',
          source: trait.name
        });
        break;
    }
  }

  private calculateFinalResult(context: MechanicalEffectContext, result: MechanicalResult): void {
    // Sum all modifiers
    const totalModifier = result.modifiers.reduce((_sum, _mod) => sum + mod.value, 0);
    result.finalValue = (context.baseModifier || 0) + totalModifier;

    // Handle advantage/disadvantage cancellation
    if (result.advantage && result.disadvantage) {
      result.advantage = false;
      result.disadvantage = false;
    }
  }

  /**
   * Process damage with all mechanical effects
   */
  processDamage(
    targetId: string,
    damage: { amount: number; type: string; source: string }
  ): {
    finalDamage: number;
    resistances: string[];
    immunities: string[];
    vulnerabilities: string[];
    effects: Array<{ type: string; value: any; source: string }>;
  } {
    const context: MechanicalEffectContext = {
      entityId: targetId,
      actionType: 'damage',
      subtype: damage.type,
      metadata: { damageSource: damage.source }
    };

    const mechanicalResult = this.calculateMechanicalEffects(context);
    let finalDamage = damage.amount;

    // Apply resistances, immunities, vulnerabilities
    if (mechanicalResult.immunities.includes(damage.type) || 
        mechanicalResult.immunities.includes('all_damage')) {
      finalDamage = 0;
    } else if (mechanicalResult.resistances.includes(damage.type) || 
               mechanicalResult.resistances.includes('all_damage')) {
      finalDamage = Math.floor(finalDamage / 2);
    } else if (mechanicalResult.vulnerabilities.includes(damage.type)) {
      finalDamage = finalDamage * 2;
    }

    // Apply damage modifiers
    const damageModifiers = mechanicalResult.modifiers
      .filter(mod => mod.type.includes('damage'))
      .reduce((_sum, _mod) => sum + mod.value, 0);

    finalDamage = Math.max(0, finalDamage + damageModifiers);

    return {
      finalDamage,
      resistances: mechanicalResult.resistances,
      immunities: mechanicalResult.immunities,
      vulnerabilities: mechanicalResult.vulnerabilities,
      effects: mechanicalResult.additionalEffects
    };
  }

  /**
   * Process healing with mechanical effects
   */
  processHealing(
    targetId: string,
    healing: { amount: number; source: string }
  ): {
    finalHealing: number;
    effects: Array<{ type: string; value: any; source: string }>;
  } {
    const context: MechanicalEffectContext = {
      entityId: targetId,
      actionType: 'healing',
      metadata: { healingSource: healing.source }
    };

    const mechanicalResult = this.calculateMechanicalEffects(context);

    // Apply healing modifiers
    const healingModifiers = mechanicalResult.modifiers
      .filter(mod => mod.type.includes('healing'))
      .reduce((_sum, _mod) => sum + mod.value, 0);

    const finalHealing = Math.max(0, healing.amount + healingModifiers);

    return {
      finalHealing,
      effects: mechanicalResult.additionalEffects
    };
  }

  /**
   * Check if an action is prevented by mechanical effects
   */
  isActionPrevented(entityId: string, actionType: string): boolean {
    const context: MechanicalEffectContext = {
      entityId,
      actionType: actionType as any,
      subtype: actionType
    };

    const result = this.calculateMechanicalEffects(context);
    return result.prevented;
  }

  /**
   * Get all active mechanical effects for an entity
   */
  getActiveMechanicalEffects(entityId: string): {
    conditions: ActiveCondition[];
    equipment: any[];
    spells: any[];
    abilities: any[];
  } {
    return {
      conditions: conditionsEngine.getActiveConditions(entityId),
      equipment: equipmentEffectsEngine.getEquippedItems(entityId),
      spells: [], // Would get from spell system
      abilities: monsterAbilitiesEngine.getAvailableAbilities(entityId).traits
    };
  }

  /**
   * Trigger effects based on events
   */
  triggerEventEffects(
    entityId: string,
    event: string,
    context?: any
  ): Array<{ type: string; effect: any; source: string }> {
    const effects: Array<{ type: string; effect: any; source: string }> = [];

    // Trigger equipment effects
    const equipmentEffects = equipmentEffectsEngine.processTriggers(
      entityId,
      event,
      this.getCharacterData(entityId),
      context
    );

    equipmentEffects.forEach(result => {
      result.effects.forEach(effect => {
        effects.push({
          type: effect.type,
          effect: effect.result,
          source: 'equipment'
        });
      });
    });

    // Trigger monster ability effects
    const abilityEffects = monsterAbilitiesEngine.processTriggers(
      entityId,
      event,
      this.getCharacterData(entityId),
      context
    );

    abilityEffects.forEach(result => {
      result.effects.forEach(effect => {
        effects.push({
          type: effect.type,
          effect: effect.result,
          source: 'monster_ability'
        });
      });
    });

    // Process condition effects at start/end of turn
    if (event === 'start_turn') {
      const conditionEffects = conditionsEngine.processTurnStart(entityId);
      conditionEffects.forEach(effect => {
        effects.push({
          type: effect.type,
          effect: effect,
          source: 'condition'
        });
      });
    } else if (event === 'end_turn') {
      const turnEndResult = conditionsEngine.processTurnEnd(entityId);
      turnEndResult.effects.forEach(effect => {
        effects.push({
          type: effect.type,
          effect: effect,
          source: 'condition'
        });
      });
    }

    return effects;
  }

  /**
   * Sync mechanical effect changes to network
   */
  syncMechanicalChanges(entityId: string, changes: any): void {
    networkSyncEngine.updateEntityState(entityId, changes, 'mechanical_effects');
  }

  private getCharacterData(entityId: string): any {
    // This would integrate with the character system to get full character data
    // For now, return a placeholder
    return {
      id: entityId,
      hitPoints: { current: 100, max: 100 },
      abilities: { STR: 10, DEX: 10, CON: 10, INT: 10, WIS: 10, CHA: 10 },
      level: 1,
      class: 'fighter',
      proficiencyBonus: 2
    };
  }

  private hasNearbyAllies(_entityId: string): boolean {
    // This would check the game state for nearby allies
    // For now, return false as placeholder
    return false;
  }
}

// Integration with combat system
export class CombatMechanicsIntegrator {
  private mechanicalEffects = new MechanicalEffectsIntegrator();

  /**
   * Process a complete attack with all mechanical effects
   */
  processAttack(
    attackerId: string,
    targetId: string,
    attackData: {
      weaponId?: string;
      attackType: 'melee' | 'ranged' | 'spell';
      baseDamage: string;
      damageType: string;
    }
  ): {
    attackRoll: { total: number; advantage: boolean; disadvantage: boolean };
    damage: { total: number; type: string; effects: any[] };
    effects: any[];
  } {
    // Process attack roll
    const attackContext: MechanicalEffectContext = {
      entityId: attackerId,
      actionType: 'attack',
      target: targetId,
      subtype: attackData.attackType,
      metadata: { weaponId: attackData.weaponId }
    };

    const attackEffects = this.mechanicalEffects.calculateMechanicalEffects(attackContext);

    // Process damage
    const damageContext: MechanicalEffectContext = {
      entityId: targetId,
      actionType: 'damage',
      subtype: attackData.damageType,
      baseDice: attackData.baseDamage,
      metadata: { attackerId, weaponId: attackData.weaponId }
    };

    const damageEffects = this.mechanicalEffects.calculateMechanicalEffects(damageContext);

    return {
      attackRoll: {
        total: attackEffects.finalValue,
        advantage: attackEffects.advantage,
        disadvantage: attackEffects.disadvantage
      },
      damage: {
        total: damageEffects.finalValue,
        type: attackData.damageType,
        effects: damageEffects.additionalEffects
      },
      effects: [...attackEffects.additionalEffects, ...damageEffects.additionalEffects]
    };
  }

  /**
   * Process a saving throw with all mechanical effects
   */
  processSavingThrow(
    entityId: string,
    saveType: string,
    dc: number,
    source?: string
  ): {
    total: number;
    success: boolean;
    advantage: boolean;
    disadvantage: boolean;
    effects: any[];
  } {
    const context: MechanicalEffectContext = {
      entityId,
      actionType: 'save',
      subtype: saveType,
      metadata: { dc, source }
    };

    const result = this.mechanicalEffects.calculateMechanicalEffects(context);

    return {
      total: result.finalValue,
      success: result.finalValue >= dc,
      advantage: result.advantage,
      disadvantage: result.disadvantage,
      effects: result.additionalEffects
    };
  }
}

// Export singleton instances
export const _mechanicalEffectsIntegrator = new MechanicalEffectsIntegrator();
export const _combatMechanicsIntegrator = new CombatMechanicsIntegrator();

/**
 * Production alerting system for VTT monitoring
 */

import { EventEmitter } from 'events';
import * as nodemailer from 'nodemailer';
import { MetricsRegistry } from './Metrics';
import { HealthCheckResult, HealthStatus } from './HealthCheck';

export type AlertSeverity = 'info' | 'warning' | 'critical';

export interface Alert {
  id: string;
  name: string;
  severity: AlertSeverity;
  message: string;
  source: string;
  timestamp: Date;
  resolved?: Date;
  metadata?: Record<string, any>;
}

export interface AlertRule {
  name: string;
  description: string;
  condition: AlertCondition;
  severity: AlertSeverity;
  enabled: boolean;
  cooldownMs: number;
  recipients: string[];
}

export interface AlertCondition {
  type: 'metric' | 'health' | 'log' | 'custom';
  evaluate(context: AlertContext): Promise<boolean>;
}

export interface AlertContext {
  metrics: MetricsRegistry;
  healthResults: HealthCheckResult[];
  currentTime: Date;
  metadata?: Record<string, any>;
}

export interface NotificationChannel {
  name: string;
  send(alert: Alert): Promise<boolean>;
}

export interface AlertManagerConfig {
  rules: AlertRule[];
  channels: NotificationChannel[];
  checkInterval: number;
  retentionDays: number;
}

export class AlertManager extends EventEmitter {
  private config: AlertManagerConfig;
  private activeAlerts = new Map<string, Alert>();
  private alertHistory: Alert[] = [];
  private ruleLastTriggered = new Map<string, number>();
  private intervalId: NodeJS.Timeout | undefined = undefined;
  private metricsRegistry: MetricsRegistry;

  constructor(config: AlertManagerConfig, metricsRegistry: MetricsRegistry) {
    super();
    this.config = config;
    this.metricsRegistry = metricsRegistry;
  }

  start(): void {
    if (this.intervalId) return;

    this.intervalId = setInterval(async () => {
      await this.evaluateAlerts();
    }, this.config.checkInterval);

    this.emit('started');
  }

  stop(): void {
    if (this.intervalId) {
      clearInterval(this.intervalId);
      this.intervalId = undefined;
    }
    this.emit('stopped');
  }

  private async evaluateAlerts(): Promise<void> {
    const context: AlertContext = {
      metrics: this.metricsRegistry,
      healthResults: [], // Would be populated from health check manager
      currentTime: new Date()
    };

    for (const rule of this.config.rules) {
      if (!rule.enabled) continue;

      try {
        const shouldAlert = await rule.condition.evaluate(context);

        if (shouldAlert) {
          await this.handleAlertTriggered(rule, context);
        } else {
          await this.handleAlertResolved(rule);
        }
      } catch (error) {
        console.error(`Error evaluating alert rule ${rule.name}:`, error);
        this.emit('ruleError', rule.name, error);
      }
    }

    this.cleanupOldAlerts();
  }

  private async handleAlertTriggered(rule: AlertRule, context: AlertContext): Promise<void> {
    const alertKey = this.getAlertKey(rule);
    const lastTriggered = this.ruleLastTriggered.get(rule.name) || 0;
    const now = Date.now();

    // Check cooldown
    if (now - lastTriggered < rule.cooldownMs) {
      return;
    }

    // Check if alert is already active
    if (this.activeAlerts.has(alertKey)) {
      return;
    }

    const alert: Alert = {
      id: this.generateAlertId(),
      name: rule.name,
      severity: rule.severity,
      message: this.buildAlertMessage(rule, context),
      source: 'alert-manager',
      timestamp: context.currentTime,
      metadata: { rule: rule.name }
    };

    this.activeAlerts.set(alertKey, alert);
    this.alertHistory.push(alert);
    this.ruleLastTriggered.set(rule.name, now);

    await this.sendNotifications(alert, rule.recipients);
    this.emit('alertTriggered', alert);
  }

  private async handleAlertResolved(rule: AlertRule): Promise<void> {
    const alertKey = this.getAlertKey(rule);
    const activeAlert = this.activeAlerts.get(alertKey);

    if (activeAlert && !activeAlert.resolved) {
      activeAlert.resolved = new Date();
      this.activeAlerts.delete(alertKey);

      await this.sendResolutionNotifications(activeAlert, rule.recipients);
      this.emit('alertResolved', activeAlert);
    }
  }

  private async sendNotifications(alert: Alert, _recipients: string[]): Promise<void> {
    const promises = this.config.channels.map(async (channel) => {
      try {
        await channel.send(alert);
        this.emit('notificationSent', alert.id, channel.name);
      } catch (error) {
        console.error(`Failed to send notification via ${channel.name}:`, error);
        this.emit('notificationFailed', alert.id, channel.name, error);
      }
    });

    await Promise.allSettled(promises);
  }

  private async sendResolutionNotifications(alert: Alert, recipients: string[]): Promise<void> {
    const resolutionAlert = {
      ...alert,
      message: `RESOLVED: ${alert.message}`,
      severity: 'info' as AlertSeverity
    };

    await this.sendNotifications(resolutionAlert, recipients);
  }

  private buildAlertMessage(rule: AlertRule, context: AlertContext): string {
    return `Alert: ${rule.description} at ${context.currentTime.toISOString()}`;
  }

  private getAlertKey(rule: AlertRule): string {
    return `${rule.name}`;
  }

  private generateAlertId(): string {
    return `alert_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  private cleanupOldAlerts(): void {
    const cutoff = new Date();
    cutoff.setDate(cutoff.getDate() - this.config.retentionDays);

    this.alertHistory = this.alertHistory.filter(alert => alert.timestamp >= cutoff);
  }

  // Public API methods
  getActiveAlerts(): Alert[] {
    return Array.from(this.activeAlerts.values());
  }

  getAlertHistory(limit = 100): Alert[] {
    return this.alertHistory
      .sort((_a, _b) => b.timestamp.getTime() - a.timestamp.getTime())
      .slice(0, limit);
  }

  acknowledgeAlert(alertId: string, userId: string): boolean {
    for (const alert of this.activeAlerts.values()) {
      if (alert.id === alertId) {
        alert.metadata = {
          ...alert.metadata,
          acknowledgedBy: userId,
          acknowledgedAt: new Date()
        };
        this.emit('alertAcknowledged', alert, userId);
        return true;
      }
    }
    return false;
  }

  silenceAlert(ruleName: string, durationMs: number): void {
    const until = Date.now() + durationMs;
    this.ruleLastTriggered.set(ruleName, until);
    this.emit('alertSilenced', ruleName, durationMs);
  }

  addRule(rule: AlertRule): void {
    this.config.rules.push(rule);
    this.emit('ruleAdded', rule.name);
  }

  updateRule(ruleName: string, updates: Partial<AlertRule>): boolean {
    const ruleIndex = this.config.rules.findIndex(r => r.name === ruleName);
    if (ruleIndex !== -1) {
      const currentRule = this.config.rules[ruleIndex];
      if (!currentRule) return false;

      this.config.rules[ruleIndex] = {
        name: currentRule.name,
        description: currentRule.description,
        condition: currentRule.condition,
        severity: currentRule.severity,
        enabled: currentRule.enabled,
        cooldownMs: currentRule.cooldownMs,
        recipients: currentRule.recipients,
        ...updates
      };
      this.emit('ruleUpdated', ruleName);
      return true;
    }
    return false;
  }

  removeRule(ruleName: string): boolean {
    const ruleIndex = this.config.rules.findIndex(r => r.name === ruleName);
    if (ruleIndex !== -1) {
      this.config.rules.splice(ruleIndex, 1);
      this.emit('ruleRemoved', ruleName);
      return true;
    }
    return false;
  }
}

// Predefined alert conditions
export class MetricThresholdCondition implements AlertCondition {
  type: 'metric' as const = 'metric';

  constructor(
    private metricName: string,
    private threshold: number,
    private operator: '>' | '<' | '>=' | '<=' | '==' | '!=',
    private timeWindowMs?: number
  ) {}

  async evaluate(context: AlertContext): Promise<boolean> {
    const metric = context.metrics.getMetric(this.metricName);
    if (!metric || metric.values.length === 0) {
      return false;
    }

    let values = metric.values;

    if (this.timeWindowMs) {
      const cutoff = new Date(context.currentTime.getTime() - this.timeWindowMs);
      values = values.filter(v => v.timestamp >= cutoff);
    }

    if (values.length === 0) {
      return false;
    }

    const latestValue = values[values.length - 1]?.value;
    if (latestValue === undefined) return false;

    switch (this.operator) {
      case '>': return latestValue > this.threshold;
      case '<': return latestValue < this.threshold;
      case '>=': return latestValue >= this.threshold;
      case '<=': return latestValue <= this.threshold;
      case '==': return latestValue === this.threshold;
      case '!=': return latestValue !== this.threshold;
      default: return false;
    }
  }
}

export class HealthCheckCondition implements AlertCondition {
  type: 'health' as const = 'health';

  constructor(
    private checkName: string,
    private expectedStatus: HealthStatus
  ) {}

  async evaluate(context: AlertContext): Promise<boolean> {
    const healthResult = context.healthResults.find(r => r.name === this.checkName);
    if (!healthResult) {
      return true; // Alert if health check is missing
    }

    return healthResult.status !== this.expectedStatus;
  }
}

export class ErrorRateCondition implements AlertCondition {
  type: 'metric' as const = 'metric';

  constructor(
    private errorMetric: string,
    private totalMetric: string,
    private thresholdPercent: number,
    private timeWindowMs: number = 5 * 60 * 1000 // 5 minutes
  ) {}

  async evaluate(context: AlertContext): Promise<boolean> {
    const errorMetric = context.metrics.getMetric(this.errorMetric);
    const totalMetric = context.metrics.getMetric(this.totalMetric);

    if (!errorMetric || !totalMetric || !errorMetric.values || !totalMetric.values) {
      return false;
    }

    const cutoff = new Date(context.currentTime.getTime() - this.timeWindowMs);

    const errorValues = errorMetric.values.filter(v => v.timestamp >= cutoff);
    const totalValues = totalMetric.values.filter(v => v.timestamp >= cutoff);

    if (errorValues.length === 0 || totalValues.length === 0) {
      return false;
    }

    const errorCount = errorValues[errorValues.length - 1]?.value ?? 0;
    const totalCount = totalValues[totalValues.length - 1]?.value ?? 0;

    if (totalCount === 0) {
      return false;
    }

    const errorRate = (errorCount / totalCount) * 100;
    return errorRate > this.thresholdPercent;
  }
}

// Notification channels
export class EmailNotificationChannel implements NotificationChannel {
  name = 'email';
  private transporter: nodemailer.Transporter;

  constructor(
    private smtpConfig: nodemailer.TransportOptions,
    private fromAddress: string
  ) {
    this.transporter = nodemailer.createTransport(this.smtpConfig);
  }

  async send(alert: Alert): Promise<boolean> {
    try {
      const subject = `[${alert.severity.toUpperCase()}] ${alert.name}`;
      const html = this.buildEmailContent(alert);

      await this.transporter.sendMail({
        from: this.fromAddress,
        to: alert.metadata?.recipients || [],
        subject,
        html
      });

      return true;
    } catch (error) {
      console.error('Failed to send email notification:', error);
      return false;
    }
  }

  private buildEmailContent(alert: Alert): string {
    const severityColor = {
      info: '#2196F3',
      warning: '#FF9800', 
      critical: '#F44336'
    }[alert.severity];

    return `
      <html>
        <body style="font-family: Arial, sans-serif; margin: 0; padding: 20px;">
          <div style="max-width: 600px; margin: 0 auto;">
            <div style="background: ${severityColor}; color: white; padding: 15px; border-radius: 5px 5px 0 0;">
              <h2 style="margin: 0;">${alert.severity.toUpperCase()} Alert</h2>
            </div>
            <div style="border: 1px solid #ddd; border-top: none; padding: 20px; border-radius: 0 0 5px 5px;">
              <h3>${alert.name}</h3>
              <p><strong>Message:</strong> ${alert.message}</p>
              <p><strong>Time:</strong> ${alert.timestamp.toISOString()}</p>
              <p><strong>Source:</strong> ${alert.source}</p>
              ${alert.metadata ? `<p><strong>Details:</strong> ${JSON.stringify(alert.metadata, null, 2)}</p>` : ''}
            </div>
          </div>
        </body>
      </html>
    `;
  }
}

