/**
 * Health check system for monitoring service availability
 */

export type HealthStatus = 'healthy' | 'degraded' | 'unhealthy';

export interface HealthCheckResult {
  name: string;
  status: HealthStatus;
  message?: string;
  duration: number;
  timestamp: Date;
  metadata?: Record<string, any>;
}

export interface HealthCheck {
  name: string;
  description: string;
  timeout: number;
  check(): Promise<HealthCheckResult>;
}

export interface HealthCheckConfig {
  checks: HealthCheck[];
  interval: number;
  timeout: number;
  retries: number;
}

export class HealthCheckManager {
  private config: HealthCheckConfig;
  private results: Map<string, HealthCheckResult> = new Map();
  private intervalId: NodeJS.Timeout | undefined = undefined;
  private isRunning = false;

  constructor(config: HealthCheckConfig) {
    this.config = config;
  }

  start(): void {
    if (this.isRunning) return;

    this.isRunning = true;
    this.runChecks(); // Run immediately

    this.intervalId = setInterval(() => {
      this.runChecks();
    }, this.config.interval);
  }

  stop(): void {
    if (this.intervalId) {
      clearInterval(this.intervalId);
      this.intervalId = undefined;
    }
    this.isRunning = false;
  }

  private async runChecks(): Promise<void> {
    const promises = this.config.checks.map(check => this.runSingleCheck(check));
    await Promise.all(promises);
  }

  private async runSingleCheck(check: HealthCheck): Promise<void> {
    let attempt = 0;
    let lastError: Error | null = null;

    while (attempt < this.config.retries) {
      try {
        const result = await this.executeCheck(check);
        this.results.set(check.name, result);
        return;
      } catch (error) {
        lastError = error as Error;
        attempt++;

        if (attempt < this.config.retries) {
          await this.sleep(1000 * attempt); // Exponential backoff
        }
      }
    }

    // All retries failed
    this.results.set(check.name, {
      name: check.name,
      status: 'unhealthy',
      message: `Check failed after ${this.config.retries} attempts: ${lastError?.message}`,
      duration: 0,
      timestamp: new Date(),
    });
  }

  private async executeCheck(check: HealthCheck): Promise<HealthCheckResult> {
    const startTime = Date.now();

    const timeoutPromise = new Promise<never>((__, __reject) => {
      setTimeout(() => reject(new Error('Health check timeout')), check.timeout);
    });

    try {
      const result = await Promise.race([check.check(), timeoutPromise]);
      return {
        ...result,
        duration: Date.now() - startTime,
      };
    } catch (error) {
      throw error;
    }
  }

  private sleep(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  getResults(): HealthCheckResult[] {
    return Array.from(this.results.values());
  }

  getOverallStatus(): HealthStatus {
    const results = this.getResults();

    if (results.length === 0) return 'unhealthy';

    const unhealthyCount = results.filter(r => r.status === 'unhealthy').length;
    const degradedCount = results.filter(r => r.status === 'degraded').length;

    if (unhealthyCount > 0) return 'unhealthy';
    if (degradedCount > 0) return 'degraded';
    return 'healthy';
  }

  getHealthSummary(): {
    status: HealthStatus;
    checks: HealthCheckResult[];
    timestamp: Date;
  } {
    return {
      status: this.getOverallStatus(),
      checks: this.getResults(),
      timestamp: new Date(),
    };
  }
}

// Database health check
export class DatabaseHealthCheck implements HealthCheck {
  name = 'database';
  description = 'Database connectivity check';
  timeout = 5000;

  private connectionString: string;

  constructor(connectionString: string) {
    this.connectionString = connectionString;
  }

  async check(): Promise<HealthCheckResult> {
    const startTime = Date.now();

    try {
      // Simulate database check
      await this.simulateDBQuery();

      return {
        name: this.name,
        status: 'healthy',
        message: 'Database connection successful',
        duration: Date.now() - startTime,
        timestamp: new Date(),
      };
    } catch (error) {
      return {
        name: this.name,
        status: 'unhealthy',
        message: `Database connection failed: ${(error as Error).message}`,
        duration: Date.now() - startTime,
        timestamp: new Date(),
      };
    }
  }

  private async simulateDBQuery(): Promise<void> {
    // Simulate database query delay
    await new Promise(resolve => setTimeout(resolve, Math.random() * 100));

    // Simulate occasional failures
    if (Math.random() < 0.05) {
      throw new Error('Connection timeout');
    }
  }
}

// Redis health check
export class RedisHealthCheck implements HealthCheck {
  name = 'redis';
  description = 'Redis connectivity check';
  timeout = 3000;

  private host: string;
  private port: number;

  constructor(host: string, port: number) {
    this.host = host;
    this.port = port;
  }

  async check(): Promise<HealthCheckResult> {
    const startTime = Date.now();

    try {
      // Simulate Redis ping
      await this.simulateRedisPing();

      return {
        name: this.name,
        status: 'healthy',
        message: 'Redis connection successful',
        duration: Date.now() - startTime,
        timestamp: new Date(),
        metadata: { host: this.host, port: this.port },
      };
    } catch (error) {
      return {
        name: this.name,
        status: 'unhealthy',
        message: `Redis connection failed: ${(error as Error).message}`,
        duration: Date.now() - startTime,
        timestamp: new Date(),
      };
    }
  }

  private async simulateRedisPing(): Promise<void> {
    await new Promise(resolve => setTimeout(resolve, Math.random() * 50));

    if (Math.random() < 0.02) {
      throw new Error('Redis unavailable');
    }
  }
}

// Memory usage health check
export class MemoryHealthCheck implements HealthCheck {
  name = 'memory';
  description = 'Memory usage check';
  timeout = 1000;

  private warningThreshold: number;
  private criticalThreshold: number;

  constructor(warningThreshold = 0.8, criticalThreshold = 0.95) {
    this.warningThreshold = warningThreshold;
    this.criticalThreshold = criticalThreshold;
  }

  async check(): Promise<HealthCheckResult> {
    const startTime = Date.now();

    try {
      const memoryUsage = this.getMemoryUsage();
      const usageRatio = memoryUsage.used / memoryUsage.total;

      let status: HealthStatus = 'healthy';
      let message = `Memory usage: ${(usageRatio * 100).toFixed(1)}%`;

      if (usageRatio >= this.criticalThreshold) {
        status = 'unhealthy';
        message += ' (Critical)';
      } else if (usageRatio >= this.warningThreshold) {
        status = 'degraded';
        message += ' (Warning)';
      }

      return {
        name: this.name,
        status,
        message,
        duration: Date.now() - startTime,
        timestamp: new Date(),
        metadata: {
          usageRatio,
          usedBytes: memoryUsage.used,
          totalBytes: memoryUsage.total,
        },
      };
    } catch (error) {
      return {
        name: this.name,
        status: 'unhealthy',
        message: `Memory check failed: ${(error as Error).message}`,
        duration: Date.now() - startTime,
        timestamp: new Date(),
      };
    }
  }

  private getMemoryUsage(): { used: number; total: number } {
    if (typeof process !== 'undefined' && process.memoryUsage) {
      const usage = process.memoryUsage();
      return {
        used: usage.heapUsed,
        total: usage.heapTotal,
      };
    }

    // Fallback for browser environment
    return {
      used: 50 * 1024 * 1024, // 50MB
      total: 100 * 1024 * 1024, // 100MB
    };
  }
}

// External service health check
export class ExternalServiceHealthCheck implements HealthCheck {
  name: string;
  description: string;
  timeout = 10000;

  private url: string;
  private expectedStatus: number;

  constructor(name: string, url: string, expectedStatus = 200) {
    this.name = name;
    this.description = `External service check: ${name}`;
    this.url = url;
    this.expectedStatus = expectedStatus;
  }

  async check(): Promise<HealthCheckResult> {
    const startTime = Date.now();

    try {
      const response = await fetch(this.url, {
        method: 'GET',
        signal: AbortSignal.timeout(this.timeout),
      });

      const duration = Date.now() - startTime;

      if (response.status === this.expectedStatus) {
        return {
          name: this.name,
          status: 'healthy',
          message: `Service responded with status ${response.status}`,
          duration,
          timestamp: new Date(),
          metadata: { statusCode: response.status, responseTime: duration },
        };
      } else {
        return {
          name: this.name,
          status: 'degraded',
          message: `Service responded with unexpected status ${response.status}`,
          duration,
          timestamp: new Date(),
          metadata: { statusCode: response.status, responseTime: duration },
        };
      }
    } catch (error) {
      return {
        name: this.name,
        status: 'unhealthy',
        message: `Service check failed: ${(error as Error).message}`,
        duration: Date.now() - startTime,
        timestamp: new Date(),
      };
    }
  }
}

/**
 * Central observability manager coordinating logging, metrics, health checks, and alerting
 */

import { EventEmitter } from 'events';
import { Logger, LoggerConfig } from './Logger';
import { MetricsRegistry, SystemMetricsCollector, GameMetricsCollector } from './Metrics';
import { HealthCheckManager, HealthCheckConfig } from './HealthCheck';
import { AlertManager, AlertManagerConfig } from './AlertManager';

export interface ObservabilityConfig {
  logging: LoggerConfig;
  healthChecks: HealthCheckConfig;
  alerts: AlertManagerConfig;
  enableMetrics: boolean;
  enableTracing: boolean;
  dashboardPort?: number;
}

export interface TracingSpan {
  traceId: string;
  spanId: string;
  parentSpanId?: string | undefined;
  operationName: string;
  startTime: Date;
  endTime?: Date;
  duration?: number;
  tags: Record<string, any>;
  logs: Array<{ timestamp: Date; message: string; level: string }>;
}

export class ObservabilityManager extends EventEmitter {
  private config: ObservabilityConfig;
  private logger!: Logger;
  private metrics!: MetricsRegistry;
  private healthManager!: HealthCheckManager;
  private alertManager!: AlertManager;
  private activeSpans = new Map<string, TracingSpan>();
  private dashboardServer?: any;

  constructor(config: ObservabilityConfig) {
    super();
    this.config = config;
    this.initialize();
  }

  private initialize(): void {
    // Initialize logger
    this.logger = new Logger(this.config.logging);

    // Initialize metrics
    this.metrics = MetricsRegistry.getInstance();
    if (this.config.enableMetrics) {
      this.setupMetricsCollectors();
    }

    // Initialize health checks
    this.healthManager = new HealthCheckManager(this.config.healthChecks);
    this.setupHealthCheckListeners();

    // Initialize alert manager
    this.alertManager = new AlertManager(this.config.alerts, this.metrics);
    this.setupAlertListeners();

    // Setup cross-component integration
    this.setupIntegration();
  }

  private setupMetricsCollectors(): void {
    const systemCollector = new SystemMetricsCollector();
    const gameCollector = new GameMetricsCollector();

    this.metrics.registerCollector(systemCollector);
    this.metrics.registerCollector(gameCollector);

    // Start collecting system metrics periodically
    setInterval(async () => {
      try {
        await systemCollector.collect();
      } catch (error) {
        this.logger.error('Failed to collect system metrics', error as Error);
      }
    }, 30000); // Every 30 seconds
  }

  private setupHealthCheckListeners(): void {
    // Health check manager doesn't extend EventEmitter in current implementation
    // This would be implemented when HealthCheckManager extends EventEmitter
  }

  private setupAlertListeners(): void {
    this.alertManager.on('alertTriggered', (alert) => {
      this.logger.warn(`Alert triggered: ${alert.name}`, { alert });
      this.metrics.incrementCounter('vtt_alerts_total', 1, {
        severity: alert.severity,
        name: alert.name
      });
    });

    this.alertManager.on('alertResolved', (alert) => {
      this.logger.info(`Alert resolved: ${alert.name}`, { alert });
    });
  }

  private setupIntegration(): void {
    // Integration would be implemented when components extend EventEmitter
    // For now, components communicate through direct method calls
  }

  // Public API
  async start(): Promise<void> {
    this.logger.info('Starting observability manager');

    // Start health checks
    this.healthManager.start();

    // Start alert manager
    this.alertManager.start();

    // Start dashboard if configured
    if (this.config.dashboardPort) {
      await this.startDashboard();
    }

    this.emit('started');
    this.logger.info('Observability manager started successfully');
  }

  async stop(): Promise<void> {
    this.logger.info('Stopping observability manager');

    // Stop components
    this.healthManager.stop();
    this.alertManager.stop();

    if (this.dashboardServer) {
      await this.stopDashboard();
    }

    await this.logger.close();

    this.emit('stopped');
  }

  // Logging interface
  getLogger(context?: Record<string, any>): Logger {
    return context ? this.logger.child(context) : this.logger;
  }

  // Metrics interface  
  getMetrics(): MetricsRegistry {
    return this.metrics;
  }

  recordUserAction(userId: string, action: string, metadata?: Record<string, any>): void {
    this.logger.logUserAction(userId, action, metadata);
    this.metrics.incrementCounter('vtt_user_actions_total', 1, {
      action,
      user_id: userId
    });
  }

  recordGameEvent(gameId: string, event: string, metadata?: Record<string, any>): void {
    this.logger.logGameEvent(gameId, event, metadata);
    this.metrics.incrementCounter('vtt_game_events_total', 1, {
      event,
      game_id: gameId
    });
  }

  recordAPIRequest(method: string, path: string, statusCode: number, duration: number): void {
    this.logger.logAPIRequest(method, path, statusCode, duration);
    this.metrics.incrementCounter('vtt_api_requests_total', 1, {
      method,
      path,
      status_code: statusCode.toString()
    });
    this.metrics.recordHistogram('vtt_api_request_duration_ms', duration, {
      method,
      path
    });
  }

  recordSecurityEvent(event: string, severity: 'low' | 'medium' | 'high', metadata?: Record<string, any>): void {
    this.logger.logSecurity(event, severity, metadata);
    this.metrics.incrementCounter('vtt_security_events_total', 1, {
      event,
      severity
    });
  }

  // Distributed tracing interface
  startSpan(operationName: string, parentSpan?: TracingSpan): TracingSpan {
    if (!this.config.enableTracing) {
      // Return a no-op span
      return {
        traceId: 'disabled',
        spanId: 'disabled',
        operationName,
        startTime: new Date(),
        tags: Record<string, any>,
        logs: []
      };
    }

    const span: TracingSpan = {
      traceId: parentSpan?.traceId || this.generateTraceId(),
      spanId: this.generateSpanId(),
      parentSpanId: parentSpan?.spanId || undefined,
      operationName,
      startTime: new Date(),
      tags: Record<string, any>,
      logs: []
    };

    this.activeSpans.set(span.spanId, span);
    return span;
  }

  finishSpan(span: TracingSpan, tags?: Record<string, any>): void {
    if (!this.config.enableTracing || span.spanId === 'disabled') {
      return;
    }

    span.endTime = new Date();
    span.duration = span.endTime.getTime() - span.startTime.getTime();

    if (tags) {
      span.tags = { ...span.tags, ...tags };
    }

    // Log span completion
    this.logger.info(`Span completed: ${span.operationName}`, {
      traceId: span.traceId,
      spanId: span.spanId,
      duration: span.duration,
      tags: span.tags
    });

    // Record span metrics
    this.metrics.recordHistogram('vtt_span_duration_ms', span.duration, {
      operation: span.operationName
    });

    this.activeSpans.delete(span.spanId);
  }

  addSpanLog(span: TracingSpan, message: string, level: string = 'info'): void {
    if (!this.config.enableTracing || span.spanId === 'disabled') {
      return;
    }

    span.logs.push({
      timestamp: new Date(),
      message,
      level
    });
  }

  // Performance monitoring utilities
  async measureAsync<T>(
    _operationName: string,
    _operation: () => Promise<T>,
    context?: Record<string, any>
  ): Promise<T> {
    const span = this.startSpan(operationName);
    const startTime = Date.now();

    try {
      const result = await operation();
      const duration = Date.now() - startTime;

      this.finishSpan(span, { success: true, ...context });
      this.recordPerformance(operationName, duration, { success: true, ...context });

      return result;
    } catch (error) {
      const duration = Date.now() - startTime;

      const errorMsg = error instanceof Error ? error.message : String(error);
      this.finishSpan(span, { success: false, error: errorMsg, ...context });
      this.recordPerformance(operationName, duration, { success: false, ...context });
      this.logger.error(`Operation failed: ${operationName}`, error as Error, context);

      throw error;
    }
  }

  measure<T>(
    _operationName: string,
    _operation: () => T,
    context?: Record<string, any>
  ): T {
    const span = this.startSpan(operationName);
    const startTime = Date.now();

    try {
      const result = operation();
      const duration = Date.now() - startTime;

      this.finishSpan(span, { success: true, ...context });
      this.recordPerformance(operationName, duration, { success: true, ...context });

      return result;
    } catch (error) {
      const duration = Date.now() - startTime;

      const errorMsg = error instanceof Error ? error.message : String(error);
      this.finishSpan(span, { success: false, error: errorMsg, ...context });
      this.recordPerformance(operationName, duration, { success: false, ...context });
      this.logger.error(`Operation failed: ${operationName}`, error as Error, context);

      throw error;
    }
  }

  private recordPerformance(operation: string, duration: number, context?: Record<string, any>): void {
    this.logger.logPerformance(operation, duration, context);
    this.metrics.recordHistogram('vtt_operation_duration_ms', duration, {
      operation,
      ...context
    });
  }

  // Health and alerting interface
  getHealthStatus(): any {
    return this.healthManager.getHealthSummary();
  }

  getActiveAlerts(): any[] {
    return this.alertManager.getActiveAlerts();
  }

  acknowledgeAlert(alertId: string, userId: string): boolean {
    return this.alertManager.acknowledgeAlert(alertId, userId);
  }

  // Dashboard management
  private async startDashboard(): Promise<void> {
    if (!this.config.dashboardPort) return;

    // In a real implementation, this would start an HTTP server
    // serving a monitoring dashboard with metrics, logs, and alerts
    this.logger.info(`Dashboard would be available at http://localhost:${this.config.dashboardPort}`);

    // Simulate dashboard server
    this.dashboardServer = {
      port: this.config.dashboardPort,
      close: () => Promise.resolve()
    };
  }

  private async stopDashboard(): Promise<void> {
    if (this.dashboardServer) {
      await this.dashboardServer.close();
      this.dashboardServer = undefined;
    }
  }

  // Utility methods
  private generateTraceId(): string {
    return Date.now().toString(36) + Math.random().toString(36).substr(2);
  }

  private generateSpanId(): string {
    return Math.random().toString(36).substr(2, 9);
  }

  // Export observability data
  async exportMetrics(format: 'prometheus' | 'json' = 'json'): Promise<string> {
    const metrics = await this.metrics.getAllMetrics();

    if (format === 'prometheus') {
      return this.formatMetricsAsPrometheus(metrics);
    }

    return JSON.stringify(metrics, null, 2);
  }

  private formatMetricsAsPrometheus(metrics: any[]): string {
    let output = '';

    for (const metric of metrics) {
      output += `# HELP ${metric.name} ${metric.description}\n`;
      output += `# TYPE ${metric.name} ${metric.type}\n`;

      for (const value of metric.values) {
        const labels = value.labels ? 
          Object.entries(value.labels).map([k, _v] => `${k}="${v}"`).join(',') : '';
        const labelStr = labels ? `{${labels}}` : '';

        output += `${metric.name}${labelStr} ${value.value} ${value.timestamp.getTime()}\n`;
      }
      output += '\n';
    }

    return output;
  }

  async exportLogs(_limit: number = 1000, level?: string): Promise<any[]> {
    // In a real implementation, this would query the log storage
    return [];
  }

  async exportTraces(traceId?: string, limit: number = 100): Promise<TracingSpan[]> {
    if (traceId) {
      return Array.from(this.activeSpans.values()).filter(span => span.traceId === traceId);
    }

    return Array.from(this.activeSpans.values()).slice(0, limit);
  }

  // System information
  getSystemInfo(): Record<string, any> {
    return {
      observability: {
        logging: {
          level: this.config.logging.level,
          transports: this.config.logging.transports.length
        },
        metrics: {
          enabled: this.config.enableMetrics,
          activeMetrics: this.metrics ? Array.from((this.metrics as any).metrics?.size || 0) : 0
        },
        tracing: {
          enabled: this.config.enableTracing,
          activeSpans: this.activeSpans.size
        },
        healthChecks: {
          count: this.config.healthChecks.checks.length,
          interval: this.config.healthChecks.interval
        },
        alerts: {
          rules: this.config.alerts.rules.length,
          activeAlerts: this.alertManager.getActiveAlerts().length
        }
      },
      runtime: {
        nodeVersion: process.version || 'unknown',
        platform: process.platform || 'unknown',
        uptime: process.uptime ? process.uptime() : 0,
        memory: process.memoryUsage ? process.memoryUsage() : Record<string, any>
      }
    };
  }
}

/**
 * Centralized logging system with structured logging and multiple transports
 */

export type LogLevel = 'debug' | 'info' | 'warn' | 'error' | 'fatal';

export interface LogEntry {
  timestamp: Date;
  level: LogLevel;
  message: string;
  context?: Record<string, any>;
  error?: Error;
  userId?: string;
  sessionId?: string;
  gameId?: string;
  component?: string;
  action?: string;
  duration?: number;
  metadata?: Record<string, any>;
}

export interface LogTransport {
  name: string;
  log(entry: LogEntry): Promise<void>;
  close(): Promise<void>;
}

export interface LoggerConfig {
  level: LogLevel;
  transports: LogTransport[];
  enableConsole: boolean;
  enableStructuredLogging: boolean;
  defaultContext?: Record<string, any>;
}

export class Logger {
  private config: LoggerConfig;
  private static instance: Logger;

  constructor(config: LoggerConfig) {
    this.config = config;
  }

  static getInstance(config?: LoggerConfig): Logger {
    if (!Logger.instance && config) {
      Logger.instance = new Logger(config);
    }
    return Logger.instance;
  }

  private shouldLog(level: LogLevel): boolean {
    const levels: LogLevel[] = ['debug', 'info', 'warn', 'error', 'fatal'];
    const configLevelIndex = levels.indexOf(this.config.level);
    const messageLevelIndex = levels.indexOf(level);
    return messageLevelIndex >= configLevelIndex;
  }

  private async writeLog(entry: LogEntry): Promise<void> {
    if (!this.shouldLog(entry.level)) {
      return;
    }

    // Console logging
    if (this.config.enableConsole) {
      this.logToConsole(entry);
    }

    // Transport logging
    const promises = this.config.transports.map(transport => 
      transport.log(entry).catch(error => 
        console.error(`Transport ${transport.name} failed:`, error)
      )
    );

    await Promise.all(promises);
  }

  private logToConsole(entry: LogEntry): void {
    const timestamp = entry.timestamp.toISOString();
    const level = entry.level.toUpperCase().padEnd(5);
    const message = entry.message;

    let logMessage = `[${timestamp}] ${level} ${message}`;

    if (entry.context && Object.keys(entry.context).length > 0) {
      logMessage += ` | Context: ${JSON.stringify(entry.context)}`;
    }

    if (entry.error) {
      logMessage += ` | Error: ${entry.error.message}`;
    }

    const logMethod = this.getConsoleMethod(entry.level);
    logMethod(logMessage);

    if (entry.error && entry.error.stack) {
      console.error(entry.error.stack);
    }
  }

  private getConsoleMethod(level: LogLevel): (_...args: any[]) => void {
    switch (level) {
      case 'debug':
        return console.debug;
      case 'info':
        return console.info;
      case 'warn':
        return console.warn;
      case 'error':
      case 'fatal':
        return console.error;
      default:
        return console.log;
    }
  }

  debug(message: string, context?: Record<string, any>): void {
    this.writeLog({
      timestamp: new Date(),
      level: 'debug',
      message,
      context: { ...this.config.defaultContext, ...context },
    });
  }

  info(message: string, context?: Record<string, any>): void {
    this.writeLog({
      timestamp: new Date(),
      level: 'info',
      message,
      context: { ...this.config.defaultContext, ...context },
    });
  }

  warn(message: string, context?: Record<string, any>): void {
    this.writeLog({
      timestamp: new Date(),
      level: 'warn',
      message,
      context: { ...this.config.defaultContext, ...context },
    });
  }

  error(message: string, error?: Error, context?: Record<string, any>): void {
    const logEntry: LogEntry = {
      timestamp: new Date(),
      level: 'error',
      message,
      context: { ...this.config.defaultContext, ...(context || {}) },
    };
    if (error !== undefined) {
      logEntry.error = error;
    }
    this.writeLog(logEntry);
  }

  fatal(message: string, error?: Error, context?: Record<string, any>): void {
    const logEntry: LogEntry = {
      timestamp: new Date(),
      level: 'fatal',
      message,
      context: { ...this.config.defaultContext, ...(context || {}) },
    };
    if (error !== undefined) {
      logEntry.error = error;
    }
    this.writeLog(logEntry);
  }

  // Structured logging methods
  logUserAction(userId: string, action: string, context?: Record<string, any>): void {
    this.info(`User action: ${action}`, {
      ...context,
      userId,
      action,
      component: 'user-action',
    });
  }

  logGameEvent(gameId: string, event: string, context?: Record<string, any>): void {
    this.info(`Game event: ${event}`, {
      ...context,
      gameId,
      event,
      component: 'game-engine',
    });
  }

  logPerformance(operation: string, duration: number, context?: Record<string, any>): void {
    this.info(`Performance: ${operation}`, {
      ...context,
      operation,
      duration,
      component: 'performance',
    });
  }

  logSecurity(event: string, severity: 'low' | 'medium' | 'high', context?: Record<string, any>): void {
    const level = severity === 'high' ? 'error' : severity === 'medium' ? 'warn' : 'info';
    this.writeLog({
      timestamp: new Date(),
      level,
      message: `Security event: ${event}`,
      context: {
        ...this.config.defaultContext,
        ...context,
        securityEvent: event,
        severity,
        component: 'security',
      },
    });
  }

  logAPIRequest(method: string, path: string, statusCode: number, duration: number, context?: Record<string, any>): void {
    const level = statusCode >= 500 ? 'error' : statusCode >= 400 ? 'warn' : 'info';
    this.writeLog({
      timestamp: new Date(),
      level,
      message: `API ${method} ${path} - ${statusCode}`,
      context: {
        ...this.config.defaultContext,
        ...context,
        method,
        path,
        statusCode,
        duration,
        component: 'api',
      },
    });
  }

  // Create child logger with additional context
  child(additionalContext: Record<string, any>): Logger {
    return new Logger({
      ...this.config,
      defaultContext: {
        ...this.config.defaultContext,
        ...additionalContext,
      },
    });
  }

  async close(): Promise<void> {
    const promises = this.config.transports.map(transport => transport.close());
    await Promise.all(promises);
  }
}

// File transport for logging to files
export class FileTransport implements LogTransport {
  name = 'file';
  private filePath: string;
  private maxFileSize: number;
  private maxFiles: number;

  constructor(filePath: string, maxFileSize = 10 * 1024 * 1024, maxFiles = 5) {
    this.filePath = filePath;
    this.maxFileSize = maxFileSize;
    this.maxFiles = maxFiles;
  }

  async log(entry: LogEntry): Promise<void> {
    const logLine = JSON.stringify({
      timestamp: entry.timestamp.toISOString(),
      level: entry.level,
      message: entry.message,
      ...entry.context,
      ...(entry.error && {
        error: {
          message: entry.error.message,
          stack: entry.error.stack,
        },
      }),
    }) + '\n';

    // In a real implementation, you would write to file system
    // For now, we'll just simulate the interface
    console.log(`[FILE LOG] ${logLine.trim()}`);
  }

  async close(): Promise<void> {
    // Close file handles
  }
}

// HTTP transport for sending logs to external services
export class HTTPTransport implements LogTransport {
  name = 'http';
  private endpoint: string;
  private headers: Record<string, string>;
  private batchSize: number;
  private flushInterval: number;
  private batch: LogEntry[] = [];
  private flushTimer?: NodeJS.Timeout;

  constructor(
    endpoint: string,
    headers: Record<string, string> = {},
    batchSize = 100,
    flushInterval = 5000
  ) {
    this.endpoint = endpoint;
    this.headers = headers;
    this.batchSize = batchSize;
    this.flushInterval = flushInterval;
    this.startFlushTimer();
  }

  async log(entry: LogEntry): Promise<void> {
    this.batch.push(entry);

    if (this.batch.length >= this.batchSize) {
      await this.flush();
    }
  }

  private async flush(): Promise<void> {
    if (this.batch.length === 0) return;

    const logsToSend = [...this.batch];
    this.batch = [];

    try {
      const response = await fetch(this.endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          ...this.headers,
        },
        body: JSON.stringify({ logs: logsToSend }),
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }
    } catch (error) {
      console.error('Failed to send logs to HTTP endpoint:', error);
      // Re-add logs to batch for retry (in a real implementation)
    }
  }

  private startFlushTimer(): void {
    this.flushTimer = setInterval(() => {
      this.flush().catch(console.error);
    }, this.flushInterval);
  }

  async close(): Promise<void> {
    if (this.flushTimer) {
      clearInterval(this.flushTimer);
    }
    await this.flush();
  }
}

/**
 * Monster-Specific AI Personalities and Advanced Behaviors
 * Extends the base AI system with D&D 5e monster-specific intelligence
 */

import { AIEntity, NPCPersonality, _NPCGoal, GameStateSnapshot, AIAction } from '@vtt/ai/src/npc/AIEntity';
import { monsterAbilitiesEngine, MonsterTrait } from '@vtt/monster-abilities';
import { socialEngine } from '@vtt/social-engine';

export interface MonsterAIProfile {
  monsterId: string;
  creatureType: 'aberration' | 'beast' | 'celestial' | 'construct' | 'dragon' | 
                'elemental' | 'fey' | 'fiend' | 'giant' | 'humanoid' | 
                'monstrosity' | 'ooze' | 'plant' | 'undead';
  intelligence: 'mindless' | 'animal' | 'low' | 'average' | 'high' | 'genius';
  alignment: string;
  personalityTraits: MonsterPersonalityTraits;
  tacticalPreferences: TacticalPreferences;
  specialBehaviors: SpecialBehavior[];
  environmentalFactors: EnvironmentalFactors;
}

export interface MonsterPersonalityTraits {
  aggression: number; // 0-1, how likely to attack
  cunning: number; // 0-1, tactical intelligence
  territorial: number; // 0-1, defends area
  packMentality: number; // 0-1, works with others
  selfPreservation: number; // 0-1, retreats when injured
  curiosity: number; // 0-1, investigates unknown
  patience: number; // 0-1, waits for opportunities
  vindictive: number; // 0-1, holds grudges
}

export interface TacticalPreferences {
  preferredRange: 'melee' | 'ranged' | 'mixed';
  fightingStyle: 'aggressive' | 'defensive' | 'hit_and_run' | 'ambush' | 'support';
  targetPriority: Array<'weakest' | 'strongest' | 'spellcaster' | 'healer' | 'nearest' | 'leader'>;
  retreatThreshold: number; // HP percentage when likely to retreat
  usesTerrain: boolean;
  coordinatesWithAllies: boolean;
}

export interface SpecialBehavior {
  id: string;
  name: string;
  trigger: 'combat_start' | 'bloodied' | 'ally_dies' | 'outnumbered' | 'spell_cast_nearby' | 'custom';
  condition?: string;
  action: 'use_ability' | 'change_tactics' | 'call_for_help' | 'retreat' | 'berserk';
  parameters?: any;
}

export interface EnvironmentalFactors {
  preferredTerrain: string[];
  lightSensitivity: 'none' | 'sunlight_sensitivity' | 'prefers_darkness';
  territorialRadius: number; // distance from home they'll pursue
  homeTerrain?: { x: number; y: number; radius: number };
}

export class MonsterAI extends AIEntity {
  private monsterProfile: MonsterAIProfile;
  private availableAbilities: MonsterTrait[] = [];
  private combatState: {
    isBloodied: boolean;
    hasUsedSpecialAbility: boolean;
    roundsInCombat: number;
    lastTargetId?: string;
    grudgeTargets: Set<string>;
  };

  constructor(
    id: string,
    monsterProfile: MonsterAIProfile,
    abilities: MonsterTrait[] = []
  ) {
    // Convert monster personality to base NPCPersonality
    const basePersonality: NPCPersonality = {
      aggression: monsterProfile.personalityTraits.aggression,
      intelligence: MonsterAI.intelligenceToNumeric(monsterProfile.intelligence),
      caution: monsterProfile.personalityTraits.selfPreservation,
      loyalty: monsterProfile.personalityTraits.packMentality
    };

    super(id, basePersonality, [], 500); // Think every 500ms for monsters

    this.monsterProfile = monsterProfile;
    this.availableAbilities = abilities;
    this.combatState = {
      isBloodied: false,
      hasUsedSpecialAbility: false,
      roundsInCombat: 0,
      grudgeTargets: new Set()
    };

    this.initializeMonsterBehaviors();
  }

  private static intelligenceToNumeric(intelligence: MonsterAIProfile['intelligence']): number {
    const mapping = {
      'mindless': 0.1,
      'animal': 0.3,
      'low': 0.4,
      'average': 0.5,
      'high': 0.7,
      'genius': 0.9
    };
    return mapping[intelligence];
  }

  private initializeMonsterBehaviors(): void {
    // Initialize monster abilities engine
    monsterAbilitiesEngine.initializeMonster(
      this.entityId,
      this.availableAbilities,
      this.getLegendaryActionsPerTurn(),
      this.getLairActions()
    );

    // Initialize social engine for relationship tracking
    socialEngine.initializeEntity(this.entityId, this.getReactions());
  }

  /**
   * Enhanced monster decision making
   */
  protected selectAction(gameState: GameStateSnapshot): AIAction | null {
    this.updateCombatState(gameState);

    // Check for special behavior triggers
    const specialAction = this.checkSpecialBehaviors(gameState);
    if (specialAction) return specialAction;

    // Use monster-specific tactical AI
    return this.selectTacticalAction(gameState);
  }

  private updateCombatState(gameState: GameStateSnapshot): void {
    // Update bloodied status
    const wasBloodied = this.combatState.isBloodied;
    this.combatState.isBloodied = gameState.healthPercentage <= 0.5;

    // Trigger bloodied behavior if just became bloodied
    if (!wasBloodied && this.combatState.isBloodied) {
      this.processSpecialBehavior('bloodied', gameState);
    }

    // Track combat rounds
    if (gameState.nearbyEnemies.length > 0) {
      this.combatState.roundsInCombat++;
    }
  }

  private selectTacticalAction(gameState: GameStateSnapshot): AIAction | null {
    const _preferences = this.monsterProfile.tacticalPreferences;

    // Check retreat conditions
    if (this.shouldRetreat(gameState)) {
      return this.createRetreatAction(gameState);
    }

    // Select target based on priorities
    const target = this.selectTarget(gameState);
    if (!target) return this.createPatrolAction();

    // Choose action based on fighting style and available abilities
    return this.selectCombatAction(target, gameState);
  }

  private shouldRetreat(gameState: GameStateSnapshot): boolean {
    const traits = this.monsterProfile.personalityTraits;

    // Check HP threshold
    if (gameState.healthPercentage <= this.monsterProfile.tacticalPreferences.retreatThreshold) {
      return traits.selfPreservation > 0.5;
    }

    // Check if severely outnumbered
    const enemyCount = gameState.nearbyEnemies.length;
    const allyCount = gameState.nearbyAllies.length;
    if (enemyCount > allyCount + 2 && traits.selfPreservation > 0.7) {
      return true;
    }

    return false;
  }

  private selectTarget(gameState: GameStateSnapshot): any {
    const enemies = gameState.nearbyEnemies;
    if (enemies.length === 0) return null;

    const priorities = this.monsterProfile.tacticalPreferences.targetPriority;

    for (const priority of priorities) {
      const target = this.findTargetByPriority(enemies, priority);
      if (target) return target;
    }

    return enemies[0]; // Fallback to first enemy
  }

  private findTargetByPriority(enemies: any[], priority: string): any {
    switch (priority) {
      case 'weakest':
        return enemies.reduce((_min, _enemy) => 
          enemy.health < min.health ? enemy : min
        );

      case 'strongest':
        return enemies.reduce((_max, _enemy) => 
          enemy.health > max.health ? enemy : max
        );

      case 'nearest':
        return enemies.reduce((_nearest, _enemy) => 
          enemy.distance < nearest.distance ? enemy : nearest
        );

      case 'spellcaster':
        // Would need additional data to identify spellcasters
        return enemies.find(e => e.class === 'wizard' || e.class === 'sorcerer');

      default:
        return null;
    }
  }

  private selectCombatAction(target: any, gameState: GameStateSnapshot): AIAction {
    const fightingStyle = this.monsterProfile.tacticalPreferences.fightingStyle;

    // Check for special ability usage
    const specialAbility = this.selectSpecialAbility(target, gameState);
    if (specialAbility) return specialAbility;

    // Select action based on fighting style
    switch (fightingStyle) {
      case 'aggressive':
        return this.createAggressiveAction(target);

      case 'defensive':
        return this.createDefensiveAction(target);

      case 'hit_and_run':
        return this.createHitAndRunAction(target, gameState);

      case 'ambush':
        return this.createAmbushAction(target, gameState);

      case 'support':
        return this.createSupportAction(gameState);

      default:
        return this.createBasicAttackAction(target);
    }
  }

  private selectSpecialAbility(target: any, gameState: GameStateSnapshot): AIAction | null {
    const availableAbilities = monsterAbilitiesEngine.getAvailableAbilities(this.entityId);

    // Prioritize recharged abilities
    for (const ability of availableAbilities.traits) {
      if (this.shouldUseAbility(ability, target, gameState)) {
        return {
          type: 'attack',
          targetId: target.id,
          priority: 9,
          data: { 
            abilityId: ability.id,
            abilityType: 'special'
          }
        };
      }
    }

    // Check legendary actions if available
    if (availableAbilities.legendaryActionsRemaining > 0) {
      const legendaryAction = this.selectLegendaryAction(availableAbilities.legendary, target);
      if (legendaryAction) return legendaryAction;
    }

    return null;
  }

  private shouldUseAbility(ability: MonsterTrait, target: any, gameState: GameStateSnapshot): boolean {
    // Intelligent monsters use abilities more strategically
    const intelligence = this.monsterProfile.personalityTraits.cunning;

    // Always use area abilities when multiple enemies present
    if (ability.area && gameState.nearbyEnemies.length >= 2) {
      return Math.random() < 0.8;
    }

    // Use powerful single-target abilities on strong enemies
    if (ability.mechanicalEffect?.damage && target.health > 50) {
      return Math.random() < intelligence;
    }

    // Save special abilities for when bloodied if intelligent
    if (intelligence > 0.6 && !this.combatState.isBloodied) {
      return Math.random() < 0.3;
    }

    return Math.random() < 0.5;
  }

  private createAggressiveAction(target: any): AIAction {
    return {
      type: 'attack',
      targetId: target.id,
      priority: 8,
      data: { attackType: 'aggressive', modifiers: { damage: 2 } }
    };
  }

  private createHitAndRunAction(target: any, gameState: GameStateSnapshot): AIAction {
    // Attack then move to new position
    return {
      type: 'attack',
      targetId: target.id,
      priority: 7,
      data: { 
        attackType: 'hit_and_run',
        followUpAction: 'move',
        moveTarget: this.findTacticalPosition(gameState)
      }
    };
  }

  private findTacticalPosition(gameState: GameStateSnapshot): { x: number; y: number } {
    // Simple tactical positioning - away from enemies, near allies
    const current = gameState.position;
    let bestPos = current;
    let bestScore = -1000;

    // Check several potential positions
    for (let i = 0; i < 8; i++) {
      const angle = (i / 8) * Math.PI * 2;
      const distance = 40; // Move 40 units
      const pos = {
        x: current.x + Math.cos(angle) * distance,
        y: current.y + Math.sin(angle) * distance
      };

      const score = this.evaluatePosition(pos, gameState);
      if (score > bestScore) {
        bestScore = score;
        bestPos = pos;
      }
    }

    return bestPos;
  }

  private evaluatePosition(pos: { x: number; y: number }, gameState: GameStateSnapshot): number {
    let score = 0;

    // Prefer positions away from enemies
    for (const enemy of gameState.nearbyEnemies) {
      const distance = Math.sqrt(
        Math.pow(pos.x - enemy.id.x || 0, 2) + 
        Math.pow(pos.y - enemy.id.y || 0, 2)
      );
      score += distance * 0.1; // Farther from enemies is better
    }

    // Prefer positions near allies
    for (const ally of gameState.nearbyAllies) {
      const distance = Math.sqrt(
        Math.pow(pos.x - ally.id.x || 0, 2) + 
        Math.pow(pos.y - ally.id.y || 0, 2)
      );
      score -= distance * 0.05; // Closer to allies is better
    }

    return score;
  }

  private checkSpecialBehaviors(gameState: GameStateSnapshot): AIAction | null {
    for (const behavior of this.monsterProfile.specialBehaviors) {
      if (this.checkBehaviorTrigger(behavior, gameState)) {
        return this.executeBehaviorAction(behavior, gameState);
      }
    }
    return null;
  }

  private checkBehaviorTrigger(behavior: SpecialBehavior, gameState: GameStateSnapshot): boolean {
    switch (behavior.trigger) {
      case 'bloodied':
        return this.combatState.isBloodied && !this.combatState.hasUsedSpecialAbility;

      case 'outnumbered':
        return gameState.nearbyEnemies.length > gameState.nearbyAllies.length + 1;

      case 'ally_dies':
        // Would need additional context to detect ally deaths
        return false;

      default:
        return false;
    }
  }

  private processSpecialBehavior(trigger: string, gameState: GameStateSnapshot): void {
    const behavior = this.monsterProfile.specialBehaviors.find(b => b.trigger === trigger);
    if (behavior) {
      this.executeBehaviorAction(behavior, gameState);
    }
  }

  private executeBehaviorAction(behavior: SpecialBehavior, gameState: GameStateSnapshot): AIAction | null {
    switch (behavior.action) {
      case 'berserk':
        // Increase aggression and ignore defensive considerations
        this.updatePersonality({ aggression: 1.0 });
        return this.createAggressiveAction(gameState.nearbyEnemies[0]);

      case 'call_for_help':
        return {
          type: 'interact',
          priority: 10,
          data: { 
            action: 'call_reinforcements',
            range: behavior.parameters?.range || 100
          }
        };

      case 'retreat':
        return this.createRetreatAction(gameState);

      case 'use_ability': {
        const abilityId = behavior.parameters?.abilityId;
        if (abilityId) {
          return {
            type: 'attack',
            targetId: gameState.nearbyEnemies[0]?.id,
            priority: 10,
            data: { abilityId, abilityType: 'special' }
          };
        }
    }
        break;
    }

    return null;
  }

  private createRetreatAction(gameState: GameStateSnapshot): AIAction {
    // Move toward home territory or away from enemies
    const retreatPos = this.monsterProfile.environmentalFactors.homeTerrain || 
      this.findTacticalPosition(gameState);

    return {
      type: 'move',
      target: retreatPos,
      priority: 9,
      data: { moveType: 'retreat', speed: 'dash' }
    };
  }

  private createPatrolAction(): AIAction {
    const homeTerritory = this.monsterProfile.environmentalFactors.homeTerrain;

    if (homeTerritory) {
      // Patrol around home territory
      const angle = Math.random() * Math.PI * 2;
      const distance = Math.random() * homeTerritory.radius;

      return {
        type: 'move',
        target: {
          x: homeTerritory.x + Math.cos(angle) * distance,
          y: homeTerritory.y + Math.sin(angle) * distance
        },
        priority: 2,
        data: { moveType: 'patrol' }
      };
    }

    return {
      type: 'move',
      target: { x: Math.random() * 100, y: Math.random() * 100 },
      priority: 1,
      data: { moveType: 'wander' }
    };
  }

  private createBasicAttackAction(target: any): AIAction {
    return {
      type: 'attack',
      targetId: target.id,
      priority: 5,
      data: { attackType: 'basic' }
    };
  }

  private createDefensiveAction(target: any): AIAction {
    return {
      type: 'defend',
      priority: 6,
      data: { 
        targetThreat: target.id,
        defensiveBonus: 2
      }
    };
  }

  private createSupportAction(gameState: GameStateSnapshot): AIAction {
    // Find wounded ally
    const woundedAlly = gameState.nearbyAllies.find(ally => ally.health < 50);

    if (woundedAlly) {
      return {
        type: 'support',
        targetId: woundedAlly.id,
        priority: 7,
        data: { supportType: 'heal' }
      };
    }

    // Default to buff nearest ally
    return {
      type: 'support',
      targetId: gameState.nearbyAllies[0]?.id,
      priority: 4,
      data: { supportType: 'buff' }
    };
  }

  private selectLegendaryAction(legendaryActions: MonsterTrait[], target: any): AIAction | null {
    if (legendaryActions.length === 0) return null;

    // Simple selection - prefer attack actions
    const attackAction = legendaryActions.find(action => 
      action.mechanicalEffect?.type === 'damage'
    );

    if (attackAction) {
      return {
        type: 'attack',
        targetId: target.id,
        priority: 8,
        data: { 
          abilityId: attackAction.id,
          abilityType: 'legendary'
        }
      };
    }

    // Use first available legendary action
    return {
      type: 'attack',
      targetId: target.id,
      priority: 6,
      data: { 
        abilityId: legendaryActions[0].id,
        abilityType: 'legendary'
      }
    };
  }

  private getLegendaryActionsPerTurn(): number {
    // Most legendary creatures have 3 actions per turn
    return this.availableAbilities.filter(a => a.type === 'legendary_action').length > 0 ? 3 : 0;
