      this.queueUpdate('combat', 'system', { event: 'combatStarted' });
    });

    this.state.combat.on('turnStarted', (_combatant: Combatant) => {
      this.queueUpdate('combat', 'system', { event: 'turnStarted', combatant });
    });

    this.state.combat.on('attackExecuted', (data: any) => {
      this.queueUpdate('combat', 'system', { event: 'attackExecuted', data });
    });

    this.state.combat.on('damageApplied', (data: any) => {
      this.queueUpdate('combat', 'system', { event: 'damageApplied', data });
    });
  }

  private startSyncLoop(): void {
    setInterval(() => {
      this.processSyncTick();
    }, this.syncInterval);
  }

  private processSyncTick(): void {
    const now = Date.now();

    // Process queued updates
    if (this.updateQueue.length > 0) {
      this.broadcastDeltaSync();
    }

    // Clean up old client states
    this.cleanupClientStates(now);

    // Update world simulation
    this.state.world.update(this.syncInterval / 1000);

    // Update AI entities if enabled
    if (this.state.settings.aiEnabled) {
      this.updateAIEntities(this.syncInterval / 1000);
    }

    this.lastSyncTime = now;
  }

  private cleanupClientStates(now: number): void {
    const timeout = 30000; // 30 seconds
    for (const [playerId, clientState] of this.clientStates) {
      if (now - clientState.lastSeen > timeout) {
        this.handlePlayerDisconnect(playerId);
      }
    }
  }

  // Player management
  public addPlayer(player: Player): void {
    this.state.players.set(player.id, player);
    this.clientStates.set(player.id, { lastSequenceId: 0, lastSeen: Date.now() });

    this.queueUpdate('player', 'system', {
      event: 'playerJoined',
      player: this.serializePlayer(player)
    });

    this.emit('playerJoined', player);
  }

  public removePlayer(playerId: string): void {
    const player = this.state.players.get(playerId);
    if (player) {
      this.state.players.delete(playerId);
      this.clientStates.delete(playerId);

      this.queueUpdate('player', 'system', {
        event: 'playerLeft',
        playerId
      });

      this.emit('playerLeft', player);
    }
  }

  public updatePlayerConnection(playerId: string, connected: boolean): void {
    const player = this.state.players.get(playerId);
    if (player) {
      player.connected = connected;
      player.lastSeen = Date.now();

      const clientState = this.clientStates.get(playerId);
      if (clientState) {
        clientState.lastSeen = Date.now();
      }

      this.queueUpdate('player', 'system', {
        event: 'playerConnectionChanged',
        playerId,
        connected
      });
    }
  }

  private handlePlayerDisconnect(playerId: string): void {
    this.updatePlayerConnection(playerId, false);
    this.emit('playerDisconnected', playerId);
  }

  // State management
  public queueUpdate(type: StateUpdate['type'], playerId: string, data: any): void {
    if (this.updateQueue.length >= this.maxUpdateQueueSize) {
      // Remove oldest updates to prevent memory issues
      this.updateQueue.splice(0, this.updateQueue.length - this.maxUpdateQueueSize + 1);
    }

    const update: StateUpdate = {
      type,
      timestamp: Date.now(),
      playerId,
      data,
      sequenceId: ++this.sequenceCounter,
    };

    this.updateQueue.push(update);
    this.state.lastUpdate = update.timestamp;
  }

  public applyUpdate(update: StateUpdate): boolean {
    try {
      switch (update.type) {
        case 'entity':
          return this.applyEntityUpdate(update);
        case 'combat':
          return this.applyCombatUpdate(update);
        case 'player':
          return this.applyPlayerUpdate(update);
        case 'scene':
          return this.applySceneUpdate(update);
        case 'settings':
          return this.applySettingsUpdate(update);
        default:
          console.warn('Unknown update type:', update.type);
          return false;
      }
    } catch (error) {
      console.error('Error applying update:', error);
      return false;
    }
  }

  private applyEntityUpdate(update: StateUpdate): boolean {
    const { action,  entityId,  componentType,  data  } = update.data;

    switch (action) {
      case 'create':
        this.state.world.createEntity(entityId);
        break;
      case 'destroy':
        this.state.world.destroyEntity(entityId);
        break;
      case 'addComponent': {
        // Add component to entity
        const component = this.state.world.getComponent(componentType);
        if (component && component.add) {
          component.add(entityId, data);
        }
    }
        break;
      case 'updateComponent': {
        // Update component data
        const updateComponent = this.state.world.getComponent(componentType);
        if (updateComponent && updateComponent.update) {
          updateComponent.update(entityId, data);
        }
    }
        break;
      case 'removeComponent': {
        const removeComponent = this.state.world.getComponent(componentType);
        if (removeComponent && removeComponent.remove) {
          removeComponent.remove(entityId);
        }
    }
        break;
      default:
        return false;
    }

    return true;
  }

  private applyCombatUpdate(update: StateUpdate): boolean {
    const { action,  data  } = update.data;

    switch (action) {
      case 'addCombatant':
        this.state.combat.addCombatant(data);
        break;
      case 'removeCombatant':
        this.state.combat.removeCombatant(data.id);
        break;
      case 'executeAction':
        this.state.combat.executeAction(data);
        break;
      case 'nextTurn':
        this.state.combat.nextTurn();
        break;
      case 'startCombat':
        this.state.combat.startCombat();
        break;
      case 'endCombat':
        this.state.combat.endCombat();
        break;
      default:
        return false;
    }

    return true;
  }

  private applyPlayerUpdate(update: StateUpdate): boolean {
    const { action,  playerId,  data  } = update.data;

    switch (action) {
      case 'updateCharacter': {
        // Update player's character data
        const player = this.state.players.get(playerId);
        if (player && data.characterId && player.characterIds.includes(data.characterId)) {
          // Apply character updates
          this.emit('characterUpdated', { playerId, characterId: data.characterId, updates: data.updates });
        }
    }
        break;
      default:
        return false;
    }

    return true;
  }

  private applySceneUpdate(update: StateUpdate): boolean {
    const { action,  data  } = update.data;

    switch (action) {
      case 'changeScene':
        this.state.currentScene = data.sceneId;
        break;
      case 'updateSceneData':
        // Update scene-specific data
        this.emit('sceneDataUpdated', data);
        break;
      default:
        return false;
    }

    return true;
  }

  private applySettingsUpdate(update: StateUpdate): boolean {
    const { settings  } = update.data;
    this.state.settings = { ...this.state.settings, ...settings };
    return true;
  }

  // Synchronization
  public getFullSync(playerId: string): SyncMessage {
    const player = this.state.players.get(playerId);
    if (!player) {
      throw new Error('Player not found');
    }

    return {
      type: 'full_sync',
      sessionId: this.state.sessionId,
      timestamp: Date.now(),
      data: {
        players: Array.from(this.state.players.values()).map(p => this.serializePlayer(p)),
        worldState: this.serializeWorldState(),
        combatState: this.serializeCombatState(),
        currentScene: this.state.currentScene,
        settings: this.state.settings,
        sequenceId: this.sequenceCounter,
      },
    };
  }

  public getDeltaSync(playerId: string, lastSequenceId: number): SyncMessage {
    const relevantUpdates = this.updateQueue.filter(update => 
      update.sequenceId > lastSequenceId &&
      this.isUpdateRelevantToPlayer(update, playerId)
    );

    return {
      type: 'delta_sync',
      sessionId: this.state.sessionId,
      timestamp: Date.now(),
      data: {
        updates: relevantUpdates,
        sequenceId: this.sequenceCounter,
      },
    };
  }

  private isUpdateRelevantToPlayer(update: StateUpdate, playerId: string): boolean {
    const player = this.state.players.get(playerId);
    if (!player) return false;

    // GM sees all updates
    if (player.role === 'gm') return true;

    // Players see updates relevant to their characters or public updates
    switch (update.type) {
      case 'player':
        return true; // All player updates are public
      case 'combat':
        return true; // All combat updates are public
      case 'scene':
        return true; // All scene updates are public
      case 'settings':
        return true; // All settings updates are public
      case 'entity':
        // Entity updates are visible if they affect visible entities
        // This would need more sophisticated visibility logic
        return true;
      default:
        return false;
    }
  }

  private broadcastDeltaSync(): void {
    const _updates = [...this.updateQueue];
    this.updateQueue = [];

    for (const [playerId, clientState] of this.clientStates) {
      const deltaSync = this.getDeltaSync(playerId, clientState.lastSequenceId);
      if (deltaSync.data.updates.length > 0) {
        this.emit('syncMessage', playerId, deltaSync);
        clientState.lastSequenceId = this.sequenceCounter;
      }
    }
  }

  public handleClientMessage(playerId: string, message: SyncMessage): void {
    const player = this.state.players.get(playerId);
    if (!player) {
      console.warn('Message from unknown player:', playerId);
      return;
    }

    // Update client state
    const clientState = this.clientStates.get(playerId);
    if (clientState) {
      clientState.lastSeen = Date.now();
      if (message.sequenceId) {
        clientState.lastSequenceId = Math.max(clientState.lastSequenceId, message.sequenceId);
      }
    }

    switch (message.type) {
      case 'state_update':
        this.handleStateUpdate(playerId, message.data);
        break;
      case 'player_action':
        this.handlePlayerAction(playerId, message.data);
        break;
      default:
        console.warn('Unknown message type:', message.type);
    }
  }

  private handleStateUpdate(playerId: string, data: any): void {
    const player = this.state.players.get(playerId);
    if (!player) return;

    // Validate that player has permission to make this update
    if (!this.validatePlayerPermission(player, data)) {
      console.warn('Player lacks permission for update:', playerId, data);
      return;
    }

    // Queue the update
    this.queueUpdate(data.type, playerId, data);
  }

  private handlePlayerAction(playerId: string, action: any): void {
    const player = this.state.players.get(playerId);
    if (!player) return;

    switch (action.type) {
      case 'move_token':
        this.handleMoveToken(playerId, action.data);
        break;
      case 'combat_action':
        this.handleCombatAction(playerId, action.data);
        break;
      case 'chat_message':
        this.handleChatMessage(playerId, action.data);
        break;
      default:
        console.warn('Unknown player action:', action.type);
    }
  }

  private handleMoveToken(playerId: string, data: any): void {
    const player = this.state.players.get(playerId);
    if (!player) return;

    // Validate token ownership or GM permissions
    if (player.role !== 'gm' && !this.playerOwnsToken(player, data.tokenId)) {
      return;
    }

    this.queueUpdate('entity', playerId, {
      action: 'updateComponent',
      entityId: data.tokenId,
      componentType: 'Transform2D',
      data: { x: data.x, y: data.y }
    });
  }

  private handleCombatAction(playerId: string, data: any): void {
    const player = this.state.players.get(playerId);
    if (!player) return;

    // Validate that it's the player's turn or they're the GM
    const currentCombatant = this.state.combat.getCurrentCombatant();
    if (player.role !== 'gm' && 
        (!currentCombatant || !player.characterIds.includes(currentCombatant.id))) {
      return;
    }

    this.queueUpdate('combat', playerId, {
      action: 'executeAction',
      data: data.combatAction
    });
  }

  private handleChatMessage(playerId: string, data: any): void {
    this.queueUpdate('player', playerId, {
      event: 'chatMessage',
      playerId,
      message: data.message,
      timestamp: Date.now()
    });
  }

  private validatePlayerPermission(player: Player, update: any): boolean {
    // GM can do anything
    if (player.role === 'gm') return true;

    // Players can only update their own characters and make certain actions
    switch (update.type) {
      case 'entity':
        // Players can only update entities they own
        return this.playerOwnsEntity(player, update.entityId);
      case 'player':
        // Players can only update their own player data
        return update.playerId === player.id;
      case 'combat':
        // Players can only take combat actions for their characters
        return update.action === 'executeAction' && 
               player.characterIds.includes(update.data.actorId);
      default:
        return false;
    }
  }

  private playerOwnsEntity(player: Player, entityId: string): boolean {
    // This would need to be implemented based on your entity ownership system
    return player.characterIds.includes(entityId);
  }

  private playerOwnsToken(player: Player, tokenId: string): boolean {
    // This would need to be implemented based on your token ownership system
    return player.characterIds.includes(tokenId);
  }

  // Serialization helpers
  private serializePlayer(player: Player): any {
    return {
      id: player.id,
      name: player.name,
      role: player.role,
      characterIds: player.characterIds,
      connected: player.connected,
    };
  }

  private serializeWorldState(): any {
    // Serialize the ECS world state
    return {
      entities: this.state.world.getEntities(),
      // Add component data serialization here
    };
  }

  private serializeCombatState(): any {
    return {
      combatants: this.state.combat.getCombatants(),
      turnOrder: this.state.combat.getTurnOrder(),
      currentRound: this.state.combat.getCurrentRound(),
      isActive: this.state.combat.isInCombat(),
    };
  }

  // Public API
  public getSessionId(): string {
    return this.state.sessionId;
  }

  public getPlayers(): Player[] {
    return Array.from(this.state.players.values());
  }

  public getPlayer(playerId: string): Player | undefined {
    return this.state.players.get(playerId);
  }

  public getWorld(): World {
    return this.state.world;
  }

  public getCombatEngine(): CombatEngine {
    return this.state.combat;
  }

  public getSettings(): GameState['settings'] {
    return this.state.settings;
  }

  public updateSettings(settings: Partial<GameState['settings']>): void {
    this.queueUpdate('settings', 'system', { settings });
  }

  // AI Entity Management
  public addAIEntity(id: string, archetype: 'guard' | 'berserker' | 'scout' | 'healer' | 'wildcard' = 'guard'): void {
    let personality;
    switch (archetype) {
      case 'guard': personality = NPCArchetypes.createGuard(); break;
      case 'berserker': personality = NPCArchetypes.createBerserker(); break;
      case 'scout': personality = NPCArchetypes.createScout(); break;
      case 'healer': personality = NPCArchetypes.createHealer(); break;
      case 'wildcard': personality = NPCArchetypes.createWildcard(); break;
    }

    const aiEntity = new AIEntity(id, personality);
    this.state.aiEntities.set(id, aiEntity);

    // Create corresponding entity in ECS world (convert string ID to number)
    const numericId = parseInt(id, 10) || this.state.world.createEntity();
    this.state.world.createEntity(numericId);

    this.queueUpdate('entity', 'system', {
      event: 'aiEntityAdded',
      entityId: id,
      numericId,
      archetype
    });
  }

  public removeAIEntity(id: string): void {
    this.state.aiEntities.delete(id);

    // Convert string ID to numeric for ECS world
    const numericId = parseInt(id, 10);
    if (!isNaN(numericId)) {
      this.state.world.destroyEntity(numericId);
    }

    this.queueUpdate('entity', 'system', {
      event: 'aiEntityRemoved',
      entityId: id
    });
  }

  private updateAIEntities(deltaTime: number): void {
    for (const [entityId, aiEntity] of this.state.aiEntities) {
      // Create game state snapshot for this AI entity
      const gameState = this.createGameStateSnapshot(entityId);

      // Update AI entity
      aiEntity.update(gameState, deltaTime);

      // Check if AI wants to take any actions
      const aiState = aiEntity.getState();
      if (aiState.currentAction && aiState.behaviorTree) {
        this.handleAIAction(entityId, aiState.currentAction);
      }
    }
  }

  private createGameStateSnapshot(_entityId: string): GameStateSnapshot {
    // This would need to be implemented based on your ECS component system
    // For now, return a basic snapshot
    return {
      nearbyEnemies: [],
      nearbyAllies: [],
      isUnderThreat: false,
      healthPercentage: 1.0,
      position: { x: 0, y: 0 },
      canMove: true,
      canAttack: true
    };
  }

  private handleAIAction(entityId: string, action: string): void {
    // Handle AI-initiated actions
    switch (action) {
      case 'attack':
        this.queueUpdate('combat', 'ai', {
          action: 'executeAction',
          data: {
            actorId: entityId,
            actionType: 'attack',
            timestamp: Date.now()
          }
        });
        break;
      case 'move':
        // Handle movement
        break;
      case 'defend':
        // Handle defensive actions
        break;
      // Add more action handlers as needed
    }
  }

  public getAIEntities(): AIEntity[] {
    return Array.from(this.state.aiEntities.values());
  }

  public getAIEntity(id: string): AIEntity | undefined {
    return this.state.aiEntities.get(id);
  }

  public destroy(): void {
    this.removeAllListeners();
    this.state.players.clear();
    this.state.aiEntities.clear();
    this.clientStates.clear();
    this.updateQueue = [];
  }
}

export interface GameState {
  timestamp: number;
  frame: number;
  entities: Map<string, EntityState>;
  inputs: InputState[];
}

export interface EntityState {
  id: string;
  position: { x: number; y: number; z: number };
  velocity: { x: number; y: number; z: number };
  rotation: number;
  health: number;
  lastUpdate: number;
  ownerId?: string;
}

export interface InputState {
  playerId: string;
  sequence: number;
  timestamp: number;
  deltaTime: number;
  keys: { [key: string]: boolean };
  mouse: { x: number; y: number; buttons: number };
  acknowledged: boolean;
}

export interface ServerSnapshot {
  timestamp: number;
  frame: number;
  entities: EntityState[];
  acknowledgedInputs: number[]; // Sequence numbers of acknowledged inputs
}

export interface PredictionConfig {
  maxHistorySize: number;
  interpolationDelay: number; // ms
  extrapolationLimit: number; // ms
  reconciliationThreshold: number; // Distance threshold for corrections
  rollbackFrames: number;
  inputBufferSize: number;
  lagCompensationWindow: number; // ms
}

export class ClientPrediction {
  private gameStates: GameState[] = [];
  private inputBuffer: InputState[] = [];
  private serverSnapshots: ServerSnapshot[] = [];
  private config: PredictionConfig;

  // Timing
  private clientTime = 0;
  private serverTime = 0;
  private latency = 0;
  private jitter = 0;
  private timeOffset = 0;

  // State tracking
  private currentFrame = 0;
  private lastAcknowledgedInput = 0;
  private inputSequence = 0;
  private localPlayerId: string;

  // Prediction state
  private predictedEntities = new Map<string, EntityState>();
  private interpolatedEntities = new Map<string, EntityState>();

  // Statistics
  private corrections = 0;
  private predictions = 0;
  private rollbacks = 0;

  constructor(localPlayerId: string, config?: Partial<PredictionConfig>) {
    this.localPlayerId = localPlayerId;
    this.config = {
      maxHistorySize: 120, // 2 seconds at 60fps
      interpolationDelay: 100, // ms
      extrapolationLimit: 200, // ms
      reconciliationThreshold: 5.0, // units
      rollbackFrames: 10,
      inputBufferSize: 60,
      lagCompensationWindow: 1000, // ms
      ...config
    };
  }

  public update(deltaTime: number): void {
    this.clientTime += deltaTime * 1000;
    this.currentFrame++;

    // Update timing estimates
    this.updateTiming();

    // Process server snapshots
    this.processServerSnapshots();

    // Perform client-side prediction
    this.performPrediction(deltaTime);

    // Interpolate non-player entities
    this.performInterpolation();

    // Clean up old data
    this.cleanup();
  }

  private updateTiming(): void {
    // Update server time estimate
    this.serverTime = this.clientTime - this.timeOffset - this.latency / 2;
  }

  private processServerSnapshots(): void {
    for (const snapshot of this.serverSnapshots) {
      if (snapshot.timestamp > this.serverTime) {
        continue; // Future snapshot, skip for now
      }

      // Update acknowledged inputs
      for (const sequence of snapshot.acknowledgedInputs) {
        this.lastAcknowledgedInput = Math.max(this.lastAcknowledgedInput, sequence);

        // Mark input as acknowledged
        const input = this.inputBuffer.find(i => i.sequence === sequence);
        if (input) {
          input.acknowledged = true;
        }
      }

      // Perform reconciliation for player entity
      this.performReconciliation(snapshot);

      // Update interpolation targets for other entities
      // this.updateInterpolationTargets(snapshot); // Method not implemented
    }

    // Remove processed snapshots
    this.serverSnapshots = this.serverSnapshots.filter(s => s.timestamp > this.serverTime - this.config.lagCompensationWindow);
  }

  private performReconciliation(snapshot: ServerSnapshot): void {
    const playerEntity = snapshot.entities.find(e => e.ownerId === this.localPlayerId);
    if (!playerEntity) return;

    // Find the game state that corresponds to this server snapshot
    const correspondingState = this.findStateByTimestamp(snapshot.timestamp);
    if (!correspondingState) return;

    const predictedEntity = correspondingState.entities.get(playerEntity.id);
    if (!predictedEntity) return;

    // Check if correction is needed
    const distance = this.calculateDistance(predictedEntity.position, playerEntity.position);

    if (distance > this.config.reconciliationThreshold) {
      this.corrections++;

      // Perform rollback and replay
      this.rollbackAndReplay(snapshot, correspondingState);
    }
  }

  private rollbackAndReplay(snapshot: ServerSnapshot, targetState: GameState): void {
    this.rollbacks++;

    const playerEntity = snapshot.entities.find(e => e.ownerId === this.localPlayerId)!;

    // Create corrected state
    const correctedState: GameState = {
      timestamp: snapshot.timestamp,
      frame: targetState.frame,
      entities: new Map(targetState.entities),
      inputs: [...targetState.inputs]
    };

    // Apply server correction
    correctedState.entities.set(playerEntity.id, { ...playerEntity });

    // Find all inputs that need to be replayed
    const unacknowledgedInputs = this.inputBuffer.filter(
      input => input.sequence > this.lastAcknowledgedInput && 
               input.timestamp >= snapshot.timestamp
    );

    // Replay inputs
    let replayState = correctedState;
    for (const input of unacknowledgedInputs) {
      replayState = this.applyInput(replayState, input);
    }

    // Update current prediction
    this.predictedEntities.set(playerEntity.id, replayState.entities.get(playerEntity.id)!);

    // Update game state history
    const stateIndex = this.gameStates.findIndex(s => s.timestamp === targetState.timestamp);
    if (stateIndex >= 0) {
      this.gameStates[stateIndex] = correctedState;

      // Remove states after the corrected one and re-predict
      this.gameStates = this.gameStates.slice(0, stateIndex + 1);

      // Re-predict from corrected state
      this.replayFromState(correctedState);
    }
  }

  private replayFromState(state: GameState): void {
    const remainingInputs = this.inputBuffer.filter(
      input => input.timestamp > state.timestamp && !input.acknowledged
    );

    let currentState = state;
    for (const input of remainingInputs) {
      currentState = this.applyInput(currentState, input);
      this.gameStates.push({ ...currentState });
    }
  }

  private performPrediction(_deltaTime: number): void {
    // Get the latest confirmed state
    let baseState = this.getLatestConfirmedState();

    if (!baseState) {
      // No confirmed state yet, create initial state
      baseState = this.createInitialState();
    }

    // Apply all unacknowledged inputs
    const unacknowledgedInputs = this.inputBuffer.filter(
      input => input.sequence > this.lastAcknowledgedInput
    );

    let predictedState = baseState;
    for (const input of unacknowledgedInputs) {
      predictedState = this.applyInput(predictedState, input);
    }

    this.predictions++;

    // Store predicted state
    this.gameStates.push({
      timestamp: this.clientTime,
      frame: this.currentFrame,
      entities: new Map(predictedState.entities),
      inputs: [...predictedState.inputs]
    });

    // Update predicted entities
    for (const [id, entity] of predictedState.entities) {
      if (entity.ownerId === this.localPlayerId) {
        this.predictedEntities.set(id, { ...entity });
      }
    }
  }

  private performInterpolation(): void {
    const interpolationTime = this.serverTime - this.config.interpolationDelay;

    // Find two snapshots to interpolate between
    const snapshots = this.serverSnapshots
      .filter(s => s.timestamp <= interpolationTime + 50) // 50ms tolerance
      .sort((_a, _b) => a.timestamp - b.timestamp);

    if (snapshots.length < 2) {
      // Not enough data for interpolation, use extrapolation or latest
      if (snapshots.length === 1 && snapshots[0]) {
        this.extrapolateFromSnapshot(snapshots[0], interpolationTime);
      }
      return;
    }

    const prevSnapshot = snapshots[snapshots.length - 2];
    const nextSnapshot = snapshots[snapshots.length - 1];

    if (!prevSnapshot || !nextSnapshot) return;

    // Calculate interpolation factor
    const timeDiff = nextSnapshot.timestamp - prevSnapshot.timestamp;
    const t = timeDiff > 0 ? (interpolationTime - prevSnapshot.timestamp) / timeDiff : 0;
    const clampedT = Math.max(0, Math.min(1, t));

    for (const entity of nextSnapshot.entities) {
      const _entityId = entity.id;
      const prevEntity = prevSnapshot.entities.find(e => e.id === entity.id);
      if (!prevEntity) {
        // New entity, just use current state
        this.interpolatedEntities.set(entity.id, { ...entity });
        continue;
      }

      // Interpolate position and rotation
      const interpolatedEntity: EntityState = {
        ...entity,
        position: {
          x: this.lerp(prevEntity.position.x, entity.position.x, clampedT),
          y: this.lerp(prevEntity.position.y, entity.position.y, clampedT),
          z: this.lerp(prevEntity.position.z, entity.position.z, clampedT)
        },
        rotation: this.lerpAngle(prevEntity.rotation, entity.rotation, clampedT)
      };

      this.interpolatedEntities.set(entity.id, interpolatedEntity);
    }
  }

  private extrapolateFromSnapshot(snapshot: ServerSnapshot, targetTime: number): void {
    const deltaTime = (targetTime - snapshot.timestamp) / 1000;

    if (deltaTime > this.config.extrapolationLimit / 1000) {
      // Too much extrapolation, just use snapshot data
      for (const entity of snapshot.entities) {
        if (entity.ownerId !== this.localPlayerId) {
          this.interpolatedEntities.set(entity.id, { ...entity });
        }
      }
      return;
    }

    // Extrapolate based on velocity
    for (const entity of snapshot.entities) {
      if (entity.ownerId === this.localPlayerId) {
        continue;
      }

      const extrapolatedEntity: EntityState = {
        ...entity,
        position: {
          x: entity.position.x + entity.velocity.x * deltaTime,
          y: entity.position.y + entity.velocity.y * deltaTime,
          z: entity.position.z + entity.velocity.z * deltaTime
        }
      };

      this.interpolatedEntities.set(entity.id, extrapolatedEntity);
    }
  }

  private applyInput(state: GameState, input: InputState): GameState {
    const newState: GameState = {
      timestamp: input.timestamp,
      frame: state.frame + 1,
      entities: new Map(state.entities),
      inputs: [...state.inputs, input]
    };

    // Apply input to player entity
    for (const [id, entity] of newState.entities) {
      if (entity.ownerId === input.playerId) {
        const updatedEntity = this.updateEntityWithInput(entity, input);
        newState.entities.set(id, updatedEntity);
      }
    }

    return newState;
  }

  private updateEntityWithInput(entity: EntityState, input: InputState): EntityState {
    const newEntity = { ...entity };
    const deltaTime = input.deltaTime / 1000;
    const speed = 100; // units per second

    // Update velocity based on input
    newEntity.velocity = { x: 0, y: 0, z: 0 };

    if (input.keys['w'] || input.keys['ArrowUp']) {
      newEntity.velocity.z -= speed;
    }
    if (input.keys['s'] || input.keys['ArrowDown']) {
      newEntity.velocity.z += speed;
    }
    if (input.keys['a'] || input.keys['ArrowLeft']) {
      newEntity.velocity.x -= speed;
    }
    if (input.keys['d'] || input.keys['ArrowRight']) {
      newEntity.velocity.x += speed;
    }

    // Update position
    newEntity.position.x += newEntity.velocity.x * deltaTime;
    newEntity.position.y += newEntity.velocity.y * deltaTime;
    newEntity.position.z += newEntity.velocity.z * deltaTime;

    newEntity.lastUpdate = input.timestamp;

    return newEntity;
  }

  public addInput(input: Omit<InputState, 'sequence' | 'acknowledged'>): void {
    const fullInput: InputState = {
      ...input,
      sequence: ++this.inputSequence,
      acknowledged: false
    };

    this.inputBuffer.push(fullInput);

    // Limit buffer size
    if (this.inputBuffer.length > this.config.inputBufferSize) {
      this.inputBuffer.shift();
    }
  }

  public addServerSnapshot(snapshot: ServerSnapshot): void {
    // Update timing estimates
    const now = Date.now();
    this.latency = now - snapshot.timestamp;

    // Add jitter calculation
    const expectedLatency = this.latency;
    this.jitter = Math.abs(expectedLatency - this.latency) * 0.1 + this.jitter * 0.9;

    this.serverSnapshots.push(snapshot);

    // Limit snapshot history
    if (this.serverSnapshots.length > this.config.maxHistorySize) {
      this.serverSnapshots.shift();
    }
  }

  // Utility methods
  private findStateByTimestamp(timestamp: number): GameState | null {
    return this.gameStates.find(state => 
      Math.abs(state.timestamp - timestamp) < 16 // Within one frame
    ) || null;
  }

  private getLatestConfirmedState(): GameState | null {
    // Find the latest state that was confirmed by server
    const confirmedStates = this.gameStates.filter(state => 
      state.inputs.every(input => input.acknowledged)
    );

    return confirmedStates.length > 0 ? (confirmedStates[confirmedStates.length - 1] || null) : null;
  }

  private createInitialState(): GameState {
    return {
      timestamp: this.clientTime,
      frame: 0,
      entities: new Map(),
      inputs: []
    };
  }

  private calculateDistance(pos1: { x: number; y: number; z: number }, pos2: { x: number; y: number; z: number }): number {
    const dx = pos1.x - pos2.x;
    const dy = pos1.y - pos2.y;
    const dz = pos1.z - pos2.z;
    return Math.sqrt(dx * dx + dy * dy + dz * dz);
  }

  private lerp(a: number, b: number, t: number): number {
    return a + (b - a) * t;
  }

  private lerpAngle(a: number, b: number, t: number): number {
    // Handle angle wrapping
    let delta = b - a;
    if (delta > Math.PI) delta -= 2 * Math.PI;
    if (delta < -Math.PI) delta += 2 * Math.PI;
    return a + delta * t;
  }

  private cleanup(): void {
    const cutoffTime = this.clientTime - this.config.lagCompensationWindow;

    // Clean up old game states
    this.gameStates = this.gameStates.filter(state => state.timestamp > cutoffTime);

    // Clean up acknowledged inputs
    this.inputBuffer = this.inputBuffer.filter(input => 
      !input.acknowledged || input.timestamp > cutoffTime
    );

    // Clean up old server snapshots
    this.serverSnapshots = this.serverSnapshots.filter(snapshot => 
      snapshot.timestamp > cutoffTime
    );
  }

  // Getters
  public getPredictedEntity(entityId: string): EntityState | null {
    return this.predictedEntities.get(entityId) || null;
  }

  public getInterpolatedEntity(entityId: string): EntityState | null {
    return this.interpolatedEntities.get(entityId) || null;
  }

  public getLatency(): number {
    return this.latency;
  }

  public getJitter(): number {
    return this.jitter;
  }

  public getStats() {
    return {
      corrections: this.corrections,
      predictions: this.predictions,
      rollbacks: this.rollbacks,
      latency: this.latency,
      jitter: this.jitter,
      gameStates: this.gameStates.length,
      inputBuffer: this.inputBuffer.length,
      snapshots: this.serverSnapshots.length,
      acknowledgedInputs: this.inputBuffer.filter(i => i.acknowledged).length,
      pendingInputs: this.inputBuffer.filter(i => !i.acknowledged).length
    };
  }

  public getConfig(): PredictionConfig {
    return { ...this.config };
  }

  public setConfig(config: Partial<PredictionConfig>): void {
    this.config = { ...this.config, ...config };
  }

  // Debug methods
  public getDebugInfo(): any {
    return {
      clientTime: this.clientTime,
      serverTime: this.serverTime,
      timeOffset: this.timeOffset,
      currentFrame: this.currentFrame,
      lastAcknowledgedInput: this.lastAcknowledgedInput,
      inputSequence: this.inputSequence,
      stats: this.getStats(),
      recentStates: this.gameStates.slice(-5),
      recentInputs: this.inputBuffer.slice(-10),
      predictedEntities: Array.from(this.predictedEntities.entries()),
      interpolatedEntities: Array.from(this.interpolatedEntities.entries())
    };
  }

  public dispose(): void {
    this.gameStates = [];
    this.inputBuffer = [];
    this.serverSnapshots = [];
    this.predictedEntities.clear();
    this.interpolatedEntities.clear();
  }
}

import { EntityState, InputState, GameState } from './ClientPrediction';

export interface LagCompensationConfig {
  maxCompensationTime: number; // Maximum time to compensate for (ms)
  historySize: number; // Number of historical states to keep
  validationThreshold: number; // Maximum difference to accept
  antiCheatEnabled: boolean;
  maxSpeedThreshold: number;
  maxAccelerationThreshold: number;
}

export interface HistoricalState {
  timestamp: number;
  entities: Map<string, EntityState>;
  authoritative: boolean;
}

export interface CompensationResult {
  compensated: boolean;
  originalState: EntityState;
  compensatedState: EntityState;
  timeDifference: number;
}

export interface ValidationResult {
  valid: boolean;
  reason?: string;
  suspiciousActivity: boolean;
  correctedState?: EntityState;
}

export class LagCompensation {
  private config: LagCompensationConfig;
  private stateHistory: HistoricalState[] = [];
  private playerLatencies = new Map<string, number>();
  private playerJitter = new Map<string, number>();

  // Anti-cheat tracking
  private suspiciousPlayers = new Map<string, number>();
  private playerVelocityHistory = new Map<string, Array<{ velocity: number; timestamp: number }>>();

  // Statistics
  private compensationCount = 0;
  private validationCount = 0;
  private rejectedInputs = 0;

  constructor(config?: Partial<LagCompensationConfig>) {
    this.config = {
      maxCompensationTime: 1000, // 1 second
      historySize: 300, // 5 seconds at 60fps
      validationThreshold: 10.0,
      antiCheatEnabled: true,
      maxSpeedThreshold: 500, // units per second
      maxAccelerationThreshold: 1000, // units per second squared
      ...config
    };
  }

  public addHistoricalState(timestamp: number, gameState: GameState, authoritative: boolean = true): void {
    const historicalState: HistoricalState = {
      timestamp,
      entities: new Map(gameState.entities),
      authoritative
    };

    this.stateHistory.push(historicalState);

    // Keep history within limits
    if (this.stateHistory.length > this.config.historySize) {
      this.stateHistory.shift();
    }

    // Sort by timestamp to maintain order
    this.stateHistory.sort((_a, _b) => a.timestamp - b.timestamp);
  }

  public compensateForLatency(
    playerId: string,
    input: InputState,
    currentTime: number
  ): CompensationResult | null {
    const playerLatency = this.playerLatencies.get(playerId) || 0;
    const compensationTime = currentTime - input.timestamp - playerLatency / 2;

    // Check if compensation is within limits
    if (compensationTime > this.config.maxCompensationTime) {
      return null; // Too much compensation requested
    }

    // Find the historical state closest to the compensation time
    const targetTime = currentTime - compensationTime;
    const historicalState = this.findHistoricalState(targetTime);

    if (!historicalState) {
      return null; // No historical state available
    }

    // Get the entity at that time
    const entity = historicalState.entities.get(input.playerId);
    if (!entity) {
      return null; // Entity not found in historical state
    }

    // Get current entity state for comparison
    const currentState = this.getCurrentEntityState(input.playerId);
    if (!currentState) {
      return null;
    }

    this.compensationCount++;

    return {
      compensated: true,
      originalState: currentState,
      compensatedState: entity,
      timeDifference: compensationTime
    };
  }

  public validateInput(playerId: string, input: InputState, previousState?: EntityState): ValidationResult {
    this.validationCount++;

    if (!this.config.antiCheatEnabled) {
      return { valid: true, suspiciousActivity: false };
    }

    // Basic timestamp validation
    const currentTime = Date.now();
    const inputAge = currentTime - input.timestamp;

    if (inputAge > this.config.maxCompensationTime * 2) {
      this.rejectedInputs++;
      return {
        valid: false,
        reason: 'Input too old',
        suspiciousActivity: true
      };
    }

    // Validate input sequence
    if (!this.validateInputSequence(playerId, input)) {
      this.rejectedInputs++;
      return {
        valid: false,
        reason: 'Invalid input sequence',
        suspiciousActivity: true
      };
    }

    // Validate movement if we have a previous state
    if (previousState) {
      const movementValidation = this.validateMovement(playerId, input, previousState);
      if (!movementValidation.valid) {
        this.rejectedInputs++;
        return movementValidation;
      }
    }

    return { valid: true, suspiciousActivity: false };
  }

  private validateInputSequence(playerId: string, input: InputState): boolean {
    // Check for duplicate or out-of-order inputs
    // This would require tracking last processed input sequence per player
    // For now, just basic validation

    if (input.sequence < 0 || input.sequence > 1000000) {
      return false; // Unreasonable sequence number
    }

    return true;
  }

  private validateMovement(
    playerId: string,
    input: InputState,
    previousState: EntityState
  ): ValidationResult {
    // Calculate expected position based on input and previous state
    const deltaTime = input.deltaTime / 1000;
    const expectedState = this.calculateExpectedMovement(previousState, input, deltaTime);

    // Get current velocity for this player
    const currentVelocity = Math.sqrt(
      expectedState.velocity.x ** 2 + 
      expectedState.velocity.y ** 2 + 
      expectedState.velocity.z ** 2
    );

    // Check speed limits
    if (currentVelocity > this.config.maxSpeedThreshold) {
      this.recordSuspiciousActivity(playerId);

      return {
        valid: false,
        reason: `Speed too high: ${currentVelocity.toFixed(2)}`,
        suspiciousActivity: true,
        correctedState: {
          ...expectedState,
          velocity: this.clampVelocity(expectedState.velocity, this.config.maxSpeedThreshold)
        }
      };
    }

    // Check acceleration limits
    const acceleration = this.calculateAcceleration(playerId, expectedState.velocity, input.timestamp);
    if (acceleration > this.config.maxAccelerationThreshold) {
      this.recordSuspiciousActivity(playerId);

      return {
        valid: false,
        reason: `Acceleration too high: ${acceleration.toFixed(2)}`,
        suspiciousActivity: true
      };
    }

    // Update velocity history for future acceleration checks
    this.updateVelocityHistory(playerId, currentVelocity, input.timestamp);

    return { valid: true, suspiciousActivity: false };
  }

  private calculateExpectedMovement(
    previousState: EntityState,
    input: InputState,
    deltaTime: number
  ): EntityState {
    const speed = 100; // Base speed units per second
    const newState = { ...previousState };

    // Calculate velocity based on input
    newState.velocity = { x: 0, y: 0, z: 0 };

    if (input.keys['w'] || input.keys['ArrowUp']) {
      newState.velocity.z -= speed;
    }
    if (input.keys['s'] || input.keys['ArrowDown']) {
      newState.velocity.z += speed;
    }
    if (input.keys['a'] || input.keys['ArrowLeft']) {
      newState.velocity.x -= speed;
    }
    if (input.keys['d'] || input.keys['ArrowRight']) {
      newState.velocity.x += speed;
    }

    // Apply diagonal movement normalization
    const velocityMagnitude = Math.sqrt(
      newState.velocity.x ** 2 + newState.velocity.z ** 2
    );

    if (velocityMagnitude > speed) {
      const scale = speed / velocityMagnitude;
      newState.velocity.x *= scale;
      newState.velocity.z *= scale;
    }

    // Update position
    newState.position = {
      x: previousState.position.x + newState.velocity.x * deltaTime,
      y: previousState.position.y + newState.velocity.y * deltaTime,
      z: previousState.position.z + newState.velocity.z * deltaTime
    };

    newState.lastUpdate = input.timestamp;

    return newState;
  }

  private calculateAcceleration(playerId: string, currentVelocity: { x: number; y: number; z: number }, timestamp: number): number {
    const history = this.playerVelocityHistory.get(playerId);
    if (!history || history.length === 0) {
      return 0; // No history to compare against
    }

    const lastVelocityEntry = history[history.length - 1];
    if (!lastVelocityEntry) {
      return 0;
    }

    const timeDiff = (timestamp - lastVelocityEntry.timestamp) / 1000;

    if (timeDiff <= 0) {
      return 0;
    }

    const currentSpeed = Math.sqrt(currentVelocity.x ** 2 + currentVelocity.y ** 2 + currentVelocity.z ** 2);
    const acceleration = Math.abs(currentSpeed - lastVelocityEntry.velocity) / timeDiff;

    return acceleration;
  }

  private updateVelocityHistory(playerId: string, velocity: number, timestamp: number): void {
    let history = this.playerVelocityHistory.get(playerId);
    if (!history) {
      history = [];
      this.playerVelocityHistory.set(playerId, history);
    }

    history.push({ velocity, timestamp });

    // Keep only recent history (last 1 second)
    const cutoffTime = timestamp - 1000;
    this.playerVelocityHistory.set(
      playerId,
      history.filter(entry => entry.timestamp > cutoffTime)
    );
  }

  private clampVelocity(velocity: { x: number; y: number; z: number }, maxSpeed: number): { x: number; y: number; z: number } {
    const magnitude = Math.sqrt(velocity.x ** 2 + velocity.y ** 2 + velocity.z ** 2);

    if (magnitude > maxSpeed) {
      const scale = maxSpeed / magnitude;
      return {
        x: velocity.x * scale,
        y: velocity.y * scale,
        z: velocity.z * scale
      };
    }

    return velocity;
  }

  private recordSuspiciousActivity(playerId: string): void {
    const current = this.suspiciousPlayers.get(playerId) || 0;
    this.suspiciousPlayers.set(playerId, current + 1);
  }

  private findHistoricalState(timestamp: number): HistoricalState | null {
    if (this.stateHistory.length === 0) {
      return null;
    }

    // Find closest historical state
    let closest: HistoricalState | null = null;
    let closestDistance = Infinity;

    for (const state of this.stateHistory) {
      const distance = Math.abs(state.timestamp - timestamp);
      if (distance < closestDistance) {
        closestDistance = distance;
        closest = state;
      }
    }

    // Only return if within reasonable time threshold
    if (closest && closestDistance < 100) { // 100ms threshold
      return closest;
    }

    return null;
  }

  private getCurrentEntityState(entityId: string): EntityState | null {
    if (this.stateHistory.length === 0) {
      return null;
    }

    const latestState = this.stateHistory[this.stateHistory.length - 1];
    return latestState?.entities.get(entityId) || null;
  }

  // Player management
  public updatePlayerLatency(playerId: string, latency: number): void {
    this.playerLatencies.set(playerId, latency);

    // Calculate jitter
    const previousLatency = this.playerLatencies.get(playerId) || latency;
    const jitter = Math.abs(latency - previousLatency);
    const currentJitter = this.playerJitter.get(playerId) || 0;

    // Exponential moving average for jitter
    this.playerJitter.set(playerId, currentJitter * 0.9 + jitter * 0.1);
  }

  public getPlayerLatency(playerId: string): number {
    return this.playerLatencies.get(playerId) || 0;
  }

  public getPlayerJitter(playerId: string): number {
    return this.playerJitter.get(playerId) || 0;
  }

  public removePlayer(playerId: string): void {
    this.playerLatencies.delete(playerId);
    this.playerJitter.delete(playerId);
    this.suspiciousPlayers.delete(playerId);
    this.playerVelocityHistory.delete(playerId);
  }

  // Interpolation helpers for smooth lag compensation
  public interpolateEntityState(
    state1: EntityState,
    state2: EntityState,
    t: number
  ): EntityState {
    const clampedT = Math.max(0, Math.min(1, t));

    return {
      ...state1,
      position: {
        x: this.lerp(state1.position.x, state2.position.x, clampedT),
        y: this.lerp(state1.position.y, state2.position.y, clampedT),
        z: this.lerp(state1.position.z, state2.position.z, clampedT)
      },
      velocity: {
        x: this.lerp(state1.velocity.x, state2.velocity.x, clampedT),
        y: this.lerp(state1.velocity.y, state2.velocity.y, clampedT),
        z: this.lerp(state1.velocity.z, state2.velocity.z, clampedT)
      },
      rotation: this.lerpAngle(state1.rotation, state2.rotation, clampedT),
      health: this.lerp(state1.health, state2.health, clampedT)
    };
  }

  private lerp(a: number, b: number, t: number): number {
    return a + (b - a) * t;
  }

  private lerpAngle(a: number, b: number, t: number): number {
    let delta = b - a;
    if (delta > Math.PI) delta -= 2 * Math.PI;
    if (delta < -Math.PI) delta += 2 * Math.PI;
    return a + delta * t;
  }

  // Statistics and monitoring
  public getStats() {
    return {
      compensationCount: this.compensationCount,
      validationCount: this.validationCount,
      rejectedInputs: this.rejectedInputs,
      rejectionRate: this.validationCount > 0 ? this.rejectedInputs / this.validationCount : 0,
      historySize: this.stateHistory.length,
      trackedPlayers: this.playerLatencies.size,
      suspiciousPlayers: this.suspiciousPlayers.size,
      averageLatency: this.calculateAverageLatency(),
      averageJitter: this.calculateAverageJitter()
    };
  }

  private calculateAverageLatency(): number {
    if (this.playerLatencies.size === 0) return 0;

    const sum = Array.from(this.playerLatencies.values()).reduce((_a, _b) => a + b, 0);
    return sum / this.playerLatencies.size;
  }

  private calculateAverageJitter(): number {
    if (this.playerJitter.size === 0) return 0;

    const sum = Array.from(this.playerJitter.values()).reduce((_a, _b) => a + b, 0);
    return sum / this.playerJitter.size;
  }

  public getSuspiciousPlayers(): Array<{ playerId: string; violations: number }> {
    return Array.from(this.suspiciousPlayers.entries())
      .map([playerId, _violations] => ({ playerId, violations }))
      .sort((_a, _b) => b.violations - a.violations);
  }

  public getPlayerStats(playerId: string) {
    return {
      latency: this.getPlayerLatency(playerId),
      jitter: this.getPlayerJitter(playerId),
      suspiciousActivity: this.suspiciousPlayers.get(playerId) || 0,
      velocityHistorySize: this.playerVelocityHistory.get(playerId)?.length || 0
    };
  }

  // Configuration
  public getConfig(): LagCompensationConfig {
    return { ...this.config };
  }

  public setConfig(config: Partial<LagCompensationConfig>): void {
    this.config = { ...this.config, ...config };
  }

  // Cleanup
  public cleanup(): void {
    const cutoffTime = Date.now() - this.config.maxCompensationTime * 2;

    // Clean old historical states
    this.stateHistory = this.stateHistory.filter(state => state.timestamp > cutoffTime);

    // Clean old velocity histories
    for (const [playerId, history] of this.playerVelocityHistory) {
      const filteredHistory = history.filter(entry => entry.timestamp > cutoffTime);
      if (filteredHistory.length === 0) {
        this.playerVelocityHistory.delete(playerId);
      } else {
        this.playerVelocityHistory.set(playerId, filteredHistory);
      }
    }
  }

  public dispose(): void {
    this.stateHistory = [];
    this.playerLatencies.clear();
    this.playerJitter.clear();
    this.suspiciousPlayers.clear();
    this.playerVelocityHistory.clear();
  }
}

// Client Prediction exports
export {
  ClientPrediction,
  type GameState,
  type EntityState,
  type InputState,
  type ServerSnapshot,
  type PredictionConfig
} from './ClientPrediction';

// Lag Compensation exports
export {
  LagCompensation,
  type LagCompensationConfig,
  type HistoricalState,
  type CompensationResult,
  type ValidationResult
} from './LagCompensation';

// Import classes and types for usage in this file
import { 
  ClientPrediction,
  type GameState,
  type EntityState,
  type InputState,
  type ServerSnapshot,
  type PredictionConfig
} from './ClientPrediction';
import { 
  LagCompensation,
  type LagCompensationConfig,
  type HistoricalState,
  type CompensationResult,
  type ValidationResult
} from './LagCompensation';

// Network Prediction Manager
export class NetworkPredictionManager {
  private clientPrediction: ClientPrediction;
  private lagCompensation: LagCompensation;
  private isServer: boolean;

  constructor(localPlayerId: string, isServer: boolean = false, config?: {
    prediction?: Partial<PredictionConfig>;
    lagCompensation?: Partial<LagCompensationConfig>;
  }) {
    this.isServer = isServer;
    this.clientPrediction = new ClientPrediction(localPlayerId, config?.prediction);
    this.lagCompensation = new LagCompensation(config?.lagCompensation);
  }

  // Client-side methods
  public updateClient(deltaTime: number): void {
    if (!this.isServer) {
      this.clientPrediction.update(deltaTime);
    }
  }

  public addInput(input: Omit<InputState, 'sequence' | 'acknowledged'>): void {
    if (!this.isServer) {
      this.clientPrediction.addInput(input);
    }
  }

  public addServerSnapshot(snapshot: ServerSnapshot): void {
    if (!this.isServer) {
      this.clientPrediction.addServerSnapshot(snapshot);
    }
  }

  public getPredictedEntity(entityId: string): EntityState | null {
    return this.clientPrediction.getPredictedEntity(entityId);
  }

  public getInterpolatedEntity(entityId: string): EntityState | null {
    return this.clientPrediction.getInterpolatedEntity(entityId);
  }

  // Server-side methods
  public addHistoricalState(timestamp: number, gameState: GameState): void {
    if (this.isServer) {
      this.lagCompensation.addHistoricalState(timestamp, gameState);
    }
  }

  public validateInput(playerId: string, input: InputState, previousState?: EntityState): ValidationResult {
    return this.lagCompensation.validateInput(playerId, input, previousState);
  }

  public compensateForLatency(playerId: string, input: InputState, currentTime: number): CompensationResult | null {
    return this.lagCompensation.compensateForLatency(playerId, input, currentTime);
  }

  public updatePlayerLatency(playerId: string, latency: number): void {
    this.lagCompensation.updatePlayerLatency(playerId, latency);
  }

  // Common methods
  public getStats() {
    return {
      client: this.clientPrediction.getStats(),
      server: this.lagCompensation.getStats(),
      isServer: this.isServer
    };
  }

  public cleanup(): void {
    this.clientPrediction.dispose();
    this.lagCompensation.dispose();
  }
}

// Utility functions for network prediction
export function createInputState(
  _playerId: string,
  _keys: { [key: string]: boolean },
  _mouse: { x: number; y: number; buttons: number },
  _deltaTime: number = 16.67
): Omit<InputState, 'sequence' | 'acknowledged'> {
  return {
    playerId,
    timestamp: Date.now(),
    deltaTime,
    keys: { ...keys },
    mouse: { ...mouse }
  };
}

export function createEntityState(
  _id: string,
  _position: { x: number; y: number; z: number },
  _velocity: { x: number; y: number; z: number } = { x: 0, _y: 0, _z: 0 },
  _rotation: number = 0,
  _health: number = 100,
  _ownerId?: string
): EntityState {
  const baseEntity = {
    id,
    position: { ...position },
    velocity: { ...velocity },
    rotation,
    health,
    lastUpdate: Date.now()
  };

  return ownerId !== undefined 
    ? { ...baseEntity, ownerId } 
    : baseEntity;
}

export function createServerSnapshot(
  _entities: EntityState[],
  _acknowledgedInputs: number[] = [],
  _frame: number = 0
): ServerSnapshot {
  return {
    timestamp: Date.now(),
    frame,
    entities: [...entities],
    acknowledgedInputs: [...acknowledgedInputs]
  };
}

/**
 * Network Synchronization Engine for VTT Entity States
 * Handles real-time synchronization of all entity states across clients
 */

import { EventEmitter } from 'events';

export interface EntityState {
  id: string;
  type: 'character' | 'monster' | 'npc' | 'token';
  position: { x: number; y: number; z?: number };
  health: {
    current: number;
    max: number;
    temporary: number;
  };
  stats: {
    abilities: Record<string, number>;
    armorClass: number;
    speed: number;
    proficiencyBonus: number;
  };
  conditions: Array<{
    id: string;
    duration: number;
    source: string;
  }>;
  combat: {
    initiative: number;
    isActive: boolean;
    hasActed: boolean;
    actionPoints: number;
    reactions: Array<{ id: string; used: boolean; }>;
  };
  equipment: Array<{
    id: string;
    equipped: boolean;
    attuned?: boolean;
  }>;
  spells: {
    slots: Record<number, { current: number; max: number; }>;
    prepared: string[];
    concentrating?: {
      spellId: string;
      duration: number;
    };
  };
  social: {
    relationships: Record<string, {
      attitude: string;
      value: number;
    }>;
  };
  ai?: {
    personality: Record<string, number>;
    currentGoal: string;
    lastAction: string;
  };
  lastUpdate: number;
  version: number;
}

export interface SyncMessage {
  type: 'full_sync' | 'delta_sync' | 'state_request' | 'state_response' | 'conflict_resolution';
  entityId: string;
  timestamp: number;
  clientId: string;
  data: any;
  version?: number;
}

export interface SyncConflict {
  entityId: string;
  field: string;
  clientAValue: any;
  clientBValue: any;
  timestampA: number;
  timestampB: number;
  resolution: 'timestamp' | 'server_authoritative' | 'manual';
}

export class NetworkSyncEngine extends EventEmitter {
  private entityStates = new Map<string, EntityState>();
  private clientStates = new Map<string, Set<string>>(); // clientId -> entityIds they're tracking
  private syncHistory = new Map<string, Array<{ timestamp: number; state: Partial<EntityState> }>>();
  private conflictResolver = new ConflictResolver();
  private broadcastCallback?: (message: SyncMessage) => void;

  constructor() {
    super();
    this.setupCleanupInterval();
  }

  /**
   * Set the callback for broadcasting messages to clients
   */
  setBroadcastCallback(_callback: (message: SyncMessage) => void): void {
    this.broadcastCallback = callback;
  }

  /**
   * Register a client for entity synchronization
   */
  registerClient(clientId: string, trackedEntityIds: string[] = []): void {
    this.clientStates.set(clientId, new Set(trackedEntityIds));

    // Send initial state for tracked entities
    for (const entityId of trackedEntityIds) {
      const state = this.entityStates.get(entityId);
      if (state) {
        this.sendToClient(clientId, {
          type: 'full_sync',
          entityId,
          timestamp: Date.now(),
          clientId: 'server',
          data: state
        });
      }
    }
  }

  /**
   * Unregister a client
   */
  unregisterClient(clientId: string): void {
    this.clientStates.delete(clientId);
  }

  /**
   * Update entity state and sync to relevant clients
   */
  updateEntityState(
    entityId: string, 
    updates: Partial<EntityState>, 
    sourceClientId: string = 'server'
  ): void {
    const currentState = this.entityStates.get(entityId);
    const timestamp = Date.now();

    if (!currentState) {
      // New entity
      const newState: EntityState = {
        id: entityId,
        type: 'character',
        position: { x: 0, y: 0 },
        health: { current: 100, max: 100, temporary: 0 },
        stats: { abilities: Record<string, any>, armorClass: 10, speed: 30, proficiencyBonus: 2 },
        conditions: [],
        combat: { initiative: 0, isActive: false, hasActed: false, actionPoints: 1, reactions: [] },
        equipment: [],
        spells: { slots: Record<string, any>, prepared: [] },
        social: { relationships: Record<string, unknown>},
        lastUpdate: timestamp,
        version: 1,
        ...updates
      };

      this.entityStates.set(entityId, newState);
      this.broadcastFullSync(entityId, newState, sourceClientId);
    } else {
      // Check for conflicts
      const conflict = this.detectConflict(entityId, updates, sourceClientId, timestamp);
      if (conflict) {
        const resolution = this.conflictResolver.resolve(conflict);
        if (resolution.rejected) {
          this.sendConflictResolution(sourceClientId, entityId, resolution);
          return;
        }
        // Apply resolved updates
        updates = resolution.resolvedState;
      }

      // Apply updates
      const updatedState = {
        ...currentState,
        ...updates,
        lastUpdate: timestamp,
        version: currentState.version + 1
      };

      this.entityStates.set(entityId, updatedState);
      this.addToSyncHistory(entityId, updates, timestamp);

      // Broadcast delta sync to other clients
      this.broadcastDeltaSync(entityId, updates, sourceClientId);
    }
  }

  /**
   * Get current entity state
   */
  getEntityState(entityId: string): EntityState | null {
    return this.entityStates.get(entityId) || null;
  }

  /**
   * Handle incoming sync message from client
   */
  handleSyncMessage(message: SyncMessage): void {
    switch (message.type) {
      case 'full_sync':
        this.handleFullSync(message);
        break;
      case 'delta_sync':
        this.handleDeltaSync(message);
        break;
      case 'state_request':
        this.handleStateRequest(message);
        break;
      case 'conflict_resolution':
        this.handleConflictResolution(message);
        break;
    }
  }

  private handleFullSync(message: SyncMessage): void {
    const entityState = message.data as EntityState;
    this.updateEntityState(entityState.id, entityState, message.clientId);
  }

  private handleDeltaSync(message: SyncMessage): void {
    const updates = message.data as Partial<EntityState>;
    this.updateEntityState(message.entityId, updates, message.clientId);
  }

  private handleStateRequest(message: SyncMessage): void {
    const state = this.entityStates.get(message.entityId);
    if (state) {
      this.sendToClient(message.clientId, {
        type: 'state_response',
        entityId: message.entityId,
        timestamp: Date.now(),
        clientId: 'server',
        data: state
      });
    }
  }

  private handleConflictResolution(message: SyncMessage): void {
    // Client accepted conflict resolution
    const updates = message.data as Partial<EntityState>;
    const currentState = this.entityStates.get(message.entityId);
    if (currentState) {
      this.entityStates.set(message.entityId, { ...currentState, ...updates });
    }
  }

  private detectConflict(
    entityId: string, 
    updates: Partial<EntityState>, 
    clientId: string, 
    timestamp: number
  ): SyncConflict | null {
    const currentState = this.entityStates.get(entityId);
    if (!currentState) return null;

    // Check if updates conflict with recent changes
    const timeDiff = timestamp - currentState.lastUpdate;
    if (timeDiff < 0) {
      // Client update is older than current state
      return {
        entityId,
        field: 'timestamp',
        clientAValue: updates,
        clientBValue: currentState,
        timestampA: timestamp,
        timestampB: currentState.lastUpdate,
        resolution: 'timestamp'
      };
    }

    // Check for concurrent modifications of critical fields
    const criticalFields = ['health', 'position', 'combat'];
    for (const field of criticalFields) {
      if (updates[field as keyof EntityState] && timeDiff < 1000) { // 1 second window
        return {
          entityId,
          field,
          clientAValue: updates[field as keyof EntityState],
          clientBValue: currentState[field as keyof EntityState],
          timestampA: timestamp,
          timestampB: currentState.lastUpdate,
          resolution: 'server_authoritative'
        };
      }
    }

    return null;
  }

  private broadcastFullSync(entityId: string, state: EntityState, excludeClient?: string): void {
    const message: SyncMessage = {
      type: 'full_sync',
      entityId,
      timestamp: Date.now(),
      clientId: 'server',
      data: state,
      version: state.version
    };

    this.broadcastToRelevantClients(entityId, message, excludeClient);
  }

  private broadcastDeltaSync(
    entityId: string, 
    updates: Partial<EntityState>, 
    excludeClient?: string
  ): void {
    const message: SyncMessage = {
      type: 'delta_sync',
      entityId,
      timestamp: Date.now(),
      clientId: 'server',
      data: updates
    };

    this.broadcastToRelevantClients(entityId, message, excludeClient);
  }

  private broadcastToRelevantClients(
    entityId: string, 
    message: SyncMessage, 
    excludeClient?: string
  ): void {
    for (const [clientId, trackedEntities] of this.clientStates) {
      if (clientId !== excludeClient && trackedEntities.has(entityId)) {
        this.sendToClient(clientId, message);
      }
    }
  }

  private sendToClient(clientId: string, message: SyncMessage): void {
    if (this.broadcastCallback) {
      this.broadcastCallback({ ...message, clientId });
    }
  }

  private sendConflictResolution(
    clientId: string, 
    entityId: string, 
    resolution: any
  ): void {
    this.sendToClient(clientId, {
      type: 'conflict_resolution',
      entityId,
      timestamp: Date.now(),
      clientId: 'server',
      data: resolution
    });
  }

  private addToSyncHistory(
    entityId: string, 
    updates: Partial<EntityState>, 
    timestamp: number
  ): void {
    if (!this.syncHistory.has(entityId)) {
      this.syncHistory.set(entityId, []);
    }

    const history = this.syncHistory.get(entityId)!;
    history.push({ timestamp, state: updates });

    // Keep only last 100 updates
    if (history.length > 100) {
      history.splice(0, history.length - 100);
    }
  }

  private setupCleanupInterval(): void {
    // Clean up old sync history every 5 minutes
    setInterval(() => {
      const cutoff = Date.now() - (5 * 60 * 1000); // 5 minutes ago

      for (const [entityId, history] of this.syncHistory) {
        const filteredHistory = history.filter(entry => entry.timestamp > cutoff);
        if (filteredHistory.length === 0) {
          this.syncHistory.delete(entityId);
        } else {
          this.syncHistory.set(entityId, filteredHistory);
        }
      }
    }, 5 * 60 * 1000);
  }

  /**
   * Get synchronization statistics
   */
  getSyncStats(): {
    totalEntities: number;
    activeClients: number;
    avgUpdatesPerMinute: number;
    conflictsResolved: number;
  } {
    return {
      totalEntities: this.entityStates.size,
      activeClients: this.clientStates.size,
      avgUpdatesPerMinute: this.calculateUpdateRate(),
      conflictsResolved: this.conflictResolver.getResolvedCount()
    };
  }

  private calculateUpdateRate(): number {
    let totalUpdates = 0;
    const oneMinuteAgo = Date.now() - 60000;

    for (const history of this.syncHistory.values()) {
      totalUpdates += history.filter(entry => entry.timestamp > oneMinuteAgo).length;
    }

    return totalUpdates;
  }
}

class ConflictResolver {
  private resolvedCount = 0;

  resolve(conflict: SyncConflict): { rejected: boolean; resolvedState?: any } {
    this.resolvedCount++;

    switch (conflict.resolution) {
      case 'timestamp':
        // Newer timestamp wins
        if (conflict.timestampA > conflict.timestampB) {
          return { rejected: false, resolvedState: conflict.clientAValue };
        } else {
          return { rejected: true };
        }

      case 'server_authoritative':
        // Server state wins
        return { rejected: true };

      case 'manual':
        // Requires manual intervention - for now, server wins
        return { rejected: true };

      default:
        return { rejected: true };
    }
  }

  getResolvedCount(): number {
    return this.resolvedCount;
  }
}

// Specialized sync managers for different entity types
export class CombatSyncManager {
  private syncEngine: NetworkSyncEngine;

  constructor(syncEngine: NetworkSyncEngine) {
    this.syncEngine = syncEngine;
  }

  syncInitiativeOrder(combatants: Array<{ id: string; initiative: number }>): void {
    for (const combatant of combatants) {
      this.syncEngine.updateEntityState(combatant.id, {
        combat: {
          initiative: combatant.initiative,
          isActive: false,
          hasActed: false,
          actionPoints: 1,
          reactions: []
        }
      });
    }
  }

  syncTurnChange(activeEntityId: string): void {
    // Deactivate all entities first
    const _allStates = new Map();

    // Then activate the current entity
    this.syncEngine.updateEntityState(activeEntityId, {
      combat: {
        initiative: 0, // Will be filled from current state
        isActive: true,
        hasActed: false,
        actionPoints: 1,
        reactions: []
      }
    });
  }

  syncActionUsed(entityId: string, _actionType: string): void {
    const currentState = this.syncEngine.getEntityState(entityId);
    if (currentState) {
      this.syncEngine.updateEntityState(entityId, {
        combat: {
          ...currentState.combat,
          hasActed: true,
          actionPoints: Math.max(0, currentState.combat.actionPoints - 1)
        }
      });
    }
  }
}

export class HealthSyncManager {
  private syncEngine: NetworkSyncEngine;

  constructor(syncEngine: NetworkSyncEngine) {
    this.syncEngine = syncEngine;
  }

  syncDamage(entityId: string, damage: number): void {
    const currentState = this.syncEngine.getEntityState(entityId);
    if (currentState) {
      let newHP = currentState.health.current - damage;

      // Apply temporary HP first
      if (currentState.health.temporary > 0) {
        const tempReduction = Math.min(currentState.health.temporary, damage);
        newHP = currentState.health.current - (damage - tempReduction);

        this.syncEngine.updateEntityState(entityId, {
          health: {
            current: Math.max(0, newHP),
            max: currentState.health.max,
            temporary: currentState.health.temporary - tempReduction
          }
        });
      } else {
        this.syncEngine.updateEntityState(entityId, {
          health: {
            ...currentState.health,
            current: Math.max(0, newHP)
          }
        });
      }
    }
  }

  syncHealing(entityId: string, healing: number): void {
    const currentState = this.syncEngine.getEntityState(entityId);
    if (currentState) {
      this.syncEngine.updateEntityState(entityId, {
        health: {
          ...currentState.health,
          current: Math.min(currentState.health.max, currentState.health.current + healing)
        }
      });
    }
  }

  syncTemporaryHP(entityId: string, tempHP: number): void {
    const currentState = this.syncEngine.getEntityState(entityId);
    if (currentState) {
      this.syncEngine.updateEntityState(entityId, {
        health: {
          ...currentState.health,
          temporary: Math.max(currentState.health.temporary, tempHP) // Temp HP doesn't stack
        }
      });
    }
  }
}

