/**
 * Automated optimization engine for VTT systems
 */

import { Profiler, PerformanceMetrics } from './Profiler';
import { BenchmarkRunner, SystemBenchmarkSuite} from './Benchmarks';

export interface OptimizationRule {
  name: string;
  condition: (_metrics: PerformanceMetrics[], systemState: any) => boolean;
  action: (systemState: any) => Promise<void>;
  priority: number;
  enabled: boolean;
}

export interface OptimizationConfig {
  rules: OptimizationRule[];
  thresholds: {
    criticalLatency: number;
    memoryWarning: number;
    fpsTarget: number;
    networkLatency: number;
  };
  autoOptimize: boolean;
  maxOptimizationsPerSecond: number;
}

export interface SystemState {
  renderer: {
    objectsRendered: number;
    drawCalls: number;
    fps: number;
    memoryUsage: number;
  };
  physics: {
    bodiesActive: number;
    collisionChecks: number;
    simulationTime: number;
  };
  network: {
    messagesPerSecond: number;
    latency: number;
    bandwidth: number;
  };
  ecs: {
    entitiesCount: number;
    systemsActive: number;
    updateTime: number;
  };
}

export class OptimizationEngine {
  private profiler: Profiler;
  private benchmarkRunner: BenchmarkRunner;
  private config: OptimizationConfig;
  private optimizationHistory: Array<{
    timestamp: number;
    rule: string;
    impact: number;
    success: boolean;
  }> = [];
  private lastOptimization = 0;
  private isOptimizing = false;

  constructor(profiler: Profiler, config: OptimizationConfig) {
    this.profiler = profiler;
    this.benchmarkRunner = new BenchmarkRunner(profiler);
    this.config = config;
  }

  // Automatic optimization monitoring
  startAutoOptimization(): void {
    if (!this.config.autoOptimize) return;

    setInterval(async () => {
      await this.runOptimizationCycle();
    }, 1000); // Check every second
  }

  private async runOptimizationCycle(): Promise<void> {
    if (this.isOptimizing) return;

    const now = Date.now();
    const timeSinceLastOptimization = now - this.lastOptimization;
    const minInterval = 1000 / this.config.maxOptimizationsPerSecond;

    if (timeSinceLastOptimization < minInterval) return;

    this.isOptimizing = true;

    try {
      const metrics = this.profiler.getProfileSummary();
      const systemState = await this.getSystemState();

      const applicableRules = this.config.rules
        .filter(rule => rule.enabled && rule.condition(metrics, systemState))
        .sort((a, b) => b.priority - a.priority);

      if (applicableRules.length > 0) {
        const rule = applicableRules[0];
        if (rule) {
          await this.applyOptimization(rule, systemState);
        }
      }
    } finally {
      this.isOptimizing = false;
    }
  }

  private async applyOptimization(rule: OptimizationRule, systemState: SystemState): Promise<void> {
    const startTime = Date.now();
    let success = false;

    try {
      await rule.action(systemState);
      success = true;
      this.lastOptimization = Date.now();
    } catch (error) {
      console.error(`Optimization rule '${rule.name}' failed:`, error);
    }

    const impact = Date.now() - startTime;
    this.optimizationHistory.push({
      timestamp: startTime,
      rule: rule.name,
      impact,
      success
    });

    // Limit history size
    if (this.optimizationHistory.length > 1000) {
      this.optimizationHistory = this.optimizationHistory.slice(-1000);
    }
  }

  // System state monitoring
  private async getSystemState(): Promise<SystemState> {
    // This would integrate with actual VTT systems
    // For now, return simulated state
    return {
      renderer: {
        objectsRendered: Math.floor(Math.random() * 1000) + 100,
        drawCalls: Math.floor(Math.random() * 50) + 10,
        fps: Math.random() * 60 + 30,
        memoryUsage: Math.random() * 100 + 50
      },
      physics: {
        bodiesActive: Math.floor(Math.random() * 200) + 50,
        collisionChecks: Math.floor(Math.random() * 500) + 100,
        simulationTime: Math.random() * 16 + 2
      },
      network: {
        messagesPerSecond: Math.floor(Math.random() * 100) + 20,
        latency: Math.random() * 100 + 10,
        bandwidth: Math.random() * 1000 + 500
      },
      ecs: {
        entitiesCount: Math.floor(Math.random() * 500) + 100,
        systemsActive: Math.floor(Math.random() * 10) + 5,
        updateTime: Math.random() * 10 + 1
      }
    };
  }

  // Predefined optimization rules
  createDefaultOptimizationRules(): OptimizationRule[] {
    return [
      {
        name: 'Reduce Renderer Quality',
        condition: (_metrics, state: SystemState) => {
          return state.renderer.fps < this.config.thresholds.fpsTarget &&
                 state.renderer.objectsRendered > 500;
        },
        action: async (state: SystemState) => {
          // Reduce render quality, enable LOD, increase culling distance
          console.log('Applying renderer quality reduction optimization');
          state.renderer.objectsRendered = Math.floor(state.renderer.objectsRendered * 0.8);
        },
        priority: 8,
        enabled: true
      },

      {
        name: 'Optimize Physics Simulation',
        condition: (_metrics, state: SystemState) => {
          return state.physics.simulationTime > 16 && // Taking more than 16ms
                 state.physics.bodiesActive > 100;
        },
        action: async (state: SystemState) => {
          // Reduce physics accuracy, increase timestep, disable sleeping bodies
          console.log('Applying physics optimization');
          state.physics.bodiesActive = Math.floor(state.physics.bodiesActive * 0.9);
        },
        priority: 7,
        enabled: true
      },

      {
        name: 'Network Message Batching',
        condition: (_metrics, state: SystemState) => {
          return state.network.messagesPerSecond > 50 &&
                 state.network.latency > this.config.thresholds.networkLatency;
        },
        action: async (state: SystemState) => {
          // Enable message batching, increase batch size
          console.log('Applying network message batching');
          state.network.messagesPerSecond = Math.floor(state.network.messagesPerSecond * 0.7);
        },
        priority: 6,
        enabled: true
      },

      {
        name: 'ECS System Frequency Reduction',
        condition: (_metrics, state: SystemState) => {
          return state.ecs.updateTime > 10 && // Taking more than 10ms
                 state.ecs.systemsActive > 8;
        },
        action: async (state: SystemState) => {
          // Reduce update frequency for non-critical systems
          console.log('Applying ECS system frequency reduction');
          state.ecs.updateTime = Math.max(1, state.ecs.updateTime * 0.8);
        },
        priority: 5,
        enabled: true
      },

      {
        name: 'Memory Garbage Collection',
        condition: (_metrics, state: SystemState) => {
          return state.renderer.memoryUsage > this.config.thresholds.memoryWarning;
        },
        action: async (state: SystemState) => {
          // Force garbage collection, clear caches
          console.log('Applying memory optimization');
          if (global.gc) {
            global.gc();
          }
          state.renderer.memoryUsage = Math.max(10, state.renderer.memoryUsage * 0.7);
        },
        priority: 9,
        enabled: true
      },

      {
        name: 'Audio Quality Reduction',
        condition: (_metrics, state: SystemState) => {
          return state.renderer.fps < this.config.thresholds.fpsTarget * 0.8; // 80% of target
        },
        action: async (_state: SystemState) => {
          // Reduce audio quality, disable spatial audio
          console.log('Applying audio quality reduction');
        },
        priority: 3,
        enabled: true
      },

      {
        name: 'Animation Frame Skipping',
        condition: (_metrics, state: SystemState) => {
          return state.renderer.fps < this.config.thresholds.fpsTarget * 0.6; // 60% of target
        },
        action: async (_state: SystemState) => {
          // Skip animation frames, reduce animation quality
          console.log('Applying animation frame skipping');
        },
        priority: 4,
        enabled: true
      }
    ];
  }

  // Performance analysis and recommendations
  async analyzePerformance(): Promise<{
    bottlenecks: Array<{ system: string; severity: number; description: string }>;
    recommendations: string[];
    optimizationOpportunities: Array<{ rule: string; expectedImpact: number }>;
  }> {
    const metrics = this.profiler.getProfileSummary();
    const systemState = await this.getSystemState();
    const bottlenecks = this.profiler.findBottlenecks();

    const analysis: {
      bottlenecks: Array<{ system: string; severity: number; description: string }>;
      recommendations: string[];
      optimizationOpportunities: Array<{ rule: string; expectedImpact: number }>;
    } = {
      bottlenecks: bottlenecks.map(b => ({
        system: this.categorizeBottleneck(b.name),
        severity: this.calculateSeverity(b.avgTime, b.impact),
        description: `${b.name} averaging ${b.avgTime.toFixed(2)}ms (${b.count} samples)`
      })),
      recommendations: [],
      optimizationOpportunities: []
    };

    // Generate recommendations
    if (systemState.renderer.fps < this.config.thresholds.fpsTarget) {
      analysis.recommendations.push('Frame rate below target - consider reducing render quality or object count');
    }

    if (systemState.physics.simulationTime > 16) {
      analysis.recommendations.push('Physics simulation taking >16ms - reduce body count or accuracy');
    }

    if (systemState.network.latency > this.config.thresholds.networkLatency) {
      analysis.recommendations.push('High network latency detected - enable message batching or compression');
    }

    // Find applicable optimization rules
    for (const rule of this.config.rules) {
      if (rule.enabled && rule.condition(metrics, systemState)) {
        analysis.optimizationOpportunities.push({
          rule: rule.name,
          expectedImpact: this.estimateOptimizationImpact(rule, systemState)
        });
      }
    }

    return analysis;
  }

  private categorizeBottleneck(name: string): string {
    if (name.toLowerCase().includes('render')) return 'Renderer';
    if (name.toLowerCase().includes('physics')) return 'Physics';
    if (name.toLowerCase().includes('network')) return 'Network';
    if (name.toLowerCase().includes('ecs')) return 'ECS';
    return 'System';
  }

  private calculateSeverity(averageTime: number, severity: string): number {
    const severityMap: Record<string, number> = { low: 1, medium: 2, high: 3, critical: 4 };
    return severityMap[severity] || 1;
  }

  private estimateOptimizationImpact(rule: OptimizationRule, state: SystemState): number {
    // Simple impact estimation based on rule priority and current state
    const baseImpact = rule.priority * 0.1;

    // Adjust based on system state
    if (rule.name.includes('Renderer') && state.renderer.fps < 30) {
      return baseImpact * 2;
    }
    if (rule.name.includes('Physics') && state.physics.simulationTime > 20) {
      return baseImpact * 1.5;
    }

    return baseImpact;
  }

  // Manual optimization triggers
  async optimizeRenderer(aggressiveness: number = 0.5): Promise<void> {
    const rules = this.config.rules.filter(r => r.name.includes('Render'));
    const systemState = await this.getSystemState();

    for (const rule of rules) {
      if (aggressiveness > 0.3 || rule.condition([], systemState)) {
        await this.applyOptimization(rule, systemState);
      }
    }
  }

  async optimizePhysics(aggressiveness: number = 0.5): Promise<void> {
    const rules = this.config.rules.filter(r => r.name.includes('Physics'));
    const systemState = await this.getSystemState();

    for (const rule of rules) {
      if (aggressiveness > 0.3 || rule.condition([], systemState)) {
        await this.applyOptimization(rule, systemState);
      }
    }
  }

  async optimizeNetwork(aggressiveness: number = 0.5): Promise<void> {
    const rules = this.config.rules.filter(r => r.name.includes('Network'));
    const systemState = await this.getSystemState();

    for (const rule of rules) {
      if (aggressiveness > 0.3 || rule.condition([], systemState)) {
        await this.applyOptimization(rule, systemState);
      }
    }
  }

  // Configuration management
  addOptimizationRule(rule: OptimizationRule): void {
    this.config.rules.push(rule);
  }

  removeOptimizationRule(name: string): boolean {
    const index = this.config.rules.findIndex(r => r.name === name);
    if (index !== -1) {
      this.config.rules.splice(index, 1);
      return true;
    }
    return false;
  }

  updateThresholds(thresholds: Partial<OptimizationConfig['thresholds']>): void {
    this.config.thresholds = { ...this.config.thresholds, ...thresholds };
  }

  // Reporting and analytics
  getOptimizationHistory(timeWindowMs?: number): typeof this.optimizationHistory {
    if (!timeWindowMs) return [...this.optimizationHistory];

    const cutoff = Date.now() - timeWindowMs;
    return this.optimizationHistory.filter(h => h.timestamp >= cutoff);
  }

  getOptimizationStats(): {
    totalOptimizations: number;
    successRate: number;
    averageImpact: number;
    mostUsedRules: Array<{ rule: string; count: number }>;
  } {
    const total = this.optimizationHistory.length;
    const successful = this.optimizationHistory.filter(h => h.success).length;
    const averageImpact = this.optimizationHistory.reduce((sum, h) => sum + h.impact, 0) / total || 0;

    const ruleCounts = new Map<string, number>();
    for (const history of this.optimizationHistory) {
      ruleCounts.set(history.rule, (ruleCounts.get(history.rule) || 0) + 1);
    }

    const mostUsedRules = Array.from(ruleCounts.entries())
      .map(([rule, count]) => ({ rule, count }))
      .sort((a, b) => b.count - a.count)
      .slice(0, 5);

    return {
      totalOptimizations: total,
      successRate: successful / total || 0,
      averageImpact,
      mostUsedRules
    };
  }

  // Cleanup
  dispose(): void {
    this.optimizationHistory = [];
  }
}

/**
 * Performance profiling and measurement utilities for VTT systems
 */

import perfNow from 'performance-now';

export interface ProfileEntry {
  name: string;
  startTime: number;
  endTime?: number;
  duration?: number;
  metadata?: Record<string, any> | undefined;
  children: ProfileEntry[];
  parent?: ProfileEntry;
}

export interface PerformanceMetrics {
  name: string;
  samples: number;
  mean: number;
  median: number;
  min: number;
  max: number;
  standardDeviation: number;
  percentiles: {
    p50: number;
    p90: number;
    p95: number;
    p99: number;
  };
}

export interface SystemResourceMetrics {
  timestamp: number;
  memory: {
    used: number;
    total: number;
    heap: number;
    external: number;
    rss: number;
  };
  cpu: {
    user: number;
    system: number;
    idle: number;
  };
  gc?: {
    collections: number;
    duration: number;
    type: string;
  };
}

export class Profiler {
  private activeProfiles = new Map<string, ProfileEntry>();
  private completedProfiles: ProfileEntry[] = [];
  private performanceData = new Map<string, number[]>();
  private resourceHistory: SystemResourceMetrics[] = [];
  private maxHistorySize = 10000;
  private gcObserver?: PerformanceObserver;

  constructor() {
    this.setupGCMonitoring();
    this.startResourceMonitoring();
  }

  // Profiling API
  startProfile(name: string, metadata?: Record<string, any>): ProfileEntry {
    const profile: ProfileEntry = {
      name,
      startTime: perfNow(),
      metadata: metadata || undefined,
      children: []
    };

    this.activeProfiles.set(name, profile);
    return profile;
  }

  endProfile(name: string): ProfileEntry | null {
    const profile = this.activeProfiles.get(name);
    if (!profile) {
      console.warn(`No active profile found for: ${name}`);
      return null;
    }

    const endTime = perfNow();
    profile.endTime = endTime;
    profile.duration = endTime - profile.startTime;

    this.activeProfiles.delete(name);
    this.completedProfiles.push(profile);

    // Store performance data for statistics
    if (!this.performanceData.has(name)) {
      this.performanceData.set(name, []);
    }
    this.performanceData.get(name)!.push(profile.duration);

    // Cleanup old data
    this.cleanupOldData();

    return profile;
  }

  // Decorator for automatic profiling
  profile<T extends (...args: any[]) => any>(
    name: string,
    fn: T,
    metadata?: Record<string, any>
  ): T {
    return ((...args: any[]) => {
      this.startProfile(name, metadata);
      try {
        const result = fn(...args);

        if (result instanceof Promise) {
          return result.finally(() => {
            this.endProfile(name);
          });
        } else {
          this.endProfile(name);
          return result;
        }
      } catch (error) {
        this.endProfile(name);
        throw error;
      }
    }) as T;
  }

  // Async profiling wrapper
  async profileAsync<T>(
    name: string,
    _operation: () => Promise<T>,
    metadata?: Record<string, any>
  ): Promise<T> {
    this.startProfile(name, metadata);
    try {
      const result = await _operation();
      return result;
    } finally {
      this.endProfile(name);
    }
  }

  // Performance measurement and analysis
  measureFunction<T extends (...args: any[]) => any>(
    name: string,
    fn: T,
    iterations: number = 1000
  ): PerformanceMetrics {
    const durations: number[] = [];

    for (let i = 0; i < iterations; i++) {
      const start = perfNow();
      fn();
      const end = perfNow();
      durations.push(end - start);
    }

    return this.calculateMetrics(name, durations);
  }

  async measureAsyncFunction<T>(
    name: string,
    _fn: () => Promise<T>,
    iterations: number = 100
  ): Promise<PerformanceMetrics> {
    const durations: number[] = [];

    for (let i = 0; i < iterations; i++) {
      const start = perfNow();
      await _fn();
      const end = perfNow();
      durations.push(end - start);
    }

    return this.calculateMetrics(name, durations);
  }

  private calculateMetrics(name: string, durations: number[]): PerformanceMetrics {
    const sorted = durations.sort((a, b) => a - b);
    const sum = durations.reduce((a, b) => a + b, 0);
    const mean = sum / durations.length;

    const variance = durations.reduce((acc, val) => acc + Math.pow(val - mean, 2), 0) / durations.length;
    const standardDeviation = Math.sqrt(variance);

    return {
      name,
      samples: durations.length,
      mean,
      median: sorted[Math.floor(sorted.length / 2)] || 0,
      min: sorted[0] || 0,
      max: sorted[sorted.length - 1] || 0,
      standardDeviation,
      percentiles: {
        p50: sorted[Math.floor(sorted.length * 0.5)] || 0,
        p90: sorted[Math.floor(sorted.length * 0.9)] || 0,
        p95: sorted[Math.floor(sorted.length * 0.95)] || 0,
        p99: sorted[Math.floor(sorted.length * 0.99)] || 0
      }
    };
  }

  // System resource monitoring
  private setupGCMonitoring(): void {
    if (typeof PerformanceObserver !== 'undefined') {
      try {
        this.gcObserver = new PerformanceObserver((list) => {
          const entries = list.getEntries();
          for (const entry of entries) {
            if (entry.entryType === 'measure' && entry.name.includes('gc')) {
              // GC event detected
              this.recordGCEvent(entry);
            }
          }
        });
        this.gcObserver.observe({ entryTypes: ['measure', 'mark'] });
      } catch (error) {
        console.warn('GC monitoring not available:', error);
      }
    }
  }

  private recordGCEvent(entry: PerformanceEntry): void {
    const latestMetrics = this.resourceHistory[this.resourceHistory.length - 1];
    if (latestMetrics) {
      latestMetrics.gc = {
        collections: (latestMetrics.gc?.collections || 0) + 1,
        duration: entry.duration,
        type: entry.name
      };
    }
  }

  private startResourceMonitoring(): void {
    setInterval(() => {
      this.collectResourceMetrics();
    }, 1000); // Collect every second
  }

  private collectResourceMetrics(): void {
    const metrics: SystemResourceMetrics = {
      timestamp: Date.now(),
      memory: this.getMemoryMetrics(),
      cpu: this.getCPUMetrics()
    };

    this.resourceHistory.push(metrics);

    // Limit history size
    if (this.resourceHistory.length > this.maxHistorySize) {
      this.resourceHistory = this.resourceHistory.slice(-this.maxHistorySize);
    }
  }

  private getMemoryMetrics() {
    if (typeof process !== 'undefined' && process.memoryUsage) {
      const usage = process.memoryUsage();
      return {
        used: usage.heapUsed,
        total: usage.heapTotal,
        heap: usage.heapUsed,
        external: usage.external,
        rss: usage.rss
      };
    }

    // Browser fallback
    return {
      used: 0,
      total: 0,
      heap: 0,
      external: 0,
      rss: 0
    };
  }

  private getCPUMetrics() {
    if (typeof process !== 'undefined' && process.cpuUsage) {
      const usage = process.cpuUsage();
      return {
        user: usage.user / 1000000, // Convert to milliseconds
        system: usage.system / 1000000,
        idle: 0
      };
    }

    return { user: 0, system: 0, idle: 0 };
  }

  private cleanupOldData(): void {
    // Keep only recent completed profiles
    if (this.completedProfiles.length > 1000) {
      this.completedProfiles = this.completedProfiles.slice(-1000);
    }

    // Cleanup performance data
    for (const [name, data] of this.performanceData) {
      if (data.length > 1000) {
        this.performanceData.set(name, data.slice(-1000));
      }
    }
  }

  // Analysis and reporting
  getProfileSummary(name?: string): PerformanceMetrics[] {
    const results: PerformanceMetrics[] = [];

    for (const [profileName, durations] of this.performanceData) {
      if (!name || profileName === name) {
        results.push(this.calculateMetrics(profileName, durations));
      }
    }
    const sortedByMean = results.sort((a, b) => b.mean - a.mean);
    return sortedByMean;
  }

  getResourceMetrics(timeWindowMs?: number): SystemResourceMetrics[] {
    if (!timeWindowMs) {
      return [...this.resourceHistory];
    }

    const cutoff = Date.now() - timeWindowMs;
    return this.resourceHistory.filter(m => m.timestamp >= cutoff);
  }

  // ... (rest of the code remains the same)

  generateReport(): {
    summary: PerformanceMetrics[];
    bottlenecks: Array<{
      name: string;
      averageTime: number;
      samples: number;
      severity: 'low' | 'medium' | 'high' | 'critical';
    }>;
    resourceUsage: {
      currentMemory: number;
      averageMemory: number;
      peakMemory: number;
      memoryTrend: 'increasing' | 'stable' | 'decreasing';
    };
    recommendations: string[];
  } {
    const summary = this.getProfileSummary();
    const bottlenecks = this.findBottlenecks();
    const recentMetrics = this.getResourceMetrics(60000); // Last minute

    const memoryHistory = this.resourceHistory.map((m) => m.memory.used);
    const currentMemory = memoryHistory[memoryHistory.length - 1] || 0;
    const recentMemory = memoryHistory.slice(-10).reduce((a, b) => a + b, 0) / Math.min(10, memoryHistory.length) || 0;
    const peakMemory = Math.max(...memoryHistory) || 0;

    // Simple trend analysis
    const firstHalf = memoryHistory.slice(0, Math.floor(memoryHistory.length / 2));
    const secondHalf = memoryHistory.slice(Math.floor(memoryHistory.length / 2));
    const firstAvg = firstHalf.reduce((a, b) => a + b, 0) / firstHalf.length || 0;
    const secondAvg = secondHalf.reduce((a, b) => a + b, 0) / secondHalf.length || 0;

    let memoryTrend: 'increasing' | 'stable' | 'decreasing' = 'stable';
    if (secondAvg > firstAvg * 1.1) memoryTrend = 'increasing';
    else if (secondAvg < firstAvg * 0.9) memoryTrend = 'decreasing';

    const recommendations = this.generateRecommendations(bottlenecks, memoryTrend);

    return {
      summary,
      bottlenecks: bottlenecks.sort((a, b) => a.count - b.count)
        .slice(0, 3)
        .map((b) => ({
          name: b.name,
          averageTime: b.avgTime,
          samples: b.count,
          severity: b.impact === 'high' ? 'critical' : 'medium'
        })),
      resourceUsage: {
        currentMemory,
        averageMemory: firstAvg,
        peakMemory,
        memoryTrend
      },
      recommendations
    };
  }

  public findBottlenecks() {
    const bottlenecks = [];

    for (const [name, times] of this.performanceData.entries()) {
      if (times.length === 0) continue;

      const avgTime = times.reduce((a, b) => a + b, 0) / times.length;
      const count = times.length;

      if (avgTime > 100) { // More than 100ms
        bottlenecks.push({
          name,
          impact: 'high',
          avgTime,
          count
        });
      } else if (avgTime > 50) { // More than 50ms
        bottlenecks.push({
          name,
          impact: 'medium', 
          avgTime,
          count
        });
      }
    }

    return bottlenecks.sort((a, b) => b.avgTime - a.avgTime);
  }

  private generateRecommendations(
    bottlenecks: any[],
    memoryTrend: string
  ): string[] {
    const recommendations: string[] = [];

    if (bottlenecks.length > 0) {
      recommendations.push(`Found ${bottlenecks.length} performance bottlenecks requiring attention`);

      const longRunning = bottlenecks.filter((b) => b.impact === 'high');
      if (longRunning.length > 0) {
        recommendations.push(`CRITICAL: ${longRunning.map(b => b.name).join(', ')} are taking >100ms per operation`);
      }
    }

    if (memoryTrend === 'increasing') {
      recommendations.push('Memory usage is trending upward - check for memory leaks');
    }

    if (bottlenecks.some(b => b.name.includes('render'))) {
      recommendations.push('Consider render optimization: object pooling, culling, or LOD systems');
    }

    if (bottlenecks.some(b => b.name.includes('network'))) {
      recommendations.push('Network operations are slow - consider batching, compression, or caching');
    }

    if (recommendations.length === 0) {
      recommendations.push('No significant performance issues detected');
    }

    return recommendations;
  }

  // Cleanup
  dispose(): void {
    if (this.gcObserver) {
      this.gcObserver.disconnect();
    }

    this.activeProfiles.clear();
    this.completedProfiles = [];
    this.performanceData.clear();
    this.resourceHistory = [];
  }
}

