}

export interface PhysicsConstraint {
  id: string;
  type: 'immobilize' | 'slow' | 'entangle' | 'web';
  strength: number;
  duration: number;
  sourceSpell?: string;
  expiresAt: number;
}

export interface SpellProjectile {
