  spellId: string;
  casterId: string;
  physicsBodyId: number;
  targetIds: string[];
  onHit: (_targetId: string) => void;
  onExpire: () => void;
}

export class PhysicsSpellBridge extends EventEmitter {
  private physicsWorld: PhysicsWorld;
  private spellEngine: SpellEngine;
  private tokens: Map<string, PhysicsAwareToken> = new Map();
  private projectiles: Map<string, SpellProjectile> = new Map();
  private barriers: Map<string, { bodyIds: number[]; expiresAt: number }> = new Map();
  private activeConstraints: Map<string, PhysicsConstraint[]> = new Map();

  constructor(physicsWorld: PhysicsWorld, spellEngine: SpellEngine) {
    super();
    this.physicsWorld = physicsWorld;
    this.spellEngine = spellEngine;
    this.setupPhysicsIntegration();
  }

  /**
   * Register a token with physics awareness
   */
  registerToken(tokenId: string, position: Vector2, size: Vector2): void {
    const body = this.physicsWorld.createBody({
      position,
      type: 'dynamic'
    });

    const token: PhysicsAwareToken = {
      id: tokenId,
      physicsBodyId: body.id,
      position,
      size,
      canMove: true,
      movementSpeed: 30, // feet per round
      constraints: []
    };

    this.tokens.set(tokenId, token);
    this.emit('tokenRegistered', tokenId);
  }

  /**
   * Cast spell with physics integration
   */
  async castSpellWithPhysics(
    spell: PhysicsSpellEffect,
    caster: any,
    targets: string[],
    spellLevel?: number,
    position?: Vector2
  ): Promise<CastingResult & { physicsEffects: any[] }> {
    // Cast spell normally first
    const spellId = spell.id || 'unknown_spell';
    const spellForEngine = { ...spell, id: spellId, ritual: spell.ritual || false };
    const result = this.spellEngine.castSpell(spellForEngine, caster, targets, spellLevel, position);

    if (!result.success) {
      return { ...result, physicsEffects: [] };
    }

    const physicsEffects: any[] = [];

    // Apply physics effects
    if (spell.physics) {
      switch (spell.physics.type) {
        case 'force':
          physicsEffects.push(...this.applyForceEffect(spell.physics.force!, targets));
          break;
        case 'teleport':
          physicsEffects.push(...this.applyTeleportEffect(spell.physics.teleport!, targets, position!));
          break;
        case 'constraint':
          physicsEffects.push(...this.applyConstraintEffect(spell.physics.constraint!, targets));
          break;
        case 'projectile':
          physicsEffects.push(this.createProjectile(spell, caster, targets, position!));
          break;
        case 'area_barrier':
          physicsEffects.push(this.createAreaBarrier(spell.physics.barrier!, position!));
          break;
        case 'movement_modifier':
          physicsEffects.push(...this.applyMovementModifier(spell.physics.movementModifier!, targets));
          break;
      }
    }

    return { ...result, physicsEffects };
  }

  /**
   * Apply force to targets (e.g., Thunderwave, Eldritch Blast)
   */
  private applyForceEffect(force: any, targets: string[]): any[] {
    const effects: any[] = [];

    for (const targetId of targets) {
      const token = this.tokens.get(targetId);
      if (!token) continue;

      const body = this.physicsWorld.getBody(token.physicsBodyId);
      if (!body) continue;

      // Calculate force direction and magnitude
      const forceVector = force.direction || { x: 0, y: -1 }; // Default up
      const magnitude = force.magnitude || 100;

      // Apply impulse
      body.velocity.x += forceVector.x * magnitude;
      body.velocity.y += forceVector.y * magnitude;

      effects.push({
        type: 'force_applied',
        targetId,
        force: { x: forceVector.x * magnitude, y: forceVector.y * magnitude }
      });

      this.emit('forceApplied', targetId, forceVector, magnitude);
    }

    return effects;
  }

  /**
   * Apply teleport effect (e.g., Misty Step, Dimension Door)
   */
  private applyTeleportEffect(teleport: any, targets: string[], position: Vector2): any[] {
    const effects: any[] = [];

    for (const targetId of targets) {
      const token = this.tokens.get(targetId);
      if (!token) continue;

      const body = this.physicsWorld.getBody(token.physicsBodyId);
      if (!body) continue;

      // Check line of sight if required
      if (teleport.requiresLineOfSight) {
        const raycast = this.physicsWorld.raycast(
          token.position,
          { x: position.x - token.position.x, y: position.y - token.position.y },
          teleport.range
        );

        if (raycast.hit && raycast.body) {
          continue; // Blocked by obstacle
        }
      }

      // Teleport
      body.position.x = position.x;
      body.position.y = position.y;
      token.position = position;

      effects.push({
        type: 'teleported',
        targetId,
        fromPosition: token.position,
        toPosition: position
      });

      this.emit('tokenTeleported', targetId, token.position, position);
    }

    return effects;
  }

  /**
   * Apply movement constraints (e.g., Web, Entangle, Hold Person)
   */
  private applyConstraintEffect(constraint: any, targets: string[]): any[] {
    const effects: any[] = [];

    for (const targetId of targets) {
      const token = this.tokens.get(targetId);
      if (!token) continue;

      const physicsConstraint: PhysicsConstraint = {
        id: `${Date.now()}_${Math.random()}`,
        type: constraint.type,
        strength: constraint.strength,
        duration: constraint.duration,
        expiresAt: Date.now() + (constraint.duration * 1000)
      };

      // Add constraint to token
      token.constraints.push(physicsConstraint);

      // Store in active constraints
      if (!this.activeConstraints.has(targetId)) {
        this.activeConstraints.set(targetId, []);
      }
      this.activeConstraints.get(targetId)!.push(physicsConstraint);

      // Modify physics body based on constraint type
      const body = this.physicsWorld.getBody(token.physicsBodyId);
      if (body) {
        switch (constraint.type) {
          case 'immobilize':
            body.velocity.x = 0;
            body.velocity.y = 0;
            token.canMove = false;
            break;
          case 'slow':
            token.movementSpeed *= (1 - constraint.strength);
            break;
          case 'entangle':
            body.config.friction = Math.min(1.0, body.config.friction + constraint.strength);
            break;
        }
      }

      effects.push({
        type: 'constraint_applied',
        targetId,
        constraint: physicsConstraint
      });

      this.emit('constraintApplied', targetId, physicsConstraint);
    }

    return effects;
  }

  /**
   * Create spell projectile (e.g., Magic Missile, Firebolt)
   */
  private createProjectile(spell: PhysicsSpellEffect, caster: any, targets: string[], position: Vector2): any {
    const projectileId = `proj_${Date.now()}_${Math.random()}`;
    const physics = spell.physics!.projectile!;

    // Create physics body for projectile
    const projectileBody = this.physicsWorld.createBody({
      position,
      width: 0.5,
      height: 0.5,
      isStatic: false,
      mass: 0.1,
      restitution: physics.maxBounces ? 0.8 : 0.0,
      friction: 0.1,
      isTrigger: true
    });

    // Calculate initial velocity towards first target
    const firstTarget = targets[0];
    const targetToken = this.tokens.get(firstTarget);

    if (targetToken) {
      const direction = {
        x: targetToken.position.x - position.x,
        y: targetToken.position.y - position.y
      };
      const length = Math.sqrt(direction.x * direction.x + direction.y * direction.y);

      if (length > 0) {
        projectileBody.velocity.x = (direction.x / length) * physics.speed;
        projectileBody.velocity.y = (direction.y / length) * physics.speed;
      }
    }

    const projectile: SpellProjectile = {
      id: projectileId,
      spellId: spell.id || `spell_${Date.now()}`,
      casterId: caster.id,
      physicsBodyId: projectileBody.id,
      targetIds: targets,
      onHit: (_targetId: string) => {
        this.handleProjectileHit(projectileId, targetId, spell);
      },
      onExpire: () => {
        this.cleanupProjectile(projectileId);
      }
    };

    this.projectiles.set(projectileId, projectile);

    // Set up collision detection
    this.physicsWorld.on('collision', (collision: any) => {
      if (collision.bodyA.id === projectileBody.id || collision.bodyB.id === projectileBody.id) {
        const otherBodyId = collision.bodyA.id === projectileBody.id ? collision.bodyB.id : collision.bodyA.id;

        // Find token by physics body
        for (const [tokenId, token] of this.tokens.entries()) {
          if (token.physicsBodyId === otherBodyId && targets.includes(tokenId)) {
            projectile.onHit(tokenId);
            break;
          }
        }
      }
    });

    this.emit('projectileCreated', projectileId, spell);

    return {
      type: 'projectile_created',
      projectileId,
      spellId: spell.id
    };
  }

  /**
   * Create area barrier (e.g., Wall of Force, Wall of Fire)
   */
  private createAreaBarrier(barrier: any, position: Vector2): any {
    const barrierId = `barrier_${Date.now()}_${Math.random()}`;
    const bodyIds: number[] = [];

    // Create multiple physics bodies to form a wall
    const segments = Math.ceil(barrier.thickness / 2); // 2 foot segments

    for (let i = 0; i < segments; i++) {
      const segmentBody = this.physicsWorld.createBody({
        position: {
          x: position.x + (i * 2),
          y: position.y
        },
        width: 2,
        height: barrier.height,
        isStatic: true,
        mass: Infinity,
        isTrigger: !barrier.passable
      });

      bodyIds.push(segmentBody.id);
    }

    this.barriers.set(barrierId, {
      bodyIds,
      expiresAt: Date.now() + (barrier.duration * 1000)
    });

    this.emit('barrierCreated', barrierId, barrier);

    return {
      type: 'barrier_created',
      barrierId,
      bodyIds
    };
  }

  /**
   * Apply movement speed modifiers (e.g., Haste, Slow)
   */
  private applyMovementModifier(modifier: any, targets: string[]): any[] {
    const effects: any[] = [];

    for (const targetId of targets) {
      const token = this.tokens.get(targetId);
      if (!token) continue;

      const originalSpeed = token.movementSpeed;
      token.movementSpeed *= modifier.speedMultiplier;

      effects.push({
        type: 'movement_modified',
        targetId,
        originalSpeed,
        newSpeed: token.movementSpeed,
        duration: modifier.duration
      });

      // Schedule restoration
      setTimeout(() => {
        if (this.tokens.has(targetId)) {
          token.movementSpeed = originalSpeed;
          this.emit('movementModifierExpired', targetId);
        }
      }, modifier.duration * 1000);

      this.emit('movementModified', targetId, modifier);
    }

    return effects;
  }

  /**
   * Handle projectile collision
   */
  private handleProjectileHit(projectileId: string, targetId: string, spell: PhysicsSpellEffect): void {
    const projectile = this.projectiles.get(projectileId);
    if (!projectile) return;

    // Apply spell effects to target
    this.spellEngine.castSpell(spell, { id: projectile.casterId }, [targetId]);

    this.emit('projectileHit', projectileId, targetId);
    this.cleanupProjectile(projectileId);
  }

  /**
   * Clean up projectile
   */
  private cleanupProjectile(projectileId: string): void {
    const projectile = this.projectiles.get(projectileId);
    if (projectile) {
      this.physicsWorld.removeBody(projectile.physicsBodyId);
      this.projectiles.delete(projectileId);
      this.emit('projectileDestroyed', projectileId);
    }
  }

  /**
   * Update physics-spell integration (called each frame)
   */
  update(_deltaTime: number): void {
    const now = Date.now();

    // Update token positions from physics
    for (const [tokenId, token] of this.tokens.entries()) {
      const body = this.physicsWorld.getBody(token.physicsBodyId);
      if (body) {
        token.position.x = body.position.x;
        token.position.y = body.position.y;
      }

      // Clean up expired constraints
      token.constraints = token.constraints.filter(constraint => {
        if (now > constraint.expiresAt) {
          this.removeConstraint(tokenId, constraint);
          return false;
        }
        return true;
      });
    }

    // Clean up expired barriers
    for (const [barrierId, barrier] of this.barriers.entries()) {
      if (now > barrier.expiresAt) {
        for (const bodyId of barrier.bodyIds) {
          this.physicsWorld.removeBody(bodyId);
        }
        this.barriers.delete(barrierId);
        this.emit('barrierExpired', barrierId);
      }
    }
  }

  /**
   * Remove constraint from token
   */
  private removeConstraint(tokenId: string, constraint: PhysicsConstraint): void {
    const token = this.tokens.get(tokenId);
    if (!token) return;

    const body = this.physicsWorld.getBody(token.physicsBodyId);
    if (!body) return;

    // Restore original properties based on constraint type
    switch (constraint.type) {
      case 'immobilize':
        token.canMove = true;
        break;
      case 'slow':
        // This would need more sophisticated tracking to restore properly
        break;
      case 'entangle':
        body.config.friction = Math.max(0, body.config.friction - constraint.strength);
        break;
    }

    this.emit('constraintRemoved', tokenId, constraint);
  }

  /**
   * Setup physics world integration
   */
  private setupPhysicsIntegration(): void {
    // Listen for physics collisions
    this.physicsWorld.on('collision', (collision: any) => {
      this.handlePhysicsCollision(collision);
    });
  }

  /**
   * Handle physics collision for spell interactions
   */
  private handlePhysicsCollision(collision: any): void {
    // Find tokens involved in collision
    const tokenA = this.findTokenByBodyId(collision.bodyA.id);
    const tokenB = this.findTokenByBodyId(collision.bodyB.id);

    if (tokenA && tokenB) {
      this.emit('tokenCollision', tokenA.id, tokenB.id, collision);
    }
  }

  /**
   * Find token by physics body ID
   */
  private findTokenByBodyId(bodyId: number): PhysicsAwareToken | undefined {
    for (const token of this.tokens.values()) {
      if (token.physicsBodyId === bodyId) {
        return token;
      }
    }
    return undefined;
  }

  /**
   * Get token physics state
   */
  getTokenState(tokenId: string): PhysicsAwareToken | undefined {
    return this.tokens.get(tokenId);
  }

  /**
   * Check if area spell affects targets using physics collision
   */
  getTargetsInArea(center: Vector2, radius: number, height?: number): string[] {
    const targets: string[] = [];

    for (const [tokenId, token] of this.tokens.entries()) {
      const distance = Math.sqrt(
        Math.pow(token.position.x - center.x, 2) + 
        Math.pow(token.position.y - center.y, 2)
      );

      if (distance <= radius) {
        // Check line of sight using physics raycast
        const raycast = this.physicsWorld.raycast(
          center,
          { 
            x: token.position.x - center.x, 
            y: token.position.y - center.y 
          },
          distance
        );

        // If raycast hits the token or nothing, target is valid
        if (!raycast.hit || this.findTokenByBodyId(raycast.body?.id || -1)?.id === tokenId) {
          targets.push(tokenId);
        }
      }
    }

    return targets;
  }
}

export const _createPhysicsSpellBridge = (physicsWorld: PhysicsWorld, spellEngine: SpellEngine): PhysicsSpellBridge => {
  return new PhysicsSpellBridge(physicsWorld, spellEngine);
};

import { PlatformDetection, PlatformInfo, FeatureSupport } from './PlatformDetection';

export interface PlatformAdapter {
  init(): Promise<void>;
  dispose(): void;

  // Rendering
  createCanvas(width: number, height: number): HTMLCanvasElement | OffscreenCanvas;
  getWebGLContext(canvas: HTMLCanvasElement | OffscreenCanvas): WebGLRenderingContext | WebGL2RenderingContext | null;
  requestAnimationFrame(callback: FrameRequestCallback): number;
  cancelAnimationFrame(id: number): void;

  // Input
  addInputListeners(element: HTMLElement): void;
  removeInputListeners(element: HTMLElement): void;
  getGamepads(): Gamepad[];
  requestPointerLock(element: HTMLElement): Promise<void>;
  exitPointerLock(): void;

  // Fullscreen
  requestFullscreen(element: HTMLElement): Promise<void>;
  exitFullscreen(): Promise<void>;
  isFullscreen(): boolean;

  // Storage
  getStorage(type: 'local' | 'session' | 'indexed'): Storage | IDBFactory | null;

  // Audio
  createAudioContext(): AudioContext | null;

  // Network
  createWebSocket(url: string): WebSocket;
  createRTCConnection(config?: RTCConfiguration): RTCPeerConnection | null;

  // Performance
  getPerformanceMetrics(): PerformanceMetrics;

  // Device capabilities
  requestDeviceAccess(type: 'camera' | 'microphone' | 'geolocation'): Promise<any>;

  // Notifications
  requestNotificationPermission(): Promise<NotificationPermission>;
  showNotification(title: string, options?: NotificationOptions): Notification | null;

  // Clipboard
  readFromClipboard(): Promise<string>;
  writeToClipboard(text: string): Promise<void>;

  // Vibration (mobile)
  vibrate(pattern: number | number[]): boolean;
}

export interface PerformanceMetrics {
  fps: number;
  frameTime: number;
  memoryUsage: number;
  cpuUsage: number;
  batteryLevel?: number;
  thermalState?: 'nominal' | 'fair' | 'serious' | 'critical';
}

export interface AdaptiveSettings {
  graphics: {
    quality: 'low' | 'medium' | 'high';
    renderScale: number;
    shadowQuality: 'off' | 'low' | 'medium' | 'high';
    antialiasing: boolean;
    postProcessing: boolean;
    maxParticles: number;
    maxLights: number;
    lodBias: number;
  };
  audio: {
    enabled: boolean;
    quality: 'low' | 'medium' | 'high';
    spatialAudio: boolean;
    maxSources: number;
    compression: boolean;
  };
  network: {
    maxConnections: number;
    bufferSize: number;
    compression: boolean;
    prioritization: boolean;
  };
  input: {
    touchEnabled: boolean;
    gamepadEnabled: boolean;
    keyboardEnabled: boolean;
    mouseEnabled: boolean;
    gestureEnabled: boolean;
  };
  storage: {
    cacheSize: number; // MB
    offlineMode: boolean;
    syncEnabled: boolean;
  };
}

export class CrossPlatformAdapter implements PlatformAdapter {
  private platformDetection: PlatformDetection;
  private platformInfo: PlatformInfo;
  private featureSupport: FeatureSupport;
  private adaptiveSettings: AdaptiveSettings;
  private performanceMonitor: PerformanceMonitor;

  // Input handling
  private inputListeners = new Map<HTMLElement, InputEventListeners>();
  private gamepadConnected = false;
  private pointerLocked = false;

  // Audio context
  private audioContext: AudioContext | null = null;

  // Storage instances
  private indexedDB: IDBFactory | null = null;

  constructor() {
    this.platformDetection = new PlatformDetection();
    this.platformInfo = this.platformDetection.getPlatformInfo();
    this.featureSupport = this.platformDetection.getFeatureSupport();
    this.adaptiveSettings = this.generateAdaptiveSettings();
    this.performanceMonitor = new PerformanceMonitor();
  }

  public async init(): Promise<void> {
    // Initialize platform-specific features
    await this.initializeFeatures();

    // Setup performance monitoring
    this.performanceMonitor.start();

    // Setup gamepad monitoring
    if (this.featureSupport.gamepadAPI) {
      this.setupGamepadMonitoring();
    }

    // Setup visibility change handling
    this.setupVisibilityHandling();

    // Setup resize handling
    this.setupResizeHandling();

    console.log('CrossPlatformAdapter initialized:', this.platformInfo.os, this.platformInfo.browser);
  }

  public dispose(): void {
    this.performanceMonitor.stop();

    // Clean up audio context
    if (this.audioContext) {
      this.audioContext.close();
      this.audioContext = null;
    }

    // Clean up input listeners
    for (const [element, _listeners] of this.inputListeners) {
      this.removeInputListeners(element);
    }

    console.log('CrossPlatformAdapter disposed');
  }

  private async initializeFeatures(): Promise<void> {
    // Initialize IndexedDB if supported
    if (this.featureSupport.indexedDB) {
      this.indexedDB = window.indexedDB;
    }

    // Request permissions for supported features
    if (this.featureSupport.notifications) {
      await this.requestNotificationPermission();
    }

    // Initialize audio context on user interaction
    if (this.featureSupport.webAudio) {
      document.addEventListener('click', this.initAudioContext.bind(this), { once: true });
      document.addEventListener('touchstart', this.initAudioContext.bind(this), { once: true });
    }
  }

  private initAudioContext(): void {
    if (!this.audioContext && this.featureSupport.webAudio) {
      this.audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
    }
  }

  private generateAdaptiveSettings(): AdaptiveSettings {
    const recommended = this.platformDetection.getRecommendedSettings();

    return {
      graphics: {
        quality: recommended.graphics.quality,
        renderScale: this.platformInfo.device === 'mobile' ? 0.8 : 1.0,
        shadowQuality: recommended.graphics.shadows ? 'medium' : 'off',
        antialiasing: recommended.graphics.antialiasing,
        postProcessing: recommended.graphics.postProcessing,
        maxParticles: recommended.graphics.particleCount,
        maxLights: this.platformInfo.performance === 'high' ? 16 : 8,
        lodBias: this.platformInfo.performance === 'low' ? 2.0 : 1.0
      },
      audio: {
        enabled: recommended.audio.enabled,
        quality: this.platformInfo.performance,
        spatialAudio: recommended.audio.spatialAudio,
        maxSources: this.platformInfo.performance === 'high' ? 32 : 16,
        compression: recommended.audio.compression
      },
      network: {
        maxConnections: this.platformInfo.device === 'mobile' ? 4 : 8,
        bufferSize: this.platformInfo.memory >= 4096 ? 64 : 32,
        compression: this.platformInfo.performance === 'low',
        prioritization: true
      },
      input: {
        touchEnabled: this.featureSupport.touchEvents,
        gamepadEnabled: this.featureSupport.gamepadAPI,
        keyboardEnabled: this.platformInfo.device === 'desktop',
        mouseEnabled: this.platformInfo.device === 'desktop',
        gestureEnabled: this.platformInfo.device !== 'desktop'
      },
      storage: {
        cacheSize: recommended.storage.cacheSize,
        offlineMode: this.featureSupport.serviceWorkers,
        syncEnabled: this.featureSupport.indexedDB
      }
    };
  }

  // Rendering methods
  public createCanvas(width: number, height: number): HTMLCanvasElement | OffscreenCanvas {
    if (this.featureSupport.offscreenCanvas && typeof OffscreenCanvas !== 'undefined') {
      return new OffscreenCanvas(width, height);
    }

    const canvas = document.createElement('canvas');
    canvas.width = width;
    canvas.height = height;
    return canvas;
  }

  public getWebGLContext(canvas: HTMLCanvasElement | OffscreenCanvas): WebGLRenderingContext | WebGL2RenderingContext | null {
    const contextOptions = {
      alpha: false,
      antialias: this.adaptiveSettings.graphics.antialiasing,
      depth: true,
      stencil: false,
      premultipliedAlpha: false,
      preserveDrawingBuffer: false,
      powerPreference: this.platformInfo.performance === 'high' ? 'high-performance' as const : 'default' as const
    };

    if (this.featureSupport.webgl2) {
      const context = canvas.getContext('webgl2', contextOptions);
      if (context) return context;
    }

    if (this.featureSupport.webgl1) {
      return canvas.getContext('webgl', contextOptions) || 
             canvas.getContext('experimental-webgl', contextOptions);
    }

    return null;
  }

  public requestAnimationFrame(callback: FrameRequestCallback): number {
    return requestAnimationFrame(callback);
  }

  public cancelAnimationFrame(id: number): void {
    cancelAnimationFrame(id);
  }

  // Input methods
  public addInputListeners(element: HTMLElement): void {
    if (this.inputListeners.has(element)) return;

    const listeners = new InputEventListeners(element, this.adaptiveSettings.input);
    this.inputListeners.set(element, listeners);
  }

  public removeInputListeners(element: HTMLElement): void {
    const listeners = this.inputListeners.get(element);
    if (listeners) {
      listeners.dispose();
      this.inputListeners.delete(element);
    }
  }

  public getGamepads(): Gamepad[] {
    if (!this.featureSupport.gamepadAPI) return [];

    const gamepads = navigator.getGamepads();
    return Array.from(gamepads).filter((gamepad): gamepad is Gamepad => gamepad !== null);
  }

  public async requestPointerLock(element: HTMLElement): Promise<void> {
    if (!this.featureSupport.pointerLock) {
      throw new Error('Pointer lock not supported');
    }

    return new Promise((_resolve, __reject) => {
      const onLockChange = () => {
        if (document.pointerLockElement === element) {
          this.pointerLocked = true;
          document.removeEventListener('pointerlockchange', onLockChange);
          resolve();
        }
      };

      const onLockError = () => {
        document.removeEventListener('pointerlockerror', onLockError);
        reject(new Error('Pointer lock failed'));
      };

      document.addEventListener('pointerlockchange', onLockChange);
      document.addEventListener('pointerlockerror', onLockError);

      element.requestPointerLock();
    });
  }

  public exitPointerLock(): void {
    if (this.pointerLocked) {
      document.exitPointerLock();
      this.pointerLocked = false;
    }
  }

  // Fullscreen methods
  public async requestFullscreen(element: HTMLElement): Promise<void> {
    if (!this.featureSupport.fullscreen) {
      throw new Error('Fullscreen not supported');
    }

    const requestFS = element.requestFullscreen || 
                     (element as any).webkitRequestFullscreen || 
                     (element as any).mozRequestFullScreen;

    if (requestFS) {
      return requestFS.call(element);
    }

    throw new Error('Fullscreen method not available');
  }

  public async exitFullscreen(): Promise<void> {
    const exitFS = document.exitFullscreen || 
                   (document as any).webkitExitFullscreen || 
                   (document as any).mozCancelFullScreen;

    if (exitFS) {
      return exitFS.call(document);
    }
  }

  public isFullscreen(): boolean {
    return !!(document.fullscreenElement || 
              (document as any).webkitFullscreenElement || 
              (document as any).mozFullScreenElement);
  }

  // Storage methods
  public getStorage(type: 'local' | 'session' | 'indexed'): Storage | IDBFactory | null {
    switch (type) {
      case 'local':
        return this.platformInfo.localStorage ? localStorage : null;
      case 'session':
        return typeof sessionStorage !== 'undefined' ? sessionStorage : null;
      case 'indexed':
        return this.indexedDB;
      default:
        return null;
    }
  }

  // Audio methods
  public createAudioContext(): AudioContext | null {
    if (!this.audioContext) {
      this.initAudioContext();
    }
    return this.audioContext;
  }

  // Network methods
  public createWebSocket(url: string): WebSocket {
    return new WebSocket(url);
  }

  public createRTCConnection(config?: RTCConfiguration): RTCPeerConnection | null {
    if (!this.featureSupport.webRTC) return null;

    return new RTCPeerConnection(config);
  }

  // Performance methods
  public getPerformanceMetrics(): PerformanceMetrics {
    return this.performanceMonitor.getMetrics();
  }

  // Device access methods
  public async requestDeviceAccess(type: 'camera' | 'microphone' | 'geolocation'): Promise<any> {
    switch (type) {
      case 'camera':
        if (!this.featureSupport.camera) throw new Error('Camera not supported');
        return navigator.mediaDevices.getUserMedia({ video: true });

      case 'microphone':
        if (!this.featureSupport.microphone) throw new Error('Microphone not supported');
        return navigator.mediaDevices.getUserMedia({ audio: true });

      case 'geolocation':
        if (!this.featureSupport.geolocation) throw new Error('Geolocation not supported');
        return new Promise((_resolve, __reject) => {
          navigator.geolocation.getCurrentPosition(resolve, reject);
        });

      default:
        throw new Error(`Unknown device type: ${type}`);
    }
  }

  // Notification methods
  public async requestNotificationPermission(): Promise<NotificationPermission> {
    if (!this.featureSupport.notifications) {
      return 'denied';
    }

    return Notification.requestPermission();
  }

  public showNotification(title: string, options?: NotificationOptions): Notification | null {
    if (!this.featureSupport.notifications || Notification.permission !== 'granted') {
      return null;
    }

    return new Notification(title, options);
  }

  // Clipboard methods
  public async readFromClipboard(): Promise<string> {
    if (!this.featureSupport.clipboard) {
      throw new Error('Clipboard access not supported');
    }

    return navigator.clipboard.readText();
  }

  public async writeToClipboard(text: string): Promise<void> {
    if (!this.featureSupport.clipboard) {
      throw new Error('Clipboard access not supported');
    }

    return navigator.clipboard.writeText(text);
  }

  // Vibration methods
  public vibrate(pattern: number | number[]): boolean {
    if (!this.featureSupport.vibration) return false;

    return navigator.vibrate(pattern);
  }

  // Event setup methods
  private setupGamepadMonitoring(): void {
    window.addEventListener('gamepadconnected', (event) => {
      console.log('Gamepad connected:', event.gamepad.id);
      this.gamepadConnected = true;
    });

    window.addEventListener('gamepaddisconnected', (event) => {
      console.log('Gamepad disconnected:', event.gamepad.id);
      this.gamepadConnected = false;
    });
  }

  private setupVisibilityHandling(): void {
    document.addEventListener('visibilitychange', () => {
      if (document.hidden) {
        // Pause performance monitoring and reduce activity
        this.performanceMonitor.pause();
      } else {
        // Resume performance monitoring
        this.performanceMonitor.resume();
      }
    });
  }

  private setupResizeHandling(): void {
    window.addEventListener('resize', () => {
      // Update adaptive settings based on new window size
      this.updateAdaptiveSettings();
    });

    // Handle orientation change on mobile
    if (this.platformInfo.device !== 'desktop') {
      window.addEventListener('orientationchange', () => {
        setTimeout(() => this.updateAdaptiveSettings(), 100);
      });
    }
  }

  private updateAdaptiveSettings(): void {
    // Adjust render scale based on window size
    const pixelRatio = window.devicePixelRatio || 1;
    const screenArea = window.innerWidth * window.innerHeight;

    if (screenArea < 500000) { // Small screen
      this.adaptiveSettings.graphics.renderScale = 0.7;
    } else if (screenArea < 2000000) { // Medium screen
      this.adaptiveSettings.graphics.renderScale = 0.9;
    } else { // Large screen
      this.adaptiveSettings.graphics.renderScale = Math.min(1.0, 2.0 / pixelRatio);
    }
  }

  // Public getters
  public getPlatformInfo(): PlatformInfo {
    return this.platformInfo;
  }

  public getFeatureSupport(): FeatureSupport {
    return this.featureSupport;
  }

  public getAdaptiveSettings(): AdaptiveSettings {
    return { ...this.adaptiveSettings };
  }

  public updateSettings(settings: Partial<AdaptiveSettings>): void {
    this.adaptiveSettings = { ...this.adaptiveSettings, ...settings };
  }
}

// Helper classes
class InputEventListeners {
  private element: HTMLElement;
  private settings: AdaptiveSettings['input'];
  private listeners: { [key: string]: EventListener } = {};

  constructor(element: HTMLElement, settings: AdaptiveSettings['input']) {
    this.element = element;
    this.settings = settings;
    this.setupListeners();
  }

  private setupListeners(): void {
    if (this.settings.keyboardEnabled) {
      this.listeners.keydown = this.onKeyDown.bind(this);
      this.listeners.keyup = this.onKeyUp.bind(this);
      document.addEventListener('keydown', this.listeners.keydown);
      document.addEventListener('keyup', this.listeners.keyup);
    }

    if (this.settings.mouseEnabled) {
      this.listeners.mousedown = this.onMouseDown.bind(this);
      this.listeners.mouseup = this.onMouseUp.bind(this);
      this.listeners.mousemove = this.onMouseMove.bind(this);
      this.listeners.wheel = this.onWheel.bind(this);

      this.element.addEventListener('mousedown', this.listeners.mousedown);
      this.element.addEventListener('mouseup', this.listeners.mouseup);
      this.element.addEventListener('mousemove', this.listeners.mousemove);
      this.element.addEventListener('wheel', this.listeners.wheel);
    }

    if (this.settings.touchEnabled) {
      this.listeners.touchstart = this.onTouchStart.bind(this);
      this.listeners.touchend = this.onTouchEnd.bind(this);
      this.listeners.touchmove = this.onTouchMove.bind(this);

      this.element.addEventListener('touchstart', this.listeners.touchstart, { passive: false });
      this.element.addEventListener('touchend', this.listeners.touchend, { passive: false });
      this.element.addEventListener('touchmove', this.listeners.touchmove, { passive: false });
    }
  }

  private onKeyDown(event: KeyboardEvent): void {
    // Dispatch custom input event
    this.element.dispatchEvent(new CustomEvent('platformInput', {
      detail: { type: 'keydown', event }
    }));
  }

  private onKeyUp(event: KeyboardEvent): void {
    this.element.dispatchEvent(new CustomEvent('platformInput', {
      detail: { type: 'keyup', event }
    }));
  }

  private onMouseDown(event: MouseEvent): void {
    this.element.dispatchEvent(new CustomEvent('platformInput', {
      detail: { type: 'mousedown', event }
    }));
  }

  private onMouseUp(event: MouseEvent): void {
    this.element.dispatchEvent(new CustomEvent('platformInput', {
      detail: { type: 'mouseup', event }
    }));
  }

  private onMouseMove(event: MouseEvent): void {
    this.element.dispatchEvent(new CustomEvent('platformInput', {
      detail: { type: 'mousemove', event }
    }));
  }

  private onWheel(event: WheelEvent): void {
    this.element.dispatchEvent(new CustomEvent('platformInput', {
      detail: { type: 'wheel', event }
    }));
  }

  private onTouchStart(event: TouchEvent): void {
    event.preventDefault();
    this.element.dispatchEvent(new CustomEvent('platformInput', {
      detail: { type: 'touchstart', event }
    }));
  }

  private onTouchEnd(event: TouchEvent): void {
    event.preventDefault();
    this.element.dispatchEvent(new CustomEvent('platformInput', {
      detail: { type: 'touchend', event }
    }));
  }

  private onTouchMove(event: TouchEvent): void {
    event.preventDefault();
    this.element.dispatchEvent(new CustomEvent('platformInput', {
      detail: { type: 'touchmove', event }
    }));
  }

  public dispose(): void {
    for (const [eventType, listener] of Object.entries(this.listeners)) {
      if (eventType.startsWith('key')) {
        document.removeEventListener(eventType, listener);
      } else {
        this.element.removeEventListener(eventType, listener);
      }
    }
    this.listeners = {};
  }
}

class PerformanceMonitor {
  private isRunning = false;
  private isPaused = false;
  private lastTime = 0;
  private frameCount = 0;
  private fps = 60;
  private frameTime = 16.67;
  private memoryUsage = 0;
  private intervalId?: number;

  public start(): void {
    if (this.isRunning) return;

    this.isRunning = true;
    this.lastTime = performance.now();
    this.intervalId = window.setInterval(() => this.updateMetrics(), 1000);
    this.requestFrame();
  }

  public stop(): void {
    this.isRunning = false;
    if (this.intervalId) {
      clearInterval(this.intervalId);
      this.intervalId = undefined;
    }
  }

  public pause(): void {
    this.isPaused = true;
  }

  public resume(): void {
    this.isPaused = false;
    this.lastTime = performance.now();
    this.frameCount = 0;
  }

  private requestFrame(): void {
    if (!this.isRunning) return;

    requestAnimationFrame((_currentTime) => {
      if (!this.isPaused) {
        this.frameTime = currentTime - this.lastTime;
        this.lastTime = currentTime;
        this.frameCount++;
      }

      this.requestFrame();
    });
  }

  private updateMetrics(): void {
    if (this.isPaused) return;

    this.fps = this.frameCount;
    this.frameCount = 0;

    // Update memory usage if available
    if ('memory' in performance) {
      const memory = (performance as any).memory;
      this.memoryUsage = memory.usedJSHeapSize / (1024 * 1024); // MB
    }
  }

  public getMetrics(): PerformanceMetrics {
    return {
      fps: this.fps,
      frameTime: this.frameTime,
      memoryUsage: this.memoryUsage,
      cpuUsage: Math.min(100, Math.max(0, (this.frameTime - 16.67) / 16.67 * 100)),
      batteryLevel: this.getBatteryLevel(),
      thermalState: this.getThermalState()
    };
  }

  private getBatteryLevel(): number | undefined {
    // @ts-expect-error - Battery API is experimental
    const battery = (navigator as any).battery;
    return battery?.level;
  }

  private getThermalState(): PerformanceMetrics['thermalState'] {
    // Estimate based on performance
    if (this.frameTime > 50) return 'critical';
    if (this.frameTime > 33) return 'serious';
    if (this.frameTime > 20) return 'fair';
    return 'nominal';
  }
}

export interface PlatformInfo {
  os: 'windows' | 'macos' | 'linux' | 'ios' | 'android' | 'unknown';
  browser: 'chrome' | 'firefox' | 'safari' | 'edge' | 'opera' | 'unknown';
  device: 'desktop' | 'tablet' | 'mobile' | 'unknown';
  architecture: 'x64' | 'arm64' | 'x86' | 'arm' | 'unknown';
  webgl: 'webgl2' | 'webgl1' | 'none';
  webxr: boolean;
  touch: boolean;
  gamepad: boolean;
  pointerLock: boolean;
  fullscreen: boolean;
  webassembly: boolean;
  sharedArrayBuffer: boolean;
  offscreenCanvas: boolean;
  webWorkers: boolean;
  serviceWorkers: boolean;
  indexedDB: boolean;
  localStorage: boolean;
  webAudio: boolean;
  webRTC: boolean;
  clipboard: boolean;
  performance: 'high' | 'medium' | 'low';
  memory: number; // MB
  cores: number;
  gpuTier: 'high' | 'medium' | 'low' | 'unknown';
}

export interface FeatureSupport {
  webgl2: boolean;
  webgl1: boolean;
  webxr: boolean;
  webgpu: boolean;
  sharedArrayBuffer: boolean;
  offscreenCanvas: boolean;
  webAssembly: boolean;
  webWorkers: boolean;
  serviceWorkers: boolean;
  webAudio: boolean;
  webRTC: boolean;
  fullscreen: boolean;
  pointerLock: boolean;
  gamepadAPI: boolean;
  touchEvents: boolean;
  accelerometer: boolean;
  gyroscope: boolean;
  clipboard: boolean;
  notifications: boolean;
  vibration: boolean;
  geolocation: boolean;
  camera: boolean;
  microphone: boolean;
}

export class PlatformDetection {
  private platformInfo: PlatformInfo;
  private featureSupport: FeatureSupport;
  private performanceBenchmark: number = 0;

  constructor() {
    this.platformInfo = this.detectPlatform();
    this.featureSupport = this.detectFeatures();
    this.runPerformanceBenchmark();
  }

  private detectPlatform(): PlatformInfo {
    const userAgent = navigator.userAgent.toLowerCase();
    const platform = navigator.platform?.toLowerCase() || '';

    return {
      os: this.detectOS(userAgent, platform),
      browser: this.detectBrowser(userAgent),
      device: this.detectDevice(userAgent),
      architecture: this.detectArchitecture(userAgent, platform),
      webgl: this.detectWebGL(),
      webxr: this.detectWebXR(),
      touch: this.detectTouch(),
      gamepad: this.detectGamepad(),
      pointerLock: this.detectPointerLock(),
      fullscreen: this.detectFullscreen(),
      webassembly: this.detectWebAssembly(),
      sharedArrayBuffer: this.detectSharedArrayBuffer(),
      offscreenCanvas: this.detectOffscreenCanvas(),
      webWorkers: this.detectWebWorkers(),
      serviceWorkers: this.detectServiceWorkers(),
      indexedDB: this.detectIndexedDB(),
      localStorage: this.detectLocalStorage(),
      webAudio: this.detectWebAudio(),
      webRTC: this.detectWebRTC(),
      clipboard: this.detectClipboard(),
      performance: this.detectPerformanceLevel(),
      memory: this.detectMemory(),
      cores: this.detectCores(),
      gpuTier: this.detectGPUTier()
    };
  }

  private detectOS(userAgent: string, platform: string): PlatformInfo['os'] {
    if (userAgent.includes('windows') || platform.includes('win')) return 'windows';
    if (userAgent.includes('macintosh') || userAgent.includes('mac os') || platform.includes('mac')) return 'macos';
    if (userAgent.includes('linux') || platform.includes('linux')) return 'linux';
    if (userAgent.includes('iphone') || userAgent.includes('ipad') || userAgent.includes('ipod')) return 'ios';
    if (userAgent.includes('android')) return 'android';
    return 'unknown';
  }

  private detectBrowser(userAgent: string): PlatformInfo['browser'] {
    if (userAgent.includes('edg/')) return 'edge';
    if (userAgent.includes('chrome') && !userAgent.includes('chromium')) return 'chrome';
    if (userAgent.includes('firefox')) return 'firefox';
    if (userAgent.includes('safari') && !userAgent.includes('chrome')) return 'safari';
    if (userAgent.includes('opera') || userAgent.includes('opr/')) return 'opera';
    return 'unknown';
  }

  private detectDevice(userAgent: string): PlatformInfo['device'] {
    if (userAgent.includes('mobile') || userAgent.includes('phone')) return 'mobile';
    if (userAgent.includes('tablet') || userAgent.includes('ipad')) return 'tablet';
    return 'desktop';
  }

  private detectArchitecture(userAgent: string, platform: string): PlatformInfo['architecture'] {
    if (userAgent.includes('arm64') || userAgent.includes('aarch64')) return 'arm64';
    if (userAgent.includes('arm')) return 'arm';
    if (userAgent.includes('x86_64') || userAgent.includes('x64') || platform.includes('64')) return 'x64';
    if (userAgent.includes('x86') || userAgent.includes('i386') || userAgent.includes('i686')) return 'x86';
    return 'unknown';
  }

  private detectWebGL(): PlatformInfo['webgl'] {
    try {
      const canvas = document.createElement('canvas');
      const gl2 = canvas.getContext('webgl2');
      if (gl2) return 'webgl2';

      const gl1 = canvas.getContext('webgl') || canvas.getContext('experimental-webgl');
      if (gl1) return 'webgl1';

      return 'none';
    } catch {
      return 'none';
    }
  }

  private detectWebXR(): boolean {
    return 'xr' in navigator && 'isSessionSupported' in (navigator as any).xr;
  }

  private detectTouch(): boolean {
    return 'ontouchstart' in window || navigator.maxTouchPoints > 0;
  }

  private detectGamepad(): boolean {
    return 'getGamepads' in navigator;
  }

  private detectPointerLock(): boolean {
    return 'requestPointerLock' in document.documentElement;
  }

  private detectFullscreen(): boolean {
    return 'requestFullscreen' in document.documentElement ||
           'webkitRequestFullscreen' in document.documentElement ||
           'mozRequestFullScreen' in document.documentElement;
  }

  private detectWebAssembly(): boolean {
    return typeof WebAssembly === 'object';
  }

  private detectSharedArrayBuffer(): boolean {
    return typeof SharedArrayBuffer !== 'undefined';
  }

  private detectOffscreenCanvas(): boolean {
    return typeof OffscreenCanvas !== 'undefined';
  }

  private detectWebWorkers(): boolean {
    return typeof Worker !== 'undefined';
  }

  private detectServiceWorkers(): boolean {
    return 'serviceWorker' in navigator;
  }

  private detectIndexedDB(): boolean {
    return 'indexedDB' in window;
  }

  private detectLocalStorage(): boolean {
    try {
      return typeof localStorage !== 'undefined';
    } catch {
      return false;
    }
  }

  private detectWebAudio(): boolean {
    return typeof AudioContext !== 'undefined' || typeof (window as any).webkitAudioContext !== 'undefined';
  }

  private detectWebRTC(): boolean {
    return typeof RTCPeerConnection !== 'undefined';
  }

  private detectClipboard(): boolean {
    return 'clipboard' in navigator;
  }

  private detectPerformanceLevel(): PlatformInfo['performance'] {
    const memory = this.detectMemory();
    const cores = this.detectCores();

    if (memory >= 8192 && cores >= 8) return 'high';
    if (memory >= 4096 && cores >= 4) return 'medium';
    return 'low';
  }

  private detectMemory(): number {
    // @ts-expect-error - deviceMemory is experimental
    return (navigator as any).deviceMemory ? (navigator as any).deviceMemory * 1024 : 4096;
  }

  private detectCores(): number {
    return navigator.hardwareConcurrency || 4;
  }

  private detectGPUTier(): PlatformInfo['gpuTier'] {
    // This would need WebGL renderer info or GPU benchmarking
    // For now, estimate based on other factors
    const webgl = this.detectWebGL();
    const performance = this.detectPerformanceLevel();

    if (webgl === 'webgl2' && performance === 'high') return 'high';
    if (webgl === 'webgl2' && performance === 'medium') return 'medium';
    if (webgl === 'webgl1') return 'low';
    return 'unknown';
  }

  private detectFeatures(): FeatureSupport {
    return {
      webgl2: this.platformInfo.webgl === 'webgl2',
      webgl1: this.platformInfo.webgl !== 'none',
      webxr: this.platformInfo.webxr,
      webgpu: 'gpu' in navigator,
      sharedArrayBuffer: this.platformInfo.sharedArrayBuffer,
      offscreenCanvas: this.platformInfo.offscreenCanvas,
      webAssembly: this.platformInfo.webassembly,
      webWorkers: this.platformInfo.webWorkers,
      serviceWorkers: this.platformInfo.serviceWorkers,
      webAudio: this.platformInfo.webAudio,
      webRTC: this.platformInfo.webRTC,
      fullscreen: this.platformInfo.fullscreen,
      pointerLock: this.platformInfo.pointerLock,
      gamepadAPI: this.platformInfo.gamepad,
      touchEvents: this.platformInfo.touch,
      accelerometer: 'DeviceMotionEvent' in window,
      gyroscope: 'DeviceOrientationEvent' in window,
      clipboard: this.platformInfo.clipboard,
      notifications: 'Notification' in window,
      vibration: 'vibrate' in navigator,
      geolocation: 'geolocation' in navigator,
      camera: 'mediaDevices' in navigator && 'getUserMedia' in navigator.mediaDevices,
      microphone: 'mediaDevices' in navigator && 'getUserMedia' in navigator.mediaDevices
    };
  }

  private async runPerformanceBenchmark(): Promise<void> {
    try {
      const start = performance.now();

      // Simple computational benchmark
      let result = 0;
      for (let i = 0; i < 1000000; i++) {
        result += Math.sqrt(i);
      }

      const computeTime = performance.now() - start;

      // WebGL benchmark if available
      let renderTime = 0;
      if (this.platformInfo.webgl !== 'none') {
        renderTime = await this.runWebGLBenchmark();
      }

      this.performanceBenchmark = Math.max(1, 100 - computeTime - renderTime);

    } catch (_error) {
      this.performanceBenchmark = 50; // Default fallback
    }
  }

  private async runWebGLBenchmark(): Promise<number> {
    try {
      const canvas = document.createElement('canvas');
      canvas.width = 256;
      canvas.height = 256;

      const gl = canvas.getContext('webgl2') || canvas.getContext('webgl');
      if (!gl) return 0;

      const start = performance.now();

      // Simple render test
      gl.clearColor(0, 0, 0, 1);
      for (let i = 0; i < 100; i++) {
        gl.clear(gl.COLOR_BUFFER_BIT);
      }

      gl.finish();
      return performance.now() - start;

    } catch {
      return 0;
    }
  }

  // Public API
  public getPlatformInfo(): PlatformInfo {
    return { ...this.platformInfo };
  }

  public getFeatureSupport(): FeatureSupport {
    return { ...this.featureSupport };
  }

  public getPerformanceBenchmark(): number {
    return this.performanceBenchmark;
  }

  public isFeatureSupported(feature: keyof FeatureSupport): boolean {
    return this.featureSupport[feature];
  }

  public isMobile(): boolean {
    return this.platformInfo.device === 'mobile';
  }

  public isTablet(): boolean {
    return this.platformInfo.device === 'tablet';
  }

  public isDesktop(): boolean {
    return this.platformInfo.device === 'desktop';
  }

  public isTouchDevice(): boolean {
    return this.platformInfo.touch;
  }

  public isHighPerformance(): boolean {
    return this.platformInfo.performance === 'high';
  }

  public canRunAdvancedFeatures(): boolean {
    return this.featureSupport.webgl2 && 
           this.featureSupport.webAssembly && 
           this.featureSupport.webWorkers &&
           this.platformInfo.performance !== 'low';
  }

  public getRecommendedSettings(): any {
    const settings: any = {
      graphics: {
        quality: this.platformInfo.performance,
        shadows: this.platformInfo.performance !== 'low',
        antialiasing: this.platformInfo.performance === 'high',
        postProcessing: this.platformInfo.performance === 'high',
        particleCount: this.platformInfo.performance === 'high' ? 1000 : 
                      this.platformInfo.performance === 'medium' ? 500 : 250
      },
      audio: {
        enabled: this.featureSupport.webAudio,
        spatialAudio: this.featureSupport.webAudio && this.platformInfo.performance !== 'low',
        compression: this.platformInfo.performance === 'low'
      },
      networking: {
        webRTC: this.featureSupport.webRTC,
        compression: this.platformInfo.performance === 'low',
        bufferSize: this.platformInfo.memory >= 4096 ? 'large' : 'small'
      },
      input: {
        touch: this.isTouchDevice(),
        gamepad: this.featureSupport.gamepadAPI,
        pointerLock: this.featureSupport.pointerLock && this.isDesktop()
      },
      storage: {
        indexedDB: this.featureSupport.indexedDB,
        localStorage: this.platformInfo.localStorage,
        cacheSize: this.platformInfo.memory >= 8192 ? 500 : 250 // MB
      }
    };

    return settings;
  }

  public generateCompatibilityReport(): string {
    const info = this.getPlatformInfo();
    const features = this.getFeatureSupport();

    let report = `Platform Compatibility Report\n`;
    report += `==============================\n\n`;

    report += `System Information:\n`;
    report += `- OS: ${info.os} (${info.architecture})\n`;
    report += `- Browser: ${info.browser}\n`;
    report += `- Device: ${info.device}\n`;
    report += `- Performance: ${info.performance}\n`;
    report += `- Memory: ${info.memory}MB\n`;
    report += `- CPU Cores: ${info.cores}\n`;
    report += `- GPU Tier: ${info.gpuTier}\n\n`;

    report += `Graphics Support:\n`;
    report += `- WebGL 2.0: ${features.webgl2 ? '✓' : '✗'}\n`;
    report += `- WebGL 1.0: ${features.webgl1 ? '✓' : '✗'}\n`;
    report += `- WebGPU: ${features.webgpu ? '✓' : '✗'}\n`;
    report += `- WebXR: ${features.webxr ? '✓' : '✗'}\n\n`;

    report += `Core Features:\n`;
    report += `- WebAssembly: ${features.webAssembly ? '✓' : '✗'}\n`;
    report += `- Web Workers: ${features.webWorkers ? '✓' : '✗'}\n`;
    report += `- Shared Array Buffer: ${features.sharedArrayBuffer ? '✓' : '✗'}\n`;
    report += `- Offscreen Canvas: ${features.offscreenCanvas ? '✓' : '✗'}\n\n`;

    report += `Input Support:\n`;
    report += `- Touch: ${features.touchEvents ? '✓' : '✗'}\n`;
    report += `- Gamepad: ${features.gamepadAPI ? '✓' : '✗'}\n`;
    report += `- Pointer Lock: ${features.pointerLock ? '✓' : '✗'}\n`;
    report += `- Fullscreen: ${features.fullscreen ? '✓' : '✗'}\n\n`;

    report += `Media Support:\n`;
    report += `- Web Audio: ${features.webAudio ? '✓' : '✗'}\n`;
    report += `- WebRTC: ${features.webRTC ? '✓' : '✗'}\n`;
    report += `- Camera: ${features.camera ? '✓' : '✗'}\n`;
    report += `- Microphone: ${features.microphone ? '✓' : '✗'}\n\n`;

    report += `Performance Benchmark: ${this.performanceBenchmark.toFixed(1)}/100\n\n`;

    const canRun = this.canRunAdvancedFeatures();
    report += `Compatibility: ${canRun ? 'FULL SUPPORT' : 'LIMITED SUPPORT'}\n`;

    if (!canRun) {
      report += `\nLimitations:\n`;
      if (!features.webgl2) report += `- Limited graphics capabilities (WebGL 1.0 only)\n`;
      if (!features.webAssembly) report += `- No WebAssembly support\n`;
      if (!features.webWorkers) report += `- No background processing support\n`;
      if (info.performance === 'low') report += `- Low performance device\n`;
    }

    return report;
  }
}

export interface RenderObject {
  id: string;
  position: [number, number, number];
  rotation: number;
  scale: [number, number];
  textureId: string;
  color: [number, number, number, number];
  visible: boolean;
  layer: number;
}

export interface Camera {
  position: [number, number];
  zoom: number;
  rotation: number;
  viewport: [number, number, number, number]; // x, y, width, height
}

export interface Texture {
  id: string;
  glTexture: WebGLTexture;
  width: number;
  height: number;
  format: number;
}

export class WebGLRenderer {
  private canvas: HTMLCanvasElement;
  private gl: WebGL2RenderingContext;
  private program!: WebGLProgram;
  private vertexBuffer!: WebGLBuffer;
  private indexBuffer!: WebGLBuffer;
  private instanceBuffer!: WebGLBuffer;

  // Shader attribute/uniform locations
  private locations!: {
    position: number;
    texCoord: number;
    instancePosition: number;
    instanceRotation: number;
    instanceScale: number;
    instanceColor: number;
    instanceTexOffset: number;
    mvpMatrix: WebGLUniformLocation | null;
    textureAtlas: WebGLUniformLocation | null;
  };

  // State
  private textures: Map<string, Texture> = new Map();
  private renderQueue: RenderObject[] = [];
  private camera: Camera = {
    position: [0, 0],
    zoom: 1,
    rotation: 0,
    viewport: [0, 0, 800, 600]
  };
  private mvpMatrix: Float32Array = new Float32Array(16);

  // Performance tracking
  private stats = {
    drawCalls: 0,
    triangles: 0,
    instancesRendered: 0,
    frameTime: 0,
    fps: 0
  };

  private lastFrameTime = 0;
  private frameCount = 0;

  constructor(canvas: HTMLCanvasElement) {
    this.canvas = canvas;

    const gl = canvas.getContext('webgl2', {
      alpha: true,
      antialias: true,
      depth: true,
      premultipliedAlpha: false,
      preserveDrawingBuffer: false
    });

    if (!gl) {
      throw new Error('WebGL2 not supported');
    }

    this.gl = gl;
    this.initializeRenderer();
  }

  private initializeRenderer(): void {
    const gl = this.gl;

    // Enable features
    gl.enable(gl.BLEND);
    gl.blendFunc(gl.SRC_ALPHA, gl.ONE_MINUS_SRC_ALPHA);
    gl.enable(gl.DEPTH_TEST);
    gl.depthFunc(gl.LEQUAL);

    // Create shader program
    this.program = this.createShaderProgram();
    gl.useProgram(this.program);

    // Get attribute and uniform locations
    this.locations = {
      position: gl.getAttribLocation(this.program, 'a_position'),
      texCoord: gl.getAttribLocation(this.program, 'a_texCoord'),
      instancePosition: gl.getAttribLocation(this.program, 'a_instancePosition'),
      instanceRotation: gl.getAttribLocation(this.program, 'a_instanceRotation'),
      instanceScale: gl.getAttribLocation(this.program, 'a_instanceScale'),
      instanceColor: gl.getAttribLocation(this.program, 'a_instanceColor'),
      instanceTexOffset: gl.getAttribLocation(this.program, 'a_instanceTexOffset'),
      mvpMatrix: gl.getUniformLocation(this.program, 'u_mvpMatrix')!,
      textureAtlas: gl.getUniformLocation(this.program, 'u_textureAtlas')!
    };

    // Create buffers
    this.createBuffers();

    // Set up vertex array
    this.setupVertexArray();

    console.log('WebGL2 Renderer initialized');
  }

  private createShaderProgram(): WebGLProgram {
    const gl = this.gl;

    const vertexShaderSource = `#version 300 es
      precision highp float;

      in vec2 a_position;
      in vec2 a_texCoord;

      // Instance attributes
      in vec3 a_instancePosition;
      in float a_instanceRotation;
      in vec2 a_instanceScale;
      in vec4 a_instanceColor;
      in vec2 a_instanceTexOffset;

      uniform mat4 u_mvpMatrix;

      out vec2 v_texCoord;
      out vec4 v_color;
      out float v_depth;

      void main() {
        // Apply instance transformations
        vec2 scaledPos = a_position * a_instanceScale;

        // Apply rotation
        float cos_r = cos(a_instanceRotation);
        float sin_r = sin(a_instanceRotation);
        vec2 rotatedPos = vec2(
          scaledPos.x * cos_r - scaledPos.y * sin_r,
          scaledPos.x * sin_r + scaledPos.y * cos_r
        );

        // Apply translation
        vec3 worldPos = vec3(rotatedPos + a_instancePosition.xy, a_instancePosition.z);

        gl_Position = u_mvpMatrix * vec4(worldPos, 1.0);

        // Pass texture coordinates with atlas offset
        v_texCoord = a_texCoord + a_instanceTexOffset;
        v_color = a_instanceColor;
        v_depth = a_instancePosition.z;
      }
    `;

    const fragmentShaderSource = `#version 300 es
      precision highp float;

      in vec2 v_texCoord;
      in vec4 v_color;
      in float v_depth;

      uniform sampler2D u_textureAtlas;

      out vec4 fragColor;

      void main() {
        vec4 texColor = texture(u_textureAtlas, v_texCoord);

        // Apply tinting
        fragColor = texColor * v_color;

        // Discard transparent pixels
        if (fragColor.a < 0.01) {
          discard;
        }

        // Depth-based fog (optional)
        // float fogFactor = clamp(v_depth * 0.1, 0.0, 1.0);
        // fragColor.rgb = mix(fragColor.rgb, vec3(0.2), fogFactor);
      }
    `;

    const vertexShader = this.createShader(gl.VERTEX_SHADER, vertexShaderSource);
    const fragmentShader = this.createShader(gl.FRAGMENT_SHADER, fragmentShaderSource);

    const program = gl.createProgram()!;
    gl.attachShader(program, vertexShader);
    gl.attachShader(program, fragmentShader);
    gl.linkProgram(program);

    if (!gl.getProgramParameter(program, gl.LINK_STATUS)) {
      const info = gl.getProgramInfoLog(program);
      throw new Error('Could not compile WebGL program: ' + info);
    }

    return program;
  }

  private createShader(type: number, source: string): WebGLShader {
    const gl = this.gl;
    const shader = gl.createShader(type)!;

    gl.shaderSource(shader, source);
    gl.compileShader(shader);

    if (!gl.getShaderParameter(shader, gl.COMPILE_STATUS)) {
      const info = gl.getShaderInfoLog(shader);
      throw new Error('Could not compile shader: ' + info);
    }

    return shader;
  }

  private createBuffers(): void {
    const gl = this.gl;

    // Quad vertices (position + texture coordinates)
    const quadVertices = new Float32Array([
      -0.5, -0.5,  0.0, 0.0,  // bottom-left
       0.5, -0.5,  1.0, 0.0,  // bottom-right
       0.5,  0.5,  1.0, 1.0,  // top-right
      -0.5,  0.5,  0.0, 1.0   // top-left
    ]);

    const quadIndices = new Uint16Array([
      0, 1, 2,
      2, 3, 0
    ]);

    // Create vertex buffer
    this.vertexBuffer = gl.createBuffer()!;
    gl.bindBuffer(gl.ARRAY_BUFFER, this.vertexBuffer);
    gl.bufferData(gl.ARRAY_BUFFER, quadVertices, gl.STATIC_DRAW);

    // Create index buffer
    this.indexBuffer = gl.createBuffer()!;
    gl.bindBuffer(gl.ELEMENT_ARRAY_BUFFER, this.indexBuffer);
    gl.bufferData(gl.ELEMENT_ARRAY_BUFFER, quadIndices, gl.STATIC_DRAW);

    // Create instance buffer (dynamic)
    this.instanceBuffer = gl.createBuffer()!;
  }

  private setupVertexArray(): void {
    const gl = this.gl;

    // Bind vertex buffer
    gl.bindBuffer(gl.ARRAY_BUFFER, this.vertexBuffer);

    // Position attribute
    gl.enableVertexAttribArray(this.locations.position);
    gl.vertexAttribPointer(this.locations.position, 2, gl.FLOAT, false, 16, 0);

    // Texture coordinate attribute
    gl.enableVertexAttribArray(this.locations.texCoord);
    gl.vertexAttribPointer(this.locations.texCoord, 2, gl.FLOAT, false, 16, 8);

    // Bind index buffer
    gl.bindBuffer(gl.ELEMENT_ARRAY_BUFFER, this.indexBuffer);
  }

  public loadTexture(id: string, image: HTMLImageElement): Texture {
    const gl = this.gl;

    const texture = gl.createTexture()!;
    gl.bindTexture(gl.TEXTURE_2D, texture);

    // Upload the image
    gl.texImage2D(gl.TEXTURE_2D, 0, gl.RGBA, gl.RGBA, gl.UNSIGNED_BYTE, image);

    // Set texture parameters
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_S, gl.CLAMP_TO_EDGE);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_T, gl.CLAMP_TO_EDGE);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MIN_FILTER, gl.LINEAR);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MAG_FILTER, gl.LINEAR);

    const textureObj: Texture = {
      id,
      glTexture: texture,
      width: image.width,
      height: image.height,
      format: gl.RGBA
    };

    this.textures.set(id, textureObj);
    return textureObj;
  }

  public setCamera(camera: Partial<Camera>): void {
    Object.assign(this.camera, camera);
  }

  public addRenderObject(obj: RenderObject): void {
    if (obj.visible) {
      this.renderQueue.push(obj);
    }
  }

  public clearRenderQueue(): void {
    this.renderQueue = [];
  }

  public render(): void {
    const startTime = performance.now();

    const gl = this.gl;

    // Clear the canvas
    gl.viewport(0, 0, this.canvas.width, this.canvas.height);
    gl.clearColor(0.1, 0.1, 0.1, 1.0);
    gl.clear(gl.COLOR_BUFFER_BIT | gl.DEPTH_BUFFER_BIT);

    if (this.renderQueue.length === 0) return;

    // Sort render queue by layer then by texture
    this.renderQueue.sort((a, b) => {
      if (a.layer !== b.layer) return a.layer - b.layer;
      return a.textureId.localeCompare(b.textureId);
    });

    // Update MVP matrix
    this.updateMVPMatrix();

    // Batch render by texture
    let currentTexture = '';
    let batchStart = 0;

    for (let i = 0; i <= this.renderQueue.length; i++) {
      const obj = this.renderQueue[i];
      const textureId = obj?.textureId || '';

      if (textureId !== currentTexture || i === this.renderQueue.length) {
        if (i > batchStart) {
          this.renderBatch(currentTexture, batchStart, i);
        }
        currentTexture = textureId;
        batchStart = i;
      }
    }

    // Update stats
    const frameTime = performance.now() - startTime;
    this.updateStats(frameTime);

    // Clear for next frame
    this.clearRenderQueue();
  }

  private renderBatch(textureId: string, start: number, end: number): void {
    const gl = this.gl;
    const batchSize = end - start;

    // Bind texture
    if (this.locations.mvpMatrix) {
      gl.uniformMatrix4fv(this.locations.mvpMatrix, false, this.mvpMatrix);
    }

    // Bind texture
    const texture = this.textures.get(textureId);
    if (texture) {
      gl.activeTexture(gl.TEXTURE0);
      gl.bindTexture(gl.TEXTURE_2D, texture.glTexture);
      if (this.locations.textureAtlas) {
        gl.uniform1i(this.locations.textureAtlas, 0);
      }
    }

    // Prepare instance data
    const instanceData = new Float32Array(batchSize * 12); // 12 floats per instance

    for (let i = start; i < end; i++) {
      const obj = this.renderQueue[i];
      if (!obj) continue;

      const offset = (i - start) * 12;

      // Position (3 floats)
      instanceData[offset] = obj.position[0];
      instanceData[offset + 1] = obj.position[1];
      instanceData[offset + 2] = obj.position[2];

      // Rotation (1 float)
      instanceData[offset + 3] = obj.rotation;

      // Scale (2 floats)
      instanceData[offset + 4] = obj.scale[0];
      instanceData[offset + 5] = obj.scale[1];

      // Color (4 floats)
      instanceData[offset + 6] = obj.color[0];
      instanceData[offset + 7] = obj.color[1];
      instanceData[offset + 8] = obj.color[2];
      instanceData[offset + 9] = obj.color[3];

      // Texture offset (2 floats) - for atlas support
      instanceData[offset + 10] = 0; // u offset
      instanceData[offset + 11] = 0; // v offset
    }

    // Upload instance data
    gl.bindBuffer(gl.ARRAY_BUFFER, this.instanceBuffer);
    gl.bufferData(gl.ARRAY_BUFFER, instanceData, gl.DYNAMIC_DRAW);

    // Set up instance attributes
    const stride = 12 * 4; // 12 floats * 4 bytes

    gl.enableVertexAttribArray(this.locations.instancePosition);
    gl.vertexAttribPointer(this.locations.instancePosition, 3, gl.FLOAT, false, stride, 0);
    gl.vertexAttribDivisor(this.locations.instancePosition, 1);

    gl.enableVertexAttribArray(this.locations.instanceRotation);
    gl.vertexAttribPointer(this.locations.instanceRotation, 1, gl.FLOAT, false, stride, 12);
    gl.vertexAttribDivisor(this.locations.instanceRotation, 1);

    gl.enableVertexAttribArray(this.locations.instanceScale);
    gl.vertexAttribPointer(this.locations.instanceScale, 2, gl.FLOAT, false, stride, 16);
    gl.vertexAttribDivisor(this.locations.instanceScale, 1);

    gl.enableVertexAttribArray(this.locations.instanceColor);
    gl.vertexAttribPointer(this.locations.instanceColor, 4, gl.FLOAT, false, stride, 24);
    gl.vertexAttribDivisor(this.locations.instanceColor, 1);

    gl.enableVertexAttribArray(this.locations.instanceTexOffset);
    gl.vertexAttribPointer(this.locations.instanceTexOffset, 2, gl.FLOAT, false, stride, 40);
    gl.vertexAttribDivisor(this.locations.instanceTexOffset, 1);

    // Draw instances
    gl.drawElementsInstanced(gl.TRIANGLES, 6, gl.UNSIGNED_SHORT, 0, batchSize);

    // Update stats
    this.stats.drawCalls++;
    this.stats.triangles += batchSize * 2;
    this.stats.instancesRendered += batchSize;
  }

  private updateMVPMatrix(): void {
    const gl = this.gl;

    // Create view matrix from camera
    const viewMatrix = this.createViewMatrix();

    // Create projection matrix
    const projMatrix = this.createProjectionMatrix();

    // Multiply projection * view
    const mvpMatrix = this.multiplyMatrices(projMatrix, viewMatrix);

    // Upload to GPU
    gl.uniformMatrix4fv(this.locations.mvpMatrix, false, mvpMatrix);
  }

  private createViewMatrix(): Float32Array {
    const cam = this.camera;
    const matrix = new Float32Array(16);

    // Create 2D view matrix with translation, rotation, and scale
    const cos_r = Math.cos(-cam.rotation);
    const sin_r = Math.sin(-cam.rotation);
    const scale = cam.zoom;

    matrix[0] = cos_r * scale;  matrix[4] = -sin_r * scale; matrix[8] = 0;  matrix[12] = -cam.position[0] * scale;
    matrix[1] = sin_r * scale;  matrix[5] = cos_r * scale;  matrix[9] = 0;  matrix[13] = -cam.position[1] * scale;
    matrix[2] = 0;              matrix[6] = 0;              matrix[10] = 1; matrix[14] = 0;
    matrix[3] = 0;              matrix[7] = 0;              matrix[11] = 0; matrix[15] = 1;

    return matrix;
  }

  private createProjectionMatrix(): Float32Array {
    const matrix = new Float32Array(16);

    const width = this.canvas.width;
    const height = this.canvas.height;
    const _aspect = width / height;

    // Orthographic projection
    const left = -width / 2;
    const right = width / 2;
    const bottom = -height / 2;
    const top = height / 2;
    const near = -1000;
    const far = 1000;

    matrix[0] = 2 / (right - left);  matrix[4] = 0;                   matrix[8] = 0;                matrix[12] = -(right + left) / (right - left);
    matrix[1] = 0;                   matrix[5] = 2 / (top - bottom);  matrix[9] = 0;                matrix[13] = -(top + bottom) / (top - bottom);
    matrix[2] = 0;                   matrix[6] = 0;                   matrix[10] = -2 / (far - near); matrix[14] = -(far + near) / (far - near);
    matrix[3] = 0;                   matrix[7] = 0;                   matrix[11] = 0;               matrix[15] = 1;

    return matrix;
  }

  private multiplyMatrices(a: Float32Array, b: Float32Array): Float32Array {
    const result = new Float32Array(16);

    for (let i = 0; i < 4; i++) {
      for (let j = 0; j < 4; j++) {
        result[i * 4 + j] = 
          (a[i * 4 + 0] || 0) * (b[0 * 4 + j] || 0) +
          (a[i * 4 + 1] || 0) * (b[1 * 4 + j] || 0) +
          (a[i * 4 + 2] || 0) * (b[2 * 4 + j] || 0) +
          (a[i * 4 + 3] || 0) * (b[3 * 4 + j] || 0);
      }
    }

    return result;
  }

  private updateStats(frameTime: number): void {
    this.stats.frameTime = frameTime;

    // Calculate FPS
    const now = performance.now();
    if (now - this.lastFrameTime >= 1000) {
      this.stats.fps = this.frameCount;
      this.frameCount = 0;
      this.lastFrameTime = now;
    }
    this.frameCount++;

    // Reset per-frame stats
    this.stats.drawCalls = 0;
    this.stats.triangles = 0;
    this.stats.instancesRendered = 0;
  }

  public getStats() {
    return { ...this.stats };
  }

  public getLoadedTextures() {
    return Array.from(this.textures.keys());
  }

  public hasTexture(url: string): boolean {
    return this.textures.has(url);
  }

  public resize(width: number, height: number): void {
    this.canvas.width = width;
    this.canvas.height = height;
    this.camera.viewport = [0, 0, width, height];
  }

  public dispose(): void {
    const gl = this.gl;

    // Clean up WebGL resources
    gl.deleteProgram(this.program);
    gl.deleteBuffer(this.vertexBuffer);
    gl.deleteBuffer(this.indexBuffer);
    gl.deleteBuffer(this.instanceBuffer);

    for (const texture of this.textures.values()) {
      gl.deleteTexture(texture.glTexture);
    }

    this.textures.clear();
    this.renderQueue = [];
  }
}

/**
 * Professional Animation System - Triple A Quality
 * Advanced skeletal animation, morphing, physics-based animation, and cinematic tools
 */

export interface AnimationClip {
  id: string;
  name: string;
  duration: number;
  frameRate: number;
  loop: boolean;
  tracks: AnimationTrack[];
  events?: AnimationEvent[];
  blendMode: 'replace' | 'additive' | 'overlay';
  weight: number;
}

export interface AnimationTrack {
  id: string;
  targetNodeId: string;
  propertyPath: string; // e.g., "transform.position.x", "material.albedo"
  interpolation: 'linear' | 'step' | 'cubic' | 'bezier';
  keyframes: Keyframe[];
}

export interface Keyframe {
  time: number;
  value: number | number[] | Float32Array;
  inTangent?: number[];
  outTangent?: number[];
  easing?: EasingFunction;
}

export interface AnimationEvent {
  time: number;
  type: string;
  data: any;
  callback?: (event: AnimationEvent) => void;
}

export interface SkeletalAnimation {
  skeleton: Skeleton;
  bindPose: Float32Array; // Bind pose matrices
  currentPose: Float32Array; // Current pose matrices
  boneTexture?: GPUTexture; // For GPU skinning
}

export interface Skeleton {
  bones: Bone[];
  boneHierarchy: number[]; // Parent indices
  rootBone: number;
}

export interface Bone {
  id: string;
  name: string;
  parentIndex: number;
  bindMatrix: Float32Array;
  inverseBindMatrix: Float32Array;
  localTransform: Transform;
  worldTransform: Transform;
}

export interface Transform {
  position: [number, number, number];
  rotation: [number, number, number, number]; // quaternion
  scale: [number, number, number];
}

export interface MorphTarget {
  id: string;
  name: string;
  vertices: Float32Array;
  normals: Float32Array;
  weight: number;
}

export interface AnimationState {
  clip: AnimationClip;
  time: number;
  speed: number;
  weight: number;
  fadeDuration: number;
  fadeTime: number;
  playing: boolean;
  paused: boolean;
}

export interface BlendTree {
  id: string;
  name: string;
  nodes: BlendNode[];
  parameters: Record<string, number>;
}

export interface BlendNode {
  id: string;
  type: 'clip' | 'blend1d' | 'blend2d' | 'additive' | 'override';
  position: [number, number];
  inputs: string[];
  outputs: string[];
  parameters: Record<string, any>;
}

export interface PhysicsAnimation {
  rigidBodies: RigidBodyAnimation[];
  constraints: ConstraintAnimation[];
  clothSimulation?: ClothAnimation;
  fluidSimulation?: FluidAnimation;
}

export interface RigidBodyAnimation {
  nodeId: string;
  mass: number;
  friction: number;
  restitution: number;
  linearDamping: number;
  angularDamping: number;
  kinematic: boolean;
}

export interface ClothAnimation {
  vertices: Float32Array;
  constraints: ClothConstraint[];
  windForce: [number, number, number];
  gravity: [number, number, number];
  damping: number;
  stiffness: number;
}

export interface ClothConstraint {
  particleA: number;
  particleB: number;
  restLength: number;
  stiffness: number;
}

export type EasingFunction = 
  | 'linear'
  | 'easeInQuad' | 'easeOutQuad' | 'easeInOutQuad'
  | 'easeInCubic' | 'easeOutCubic' | 'easeInOutCubic'
  | 'easeInQuart' | 'easeOutQuart' | 'easeInOutQuart'
  | 'easeInElastic' | 'easeOutElastic' | 'easeInOutElastic'
  | 'easeInBounce' | 'easeOutBounce' | 'easeInOutBounce';

export class ProfessionalAnimationSystem {
  private device: GPUDevice;

  // Animation state
  private animationClips: Map<string, AnimationClip> = new Map();
  private activeAnimations: Map<string, AnimationState> = new Map();
  private skeletalAnimations: Map<string, SkeletalAnimation> = new Map();
  private morphTargets: Map<string, MorphTarget[]> = new Map();
  private blendTrees: Map<string, BlendTree> = new Map();

  // GPU resources for skeletal animation
  private boneTextureBuffer: GPUBuffer | null = null;
  private boneTexture: GPUTexture | null = null;
  private skinningPipeline: GPUComputePipeline | null = null;

  // Physics integration
  private physicsAnimations: Map<string, PhysicsAnimation> = new Map();

  // Performance tracking
  private stats = {
    activeAnimations: 0,
    skeletalAnimations: 0,
    morphTargets: 0,
    physicsAnimations: 0,
    updateTime: 0,
    gpuSkinningTime: 0,
  };

  constructor(device: GPUDevice) {
    this.device = device;
  }

  async initialize(): Promise<void> {
    await this.createGPUSkinningResources();
  }

  private async createGPUSkinningResources(): Promise<void> {
    // Bone texture for GPU skinning (supports up to 256 bones)
    this.boneTexture = this.device.createTexture({
      size: [256, 4], // 4 rows per bone matrix
      format: 'rgba32float',
      usage: GPUTextureUsage.STORAGE_BINDING | GPUTextureUsage.COPY_DST,
    });

    this.boneTextureBuffer = this.device.createBuffer({
      size: 256 * 16 * 4, // 256 bones * 16 floats * 4 bytes
      usage: GPUBufferUsage.STORAGE | GPUBufferUsage.COPY_DST,
    });

    // GPU skinning compute shader
    const skinningShader = this.device.createShaderModule({
      code: `
        struct Vertex {
          position: vec3<f32>,
          normal: vec3<f32>,
          uv: vec2<f32>,
          boneIndices: vec4<u32>,
          boneWeights: vec4<f32>,
        }

        struct SkinnedVertex {
          position: vec3<f32>,
          normal: vec3<f32>,
          uv: vec2<f32>,
        }

        @group(0) @binding(0) var<storage, read> inputVertices: array<Vertex>;
        @group(0) @binding(1) var<storage, read_write> outputVertices: array<SkinnedVertex>;
        @group(0) @binding(2) var<storage, read> boneMatrices: array<mat4x4<f32>>;

        @compute @workgroup_size(64)
        fn cs_main(@builtin(global_invocation_id) globalId: vec3<u32>) {
          let vertexIndex = globalId.x;
          if (vertexIndex >= arrayLength(&inputVertices)) {
            return;
          }

          let vertex = inputVertices[vertexIndex];

          // Compute skinned position
          var skinnedPosition = vec3<f32>(0.0);
          var skinnedNormal = vec3<f32>(0.0);

          for (var i = 0u; i < 4u; i++) {
            let boneIndex = vertex.boneIndices[i];
            let weight = vertex.boneWeights[i];

            if (weight > 0.0) {
              let boneMatrix = boneMatrices[boneIndex];
              let transformedPos = (boneMatrix * vec4<f32>(vertex.position, 1.0)).xyz;
              let transformedNormal = (boneMatrix * vec4<f32>(vertex.normal, 0.0)).xyz;

              skinnedPosition += transformedPos * weight;
              skinnedNormal += transformedNormal * weight;
            }
          }

          outputVertices[vertexIndex] = SkinnedVertex(
            skinnedPosition,
            normalize(skinnedNormal),
            vertex.uv
          );
        }
      `,
    });

    this.skinningPipeline = this.device.createComputePipeline({
      layout: 'auto',
      compute: {
        module: skinningShader,
        entryPoint: 'cs_main',
      },
    });
  }

  // Animation clip management
  addAnimationClip(clip: AnimationClip): void {
    this.animationClips.set(clip.id, clip);
  }

  removeAnimationClip(id: string): void {
    this.animationClips.delete(id);
    this.activeAnimations.delete(id);
  }

  playAnimation(clipId: string, objectId: string, options?: {
    fadeInDuration?: number;
    speed?: number;
    weight?: number;
    startTime?: number;
  }): void {
    const clip = this.animationClips.get(clipId);
    if (!clip) return;

    const animationState: AnimationState = {
      clip,
      time: options?.startTime || 0,
      speed: options?.speed || 1,
      weight: options?.weight || 1,
      fadeDuration: options?.fadeInDuration || 0,
      fadeTime: 0,
      playing: true,
      paused: false,
    };

    this.activeAnimations.set(`${objectId}_${clipId}`, animationState);
  }

  stopAnimation(clipId: string, objectId: string, fadeOutDuration?: number): void {
    const key = `${objectId}_${clipId}`;
    const animation = this.activeAnimations.get(key);

    if (animation && fadeOutDuration) {
      animation.fadeDuration = fadeOutDuration;
      animation.fadeTime = 0;
      animation.playing = false;
    } else {
      this.activeAnimations.delete(key);
    }
  }

  // Skeletal animation
  createSkeletalAnimation(objectId: string, skeleton: Skeleton): void {
    const bindPose = new Float32Array(skeleton.bones.length * 16);
    const currentPose = new Float32Array(skeleton.bones.length * 16);

    // Initialize with bind pose
    skeleton.bones.forEach((bone, _index) => {
      const offset = index * 16;
      bindPose.set(bone.bindMatrix, offset);
      currentPose.set(bone.bindMatrix, offset);
    });

    this.skeletalAnimations.set(objectId, {
      skeleton,
      bindPose,
      currentPose,
    });
  }

  // Morph target animation
  addMorphTargets(objectId: string, targets: MorphTarget[]): void {
    this.morphTargets.set(objectId, targets);
  }

  setMorphTargetWeight(objectId: string, targetId: string, weight: number): void {
    const targets = this.morphTargets.get(objectId);
    if (targets) {
      const target = targets.find(t => t.id === targetId);
      if (target) {
        target.weight = Math.max(0, Math.min(1, weight));
      }
    }
  }

  // Blend trees for complex animation blending
  createBlendTree(tree: BlendTree): void {
    this.blendTrees.set(tree.id, tree);
  }

  evaluateBlendTree(treeId: string, parameters: Record<string, number>): AnimationClip | null {
    const tree = this.blendTrees.get(treeId);
    if (!tree) return null;

    // Update tree parameters
    Object.assign(tree.parameters, parameters);

    // Evaluate blend tree (simplified implementation)
    // Real implementation would traverse nodes and blend animations
    return null;
  }

  // Physics-based animation
  addPhysicsAnimation(objectId: string, physics: PhysicsAnimation): void {
    this.physicsAnimations.set(objectId, physics);
  }

  // Main update loop
  update(deltaTime: number): void {
    const startTime = performance.now();

    this.updateActiveAnimations(deltaTime);
    this.updateSkeletalAnimations(deltaTime);
    this.updateMorphTargets(deltaTime);
    this.updatePhysicsAnimations(deltaTime);

    this.stats.updateTime = performance.now() - startTime;
    this.updateStats();
  }

  private updateActiveAnimations(deltaTime: number): void {
    for (const [key, animation] of this.activeAnimations) {
      if (animation.paused) continue;

      // Update animation time
      animation.time += deltaTime * animation.speed;

      // Handle fading
      if (animation.fadeDuration > 0) {
        animation.fadeTime += deltaTime;
        const fadeProgress = Math.min(animation.fadeTime / animation.fadeDuration, 1);

        if (animation.playing) {
          animation.weight = fadeProgress;
        } else {
          animation.weight = 1 - fadeProgress;
          if (fadeProgress >= 1) {
            this.activeAnimations.delete(key);
            continue;
          }
        }
      }

      // Handle looping
      if (animation.clip.loop && animation.time >= animation.clip.duration) {
        animation.time = animation.time % animation.clip.duration;
      }

      // Trigger animation events
      this.processAnimationEvents(animation, deltaTime);

      // Apply animation to targets
      this.applyAnimation(animation);
    }
  }

  private processAnimationEvents(animation: AnimationState, deltaTime: number): void {
    if (!animation.clip.events) return;

    const previousTime = animation.time - deltaTime * animation.speed;

    for (const event of animation.clip.events) {
      if (previousTime < event.time && animation.time >= event.time) {
        event.callback?.(event);
      }
    }
  }

  private applyAnimation(animation: AnimationState): void {
    const { clip,  time,  weight  } = animation;

    for (const track of clip.tracks) {
      const value = this.evaluateTrack(track, time);
      this.applyTrackValue(track.targetNodeId, track.propertyPath, value, weight, clip.blendMode);
    }
  }

  private evaluateTrack(track: AnimationTrack, time: number): any {
    if (track.keyframes.length === 0) return null;
    if (track.keyframes.length === 1) return track.keyframes[0].value;

    // Find surrounding keyframes
    let beforeIndex = 0;
    let afterIndex = track.keyframes.length - 1;

    for (let i = 0; i < track.keyframes.length - 1; i++) {
      if (time >= track.keyframes[i].time && time <= track.keyframes[i + 1].time) {
        beforeIndex = i;
        afterIndex = i + 1;
        break;
      }
    }

    const beforeKey = track.keyframes[beforeIndex];
    const afterKey = track.keyframes[afterIndex];

    if (beforeIndex === afterIndex) return beforeKey.value;

    // Calculate interpolation factor
    const duration = afterKey.time - beforeKey.time;
    let t = duration > 0 ? (time - beforeKey.time) / duration : 0;

    // Apply easing if specified
    if (beforeKey.easing) {
      t = this.applyEasing(t, beforeKey.easing);
    }

    // Interpolate based on interpolation mode
    return this.interpolateValues(beforeKey.value, afterKey.value, t, track.interpolation);
  }

  private applyEasing(t: number, easing: EasingFunction): number {
    switch (easing) {
      case 'linear': return t;
      case 'easeInQuad': return t * t;
      case 'easeOutQuad': return t * (2 - t);
      case 'easeInOutQuad': return t < 0.5 ? 2 * t * t : -1 + (4 - 2 * t) * t;
      case 'easeInCubic': return t * t * t;
      case 'easeOutCubic': return (--t) * t * t + 1;
      case 'easeInOutCubic': return t < 0.5 ? 4 * t * t * t : (t - 1) * (2 * t - 2) * (2 * t - 2) + 1;
      case 'easeInElastic': return Math.sin(13 * Math.PI / 2 * t) * Math.pow(2, 10 * (t - 1));
      case 'easeOutElastic': return Math.sin(-13 * Math.PI / 2 * (t + 1)) * Math.pow(2, -10 * t) + 1;
      case 'easeInBounce': return 1 - this.applyEasing(1 - t, 'easeOutBounce');
      case 'easeOutBounce': {
        if (t < 1 / 2.75) return 7.5625 * t * t;
        else if (t < 2 / 2.75) return 7.5625 * (t -= 1.5 / 2.75) * t + 0.75;
        else if (t < 2.5 / 2.75) return 7.5625 * (t -= 2.25 / 2.75) * t + 0.9375;
        else return 7.5625 * (t -= 2.625 / 2.75) * t + 0.984375;
      }
      default: return t;
    }
  }

  private interpolateValues(from: any, to: any, t: number, mode: string): any {
    if (typeof from === 'number' && typeof to === 'number') {
      return from + (to - from) * t;
    }

    if (Array.isArray(from) && Array.isArray(to)) {
      return from.map((_f, __i) => f + (to[i] - f) * t);
    }

    // Handle quaternion slerp for rotations
    if (from instanceof Float32Array && to instanceof Float32Array && from.length === 4) {
      return this.slerpQuaternion(from, to, t);
    }

    return mode === 'step' ? (t < 1 ? from : to) : from;
  }

  private slerpQuaternion(q1: Float32Array, q2: Float32Array, t: number): Float32Array {
    const result = new Float32Array(4);
    let dot = q1[0] * q2[0] + q1[1] * q2[1] + q1[2] * q2[2] + q1[3] * q2[3];

    if (dot < 0) {
      dot = -dot;
      q2 = new Float32Array([-q2[0], -q2[1], -q2[2], -q2[3]]);
    }

    if (dot > 0.9995) {
      // Linear interpolation for very close quaternions
      result[0] = q1[0] + t * (q2[0] - q1[0]);
      result[1] = q1[1] + t * (q2[1] - q1[1]);
      result[2] = q1[2] + t * (q2[2] - q1[2]);
      result[3] = q1[3] + t * (q2[3] - q1[3]);
    } else {
      // Spherical linear interpolation
      const theta = Math.acos(dot);
      const sinTheta = Math.sin(theta);
      const w1 = Math.sin((1 - t) * theta) / sinTheta;
      const w2 = Math.sin(t * theta) / sinTheta;

      result[0] = w1 * q1[0] + w2 * q2[0];
      result[1] = w1 * q1[1] + w2 * q2[1];
      result[2] = w1 * q1[2] + w2 * q2[2];
      result[3] = w1 * q1[3] + w2 * q2[3];
    }

    // Normalize
    const length = Math.sqrt(result[0] * result[0] + result[1] * result[1] + result[2] * result[2] + result[3] * result[3]);
    result[0] /= length;
    result[1] /= length;
    result[2] /= length;
    result[3] /= length;

    return result;
  }

