export class GLTFLoader implements AssetLoader<ModelData> {
  supportedTypes = ['model' as any];

  async load(metadata: AssetMetadata, options?: AssetLoadOptions): Promise<ModelData> {
    const isGLB = metadata.path.toLowerCase().endsWith('.glb');

    if (isGLB) {
      return this.loadGLB(metadata.path);
    } else {
      return this.loadGLTF(metadata.path);
    }
  }

  private async loadGLTF(path: string): Promise<ModelData> {
    const response = await fetch(path);
    const gltf = await response.json();

    // Resolve relative paths for binary data and images
    const baseDir = path.substring(0, path.lastIndexOf('/') + 1);

    return this.parseGLTF(gltf, baseDir);
  }

  private async loadGLB(path: string): Promise<ModelData> {
    const response = await fetch(path);
    const arrayBuffer = await response.arrayBuffer();

    return this.parseGLB(arrayBuffer);
  }

  private parseGLB(arrayBuffer: ArrayBuffer): ModelData {
    const dataView = new DataView(arrayBuffer);
    let offset = 0;

    // Read GLB header
    const magic = dataView.getUint32(offset, true);
    if (magic !== 0x46546C67) { // 'glTF'
      throw new Error('Invalid GLB magic number');
    }
    offset += 4;

    const version = dataView.getUint32(offset, true);
    if (version !== 2) {
      throw new Error(`Unsupported GLB version: ${version}`);
    }
    offset += 4;

    const length = dataView.getUint32(offset, true);
    offset += 4;

    // Read JSON chunk
    const jsonLength = dataView.getUint32(offset, true);
    offset += 4;

    const jsonType = dataView.getUint32(offset, true);
    if (jsonType !== 0x4E4F534A) { // 'JSON'
      throw new Error('Expected JSON chunk');
    }
    offset += 4;

    const jsonBytes = new Uint8Array(arrayBuffer, offset, jsonLength);
    const gltf = JSON.parse(new TextDecoder().decode(jsonBytes));
    offset += jsonLength;

    // Read binary chunk if present
    let binaryData: ArrayBuffer | null = null;
    if (offset < length) {
      const binaryLength = dataView.getUint32(offset, true);
      offset += 4;

      const binaryType = dataView.getUint32(offset, true);
      if (binaryType !== 0x004E4942) { // 'BIN\0'
        throw new Error('Expected BIN chunk');
      }
      offset += 4;

      binaryData = arrayBuffer.slice(offset, offset + binaryLength);
    }

    return this.parseGLTF(gltf, null, binaryData);
  }

  private parseGLTF(gltf: any, baseDir?: string | null, binaryData?: ArrayBuffer | null): ModelData {
    const buffers = this.loadBuffers(gltf.buffers || [], baseDir, binaryData);
    const bufferViews = this.createBufferViews(gltf.bufferViews || [], buffers);
    const accessors = this.createAccessors(gltf.accessors || [], bufferViews);

    const meshes = this.parseMeshes(gltf.meshes || [], accessors);
    const materials = this.parseMaterials(gltf.materials || []);
    const textures = this.parseTextures(gltf.textures || []);
    const animations = this.parseAnimations(gltf.animations || [], accessors);

    // Parse scene (use default scene or first scene)
    const sceneIndex = gltf.scene !== undefined ? gltf.scene : 0;
    const sceneData = gltf.scenes && gltf.scenes[sceneIndex] ? gltf.scenes[sceneIndex] : { nodes: [] };
    const scene = this.parseScene(sceneData, gltf.nodes || []);

    return {
      meshes,
      materials,
      textures,
      animations,
      scene,
      metadata: {
        generator: gltf.asset?.generator,
        version: gltf.asset?.version,
        copyright: gltf.asset?.copyright,
        extras: gltf.extras
      }
    };
  }

  private loadBuffers(buffers: any[], baseDir?: string | null, binaryData?: ArrayBuffer | null): ArrayBuffer[] {
    return buffers.map((buffer, _index) => {
      if (buffer.uri) {
        if (buffer.uri.startsWith('data:')) {
          // Data URI
          const base64 = buffer.uri.split(',')[1];
          const binaryString = atob(base64);
          const bytes = new Uint8Array(binaryString.length);
          for (let i = 0; i < binaryString.length; i++) {
            bytes[i] = binaryString.charCodeAt(i);
          }
          return bytes.buffer;
        } else {
          // External file - would need to be loaded separately
          throw new Error('External buffer files not supported in this implementation');
        }
      } else if (index === 0 && binaryData) {
        // First buffer without URI in GLB format
        return binaryData;
      } else {
        throw new Error(`Buffer ${index} has no data`);
      }
    });
  }

  private createBufferViews(bufferViews: any[], buffers: ArrayBuffer[]): DataView[] {
    return bufferViews.map(bv => {
      const buffer = buffers[bv.buffer];
      return new DataView(buffer, bv.byteOffset || 0, bv.byteLength);
    });
  }

  private createAccessors(accessors: any[], bufferViews: DataView[]): any[] {
    return accessors.map(accessor => {
      const bufferView = bufferViews[accessor.bufferView];
      const componentType = accessor.componentType;
      const type = accessor.type;
      const count = accessor.count;
      const byteOffset = accessor.byteOffset || 0;

      const componentSize = this.getComponentSize(componentType);
      const elementSize = this.getElementSize(type) * componentSize;

      return {
        bufferView,
        componentType,
        type,
        count,
        byteOffset,
        componentSize,
        elementSize,
        normalized: accessor.normalized || false,
        min: accessor.min,
        max: accessor.max
      };
    });
  }

  private getComponentSize(componentType: number): number {
    switch (componentType) {
      case 5120: return 1; // BYTE
      case 5121: return 1; // UNSIGNED_BYTE
      case 5122: return 2; // SHORT
      case 5123: return 2; // UNSIGNED_SHORT
      case 5125: return 4; // UNSIGNED_INT
      case 5126: return 4; // FLOAT
      default: throw new Error(`Unknown component type: ${componentType}`);
    }
  }

  private getElementSize(type: string): number {
    switch (type) {
      case 'SCALAR': return 1;
      case 'VEC2': return 2;
      case 'VEC3': return 3;
      case 'VEC4': return 4;
      case 'MAT2': return 4;
      case 'MAT3': return 9;
      case 'MAT4': return 16;
      default: throw new Error(`Unknown type: ${type}`);
    }
  }

  private readAccessorData(accessor: any): Float32Array | Uint16Array | Uint32Array {
    const { bufferView,  componentType,  count,  byteOffset,  elementSize, _componentSize} = accessor;
    const totalBytes = count * elementSize;

    const bytes = new Uint8Array(bufferView.buffer, bufferView.byteOffset + byteOffset, totalBytes);

    switch (componentType) {
      case 5121: // UNSIGNED_BYTE
        return new Uint8Array(bytes);
      case 5123: // UNSIGNED_SHORT
        return new Uint16Array(bytes.buffer, bytes.byteOffset, totalBytes / 2);
      case 5125: // UNSIGNED_INT
        return new Uint32Array(bytes.buffer, bytes.byteOffset, totalBytes / 4);
      case 5126: // FLOAT
        return new Float32Array(bytes.buffer, bytes.byteOffset, totalBytes / 4);
      default:
        throw new Error(`Unsupported component type for reading: ${componentType}`);
    }
  }

  private parseMeshes(meshes: any[], accessors: any[]): MeshData[] {
    return meshes.map(mesh => {
      const primitives = mesh.primitives || [];

      // For simplicity, take the first primitive
      // In a full implementation, you'd handle multiple primitives per mesh
      const primitive = primitives[0];
      if (!primitive) {
        throw new Error('Mesh has no primitives');
      }

      const attributes = primitive.attributes;
      const indices = primitive.indices;

      // Read vertex data
      const vertexData: number[] = [];
      const attributeInfo: VertexAttribute[] = [];
      let stride = 0;

      // Position
      if (attributes.POSITION !== undefined) {
        const posAccessor = accessors[attributes.POSITION];
        const positions = this.readAccessorData(posAccessor) as Float32Array;

        attributeInfo.push({
          name: 'a_position',
          size: 3,
          type: WebGL2RenderingContext.FLOAT,
          normalized: false,
          stride: 0, // Will be updated
          offset: stride * 4
        });

        stride += 3;

        for (let i = 0; i < positions.length; i += 3) {
          vertexData.push(positions[i], positions[i + 1], positions[i + 2]);
        }
      }

      // Normal
      if (attributes.NORMAL !== undefined) {
        const normalAccessor = accessors[attributes.NORMAL];
        const normals = this.readAccessorData(normalAccessor) as Float32Array;

        attributeInfo.push({
          name: 'a_normal',
          size: 3,
          type: WebGL2RenderingContext.FLOAT,
          normalized: false,
          stride: 0, // Will be updated
          offset: stride * 4
        });

        stride += 3;

        // Interleave with position data
        const positions = this.readAccessorData(accessors[attributes.POSITION]) as Float32Array;
        const interleavedData: number[] = [];

        for (let i = 0; i < positions.length; i += 3) {
          interleavedData.push(positions[i], positions[i + 1], positions[i + 2]);
          interleavedData.push(normals[i], normals[i + 1], normals[i + 2]);
        }

        vertexData.length = 0;
        vertexData.push(...interleavedData);
      }

      // Texture coordinates
      if (attributes.TEXCOORD_0 !== undefined) {
        const uvAccessor = accessors[attributes.TEXCOORD_0];
        const uvs = this.readAccessorData(uvAccessor) as Float32Array;

        attributeInfo.push({
          name: 'a_texCoord',
          size: 2,
          type: WebGL2RenderingContext.FLOAT,
          normalized: false,
          stride: 0, // Will be updated
          offset: stride * 4
        });

        stride += 2;

        // Re-interleave all data
        const positions = this.readAccessorData(accessors[attributes.POSITION]) as Float32Array;
        const normals = attributes.NORMAL ? this.readAccessorData(accessors[attributes.NORMAL]) as Float32Array : null;
        const interleavedData: number[] = [];

        for (let i = 0; i < positions.length; i += 3) {
          interleavedData.push(positions[i], positions[i + 1], positions[i + 2]);

          if (normals) {
            interleavedData.push(normals[i], normals[i + 1], normals[i + 2]);
          }

          const uvIndex = (i / 3) * 2;
          interleavedData.push(uvs[uvIndex], uvs[uvIndex + 1]);
        }

        vertexData.length = 0;
        vertexData.push(...interleavedData);
      }

      // Update stride in attributes
      const strideBytes = stride * 4;
      for (const attr of attributeInfo) {
        attr.stride = strideBytes;
      }

      // Read indices
      let indicesArray: Uint16Array | Uint32Array;
      if (indices !== undefined) {
        const indexAccessor = accessors[indices];
        indicesArray = this.readAccessorData(indexAccessor) as Uint16Array | Uint32Array;
      } else {
        // Generate indices for triangles
        const vertexCount = vertexData.length / stride;
        indicesArray = new Uint16Array(vertexCount);
        for (let i = 0; i < vertexCount; i++) {
          indicesArray[i] = i;
        }
      }

      const geometry: GeometryData = {
        vertices: new Float32Array(vertexData),
        indices: indicesArray,
        attributes: attributeInfo,
        drawMode: this.getDrawMode(primitive.mode)
      };

      return {
        name: mesh.name || `Mesh_${meshes.indexOf(mesh)}`,
        geometry,
        materialIndex: primitive.material,
        extras: mesh.extras
      };
    });
  }

  private getDrawMode(mode?: number): number {
    switch (mode) {
      case 0: return WebGL2RenderingContext.POINTS;
      case 1: return WebGL2RenderingContext.LINES;
      case 2: return WebGL2RenderingContext.LINE_LOOP;
      case 3: return WebGL2RenderingContext.LINE_STRIP;
      case 4: return WebGL2RenderingContext.TRIANGLES;
      case 5: return WebGL2RenderingContext.TRIANGLE_STRIP;
      case 6: return WebGL2RenderingContext.TRIANGLE_FAN;
      default: return WebGL2RenderingContext.TRIANGLES;
    }
  }

  private parseMaterials(materials: any[]): MaterialData[] {
    return materials.map(material => ({
      name: material.name || 'Unnamed Material',
      pbrMetallicRoughness: material.pbrMetallicRoughness ? {
        baseColorFactor: material.pbrMetallicRoughness.baseColorFactor || [1, 1, 1, 1],
        baseColorTexture: material.pbrMetallicRoughness.baseColorTexture,
        metallicFactor: material.pbrMetallicRoughness.metallicFactor !== undefined ? material.pbrMetallicRoughness.metallicFactor : 1,
        roughnessFactor: material.pbrMetallicRoughness.roughnessFactor !== undefined ? material.pbrMetallicRoughness.roughnessFactor : 1,
        metallicRoughnessTexture: material.pbrMetallicRoughness.metallicRoughnessTexture
      } : undefined,
      normalTexture: material.normalTexture,
      occlusionTexture: material.occlusionTexture,
      emissiveTexture: material.emissiveTexture,
      emissiveFactor: material.emissiveFactor || [0, 0, 0],
      alphaMode: material.alphaMode || 'OPAQUE',
      alphaCutoff: material.alphaCutoff !== undefined ? material.alphaCutoff : 0.5,
      doubleSided: material.doubleSided || false,
      extras: material.extras
    }));
  }

  private parseTextures(textures: any[]): TextureReference[] {
    return textures.map((_texture, __index) => ({
      index,
      extras: texture.extras
    }));
  }

  private parseAnimations(animations: any[], _accessors: any[]): AnimationData[] {
    return animations.map(animation => ({
      name: animation.name || 'Unnamed Animation',
      channels: animation.channels || [],
      samplers: animation.samplers || [],
      extras: animation.extras
    }));
  }

  private parseScene(sceneData: any, nodes: any[]): SceneNode {
    const rootNode: SceneNode = {
      name: sceneData.name || 'Scene',
      children: [],
      transform: Record<string, any>
    };

    // Parse root nodes
    for (const nodeIndex of sceneData.nodes || []) {
      const childNode = this.parseNode(nodes[nodeIndex], nodes);
      rootNode.children.push(childNode);
    }

    return rootNode;
  }

  private parseNode(nodeData: any, allNodes: any[]): SceneNode {
    const node: SceneNode = {
      name: nodeData.name,
      children: [],
      meshIndex: nodeData.mesh,
      transform: {
        translation: nodeData.translation,
        rotation: nodeData.rotation,
        scale: nodeData.scale,
        matrix: nodeData.matrix
      },
      extras: nodeData.extras
    };

    // Parse child nodes
    if (nodeData.children) {
      for (const childIndex of nodeData.children) {
        const childNode = this.parseNode(allNodes[childIndex], allNodes);
        node.children.push(childNode);
      }
    }

    return node;
  }

  unload(_asset: any): void {
    // Clean up any WebGL resources if needed
    // This would be handled by the geometry manager
  }

  validate(data: ModelData): boolean {
    return data && 
           Array.isArray(data.meshes) && 
           Array.isArray(data.materials) && 
           data.scene !== undefined;
  }
}

export class OBJLoader implements AssetLoader<ModelData> {
  supportedTypes = ['model' as any];

  async load(metadata: AssetMetadata, options?: AssetLoadOptions): Promise<ModelData> {
    const response = await fetch(metadata.path);
    const text = await response.text();

    return this.parseOBJ(text, metadata.path);
  }

  private parseOBJ(text: string, path: string): ModelData {
    const lines = text.split('\n');

    const vertices: number[] = [];
    const normals: number[] = [];
    const texCoords: number[] = [];
    const faces: number[][] = [];

    let currentMaterial: string | null = null;

    for (const line of lines) {
      const parts = line.trim().split(/\s+/);
      const command = parts[0];

      switch (command) {
        case 'v': // Vertex
          vertices.push(parseFloat(parts[1]), parseFloat(parts[2]), parseFloat(parts[3]));
          break;

        case 'vn': // Normal
          normals.push(parseFloat(parts[1]), parseFloat(parts[2]), parseFloat(parts[3]));
          break;

        case 'vt': // Texture coordinate
          texCoords.push(parseFloat(parts[1]), parseFloat(parts[2]));
          break;

        case 'f': // Face {
          const face: number[] = [];
          for (let i = 1; i < parts.length; i++) {
            const indices = parts[i].split('/');
            face.push(
              parseInt(indices[0]) - 1, // Vertex index (OBJ is 1-based)
              indices[1] ? parseInt(indices[1]) - 1 : -1, // Texture coord index
              indices[2] ? parseInt(indices[2]) - 1 : -1  // Normal index
            );
          }
          faces.push(face);
    }
          break;

        case 'usemtl': // Use material
          currentMaterial = parts[1];
          break;
      }
    }

    // Convert to interleaved vertex data
    const vertexData: number[] = [];
    const indices: number[] = [];
    let vertexIndex = 0;

    for (const face of faces) {
      // Triangulate face (assuming triangles or quads)
      const vertexCount = face.length / 3;

      for (let i = 0; i < vertexCount; i++) {
        const vIndex = face[i * 3];
        const tIndex = face[i * 3 + 1];
        const nIndex = face[i * 3 + 2];

        // Position
        if (vIndex >= 0) {
          vertexData.push(
            vertices[vIndex * 3],
            vertices[vIndex * 3 + 1],
            vertices[vIndex * 3 + 2]
          );
        } else {
          vertexData.push(0, 0, 0);
        }

        // Normal
        if (nIndex >= 0) {
          vertexData.push(
            normals[nIndex * 3],
            normals[nIndex * 3 + 1],
            normals[nIndex * 3 + 2]
          );
        } else {
          vertexData.push(0, 1, 0); // Default up normal
        }

        // Texture coordinates
        if (tIndex >= 0) {
          vertexData.push(texCoords[tIndex * 2], texCoords[tIndex * 2 + 1]);
        } else {
          vertexData.push(0, 0);
        }

        indices.push(vertexIndex++);
      }

      // If face has 4 vertices (quad), add second triangle
      if (vertexCount === 4) {
        // Add triangle: 0, 2, 3
        const baseIndex = vertexIndex - 4;
        indices.push(baseIndex, baseIndex + 2, baseIndex + 3);
        vertexIndex += 3; // We'll duplicate the vertices

        // Duplicate vertices for second triangle
        for (const i of [0, 2, 3]) {
          const vIndex = face[i * 3];
          const tIndex = face[i * 3 + 1];
          const nIndex = face[i * 3 + 2];

          if (vIndex >= 0) {
            vertexData.push(
              vertices[vIndex * 3],
              vertices[vIndex * 3 + 1],
              vertices[vIndex * 3 + 2]
            );
          } else {
            vertexData.push(0, 0, 0);
          }

          if (nIndex >= 0) {
            vertexData.push(
              normals[nIndex * 3],
              normals[nIndex * 3 + 1],
              normals[nIndex * 3 + 2]
            );
          } else {
            vertexData.push(0, 1, 0);
          }

          if (tIndex >= 0) {
            vertexData.push(texCoords[tIndex * 2], texCoords[tIndex * 2 + 1]);
          } else {
            vertexData.push(0, 0);
          }
        }
      }
    }

    const attributes: VertexAttribute[] = [
      { name: 'a_position', size: 3, type: WebGL2RenderingContext.FLOAT, normalized: false, stride: 32, offset: 0 },
      { name: 'a_normal', size: 3, type: WebGL2RenderingContext.FLOAT, normalized: false, stride: 32, offset: 12 },
      { name: 'a_texCoord', size: 2, type: WebGL2RenderingContext.FLOAT, normalized: false, stride: 32, offset: 24 }
    ];

    const geometry: GeometryData = {
      vertices: new Float32Array(vertexData),
      indices: new Uint16Array(indices),
      attributes
    };

    const mesh: MeshData = {
      name: path.split('/').pop()?.replace('.obj', '') || 'OBJ Model',
      geometry
    };

    const rootNode: SceneNode = {
      name: 'Root',
      children: [],
      meshIndex: 0,
      transform: Record<string, any>
    };

    return {
      meshes: [mesh],
      materials: [{ name: 'Default', extras: Record<string, unknown>}],
      textures: [],
      scene: rootNode,
      metadata: {
        generator: 'OBJ Loader',
        version: '1.0'
      }
    };
  }

  validate(data: ModelData): boolean {
    return data && data.meshes.length > 0;
  }
}

import { vec3, mat4 } from 'gl-matrix';

export interface AudioListener {
  position: vec3;
  orientation: {
    forward: vec3;
    up: vec3;
  };
  velocity?: vec3;
}

export interface AudioSource {
  id: string;
  position: vec3;
  velocity?: vec3;
  volume: number;
  pitch: number;

  // Spatial properties
  minDistance: number;
  maxDistance: number;
  rolloffFactor: number;
  coneInnerAngle: number;
  coneOuterAngle: number;
  coneOuterGain: number;
  orientation?: vec3;

  // Audio properties
  buffer: AudioBuffer | null;
  loop: boolean;
  autoplay: boolean;

  // State
  isPlaying: boolean;
  isPaused: boolean;
  currentTime: number;
  duration: number;

  // Effects
  effects: AudioEffect[];

  // 3D properties
  dopplerFactor: number;
  distanceModel: 'linear' | 'inverse' | 'exponential';
}

export interface AudioEffect {
  type: 'reverb' | 'delay' | 'filter' | 'distortion' | 'chorus' | 'compressor';
  enabled: boolean;
  parameters: { [key: string]: number };
}

export interface ReverbSettings {
  roomSize: number;
  decay: number;
  damping: number;
  earlyReflections: number;
  lateReflections: number;
  diffusion: number;
  density: number;
  wetGain: number;
  dryGain: number;
}

export interface AudioZone {
  id: string;
  bounds: {
    center: vec3;
    size: vec3;
    rotation?: mat4;
  };
  reverbSettings: ReverbSettings;
  ambientSounds: string[];
  acousticProperties: {
    absorption: number;
    reflection: number;
    transmission: number;
  };
}

export class SpatialAudioEngine {
  private audioContext: AudioContext;
  private listener: AudioListener;
  private sources = new Map<string, AudioSource>();
  private zones = new Map<string, AudioZone>();

  // Web Audio nodes
  private masterGainNode: GainNode;
  private compressorNode: DynamicsCompressorNode;
  private convolverNode: ConvolverNode;
  private analyserNode: AnalyserNode;

  // HRTF and spatial processing
  private pannerNodes = new Map<string, PannerNode>();
  private gainNodes = new Map<string, GainNode>();
  private sourceNodes = new Map<string, AudioBufferSourceNode>();

  // Audio loading and management
  private audioBuffers = new Map<string, AudioBuffer>();
  private loadingPromises = new Map<string, Promise<AudioBuffer>>();

  // Performance and analysis
  private analyserData: {
    frequencyData: Float32Array;
    timeData: Float32Array;
    volume: number;
  };

  // Configuration
  private config = {
    maxSources: 64,
    masterVolume: 1.0,
    dopplerFactor: 1.0,
    speedOfSound: 343.3, // m/s
    enableHRTF: true,
    enableReverb: true,
    enableOcclusion: true,
    enableDoppler: true,
    updateInterval: 16, // ms (~60fps)
  };

  private updateTimer: number | null = null;
  private lastUpdateTime = 0;

  constructor() {
    this.audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();

    this.listener = {
      position: vec3.create(),
      orientation: {
        forward: vec3.fromValues(0, 0, -1),
        up: vec3.fromValues(0, 1, 0)
      }
    };

    this.initializeAudioGraph();
    this.initializeAnalyser();
    this.startUpdateLoop();
  }

  private initializeAudioGraph(): void {
    const ctx = this.audioContext;

    // Create master audio graph
    this.masterGainNode = ctx.createGain();
    this.compressorNode = ctx.createDynamicsCompressor();
    this.convolverNode = ctx.createConvolver();

    // Set up compressor
    this.compressorNode.threshold.value = -24;
    this.compressorNode.knee.value = 30;
    this.compressorNode.ratio.value = 12;
    this.compressorNode.attack.value = 0.003;
    this.compressorNode.release.value = 0.25;

    // Connect master chain
    this.masterGainNode.connect(this.compressorNode);
    this.compressorNode.connect(ctx.destination);

    // Set master volume
    this.masterGainNode.gain.value = this.config.masterVolume;

    // Set up listener
    if (ctx.listener.positionX) {
      // Modern Web Audio API
      ctx.listener.positionX.value = this.listener.position[0];
      ctx.listener.positionY.value = this.listener.position[1];
      ctx.listener.positionZ.value = this.listener.position[2];

      ctx.listener.forwardX.value = this.listener.orientation.forward[0];
      ctx.listener.forwardY.value = this.listener.orientation.forward[1];
      ctx.listener.forwardZ.value = this.listener.orientation.forward[2];

      ctx.listener.upX.value = this.listener.orientation.up[0];
      ctx.listener.upY.value = this.listener.orientation.up[1];
      ctx.listener.upZ.value = this.listener.orientation.up[2];
    } else {
      // Legacy Web Audio API
      (ctx.listener as any).setPosition(
        this.listener.position[0],
        this.listener.position[1],
        this.listener.position[2]
      );

      (ctx.listener as any).setOrientation(
        this.listener.orientation.forward[0],
        this.listener.orientation.forward[1],
        this.listener.orientation.forward[2],
        this.listener.orientation.up[0],
        this.listener.orientation.up[1],
        this.listener.orientation.up[2]
      );
    }
  }

  private initializeAnalyser(): void {
    this.analyserNode = this.audioContext.createAnalyser();
    this.analyserNode.fftSize = 2048;
    this.analyserNode.smoothingTimeConstant = 0.8;

    this.compressorNode.connect(this.analyserNode);

    this.analyserData = {
      frequencyData: new Float32Array(this.analyserNode.frequencyBinCount),
      timeData: new Float32Array(this.analyserNode.frequencyBinCount),
      volume: 0
    };
  }

  private startUpdateLoop(): void {
    this.updateTimer = window.setInterval(() => {
      this.update();
    }, this.config.updateInterval);
  }

  private update(): void {
    const now = performance.now();
    const deltaTime = (now - this.lastUpdateTime) / 1000;
    this.lastUpdateTime = now;

    // Update all sources
    for (const source of this.sources.values()) {
      this.updateSource(source, deltaTime);
    }

    // Update analyser data
    this.updateAnalyser();
  }

  private updateSource(source: AudioSource, deltaTime: number): void {
    if (!source.isPlaying) return;

    const pannerNode = this.pannerNodes.get(source.id);
    const gainNode = this.gainNodes.get(source.id);

    if (!pannerNode || !gainNode) return;

    // Update 3D position
    this.updateSourcePosition(source, pannerNode);

    // Update volume based on distance and effects
    const volume = this.calculateSourceVolume(source);
    gainNode.gain.value = volume;

    // Update current time for non-looping sources
    if (!source.loop) {
      source.currentTime += deltaTime;
      if (source.currentTime >= source.duration) {
        this.stopSource(source.id);
      }
    }

    // Apply zone effects
    this.applyZoneEffects(source);
  }

  private updateSourcePosition(source: AudioSource, pannerNode: PannerNode): void {
    const _ctx = this.audioContext;

    if (pannerNode.positionX) {
      // Modern API
      pannerNode.positionX.value = source.position[0];
      pannerNode.positionY.value = source.position[1];
      pannerNode.positionZ.value = source.position[2];

      if (source.orientation) {
        pannerNode.orientationX.value = source.orientation[0];
        pannerNode.orientationY.value = source.orientation[1];
        pannerNode.orientationZ.value = source.orientation[2];
      }
    } else {
      // Legacy API
      (pannerNode as any).setPosition(source.position[0], source.position[1], source.position[2]);

      if (source.orientation) {
        (pannerNode as any).setOrientation(source.orientation[0], source.orientation[1], source.orientation[2]);
      }
    }

    // Update velocity for Doppler effect
    if (this.config.enableDoppler && source.velocity) {
      if (pannerNode.positionX) {
        // Modern API doesn't have velocity, so we skip Doppler for now
        // In a real implementation, you'd calculate pitch shift manually
      } else {
        (pannerNode as any).setVelocity(source.velocity[0], source.velocity[1], source.velocity[2]);
      }
    }
  }

  private calculateSourceVolume(source: AudioSource): number {
    const distance = vec3.distance(source.position, this.listener.position);
    let volume = source.volume;

    // Distance attenuation
    switch (source.distanceModel) {
      case 'linear':
        if (distance <= source.minDistance) {
          volume *= 1;
        } else if (distance >= source.maxDistance) {
          volume *= 0;
        } else {
          const ratio = (distance - source.minDistance) / (source.maxDistance - source.minDistance);
          volume *= (1 - ratio);
        }
        break;

      case 'inverse':
        volume *= source.minDistance / (source.minDistance + source.rolloffFactor * (distance - source.minDistance));
        break;

      case 'exponential':
        volume *= Math.pow(distance / source.minDistance, -source.rolloffFactor);
        break;
    }

    // Cone attenuation
    if (source.orientation) {
      const angle = this.calculateConeAngle(source);
      if (angle > source.coneInnerAngle) {
        if (angle > source.coneOuterAngle) {
          volume *= source.coneOuterGain;
        } else {
          const ratio = (angle - source.coneInnerAngle) / (source.coneOuterAngle - source.coneInnerAngle);
          volume *= (1 - ratio) + ratio * source.coneOuterGain;
        }
      }
    }

    // Occlusion (simplified)
    if (this.config.enableOcclusion) {
      const occlusion = this.calculateOcclusion(source);
      volume *= (1 - occlusion);
    }

    return Math.max(0, Math.min(1, volume));
  }

  private calculateConeAngle(source: AudioSource): number {
    if (!source.orientation) return 0;

    const toListener = vec3.subtract(vec3.create(), this.listener.position, source.position);
    vec3.normalize(toListener, toListener);

    const dot = vec3.dot(source.orientation, toListener);
    return Math.acos(Math.max(-1, Math.min(1, dot)));
  }

  private calculateOcclusion(_source: AudioSource): number {
    // Simplified occlusion calculation
    // In a real implementation, this would use raycasting against the scene geometry
    return 0;
  }

  private applyZoneEffects(source: AudioSource): void {
    const zone = this.getCurrentZone(source.position);
    if (!zone) return;

    // Apply reverb based on zone settings
    if (this.config.enableReverb) {
      this.updateReverbForZone(zone);
    }
  }

  private getCurrentZone(position: vec3): AudioZone | null {
    for (const zone of this.zones.values()) {
      if (this.isPositionInZone(position, zone)) {
        return zone;
      }
    }
    return null;
  }

  private isPositionInZone(position: vec3, zone: AudioZone): boolean {
    // Simple box bounds check
    const bounds = zone.bounds;
    const halfSize = vec3.scale(vec3.create(), bounds.size, 0.5);

    const localPos = vec3.subtract(vec3.create(), position, bounds.center);

    return Math.abs(localPos[0]) <= halfSize[0] &&
           Math.abs(localPos[1]) <= halfSize[1] &&
           Math.abs(localPos[2]) <= halfSize[2];
  }

  private updateReverbForZone(_zone: AudioZone): void {
    // Update convolver node with zone's reverb settings
    // This would require generating impulse responses based on zone properties
    // For now, this is a placeholder
  }

  private updateAnalyser(): void {
    this.analyserNode.getFloatFrequencyData(this.analyserData.frequencyData);
    this.analyserNode.getFloatTimeDomainData(this.analyserData.timeData);

    // Calculate RMS volume
    let rms = 0;
    for (let i = 0; i < this.analyserData.timeData.length; i++) {
      rms += this.analyserData.timeData[i] * this.analyserData.timeData[i];
    }
    this.analyserData.volume = Math.sqrt(rms / this.analyserData.timeData.length);
  }

  // Public API
  async loadAudio(id: string, url: string): Promise<AudioBuffer> {
    if (this.audioBuffers.has(id)) {
      return this.audioBuffers.get(id)!;
    }

    if (this.loadingPromises.has(id)) {
      return this.loadingPromises.get(id)!;
    }

    const loadingPromise = this.fetchAndDecodeAudio(url);
    this.loadingPromises.set(id, loadingPromise);

    try {
      const buffer = await loadingPromise;
      this.audioBuffers.set(id, buffer);
      this.loadingPromises.delete(id);
      return buffer;
    } catch (error) {
      this.loadingPromises.delete(id);
      throw error;
    }
  }

  private async fetchAndDecodeAudio(url: string): Promise<AudioBuffer> {
    const response = await fetch(url);
    const arrayBuffer = await response.arrayBuffer();
    return this.audioContext.decodeAudioData(arrayBuffer);
  }

  createSource(config: Partial<AudioSource> & { id: string }): AudioSource {
    const source: AudioSource = {
      position: vec3.create(),
      velocity: vec3.create(),
      volume: 1.0,
      pitch: 1.0,
      minDistance: 1.0,
      maxDistance: 100.0,
      rolloffFactor: 1.0,
      coneInnerAngle: Math.PI * 2,
      coneOuterAngle: Math.PI * 2,
      coneOuterGain: 0.0,
      buffer: null,
      loop: false,
      autoplay: false,
      isPlaying: false,
      isPaused: false,
      currentTime: 0,
      duration: 0,
      effects: [],
      dopplerFactor: 1.0,
      distanceModel: 'inverse',
      ...config
    };

    if (source.buffer) {
      source.duration = source.buffer.duration;
    }

    this.sources.set(source.id, source);
    this.createAudioNodes(source);

    if (source.autoplay) {
      this.playSource(source.id);
    }

    return source;
  }

  private createAudioNodes(source: AudioSource): void {
    const ctx = this.audioContext;

    // Create gain node for volume control
    const gainNode = ctx.createGain();
    gainNode.gain.value = source.volume;
    this.gainNodes.set(source.id, gainNode);

    // Create panner node for 3D positioning
    const pannerNode = ctx.createPanner();
    pannerNode.panningModel = 'HRTF';
    pannerNode.distanceModel = source.distanceModel;
    pannerNode.refDistance = source.minDistance;
    pannerNode.maxDistance = source.maxDistance;
    pannerNode.rolloffFactor = source.rolloffFactor;
    pannerNode.coneInnerAngle = source.coneInnerAngle;
    pannerNode.coneOuterAngle = source.coneOuterAngle;
    pannerNode.coneOuterGain = source.coneOuterGain;

    this.pannerNodes.set(source.id, pannerNode);

    // Connect nodes
    gainNode.connect(pannerNode);
    pannerNode.connect(this.masterGainNode);
  }

  playSource(sourceId: string): void {
    const source = this.sources.get(sourceId);
    if (!source || !source.buffer) return;

    // Stop existing source node if playing
    this.stopSource(sourceId);

    const ctx = this.audioContext;
    const gainNode = this.gainNodes.get(sourceId);

    if (!gainNode) return;

    // Create new source node
    const sourceNode = ctx.createBufferSource();
    sourceNode.buffer = source.buffer;
    sourceNode.loop = source.loop;
    sourceNode.playbackRate.value = source.pitch;

    // Connect source to gain
    sourceNode.connect(gainNode);

    // Store reference and start playing
    this.sourceNodes.set(sourceId, sourceNode);
    sourceNode.start(0, source.currentTime);

    source.isPlaying = true;
    source.isPaused = false;

    // Handle end event
    sourceNode.onended = () => {
      if (!source.loop) {
        source.isPlaying = false;
        source.currentTime = 0;
        this.sourceNodes.delete(sourceId);
      }
    };
  }

  pauseSource(sourceId: string): void {
    const source = this.sources.get(sourceId);
    if (!source || !source.isPlaying) return;

    this.stopSource(sourceId);
    source.isPaused = true;
  }

  stopSource(sourceId: string): void {
    const source = this.sources.get(sourceId);
    const sourceNode = this.sourceNodes.get(sourceId);

    if (sourceNode) {
      sourceNode.stop();
      sourceNode.disconnect();
      this.sourceNodes.delete(sourceId);
    }

    if (source) {
      source.isPlaying = false;
      source.isPaused = false;
      source.currentTime = 0;
    }
  }

  updateListener(listener: Partial<AudioListener>): void {
    Object.assign(this.listener, listener);

    const ctx = this.audioContext;

    if (ctx.listener.positionX) {
      ctx.listener.positionX.value = this.listener.position[0];
      ctx.listener.positionY.value = this.listener.position[1];
      ctx.listener.positionZ.value = this.listener.position[2];

      ctx.listener.forwardX.value = this.listener.orientation.forward[0];
      ctx.listener.forwardY.value = this.listener.orientation.forward[1];
      ctx.listener.forwardZ.value = this.listener.orientation.forward[2];

      ctx.listener.upX.value = this.listener.orientation.up[0];
      ctx.listener.upY.value = this.listener.orientation.up[1];
      ctx.listener.upZ.value = this.listener.orientation.up[2];
    } else {
      (ctx.listener as any).setPosition(
        this.listener.position[0],
        this.listener.position[1],
        this.listener.position[2]
      );

      (ctx.listener as any).setOrientation(
        this.listener.orientation.forward[0],
        this.listener.orientation.forward[1],
        this.listener.orientation.forward[2],
        this.listener.orientation.up[0],
        this.listener.orientation.up[1],
        this.listener.orientation.up[2]
      );
    }
  }

  updateSourcePosition(sourceId: string, position: vec3): void {
    const source = this.sources.get(sourceId);
    if (source) {
      vec3.copy(source.position, position);
    }
  }

  updateSourceVelocity(sourceId: string, velocity: vec3): void {
    const source = this.sources.get(sourceId);
    if (source) {
      if (!source.velocity) source.velocity = vec3.create();
      vec3.copy(source.velocity, velocity);
    }
  }

  setMasterVolume(volume: number): void {
    this.config.masterVolume = Math.max(0, Math.min(1, volume));
    this.masterGainNode.gain.value = this.config.masterVolume;
  }

  createAudioZone(zone: AudioZone): void {
    this.zones.set(zone.id, zone);
  }

  removeAudioZone(zoneId: string): void {
    this.zones.delete(zoneId);
  }

  removeSource(sourceId: string): void {
    this.stopSource(sourceId);

    const gainNode = this.gainNodes.get(sourceId);
    const pannerNode = this.pannerNodes.get(sourceId);

    if (gainNode) {
      gainNode.disconnect();
      this.gainNodes.delete(sourceId);
    }

    if (pannerNode) {
      pannerNode.disconnect();
      this.pannerNodes.delete(sourceId);
    }

    this.sources.delete(sourceId);
  }

  getAnalyserData(): typeof this.analyserData {
    return this.analyserData;
  }

  getStats() {
    const activeSources = Array.from(this.sources.values()).filter(s => s.isPlaying).length;

    return {
      activeSources,
      totalSources: this.sources.size,
      loadedBuffers: this.audioBuffers.size,
      audioZones: this.zones.size,
      contextState: this.audioContext.state,
      sampleRate: this.audioContext.sampleRate,
      masterVolume: this.config.masterVolume,
      memoryUsage: this.getMemoryUsage()
    };
  }

  private getMemoryUsage(): number {
    let totalBytes = 0;

    for (const buffer of this.audioBuffers.values()) {
      totalBytes += buffer.length * buffer.numberOfChannels * 4; // 32-bit float
    }

    return totalBytes;
  }

  suspend(): void {
    this.audioContext.suspend();
  }

  resume(): void {
    this.audioContext.resume();
  }

  dispose(): void {
    // Stop all sources
    for (const sourceId of this.sources.keys()) {
      this.removeSource(sourceId);
    }

    // Clear update timer
    if (this.updateTimer) {
      clearInterval(this.updateTimer);
      this.updateTimer = null;
    }

    // Disconnect audio graph
    this.masterGainNode.disconnect();
    this.compressorNode.disconnect();
    this.convolverNode.disconnect();
    this.analyserNode.disconnect();

    // Clear maps
    this.sources.clear();
    this.zones.clear();
    this.audioBuffers.clear();
    this.loadingPromises.clear();
    this.pannerNodes.clear();
    this.gainNodes.clear();
    this.sourceNodes.clear();

    // Close audio context
    this.audioContext.close();
  }
}

import { mat4, vec3, quat } from 'gl-matrix';

export enum CameraType {
  PERSPECTIVE = 'perspective',
  ORTHOGRAPHIC = 'orthographic'
}

export interface CameraSettings {
  type: CameraType;
  fov?: number;
  aspect?: number;
  near: number;
  far: number;
  left?: number;
  right?: number;
  top?: number;
  bottom?: number;
}

export class Camera {
  public position = vec3.create();
  public rotation = quat.create();
  public target = vec3.create();
  public up = vec3.fromValues(0, 1, 0);

  public viewMatrix = mat4.create();
  public projectionMatrix = mat4.create();
  public viewProjectionMatrix = mat4.create();
  public inverseViewMatrix = mat4.create();
  public inverseProjectionMatrix = mat4.create();

  private settings: CameraSettings;
  private dirty = true;
  private frustumPlanes = new Float32Array(24); // 6 planes * 4 components

  constructor(settings: CameraSettings) {
    this.settings = { ...settings };
    this.updateProjectionMatrix();
  }

  setPosition(x: number, y: number, z: number): void {
    vec3.set(this.position, x, y, z);
    this.dirty = true;
  }

  setRotation(x: number, y: number, z: number, w: number): void {
    quat.set(this.rotation, x, y, z, w);
    this.dirty = true;
  }

  lookAt(target: vec3): void {
    vec3.copy(this.target, target);
    mat4.lookAt(this.viewMatrix, this.position, target, this.up);
    mat4.invert(this.inverseViewMatrix, this.viewMatrix);
    this.extractRotationFromViewMatrix();
    this.updateViewProjectionMatrix();
    this.updateFrustumPlanes();
    this.dirty = false;
  }

  setTarget(x: number, y: number, z: number): void {
    vec3.set(this.target, x, y, z);
    this.dirty = true;
  }

  translate(delta: vec3): void {
    vec3.add(this.position, this.position, delta);
    this.dirty = true;
  }

  rotate(axis: vec3, angle: number): void {
    const rotation = quat.create();
    quat.setAxisAngle(rotation, axis, angle);
    quat.multiply(this.rotation, this.rotation, rotation);
    this.dirty = true;
  }

  rotateX(angle: number): void {
    this.rotate(vec3.fromValues(1, 0, 0), angle);
  }

  rotateY(angle: number): void {
    this.rotate(vec3.fromValues(0, 1, 0), angle);
  }

  rotateZ(angle: number): void {
    this.rotate(vec3.fromValues(0, 0, 1), angle);
  }

  moveForward(distance: number): void {
    const forward = this.getForwardVector();
    vec3.scaleAndAdd(this.position, this.position, forward, distance);
    this.dirty = true;
  }

  moveBackward(distance: number): void {
    this.moveForward(-distance);
  }

  moveRight(distance: number): void {
    const right = this.getRightVector();
    vec3.scaleAndAdd(this.position, this.position, right, distance);
    this.dirty = true;
  }

  moveLeft(distance: number): void {
    this.moveRight(-distance);
  }

  moveUp(distance: number): void {
    const up = this.getUpVector();
    vec3.scaleAndAdd(this.position, this.position, up, distance);
    this.dirty = true;
  }

  moveDown(distance: number): void {
    this.moveUp(-distance);
  }

  getForwardVector(): vec3 {
    const forward = vec3.create();
    vec3.transformQuat(forward, vec3.fromValues(0, 0, -1), this.rotation);
    return forward;
  }

  getRightVector(): vec3 {
    const right = vec3.create();
    vec3.transformQuat(right, vec3.fromValues(1, 0, 0), this.rotation);
    return right;
  }

  getUpVector(): vec3 {
    const up = vec3.create();
    vec3.transformQuat(up, vec3.fromValues(0, 1, 0), this.rotation);
    return up;
  }

  updateMatrices(): void {
    if (!this.dirty) return;

    // Update view matrix from position and rotation
    const translationMatrix = mat4.create();
    const rotationMatrix = mat4.create();

    mat4.fromTranslation(translationMatrix, this.position);
    mat4.fromQuat(rotationMatrix, this.rotation);

    mat4.multiply(this.viewMatrix, rotationMatrix, translationMatrix);
    mat4.invert(this.viewMatrix, this.viewMatrix);
    mat4.invert(this.inverseViewMatrix, this.viewMatrix);

    this.updateViewProjectionMatrix();
    this.updateFrustumPlanes();
    this.dirty = false;
  }

  private updateViewProjectionMatrix(): void {
    mat4.multiply(this.viewProjectionMatrix, this.projectionMatrix, this.viewMatrix);
  }

  private extractRotationFromViewMatrix(): void {
    const _rotationMatrix = mat4.create();
    mat4.getRotation(this.rotation, this.inverseViewMatrix);
  }

  updateProjectionMatrix(): void {
    if (this.settings.type === CameraType.PERSPECTIVE) {
      mat4.perspective(
        this.projectionMatrix,
        this.settings.fov || Math.PI / 4,
        this.settings.aspect || 1,
        this.settings.near,
        this.settings.far
      );
    } else {
      mat4.ortho(
        this.projectionMatrix,
        this.settings.left || -1,
        this.settings.right || 1,
        this.settings.bottom || -1,
        this.settings.top || 1,
        this.settings.near,
        this.settings.far
      );
    }

    mat4.invert(this.inverseProjectionMatrix, this.projectionMatrix);
    this.updateViewProjectionMatrix();
    this.updateFrustumPlanes();
  }

  private updateFrustumPlanes(): void {
    // Extract frustum planes from view-projection matrix
    const vp = this.viewProjectionMatrix;

    // Left plane
    this.frustumPlanes[0] = vp[3] + vp[0];
    this.frustumPlanes[1] = vp[7] + vp[4];
    this.frustumPlanes[2] = vp[11] + vp[8];
    this.frustumPlanes[3] = vp[15] + vp[12];

    // Right plane
    this.frustumPlanes[4] = vp[3] - vp[0];
    this.frustumPlanes[5] = vp[7] - vp[4];
    this.frustumPlanes[6] = vp[11] - vp[8];
    this.frustumPlanes[7] = vp[15] - vp[12];

    // Bottom plane
    this.frustumPlanes[8] = vp[3] + vp[1];
    this.frustumPlanes[9] = vp[7] + vp[5];
    this.frustumPlanes[10] = vp[11] + vp[9];
    this.frustumPlanes[11] = vp[15] + vp[13];

    // Top plane
    this.frustumPlanes[12] = vp[3] - vp[1];
    this.frustumPlanes[13] = vp[7] - vp[5];
    this.frustumPlanes[14] = vp[11] - vp[9];
    this.frustumPlanes[15] = vp[15] - vp[13];

    // Near plane
    this.frustumPlanes[16] = vp[3] + vp[2];
    this.frustumPlanes[17] = vp[7] + vp[6];
    this.frustumPlanes[18] = vp[11] + vp[10];
    this.frustumPlanes[19] = vp[15] + vp[14];

    // Far plane
    this.frustumPlanes[20] = vp[3] - vp[2];
    this.frustumPlanes[21] = vp[7] - vp[6];
    this.frustumPlanes[22] = vp[11] - vp[10];
    this.frustumPlanes[23] = vp[15] - vp[14];

    // Normalize planes
    for (let i = 0; i < 6; i++) {
      const offset = i * 4;
      const length = Math.sqrt(
        this.frustumPlanes[offset] * this.frustumPlanes[offset] +
        this.frustumPlanes[offset + 1] * this.frustumPlanes[offset + 1] +
        this.frustumPlanes[offset + 2] * this.frustumPlanes[offset + 2]
      );

      if (length > 0) {
        this.frustumPlanes[offset] /= length;
        this.frustumPlanes[offset + 1] /= length;
        this.frustumPlanes[offset + 2] /= length;
        this.frustumPlanes[offset + 3] /= length;
      }
    }
  }

  // Frustum culling methods
  isPointInFrustum(point: vec3): boolean {
    for (let i = 0; i < 6; i++) {
      const offset = i * 4;
      const distance = 
        this.frustumPlanes[offset] * point[0] +
        this.frustumPlanes[offset + 1] * point[1] +
        this.frustumPlanes[offset + 2] * point[2] +
        this.frustumPlanes[offset + 3];

      if (distance < 0) return false;
    }
    return true;
  }

  isSphereInFrustum(center: vec3, radius: number): boolean {
    for (let i = 0; i < 6; i++) {
      const offset = i * 4;
      const distance = 
        this.frustumPlanes[offset] * center[0] +
        this.frustumPlanes[offset + 1] * center[1] +
        this.frustumPlanes[offset + 2] * center[2] +
        this.frustumPlanes[offset + 3];

      if (distance < -radius) return false;
    }
    return true;
  }

  isAABBInFrustum(min: vec3, max: vec3): boolean {
    for (let i = 0; i < 6; i++) {
      const offset = i * 4;
      const nx = this.frustumPlanes[offset];
      const ny = this.frustumPlanes[offset + 1];
      const nz = this.frustumPlanes[offset + 2];
      const d = this.frustumPlanes[offset + 3];

      // Find the positive vertex (farthest along the normal)
      const px = nx > 0 ? max[0] : min[0];
      const py = ny > 0 ? max[1] : min[1];
      const pz = nz > 0 ? max[2] : min[2];

      if (nx * px + ny * py + nz * pz + d < 0) {
        return false;
      }
    }
    return true;
  }

  // Ray casting
  screenToWorldRay(screenX: number, screenY: number, viewportWidth: number, viewportHeight: number): { origin: vec3, direction: vec3 } {
    // Convert screen coordinates to NDC
    const ndcX = (2 * screenX) / viewportWidth - 1;
    const ndcY = 1 - (2 * screenY) / viewportHeight;

    // Convert to clip space
    const clipCoords = vec4.fromValues(ndcX, ndcY, -1, 1);

    // Convert to eye space
    const eyeCoords = vec4.create();
    vec4.transformMat4(eyeCoords, clipCoords, this.inverseProjectionMatrix);
    eyeCoords[2] = -1;
    eyeCoords[3] = 0;

    // Convert to world space
    const worldCoords = vec4.create();
    vec4.transformMat4(worldCoords, eyeCoords, this.inverseViewMatrix);

    const direction = vec3.fromValues(worldCoords[0], worldCoords[1], worldCoords[2]);
    vec3.normalize(direction, direction);

    return {
      origin: vec3.clone(this.position),
      direction
    };
  }

  // Settings
  setFOV(fov: number): void {
    this.settings.fov = fov;
    this.updateProjectionMatrix();
  }

  setAspectRatio(aspect: number): void {
    this.settings.aspect = aspect;
    this.updateProjectionMatrix();
  }

  setNearFar(near: number, far: number): void {
    this.settings.near = near;
    this.settings.far = far;
    this.updateProjectionMatrix();
  }

  setOrthographicBounds(left: number, right: number, bottom: number, top: number): void {
    this.settings.left = left;
    this.settings.right = right;
    this.settings.bottom = bottom;
    this.settings.top = top;
    this.updateProjectionMatrix();
  }

  getFrustumPlanes(): Float32Array {
    return this.frustumPlanes;
  }

  getSettings(): CameraSettings {
    return { ...this.settings };
  }
}

import { vec3, _vec4} from 'gl-matrix';
import { TextureManager } from './TextureManager';
import { ShaderProgram } from './Shader';

export interface MaterialProperties {
  albedo?: vec3;
  metallic?: number;
  roughness?: number;
  normalScale?: number;
  emissive?: vec3;
  emissiveIntensity?: number;
  opacity?: number;
  alphaTest?: number;
  doubleSided?: boolean;
  transparent?: boolean;
  depthWrite?: boolean;
  depthTest?: boolean;
  blendMode?: BlendMode;
  cullMode?: CullMode;
}

export interface MaterialTextures {
  albedoMap?: string;
  normalMap?: string;
  metallicRoughnessMap?: string;
  emissiveMap?: string;
  occlusionMap?: string;
  heightMap?: string;
  detailNormalMap?: string;
  detailAlbedoMap?: string;
  cubeMap?: string;
}

export enum BlendMode {
  OPAQUE = 'OPAQUE',
  ALPHA_BLEND = 'ALPHA_BLEND',
  ADDITIVE = 'ADDITIVE',
  MULTIPLY = 'MULTIPLY',
  PREMULTIPLIED_ALPHA = 'PREMULTIPLIED_ALPHA'
}

export enum CullMode {
  NONE = 'NONE',
  FRONT = 'FRONT',
  BACK = 'BACK'
}

export class Material {
  public shaderName: string;
  public properties: MaterialProperties;
  public textures: MaterialTextures;
  public uniformValues = new Map<string, any>();

  private gl: WebGL2RenderingContext;
  private textureSlots = new Map<string, number>();
  private nextTextureSlot = 0;

  constructor(
    gl: WebGL2RenderingContext,
    shaderName: string = 'pbr',
    properties: MaterialProperties = {},
    textures: MaterialTextures = {}
  ) {
    this.gl = gl;
    this.shaderName = shaderName;
    this.properties = {
      albedo: vec3.fromValues(1.0, 1.0, 1.0),
      metallic: 0.0,
      roughness: 0.5,
      normalScale: 1.0,
      emissive: vec3.fromValues(0.0, 0.0, 0.0),
      emissiveIntensity: 1.0,
      opacity: 1.0,
      alphaTest: 0.5,
      doubleSided: false,
      transparent: false,
      depthWrite: true,
      depthTest: true,
      blendMode: BlendMode.OPAQUE,
      cullMode: CullMode.BACK,
      ...properties
    };
    this.textures = { ...textures };

    this.setupTextureSlots();
  }

  private setupTextureSlots(): void {
    const textureNames = [
      'albedoMap',
      'normalMap', 
      'metallicRoughnessMap',
      'emissiveMap',
      'occlusionMap',
      'heightMap',
      'detailNormalMap',
      'detailAlbedoMap',
      'cubeMap'
    ];

    for (const textureName of textureNames) {
      this.textureSlots.set(textureName, this.nextTextureSlot++);
    }
  }

  bindTextures(gl: WebGL2RenderingContext, textureManager: TextureManager): void {
    for (const [textureName, texturePath] of Object.entries(this.textures)) {
      if (!texturePath) continue;

      const slot = this.textureSlots.get(textureName);
      if (slot === undefined) continue;

      gl.activeTexture(gl.TEXTURE0 + slot);

      const texture = textureManager.getTexture(texturePath);
      if (texture) {
        gl.bindTexture(gl.TEXTURE_2D, texture);
      } else {
        // Bind default texture
        gl.bindTexture(gl.TEXTURE_2D, textureManager.getDefaultTexture(textureName));
      }
    }
  }

  setUniforms(shader: ShaderProgram): void {
    // Material properties
    shader.setUniform3f('u_albedo', this.properties.albedo![0], this.properties.albedo![1], this.properties.albedo![2]);
    shader.setUniform1f('u_metallic', this.properties.metallic!);
    shader.setUniform1f('u_roughness', this.properties.roughness!);
    shader.setUniform1f('u_normalScale', this.properties.normalScale!);
    shader.setUniform3f('u_emissive', this.properties.emissive![0], this.properties.emissive![1], this.properties.emissive![2]);
    shader.setUniform1f('u_emissiveIntensity', this.properties.emissiveIntensity!);
    shader.setUniform1f('u_opacity', this.properties.opacity!);
    shader.setUniform1f('u_alphaTest', this.properties.alphaTest!);

    // Texture samplers
    for (const [textureName, slot] of this.textureSlots) {
      const uniformName = `u_${textureName}`;
      shader.setUniform1i(uniformName, slot);
    }

    // Texture flags
    shader.setUniform1i('u_hasAlbedoMap', this.textures.albedoMap ? 1 : 0);
    shader.setUniform1i('u_hasNormalMap', this.textures.normalMap ? 1 : 0);
    shader.setUniform1i('u_hasMetallicRoughnessMap', this.textures.metallicRoughnessMap ? 1 : 0);
    shader.setUniform1i('u_hasEmissiveMap', this.textures.emissiveMap ? 1 : 0);
    shader.setUniform1i('u_hasOcclusionMap', this.textures.occlusionMap ? 1 : 0);
    shader.setUniform1i('u_hasHeightMap', this.textures.heightMap ? 1 : 0);
    shader.setUniform1i('u_hasDetailNormalMap', this.textures.detailNormalMap ? 1 : 0);
    shader.setUniform1i('u_hasDetailAlbedoMap', this.textures.detailAlbedoMap ? 1 : 0);
    shader.setUniform1i('u_hasCubeMap', this.textures.cubeMap ? 1 : 0);

    // Custom uniform values
    for (const [name, value] of this.uniformValues) {
      this.setUniformValue(shader, name, value);
    }
  }

  private setUniformValue(shader: ShaderProgram, name: string, value: any): void {
    if (typeof value === 'number') {
      shader.setUniform1f(name, value);
    } else if (Array.isArray(value)) {
      switch (value.length) {
        case 2:
          shader.setUniform2f(name, value[0], value[1]);
          break;
        case 3:
          shader.setUniform3f(name, value[0], value[1], value[2]);
          break;
        case 4:
          shader.setUniform4f(name, value[0], value[1], value[2], value[3]);
          break;
        default:
          shader.setUniform1fv(name, value);
      }
    } else if (value instanceof Float32Array) {
      if (value.length === 16) {
        shader.setUniformMatrix4fv(name, value);
      } else if (value.length === 9) {
        shader.setUniformMatrix3fv(name, value);
      } else if (value.length === 4) {
        shader.setUniformMatrix2fv(name, value);
      } else {
        shader.setUniform1fv(name, value);
      }
    }
  }

  setRenderState(gl: WebGL2RenderingContext): void {
    // Depth testing
    if (this.properties.depthTest) {
      gl.enable(gl.DEPTH_TEST);
    } else {
      gl.disable(gl.DEPTH_TEST);
    }

    // Depth writing
    gl.depthMask(this.properties.depthWrite!);

    // Face culling
    switch (this.properties.cullMode) {
      case CullMode.NONE:
        gl.disable(gl.CULL_FACE);
        break;
      case CullMode.FRONT:
        gl.enable(gl.CULL_FACE);
        gl.cullFace(gl.FRONT);
        break;
      case CullMode.BACK:
        gl.enable(gl.CULL_FACE);
        gl.cullFace(gl.BACK);
        break;
    }

    // Blending
    if (this.properties.transparent || this.properties.blendMode !== BlendMode.OPAQUE) {
      gl.enable(gl.BLEND);
      this.setBlendMode(gl);
    } else {
      gl.disable(gl.BLEND);
    }
  }

  private setBlendMode(gl: WebGL2RenderingContext): void {
    switch (this.properties.blendMode) {
      case BlendMode.ALPHA_BLEND:
        gl.blendFunc(gl.SRC_ALPHA, gl.ONE_MINUS_SRC_ALPHA);
        break;
      case BlendMode.ADDITIVE:
        gl.blendFunc(gl.ONE, gl.ONE);
        break;
      case BlendMode.MULTIPLY:
        gl.blendFunc(gl.DST_COLOR, gl.ZERO);
        break;
      case BlendMode.PREMULTIPLIED_ALPHA:
        gl.blendFunc(gl.ONE, gl.ONE_MINUS_SRC_ALPHA);
        break;
      default:
        gl.blendFunc(gl.SRC_ALPHA, gl.ONE_MINUS_SRC_ALPHA);
    }
  }

  // Setters for material properties
  setAlbedo(color: vec3): void {
    vec3.copy(this.properties.albedo!, color);
  }

  setMetallic(metallic: number): void {
    this.properties.metallic = metallic;
  }

  setRoughness(roughness: number): void {
    this.properties.roughness = roughness;
  }

  setEmissive(color: vec3, intensity: number = 1.0): void {
    vec3.copy(this.properties.emissive!, color);
    this.properties.emissiveIntensity = intensity;
  }

  setOpacity(opacity: number): void {
    this.properties.opacity = opacity;
    this.properties.transparent = opacity < 1.0;
  }

  setTexture(type: keyof MaterialTextures, path: string): void {
    this.textures[type] = path;
  }

  setUniform(name: string, value: any): void {
    this.uniformValues.set(name, value);
  }

  // Getters
  isTransparent(): boolean {
    return this.properties.transparent! || this.properties.opacity! < 1.0;
  }

  needsAlphaTest(): boolean {
    return this.properties.alphaTest! > 0.0 && this.properties.alphaTest! < 1.0;
  }

  clone(): Material {
    const cloned = new Material(
      this.gl,
      this.shaderName,
      { ...this.properties },
      { ...this.textures }
    );

    // Copy uniform values
    for (const [name, value] of this.uniformValues) {
      cloned.uniformValues.set(name, value);
    }

    return cloned;
  }

  // Static factory methods for common materials
  static createPBRMaterial(
    gl: WebGL2RenderingContext,
    albedo: vec3 = vec3.fromValues(1, 1, 1),
    metallic: number = 0.0,
    roughness: number = 0.5
  ): Material {
    return new Material(gl, 'pbr', {
      albedo,
      metallic,
      roughness
    });
  }

  static createUnlitMaterial(
    gl: WebGL2RenderingContext,
    color: vec3 = vec3.fromValues(1, 1, 1),
    texture?: string
  ): Material {
    return new Material(gl, 'unlit', {
      albedo: color
    }, texture ? { albedoMap: texture } : Record<string, any>);
  }

  static createEmissiveMaterial(
    gl: WebGL2RenderingContext,
    emissiveColor: vec3,
    intensity: number = 1.0
  ): Material {
    return new Material(gl, 'emissive', {
      emissive: emissiveColor,
      emissiveIntensity: intensity
    });
  }

  static createTransparentMaterial(
    gl: WebGL2RenderingContext,
    albedo: vec3,
    opacity: number
  ): Material {
    return new Material(gl, 'pbr', {
      albedo,
      opacity,
      transparent: true,
      blendMode: BlendMode.ALPHA_BLEND
    });
  }
}

export interface TextureOptions {
  format?: number;
  internalFormat?: number;
  type?: number;
  wrapS?: number;
  wrapT?: number;
  minFilter?: number;
  magFilter?: number;
  generateMipmaps?: boolean;
  flipY?: boolean;
  premultiplyAlpha?: boolean;
  unpackAlignment?: number;
  anisotropy?: number;
}

export interface CubeMapFaces {
  positiveX: string | ImageData | HTMLImageElement;
  negativeX: string | ImageData | HTMLImageElement;
  positiveY: string | ImageData | HTMLImageElement;
  negativeY: string | ImageData | HTMLImageElement;
  positiveZ: string | ImageData | HTMLImageElement;
  negativeZ: string | ImageData | HTMLImageElement;
}

export class TextureManager {
  private gl: WebGL2RenderingContext;
  private textures = new Map<string, WebGLTexture>();
  private textureData = new Map<string, { width: number; height: number; format: number }>();
  private loadingPromises = new Map<string, Promise<WebGLTexture>>();
  private defaultTextures = new Map<string, WebGLTexture>();
  private anisotropyExt: EXT_texture_filter_anisotropic | null;

  constructor(gl: WebGL2RenderingContext) {
    this.gl = gl;
    this.anisotropyExt = gl.getExtension('EXT_texture_filter_anisotropic');
    this.createDefaultTextures();
  }

  private createDefaultTextures(): void {
    const _gl = this.gl;

    // White 1x1 texture
    this.defaultTextures.set('white', this.createSolidColorTexture([255, 255, 255, 255]));

    // Black 1x1 texture
    this.defaultTextures.set('black', this.createSolidColorTexture([0, 0, 0, 255]));

    // Normal map default (128, 128, 255, 255) - pointing up
    this.defaultTextures.set('normalMap', this.createSolidColorTexture([128, 128, 255, 255]));

    // Default metallic/roughness (0, 128, 0, 255) - no metallic, 0.5 roughness
    this.defaultTextures.set('metallicRoughnessMap', this.createSolidColorTexture([0, 128, 0, 255]));

    // Default occlusion (full white - no occlusion)
    this.defaultTextures.set('occlusionMap', this.createSolidColorTexture([255, 255, 255, 255]));

    // Default emissive (black - no emission)
    this.defaultTextures.set('emissiveMap', this.createSolidColorTexture([0, 0, 0, 255]));

    // Checkerboard pattern for missing textures
    this.defaultTextures.set('missing', this.createCheckerboardTexture(64, 64));

    // Default cube map
    this.defaultTextures.set('cubeMap', this.createDefaultCubeMap());
  }

  private createSolidColorTexture(color: number[]): WebGLTexture {
    const gl = this.gl;
    const texture = gl.createTexture()!;

    gl.bindTexture(gl.TEXTURE_2D, texture);
    gl.texImage2D(
      gl.TEXTURE_2D,
      0,
      gl.RGBA,
      1,
      1,
      0,
      gl.RGBA,
      gl.UNSIGNED_BYTE,
      new Uint8Array(color)
    );

    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MIN_FILTER, gl.NEAREST);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MAG_FILTER, gl.NEAREST);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_S, gl.REPEAT);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_T, gl.REPEAT);

    return texture;
  }

  private createCheckerboardTexture(width: number, height: number): WebGLTexture {
    const gl = this.gl;
    const texture = gl.createTexture()!;
    const data = new Uint8Array(width * height * 4);

    for (let y = 0; y < height; y++) {
      for (let x = 0; x < width; x++) {
        const index = (y * width + x) * 4;
        const checker = ((x >> 3) + (y >> 3)) & 1;
        const color = checker ? 255 : 128;

        data[index] = color;     // R
        data[index + 1] = color; // G
        data[index + 2] = color; // B
        data[index + 3] = 255;   // A
      }
    }

    gl.bindTexture(gl.TEXTURE_2D, texture);
    gl.texImage2D(gl.TEXTURE_2D, 0, gl.RGBA, width, height, 0, gl.RGBA, gl.UNSIGNED_BYTE, data);
    gl.generateMipmap(gl.TEXTURE_2D);

    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MIN_FILTER, gl.LINEAR_MIPMAP_LINEAR);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MAG_FILTER, gl.LINEAR);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_S, gl.REPEAT);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_T, gl.REPEAT);

    return texture;
  }

  private createDefaultCubeMap(): WebGLTexture {
    const gl = this.gl;
    const texture = gl.createTexture()!;

    gl.bindTexture(gl.TEXTURE_CUBE_MAP, texture);

    // Create a simple gradient cube map
    const faces = [
      gl.TEXTURE_CUBE_MAP_POSITIVE_X, // +X (red)
      gl.TEXTURE_CUBE_MAP_NEGATIVE_X, // -X (dark red)
      gl.TEXTURE_CUBE_MAP_POSITIVE_Y, // +Y (green)
      gl.TEXTURE_CUBE_MAP_NEGATIVE_Y, // -Y (dark green)
      gl.TEXTURE_CUBE_MAP_POSITIVE_Z, // +Z (blue)
      gl.TEXTURE_CUBE_MAP_NEGATIVE_Z  // -Z (dark blue)
    ];

    const colors = [
      [255, 128, 128, 255], // Light red
      [128, 64, 64, 255],   // Dark red
      [128, 255, 128, 255], // Light green
      [64, 128, 64, 255],   // Dark green
      [128, 128, 255, 255], // Light blue
      [64, 64, 128, 255]    // Dark blue
    ];

    for (let i = 0; i < 6; i++) {
      gl.texImage2D(
        faces[i],
        0,
        gl.RGBA,
        1,
        1,
        0,
        gl.RGBA,
        gl.UNSIGNED_BYTE,
        new Uint8Array(colors[i])
      );
    }

    gl.texParameteri(gl.TEXTURE_CUBE_MAP, gl.TEXTURE_MIN_FILTER, gl.LINEAR);
    gl.texParameteri(gl.TEXTURE_CUBE_MAP, gl.TEXTURE_MAG_FILTER, gl.LINEAR);
    gl.texParameteri(gl.TEXTURE_CUBE_MAP, gl.TEXTURE_WRAP_S, gl.CLAMP_TO_EDGE);
    gl.texParameteri(gl.TEXTURE_CUBE_MAP, gl.TEXTURE_WRAP_T, gl.CLAMP_TO_EDGE);
    gl.texParameteri(gl.TEXTURE_CUBE_MAP, gl.TEXTURE_WRAP_R, gl.CLAMP_TO_EDGE);

    return texture;
  }

  async loadTexture(path: string, options: TextureOptions = {}): Promise<WebGLTexture> {
    // Return cached texture if available
    if (this.textures.has(path)) {
      return this.textures.get(path)!;
    }

    // Return existing loading promise if in progress
    if (this.loadingPromises.has(path)) {
      return this.loadingPromises.get(path)!;
    }

    // Start loading
    const loadingPromise = this._loadTexture(path, options);
    this.loadingPromises.set(path, loadingPromise);

    try {
      const texture = await loadingPromise;
      this.textures.set(path, texture);
      return texture;
    } catch (error) {
      console.error(`Failed to load texture: ${path}`, error);
      // Return missing texture fallback
      return this.defaultTextures.get('missing')!;
    } finally {
      this.loadingPromises.delete(path);
    }
  }

  private async _loadTexture(path: string, options: TextureOptions): Promise<WebGLTexture> {
    const gl = this.gl;
    const texture = gl.createTexture();

    if (!texture) {
      throw new Error('Failed to create texture');
    }

    // Set default options
    const opts = {
      format: gl.RGBA,
      internalFormat: gl.RGBA8,
      type: gl.UNSIGNED_BYTE,
      wrapS: gl.REPEAT,
      wrapT: gl.REPEAT,
      minFilter: gl.LINEAR_MIPMAP_LINEAR,
      magFilter: gl.LINEAR,
      generateMipmaps: true,
      flipY: true,
      premultiplyAlpha: false,
      unpackAlignment: 4,
      anisotropy: 16,
      ...options
    };

    gl.bindTexture(gl.TEXTURE_2D, texture);

    // Set texture parameters
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_S, opts.wrapS);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_T, opts.wrapT);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MIN_FILTER, opts.minFilter);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MAG_FILTER, opts.magFilter);

    // Set anisotropic filtering if available
    if (this.anisotropyExt && opts.anisotropy > 1) {
      const maxAnisotropy = gl.getParameter(this.anisotropyExt.MAX_TEXTURE_MAX_ANISOTROPY_EXT);
      const anisotropy = Math.min(opts.anisotropy, maxAnisotropy);
      gl.texParameterf(gl.TEXTURE_2D, this.anisotropyExt.TEXTURE_MAX_ANISOTROPY_EXT, anisotropy);
    }

    // Set pixel store parameters
    gl.pixelStorei(gl.UNPACK_FLIP_Y_WEBGL, opts.flipY);
    gl.pixelStorei(gl.UNPACK_PREMULTIPLY_ALPHA_WEBGL, opts.premultiplyAlpha);
    gl.pixelStorei(gl.UNPACK_ALIGNMENT, opts.unpackAlignment);

    // Load image
    const image = await this.loadImage(path);

    // Upload texture data
    gl.texImage2D(
      gl.TEXTURE_2D,
      0,
      opts.internalFormat,
      opts.format,
      opts.type,
      image
    );

    // Generate mipmaps if requested
    if (opts.generateMipmaps && this.isPowerOfTwo(image.width) && this.isPowerOfTwo(image.height)) {
      gl.generateMipmap(gl.TEXTURE_2D);
    } else if (opts.generateMipmaps) {
      console.warn(`Cannot generate mipmaps for non-power-of-two texture: ${path} (${image.width}x${image.height})`);
      // Set filters to not use mipmaps
      gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MIN_FILTER, gl.LINEAR);
    }

    // Store texture metadata
    this.textureData.set(path, {
      width: image.width,
      height: image.height,
      format: opts.format
    });

    return texture;
  }

  async loadCubeMapTexture(faces: CubeMapFaces, options: TextureOptions = {}): Promise<WebGLTexture> {
    const gl = this.gl;
    const texture = gl.createTexture();

    if (!texture) {
      throw new Error('Failed to create cube map texture');
    }

    gl.bindTexture(gl.TEXTURE_CUBE_MAP, texture);

    const opts = {
      format: gl.RGBA,
      internalFormat: gl.RGBA8,
      type: gl.UNSIGNED_BYTE,
      minFilter: gl.LINEAR_MIPMAP_LINEAR,
      magFilter: gl.LINEAR,
      generateMipmaps: true,
      flipY: false, // Cube maps usually don't need flipping
      ...options
    };

    // Set texture parameters
    gl.texParameteri(gl.TEXTURE_CUBE_MAP, gl.TEXTURE_MIN_FILTER, opts.minFilter);
    gl.texParameteri(gl.TEXTURE_CUBE_MAP, gl.TEXTURE_MAG_FILTER, opts.magFilter);
    gl.texParameteri(gl.TEXTURE_CUBE_MAP, gl.TEXTURE_WRAP_S, gl.CLAMP_TO_EDGE);
    gl.texParameteri(gl.TEXTURE_CUBE_MAP, gl.TEXTURE_WRAP_T, gl.CLAMP_TO_EDGE);
    gl.texParameteri(gl.TEXTURE_CUBE_MAP, gl.TEXTURE_WRAP_R, gl.CLAMP_TO_EDGE);

    // Set pixel store parameters
    gl.pixelStorei(gl.UNPACK_FLIP_Y_WEBGL, opts.flipY);

    // Load all faces
    const faceTargets = [
      { target: gl.TEXTURE_CUBE_MAP_POSITIVE_X, source: faces.positiveX },
      { target: gl.TEXTURE_CUBE_MAP_NEGATIVE_X, source: faces.negativeX },
      { target: gl.TEXTURE_CUBE_MAP_POSITIVE_Y, source: faces.positiveY },
      { target: gl.TEXTURE_CUBE_MAP_NEGATIVE_Y, source: faces.negativeY },
      { target: gl.TEXTURE_CUBE_MAP_POSITIVE_Z, source: faces.positiveZ },
      { target: gl.TEXTURE_CUBE_MAP_NEGATIVE_Z, source: faces.negativeZ }
    ];

    for (const face of faceTargets) {
      let image: HTMLImageElement | ImageData;

      if (typeof face.source === 'string') {
        image = await this.loadImage(face.source);
      } else {
        image = face.source as HTMLImageElement | ImageData;
      }

      gl.texImage2D(
        face.target,
        0,
        opts.internalFormat,
        opts.format,
        opts.type,
        image
      );
    }

    // Generate mipmaps if requested
    if (opts.generateMipmaps) {
      gl.generateMipmap(gl.TEXTURE_CUBE_MAP);
    }

    return texture;
  }

  private loadImage(src: string): Promise<HTMLImageElement> {
    return new Promise((_resolve, __reject) => {
      const image = new Image();
      image.crossOrigin = 'anonymous';

      image.onload = () => resolve(image);
      image.onerror = (_error) => reject(new Error(`Failed to load image: ${src}`));

      image.src = src;
    });
  }

  private isPowerOfTwo(value: number): boolean {
    return (value & (value - 1)) === 0 && value > 0;
  }

  createDataTexture(
    data: ArrayBufferView,
    width: number,
    height: number,
    options: TextureOptions = {}
  ): WebGLTexture {
    const gl = this.gl;
    const texture = gl.createTexture();

    if (!texture) {
      throw new Error('Failed to create data texture');
    }

    const opts = {
      format: gl.RGBA,
      internalFormat: gl.RGBA8,
      type: gl.UNSIGNED_BYTE,
      wrapS: gl.CLAMP_TO_EDGE,
      wrapT: gl.CLAMP_TO_EDGE,
      minFilter: gl.LINEAR,
      magFilter: gl.LINEAR,
      generateMipmaps: false,
      ...options
    };

    gl.bindTexture(gl.TEXTURE_2D, texture);

    gl.texImage2D(
      gl.TEXTURE_2D,
      0,
      opts.internalFormat,
      width,
      height,
      0,
      opts.format,
      opts.type,
      data
    );

    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_S, opts.wrapS);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_T, opts.wrapT);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MIN_FILTER, opts.minFilter);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MAG_FILTER, opts.magFilter);

    if (opts.generateMipmaps) {
      gl.generateMipmap(gl.TEXTURE_2D);
    }

    return texture;
  }

  createRenderTexture(
    width: number,
    height: number,
    options: TextureOptions = {}
  ): WebGLTexture {
    return this.createDataTexture(null as any, width, height, {
      wrapS: this.gl.CLAMP_TO_EDGE,
      wrapT: this.gl.CLAMP_TO_EDGE,
      minFilter: this.gl.LINEAR,
      magFilter: this.gl.LINEAR,
      generateMipmaps: false,
      ...options
    });
  }

  getTexture(path: string): WebGLTexture | null {
    return this.textures.get(path) || null;
  }

  getDefaultTexture(type: string): WebGLTexture {
    return this.defaultTextures.get(type) || this.defaultTextures.get('white')!;
  }

  getTextureData(path: string): { width: number; height: number; format: number } | null {
    return this.textureData.get(path) || null;
  }

  hasTexture(path: string): boolean {
    return this.textures.has(path);
  }

  removeTexture(path: string): void {
    const texture = this.textures.get(path);
    if (texture) {
      this.gl.deleteTexture(texture);
      this.textures.delete(path);
      this.textureData.delete(path);
    }
  }

  clear(): void {
    for (const texture of this.textures.values()) {
      this.gl.deleteTexture(texture);
    }
    this.textures.clear();
    this.textureData.clear();
    this.loadingPromises.clear();
  }

  dispose(): void {
    this.clear();

    // Dispose default textures
    for (const texture of this.defaultTextures.values()) {
      this.gl.deleteTexture(texture);
    }
    this.defaultTextures.clear();
  }

  // Utility methods
  getMemoryUsage(): number {
    let totalBytes = 0;

    for (const data of this.textureData.values()) {
      const bytesPerPixel = this.getBytesPerPixel(data.format);
      totalBytes += data.width * data.height * bytesPerPixel;
    }

    return totalBytes;
  }

  private getBytesPerPixel(format: number): number {
    const gl = this.gl;

    switch (format) {
      case gl.RGBA:
        return 4;
      case gl.RGB:
        return 3;
      case gl.RG:
        return 2;
      case gl.RED:
        return 1;
      default:
        return 4;
    }
  }

  getLoadedTextureCount(): number {
    return this.textures.size;
  }

  getLoadingTextureCount(): number {
    return this.loadingPromises.size;
  }
}

import { mat4, vec3, _vec4, quat } from 'gl-matrix';
import { _Shader, ShaderProgram } from './Shader';
import { Material } from './Material';
import { _Mesh } from './Mesh';
import { Camera } from './Camera';
import { Light } from './Light';
import { RenderTarget } from './RenderTarget';
import { TextureManager } from './TextureManager';
import { GeometryManager } from './GeometryManager';

export interface RenderStats {
  frameTime: number;
  drawCalls: number;
  triangles: number;
  vertices: number;
  textureBinds: number;
  stateChanges: number;
}

export interface RenderSettings {
  shadows: boolean;
  shadowMapSize: number;
  antialiasing: boolean;
  ambientOcclusion: boolean;
  bloom: boolean;
  toneMapping: boolean;
  hdr: boolean;
  anisotropicFiltering: number;
  maxLights: number;
  cullingEnabled: boolean;
  lodEnabled: boolean;
}

export class WebGLEngine {
  private gl: WebGL2RenderingContext;
  private canvas: HTMLCanvasElement;
  private settings: RenderSettings;
  private stats: RenderStats;

  // Core systems
  private textureManager: TextureManager;
  private geometryManager: GeometryManager;
  private shaderCache = new Map<string, ShaderProgram>();

  // Render targets
  private shadowMapTarget: RenderTarget | null = null;
  private hdrTarget: RenderTarget | null = null;
  private postProcessTargets: RenderTarget[] = [];

  // Current state
  private currentShader: ShaderProgram | null = null;
  private currentMaterial: Material | null = null;
  private viewMatrix = mat4.create();
  private projectionMatrix = mat4.create();
  private modelMatrix = mat4.create();
  private mvpMatrix = mat4.create();

  // Lighting
  private lights: Light[] = [];
  private lightUniforms = {
    positions: new Float32Array(16 * 3), // Max 16 lights
    colors: new Float32Array(16 * 3),
    directions: new Float32Array(16 * 3),
    properties: new Float32Array(16 * 4), // intensity, range, spotAngle, type
  };

  // Extensions
  private extensions: { [key: string]: any } = {};

  constructor(canvas: HTMLCanvasElement, settings: Partial<RenderSettings> = {}) {
    this.canvas = canvas;
    this.settings = {
      shadows: true,
      shadowMapSize: 2048,
      antialiasing: true,
      ambientOcclusion: true,
      bloom: true,
      toneMapping: true,
      hdr: true,
      anisotropicFiltering: 16,
      maxLights: 16,
      cullingEnabled: true,
      lodEnabled: true,
      ...settings
    };

    this.stats = {
      frameTime: 0,
      drawCalls: 0,
      triangles: 0,
      vertices: 0,
      textureBinds: 0,
      stateChanges: 0
    };

    // Initialize WebGL context
    const gl = canvas.getContext('webgl2', {
      alpha: false,
      depth: true,
      stencil: true,
      antialias: this.settings.antialiasing,
      premultipliedAlpha: false,
      preserveDrawingBuffer: false,
      powerPreference: 'high-performance'
    });

    if (!gl) {
      throw new Error('WebGL2 not supported');
    }

    this.gl = gl;
    this.textureManager = new TextureManager(gl);
    this.geometryManager = new GeometryManager(gl);

    this.initializeExtensions();
    this.setupDefaultState();
    this.createRenderTargets();
  }

  private initializeExtensions(): void {
    const gl = this.gl;

    // Required extensions
    const extensions = [
      'EXT_color_buffer_float',
      'EXT_texture_filter_anisotropic',
      'OES_texture_float_linear',
      'WEBGL_depth_texture',
      'WEBGL_draw_buffers'
    ];

    for (const ext of extensions) {
      this.extensions[ext] = gl.getExtension(ext);
      if (!this.extensions[ext] && ext.includes('EXT_color_buffer_float')) {
        console.warn(`Extension ${ext} not supported - HDR disabled`);
        this.settings.hdr = false;
      }
    }
  }

  private setupDefaultState(): void {
    const gl = this.gl;

    // Enable depth testing
    gl.enable(gl.DEPTH_TEST);
    gl.depthFunc(gl.LEQUAL);

    // Enable culling
    if (this.settings.cullingEnabled) {
      gl.enable(gl.CULL_FACE);
      gl.cullFace(gl.BACK);
      gl.frontFace(gl.CCW);
    }

    // Enable blending for transparency
    gl.enable(gl.BLEND);
    gl.blendFunc(gl.SRC_ALPHA, gl.ONE_MINUS_SRC_ALPHA);

    // Set clear color
    gl.clearColor(0.1, 0.1, 0.1, 1.0);

    // Enable seamless cubemap filtering
    if (this.extensions['EXT_texture_filter_anisotropic']) {
      gl.texParameterf(gl.TEXTURE_2D, gl.TEXTURE_MAX_ANISOTROPY_EXT, this.settings.anisotropicFiltering);
    }
  }

  private createRenderTargets(): void {
    const gl = this.gl;
    const width = this.canvas.width;
    const height = this.canvas.height;

    // Shadow map target
    if (this.settings.shadows) {
      this.shadowMapTarget = new RenderTarget(gl, this.settings.shadowMapSize, this.settings.shadowMapSize, {
        colorFormat: gl.RGBA,
        depthFormat: gl.DEPTH_COMPONENT24,
        filter: gl.LINEAR,
        wrap: gl.CLAMP_TO_EDGE
      });
    }

    // HDR target
    if (this.settings.hdr) {
      this.hdrTarget = new RenderTarget(gl, width, height, {
        colorFormat: gl.RGBA16F,
        depthFormat: gl.DEPTH_COMPONENT24,
        filter: gl.LINEAR,
        wrap: gl.CLAMP_TO_EDGE
      });
    }

    // Post-process targets for effects
    const targetCount = 2; // Ping-pong buffers
    for (let i = 0; i < targetCount; i++) {
      this.postProcessTargets.push(new RenderTarget(gl, width, height, {
        colorFormat: this.settings.hdr ? gl.RGBA16F : gl.RGBA,
        filter: gl.LINEAR,
        wrap: gl.CLAMP_TO_EDGE
      }));
    }
  }

  // Main render function
  render(scene: any, camera: Camera): void {
    const startTime = performance.now();
    this.resetStats();

    this.updateMatrices(camera);
    this.updateLighting(scene.lights || []);

    // Shadow pass
    if (this.settings.shadows && this.shadowMapTarget) {
      this.renderShadowMap(scene, camera);
    }

    // Main pass
    const target = this.settings.hdr ? this.hdrTarget : null;
    this.renderScene(scene, camera, target);

    // Post-processing
    if (this.settings.hdr || this.settings.bloom || this.settings.toneMapping) {
      this.renderPostProcess();
    }

    this.stats.frameTime = performance.now() - startTime;
  }

  private updateMatrices(camera: Camera): void {
    camera.updateMatrices();
    mat4.copy(this.viewMatrix, camera.viewMatrix);
    mat4.copy(this.projectionMatrix, camera.projectionMatrix);
  }

  private updateLighting(lights: Light[]): void {
    this.lights = lights.slice(0, this.settings.maxLights);

    for (let i = 0; i < this.lights.length; i++) {
      const light = this.lights[i];
      const offset3 = i * 3;
      const offset4 = i * 4;

      // Position
      this.lightUniforms.positions[offset3] = light.position[0];
      this.lightUniforms.positions[offset3 + 1] = light.position[1];
      this.lightUniforms.positions[offset3 + 2] = light.position[2];

      // Color
      this.lightUniforms.colors[offset3] = light.color[0];
      this.lightUniforms.colors[offset3 + 1] = light.color[1];
      this.lightUniforms.colors[offset3 + 2] = light.color[2];

      // Direction (for directional/spot lights)
      this.lightUniforms.directions[offset3] = light.direction[0];
      this.lightUniforms.directions[offset3 + 1] = light.direction[1];
      this.lightUniforms.directions[offset3 + 2] = light.direction[2];

      // Properties: intensity, range, spotAngle, type
      this.lightUniforms.properties[offset4] = light.intensity;
      this.lightUniforms.properties[offset4 + 1] = light.range;
      this.lightUniforms.properties[offset4 + 2] = light.spotAngle || 0;
      this.lightUniforms.properties[offset4 + 3] = light.type; // 0=directional, 1=point, 2=spot
    }
  }

  private renderShadowMap(scene: any, camera: Camera): void {
    if (!this.shadowMapTarget) return;

    const gl = this.gl;
    this.shadowMapTarget.bind();

    gl.viewport(0, 0, this.settings.shadowMapSize, this.settings.shadowMapSize);
    gl.clear(gl.DEPTH_BUFFER_BIT);

    // Use shadow shader
    const shadowShader = this.getShader('shadow');
    this.bindShader(shadowShader);

    // Render scene from light's perspective
    const directionalLights = this.lights.filter(light => light.type === 0);
    if (directionalLights.length > 0) {
      const light = directionalLights[0];
      this.renderSceneWithShader(scene, light.shadowCamera || camera, shadowShader);
    }

    this.shadowMapTarget.unbind();
  }

  private renderScene(scene: any, camera: Camera, target: RenderTarget | null): void {
    const gl = this.gl;

    if (target) {
      target.bind();
    }

    gl.viewport(0, 0, this.canvas.width, this.canvas.height);
    gl.clear(gl.COLOR_BUFFER_BIT | gl.DEPTH_BUFFER_BIT);

    // Render opaque objects first
    this.renderObjects(scene.opaqueObjects || [], camera, false);

    // Render transparent objects (back to front)
    this.renderObjects(scene.transparentObjects || [], camera, true);

    if (target) {
      target.unbind();
    }
  }

  private renderObjects(objects: any[], camera: Camera, transparent: boolean): void {
    const gl = this.gl;

    // Sort objects if transparent
    if (transparent) {
      objects.sort((_a, _b) => {
        const distA = vec3.squaredDistance(camera.position, a.position);
        const distB = vec3.squaredDistance(camera.position, b.position);
        return distB - distA; // Back to front
      });

      gl.depthMask(false); // Disable depth writes for transparent objects
    }

    for (const obj of objects) {
      this.renderObject(obj, camera);
    }

    if (transparent) {
      gl.depthMask(true); // Re-enable depth writes
    }
  }

  private renderObject(object: any, camera: Camera): void {
    const gl = this.gl;

    // Frustum culling
    if (this.settings.cullingEnabled && !this.isInFrustum(object, camera)) {
      return;
    }

    // LOD selection
    const mesh = this.settings.lodEnabled ? this.selectLOD(object, camera) : object.mesh;
    if (!mesh) return;

    // Update model matrix
    this.updateModelMatrix(object);

    // Bind material and shader
    this.bindMaterial(object.material);

    // Bind geometry
    this.geometryManager.bindMesh(mesh);

    // Draw
    gl.drawElements(gl.TRIANGLES, mesh.indexCount, gl.UNSIGNED_SHORT, 0);

    this.stats.drawCalls++;
    this.stats.triangles += mesh.indexCount / 3;
    this.stats.vertices += mesh.vertexCount;
  }

  private renderPostProcess(): void {
    if (!this.hdrTarget) return;

    const _gl = this.gl;
    let currentSource = this.hdrTarget;
    let currentTarget = this.postProcessTargets[0];

    // Bloom pass
    if (this.settings.bloom) {
      // Extract bright pixels
      this.renderFullscreenQuad(currentSource, currentTarget, 'bloom_extract');
      [currentSource, currentTarget] = [currentTarget, currentSource];

      // Blur passes
      for (let i = 0; i < 4; i++) {
        this.renderFullscreenQuad(currentSource, currentTarget, 'blur_horizontal');
        [currentSource, currentTarget] = [currentTarget, currentSource];
        this.renderFullscreenQuad(currentSource, currentTarget, 'blur_vertical');
        [currentSource, currentTarget] = [currentTarget, currentSource];
      }

      // Combine with original
      this.renderFullscreenQuad(currentSource, null, 'bloom_combine', this.hdrTarget);
    }

    // Tone mapping
    if (this.settings.toneMapping) {
      this.renderFullscreenQuad(currentSource, null, 'tonemap');
    }
  }

  private renderFullscreenQuad(source: RenderTarget, target: RenderTarget | null, shaderName: string, additionalTexture?: RenderTarget): void {
    const gl = this.gl;
    const shader = this.getShader(shaderName);

    if (target) {
      target.bind();
    }

    this.bindShader(shader);

    // Bind source texture
    gl.activeTexture(gl.TEXTURE0);
    gl.bindTexture(gl.TEXTURE_2D, source.colorTexture);
    gl.uniform1i(shader.getUniformLocation('u_texture'), 0);

    if (additionalTexture) {
      gl.activeTexture(gl.TEXTURE1);
      gl.bindTexture(gl.TEXTURE_2D, additionalTexture.colorTexture);
      gl.uniform1i(shader.getUniformLocation('u_additionalTexture'), 1);
    }

    // Render fullscreen quad
    this.geometryManager.renderFullscreenQuad();

    if (target) {
      target.unbind();
    }
  }

  private updateModelMatrix(object: any): void {
    mat4.fromRotationTranslationScale(
      this.modelMatrix,
      object.rotation || quat.create(),
      object.position || vec3.create(),
      object.scale || vec3.fromValues(1, 1, 1)
    );

    // Update MVP matrix
    mat4.multiply(this.mvpMatrix, this.viewMatrix, this.modelMatrix);
    mat4.multiply(this.mvpMatrix, this.projectionMatrix, this.mvpMatrix);
  }

  private bindMaterial(material: Material): void {
    if (this.currentMaterial === material) return;

    this.currentMaterial = material;
    const shader = this.getShader(material.shaderName);
    this.bindShader(shader);

    // Bind textures
    material.bindTextures(this.gl, this.textureManager);

    // Set uniforms
    material.setUniforms(shader);

    this.stats.stateChanges++;
  }

  private bindShader(shader: ShaderProgram): void {
    if (this.currentShader === shader) return;

    this.currentShader = shader;
    shader.use();

    // Set common uniforms
    shader.setUniformMatrix4fv('u_mvpMatrix', this.mvpMatrix);
    shader.setUniformMatrix4fv('u_modelMatrix', this.modelMatrix);
    shader.setUniformMatrix4fv('u_viewMatrix', this.viewMatrix);
    shader.setUniformMatrix4fv('u_projectionMatrix', this.projectionMatrix);

    // Set lighting uniforms
    shader.setUniform3fv('u_lightPositions', this.lightUniforms.positions);
    shader.setUniform3fv('u_lightColors', this.lightUniforms.colors);
    shader.setUniform3fv('u_lightDirections', this.lightUniforms.directions);
    shader.setUniform4fv('u_lightProperties', this.lightUniforms.properties);
    shader.setUniform1i('u_numLights', this.lights.length);

    this.stats.stateChanges++;
  }

  private getShader(name: string): ShaderProgram {
    if (!this.shaderCache.has(name)) {
      const shader = this.createShader(name);
      this.shaderCache.set(name, shader);
    }
    return this.shaderCache.get(name)!;
  }

  private createShader(_name: string): ShaderProgram {
    // Shader creation logic would go here
    // For now, return a placeholder
    return new ShaderProgram(this.gl, '', '');
  }

  private renderSceneWithShader(_scene: any, _camera: Camera, _shader: ShaderProgram): void {
    // Implementation for rendering with specific shader
  }

  private isInFrustum(_object: any, _camera: Camera): boolean {
    // Frustum culling implementation
    return true; // Simplified for now
  }

  private selectLOD(object: any, camera: Camera): any {
    // LOD selection based on distance
    const distance = vec3.distance(camera.position, object.position);

    if (distance < 50) return object.meshLOD0;
    if (distance < 100) return object.meshLOD1;
    if (distance < 200) return object.meshLOD2;
    return object.meshLOD3;
  }

  private resetStats(): void {
    this.stats.drawCalls = 0;
    this.stats.triangles = 0;
    this.stats.vertices = 0;
    this.stats.textureBinds = 0;
    this.stats.stateChanges = 0;
  }

  // Public API
  resize(width: number, height: number): void {
    this.canvas.width = width;
    this.canvas.height = height;
    this.gl.viewport(0, 0, width, height);

    // Recreate render targets
    if (this.hdrTarget) {
      this.hdrTarget.resize(width, height);
    }

    for (const target of this.postProcessTargets) {
      target.resize(width, height);
    }
  }

  getStats(): RenderStats {
    return { ...this.stats };
  }

  getSettings(): RenderSettings {
    return { ...this.settings };
  }

  updateSettings(settings: Partial<RenderSettings>): void {
    Object.assign(this.settings, settings);

    // Recreate render targets if needed
    if (settings.shadowMapSize && this.shadowMapTarget) {
      this.shadowMapTarget.resize(settings.shadowMapSize, settings.shadowMapSize);
    }
  }

  dispose(): void {
    // Cleanup resources
    this.shadowMapTarget?.dispose();
    this.hdrTarget?.dispose();
    this.postProcessTargets.forEach(target => target.dispose());

    for (const shader of this.shaderCache.values()) {
      shader.dispose();
    }

    this.textureManager.dispose();
    this.geometryManager.dispose();
  }
}

import { Light, LightType } from '../engine/Light';
import { ShadowManager, ShadowData, ShadowMapConfig } from './ShadowManager';
import { Camera } from '../engine/Camera';
import { ShaderProgram } from '../engine/Shader';
import { mat4, _vec3, _vec4 } from 'gl-matrix';

export interface LightingConfig {
  maxDirectionalLights: number;
  maxPointLights: number;
  maxSpotLights: number;
  maxAreaLights: number;
  enableShadows: boolean;
  enableIBL: boolean;
  ambientIntensity: number;
  ambientColor: [number, number, number];
  exposure: number;
  gamma: number;
}

export interface LightUniformData {
  // Light properties (vec4 for alignment)
  positions: Float32Array;       // xyz = position, w = type
  directions: Float32Array;      // xyz = direction, w = range
  colors: Float32Array;          // xyz = color, w = intensity
  parameters: Float32Array;      // x = innerCone, y = outerCone, z = falloff, w = shadowIndex
  shadowMatrices: Float32Array;  // 4x4 matrices for shadow mapping
  cascadeSplits: Float32Array;   // Cascade split distances for CSM
}

export interface IBLData {
  diffuseMap: WebGLTexture;      // Irradiance cube map
  specularMap: WebGLTexture;     // Pre-filtered environment map
  brdfLUT: WebGLTexture;         // BRDF lookup table
  intensity: number;
  rotation: mat4;
}

export class LightingManager {
  private gl: WebGL2RenderingContext;
  private shadowManager: ShadowManager;
  private lights = new Map<string, Light>();
  private lightArray: Light[] = [];
  private config: LightingConfig;

  // Light categorization
  private directionalLights: Light[] = [];
  private pointLights: Light[] = [];
  private spotLights: Light[] = [];
  private areaLights: Light[] = [];

  // Uniform data
  private lightUniforms: LightUniformData;
  private uniformBuffer: WebGLBuffer | null = null;
  private uniformBindingPoint = 1;

  // IBL
  private iblData: IBLData | null = null;

  // Statistics
  private stats = {
    totalLights: 0,
    shadowCastingLights: 0,
    culledLights: 0,
    renderTime: 0,
    shadowRenderTime: 0
  };

  constructor(gl: WebGL2RenderingContext, config?: Partial<LightingConfig>) {
    this.gl = gl;
    this.shadowManager = new ShadowManager(gl);

    this.config = {
      maxDirectionalLights: 4,
      maxPointLights: 32,
      maxSpotLights: 16,
      maxAreaLights: 8,
      enableShadows: true,
      enableIBL: true,
      ambientIntensity: 0.1,
      ambientColor: [0.2, 0.2, 0.3],
      exposure: 1.0,
      gamma: 2.2,
      ...config
    };

    this.initializeUniforms();
    this.createBRDFLUT();
  }

  private initializeUniforms(): void {
    const maxLights = this.config.maxDirectionalLights + 
                      this.config.maxPointLights + 
                      this.config.maxSpotLights + 
                      this.config.maxAreaLights;

    this.lightUniforms = {
      positions: new Float32Array(maxLights * 4),
      directions: new Float32Array(maxLights * 4),
      colors: new Float32Array(maxLights * 4),
      parameters: new Float32Array(maxLights * 4),
      shadowMatrices: new Float32Array(maxLights * 16), // 4x4 matrices
      cascadeSplits: new Float32Array(16) // Max 4 cascades per light
    };

    // Create uniform buffer
    const gl = this.gl;
    this.uniformBuffer = gl.createBuffer();

    if (this.uniformBuffer) {
      gl.bindBuffer(gl.UNIFORM_BUFFER, this.uniformBuffer);

      // Calculate buffer size
      const bufferSize = (maxLights * 4 * 4 * 4) + // positions, directions, colors, parameters
                        (maxLights * 16 * 4) +      // shadow matrices
                        (16 * 4) +                  // cascade splits
                        (16 * 4);                   // padding/other uniforms

      gl.bufferData(gl.UNIFORM_BUFFER, bufferSize, gl.DYNAMIC_DRAW);
      gl.bindBufferBase(gl.UNIFORM_BUFFER, this.uniformBindingPoint, this.uniformBuffer);
    }
  }

  addLight(light: Light): void {
    this.lights.set(light.id, light);
    this.categorizeLights();
    this.updateUniforms();

    // Create shadow map if light casts shadows
    if (this.config.enableShadows && light.castsShadows) {
      const shadowConfig: Partial<ShadowMapConfig> = {
        resolution: light.type === LightType.DIRECTIONAL ? 4096 : 2048,
        cascadeCount: light.type === LightType.DIRECTIONAL ? 4 : 1,
        softShadows: true
      };

      this.shadowManager.createShadowMap(light, shadowConfig);
      this.stats.shadowCastingLights++;
    }

    this.stats.totalLights++;
  }

  removeLight(lightId: string): void {
    const light = this.lights.get(lightId);
    if (light) {
      this.lights.delete(lightId);
      this.shadowManager.removeShadowMap(lightId);
      this.categorizeLights();
      this.updateUniforms();

      if (light.castsShadows) {
        this.stats.shadowCastingLights--;
      }
      this.stats.totalLights--;
    }
  }

  getLight(lightId: string): Light | null {
    return this.lights.get(lightId) || null;
  }

  updateLight(lightId: string, updates: Partial<Light>): void {
    const light = this.lights.get(lightId);
    if (light) {
      Object.assign(light, updates);
      this.updateUniforms();

      // Update shadow map if needed
      if (this.config.enableShadows && light.castsShadows) {
        const shadowData = this.shadowManager.getShadowData(lightId);
        if (shadowData) {
          // Shadow data will be updated during render
        }
      }
    }
  }

  private categorizeLights(): void {
    this.directionalLights = [];
    this.pointLights = [];
    this.spotLights = [];
    this.areaLights = [];
    this.lightArray = [];

    for (const light of this.lights.values()) {
      this.lightArray.push(light);

      switch (light.type) {
        case LightType.DIRECTIONAL:
          if (this.directionalLights.length < this.config.maxDirectionalLights) {
            this.directionalLights.push(light);
          }
          break;
        case LightType.POINT:
          if (this.pointLights.length < this.config.maxPointLights) {
            this.pointLights.push(light);
          }
          break;
        case LightType.SPOT:
          if (this.spotLights.length < this.config.maxSpotLights) {
            this.spotLights.push(light);
          }
          break;
        case LightType.AREA:
          if (this.areaLights.length < this.config.maxAreaLights) {
            this.areaLights.push(light);
          }
          break;
      }
    }
  }

  private updateUniforms(): void {
    let index = 0;

    // Pack light data
    for (const light of this.lightArray) {
      if (index >= this.lightUniforms.positions.length / 4) break;

      const baseIndex = index * 4;

      // Position (xyz) + type (w)
      if (light.position) {
        this.lightUniforms.positions[baseIndex] = light.position[0];
        this.lightUniforms.positions[baseIndex + 1] = light.position[1];
        this.lightUniforms.positions[baseIndex + 2] = light.position[2];
      }
      this.lightUniforms.positions[baseIndex + 3] = light.type;

      // Direction (xyz) + range (w)
      if (light.direction) {
        this.lightUniforms.directions[baseIndex] = light.direction[0];
        this.lightUniforms.directions[baseIndex + 1] = light.direction[1];
        this.lightUniforms.directions[baseIndex + 2] = light.direction[2];
      }
      this.lightUniforms.directions[baseIndex + 3] = light.range || 0;

      // Color (xyz) + intensity (w)
      this.lightUniforms.colors[baseIndex] = light.color[0];
      this.lightUniforms.colors[baseIndex + 1] = light.color[1];
      this.lightUniforms.colors[baseIndex + 2] = light.color[2];
      this.lightUniforms.colors[baseIndex + 3] = light.intensity;

      // Parameters: innerCone, outerCone, falloff, shadowIndex
      this.lightUniforms.parameters[baseIndex] = light.innerConeAngle || 0;
      this.lightUniforms.parameters[baseIndex + 1] = light.outerConeAngle || 0;
      this.lightUniforms.parameters[baseIndex + 2] = light.falloffExponent || 1;
      this.lightUniforms.parameters[baseIndex + 3] = light.castsShadows ? index : -1;

      // Shadow matrix (if available)
      const shadowData = this.shadowManager.getShadowData(light.id);
      if (shadowData) {
        const matrixIndex = index * 16;
        for (let i = 0; i < 16; i++) {
          this.lightUniforms.shadowMatrices[matrixIndex + i] = shadowData.shadowMatrix[i];
        }
      }

      index++;
    }

    // Update GPU buffer
    this.uploadUniformData();
  }

  private uploadUniformData(): void {
    if (!this.uniformBuffer) return;

    const gl = this.gl;
    gl.bindBuffer(gl.UNIFORM_BUFFER, this.uniformBuffer);

    let offset = 0;

    // Upload positions
    gl.bufferSubData(gl.UNIFORM_BUFFER, offset, this.lightUniforms.positions);
    offset += this.lightUniforms.positions.byteLength;

    // Upload directions
    gl.bufferSubData(gl.UNIFORM_BUFFER, offset, this.lightUniforms.directions);
    offset += this.lightUniforms.directions.byteLength;

    // Upload colors
    gl.bufferSubData(gl.UNIFORM_BUFFER, offset, this.lightUniforms.colors);
    offset += this.lightUniforms.colors.byteLength;

    // Upload parameters
    gl.bufferSubData(gl.UNIFORM_BUFFER, offset, this.lightUniforms.parameters);
    offset += this.lightUniforms.parameters.byteLength;

    // Upload shadow matrices
    gl.bufferSubData(gl.UNIFORM_BUFFER, offset, this.lightUniforms.shadowMatrices);
    offset += this.lightUniforms.shadowMatrices.byteLength;

    // Upload cascade splits
    gl.bufferSubData(gl.UNIFORM_BUFFER, offset, this.lightUniforms.cascadeSplits);
  }

  cullLights(camera: Camera): Light[] {
    const frustum = camera.getFrustum();
    const culledLights: Light[] = [];
    this.stats.culledLights = 0;

    for (const light of this.lightArray) {
      if (this.isLightVisible(light, frustum)) {
        culledLights.push(light);
      } else {
        this.stats.culledLights++;
      }
    }

    return culledLights;
  }

  private isLightVisible(light: Light, _frustum: any): boolean {
    // Simplified frustum culling - in a real implementation you'd use proper frustum planes
    switch (light.type) {
      case LightType.DIRECTIONAL:
        return true; // Directional lights always visible

      case LightType.POINT:
        if (light.position && light.range) {
          // Check if point light sphere intersects frustum
          // Simplified check - implement proper sphere-frustum intersection
          return true;
        }
        break;

      case LightType.SPOT:
        if (light.position && light.direction && light.range) {
          // Check if spot light cone intersects frustum
          // Simplified check - implement proper cone-frustum intersection
          return true;
        }
        break;

      case LightType.AREA:
        if (light.position && light.width && light.height) {
          // Check if area light bounds intersect frustum
          return true;
        }
        break;
    }

    return false;
  }

  renderShadowMaps(camera: Camera, _renderScene: (shadowData: ShadowData) => void): void {
    const startTime = performance.now();

    for (const light of this.lightArray) {
      if (!light.castsShadows) continue;

      const shadowData = this.shadowManager.getShadowData(light.id);
      if (!shadowData) continue;

      // Update cascade matrices for directional lights
      if (light.type === LightType.DIRECTIONAL && shadowData.cascades) {
        this.shadowManager.updateCascadedShadowMap(shadowData, camera);

        // Render each cascade
        for (const cascade of shadowData.cascades) {
          cascade.renderTarget.bind();

          // Set up shadow rendering state
          const gl = this.gl;
          gl.enable(gl.DEPTH_TEST);
          gl.depthFunc(gl.LESS);
          gl.colorMask(false, false, false, false);
          gl.clear(gl.DEPTH_BUFFER_BIT);

          // Create temporary shadow data for this cascade
          const cascadeShadowData: ShadowData = {
            ...shadowData,
            shadowMap: cascade.renderTarget,
            lightViewMatrix: cascade.viewMatrix,
            lightProjectionMatrix: cascade.projectionMatrix,
            shadowMatrix: cascade.shadowMatrix
          };

          renderScene(cascadeShadowData);
          cascade.renderTarget.unbind();
        }
      } else {
        // Render single shadow map
        shadowData.shadowMap.bind();

        const gl = this.gl;
        gl.enable(gl.DEPTH_TEST);
        gl.depthFunc(gl.LESS);
        gl.colorMask(false, false, false, false);
        gl.clear(gl.DEPTH_BUFFER_BIT);

        renderScene(shadowData);
        shadowData.shadowMap.unbind();
      }
    }

    // Restore rendering state
    const gl = this.gl;
    gl.colorMask(true, true, true, true);

    this.stats.shadowRenderTime = performance.now() - startTime;
  }

  bindLightingUniforms(shader: ShaderProgram): void {
    const gl = this.gl;

    // Bind uniform buffer
    if (this.uniformBuffer) {
      gl.bindBufferBase(gl.UNIFORM_BUFFER, this.uniformBindingPoint, this.uniformBuffer);
    }

    // Set light counts
    shader.setUniform('u_numDirectionalLights', this.directionalLights.length);
    shader.setUniform('u_numPointLights', this.pointLights.length);
    shader.setUniform('u_numSpotLights', this.spotLights.length);
    shader.setUniform('u_numAreaLights', this.areaLights.length);

    // Set ambient lighting
    shader.setUniform('u_ambientColor', this.config.ambientColor);
    shader.setUniform('u_ambientIntensity', this.config.ambientIntensity);

    // Set camera exposure
    shader.setUniform('u_exposure', this.config.exposure);
    shader.setUniform('u_gamma', this.config.gamma);

    // Bind shadow maps
    if (this.config.enableShadows) {
      this.shadowManager.bindShadowMapForReading(10); // Use texture unit 10
      shader.setUniform('u_shadowMaps', 10);
    }

    // Bind IBL textures
    if (this.config.enableIBL && this.iblData) {
      gl.activeTexture(gl.TEXTURE0 + 11);
      gl.bindTexture(gl.TEXTURE_CUBE_MAP, this.iblData.diffuseMap);
      shader.setUniform('u_irradianceMap', 11);

      gl.activeTexture(gl.TEXTURE0 + 12);
      gl.bindTexture(gl.TEXTURE_CUBE_MAP, this.iblData.specularMap);
      shader.setUniform('u_prefilterMap', 12);

      gl.activeTexture(gl.TEXTURE0 + 13);
      gl.bindTexture(gl.TEXTURE_2D, this.iblData.brdfLUT);
      shader.setUniform('u_brdfLUT', 13);

      shader.setUniform('u_iblIntensity', this.iblData.intensity);
      shader.setUniform('u_iblRotation', this.iblData.rotation);
    }
  }

  // IBL setup
  async loadHDREnvironment(hdriPath: string, intensity: number = 1.0): Promise<void> {
    // Load HDRI texture
    const hdriTexture = await this.loadHDRITexture(hdriPath);

    // Generate IBL maps
    const diffuseMap = this.generateIrradianceMap(hdriTexture);
    const specularMap = this.generatePrefilterMap(hdriTexture);

    this.iblData = {
      diffuseMap,
      specularMap,
      brdfLUT: await this.getBRDFLUT(),
      intensity,
      rotation: mat4.create()
    };

    // Clean up HDRI texture
    this.gl.deleteTexture(hdriTexture);
  }

  private async loadHDRITexture(_hdriPath: string): Promise<WebGLTexture> {
