import { describe, it, expect, beforeEach, vi } from 'vitest';
import { CombatEngine, Combatant, CombatAction, _Attack, _DamageRoll } from '../src/combat/CombatEngine';

describe('CombatEngine', () => {
  let combatEngine: CombatEngine;
  let testCombatant: Combatant;

  beforeEach(() => {
    combatEngine = new CombatEngine();

    testCombatant = {
      id: 'test-combatant',
      name: 'Test Fighter',
      type: 'player',
      stats: {
        hitPoints: { current: 50, max: 50, temporary: 0 },
        armorClass: 16,
        speed: 30,
        proficiencyBonus: 2,
        abilities: {
          strength: 16,
          dexterity: 14,
          constitution: 14,
          intelligence: 10,
          wisdom: 12,
          charisma: 8,
        },
        savingThrows: {
          strength: 5,
          constitution: 4,
        },
        skills: {
          athletics: 5,
          perception: 3,
        },
        resistances: [],
        immunities: [],
        vulnerabilities: [],
        conditions: [],
      },
      attacks: [
        {
          id: 'longsword',
          name: 'Longsword',
          type: 'melee',
          attackBonus: 5,
          damage: [{ dice: '1d8', bonus: 3, type: 'slashing' }],
          range: 5,
          properties: ['versatile'],
        },
      ],
      spells: [],
      position: { x: 0, y: 0 },
      initiative: 0,
      isActive: true,
      actionsUsed: 0,
      bonusActionUsed: false,
      reactionUsed: false,
      movementUsed: 0,
    };
  });

  describe('combatant management', () => {
    it('should add combatant', () => {
      combatEngine.addCombatant(testCombatant);
      const combatants = combatEngine.getCombatants();

      expect(combatants).toHaveLength(1);
      expect(combatants[0]).toEqual(testCombatant);
    });

    it('should remove combatant', () => {
      combatEngine.addCombatant(testCombatant);
      combatEngine.removeCombatant('test-combatant');

      const combatants = combatEngine.getCombatants();
      expect(combatants).toHaveLength(0);
    });

    it('should emit events when adding combatant', () => {
      const eventSpy = vi.fn();
      combatEngine.on('combatantAdded', eventSpy);

      combatEngine.addCombatant(testCombatant);

      expect(eventSpy).toHaveBeenCalledWith(testCombatant);
    });
  });

  describe('initiative and turn order', () => {
    it('should roll initiative for all combatants', () => {
      const combatant2: Combatant = {
        ...testCombatant,
        id: 'test-combatant-2',
        name: 'Test Rogue',
        stats: {
          ...testCombatant.stats,
          abilities: { ...testCombatant.stats.abilities, dexterity: 18 },
        },
      };

      combatEngine.addCombatant(testCombatant);
      combatEngine.addCombatant(combatant2);

      const eventSpy = vi.fn();
      combatEngine.on('initiativeRolled', eventSpy);

      combatEngine.rollInitiative();

      expect(eventSpy).toHaveBeenCalled();
      expect(testCombatant.initiative).toBeGreaterThan(0);
      expect(combatant2.initiative).toBeGreaterThan(0);

      const turnOrder = combatEngine.getTurnOrder();
      expect(turnOrder).toHaveLength(2);
    });

    it('should sort initiative correctly', () => {
      // Mock Math.random to control dice rolls
      const originalRandom = Math.random;
      let callCount = 0;
      Math.random = vi.fn(() => {
        // First roll: 0.5 (11 + 2 = 13 for testCombatant)
        // Second roll: 0.9 (19 + 4 = 23 for highDexCombatant)
        return callCount++ === 0 ? 0.5 : 0.9;
      });

      const highDexCombatant: Combatant = {
        ...testCombatant,
        id: 'high-dex',
        stats: {
          ...testCombatant.stats,
          abilities: { ...testCombatant.stats.abilities, dexterity: 18 },
        },
      };

      combatEngine.addCombatant(testCombatant);
      combatEngine.addCombatant(highDexCombatant);
      combatEngine.rollInitiative();

      const turnOrder = combatEngine.getTurnOrder();
      expect(turnOrder[0]).toBe('high-dex'); // Higher initiative goes first

      Math.random = originalRandom;
    });
  });

  describe('combat flow', () => {
    beforeEach(() => {
      combatEngine.addCombatant(testCombatant);
    });

    it('should start combat', () => {
      const startSpy = vi.fn();
      const turnSpy = vi.fn();

      combatEngine.on('combatStarted', startSpy);
      combatEngine.on('turnStarted', turnSpy);

      combatEngine.startCombat();

      expect(combatEngine.isInCombat()).toBe(true);
      expect(combatEngine.getCurrentRound()).toBe(1);
      expect(startSpy).toHaveBeenCalled();
      expect(turnSpy).toHaveBeenCalledWith(testCombatant);
    });

    it('should advance turns', () => {
      const combatant2: Combatant = { ...testCombatant, id: 'combatant-2' };
      combatEngine.addCombatant(combatant2);

      combatEngine.startCombat();
      const firstCombatant = combatEngine.getCurrentCombatant();

      const turnEndSpy = vi.fn();
      const turnStartSpy = vi.fn();

      combatEngine.on('turnEnded', turnEndSpy);
      combatEngine.on('turnStarted', turnStartSpy);

      combatEngine.nextTurn();

      expect(turnEndSpy).toHaveBeenCalledWith(firstCombatant);
      expect(turnStartSpy).toHaveBeenCalled();
      expect(combatEngine.getCurrentCombatant()).not.toBe(firstCombatant);
    });

    it('should advance rounds', () => {
      combatEngine.startCombat();

      const roundEndSpy = vi.fn();
      const roundStartSpy = vi.fn();

      combatEngine.on('roundEnded', roundEndSpy);
      combatEngine.on('roundStarted', roundStartSpy);

      combatEngine.nextTurn(); // Should advance to round 2

      expect(combatEngine.getCurrentRound()).toBe(2);
      expect(roundEndSpy).toHaveBeenCalledWith(1);
      expect(roundStartSpy).toHaveBeenCalledWith(2);
    });

    it('should end combat', () => {
      combatEngine.startCombat();

      const endSpy = vi.fn();
      combatEngine.on('combatEnded', endSpy);

      combatEngine.endCombat();

      expect(combatEngine.isInCombat()).toBe(false);
      expect(endSpy).toHaveBeenCalled();
    });
  });

  describe('combat actions', () => {
    let targetCombatant: Combatant;

    beforeEach(() => {
      targetCombatant = {
        ...testCombatant,
        id: 'target',
        name: 'Target',
        position: { x: 1, y: 0 }, // Within range
      };

      combatEngine.addCombatant(testCombatant);
      combatEngine.addCombatant(targetCombatant);
      combatEngine.startCombat();
    });

    it('should execute attack action', () => {
      const attackAction: CombatAction = {
        type: 'attack',
        actorId: 'test-combatant',
        targetId: 'target',
        attackId: 'longsword',
      };

      const result = combatEngine.executeAction(attackAction);

      expect(result.success).toBe(true);
      expect(testCombatant.actionsUsed).toBe(1);
    });

    it('should prevent actions when not actor\'s turn', () => {
      combatEngine.nextTurn(); // Move to next combatant's turn

      const attackAction: CombatAction = {
        type: 'attack',
        actorId: 'test-combatant',
        targetId: 'target',
        attackId: 'longsword',
      };

      const result = combatEngine.executeAction(attackAction);

      expect(result.success).toBe(false);
      expect(result.message).toBe('Not your turn');
    });

    it('should prevent actions when no actions remaining', () => {
      testCombatant.actionsUsed = 1;

      const attackAction: CombatAction = {
        type: 'attack',
        actorId: 'test-combatant',
        targetId: 'target',
        attackId: 'longsword',
      };

      const result = combatEngine.executeAction(attackAction);

      expect(result.success).toBe(false);
      expect(result.message).toBe('No actions remaining');
    });

    it('should check attack range', () => {
      targetCombatant.position = { x: 10, y: 10 }; // Out of range

      const attackAction: CombatAction = {
        type: 'attack',
        actorId: 'test-combatant',
        targetId: 'target',
        attackId: 'longsword',
      };

      const result = combatEngine.executeAction(attackAction);

      expect(result.success).toBe(false);
      expect(result.message).toBe('Target out of range');
    });

    it('should execute move action', () => {
      const moveAction: CombatAction = {
        type: 'move',
        actorId: 'test-combatant',
        targetPosition: { x: 1, y: 1 },
      };

      const result = combatEngine.executeAction(moveAction);

      expect(result.success).toBe(true);
      expect(testCombatant.position).toEqual({ x: 1, y: 1 });
      expect(testCombatant.movementUsed).toBeGreaterThan(0);
    });

    it('should prevent movement beyond speed', () => {
      testCombatant.movementUsed = testCombatant.stats.speed;

      const moveAction: CombatAction = {
        type: 'move',
        actorId: 'test-combatant',
        targetPosition: { x: 10, y: 10 },
      };

      const result = combatEngine.executeAction(moveAction);

      expect(result.success).toBe(false);
      expect(result.message).toBe('Not enough movement remaining');
    });

    it('should execute dash action', () => {
      const dashAction: CombatAction = {
        type: 'dash',
        actorId: 'test-combatant',
      };

      const result = combatEngine.executeAction(dashAction);

      expect(result.success).toBe(true);
      expect(testCombatant.actionsUsed).toBe(1);
      expect(result.effects).toContain('+30 movement');
    });
  });

  describe('damage and healing', () => {
    beforeEach(() => {
      combatEngine.addCombatant(testCombatant);
    });

    it('should apply damage', () => {
      const originalHP = testCombatant.stats.hitPoints.current;

      const damageSpy = vi.fn();
      combatEngine.on('damageApplied', damageSpy);

      combatEngine.applyDamage('test-combatant', 10);

      expect(testCombatant.stats.hitPoints.current).toBe(originalHP - 10);
      expect(damageSpy).toHaveBeenCalled();
    });

    it('should apply temporary hit points first', () => {
      testCombatant.stats.hitPoints.temporary = 5;
      const originalHP = testCombatant.stats.hitPoints.current;

      combatEngine.applyDamage('test-combatant', 10);

      expect(testCombatant.stats.hitPoints.temporary).toBe(0);
      expect(testCombatant.stats.hitPoints.current).toBe(originalHP - 5);
    });

    it('should not reduce HP below 0', () => {
      combatEngine.applyDamage('test-combatant', 100);

      expect(testCombatant.stats.hitPoints.current).toBe(0);
    });

    it('should handle unconsciousness at 0 HP', () => {
      const unconsciousSpy = vi.fn();
      combatEngine.on('combatantUnconscious', unconsciousSpy);

      combatEngine.applyDamage('test-combatant', 100);

      expect(testCombatant.stats.conditions).toContain('unconscious');
      expect(unconsciousSpy).toHaveBeenCalledWith(testCombatant);
    });

    it('should apply healing', () => {
      testCombatant.stats.hitPoints.current = 25;

      const healingSpy = vi.fn();
      combatEngine.on('healingApplied', healingSpy);

      combatEngine.heal('test-combatant', 10);

      expect(testCombatant.stats.hitPoints.current).toBe(35);
      expect(healingSpy).toHaveBeenCalled();
    });

    it('should not heal above maximum', () => {
      combatEngine.heal('test-combatant', 100);

      expect(testCombatant.stats.hitPoints.current).toBe(testCombatant.stats.hitPoints.max);
    });
  });

  describe('damage modifiers', () => {
    beforeEach(() => {
      combatEngine.addCombatant(testCombatant);
    });

    it('should apply resistances', () => {
      testCombatant.stats.resistances = ['fire'];

      // Access private method for testing
      const applyModifiers = (combatEngine as any).applyDamageModifiers;
      const modifiedDamage = applyModifiers.call(combatEngine, testCombatant, 10, 'fire');

      expect(modifiedDamage).toBe(5); // Half damage
    });

    it('should apply immunities', () => {
      testCombatant.stats.immunities = ['poison'];

      const applyModifiers = (combatEngine as any).applyDamageModifiers;
      const modifiedDamage = applyModifiers.call(combatEngine, testCombatant, 10, 'poison');

      expect(modifiedDamage).toBe(0); // No damage
    });

    it('should apply vulnerabilities', () => {
      testCombatant.stats.vulnerabilities = ['cold'];

      const applyModifiers = (combatEngine as any).applyDamageModifiers;
      const modifiedDamage = applyModifiers.call(combatEngine, testCombatant, 10, 'cold');

      expect(modifiedDamage).toBe(20); // Double damage
    });
  });

  describe('utility functions', () => {
    it('should calculate ability modifiers correctly', () => {
      const getAbilityModifier = (combatEngine as any).getAbilityModifier;

      expect(getAbilityModifier.call(combatEngine, 10)).toBe(0);
      expect(getAbilityModifier.call(combatEngine, 16)).toBe(3);
      expect(getAbilityModifier.call(combatEngine, 8)).toBe(-1);
    });

    it('should calculate distance correctly', () => {
      const calculateDistance = (combatEngine as any).calculateDistance;

      const distance = calculateDistance.call(combatEngine, 
        { x: 0, y: 0 }, 
        { x: 3, y: 4 }
      );

      expect(distance).toBe(25); // 5 feet per square, 5 squares = 25 feet
    });

    it('should roll dice correctly', () => {
      const rollDice = (combatEngine as any).rollDice;

      // Mock Math.random for consistent results
      const originalRandom = Math.random;
      Math.random = vi.fn(() => 0.5); // Always roll middle value

      const result = rollDice.call(combatEngine, '2d6');

      expect(result.rolls).toHaveLength(2);
      expect(result.total).toBeGreaterThan(0);

      Math.random = originalRandom;
    });
  });

  describe('event system', () => {
    it('should register and call event handlers', () => {
      const handler = vi.fn();
      combatEngine.on('test-event', handler);

      // Access private emit method
      const emit = (combatEngine as any).emit;
      emit.call(combatEngine, 'test-event', 'test-data');

      expect(handler).toHaveBeenCalledWith('test-data');
    });

    it('should remove event handlers', () => {
      const handler = vi.fn();
      combatEngine.on('test-event', handler);
      combatEngine.off('test-event', handler);

      const emit = (combatEngine as any).emit;
      emit.call(combatEngine, 'test-event', 'test-data');

      expect(handler).not.toHaveBeenCalled();
    });
  });
});

import { describe, it, expect } from "vitest";
import SRDMonsters, { Goblin, Orc, Skeleton, _Wolf} from "@vtt/content-5e-srd";
import { compileMonster, abilityMod, CR_XP_MAP, crToProficiency } from "../src";

describe("abilityMod()", () => {
  it("computes standard modifiers", () => {
    expect(abilityMod(10)).toBe(0);
    expect(abilityMod(8)).toBe(-1);
    expect(abilityMod(12)).toBe(1);
    expect(abilityMod(20)).toBe(5);
  });
});

describe("CR mappings", () => {
  it("maps CR to XP and proficiency", () => {
    expect(CR_XP_MAP["1/4"]).toBe(50);
    expect(crToProficiency("1/2")).toBe(2);
    expect(crToProficiency("5")).toBe(3);
    expect(crToProficiency("15")).toBe(5);
    expect(crToProficiency("21")).toBe(7);
    expect(crToProficiency("29")).toBe(9);
  });
});

describe("compileMonster()", () => {
  it("compiles Goblin correctly", () => {
    const cm = compileMonster(Goblin);
    expect(cm.name).toBe("Goblin");
    expect(cm.challengeRating).toBe("1/4");
    expect(cm.xp).toBe(50);
    expect(cm.proficiencyBonus).toBe(2);
    expect(cm.passivePerception).toBe(9); // explicit in data
  });

  it("compiles Orc and derives values when missing", () => {
    const { xp: _xp,  proficiencyBonus: _pb,  passivePerception: _pp,  _...rest  } = Orc as any;
    const cm = compileMonster(rest);
    expect(cm.xp).toBe(100); // from CR map
    expect(cm.proficiencyBonus).toBe(2); // from CR
    expect(cm.passivePerception).toBe(10); // 10 + WIS mod (0)
  });

  it("includes resistances/vulnerabilities and conditions", () => {
    const cm = compileMonster(Skeleton);
    expect(cm.damageVulnerabilities).toContain("bludgeoning");
    expect(cm.damageImmunities).toContain("poison");
    expect(cm.conditionImmunities).toContain("poisoned");
  });

  it("compiles a list without error", () => {
    const list = SRDMonsters.map((_m) => compileMonster(m));
    expect(list.length).toBeGreaterThan(0);
    const wolf = list.find((_m) => m.id === "srd-wolf")!;
    expect(wolf.passivePerception).toBe(13);
  });
});

/**
 * Rule-Driven Content Generation Workflows
 * Automatically triggers procedural content generation based on game rules and events
 */

import { DeepRuleEngine, Rule, RuleContext, _RuleEffect, RuleCondition } from './DeepRuleEngine';
import { ProfessionalContentSuite } from '../content-creation/src/ProfessionalContentSuite';
import { ProceduralBehaviorGenerator } from '../ai/src/ProceduralBehaviorGenerator';
import { globalEventBus, _GameEvents, ContentEvents, _RuleEvents} from '../core/src/EventBus';

export interface ContentGenerationRule extends Rule {
  contentType: 'dungeon' | 'encounter' | 'treasure' | 'npc' | 'quest' | 'behavior' | 'item' | 'spell';
  generationParams: Record<string, any>;
  priority: number;
  cooldown?: number; // Seconds between generations
  maxGenerations?: number; // Max times this rule can trigger
}

export interface GenerationTrigger {
  id: string;
  eventType: string;
  conditions: RuleCondition[];
  contentRules: string[]; // Rule IDs to evaluate
  context: RuleContext;
}

export interface GenerationHistory {
  ruleId: string;
  contentType: string;
  generatedAt: Date;
  parameters: Record<string, any>;
  result: any;
}

export class ContentGenerationWorkflowEngine {
  private ruleEngine: DeepRuleEngine;
  private contentSuite: ProfessionalContentSuite;
  private behaviorGenerator: ProceduralBehaviorGenerator;
  private contentRules: Map<string, ContentGenerationRule> = new Map();
  private generationHistory: GenerationHistory[] = [];
  private activeTriggers: Map<string, GenerationTrigger> = new Map();
  private lastGeneration: Map<string, number> = new Map(); // Rule ID -> timestamp
  private generationCounts: Map<string, number> = new Map(); // Rule ID -> count

  constructor(
    ruleEngine: DeepRuleEngine,
    contentSuite: ProfessionalContentSuite,
    behaviorGenerator: ProceduralBehaviorGenerator
  ) {
    this.ruleEngine = ruleEngine;
    this.contentSuite = contentSuite;
    this.behaviorGenerator = behaviorGenerator;

    this.initializeWorkflows();
    this.setupEventListeners();
  }

  /**
   * Register a content generation rule
   */
  registerContentRule(rule: ContentGenerationRule): void {
    this.contentRules.set(rule.id, rule);

    // Also register with the main rule engine
    this.ruleEngine.addRule(rule);
  }

  /**
   * Create a generation trigger that listens for specific events
   */
  createGenerationTrigger(trigger: GenerationTrigger): void {
    this.activeTriggers.set(trigger.id, trigger);
  }

  /**
   * Process an event and trigger appropriate content generation
   */
  async processEvent(eventType: string, eventData: any, context: RuleContext): Promise<void> {
    // Find matching triggers
    const matchingTriggers = Array.from(this.activeTriggers.values())
      .filter(trigger => trigger.eventType === eventType);

    for (const trigger of matchingTriggers) {
      // Check trigger conditions
      const conditionsMet = await this.evaluateTriggerConditions(trigger, eventData, context);

      if (conditionsMet) {
        // Evaluate associated content rules
        for (const ruleId of trigger.contentRules) {
          await this.evaluateContentRule(ruleId, { ...context, ...trigger.context, eventData });
        }
      }
    }
  }

  /**
   * Evaluate a specific content generation rule
   */
  async evaluateContentRule(ruleId: string, context: RuleContext): Promise<void> {
    const rule = this.contentRules.get(ruleId);
    if (!rule) return;

    // Check cooldown
    if (rule.cooldown) {
      const lastGen = this.lastGeneration.get(ruleId) || 0;
      const now = Date.now();
      if ((now - lastGen) < rule.cooldown * 1000) {
        return; // Still on cooldown
      }
    }

    // Check max generations
    if (rule.maxGenerations) {
      const count = this.generationCounts.get(ruleId) || 0;
      if (count >= rule.maxGenerations) {
        return; // Already hit max generations
      }
    }

    // Evaluate rule conditions
    const conditionsMet = await this.ruleEngine.evaluateConditions(rule.conditions, context);

    if (conditionsMet) {
      await this.executeContentGeneration(rule, context);
    }
  }

  /**
   * Execute content generation based on rule
   */
  private async executeContentGeneration(rule: ContentGenerationRule, context: RuleContext): Promise<void> {
    try {
      let result: any = null;
      const params = { ...rule.generationParams, context };

      switch (rule.contentType) {
        case 'dungeon':
          result = await this.generateDungeon(params);
          break;
        case 'encounter':
          result = await this.generateEncounter(params);
          break;
        case 'treasure':
          result = await this.generateTreasure(params);
          break;
        case 'npc':
          result = await this.generateNPC(params);
          break;
        case 'quest':
          result = await this.generateQuest(params);
          break;
        case 'behavior':
          result = await this.generateBehavior(params);
          break;
        case 'item':
          result = await this.generateItem(params);
          break;
        case 'spell':
          result = await this.generateSpell(params);
          break;
      }

      if (result) {
        // Record generation
        this.recordGeneration(rule, params, result);

        // Update counters
        this.lastGeneration.set(rule.id, Date.now());
        this.generationCounts.set(rule.id, (this.generationCounts.get(rule.id) || 0) + 1);

        // Emit content generation event
        await globalEventBus.emit(ContentEvents.contentGenerated(rule.contentType, result.id || 'unknown', result));

        // Execute rule effects if any
        if (rule.effects) {
          await this.ruleEngine.executeEffects(rule.effects, { ...context, generatedContent: result });
        }
      }
    } catch (error) {
      console.error(`Failed to generate ${rule.contentType} for rule ${rule.id}:`, error);
    }
  }

  // Content generation methods
  private async generateDungeon(params: any): Promise<any> {
    const options = {
      width: params.width || 50,
      height: params.height || 50,
      theme: params.theme || 'stone',
      difficulty: params.difficulty || 'moderate',
      roomCount: params.roomCount || { min: 5, max: 15 },
      ...params
    };

    return await this.contentSuite.generateDungeon(options);
  }

  private async generateEncounter(params: any): Promise<any> {
    const partyLevel = params.partyLevel || params.context?.partyLevel || 5;
    const partySize = params.partySize || params.context?.partySize || 4;
    const environment = params.environment || params.context?.currentLocation?.environment || 'dungeon';

    const options = {
      partyLevel,
      partySize,
      environment,
      difficulty: params.difficulty || 'balanced',
      theme: params.theme,
      specialConditions: params.specialConditions || [],
      ...params
    };

    return await this.contentSuite.generateEncounter(options);
  }

  private async generateTreasure(params: any): Promise<any> {
    const options = {
      level: params.level || params.context?.partyLevel || 5,
      type: params.type || 'mixed',
      value: params.value,
      magical: params.magical !== false,
      theme: params.theme,
      ...params
    };

    return await this.contentSuite.generateTreasure(options);
  }

  private async generateNPC(params: any): Promise<any> {
    const options = {
      race: params.race,
      class: params.class,
      level: params.level || 1,
      role: params.role || 'citizen',
      personality: params.personality,
      background: params.background,
      location: params.location || params.context?.currentLocation,
      ...params
    };

    return await this.contentSuite.generateNPC(options);
  }

  private async generateQuest(params: any): Promise<any> {
    const options = {
      type: params.type || 'fetch',
      difficulty: params.difficulty || 'moderate',
      location: params.location || params.context?.currentLocation,
      giver: params.giver,
      reward: params.reward,
      timeLimit: params.timeLimit,
      requirements: params.requirements || [],
      ...params
    };

    return await this.contentSuite.generateQuest(options);
  }

  private async generateBehavior(params: any): Promise<any> {
    const options = {
      creatureType: params.creatureType,
      intelligence: params.intelligence || 'average',
      alignment: params.alignment,
      environment: params.environment || params.context?.currentLocation?.environment,
      role: params.role || 'minion',
      complexity: params.complexity || 'moderate',
      ...params
    };

    return await this.behaviorGenerator.generateBehavior(options);
  }

  private async generateItem(params: any): Promise<any> {
    const options = {
      type: params.type || 'weapon',
      rarity: params.rarity || 'common',
      level: params.level || params.context?.partyLevel || 5,
      properties: params.properties || [],
      theme: params.theme,
      ...params
    };

    return await this.contentSuite.generateItem(options);
  }

  private async generateSpell(params: any): Promise<any> {
    const options = {
      level: params.level || 1,
      school: params.school,
      damage: params.damage,
      duration: params.duration,
      range: params.range,
      components: params.components,
      theme: params.theme,
      ...params
    };

    return await this.contentSuite.generateSpell(options);
  }

  /**
   * Evaluate trigger conditions
   */
  private async evaluateTriggerConditions(
    trigger: GenerationTrigger, 
    eventData: any, 
    context: RuleContext
  ): Promise<boolean> {
    for (const condition of trigger.conditions) {
      const result = await this.ruleEngine.evaluateCondition(condition, { ...context, eventData });
      if (!result) return false;
    }
    return true;
  }

  /**
   * Record a generation in history
   */
  private recordGeneration(rule: ContentGenerationRule, params: any, result: any): void {
    this.generationHistory.push({
      ruleId: rule.id,
      contentType: rule.contentType,
      generatedAt: new Date(),
      parameters: params,
      result
    });

    // Keep history limited to prevent memory issues
    if (this.generationHistory.length > 1000) {
      this.generationHistory = this.generationHistory.slice(-500);
    }
  }

  /**
   * Setup event listeners for automatic workflow triggers
   */
  private setupEventListeners(): void {
    // Listen for game events that might trigger content generation
    globalEventBus.on('game:playerEntered', (event) => {
      this.processEvent('playerEntered', event.data, event.context || {});
    });

    globalEventBus.on('game:combatEnded', (event) => {
      this.processEvent('combatEnded', event.data, event.context || {});
    });

    globalEventBus.on('game:questCompleted', (event) => {
      this.processEvent('questCompleted', event.data, event.context || {});
    });

    globalEventBus.on('game:treasureFound', (event) => {
      this.processEvent('treasureFound', event.data, event.context || {});
    });

    globalEventBus.on('rule:conditionMet', (event) => {
      this.processEvent('ruleConditionMet', event.data, event.context || {});
    });
  }

  /**
   * Initialize common workflow rules
   */
  private initializeWorkflows(): void {
    // Auto-generate encounters for empty rooms
    this.registerContentRule({
      id: 'empty_room_encounter',
      name: 'Generate Encounter for Empty Rooms',
      description: 'Automatically populate empty rooms with encounters',
      contentType: 'encounter',
      priority: 5,
      cooldown: 300, // 5 minutes
      conditions: [
        {
          type: 'property',
          property: 'room.isEmpty',
          operator: 'equals',
          value: true
        },
        {
          type: 'property',
          property: 'room.visited',
          operator: 'equals',
          value: false
        },
        {
          type: 'random',
          probability: 0.7
        }
      ],
      effects: [
        {
          type: 'setProperty',
          target: 'room',
          property: 'isEmpty',
          value: false
        }
      ],
      generationParams: {
        difficulty: 'balanced',
        environment: 'dungeon'
      }
    });

    // Generate treasure after combat
    this.registerContentRule({
      id: 'post_combat_treasure',
      name: 'Generate Treasure After Combat',
      description: 'Create treasure rewards after successful combat',
      contentType: 'treasure',
      priority: 7,
      cooldown: 60,
      conditions: [
        {
          type: 'property',
          property: 'combat.result',
          operator: 'equals',
          value: 'victory'
        },
        {
          type: 'property',
          property: 'combat.difficulty',
          operator: 'greaterThan',
          value: 'easy'
        }
      ],
      effects: [
        {
          type: 'addToInventory',
          target: 'currentParty',
          property: 'treasure'
        }
      ],
      generationParams: {
        type: 'combat_reward',
        magical: true
      }
    });

    // Generate random NPCs in towns
    this.registerContentRule({
      id: 'town_npc_generation',
      name: 'Generate Town NPCs',
      description: 'Populate towns with interesting NPCs',
      contentType: 'npc',
      priority: 3,
      cooldown: 1800, // 30 minutes
      maxGenerations: 10,
      conditions: [
        {
          type: 'property',
          property: 'location.type',
          operator: 'equals',
          value: 'town'
        },
        {
          type: 'property',
          property: 'location.npcCount',
          operator: 'lessThan',
          value: 15
        },
        {
          type: 'random',
          probability: 0.3
        }
      ],
      effects: [
        {
          type: 'increment',
          target: 'location',
          property: 'npcCount'
        }
      ],
      generationParams: {
        role: 'citizen',
        personality: 'varied'
      }
    });

    // Generate quests from NPCs
    this.registerContentRule({
      id: 'npc_quest_generation',
      name: 'Generate NPC Quests',
      description: 'Create quests from NPCs when players interact',
      contentType: 'quest',
      priority: 6,
      cooldown: 600, // 10 minutes
      conditions: [
        {
          type: 'property',
          property: 'npc.hasQuest',
          operator: 'equals',
          value: false
        },
        {
          type: 'property',
          property: 'npc.questPotential',
          operator: 'greaterThan',
          value: 0.5
        },
        {
          type: 'property',
          property: 'interaction.type',
          operator: 'equals',
          value: 'conversation'
        }
      ],
      effects: [
        {
          type: 'setProperty',
          target: 'npc',
          property: 'hasQuest',
          value: true
        }
      ],
      generationParams: {
        difficulty: 'moderate',
        type: 'varied'
      }
    });

    // Create corresponding triggers
    this.createGenerationTrigger({
      id: 'room_entry_trigger',
      eventType: 'playerEntered',
      conditions: [
        {
          type: 'property',
          property: 'location.type',
          operator: 'equals',
          value: 'room'
        }
      ],
      contentRules: ['empty_room_encounter'],
      context: Record<string, any>
    });

    this.createGenerationTrigger({
      id: 'combat_end_trigger',
      eventType: 'combatEnded',
      conditions: [],
      contentRules: ['post_combat_treasure'],
      context: Record<string, any>
    });

    this.createGenerationTrigger({
      id: 'town_visit_trigger',
      eventType: 'playerEntered',
      conditions: [
        {
          type: 'property',
          property: 'location.type',
          operator: 'equals',
          value: 'town'
        }
      ],
      contentRules: ['town_npc_generation', 'npc_quest_generation'],
      context: Record<string, any>
    });
  }

  /**
   * Get generation history for analysis
   */
  getGenerationHistory(contentType?: string, limit: number = 50): GenerationHistory[] {
    let history = this.generationHistory;

    if (contentType) {
      history = history.filter(h => h.contentType === contentType);
    }

    return history
      .sort((_a, _b) => b.generatedAt.getTime() - a.generatedAt.getTime())
      .slice(0, limit);
  }

  /**
   * Get statistics about content generation
   */
  getGenerationStats(): Record<string, any> {
    const stats = {
      totalGenerations: this.generationHistory.length,
      byContentType: {} as Record<string, number>,
      byRule: {} as Record<string, number>,
      recentActivity: 0
    };

    const oneDayAgo = Date.now() - (24 * 60 * 60 * 1000);

    this.generationHistory.forEach(gen => {
      // Count by content type
      stats.byContentType[gen.contentType] = (stats.byContentType[gen.contentType] || 0) + 1;

      // Count by rule
      stats.byRule[gen.ruleId] = (stats.byRule[gen.ruleId] || 0) + 1;

      // Count recent activity
      if (gen.generatedAt.getTime() > oneDayAgo) {
        stats.recentActivity++;
      }
    });

    return stats;
  }
}

/**
 * Deep Rule System Integration - Triple A Quality Game Rule Automation
 * Advanced rule engine that exceeds Fantasy Grounds and other VTT automation capabilities
 */

export interface GameSystem {
  id: string;
  name: string;
  version: string;
  rulebooks: Rulebook[];
  mechanics: GameMechanic[];
  entities: EntityDefinition[];
  workflows: RuleWorkflow[];
  customizations: SystemCustomization[];
}

export interface Rulebook {
  id: string;
  name: string;
  publisher: string;
  version: string;
  rules: Rule[];
  tables: RuleTable[];
  spells: SpellDefinition[];
  items: ItemDefinition[];
  conditions: ConditionDefinition[];
}

export interface Rule {
  id: string;
  name: string;
  category: 'combat' | 'social' | 'exploration' | 'magic' | 'skill' | 'condition' | 'general';
  description: string;
  triggers: RuleTrigger[];
  conditions: RuleCondition[];
  effects: RuleEffect[];
  priority: number;
  source: string;
  tags: string[];
  dependencies: string[];
  overrides: string[];
}

export interface RuleTrigger {
  type: 'action' | 'event' | 'condition' | 'time' | 'dice' | 'damage' | 'movement';
  event: string;
  parameters: Record<string, any>;
  timing: 'before' | 'after' | 'during' | 'instead';
}

export interface RuleCondition {
  type: 'attribute' | 'skill' | 'condition' | 'item' | 'distance' | 'resource' | 'custom';
  property: string;
  operator: 'eq' | 'ne' | 'gt' | 'gte' | 'lt' | 'lte' | 'contains' | 'within' | 'has';
  value: any;
  modifiers: ConditionModifier[];
}

export interface ConditionModifier {
  type: 'advantage' | 'disadvantage' | 'bonus' | 'penalty' | 'multiplier' | 'reroll';
  value: number;
  source: string;
}

export interface RuleEffect {
  type: 'damage' | 'heal' | 'condition' | 'movement' | 'resource' | 'attribute' | 'skill' | 'custom';
  target: string;
  property: string;
  operation: 'set' | 'add' | 'subtract' | 'multiply' | 'divide' | 'min' | 'max';
  value: any;
  duration: EffectDuration;
  stacking: 'none' | 'add' | 'multiply' | 'max' | 'replace';
}

export interface EffectDuration {
  type: 'instant' | 'rounds' | 'minutes' | 'hours' | 'days' | 'permanent' | 'condition';
  value: number;
  condition?: string;
}

export interface GameMechanic {
  id: string;
  name: string;
  type: 'dice' | 'resource' | 'action_economy' | 'combat' | 'magic' | 'skill_system';
  rules: Rule[];
  calculations: CalculationFormula[];
  automation: AutomationScript[];
}

export interface CalculationFormula {
  id: string;
  name: string;
  formula: string; // Mathematical expression with variables
  variables: FormulaVariable[];
  context: 'combat' | 'skill' | 'damage' | 'healing' | 'general';
}

export interface FormulaVariable {
  name: string;
  type: 'number' | 'dice' | 'attribute' | 'skill' | 'modifier' | 'condition';
  source: string;
  defaultValue?: any;
}

export interface AutomationScript {
  id: string;
  name: string;
  trigger: string;
  conditions: string[];
  script: string; // JavaScript-like automation script
  parameters: ScriptParameter[];
  safety: SafetyConstraints;
}

export interface ScriptParameter {
  name: string;
  type: string;
  required: boolean;
  defaultValue?: any;
  validation?: string;
}

export interface SafetyConstraints {
  maxExecutionTime: number;
  allowedOperations: string[];
  restrictedProperties: string[];
  sandboxed: boolean;
}

export interface EntityDefinition {
  id: string;
  name: string;
  type: 'character' | 'npc' | 'monster' | 'item' | 'spell' | 'location' | 'custom';
  attributes: AttributeDefinition[];
  skills: SkillDefinition[];
  resources: ResourceDefinition[];
  traits: TraitDefinition[];
  templates: EntityTemplate[];
}

export interface AttributeDefinition {
  id: string;
  name: string;
  category: 'primary' | 'secondary' | 'derived' | 'custom';
  dataType: 'number' | 'string' | 'boolean' | 'array' | 'object';
  defaultValue: any;
  min?: number;
  max?: number;
  formula?: string;
  modifiers: AttributeModifier[];
}

export interface AttributeModifier {
  id: string;
  source: string;
  type: 'bonus' | 'penalty' | 'multiplier' | 'set' | 'advantage' | 'disadvantage';
  value: any;
  conditions: string[];
  duration: EffectDuration;
  stacking: boolean;
}

export interface SkillDefinition {
  id: string;
  name: string;
  attribute: string;
  trained: boolean;
  specializations: string[];
  synergies: SkillSynergy[];
