export class DeepRuleEngine {
  private gameSystems: Map<string, GameSystem> = new Map();
  private activeRules: Map<string, Rule[]> = new Map();
  private ruleCache: Map<string, any> = new Map();
  private workflows: Map<string, RuleWorkflow> = new Map();

  // Rule execution engine
  private interpreter: RuleInterpreter;
  private automationEngine: AutomationEngine;
  private calculationEngine: CalculationEngine;

  // Context management
  private currentContext: RuleContext | null = null;
  private contextStack: RuleContext[] = [];

  // Event system for rule triggers
  private eventEmitter: EventEmitter;
  private ruleQueue: QueuedRule[] = [];

  // Performance and monitoring
  private stats = {
    rulesEvaluated: 0,
    automationsExecuted: 0,
    calculationsPerformed: 0,
    averageExecutionTime: 0,
    cacheHitRate: 0,
    errorRate: 0,
  };

  constructor() {
    this.interpreter = new RuleInterpreter();
    this.automationEngine = new AutomationEngine();
    this.calculationEngine = new CalculationEngine();
    this.eventEmitter = new EventEmitter();

    this.setupEventHandlers();
  }

  private setupEventHandlers(): void {
    // Listen for game events that might trigger rules
    this.eventEmitter.on('action:taken', this.handleAction.bind(this));
    this.eventEmitter.on('damage:dealt', this.handleDamage.bind(this));
    this.eventEmitter.on('condition:applied', this.handleCondition.bind(this));
    this.eventEmitter.on('movement:completed', this.handleMovement.bind(this));
    this.eventEmitter.on('dice:rolled', this.handleDiceRoll.bind(this));
    this.eventEmitter.on('turn:started', this.handleTurnStart.bind(this));
    this.eventEmitter.on('turn:ended', this.handleTurnEnd.bind(this));
  }

  // Game system management
  loadGameSystem(system: GameSystem): void {
    this.gameSystems.set(system.id, system);

    // Index rules for quick lookup
    const rules: Rule[] = [];
    system.rulebooks.forEach(book => rules.push(...book.rules));
    system.mechanics.forEach(mechanic => rules.push(...mechanic.rules));

    this.activeRules.set(system.id, rules);
    this.indexRules(system.id, rules);

    // Load workflows
    system.workflows.forEach(workflow => {
      this.workflows.set(workflow.id, workflow);
    });
  }

  private indexRules(systemId: string, rules: Rule[]): void {
    // Create indexes for fast rule lookup by trigger type
    const indexes = {
      byTrigger: new Map<string, Rule[]>(),
      byCategory: new Map<string, Rule[]>(),
      byTag: new Map<string, Rule[]>(),
    };

    rules.forEach(rule => {
      // Index by triggers
      rule.triggers.forEach(trigger => {
        const key = `${trigger.type}:${trigger.event}`;
        if (!indexes.byTrigger.has(key)) {
          indexes.byTrigger.set(key, []);
        }
        indexes.byTrigger.get(key)!.push(rule);
      });

      // Index by category
      if (!indexes.byCategory.has(rule.category)) {
        indexes.byCategory.set(rule.category, []);
      }
      indexes.byCategory.get(rule.category)!.push(rule);

      // Index by tags
      rule.tags.forEach(tag => {
        if (!indexes.byTag.has(tag)) {
          indexes.byTag.set(tag, []);
        }
        indexes.byTag.get(tag)!.push(rule);
      });
    });

    this.ruleCache.set(`indexes_${systemId}`, indexes);
  }

  // Rule execution
  async processEvent(eventType: string, eventData: any, context?: RuleContext): Promise<RuleExecutionResult[]> {
    const startTime = performance.now();

    if (context) {
      this.pushContext(context);
    }

    const applicableRules = this.findApplicableRules(eventType, eventData);
    const results: RuleExecutionResult[] = [];

    // Sort rules by priority
    applicableRules.sort((_a, _b) => b.priority - a.priority);

    // Execute rules
    for (const rule of applicableRules) {
      try {
        const result = await this.executeRule(rule, eventData);
        results.push(result);

        // Break if rule prevents further execution
        if (result.preventsFurtherExecution) {
          break;
        }
      } catch (error) {
        console.error(`Error executing rule ${rule.id}:`, error);
        this.stats.errorRate++;

        results.push({
          ruleId: rule.id,
          success: false,
          error: error.message,
          preventsFurtherExecution: false,
          effects: [],
          notifications: [],
        });
      }
    }

    if (context) {
      this.popContext();
    }

    // Update statistics
    this.stats.rulesEvaluated += applicableRules.length;
    this.stats.averageExecutionTime = (this.stats.averageExecutionTime + (performance.now() - startTime)) / 2;

    return results;
  }

  private findApplicableRules(eventType: string, eventData: any): Rule[] {
    const rules: Rule[] = [];

    for (const [systemId, _systemRules] of this.activeRules) {
      const indexes = this.ruleCache.get(`indexes_${systemId}`);
      if (!indexes) continue;

      // Find rules by trigger
      const triggerKey = `${eventType}:${eventData.type || '*'}`;
      const triggerRules = indexes.byTrigger.get(triggerKey) || [];

      for (const rule of triggerRules) {
        if (this.evaluateRuleConditions(rule, eventData)) {
          rules.push(rule);
        }
      }
    }

    return rules;
  }

  private evaluateRuleConditions(rule: Rule, eventData: any): boolean {
    return rule.conditions.every(condition => 
      this.evaluateCondition(condition, eventData)
    );
  }

  private evaluateCondition(condition: RuleCondition, eventData: any): boolean {
    const value = this.getPropertyValue(condition.property, eventData);

    switch (condition.operator) {
      case 'eq': return value === condition.value;
      case 'ne': return value !== condition.value;
      case 'gt': return value > condition.value;
      case 'gte': return value >= condition.value;
      case 'lt': return value < condition.value;
      case 'lte': return value <= condition.value;
      case 'contains': return Array.isArray(value) ? value.includes(condition.value) : String(value).includes(condition.value);
      case 'within': return this.isWithinRange(value, condition.value);
      case 'has': return this.hasProperty(value, condition.value);
      default: return false;
    }
  }

  private getPropertyValue(property: string, data: any): any {
    const path = property.split('.');
    let value = data;

    for (const key of path) {
      value = value?.[key];
      if (value === undefined) break;
    }

    return value;
  }

  private async executeRule(rule: Rule, eventData: any): Promise<RuleExecutionResult> {
    const result: RuleExecutionResult = {
      ruleId: rule.id,
      success: true,
      preventsFurtherExecution: false,
      effects: [],
      notifications: [],
    };

    // Execute rule effects
    for (const effect of rule.effects) {
      try {
        const effectResult = await this.applyEffect(effect, eventData);
        result.effects.push(effectResult);

        // Add notification if effect has user-visible impact
        if (effectResult.notification) {
          result.notifications.push(effectResult.notification);
        }
      } catch (error) {
        result.success = false;
        result.error = `Effect execution failed: ${error.message}`;
        break;
      }
    }

    return result;
  }

  private async applyEffect(effect: RuleEffect, eventData: any): Promise<EffectResult> {
    const target = this.resolveTarget(effect.target, eventData);
    if (!target) {
      throw new Error(`Target ${effect.target} not found`);
    }

    const currentValue = this.getPropertyValue(effect.property, target);
    let newValue: any;

    switch (effect.operation) {
      case 'set':
        newValue = this.resolveValue(effect.value, eventData);
        break;
      case 'add':
        newValue = currentValue + this.resolveValue(effect.value, eventData);
        break;
      case 'subtract':
        newValue = currentValue - this.resolveValue(effect.value, eventData);
        break;
      case 'multiply':
        newValue = currentValue * this.resolveValue(effect.value, eventData);
        break;
      case 'divide':
        newValue = currentValue / this.resolveValue(effect.value, eventData);
        break;
      case 'min':
        newValue = Math.min(currentValue, this.resolveValue(effect.value, eventData));
        break;
      case 'max':
        newValue = Math.max(currentValue, this.resolveValue(effect.value, eventData));
        break;
      default:
        throw new Error(`Unknown operation: ${effect.operation}`);
    }

    this.setPropertyValue(effect.property, target, newValue);

    return {
      target: effect.target,
      property: effect.property,
      oldValue: currentValue,
      newValue: newValue,
      notification: this.createEffectNotification(effect, currentValue, newValue),
    };
  }

  // Calculation engine integration
  async evaluateFormula(formula: CalculationFormula, variables: Record<string, any>): Promise<number> {
    this.stats.calculationsPerformed++;
    return this.calculationEngine.evaluate(formula, variables);
  }

  // Automation workflows
  async executeWorkflow(workflowId: string, parameters: Record<string, any>): Promise<WorkflowResult> {
    const workflow = this.workflows.get(workflowId);
    if (!workflow) {
      throw new Error(`Workflow ${workflowId} not found`);
    }

    this.stats.automationsExecuted++;
    return this.automationEngine.execute(workflow, parameters, this.currentContext);
  }

  // Context management
  pushContext(context: RuleContext): void {
    if (this.currentContext) {
      this.contextStack.push(this.currentContext);
    }
    this.currentContext = context;
  }

  popContext(): RuleContext | null {
    const previous = this.currentContext;
    this.currentContext = this.contextStack.pop() || null;
    return previous;
  }

  getCurrentContext(): RuleContext | null {
    return this.currentContext;
  }

  // Event handlers
  private async handleAction(actionData: any): Promise<void> {
    await this.processEvent('action', actionData);
  }

  private async handleDamage(damageData: any): Promise<void> {
    await this.processEvent('damage', damageData);
  }

  private async handleCondition(conditionData: any): Promise<void> {
    await this.processEvent('condition', conditionData);
  }

  private async handleMovement(movementData: any): Promise<void> {
    await this.processEvent('movement', movementData);
  }

  private async handleDiceRoll(rollData: any): Promise<void> {
    await this.processEvent('dice', rollData);
  }

  private async handleTurnStart(turnData: any): Promise<void> {
    await this.processEvent('turn_start', turnData);
  }

  private async handleTurnEnd(turnData: any): Promise<void> {
    await this.processEvent('turn_end', turnData);
  }

  // Utility methods
  private resolveTarget(_target: string, _eventData: any): any {
    // Implementation to resolve target references
    return null;
  }

  private resolveValue(value: any, eventData: any): any {
    if (typeof value === 'string' && value.startsWith('$')) {
      return this.getPropertyValue(value.substring(1), eventData);
    }
    return value;
  }

  private setPropertyValue(property: string, target: any, value: any): void {
    const path = property.split('.');
    let current = target;

    for (let i = 0; i < path.length - 1; i++) {
      if (!current[path[i]]) {
        current[path[i]] = {};
      }
      current = current[path[i]];
    }

    current[path[path.length - 1]] = value;
  }

  private createEffectNotification(effect: RuleEffect, oldValue: any, newValue: any): string {
    return `${effect.property} changed from ${oldValue} to ${newValue}`;
  }

  private isWithinRange(value: any, range: [number, number]): boolean {
    return typeof value === 'number' && value >= range[0] && value <= range[1];
  }

  private hasProperty(object: any, property: string): boolean {
    return object && Object.prototype.hasOwnProperty.call(object, property);
  }

  getStats() {
    return { ...this.stats };
  }

  destroy(): void {
    this.gameSystems.clear();
    this.activeRules.clear();
    this.ruleCache.clear();
    this.workflows.clear();
    this.eventEmitter.removeAllListeners();
  }
}

// Supporting interfaces
interface RuleExecutionResult {
  ruleId: string;
  success: boolean;
  error?: string;
  preventsFurtherExecution: boolean;
  effects: EffectResult[];
  notifications: string[];
}

interface EffectResult {
  target: string;
  property: string;
  oldValue: any;
  newValue: any;
  notification?: string;
}

interface WorkflowResult {
  success: boolean;
  steps: StepResult[];
  outputs: Record<string, any>;
  error?: string;
}

interface StepResult {
  stepId: string;
  success: boolean;
  outputs: Record<string, any>;
  error?: string;
}

interface QueuedRule {
  rule: Rule;
  eventData: any;
  context: RuleContext;
  priority: number;
}

// Helper classes (simplified interfaces)
class RuleInterpreter {
  evaluate(_expression: string, _context: any): boolean { return true; }
}

class AutomationEngine {
  async execute(_workflow: RuleWorkflow, _parameters: any, _context: any): Promise<WorkflowResult> {
    return { success: true, steps: [], outputs: Record<string, unknown>};
  }
}

class CalculationEngine {
  evaluate(_formula: CalculationFormula, _variables: Record<string, any>): number { return 0; }
}

class EventEmitter {
  private listeners = new Map<string, Function[]>();

  on(_event: string, _callback: (...args: any[]) => any): void {
    if (!this.listeners.has(event)) {
      this.listeners.set(event, []);
    }
    this.listeners.get(event)!.push(callback);
  }

  emit(event: string, data: any): void {
    const callbacks = this.listeners.get(event) || [];
    callbacks.forEach(callback => callback(data));
  }

  removeAllListeners(): void {
    this.listeners.clear();
  }
}

/**
 * Fog of War Management System
 * Handles vision, line of sight, and fog exploration for VTT scenes
 */

export interface FogSettings {
  enabled: boolean;
  exploredColor: string;
  unexploredColor: string;
  opacity: number;
  blurRadius: number;
  autoReveal: boolean;
  persistExplored: boolean;
}

export interface VisionSource {
  id: string;
  x: number;
  y: number;
  radius: number;
  dimRadius?: number;
  angle?: number;
  direction?: number;
  tokenId?: string;
}

export interface ExploredArea {
  id: string;
  points: Array<{ x: number; y: number }>;
  timestamp: number;
}

export interface LineOfSightResult {
  visible: boolean;
  blocked: boolean;
  blockingPoints: Array<{ x: number; y: number }>;
  distance: number;
}

export class FogOfWarManager {
  private settings: FogSettings;
  private visionSources: Map<string, VisionSource> = new Map();
  private exploredAreas: Map<string, ExploredArea> = new Map();
  private walls: Array<{ x1: number; y1: number; x2: number; y2: number }> = [];
  private sceneWidth: number;
  private sceneHeight: number;

  constructor(settings: FogSettings, sceneWidth: number, sceneHeight: number) {
    this.settings = settings;
    this.sceneWidth = sceneWidth;
    this.sceneHeight = sceneHeight;
  }

  /**
   * Add a vision source (typically attached to a token)
   */
  addVisionSource(source: VisionSource): void {
    this.visionSources.set(source.id, source);
  }

  /**
   * Remove a vision source
   */
  removeVisionSource(sourceId: string): void {
    this.visionSources.delete(sourceId);
  }

  /**
   * Update a vision source
   */
  updateVisionSource(sourceId: string, updates: Partial<VisionSource>): void {
    const source = this.visionSources.get(sourceId);
    if (!source) {
      throw new Error(`Vision source ${sourceId} not found`);
    }

    this.visionSources.set(sourceId, { ...source, ...updates });
  }

  /**
   * Add wall segments for line of sight calculations
   */
  addWall(x1: number, y1: number, x2: number, y2: number): void {
    this.walls.push({ x1, y1, x2, y2 });
  }

  /**
   * Remove all walls
   */
  clearWalls(): void {
    this.walls = [];
  }

  /**
   * Calculate line of sight between two points
   */
  calculateLineOfSight(fromX: number, fromY: number, toX: number, toY: number): LineOfSightResult {
    const distance = Math.sqrt((toX - fromX) ** 2 + (toY - fromY) ** 2);
    const blockingPoints: Array<{ x: number; y: number }> = [];
    let blocked = false;

    for (const wall of this.walls) {
      const intersection = this.lineIntersection(
        fromX, fromY, toX, toY,
        wall.x1, wall.y1, wall.x2, wall.y2
      );

      if (intersection) {
        blocked = true;
        blockingPoints.push(intersection);
      }
    }

    return {
      visible: !blocked,
      blocked,
      blockingPoints,
      distance
    };
  }

  private lineIntersection(
    x1: number, y1: number, x2: number, y2: number,
    x3: number, y3: number, x4: number, y4: number
  ): { x: number; y: number } | null {
    const denom = (x1 - x2) * (y3 - y4) - (y1 - y2) * (x3 - x4);
    if (Math.abs(denom) < 0.0001) return null;

    const t = ((x1 - x3) * (y3 - y4) - (y1 - y3) * (x3 - x4)) / denom;
    const u = -((x1 - x2) * (y1 - y3) - (y1 - y2) * (x1 - x3)) / denom;

    if (t >= 0 && t <= 1 && u >= 0 && u <= 1) {
      return {
        x: x1 + t * (x2 - x1),
        y: y1 + t * (y2 - y1)
      };
    }

    return null;
  }

  /**
   * Calculate vision polygon for a vision source
   */
  calculateVisionPolygon(sourceId: string): Array<{ x: number; y: number }> {
    const source = this.visionSources.get(sourceId);
    if (!source) {
      return [];
    }

    const rays = this.castRays(source);
    return this.raysToPolygon(rays, source);
  }

  private castRays(source: VisionSource): Array<{ x: number; y: number; distance: number }> {
    const rays: Array<{ x: number; y: number; distance: number }> = [];
    const rayCount = 360; // Cast rays in 1-degree increments

    const startAngle = source.angle ? source.direction! - source.angle / 2 : 0;
    const endAngle = source.angle ? source.direction! + source.angle / 2 : 360;

    for (let angle = startAngle; angle < endAngle; angle += 360 / rayCount) {
      const rad = (angle * Math.PI) / 180;
      const endX = source.x + Math.cos(rad) * source.radius;
      const endY = source.y + Math.sin(rad) * source.radius;

      const lineOfSight = this.calculateLineOfSight(source.x, source.y, endX, endY);

      if (lineOfSight.blocked && lineOfSight.blockingPoints.length > 0) {
        // Use the closest blocking point
        let closestPoint = lineOfSight.blockingPoints[0]!;
        let closestDistance = Math.sqrt(
          (closestPoint.x - source.x) ** 2 + (closestPoint.y - source.y) ** 2
        );

        for (const point of lineOfSight.blockingPoints) {
          const distance = Math.sqrt((point.x - source.x) ** 2 + (point.y - source.y) ** 2);
          if (distance < closestDistance) {
            closestPoint = point;
            closestDistance = distance;
          }
        }

        rays.push({
          x: closestPoint.x,
          y: closestPoint.y,
          distance: closestDistance
        });
      } else {
        rays.push({
          x: endX,
          y: endY,
          distance: source.radius
        });
      }
    }

    return rays;
  }

  private raysToPolygon(
    rays: Array<{ x: number; y: number; distance: number }>,
    source: VisionSource
  ): Array<{ x: number; y: number }> {
    const polygon: Array<{ x: number; y: number }> = [];

    // Add source point
    polygon.push({ x: source.x, y: source.y });

    // Add ray endpoints
    rays.forEach(ray => {
      polygon.push({ x: ray.x, y: ray.y });
    });

    return polygon;
  }

  /**
   * Mark area as explored
   */
  addExploredArea(points: Array<{ x: number; y: number }>): string {
    const areaId = `area-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

    this.exploredAreas.set(areaId, {
      id: areaId,
      points,
      timestamp: Date.now()
    });

    return areaId;
  }

  /**
   * Remove explored area
   */
  removeExploredArea(areaId: string): void {
    this.exploredAreas.delete(areaId);
  }

  /**
   * Clear all explored areas
   */
  clearExploredAreas(): void {
    this.exploredAreas.clear();
  }

  /**
   * Check if a point is in explored area
   */
  isPointExplored(x: number, y: number): boolean {
    if (!this.settings.enabled) return true;

    for (const area of this.exploredAreas.values()) {
      if (this.pointInPolygon(x, y, area.points)) {
        return true;
      }
    }

    return false;
  }

  private pointInPolygon(x: number, y: number, polygon: Array<{ x: number; y: number }>): boolean {
    let inside = false;

    for (let i = 0, j = polygon.length - 1; i < polygon.length; j = i++) {
      const pointI = polygon[i]!;
      const pointJ = polygon[j]!;

      if (
        (pointI.y > y) !== (pointJ.y > y) &&
        x < ((pointJ.x - pointI.x) * (y - pointI.y)) / (pointJ.y - pointI.y) + pointI.x
      ) {
        inside = !inside;
      }
    }

    return inside;
  }

  /**
   * Update vision for all sources and auto-reveal areas
   */
  updateVision(): void {
    if (!this.settings.enabled || !this.settings.autoReveal) return;

    for (const source of this.visionSources.values()) {
      const visionPolygon = this.calculateVisionPolygon(source.id);

      if (visionPolygon.length > 0) {
        this.addExploredArea(visionPolygon);
      }
    }
  }

  /**
   * Get fog mask data for rendering
   */
  getFogMask(): {
    exploredAreas: ExploredArea[];
    visibleAreas: Array<{ sourceId: string; polygon: Array<{ x: number; y: number }> }>;
  } {
    const exploredAreas = Array.from(this.exploredAreas.values());
    const visibleAreas: Array<{ sourceId: string; polygon: Array<{ x: number; y: number }> }> = [];

    for (const [sourceId, _source] of this.visionSources.entries()) {
      const polygon = this.calculateVisionPolygon(sourceId);
      visibleAreas.push({ sourceId, polygon });
    }

    return { exploredAreas, visibleAreas };
  }

  /**
   * Check if a token can see another token
   */
  canTokenSeeToken(fromTokenId: string, toX: number, toY: number): boolean {
    const source = Array.from(this.visionSources.values()).find(s => s.tokenId === fromTokenId);
    if (!source) return false;

    const distance = Math.sqrt((toX - source.x) ** 2 + (toY - source.y) ** 2);
    if (distance > source.radius) return false;

    const lineOfSight = this.calculateLineOfSight(source.x, source.y, toX, toY);
    return lineOfSight.visible;
  }

  /**
   * Get all tokens visible to a specific token
   */
  getVisibleTokens(fromTokenId: string, allTokens: Array<{ id: string; x: number; y: number }>): string[] {
    const visibleTokenIds: string[] = [];

    for (const token of allTokens) {
      if (token.id === fromTokenId) continue;

      if (this.canTokenSeeToken(fromTokenId, token.x, token.y)) {
        visibleTokenIds.push(token.id);
      }
    }

    return visibleTokenIds;
  }

  /**
   * Update fog settings
   */
  updateSettings(newSettings: Partial<FogSettings>): void {
    this.settings = { ...this.settings, ...newSettings };
  }

  /**
   * Get current fog settings
   */
  getSettings(): FogSettings {
    return { ...this.settings };
  }

  /**
   * Export fog data
   */
  exportFogData(): {
    settings: FogSettings;
    visionSources: VisionSource[];
    exploredAreas: ExploredArea[];
    walls: Array<{ x1: number; y1: number; x2: number; y2: number }>;
  } {
    return {
      settings: this.settings,
      visionSources: Array.from(this.visionSources.values()),
      exploredAreas: Array.from(this.exploredAreas.values()),
      walls: [...this.walls]
    };
  }

  /**
   * Import fog data
   */
  importFogData(data: {
    settings: FogSettings;
    visionSources: VisionSource[];
    exploredAreas: ExploredArea[];
    walls: Array<{ x1: number; y1: number; x2: number; y2: number }>;
  }): void {
    this.settings = data.settings;
    this.walls = data.walls;

    this.visionSources.clear();
    data.visionSources.forEach(source => {
      this.visionSources.set(source.id, source);
    });

    this.exploredAreas.clear();
    data.exploredAreas.forEach(area => {
      this.exploredAreas.set(area.id, area);
    });
  }
}

/**
 * Advanced Grid Management System
 * Supports multiple grid types, snap-to-grid, and grid calculations
 */

export type GridType = 'square' | 'hexagonal' | 'isometric' | 'none';

export interface GridSettings {
  type: GridType;
  size: number;
  offsetX: number;
  offsetY: number;
  color: string;
  opacity: number;
  snapToGrid: boolean;
  showGrid: boolean;
  subdivisions?: number;
  // Hexagonal specific
  hexOrientation?: 'pointy' | 'flat';
  // Isometric specific
  isoAngle?: number;
}

export interface GridCoordinate {
  x: number;
  y: number;
  col?: number;
  row?: number;
}

export interface GridBounds {
  minX: number;
  maxX: number;
  minY: number;
  maxY: number;
  cols: number;
  rows: number;
  minCol: number;
  maxCol: number;
  minRow: number;
  maxRow: number;
}

export class GridManager {
  private settings: GridSettings;
  private bounds: GridBounds;
  private eventListeners: Map<string, ((_data: any) => void)[]> = new Map();

  constructor(settings: GridSettings, sceneWidth?: number, sceneHeight?: number) {
    this.settings = settings;
    this.bounds = this.calculateBounds(sceneWidth || 1000, sceneHeight || 1000);
  }

  private calculateBounds(width: number, height: number): GridBounds {
    const cols = Math.ceil(width / this.settings.size);
    const rows = Math.ceil(height / this.settings.size);

    return {
      minX: this.settings.offsetX,
      maxX: this.settings.offsetX + (cols * this.settings.size),
      minY: this.settings.offsetY,
      maxY: this.settings.offsetY + (rows * this.settings.size),
      cols,
      rows,
      minCol: 0,
      maxCol: cols,
      minRow: 0,
      maxRow: rows
    };
  }

  /**
   * Convert pixel coordinates to grid coordinates
   */
  pixelToGrid(pixelX: number, pixelY: number): GridCoordinate {
    const adjustedX = pixelX - this.settings.offsetX;
    const adjustedY = pixelY - this.settings.offsetY;

    switch (this.settings.type) {
      case 'square':
        return this.pixelToSquareGrid(adjustedX, adjustedY);
      case 'hexagonal':
        return this.pixelToHexGrid(adjustedX, adjustedY);
      case 'isometric':
        return this.pixelToIsoGrid(adjustedX, adjustedY);
      default:
        return { x: pixelX, y: pixelY };
    }
  }

  /**
   * Convert grid coordinates to pixel coordinates
   */
  gridToPixel(gridCoord: GridCoordinate): GridCoordinate {
    // Handle cases where only col/row are provided
    if (gridCoord.col !== undefined && gridCoord.row !== undefined && (!gridCoord.x || !gridCoord.y)) {
      gridCoord = { 
        x: gridCoord.col * this.settings.size, 
        y: gridCoord.row * this.settings.size, 
        col: gridCoord.col, 
        row: gridCoord.row 
      };
    }

    switch (this.settings.type) {
      case 'square':
        return this.squareGridToPixel(gridCoord);
      case 'hexagonal':
        return this.hexGridToPixel(gridCoord);
      case 'isometric':
        return this.isoGridToPixel(gridCoord);
      default:
        return gridCoord;
    }
  }

  /**
   * Snap pixel coordinates to grid
   */
  snapToGrid(coord: GridCoordinate): GridCoordinate;
  snapToGrid(pixelX: number, pixelY: number): GridCoordinate;
  snapToGrid(coordOrX: GridCoordinate | number, pixelY?: number): GridCoordinate {
    let x: number, y: number;

    if (typeof coordOrX === 'object') {
      x = coordOrX.x;
      y = coordOrX.y;
    } else {
      x = coordOrX;
      y = pixelY!;
    }

    if (!this.settings.snapToGrid) {
      return { x, y };
    }

    const gridCoord = this.pixelToGrid(x, y);
    return this.gridToPixel(gridCoord);
  }

  private pixelToSquareGrid(x: number, y: number): GridCoordinate {
    const col = Math.floor(x / this.settings.size);
    const row = Math.floor(y / this.settings.size);

    return {
      x: col * this.settings.size + this.settings.offsetX,
      y: row * this.settings.size + this.settings.offsetY,
      col,
      row
    };
  }

  private squareGridToPixel(gridCoord: GridCoordinate): GridCoordinate {
    const col = gridCoord.col ?? Math.floor(gridCoord.x / this.settings.size);
    const row = gridCoord.row ?? Math.floor(gridCoord.y / this.settings.size);

    return {
      x: col * this.settings.size + this.settings.offsetX + this.settings.size / 2,
      y: row * this.settings.size + this.settings.offsetY + this.settings.size / 2,
      col,
      row
    };
  }

  private pixelToHexGrid(x: number, y: number): GridCoordinate {
    const size = this.settings.size;
    const isPointy = this.settings.hexOrientation === 'pointy';

    if (isPointy) {
      const q = (Math.sqrt(3)/3 * x - 1/3 * y) / size;
      const r = (2/3 * y) / size;
      return this.hexRoundingToPixel(q, r);
    } else {
      const q = (2/3 * x) / size;
      const r = (-1/3 * x + Math.sqrt(3)/3 * y) / size;
      return this.hexRoundingToPixel(q, r);
    }
  }

  private hexGridToPixel(gridCoord: GridCoordinate): GridCoordinate {
    const size = this.settings.size;
    const q = gridCoord.col ?? 0;
    const r = gridCoord.row ?? 0;
    const isPointy = this.settings.hexOrientation === 'pointy';

    if (isPointy) {
      const x = size * (Math.sqrt(3) * q + Math.sqrt(3)/2 * r);
      const y = size * (3/2 * r);
      return { x: x + this.settings.offsetX, y: y + this.settings.offsetY, col: q, row: r };
    } else {
      const x = size * (3/2 * q);
      const y = size * (Math.sqrt(3)/2 * q + Math.sqrt(3) * r);
      return { x: x + this.settings.offsetX, y: y + this.settings.offsetY, col: q, row: r };
    }
  }

  private hexRoundingToPixel(q: number, r: number): GridCoordinate {
    const s = -q - r;
    let rq = Math.round(q);
    let rr = Math.round(r);
    const rs = Math.round(s);

    const qDiff = Math.abs(rq - q);
    const rDiff = Math.abs(rr - r);
    const sDiff = Math.abs(rs - s);

    if (qDiff > rDiff && qDiff > sDiff) {
      rq = -rr - rs;
    } else if (rDiff > sDiff) {
      rr = -rq - rs;
    }

    return this.hexGridToPixel({ x: 0, y: 0, col: rq, row: rr });
  }

  private pixelToIsoGrid(x: number, y: number): GridCoordinate {
    const size = this.settings.size;
    const angle = this.settings.isoAngle ?? 30;
    const angleRad = (angle * Math.PI) / 180;

    const isoX = (x / (size * Math.cos(angleRad)) + y / (size * Math.sin(angleRad))) / 2;
    const isoY = (y / (size * Math.sin(angleRad)) - x / (size * Math.cos(angleRad))) / 2;

    const col = Math.floor(isoX);
    const row = Math.floor(isoY);

    return {
      x: col * size * Math.cos(angleRad) + this.settings.offsetX,
      y: row * size * Math.sin(angleRad) + this.settings.offsetY,
      col,
      row
    };
  }

  private isoGridToPixel(gridCoord: GridCoordinate): GridCoordinate {
    const size = this.settings.size;
    const angle = this.settings.isoAngle ?? 30;
    const angleRad = (angle * Math.PI) / 180;
    const col = gridCoord.col ?? 0;
    const row = gridCoord.row ?? 0;

    const x = (col - row) * size * Math.cos(angleRad);
    const y = (col + row) * size * Math.sin(angleRad);

    return {
      x: x + this.settings.offsetX,
      y: y + this.settings.offsetY,
      col,
      row
    };
  }

  /**
   * Calculate distance between two grid positions
   */
  calculateDistance(from: GridCoordinate, to: GridCoordinate): number {
    switch (this.settings.type) {
      case 'square':
        return this.calculateSquareDistance(from, to);
      case 'hexagonal':
        return this.calculateHexDistance(from, to);
      default: {
        const dx = to.x - from.x;
        const dy = to.y - from.y;
        return Math.sqrt(dx * dx + dy * dy);
    }
  }

  private calculateSquareDistance(from: GridCoordinate, to: GridCoordinate): number {
    const fromGrid = this.pixelToGrid(from.x, from.y);
    const toGrid = this.pixelToGrid(to.x, to.y);

    return Math.max(
      Math.abs((toGrid.col ?? 0) - (fromGrid.col ?? 0)),
      Math.abs((toGrid.row ?? 0) - (fromGrid.row ?? 0))
    );
  }

  private calculateHexDistance(from: GridCoordinate, to: GridCoordinate): number {
    const fromHex = this.pixelToGrid(from.x, from.y);
    const toHex = this.pixelToGrid(to.x, to.y);

    const q1 = fromHex.col ?? 0;
    const r1 = fromHex.row ?? 0;
    const q2 = toHex.col ?? 0;
    const r2 = toHex.row ?? 0;

    return (Math.abs(q1 - q2) + Math.abs(q1 + r1 - q2 - r2) + Math.abs(r1 - r2)) / 2;
  }

  /**
   * Get all grid cells within a radius
   */
  getCellsInRadius(center: GridCoordinate, radius: number): GridCoordinate[] {
    const cells: GridCoordinate[] = [];
    const centerGrid = this.pixelToGrid(center.x, center.y);

    for (let col = -radius; col <= radius; col++) {
      for (let row = -radius; row <= radius; row++) {
        const testCoord = {
          x: 0, y: 0,
          col: (centerGrid.col ?? 0) + col,
          row: (centerGrid.row ?? 0) + row
        };

        const testPixel = this.gridToPixel(testCoord);
        const distance = this.calculateDistance(center, testPixel);

        if (distance <= radius) {
          cells.push(testPixel);
        }
      }
    }

    return cells;
  }


  /**
   * Get current grid settings
   */
  getSettings(): GridSettings {
    return { ...this.settings };
  }

  /**
   * Get grid bounds
   */
  getBounds(): GridBounds {
    return { ...this.bounds };
  }

  /**
   * Get grid bounds (alternative method name for compatibility)
   */
  getGridBounds(): GridBounds;
  getGridBounds(viewport?: { x: number; y: number; width: number; height: number }): GridBounds;
  getGridBounds(viewport?: { x: number; y: number; width: number; height: number }): GridBounds {
    // Ignore viewport parameter for now, just return standard bounds
    return this.getBounds();
  }

  /**
   * Convert world coordinates to grid coordinates (alternative method name)
   */
  worldToGrid(coord: GridCoordinate): GridCoordinate;
  worldToGrid(x: number, y: number): GridCoordinate;
  worldToGrid(coordOrX: GridCoordinate | number, y?: number): GridCoordinate {
    if (typeof coordOrX === 'object') {
      return this.pixelToGrid(coordOrX.x, coordOrX.y);
    }
    return this.pixelToGrid(coordOrX, y!);
  }

  /**
   * Convert grid coordinates to world coordinates (alternative method name)
   */
  gridToWorld(gridCoord: GridCoordinate): GridCoordinate {
    return this.gridToPixel(gridCoord);
  }

  /**
   * Convert world coordinates to isometric coordinates
   */
  worldToIsometric(worldCoord: GridCoordinate): GridCoordinate {
    const _size = this.settings.size;
    const angle = this.settings.isoAngle ?? 30;
    const angleRad = (angle * Math.PI) / 180;

    const isoX = (worldCoord.x * Math.cos(angleRad) - worldCoord.y * Math.sin(angleRad));
    const isoY = (worldCoord.x * Math.sin(angleRad) + worldCoord.y * Math.cos(angleRad));

    return { x: isoX, y: isoY };
  }

  /**
   * Convert isometric coordinates to world coordinates
   */
  isometricToWorld(isoCoord: GridCoordinate): GridCoordinate {
    const angle = this.settings.isoAngle ?? 30;
    const angleRad = (angle * Math.PI) / 180;

    const worldX = (isoCoord.x * Math.cos(angleRad) + isoCoord.y * Math.sin(angleRad));
    const worldY = (-isoCoord.x * Math.sin(angleRad) + isoCoord.y * Math.cos(angleRad));

    return { x: worldX, y: worldY };
  }

  /**
   * Get grid lines for rendering
   */
  getGridLines(viewport: { x: number; y: number; width: number; height: number }) {
    const horizontal: Array<{ x1: number; y1: number; x2: number; y2: number }> = [];
    const vertical: Array<{ x1: number; y1: number; x2: number; y2: number }> = [];

    const startX = Math.floor((viewport.x - this.settings.offsetX) / this.settings.size) * this.settings.size + this.settings.offsetX;
    const endX = viewport.x + viewport.width;
    const startY = Math.floor((viewport.y - this.settings.offsetY) / this.settings.size) * this.settings.size + this.settings.offsetY;
    const endY = viewport.y + viewport.height;

    // Vertical lines  
    for (let x = startX; x <= endX; x += this.settings.size) {
      vertical.push({
        x1: x, y1: viewport.y,
        x2: x, y2: viewport.y + viewport.height
      });
    }

    // Horizontal lines
    for (let y = startY; y <= endY; y += this.settings.size) {
      horizontal.push({
        x1: viewport.x, y1: y,
        x2: viewport.x + viewport.width, y2: y
      });
    }

    return { horizontal, vertical };
  }

  /**
   * Get hexagonal grid points for rendering
   */
  getHexGridPoints(viewport: { x: number; y: number; width: number; height: number }): Array<{ x: number; y: number; center: { x: number; y: number }; points: Array<{ x: number; y: number }> }> {
    const hexagons: Array<{ x: number; y: number; center: { x: number; y: number }; points: Array<{ x: number; y: number }> }> = [];
    const size = this.settings.size;
    const isPointy = this.settings.hexOrientation === 'pointy';

    // Calculate grid range based on viewport
    const cols = Math.ceil(viewport.width / size) + 2;
    const rows = Math.ceil(viewport.height / size) + 2;

    for (let col = -1; col < cols; col++) {
      for (let row = -1; row < rows; row++) {
        const center = this.hexGridToPixel({ x: 0, y: 0, col, row });

        // Check if hex is in viewport
        if (center.x < viewport.x - size || center.x > viewport.x + viewport.width + size ||
            center.y < viewport.y - size || center.y > viewport.y + viewport.height + size) {
          continue;
        }

        const points: Array<{ x: number; y: number }> = [];
        for (let i = 0; i < 6; i++) {
          const angle = (isPointy ? (60 * i) : (60 * i + 30)) * Math.PI / 180;
          points.push({
            x: center.x + size * Math.cos(angle),
            y: center.y + size * Math.sin(angle)
          });
        }

        hexagons.push({ x: center.x, y: center.y, center: { x: center.x, y: center.y }, points });
      }
    }

    return hexagons;
  }

  /**
   * Calculate grid distance (alias for calculateDistance)
   */
  calculateGridDistance(from: GridCoordinate, to: GridCoordinate): number {
    return this.calculateDistance(from, to);
  }

  /**
   * Calculate area of a polygon in grid units
   */
  calculateGridArea(polygon: Array<{ x: number; y: number }>): number {
    if (polygon.length < 3) return 0;

    let area = 0;
    for (let i = 0; i < polygon.length; i++) {
      const j = (i + 1) % polygon.length;
      const currentPoint = polygon[i];
      const nextPoint = polygon[j];
      if (currentPoint && nextPoint) {
        area += currentPoint.x * nextPoint.y;
        area -= nextPoint.x * currentPoint.y;
      }
    }

    return Math.abs(area) / 2 / (this.settings.size * this.settings.size);
  }

  /**
   * Measure path length in grid units
   */
  measurePath(path: Array<{ x: number; y: number }>): number {
    if (path.length < 2) return 0;

    let totalDistance = 0;
    for (let i = 1; i < path.length; i++) {
      const prevPoint = path[i - 1];
      const currPoint = path[i];
      if (prevPoint && currPoint) {
        totalDistance += this.calculateDistance(prevPoint, currPoint);
      }
    }

    return totalDistance;
  }

  /**
   * Get all cells within bounds
   */
  getCellsInBounds(bounds: { x: number; y: number; width: number; height: number }): GridCoordinate[] {
    const cells: GridCoordinate[] = [];

    const startCol = Math.floor((bounds.x - this.settings.offsetX) / this.settings.size);
    const endCol = Math.ceil((bounds.x + bounds.width - this.settings.offsetX) / this.settings.size);
    const startRow = Math.floor((bounds.y - this.settings.offsetY) / this.settings.size);
    const endRow = Math.ceil((bounds.y + bounds.height - this.settings.offsetY) / this.settings.size);

    for (let col = startCol; col < endCol; col++) {
      for (let row = startRow; row < endRow; row++) {
        const gridCoord = { x: 0, y: 0, col, row };
        const pixelCoord = this.gridToPixel(gridCoord);
        cells.push(pixelCoord);
      }
    }

    return cells;
  }

  /**
   * Get line of sight cells between two points
   */
  getLineOfSightCells(start: GridCoordinate, end: GridCoordinate): GridCoordinate[] {
    const cells: GridCoordinate[] = [];
    const startGrid = this.pixelToGrid(start.x, start.y);
    const endGrid = this.pixelToGrid(end.x, end.y);

    const dx = Math.abs((endGrid.col ?? 0) - (startGrid.col ?? 0));
    const dy = Math.abs((endGrid.row ?? 0) - (startGrid.row ?? 0));
    const steps = Math.max(dx, dy);

    for (let i = 0; i <= steps; i++) {
      const t = steps === 0 ? 0 : i / steps;
      const col = Math.round((startGrid.col ?? 0) * (1 - t) + (endGrid.col ?? 0) * t);
      const row = Math.round((startGrid.row ?? 0) * (1 - t) + (endGrid.row ?? 0) * t);

      const gridCoord = { x: 0, y: 0, col, row };
      const pixelCoord = this.gridToPixel(gridCoord);
      cells.push(pixelCoord);
    }

    return cells;
  }

  /**
   * Convert hexagonal coordinates to world coordinates
   */
  hexToWorld(hexCoord: { q: number; r: number; s: number }): GridCoordinate {
    const gridCoord = { x: 0, y: 0, col: hexCoord.q, row: hexCoord.r };
    return this.hexGridToPixel(gridCoord);
  }

  /**
   * Convert world coordinates to hexagonal coordinates
   */
  worldToHex(coord: GridCoordinate): { q: number; r: number; s: number } {
    const hexGrid = this.pixelToGrid(coord.x, coord.y);
    const q = hexGrid.col ?? 0;
    const r = hexGrid.row ?? 0;
    const s = -q - r;
    return { q, r, s };
  }

  /**
   * Calculate distance between hexagonal coordinates
   */
  hexDistance(hex1: { q: number; r: number; s: number }, hex2: { q: number; r: number; s: number }): number {
    return (Math.abs(hex1.q - hex2.q) + Math.abs(hex1.q + hex1.r - hex2.q - hex2.r) + Math.abs(hex1.r - hex2.r)) / 2;
  }

  /**
   * Get hexagonal neighbors
   */
  getHexNeighbors(center: { q: number; r: number; s: number }): Array<{ q: number; r: number; s: number }> {
    const directions = [
      { q: 1, r: 0, s: -1 }, { q: 1, r: -1, s: 0 }, { q: 0, r: -1, s: 1 },
      { q: -1, r: 0, s: 1 }, { q: -1, r: 1, s: 0 }, { q: 0, r: 1, s: -1 }
    ];

    return directions.map(dir => ({
      q: center.q + dir.q,
      r: center.r + dir.r,
      s: center.s + dir.s
    }));
  }

  /**
   * Event system - add event listener
   */
  on(_event: string, _callback: (data: any) => void): void {
    if (!this.eventListeners.has(event)) {
      this.eventListeners.set(event, []);
    }
    this.eventListeners.get(event)!.push(callback);
  }

  /**
   * Event system - remove event listener
   */
  off(_event: string, _callback: (data: any) => void): void {
    const listeners = this.eventListeners.get(event);
    if (listeners) {
      const index = listeners.indexOf(callback);
      if (index !== -1) {
        listeners.splice(index, 1);
      }
    }
  }

  /**
   * Event system - emit event
   */
  private emit(event: string, data?: any): void {
    const listeners = this.eventListeners.get(event);
    if (listeners) {
      listeners.forEach(callback => callback(data));
    }
  }

  /**
   * Update grid settings (main implementation)
   */
  updateSettings(newSettings: Partial<GridSettings>): void {
    const oldSettings = { ...this.settings };
    this.settings = { ...this.settings, ...newSettings };
    this.emit('settingsChanged', { old: oldSettings, new: this.settings });
  }
}

export interface ModManifest {
  id: string;
  name: string;
  version: string;
  author: string;
  description: string;
  gameVersion: string;
  dependencies: ModDependency[];
  conflicts: string[];
  assets: ModAsset[];
  scripts: string[];
  hooks: ModHook[];
  config: ModConfig[];
}

export interface ModDependency {
  id: string;
  version: string;
  optional: boolean;
}

export interface ModAsset {
  id: string;
  type: 'texture' | 'model' | 'audio' | 'data' | 'shader';
  path: string;
  override?: string; // Original asset to override
}

export interface ModHook {
  event: string;
  script: string;
  priority: number;
}

export interface ModConfig {
  key: string;
  type: 'string' | 'number' | 'boolean' | 'select' | 'range';
  default: any;
  options?: any[];
  min?: number;
  max?: number;
  description: string;
}

export interface ModInstance {
  manifest: ModManifest;
  isEnabled: boolean;
  isLoaded: boolean;
  loadOrder: number;
  configValues: Map<string, any>;
  assetOverrides: Map<string, string>;
  scriptInstances: Map<string, any>;
  lastError?: Error;
}

export interface ModLoadResult {
  success: boolean;
  mod?: ModInstance;
  errors: string[];
  warnings: string[];
}

export class ModdingFramework {
  private mods = new Map<string, ModInstance>();
  private loadOrder: string[] = [];
  private assetRegistry = new Map<string, ModAsset>();
  private configStorage = new Map<string, Map<string, any>>();

  // Dependencies
  private scriptingEngine: any; // ScriptingEngine
  private assetManager: any; // AssetManager

  // Statistics
  private stats = {
    totalMods: 0,
    enabledMods: 0,
    totalAssets: 0,
    totalOverrides: 0,
    loadErrors: 0
  };

  constructor(scriptingEngine: any, assetManager: any) {
    this.scriptingEngine = scriptingEngine;
    this.assetManager = assetManager;
  }

  public async loadMod(manifest: ModManifest, modPath: string): Promise<ModLoadResult> {
    const errors: string[] = [];
    const warnings: string[] = [];

    try {
      // Validate manifest
      const validationResult = this.validateManifest(manifest);
      if (!validationResult.valid) {
        errors.push(...validationResult.errors);
        warnings.push(...validationResult.warnings);

        if (errors.length > 0) {
          return { success: false, errors, warnings };
        }
      }

      // Check dependencies
      const depResult = this.checkDependencies(manifest.dependencies);
      if (!depResult.satisfied) {
        errors.push(...depResult.missing.map(dep => `Missing dependency: ${dep.id} ${dep.version}`));
      }

      // Check conflicts
      const conflictResult = this.checkConflicts(manifest.conflicts);
      if (conflictResult.hasConflicts) {
        errors.push(...conflictResult.conflicts.map(mod => `Conflicts with: ${mod}`));
      }

      if (errors.length > 0) {
        return { success: false, errors, warnings };
      }

      // Create mod instance
      const modInstance: ModInstance = {
        manifest,
        isEnabled: false,
        isLoaded: false,
        loadOrder: this.generateLoadOrder(manifest),
        configValues: new Map(),
        assetOverrides: new Map(),
        scriptInstances: new Map(),
      };

      // Load configuration
      await this.loadModConfig(modInstance, modPath);

      // Load assets
      await this.loadModAssets(modInstance, modPath);

      // Load scripts
      await this.loadModScripts(modInstance, modPath);

      // Register mod
      this.mods.set(manifest.id, modInstance);
      this.insertInLoadOrder(manifest.id, modInstance.loadOrder);

      modInstance.isLoaded = true;
      this.stats.totalMods++;

      return {
        success: true,
        mod: modInstance,
        errors,
        warnings
      };

    } catch (error) {
      errors.push(`Failed to load mod: ${error}`);
      this.stats.loadErrors++;

      return { success: false, errors, warnings };
    }
  }

  public unloadMod(modId: string): boolean {
    const mod = this.mods.get(modId);
    if (!mod) return false;

    try {
      // Disable if enabled
      if (mod.isEnabled) {
        this.disableMod(modId);
      }

      // Unload scripts
      for (const scriptId of mod.scriptInstances.keys()) {
        this.scriptingEngine.unloadScript(scriptId);
      }

      // Remove asset overrides
      for (const [originalAsset, _] of mod.assetOverrides) {
        this.assetManager.removeOverride(originalAsset);
      }

      // Remove from registries
      this.mods.delete(modId);
      this.loadOrder = this.loadOrder.filter(id => id !== modId);

      // Clean up asset registry
      for (const [assetId, asset] of this.assetRegistry) {
        if (asset.path.startsWith(`mods/${modId}/`)) {
          this.assetRegistry.delete(assetId);
        }
      }

      this.stats.totalMods--;
      return true;

    } catch (error) {
      console.error(`Failed to unload mod ${modId}:`, error);
      return false;
    }
  }

  public enableMod(modId: string): boolean {
    const mod = this.mods.get(modId);
    if (!mod || !mod.isLoaded || mod.isEnabled) return false;

    try {
      // Check dependencies are enabled
      for (const dep of mod.manifest.dependencies) {
        if (!dep.optional) {
          const depMod = this.mods.get(dep.id);
          if (!depMod || !depMod.isEnabled) {
            throw new Error(`Dependency not enabled: ${dep.id}`);
          }
        }
      }

      // Apply asset overrides
      for (const asset of mod.manifest.assets) {
        if (asset.override) {
          this.assetManager.addOverride(asset.override, `mods/${modId}/${asset.path}`);
          mod.assetOverrides.set(asset.override, `mods/${modId}/${asset.path}`);
        }
      }

      // Activate scripts
      for (const scriptId of mod.scriptInstances.keys()) {
        this.scriptingEngine.activateScript(scriptId);
      }

      mod.isEnabled = true;
      this.stats.enabledMods++;

      return true;

    } catch (error) {
      console.error(`Failed to enable mod ${modId}:`, error);
      mod.lastError = error instanceof Error ? error : new Error(String(error));
      return false;
    }
  }

  public disableMod(modId: string): boolean {
    const mod = this.mods.get(modId);
    if (!mod || !mod.isEnabled) return false;

    try {
      // Check if other mods depend on this one
      const dependents = this.findDependents(modId);
      for (const dependent of dependents) {
        const depMod = this.mods.get(dependent);
        if (depMod && depMod.isEnabled) {
          throw new Error(`Cannot disable: ${dependent} depends on this mod`);
        }
      }

      // Deactivate scripts
      for (const scriptId of mod.scriptInstances.keys()) {
        this.scriptingEngine.deactivateScript(scriptId);
      }

      // Remove asset overrides
      for (const [originalAsset, _] of mod.assetOverrides) {
        this.assetManager.removeOverride(originalAsset);
      }
      mod.assetOverrides.clear();

      mod.isEnabled = false;
      this.stats.enabledMods--;

      return true;

    } catch (error) {
      console.error(`Failed to disable mod ${modId}:`, error);
      mod.lastError = error instanceof Error ? error : new Error(String(error));
      return false;
    }
  }

  private validateManifest(manifest: ModManifest): { valid: boolean; errors: string[]; warnings: string[] } {
    const errors: string[] = [];
    const warnings: string[] = [];

    // Required fields
    const requiredFields = ['id', 'name', 'version', 'author', 'gameVersion'];
    for (const field of requiredFields) {
      if (!manifest[field as keyof ModManifest]) {
        errors.push(`Missing required field: ${field}`);
      }
    }

    // Version format
    if (manifest.version && !/^\d+\.\d+\.\d+(-[\w.]+)?$/.test(manifest.version)) {
      errors.push('Invalid version format. Use semantic versioning (e.g., 1.0.0)');
    }

    // Game version compatibility
    if (manifest.gameVersion && !this.isVersionCompatible(manifest.gameVersion)) {
      warnings.push(`Mod may not be compatible with current game version`);
    }

    // ID format
    if (manifest.id && !/^[a-z][a-z0-9_-]*$/.test(manifest.id)) {
      errors.push('Invalid mod ID format. Use lowercase letters, numbers, underscores, and hyphens only');
    }

    // Check for duplicate assets
    const assetIds = new Set<string>();
    for (const asset of manifest.assets || []) {
      if (assetIds.has(asset.id)) {
        errors.push(`Duplicate asset ID: ${asset.id}`);
      }
      assetIds.add(asset.id);
    }

    return {
      valid: errors.length === 0,
      errors,
      warnings
    };
  }

  private checkDependencies(dependencies: ModDependency[]): { satisfied: boolean; missing: ModDependency[] } {
    const missing: ModDependency[] = [];

    for (const dep of dependencies) {
      const depMod = this.mods.get(dep.id);

      if (!depMod) {
        if (!dep.optional) {
          missing.push(dep);
        }
        continue;
      }

      // Check version compatibility
      if (!this.isVersionCompatible(dep.version, depMod.manifest.version)) {
        missing.push(dep);
      }
    }

    return {
      satisfied: missing.length === 0,
      missing
    };
  }

  private checkConflicts(conflicts: string[]): { hasConflicts: boolean; conflicts: string[] } {
    const foundConflicts: string[] = [];

    for (const conflictId of conflicts) {
      if (this.mods.has(conflictId)) {
        foundConflicts.push(conflictId);
      }
    }

    return {
      hasConflicts: foundConflicts.length > 0,
      conflicts: foundConflicts
    };
  }

  private isVersionCompatible(required: string, actual?: string): boolean {
    if (!actual) return false;

    // Simple semver compatibility check
    const parseVersion = (v: string) => v.split('.').map(n => parseInt(n) || 0);

    const req = parseVersion(required);
    const act = parseVersion(actual);

    // Major version must match, minor and patch can be higher
    return req[0] === act[0] && 
           (act[1] > req[1] || (act[1] === req[1] && act[2] >= req[2]));
  }

  private generateLoadOrder(manifest: ModManifest): number {
    // Generate load order based on dependencies
    let order = 1000; // Default priority

    // Higher priority for mods with fewer dependencies
    order -= manifest.dependencies.length * 100;

    // Lower priority for mods that override core assets
    const coreOverrides = manifest.assets.filter(asset => 
      asset.override && asset.override.startsWith('core/')
    );
    order += coreOverrides.length * 50;

    return order;
  }

  private insertInLoadOrder(modId: string, order: number): void {
    // Insert mod in load order maintaining sort
    let insertIndex = 0;

    for (let i = 0; i < this.loadOrder.length; i++) {
      const existingMod = this.mods.get(this.loadOrder[i]);
      if (existingMod && existingMod.loadOrder <= order) {
        insertIndex = i + 1;
      } else {
        break;
      }
    }

    this.loadOrder.splice(insertIndex, 0, modId);
  }

  private findDependents(modId: string): string[] {
    const dependents: string[] = [];

    for (const [id, mod] of this.mods) {
      if (mod.manifest.dependencies.some(dep => dep.id === modId)) {
        dependents.push(id);
      }
    }

    return dependents;
  }

  private async loadModConfig(mod: ModInstance, _modPath: string): Promise<void> {
    // Load saved configuration
    const savedConfig = this.configStorage.get(mod.manifest.id) || new Map();

    // Apply default values and saved values
    for (const config of mod.manifest.config) {
      let value = savedConfig.get(config.key);

      if (value === undefined) {
        value = config.default;
      }

      // Validate value
      if (!this.validateConfigValue(config, value)) {
        value = config.default;
      }

      mod.configValues.set(config.key, value);
    }
  }

  private validateConfigValue(config: ModConfig, value: any): boolean {
    switch (config.type) {
      case 'string':
        return typeof value === 'string';
      case 'number':
        return typeof value === 'number' && 
               (config.min === undefined || value >= config.min) &&
               (config.max === undefined || value <= config.max);
      case 'boolean':
        return typeof value === 'boolean';
      case 'select':
        return config.options && config.options.includes(value);
      case 'range':
        return typeof value === 'number' &&
               config.min !== undefined && value >= config.min &&
               config.max !== undefined && value <= config.max;
      default:
        return true;
    }
  }

  private async loadModAssets(mod: ModInstance, modPath: string): Promise<void> {
    for (const asset of mod.manifest.assets) {
      const fullPath = `${modPath}/${asset.path}`;

      // Register asset with asset manager
      try {
        await this.assetManager.loadAsset(asset.id, fullPath, asset.type);
        this.assetRegistry.set(asset.id, asset);
        this.stats.totalAssets++;

        if (asset.override) {
          this.stats.totalOverrides++;
        }
      } catch (error) {
        console.warn(`Failed to load asset ${asset.id}:`, error);
      }
    }
  }

  private async loadModScripts(mod: ModInstance, modPath: string): Promise<void> {
    for (const scriptPath of mod.manifest.scripts) {
      try {
        // Load script file
        const fullPath = `${modPath}/${scriptPath}`;
        const scriptCode = await this.loadScriptFile(fullPath);

        // Create script manifest
        const scriptManifest = {
          id: `${mod.manifest.id}_${scriptPath}`,
          name: `${mod.manifest.name} Script`,
          version: mod.manifest.version,
          author: mod.manifest.author,
          description: `Script from mod: ${mod.manifest.name}`,
          dependencies: [],
          permissions: ['gamestate.read', 'entities.write', 'events.register'],
          entryPoint: scriptPath,
          files: [scriptPath],
          hooks: mod.manifest.hooks.map(hook => hook.event),
          commands: []
        };

        // Load script
        const success = await this.scriptingEngine.loadScript(scriptManifest, scriptCode);
        if (success) {
          mod.scriptInstances.set(scriptManifest.id, true);
        }

      } catch (error) {
        console.warn(`Failed to load script ${scriptPath}:`, error);
      }
    }
  }

  private async loadScriptFile(path: string): Promise<string> {
    // In a real implementation, this would read from the file system
    // For now, return empty script
    return `// Mod script: ${path}`;
  }

  // Public API
  public getMod(modId: string): ModInstance | undefined {
    return this.mods.get(modId);
  }

  public getAllMods(): ModInstance[] {
    return Array.from(this.mods.values());
  }

  public getEnabledMods(): ModInstance[] {
    return Array.from(this.mods.values()).filter(mod => mod.isEnabled);
  }

  public getLoadOrder(): string[] {
    return [...this.loadOrder];
  }

  public setModConfig(modId: string, key: string, value: any): boolean {
    const mod = this.mods.get(modId);
    if (!mod) return false;

    const config = mod.manifest.config.find(c => c.key === key);
    if (!config) return false;

    if (!this.validateConfigValue(config, value)) return false;

    mod.configValues.set(key, value);

    // Save to persistent storage
    let savedConfig = this.configStorage.get(modId);
    if (!savedConfig) {
      savedConfig = new Map();
      this.configStorage.set(modId, savedConfig);
    }
    savedConfig.set(key, value);

    return true;
  }

  public getModConfig(modId: string, key: string): any {
    const mod = this.mods.get(modId);
    if (!mod) return undefined;

    return mod.configValues.get(key);
  }

  public getStats() {
    return { ...this.stats };
  }

  public exportModList(): any {
    return {
      mods: Array.from(this.mods.values()).map(mod => ({
        id: mod.manifest.id,
        name: mod.manifest.name,
        version: mod.manifest.version,
        author: mod.manifest.author,
        enabled: mod.isEnabled,
        loadOrder: mod.loadOrder
      })),
      loadOrder: this.loadOrder,
      stats: this.stats
    };
  }

  public dispose(): void {
    // Disable all mods
    for (const modId of this.mods.keys()) {
      this.disableMod(modId);
    }

    // Unload all mods
    for (const modId of this.mods.keys()) {
      this.unloadMod(modId);
    }

    this.mods.clear();
    this.loadOrder = [];
    this.assetRegistry.clear();
    this.configStorage.clear();
  }
}

export interface ScriptContext {
  gameState: any;
  entities: Map<string, any>;
  events: ScriptEventSystem;
  api: GameAPI;
  storage: ScriptStorage;
  logger: ScriptLogger;
  permissions: ScriptPermissions;
}

export interface ScriptManifest {
  id: string;
  name: string;
  version: string;
  author: string;
  description: string;
  dependencies: string[];
  permissions: string[];
  entryPoint: string;
  files: string[];
  hooks: string[];
  commands: ScriptCommand[];
}

export interface ScriptCommand {
  name: string;
  description: string;
  usage: string;
  permission?: string;
}

export interface ScriptModule {
  manifest: ScriptManifest;
  code: string;
  instance?: any;
  isActive: boolean;
  loadTime: number;
  executionTime: number;
  errorCount: number;
  lastError?: Error;
}

export interface ScriptPermissions {
  canAccessGameState: boolean;
  canModifyEntities: boolean;
  canRegisterEvents: boolean;
  canAccessNetwork: boolean;
  canAccessFileSystem: boolean;
  canExecuteCommands: boolean;
  allowedAPIs: string[];
}

export class ScriptingEngine {
  private modules = new Map<string, ScriptModule>();
  private hooks = new Map<string, ScriptHook[]>();
  private commands = new Map<string, ScriptCommandHandler>();
  private eventSystem: ScriptEventSystem;
  private api: GameAPI;
  private storage: ScriptStorage;
  private logger: ScriptLogger;
  private sandboxes = new Map<string, ScriptSandbox>();

  // Security and performance
  private executionLimits = {
    maxExecutionTime: 1000, // ms
    maxMemoryUsage: 50 * 1024 * 1024, // 50MB
    maxAPICallsPerSecond: 1000,
    maxEventListeners: 100
  };

  private stats = {
    totalScripts: 0,
    activeScripts: 0,
    totalExecutions: 0,
    totalErrors: 0,
    averageExecutionTime: 0
  };

  constructor() {
    this.eventSystem = new ScriptEventSystem();
    this.api = new GameAPI();
    this.storage = new ScriptStorage();
    this.logger = new ScriptLogger();

    this.initializeDefaultHooks();
  }

  private initializeDefaultHooks(): void {
    // Core game hooks
    this.registerHook('game.start');
    this.registerHook('game.end');
    this.registerHook('game.tick');
    this.registerHook('game.pause');
    this.registerHook('game.resume');

    // Entity hooks
    this.registerHook('entity.create');
    this.registerHook('entity.update');
    this.registerHook('entity.delete');
    this.registerHook('entity.move');

    // Player hooks
    this.registerHook('player.join');
    this.registerHook('player.leave');
    this.registerHook('player.action');
    this.registerHook('player.chat');

    // Combat hooks
    this.registerHook('combat.start');
    this.registerHook('combat.end');
    this.registerHook('combat.turn');
    this.registerHook('combat.damage');
    this.registerHook('combat.heal');

    // UI hooks
    this.registerHook('ui.render');
    this.registerHook('ui.click');
    this.registerHook('ui.hover');
  }

  public async loadScript(manifest: ScriptManifest, code: string): Promise<boolean> {
    try {
      // Validate manifest
      if (!this.validateManifest(manifest)) {
        throw new Error(`Invalid manifest for script ${manifest.id}`);
      }

      // Check dependencies
      if (!this.checkDependencies(manifest.dependencies)) {
        throw new Error(`Missing dependencies for script ${manifest.id}`);
      }

      // Create sandbox
      const sandbox = new ScriptSandbox(manifest.id, this.createScriptContext(manifest));
      this.sandboxes.set(manifest.id, sandbox);

      // Create module
      const module: ScriptModule = {
        manifest,
        code,
        isActive: false,
        loadTime: Date.now(),
        executionTime: 0,
        errorCount: 0
      };

      // Execute script in sandbox
      const startTime = performance.now();
      module.instance = await sandbox.execute(code);
      module.executionTime = performance.now() - startTime;

      this.modules.set(manifest.id, module);
      this.stats.totalScripts++;

      // Register hooks and commands
      this.registerScriptHooks(manifest);
      this.registerScriptCommands(manifest);

      this.logger.info(`Script loaded: ${manifest.name} v${manifest.version}`);
      return true;

    } catch (error) {
      this.logger.error(`Failed to load script ${manifest.id}:`, error);
      return false;
    }
  }

  public unloadScript(scriptId: string): boolean {
    const module = this.modules.get(scriptId);
    if (!module) return false;

    try {
      // Deactivate if active
      if (module.isActive) {
        this.deactivateScript(scriptId);
      }

      // Clean up hooks and commands
      this.unregisterScriptHooks(module.manifest);
      this.unregisterScriptCommands(module.manifest);

      // Clean up sandbox
      const sandbox = this.sandboxes.get(scriptId);
      if (sandbox) {
        sandbox.dispose();
        this.sandboxes.delete(scriptId);
      }

      // Remove module
      this.modules.delete(scriptId);
      this.stats.totalScripts--;

      this.logger.info(`Script unloaded: ${module.manifest.name}`);
      return true;

    } catch (error) {
      this.logger.error(`Failed to unload script ${scriptId}:`, error);
      return false;
    }
  }

  public activateScript(scriptId: string): boolean {
    const module = this.modules.get(scriptId);
    if (!module || module.isActive) return false;

    try {
      // Call onActivate if it exists
      if (module.instance && typeof module.instance.onActivate === 'function') {
        module.instance.onActivate();
      }

      module.isActive = true;
      this.stats.activeScripts++;

      this.logger.info(`Script activated: ${module.manifest.name}`);
      return true;

    } catch (error) {
      this.logger.error(`Failed to activate script ${scriptId}:`, error);
      module.errorCount++;
      module.lastError = error instanceof Error ? error : new Error(String(error));
      return false;
    }
  }

  public deactivateScript(scriptId: string): boolean {
    const module = this.modules.get(scriptId);
    if (!module || !module.isActive) return false;

    try {
      // Call onDeactivate if it exists
      if (module.instance && typeof module.instance.onDeactivate === 'function') {
        module.instance.onDeactivate();
      }

      module.isActive = false;
      this.stats.activeScripts--;

      this.logger.info(`Script deactivated: ${module.manifest.name}`);
      return true;

    } catch (error) {
      this.logger.error(`Failed to deactivate script ${scriptId}:`, error);
      module.errorCount++;
      module.lastError = error instanceof Error ? error : new Error(String(error));
      return false;
    }
  }

  private createScriptContext(manifest: ScriptManifest): ScriptContext {
    const permissions = this.createPermissions(manifest.permissions);

    return {
      gameState: permissions.canAccessGameState ? this.api.getGameState() : Record<string, any>,
      entities: permissions.canModifyEntities ? this.api.getEntities() : new Map(),
      events: this.eventSystem,
      api: this.createRestrictedAPI(permissions),
      storage: this.storage.createNamespace(manifest.id),
      logger: this.logger.createNamespace(manifest.id),
      permissions
    };
  }

  private createPermissions(requestedPermissions: string[]): ScriptPermissions {
    // This would normally check against user-granted permissions
    // For now, we'll grant based on requested permissions

    return {
      canAccessGameState: requestedPermissions.includes('gamestate.read'),
      canModifyEntities: requestedPermissions.includes('entities.write'),
      canRegisterEvents: requestedPermissions.includes('events.register'),
      canAccessNetwork: requestedPermissions.includes('network.access'),
      canAccessFileSystem: requestedPermissions.includes('fs.access'),
      canExecuteCommands: requestedPermissions.includes('commands.execute'),
      allowedAPIs: requestedPermissions.filter(p => p.startsWith('api.'))
    };
  }

  private createRestrictedAPI(permissions: ScriptPermissions): GameAPI {
    return new RestrictedGameAPI(this.api, permissions);
  }

  private validateManifest(manifest: ScriptManifest): boolean {
    const required = ['id', 'name', 'version', 'author', 'entryPoint'];

    for (const field of required) {
      if (!manifest[field as keyof ScriptManifest]) {
        return false;
      }
    }

    // Validate version format
    if (!/^\d+\.\d+\.\d+$/.test(manifest.version)) {
      return false;
    }

    // Validate ID format
    if (!/^[a-z][a-z0-9_-]*$/.test(manifest.id)) {
      return false;
    }

    return true;
  }

  private checkDependencies(dependencies: string[]): boolean {
    for (const dep of dependencies) {
      if (!this.modules.has(dep)) {
        return false;
      }
    }
    return true;
  }

  private registerScriptHooks(manifest: ScriptManifest): void {
    for (const hookName of manifest.hooks) {
      if (!this.hooks.has(hookName)) {
        this.hooks.set(hookName, []);
      }

      this.hooks.get(hookName)!.push({_scriptId: manifest.id, _callback: (data: any) => this.executeHook(manifest.id, _hookName, _data)});
    }
  }

  private unregisterScriptHooks(manifest: ScriptManifest): void {
    for (const hookName of manifest.hooks) {
      const hooks = this.hooks.get(hookName);
      if (hooks) {
        const filtered = hooks.filter(hook => hook.scriptId !== manifest.id);
        if (filtered.length === 0) {
          this.hooks.delete(hookName);
        } else {
          this.hooks.set(hookName, filtered);
        }
      }
    }
  }

  private registerScriptCommands(manifest: ScriptManifest): void {
    for (const command of manifest.commands) {
      this.commands.set(_command.name, _{
        scriptId: manifest.id,
        command,
        _handler: (args: string[], _context: any) => this.executeCommand(manifest.id, command.name, args, context)
      });
    }
  }

  private unregisterScriptCommands(manifest: ScriptManifest): void {
    for (const command of manifest.commands) {
      this.commands.delete(command.name);
    }
  }

  public async executeHook(hookName: string, data: any = {}): Promise<void> {
    const hookList = this.hooks.get(hookName);
    if (!hookList) return;

    for (const hook of hookList) {
      try {
        const startTime = performance.now();

        await this.withTimeout(
          hook.callback(data),
          this.executionLimits.maxExecutionTime
        );

        const executionTime = performance.now() - startTime;
        this.updateExecutionStats(hook.scriptId, executionTime);

      } catch (error) {
        this.handleScriptError(hook.scriptId, error);
      }
    }
  }

  private async executeHook(scriptId: string, hookName: string, data: any): Promise<void> {
    const module = this.modules.get(scriptId);
    if (!module || !module.isActive || !module.instance) return;

    const hookMethod = `on${hookName.charAt(0).toUpperCase() + hookName.slice(1).replace('.', '')}`;

    if (typeof module.instance[hookMethod] === 'function') {
      await module.instance[hookMethod](data);
    }
  }

  public async executeCommand(commandName: string, args: string[], context: any = {}): Promise<string> {
    const commandHandler = this.commands.get(commandName);
    if (!commandHandler) {
      throw new Error(`Command not found: ${commandName}`);
    }

    try {
      const startTime = performance.now();

      const result = await this.withTimeout(
        commandHandler.handler(args, context),
        this.executionLimits.maxExecutionTime
      );

      const executionTime = performance.now() - startTime;
      this.updateExecutionStats(commandHandler.scriptId, executionTime);

      return result || 'Command executed successfully';

    } catch (error) {
      this.handleScriptError(commandHandler.scriptId, error);
      throw error;
    }
  }

  private async executeCommand(scriptId: string, commandName: string, args: string[], context: any): Promise<string> {
    const module = this.modules.get(scriptId);
    if (!module || !module.isActive || !module.instance) {
      throw new Error(`Script not active: ${scriptId}`);
    }

    const commandMethod = `command${commandName.charAt(0).toUpperCase() + commandName.slice(1)}`;

    if (typeof module.instance[commandMethod] === 'function') {
      return await module.instance[commandMethod](args, context);
    }

    throw new Error(`Command method not found: ${commandMethod}`);
  }

  private registerHook(hookName: string): void {
    if (!this.hooks.has(hookName)) {
      this.hooks.set(hookName, []);
    }
  }

  private async withTimeout<T>(promise: Promise<T>, timeoutMs: number): Promise<T> {
    return Promise.race([_
      promise,
      _new Promise<T>((_, _reject) => 
        setTimeout(() => reject(new Error('Script execution timeout')), timeoutMs)
      )
    ]);
  }

  private updateExecutionStats(scriptId: string, executionTime: number): void {
    const module = this.modules.get(scriptId);
    if (module) {
      module.executionTime += executionTime;
    }

    this.stats.totalExecutions++;
    this.stats.averageExecutionTime = 
      (this.stats.averageExecutionTime * (this.stats.totalExecutions - 1) + executionTime) / 
      this.stats.totalExecutions;
  }

  private handleScriptError(scriptId: string, error: any): void {
    const module = this.modules.get(scriptId);
    if (module) {
      module.errorCount++;
      module.lastError = error instanceof Error ? error : new Error(String(error));

      // Deactivate script if too many errors
      if (module.errorCount >= 10) {
        this.deactivateScript(scriptId);
        this.logger.warn(`Script deactivated due to errors: ${module.manifest.name}`);
      }
    }

    this.stats.totalErrors++;
    this.logger.error(`Script error in ${scriptId}:`, error);
  }

  // Public API methods
  public getLoadedScripts(): ScriptModule[] {
    return Array.from(this.modules.values());
  }

  public getActiveScripts(): ScriptModule[] {
    return Array.from(this.modules.values()).filter(module => module.isActive);
  }

  public getScript(scriptId: string): ScriptModule | undefined {
    return this.modules.get(scriptId);
  }

  public getAvailableHooks(): string[] {
    return Array.from(this.hooks.keys());
  }

  public getAvailableCommands(): string[] {
    return Array.from(this.commands.keys());
  }

  public getStats() {
    return { ...this.stats };
  }

  public getScriptStats(scriptId: string) {
    const module = this.modules.get(scriptId);
    if (!module) return null;

    return {
      id: module.manifest.id,
      name: module.manifest.name,
      version: module.manifest.version,
      isActive: module.isActive,
      loadTime: module.loadTime,
      executionTime: module.executionTime,
      errorCount: module.errorCount,
      lastError: module.lastError?.message
    };
  }

  public dispose(): void {
    // Unload all scripts
    for (const scriptId of this.modules.keys()) {
      this.unloadScript(scriptId);
    }

    // Clean up sandboxes
    for (const sandbox of this.sandboxes.values()) {
      sandbox.dispose();
    }

    this.modules.clear();
    this.hooks.clear();
    this.commands.clear();
    this.sandboxes.clear();
  }
}

// Supporting classes
interface ScriptHook {
  scriptId: string;
  callback: (data: any) => Promise<void>;
}

interface ScriptCommandHandler {
  scriptId: string;
  command: ScriptCommand;
  handler: (_args: string[], _context: any) => Promise<string>;
}

export class ScriptEventSystem {
  private listeners = new Map<string, Array<{ scriptId: string; callback: (_...args: any[]) => any }>>();

  public on(_event: string, _callback: (...args: any[]) => any, scriptId: string): void {
    if (!this.listeners.has(event)) {
      this.listeners.set(event, []);
    }

    this.listeners.get(event)!.push({ scriptId, callback });
  }

  public off(event: string, scriptId: string): void {
    const listeners = this.listeners.get(event);
    if (listeners) {
      const filtered = listeners.filter(listener => listener.scriptId !== scriptId);
      if (filtered.length === 0) {
        this.listeners.delete(event);
      } else {
        this.listeners.set(event, filtered);
      }
    }
  }

  public emit(event: string, data?: any): void {
    const listeners = this.listeners.get(event);
    if (listeners) {
      for (const listener of listeners) {
        try {
          listener.callback(data);
        } catch (error) {
          console.error(`Event listener error in script ${listener.scriptId}:`, error);
        }
      }
    }
  }

  public removeScriptListeners(scriptId: string): void {
    for (const [event, listeners] of this.listeners) {
      const filtered = listeners.filter(listener => listener.scriptId !== scriptId);
      if (filtered.length === 0) {
        this.listeners.delete(event);
      } else {
        this.listeners.set(event, filtered);
      }
    }
  }
}

