/**
 * Authentication & Authorization System
 * JWT-based authentication with role-based access control and session management
 */

import jwt from 'jsonwebtoken';
import bcrypt from 'bcrypt';
import { EventEmitter } from 'events';
import { v4 as uuidv4 } from 'uuid';

export interface User {
  id: string;
  email: string;
  username: string;
  passwordHash: string;
  roles: string[];
  permissions: string[];
  isActive: boolean;
  emailVerified: boolean;
  lastLogin?: Date;
  createdAt: Date;
  updatedAt: Date;
  metadata?: Record<string, any>;
}

export interface AuthToken {
  accessToken: string;
  refreshToken: string;
  expiresIn: number;
  tokenType: 'Bearer';
}

export interface AuthSession {
  id: string;
  userId: string;
  token: string;
  refreshToken: string;
  expiresAt: Date;
  createdAt: Date;
  ipAddress?: string;
  userAgent?: string;
  isActive: boolean;
}

export interface AuthConfig {
  jwtSecret: string;
  jwtRefreshSecret: string;
  accessTokenExpiry: string; // e.g., '15m', '1h'
  refreshTokenExpiry: string; // e.g., '7d', '30d'
  bcryptRounds: number;
  maxSessions: number;
  requireEmailVerification: boolean;
}

export interface LoginCredentials {
  email: string;
  password: string;
  rememberMe?: boolean;
  ipAddress?: string;
  userAgent?: string;
}

export interface RegisterData {
  email: string;
  username: string;
  password: string;
  acceptTerms: boolean;
  ipAddress?: string;
}

export interface AuthResult {
  success: boolean;
  user?: User;
  token?: AuthToken;
  session?: AuthSession;
  error?: string;
  code?: string;
}

export class AuthenticationManager extends EventEmitter {
  private config: AuthConfig;
  private users = new Map<string, User>();
  private sessions = new Map<string, AuthSession>();
  private userSessions = new Map<string, Set<string>>(); // userId -> sessionIds
  private blacklistedTokens = new Set<string>();
  private refreshTokens = new Map<string, string>(); // refreshToken -> sessionId

  constructor(config: AuthConfig) {
    super();
    this.config = config;
  }

  /**
   * Register a new user
   */
  async register(data: RegisterData): Promise<AuthResult> {
    try {
      // Check if user already exists
      const existingUser = Array.from(this.users.values())
        .find(u => u.email === data.email || u.username === data.username);

      if (existingUser) {
        return {
          success: false,
          error: existingUser.email === data.email ? 'Email already registered' : 'Username already taken',
          code: 'USER_EXISTS',
        };
      }

      // Hash password
      const passwordHash = await bcrypt.hash(data.password, this.config.bcryptRounds);

      // Create user
      const user: User = {
        id: uuidv4(),
        email: data.email,
        username: data.username,
        passwordHash,
        roles: ['user'], // Default role
        permissions: ['read:own', 'write:own'],
        isActive: true,
        emailVerified: !this.config.requireEmailVerification,
        createdAt: new Date(),
        updatedAt: new Date(),
      };

      this.users.set(user.id, user);

      this.emit('userRegistered', { user: this.sanitizeUser(user), ipAddress: data.ipAddress });

      // Auto-login if email verification not required
      if (!this.config.requireEmailVerification) {
        const loginResult = await this.login({
          email: data.email,
          password: data.password,
          ipAddress: data.ipAddress,
        });

        return loginResult;
      }

      return {
        success: true,
        user: this.sanitizeUser(user),
      };
    } catch (_error) {
      return {
        success: false,
        error: 'Registration failed',
        code: 'REGISTRATION_ERROR',
      };
    }
  }

  /**
   * Authenticate user and create session
   */
  async login(credentials: LoginCredentials): Promise<AuthResult> {
    try {
      // Find user by email
      const user = Array.from(this.users.values())
        .find(u => u.email === credentials.email);

      if (!user) {
        this.emit('loginFailed', { 
          email: credentials.email, 
          reason: 'user_not_found',
          ipAddress: credentials.ipAddress 
        });
        return {
          success: false,
          error: 'Invalid credentials',
          code: 'INVALID_CREDENTIALS',
        };
      }

      // Check if user is active
      if (!user.isActive) {
        this.emit('loginFailed', { 
          userId: user.id, 
          reason: 'account_inactive',
          ipAddress: credentials.ipAddress 
        });
        return {
          success: false,
          error: 'Account is inactive',
          code: 'ACCOUNT_INACTIVE',
        };
      }

      // Check email verification if required
      if (this.config.requireEmailVerification && !user.emailVerified) {
        return {
          success: false,
          error: 'Email not verified',
          code: 'EMAIL_NOT_VERIFIED',
        };
      }

      // Verify password
      const isValidPassword = await bcrypt.compare(credentials.password, user.passwordHash);
      if (!isValidPassword) {
        this.emit('loginFailed', { 
          userId: user.id, 
          reason: 'invalid_password',
          ipAddress: credentials.ipAddress 
        });
        return {
          success: false,
          error: 'Invalid credentials',
          code: 'INVALID_CREDENTIALS',
        };
      }

      // Create session
      const session = await this.createSession(user, credentials);

      // Generate tokens
      const token = this.generateTokens(user, session);

      // Update user last login
      user.lastLogin = new Date();
      user.updatedAt = new Date();

      this.emit('loginSuccess', { 
        user: this.sanitizeUser(user), 
        session: session,
        ipAddress: credentials.ipAddress 
      });

      return {
        success: true,
        user: this.sanitizeUser(user),
        token,
        session,
      };
    } catch (error) {
      this.emit('loginError', { error, credentials: { email: credentials.email } });
      return {
        success: false,
        error: 'Login failed',
        code: 'LOGIN_ERROR',
      };
    }
  }

  /**
   * Logout user and invalidate session
   */
  async logout(token: string): Promise<{ success: boolean; error?: string }> {
    try {
      const decoded = this.verifyAccessToken(token);
      if (!decoded.valid || !decoded.payload) {
        return { success: false, error: 'Invalid token' };
      }

      const sessionId = decoded.payload.sessionId;
      const session = this.sessions.get(sessionId);

      if (session) {
        this.invalidateSession(sessionId);
        this.emit('logout', { userId: session.userId, sessionId });
      }

      this.blacklistedTokens.add(token);

      return { success: true };
    } catch (_error) {
      return { success: false, error: 'Logout failed' };
    }
  }

  /**
   * Refresh access token using refresh token
   */
  async refreshToken(refreshToken: string): Promise<AuthResult> {
    try {
      const sessionId = this.refreshTokens.get(refreshToken);
      if (!sessionId) {
        return {
          success: false,
          error: 'Invalid refresh token',
          code: 'INVALID_REFRESH_TOKEN',
        };
      }

      const session = this.sessions.get(sessionId);
      if (!session || !session.isActive || session.expiresAt < new Date()) {
        this.refreshTokens.delete(refreshToken);
        return {
          success: false,
          error: 'Session expired',
          code: 'SESSION_EXPIRED',
        };
      }

      const user = this.users.get(session.userId);
      if (!user || !user.isActive) {
        this.invalidateSession(sessionId);
        return {
          success: false,
          error: 'User not found or inactive',
          code: 'USER_INACTIVE',
        };
      }

      // Generate new tokens
      const newTokens = this.generateTokens(user, session);

      // Update session with new refresh token
      this.refreshTokens.delete(refreshToken);
      this.refreshTokens.set(newTokens.refreshToken, sessionId);

      this.emit('tokenRefreshed', { userId: user.id, sessionId });

      return {
        success: true,
        user: this.sanitizeUser(user),
        token: newTokens,
        session,
      };
    } catch (_error) {
      return {
        success: false,
        error: 'Token refresh failed',
        code: 'REFRESH_ERROR',
      };
    }
  }

  /**
   * Verify access token
   */
  verifyAccessToken(token: string): { valid: boolean; payload?: any; error?: string } {
    try {
      if (this.blacklistedTokens.has(token)) {
        return { valid: false, error: 'Token blacklisted' };
      }

      const payload = jwt.verify(token, this.config.jwtSecret) as any;

      // Check if session is still active
      const session = this.sessions.get(payload.sessionId);
      if (!session || !session.isActive) {
        return { valid: false, error: 'Session inactive' };
      }

      return { valid: true, payload };
    } catch (_error) {
      return { valid: false, error: 'Invalid token' };
    }
  }

  /**
   * Check if user has permission
   */
  hasPermission(userId: string, permission: string): boolean {
    const user = this.users.get(userId);
    if (!user || !user.isActive) return false;

    // Check direct permissions
    if (user.permissions.includes(permission)) return true;

    // Check role-based permissions
    return this.checkRolePermissions(user.roles, permission);
  }

  /**
   * Check if user has role
   */
  hasRole(userId: string, role: string): boolean {
    const user = this.users.get(userId);
    return user?.roles.includes(role) || false;
  }

  /**
   * Add role to user
   */
  addRole(userId: string, role: string): boolean {
    const user = this.users.get(userId);
    if (!user) return false;

    if (!user.roles.includes(role)) {
      user.roles.push(role);
      user.updatedAt = new Date();
      this.emit('roleAdded', { userId, role });
    }

    return true;
  }

  /**
   * Remove role from user
   */
  removeRole(userId: string, role: string): boolean {
    const user = this.users.get(userId);
    if (!user) return false;

    const index = user.roles.indexOf(role);
    if (index > -1) {
      user.roles.splice(index, 1);
      user.updatedAt = new Date();
      this.emit('roleRemoved', { userId, role });
    }

    return true;
  }

  /**
   * Get user sessions
   */
  getUserSessions(userId: string): AuthSession[] {
    const sessionIds = this.userSessions.get(userId) || new Set();
    return Array.from(sessionIds)
      .map(id => this.sessions.get(id))
      .filter(session => session && session.isActive) as AuthSession[];
  }

  /**
   * Invalidate all user sessions except current
   */
  invalidateOtherSessions(userId: string, currentSessionId: string): number {
    const sessionIds = this.userSessions.get(userId) || new Set();
    let invalidatedCount = 0;

    for (const sessionId of sessionIds) {
      if (sessionId !== currentSessionId) {
        this.invalidateSession(sessionId);
        invalidatedCount++;
      }
    }

    this.emit('otherSessionsInvalidated', { userId, count: invalidatedCount });
    return invalidatedCount;
  }

  /**
   * Change user password
   */
  async changePassword(userId: string, oldPassword: string, newPassword: string): Promise<{ success: boolean; error?: string }> {
    try {
      const user = this.users.get(userId);
      if (!user) {
        return { success: false, error: 'User not found' };
      }

      // Verify old password
      const isValidPassword = await bcrypt.compare(oldPassword, user.passwordHash);
      if (!isValidPassword) {
        this.emit('passwordChangeFailed', { userId, reason: 'invalid_old_password' });
        return { success: false, error: 'Invalid current password' };
      }

      // Hash new password
      const newPasswordHash = await bcrypt.hash(newPassword, this.config.bcryptRounds);
      user.passwordHash = newPasswordHash;
      user.updatedAt = new Date();

      // Invalidate all sessions except current to force re-login
      this.invalidateAllUserSessions(userId);

      this.emit('passwordChanged', { userId });
      return { success: true };
    } catch (_error) {
      return { success: false, error: 'Password change failed' };
    }
  }

  private async createSession(user: User, credentials: LoginCredentials): Promise<AuthSession> {
    const sessionId = uuidv4();
    const refreshToken = uuidv4();

    const session: AuthSession = {
      id: sessionId,
      userId: user.id,
      token: '', // Will be set when generating JWT
      refreshToken,
      expiresAt: new Date(Date.now() + this.parseTimeToMs(this.config.refreshTokenExpiry)),
      createdAt: new Date(),
      ipAddress: credentials.ipAddress,
      userAgent: credentials.userAgent,
      isActive: true,
    };

    // Store session
    this.sessions.set(sessionId, session);
    this.refreshTokens.set(refreshToken, sessionId);

    // Track user sessions
    if (!this.userSessions.has(user.id)) {
      this.userSessions.set(user.id, new Set());
    }
    this.userSessions.get(user.id)!.add(sessionId);

    // Enforce max sessions limit
    this.enforceMaxSessions(user.id);

    return session;
  }

  private generateTokens(user: User, session: AuthSession): AuthToken {
    const accessTokenExpiry = this.parseTimeToMs(this.config.accessTokenExpiry);

    const accessToken = jwt.sign(
      {
        userId: user.id,
        sessionId: session.id,
        email: user.email,
        roles: user.roles,
        permissions: user.permissions,
      },
      this.config.jwtSecret,
      { expiresIn: this.config.accessTokenExpiry }
    );

    session.token = accessToken;

    return {
      accessToken,
      refreshToken: session.refreshToken,
      expiresIn: Math.floor(accessTokenExpiry / 1000),
      tokenType: 'Bearer',
    };
  }

  private invalidateSession(sessionId: string): void {
    const session = this.sessions.get(sessionId);
    if (!session) return;

    session.isActive = false;
    this.sessions.delete(sessionId);
    this.refreshTokens.delete(session.refreshToken);

    const userSessionIds = this.userSessions.get(session.userId);
    if (userSessionIds) {
      userSessionIds.delete(sessionId);
    }

    if (session.token) {
      this.blacklistedTokens.add(session.token);
    }
  }

  private invalidateAllUserSessions(userId: string): void {
    const sessionIds = this.userSessions.get(userId) || new Set();
    for (const sessionId of sessionIds) {
      this.invalidateSession(sessionId);
    }
    this.userSessions.delete(userId);
  }

  private enforceMaxSessions(userId: string): void {
    const sessionIds = this.userSessions.get(userId);
    if (!sessionIds || sessionIds.size <= this.config.maxSessions) return;

    // Get sessions sorted by creation time (oldest first)
    const sessions = Array.from(sessionIds)
      .map(id => this.sessions.get(id))
      .filter(session => session)
      .sort((_a, __b) => a!.createdAt.getTime() - b!.createdAt.getTime()) as AuthSession[];

    // Remove oldest sessions
    const toRemove = sessions.slice(0, sessions.length - this.config.maxSessions);
    for (const session of toRemove) {
      this.invalidateSession(session.id);
    }
  }

  private checkRolePermissions(roles: string[], permission: string): boolean {
    // Simple role-based permissions - in practice would be more sophisticated
    const rolePermissions: Record<string, string[]> = {
      admin: ['*'], // Admin has all permissions
      gm: ['read:*', 'write:*', 'manage:scenes', 'manage:tokens'],
      player: ['read:own', 'write:own', 'read:scenes', 'write:tokens'],
      user: ['read:own', 'write:own'],
    };

    for (const role of roles) {
      const permissions = rolePermissions[role] || [];
      if (permissions.includes('*') || permissions.includes(permission)) {
        return true;
      }

      // Check wildcard permissions
      if (permissions.some(p => p.endsWith('*') && permission.startsWith(p.slice(0, -1)))) {
        return true;
      }
    }

    return false;
  }

  private sanitizeUser(user: User): Omit<User, 'passwordHash'> {
    const { _passwordHash,  _...sanitized  } = user;
    return sanitized;
  }

  private parseTimeToMs(timeString: string): number {
    const units: Record<string, number> = {
      s: 1000,
      m: 60 * 1000,
      h: 60 * 60 * 1000,
      d: 24 * 60 * 60 * 1000,
    };

    const match = timeString.match(/^(\d+)([smhd])$/);
    if (!match) throw new Error(`Invalid time format: ${timeString}`);

    const [, amount, unit] = match;
    return parseInt(amount) * units[unit];
  }
}

/**
 * Input Validation System
 * Comprehensive input validation, sanitization, and schema enforcement
 */

import { z } from 'zod';

export interface ValidationRule {
  name: string;
  description: string;
  validator: (_value: any) => ValidationResult;
  severity: 'error' | 'warning' | 'info';
}

export interface ValidationResult {
  valid: boolean;
  errors: ValidationError[];
  warnings: ValidationWarning[];
  sanitized?: any;
}

export interface ValidationError {
  field: string;
  message: string;
  code: string;
  value?: any;
}

export interface ValidationWarning {
  field: string;
  message: string;
  code: string;
  value?: any;
}

export interface SanitizationOptions {
  stripHtml?: boolean;
  normalizeWhitespace?: boolean;
  trimStrings?: boolean;
  lowercaseEmails?: boolean;
  removeScripts?: boolean;
  maxLength?: number;
}

export class InputValidator {
  private rules = new Map<string, ValidationRule>();
  private schemas = new Map<string, z.ZodSchema>();

  constructor() {
    this.setupDefaultRules();
    this.setupDefaultSchemas();
  }

  /**
   * Validate input against a registered schema
   */
  validateSchema(schemaName: string, data: any): ValidationResult {
    const schema = this.schemas.get(schemaName);
    if (!schema) {
      return {
        valid: false,
        errors: [{ field: 'schema', message: `Schema not found: ${schemaName}`, code: 'SCHEMA_NOT_FOUND' }],
        warnings: [],
      };
    }

    try {
      const result = schema.parse(data);
      return {
        valid: true,
        errors: [],
        warnings: [],
        sanitized: result,
      };
    } catch (error) {
      if (error instanceof z.ZodError) {
        return {
          valid: false,
          errors: error.errors.map(err => ({
            field: err.path.join('.'),
            message: err.message,
            code: err.code,
            value: data,
          })),
          warnings: [],
        };
      }

      return {
        valid: false,
        errors: [{ field: 'unknown', message: 'Validation failed', code: 'VALIDATION_ERROR' }],
        warnings: [],
      };
    }
  }

  /**
   * Validate and sanitize user input
   */
  validateInput(
    data: Record<string, any>,
    schemaName?: string,
    options: SanitizationOptions = {}
  ): ValidationResult {
    // First sanitize the input
    const sanitized = this.sanitizeInput(data, options);

    // Then validate against schema if provided
    if (schemaName) {
      return this.validateSchema(schemaName, sanitized);
    }

    // Otherwise apply general validation rules
    return this.applyValidationRules(sanitized);
  }

  /**
   * Sanitize input data
   */
  sanitizeInput(data: any, options: SanitizationOptions = {}): any {
    if (typeof data === 'string') {
      return this.sanitizeString(data, options);
    }

    if (Array.isArray(data)) {
      return data.map(item => this.sanitizeInput(item, options));
    }

    if (typeof data === 'object' && data !== null) {
      const sanitized: Record<string, any> = {};
      for (const [key, value] of Object.entries(data)) {
        sanitized[key] = this.sanitizeInput(value, options);
      }
      return sanitized;
    }

    return data;
  }

  /**
   * Register a custom validation rule
   */
  registerRule(rule: ValidationRule): void {
    this.rules.set(rule.name, rule);
  }

  /**
   * Register a custom schema
   */
  registerSchema(name: string, schema: z.ZodSchema): void {
    this.schemas.set(name, schema);
  }

  /**
   * Validate file upload
   */
  validateFileUpload(file: {
    name: string;
    size: number;
    type: string;
    content?: ArrayBuffer;
  }): ValidationResult {
    const errors: ValidationError[] = [];
    const warnings: ValidationWarning[] = [];

    // File name validation
    if (!file.name || file.name.trim().length === 0) {
      errors.push({
        field: 'name',
        message: 'File name is required',
        code: 'REQUIRED',
      });
    } else if (file.name.length > 255) {
      errors.push({
        field: 'name',
        message: 'File name too long',
        code: 'MAX_LENGTH',
        value: file.name,
      });
    } else if (/[<>:"/\\|?*\x00-\x1f]/.test(file.name)) {
      errors.push({
        field: 'name',
        message: 'File name contains invalid characters',
        code: 'INVALID_CHARACTERS',
        value: file.name,
      });
    }

    // File size validation
    const maxFileSize = 50 * 1024 * 1024; // 50MB
    if (file.size > maxFileSize) {
      errors.push({
        field: 'size',
        message: `File too large (max: ${maxFileSize / 1024 / 1024}MB)`,
        code: 'FILE_TOO_LARGE',
        value: file.size,
      });
    }

    return {
      valid: errors.length === 0,
      errors,
      warnings,
    };
  }

  private sanitizeString(str: string, options: SanitizationOptions): string {
    let result = str;

    if (options.trimStrings !== false) {
      result = result.trim();
    }

    if (options.normalizeWhitespace) {
      result = result.replace(/\\s+/g, ' ');
    }

    if (options.stripHtml) {
      result = result.replace(/<[^>]*>/g, '');
    }

    if (options.removeScripts) {
      result = result.replace(/<script\b[^<]*(?:(?!</script>)<[^<]*)*</script>/gi, '');
      result = result.replace(/javascript:/gi, '');
      result = result.replace(/on\w+\s*=/gi, '');
    }

    if (options.lowercaseEmails && this.isEmail(result)) {
      result = result.toLowerCase();
    }

    if (options.maxLength && result.length > options.maxLength) {
      result = result.substring(0, options.maxLength);
    }

    return result;
  }

  private isEmail(str: string): boolean {
    return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(str);
  }

  private applyValidationRules(data: any): ValidationResult {
    const errors: ValidationError[] = [];
    const warnings: ValidationWarning[] = [];

    for (const rule of this.rules.values()) {
      try {
        const result = rule.validator(data);
        errors.push(...result.errors);
        warnings.push(...result.warnings);
      } catch (error) {
        errors.push({
          field: 'validation',
          message: `Rule "${rule.name}" failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
          code: 'RULE_ERROR',
        });
      }
    }

    return {
      valid: errors.length === 0,
      errors,
      warnings,
      sanitized: data,
    };
  }

  private setupDefaultRules(): void {
    // SQL Injection detection
    this.registerRule({_
      name: 'sql_injection',
      _description: 'Detects potential SQL injection attempts',
      _severity: 'error',
      _validator: (_data) => {
        const errors: ValidationError[] = [];
        const sqlPatterns = [
          /('|(\x27)|(\x2D)|(\x23)|\(|\*|;|<|>|\[|\]|\{|\}|\||\&)/i,
          /(select|union|insert|update|delete|drop|create|alter|exec|execute|script)/i,
        ];

        const checkString = (_str: string, _field: string) => {
          for (const pattern of sqlPatterns) {
            if (pattern.test(str)) {
              errors.push({
                field,
                message: 'Potential SQL injection detected',
                code: 'SQL_INJECTION',
                value: str,
              });
              break;
            }
          }
        };

        this.traverseObject(data, checkString);

        return { valid: errors.length === 0, errors, warnings: [] };
      },
    });

    // XSS detection
    this.registerRule({_name: 'xss_detection', _description: 'Detects potential XSS attacks', _severity: 'error', _validator: (_data) => {
        const errors: ValidationError[] = [];
        const xssPatterns = [
          /<script[^>]*>.*?</script>/gi, _/javascript:/gi, _/on\w+\s*=/gi, _/<iframe[^>]*>/gi, _/<object[^>]*>/gi, _];

        const checkString = (_str: string, _field: string) => {
          for (const pattern of xssPatterns) {
            if (pattern.test(str)) {
              errors.push({
                field, _message: 'Potential XSS attack detected', _code: 'XSS_DETECTED', _value: str, });
              break;
            }
          }
        };

        this.traverseObject(data, checkString);

        return { valid: errors.length === 0, errors, warnings: [] };
      },
    });
  }

  private setupDefaultSchemas(): void {
    // User authentication schemas
    this.registerSchema('user/login', z.object({
      email: z.string().email(),
      password: z.string().min(8),
      rememberMe: z.boolean().optional(),
    }));

    this.registerSchema('user/register', z.object({
      email: z.string().email(),
      password: z.string().min(8).max(128),
      username: z.string().min(3).max(30),
      acceptTerms: z.boolean().refine(val => val === true),
    }));

    // Scene management schemas
    this.registerSchema('scene/create', z.object({
      name: z.string().min(1).max(100),
      width: z.number().int().min(100).max(10000),
      height: z.number().int().min(100).max(10000),
      gridSize: z.number().int().min(5).max(200).optional(),
      backgroundColor: z.string().regex(/^#[0-9A-Fa-f]{6}$/).optional(),
    }));

    // Token management schemas
    this.registerSchema('token/create', z.object({
      name: z.string().min(1).max(50),
      x: z.number(),
      y: z.number(),
      size: z.number().min(0.1).max(5),
      color: z.string().regex(/^#[0-9A-Fa-f]{6}$/).optional(),
      imageUrl: z.string().url().optional(),
    }));
  }

  private traverseObject(obj: any, _callback: (str: string, _field: string) => void, path = ''): void {
    if (typeof obj === 'string') {
      callback(obj, path);
    } else if (Array.isArray(obj)) {
      obj.forEach((_item, __index) => {
        this.traverseObject(item, callback, `${path}[${index}]`);
      });
    } else if (typeof obj === 'object' && obj !== null) {
      for (const [key, value] of Object.entries(obj)) {
        const newPath = path ? `${path}.${key}` : key;
        this.traverseObject(value, callback, newPath);
      }
    }
  }
}

/**
 * Rate Limiting System
 * Advanced rate limiting with sliding windows, token buckets, and adaptive limits
 */

import { EventEmitter } from 'events';

export interface RateLimitConfig {
  windowMs: number; // Time window in milliseconds
  max: number; // Maximum requests per window
  keyGenerator?: (_identifier: string) => string;
  skipSuccessfulRequests?: boolean;
  skipFailedRequests?: boolean;
  resetTime?: Date;
  message?: string;
  standardHeaders?: boolean;
  legacyHeaders?: boolean;
}

export interface RateLimitInfo {
  totalRequests: number;
  remainingRequests: number;
  resetTime: Date;
  retryAfter?: number;
}

export interface RateLimitResult {
  allowed: boolean;
  info: RateLimitInfo;
  reason?: string;
}

export interface TokenBucketConfig {
  capacity: number; // Maximum tokens
  refillRate: number; // Tokens per second
  initialTokens?: number;
}

export interface AdaptiveRateLimitConfig {
  baseLimit: number;
  maxLimit: number;
  minLimit: number;
  adaptationFactor: number; // 0-1, how much to adapt based on system load
  loadThreshold: number; // 0-1, system load threshold for adaptation
}

export class RateLimiter extends EventEmitter {
  private windows = new Map<string, { count: number; resetTime: number }>();
  private config: RateLimitConfig;
  private cleanupInterval?: NodeJS.Timeout;

  constructor(config: RateLimitConfig) {
    super();
    this.config = config;
    this.startCleanup();
  }

  /**
   * Check if a request should be rate limited
   */
  checkLimit(identifier: string): RateLimitResult {
    const key = this.config.keyGenerator ? this.config.keyGenerator(identifier) : identifier;
    const now = Date.now();

    // Get or create window
    let window = this.windows.get(key);
    if (!window || now >= window.resetTime) {
      window = {
        count: 0,
        resetTime: now + this.config.windowMs,
      };
      this.windows.set(key, window);
    }

    const remainingRequests = Math.max(0, this.config.max - window.count);
    const allowed = window.count < this.config.max;

    if (allowed) {
      window.count++;
    }

    const info: RateLimitInfo = {
      totalRequests: window.count,
      remainingRequests: allowed ? remainingRequests - 1 : remainingRequests,
      resetTime: new Date(window.resetTime),
      retryAfter: allowed ? undefined : Math.ceil((window.resetTime - now) / 1000),
    };

    const result: RateLimitResult = {
      allowed,
      info,
      reason: allowed ? undefined : this.config.message || 'Rate limit exceeded',
    };

    this.emit(allowed ? 'request' : 'rateLimited', { key, identifier, result });

    return result;
  }

  /**
   * Reset rate limit for a specific identifier
   */
  reset(identifier: string): void {
    const key = this.config.keyGenerator ? this.config.keyGenerator(identifier) : identifier;
    this.windows.delete(key);
    this.emit('reset', { key, identifier });
  }

  /**
   * Get current statistics
   */
  getStats(): {
    activeWindows: number;
    totalRequests: number;
    rateLimitedRequests: number;
  } {
    let totalRequests = 0;
    let rateLimitedRequests = 0;

    for (const window of this.windows.values()) {
      totalRequests += window.count;
      if (window.count >= this.config.max) {
        rateLimitedRequests++;
      }
    }

    return {
      activeWindows: this.windows.size,
      totalRequests,
      rateLimitedRequests,
    };
  }

  /**
   * Update configuration
   */
  updateConfig(config: Partial<RateLimitConfig>): void {
    this.config = { ...this.config, ...config };
  }

  private startCleanup(): void {
    this.cleanupInterval = setInterval(() => {
      const now = Date.now();
      const keysToDelete: string[] = [];

      for (const [key, window] of this.windows) {
        if (now >= window.resetTime) {
          keysToDelete.push(key);
        }
      }

      for (const key of keysToDelete) {
        this.windows.delete(key);
      }

      this.emit('cleanup', { removedWindows: keysToDelete.length });
    }, this.config.windowMs / 2); // Cleanup twice per window
  }

  /**
   * Destroy rate limiter and cleanup resources
   */
  destroy(): void {
    if (this.cleanupInterval) {
      clearInterval(this.cleanupInterval);
      this.cleanupInterval = undefined;
    }
    this.windows.clear();
    this.removeAllListeners();
  }
}

/**
 * Token Bucket Rate Limiter
 */
export class TokenBucketRateLimiter extends EventEmitter {
  private buckets = new Map<string, { tokens: number; lastRefill: number }>();
  private config: TokenBucketConfig;
  private refillInterval?: NodeJS.Timeout;

  constructor(config: TokenBucketConfig) {
    super();
    this.config = config;
    this.startRefill();
  }

  /**
   * Try to consume tokens
   */
  consume(identifier: string, tokens = 1): RateLimitResult {
    let bucket = this.buckets.get(identifier);
    if (!bucket) {
      bucket = {
        tokens: this.config.initialTokens ?? this.config.capacity,
        lastRefill: Date.now(),
      };
      this.buckets.set(identifier, bucket);
    }

    const allowed = bucket.tokens >= tokens;
    if (allowed) {
      bucket.tokens -= tokens;
    }

    const result: RateLimitResult = {
      allowed,
      info: {
        totalRequests: this.config.capacity - bucket.tokens,
        remainingRequests: Math.floor(bucket.tokens),
        resetTime: new Date(Date.now() + (this.config.capacity - bucket.tokens) / this.config.refillRate * 1000),
        retryAfter: allowed ? undefined : Math.ceil((tokens - bucket.tokens) / this.config.refillRate),
      },
    };

    this.emit(allowed ? 'consumed' : 'rateLimited', { identifier, tokens, bucket: { ...bucket }, result });

    return result;
  }

  /**
   * Add tokens to a bucket
   */
  addTokens(identifier: string, tokens: number): void {
    let bucket = this.buckets.get(identifier);
    if (!bucket) {
      bucket = {
        tokens: this.config.initialTokens ?? this.config.capacity,
        lastRefill: Date.now(),
      };
      this.buckets.set(identifier, bucket);
    }

    bucket.tokens = Math.min(this.config.capacity, bucket.tokens + tokens);
    this.emit('tokensAdded', { identifier, tokens, newTotal: bucket.tokens });
  }

  private startRefill(): void {
    this.refillInterval = setInterval(() => {
      const now = Date.now();

      for (const [_identifier, bucket] of this.buckets) {
        const timeSinceRefill = (now - bucket.lastRefill) / 1000;
        const tokensToAdd = timeSinceRefill * this.config.refillRate;

        if (tokensToAdd >= 1) {
          bucket.tokens = Math.min(this.config.capacity, bucket.tokens + Math.floor(tokensToAdd));
          bucket.lastRefill = now;
        }
      }
    }, 1000); // Refill every second
  }

  /**
   * Destroy token bucket and cleanup resources
   */
  destroy(): void {
    if (this.refillInterval) {
      clearInterval(this.refillInterval);
      this.refillInterval = undefined;
    }
    this.buckets.clear();
    this.removeAllListeners();
  }
}

/**
 * Adaptive Rate Limiter
 */
export class AdaptiveRateLimiter extends RateLimiter {
  private adaptiveConfig: AdaptiveRateLimitConfig;
  private currentLimit: number;
  private systemLoadMonitor?: () => number; // Function that returns system load 0-1

  constructor(config: RateLimitConfig, adaptiveConfig: AdaptiveRateLimitConfig) {
    super(config);
    this.adaptiveConfig = adaptiveConfig;
    this.currentLimit = adaptiveConfig.baseLimit;
    this.startAdaptation();
  }

  /**
   * Set system load monitor function
   */
  setSystemLoadMonitor(_monitor: () => number): void {
    this.systemLoadMonitor = monitor;
  }

  /**
   * Check limit with adaptive adjustment
   */
  checkLimit(identifier: string): RateLimitResult {
    // Update config with current adaptive limit
    this.config.max = this.currentLimit;
    return super.checkLimit(identifier);
  }

  private startAdaptation(): void {
    setInterval(() => {
      if (!this.systemLoadMonitor) return;

      const systemLoad = this.systemLoadMonitor();

      if (systemLoad > this.adaptiveConfig.loadThreshold) {
        // High load - decrease limit
        const reduction = (systemLoad - this.adaptiveConfig.loadThreshold) * this.adaptiveConfig.adaptationFactor;
        this.currentLimit = Math.max(
          this.adaptiveConfig.minLimit,
          this.currentLimit - (this.adaptiveConfig.baseLimit * reduction)
        );
      } else {
        // Normal/low load - increase limit toward base
        const increase = (this.adaptiveConfig.loadThreshold - systemLoad) * this.adaptiveConfig.adaptationFactor * 0.5;
        this.currentLimit = Math.min(
          this.adaptiveConfig.maxLimit,
          this.currentLimit + (this.adaptiveConfig.baseLimit * increase)
        );
      }

      this.emit('limitAdapted', { 
        systemLoad, 
        oldLimit: this.config.max, 
        newLimit: this.currentLimit 
      });
    }, 5000); // Adapt every 5 seconds
  }
}

/**
 * Rate Limiter Manager
 */
export class RateLimiterManager {
  private limiters = new Map<string, RateLimiter>();
  private globalLimiter?: RateLimiter;

  /**
   * Add a rate limiter for a specific resource
   */
  addLimiter(name: string, limiter: RateLimiter): void {
    this.limiters.set(name, limiter);
  }

  /**
   * Set global rate limiter
   */
  setGlobalLimiter(limiter: RateLimiter): void {
    this.globalLimiter = limiter;
  }

  /**
   * Check all applicable rate limits
   */
  checkLimits(identifier: string, resource?: string): RateLimitResult[] {
    const results: RateLimitResult[] = [];

    // Check global limiter first
    if (this.globalLimiter) {
      results.push(this.globalLimiter.checkLimit(identifier));
    }

    // Check resource-specific limiter
    if (resource && this.limiters.has(resource)) {
      results.push(this.limiters.get(resource)!.checkLimit(identifier));
    }

    return results;
  }

  /**
   * Check if any rate limit is exceeded
   */
  isRateLimited(identifier: string, resource?: string): { limited: boolean; results: RateLimitResult[] } {
    const results = this.checkLimits(identifier, resource);
    const limited = results.some(result => !result.allowed);

    return { limited, results };
  }

  /**
   * Get combined statistics
   */
  getStats(): Record<string, any> {
    const stats: Record<string, any> = {};

    if (this.globalLimiter) {
      stats.global = this.globalLimiter.getStats();
    }

    for (const [name, limiter] of this.limiters) {
      stats[name] = limiter.getStats();
    }

    return stats;
  }

  /**
   * Reset all rate limits for an identifier
   */
  resetAll(identifier: string): void {
    if (this.globalLimiter) {
      this.globalLimiter.reset(identifier);
    }

    for (const limiter of this.limiters.values()) {
      limiter.reset(identifier);
    }
  }

  /**
   * Destroy all rate limiters
   */
  destroy(): void {
    if (this.globalLimiter) {
      this.globalLimiter.destroy();
    }

    for (const limiter of this.limiters.values()) {
      limiter.destroy();
    }

    this.limiters.clear();
  }
}

