/**
 * Threat Protection System
 * Advanced security monitoring, anomaly detection, and automated threat response
 */

import { EventEmitter } from 'events';

export interface SecurityEvent {
  id: string;
  type: ThreatType;
  severity: 'low' | 'medium' | 'high' | 'critical';
  source: string; // IP address, user ID, etc.
  description: string;
  metadata: Record<string, any>;
  timestamp: Date;
  resolved: boolean;
}

export type ThreatType = 
  | 'brute_force'
  | 'sql_injection'
  | 'xss_attempt'
  | 'suspicious_activity'
  | 'rate_limit_violation'
  | 'unauthorized_access'
  | 'malformed_request'
  | 'file_upload_threat'
  | 'session_hijacking'
  | 'csrf_attempt';

export interface ThreatRule {
  id: string;
  name: string;
  type: ThreatType;
  enabled: boolean;
  detector: (context: SecurityContext) => ThreatDetectionResult;
  threshold?: number;
  timeWindow?: number; // milliseconds
  actions: ThreatAction[];
}

export interface SecurityContext {
  request?: {
    ip: string;
    userAgent: string;
    path: string;
    method: string;
    headers: Record<string, string>;
    body?: any;
  };
  user?: {
    id: string;
    email: string;
    roles: string[];
  };
  session?: {
    id: string;
    createdAt: Date;
    lastActivity: Date;
  };
  metadata?: Record<string, any>;
}

export interface ThreatDetectionResult {
  threat: boolean;
  confidence: number; // 0-1
  description: string;
  evidence: Record<string, any>;
}

export interface ThreatAction {
  type: 'block' | 'limit' | 'alert' | 'log' | 'captcha' | 'quarantine';
  duration?: number; // milliseconds
  metadata?: Record<string, any>;
}

export interface SecurityMetrics {
  totalEvents: number;
  eventsByType: Record<ThreatType, number>;
  eventsBySeverity: Record<string, number>;
  blockedRequests: number;
  falsePositives: number;
  responseTime: number;
}

export class ThreatProtection extends EventEmitter {
  private rules = new Map<string, ThreatRule>();
  private events: SecurityEvent[] = [];
  private blockedIPs = new Set<string>();
  private suspiciousActivity = new Map<string, { count: number; lastSeen: Date }>();
  private quarantineList = new Set<string>();
  private maxEvents = 10000;

  constructor() {
    super();
    this.setupDefaultRules();
    this.startCleanupTimer();
  }

  /**
   * Analyze request for potential threats
   */
  analyzeRequest(context: SecurityContext): {
    threats: SecurityEvent[];
    blocked: boolean;
    actions: ThreatAction[];
  } {
    const threats: SecurityEvent[] = [];
    const actions: ThreatAction[] = [];
    let blocked = false;

    // Check if source is already blocked
    if (context.request?.ip && this.isBlocked(context.request.ip)) {
      blocked = true;
      actions.push({ type: 'block', metadata: { reason: 'ip_blocked' } });
    }

    // Run threat detection rules
    for (const rule of this.rules.values()) {
      if (!rule.enabled) continue;

      try {
        const result = rule.detector(context);

        if (result.threat && result.confidence > 0.5) {
          const event = this.createSecurityEvent(rule.type, context, result);
          threats.push(event);

          // Execute rule actions
          for (const action of rule.actions) {
            if (action.type === 'block') {
              blocked = true;
            }
            actions.push(action);
          }

          this.emit('threatDetected', { event, rule, context });
        }
      } catch (error) {
        this.emit('ruleError', { rule: rule.id, error, context });
      }
    }

    // Apply actions
    this.executeActions(actions, context);

    return { threats, blocked, actions };
  }

  /**
   * Report security incident
   */
  reportIncident(
    type: ThreatType,
    source: string,
    description: string,
    severity: SecurityEvent['severity'] = 'medium',
    metadata: Record<string, any> = {}
  ): SecurityEvent {
    const event: SecurityEvent = {
      id: this.generateEventId(),
      type,
      severity,
      source,
      description,
      metadata,
      timestamp: new Date(),
      resolved: false,
    };

    this.addEvent(event);
    this.emit('incidentReported', event);

    return event;
  }

  /**
   * Block IP address
   */
  blockIP(ip: string, duration?: number): void {
    this.blockedIPs.add(ip);

    if (duration) {
      setTimeout(() => {
        this.blockedIPs.delete(ip);
        this.emit('ipUnblocked', { ip });
      }, duration);
    }

    this.emit('ipBlocked', { ip, duration });
  }

  /**
   * Check if IP is blocked
   */
  isBlocked(ip: string): boolean {
    return this.blockedIPs.has(ip) || this.quarantineList.has(ip);
  }

  /**
   * Add to quarantine list
   */
  quarantine(identifier: string, reason: string): void {
    this.quarantineList.add(identifier);
    this.emit('quarantined', { identifier, reason });
  }

  /**
   * Remove from quarantine
   */
  unquarantine(identifier: string): void {
    this.quarantineList.delete(identifier);
    this.emit('unquarantined', { identifier });
  }

  /**
   * Register custom threat rule
   */
  addRule(rule: ThreatRule): void {
    this.rules.set(rule.id, rule);
    this.emit('ruleAdded', rule);
  }

  /**
   * Remove threat rule
   */
  removeRule(ruleId: string): void {
    this.rules.delete(ruleId);
    this.emit('ruleRemoved', { ruleId });
  }

  /**
   * Get security metrics
   */
  getMetrics(): SecurityMetrics {
    const eventsByType: Record<ThreatType, number> = {} as any;
    const eventsBySeverity: Record<string, number> = {};
    let blockedRequests = 0;

    for (const event of this.events) {
      eventsByType[event.type] = (eventsByType[event.type] || 0) + 1;
      eventsBySeverity[event.severity] = (eventsBySeverity[event.severity] || 0) + 1;

      if (event.metadata.blocked) {
        blockedRequests++;
      }
    }

    return {
      totalEvents: this.events.length,
      eventsByType,
      eventsBySeverity,
      blockedRequests,
      falsePositives: this.events.filter(e => e.metadata.falsePositive).length,
      responseTime: 0, // Would be calculated from actual response times
    };
  }

  /**
   * Get recent security events
   */
  getRecentEvents(limit = 100): SecurityEvent[] {
    return this.events
      .slice(-limit)
      .sort((_a, _b) => b.timestamp.getTime() - a.timestamp.getTime());
  }

  /**
   * Mark event as resolved
   */
  resolveEvent(eventId: string): boolean {
    const event = this.events.find(e => e.id === eventId);
    if (event) {
      event.resolved = true;
      this.emit('eventResolved', event);
      return true;
    }
    return false;
  }

  /**
   * Mark event as false positive
   */
  markFalsePositive(eventId: string): boolean {
    const event = this.events.find(e => e.id === eventId);
    if (event) {
      event.metadata.falsePositive = true;
      event.resolved = true;
      this.emit('falsePositiveMarked', event);
      return true;
    }
    return false;
  }

  private setupDefaultRules(): void {
    // Brute force detection
    this.addRule({_
      id: 'brute_force_login',
      name: 'Brute Force Login Detection',
      _type: 'brute_force',
      _enabled: true,
      _threshold: 5,
      _timeWindow: 15 * 60 * 1000, _// 15 minutes
      detector: (context) => {
        if (!context.request || !context.request.path.includes('/login')) {
          return { threat: false, confidence: 0, description: '', evidence: Record<string, unknown>};
        }

        const ip = context.request.ip;
        const activity = this.suspiciousActivity.get(ip);
        const now = new Date();

        if (!activity) {
          this.suspiciousActivity.set(ip, { count: 1, lastSeen: now });
          return { threat: false, confidence: 0, description: '', evidence: Record<string, unknown>};
        }

        // Check if within time window
        if (now.getTime() - activity.lastSeen.getTime() < this.rules.get('brute_force_login')!.timeWindow!) {
          activity.count++;
          activity.lastSeen = now;

          if (activity.count >= this.rules.get('brute_force_login')!.threshold!) {
            return {
              threat: true,
              confidence: 0.9,
              description: `Brute force login attempt detected from ${ip}`,
              evidence: { attempts: activity.count, timeWindow: '15m' },
            };
          }
        } else {
          // Reset counter after time window
          activity.count = 1;
          activity.lastSeen = now;
        }

        return { threat: false, confidence: 0, description: '', evidence: Record<string, unknown>};
      },
      actions: [
        { type: 'block', duration: 60 * 60 * 1000 }, // Block for 1 hour
        { type: 'alert' },
      ],
    });

    // SQL injection detection
    this.addRule({_
      id: 'sql_injection',
      name: 'SQL Injection Detection',
      _type: 'sql_injection',
      _enabled: true,
      _detector: (context) => {
        if (!context.request?.body && !context.request?.path) {
          return { threat: false, confidence: 0, description: '', evidence: Record<string, unknown>};
        }

        const content = JSON.stringify(context.request.body) + context.request.path;
        const sqlPatterns = [
          /(\b(SELECT|INSERT|UPDATE|DELETE|DROP|CREATE|ALTER|EXEC|EXECUTE)\b)/i,
          /(\b(UNION|OR|AND)\b.*\b(SELECT|INSERT|UPDATE|DELETE)\b)/i,
          /(--|#|/\*|\*/)/,
          /(\b(SLEEP|BENCHMARK|WAITFOR)\b)/i,
        ];

        for (const pattern of sqlPatterns) {
          if (pattern.test(content)) {
            return {
              threat: true,
              confidence: 0.8,
              description: 'Potential SQL injection detected',
              evidence: { pattern: pattern.toString(), content: content.substring(0, 200) },
            };
          }
        }

        return { threat: false, confidence: 0, description: '', evidence: Record<string, unknown>};
      },
      actions: [
        { type: 'block' },
        { type: 'alert' },
        { type: 'log' },
      ],
    });

    // XSS detection
    this.addRule({_
      id: 'xss_detection',
      name: 'XSS Attack Detection',
      _type: 'xss_attempt',
      _enabled: true,
      _detector: (context) => {
        if (!context.request?.body) {
          return { threat: false, confidence: 0, description: '', evidence: Record<string, unknown>};
        }

        const content = JSON.stringify(context.request.body);
        const xssPatterns = [
          /<script[^>]*>.*?</script>/gi,
          /javascript:/gi,
          /on\w+\s*=/gi,
          /<iframe[^>]*>/gi,
          /<object[^>]*>/gi,
          /eval\s*\(/gi,
        ];

        for (const pattern of xssPatterns) {
          if (pattern.test(content)) {
            return {
              threat: true,
              confidence: 0.85,
              description: 'Potential XSS attack detected',
              evidence: { pattern: pattern.toString(), content: content.substring(0, 200) },
            };
          }
        }

        return { threat: false, confidence: 0, description: '', evidence: Record<string, unknown>};
      },
      actions: [
        { type: 'block' },
        { type: 'alert' },
        { type: 'log' },
      ],
    });

    // Suspicious user agent detection
    this.addRule({_
      id: 'suspicious_user_agent',
      name: 'Suspicious User Agent Detection',
      _type: 'suspicious_activity',
      _enabled: true,
      _detector: (context) => {
        const userAgent = context.request?.userAgent?.toLowerCase();
        if (!userAgent) {
          return { threat: false, confidence: 0, description: '', evidence: Record<string, unknown>};
        }

        const suspiciousPatterns = [
          /bot|crawler|spider|scraper/,
          /curl|wget|python|perl|php/,
          /sqlmap|nikto|nmap|masscan/,
          /burp|owasp|zap/,
        ];

        const normalPatterns = [
          /googlebot|bingbot|slurp|duckduckbot/,
          /facebook|twitter|linkedin|discord/,
        ];

        // Check if it's a known good bot
        if (normalPatterns.some(pattern => pattern.test(userAgent))) {
          return { threat: false, confidence: 0, description: '', evidence: Record<string, unknown>};
        }

        // Check for suspicious patterns
        const matchedPattern = suspiciousPatterns.find(pattern => pattern.test(userAgent));
        if (matchedPattern) {
          return {
            threat: true,
            confidence: 0.7,
            description: 'Suspicious user agent detected',
            evidence: { userAgent, pattern: matchedPattern.toString() },
          };
        }

        return { threat: false, confidence: 0, description: '', evidence: Record<string, unknown>};
      },
      actions: [
        { type: 'limit' },
        { type: 'log' },
      ],
    });

    // File upload threat detection
    this.addRule({_
      id: 'malicious_file_upload',
      name: 'Malicious File Upload Detection',
      _type: 'file_upload_threat',
      _enabled: true,
      _detector: (context) => {
        if (!context.request?.path.includes('/upload') && !context.metadata?.filename) {
          return { threat: false, confidence: 0, description: '', evidence: Record<string, unknown>};
        }

        const filename = context.metadata?.filename?.toLowerCase();
        if (!filename) {
          return { threat: false, confidence: 0, description: '', evidence: Record<string, unknown>};
        }

        // Check for dangerous extensions
        const dangerousExtensions = [
          '.exe', '.bat', '.cmd', '.com', '.pif', '.scr', '.vbs', '.js',
          '.jar', '.zip', '.rar', '.7z', '.tar', '.gz',
          '.php', '.asp', '.jsp', '.py', '.pl', '.sh',
        ];

        const hasDangerousExtension = dangerousExtensions.some(ext => filename.endsWith(ext));

        // Check for double extensions
        const hasDoubleExtension = /\.[^.]+\.[^.]+$/.test(filename);

        // Check for suspicious names
        const suspiciousNames = ['shell', 'backdoor', 'exploit', 'payload', 'reverse'];
        const hasSuspiciousName = suspiciousNames.some(name => filename.includes(name));

        if (hasDangerousExtension || hasDoubleExtension || hasSuspiciousName) {
          return {
            threat: true,
            confidence: 0.9,
            description: 'Potentially malicious file upload detected',
            evidence: { 
              filename, 
              dangerousExtension: hasDangerousExtension,
              doubleExtension: hasDoubleExtension,
              suspiciousName: hasSuspiciousName,
            },
          };
        }

        return { threat: false, confidence: 0, description: '', evidence: Record<string, unknown>};
      },
      actions: [
        { type: 'block' },
        { type: 'quarantine' },
        { type: 'alert' },
      ],
    });
  }

  private createSecurityEvent(
    type: ThreatType,
    context: SecurityContext,
    result: ThreatDetectionResult
  ): SecurityEvent {
    const event: SecurityEvent = {
      id: this.generateEventId(),
      type,
      severity: this.calculateSeverity(type, result.confidence),
      source: context.request?.ip || context.user?.id || 'unknown',
      description: result.description,
      metadata: {
        confidence: result.confidence,
        evidence: result.evidence,
        context: {
          path: context.request?.path,
          method: context.request?.method,
          userAgent: context.request?.userAgent,
          userId: context.user?.id,
        },
      },
      timestamp: new Date(),
      resolved: false,
    };

    this.addEvent(event);
    return event;
  }

  private calculateSeverity(type: ThreatType, confidence: number): SecurityEvent['severity'] {
    const baseSeverity: Record<ThreatType, number> = {
      sql_injection: 4,
      xss_attempt: 3,
      brute_force: 3,
      file_upload_threat: 4,
      session_hijacking: 4,
      csrf_attempt: 3,
      unauthorized_access: 3,
      suspicious_activity: 2,
      rate_limit_violation: 1,
      malformed_request: 1,
    };

    const score = (baseSeverity[type] || 2) * confidence;

    if (score >= 3.5) return 'critical';
    if (score >= 2.5) return 'high';
    if (score >= 1.5) return 'medium';
    return 'low';
  }

  private executeActions(actions: ThreatAction[], context: SecurityContext): void {
    for (const action of actions) {
      switch (action.type) {
        case 'block':
          if (context.request?.ip) {
            this.blockIP(context.request.ip, action.duration);
          }
          break;

        case 'quarantine':
          if (context.user?.id) {
            this.quarantine(context.user.id, 'Threat detected');
          }
          break;

        case 'alert':
          this.emit('securityAlert', { action, context });
          break;

        case 'log':
          this.emit('securityLog', { action, context });
          break;
      }
    }
  }

  private addEvent(event: SecurityEvent): void {
    this.events.push(event);

    // Keep events within limit
    if (this.events.length > this.maxEvents) {
      this.events.shift();
    }
  }

  private generateEventId(): string {
    return `evt_${Date.now()}_${Math.random().toString(36).substring(2)}`;
  }

  private startCleanupTimer(): void {
    // Clean up old suspicious activity records every hour
    setInterval(() => {
      const oneHourAgo = new Date(Date.now() - 60 * 60 * 1000);

      for (const [ip, activity] of this.suspiciousActivity) {
        if (activity.lastSeen < oneHourAgo) {
          this.suspiciousActivity.delete(ip);
        }
      }
    }, 60 * 60 * 1000);
  }
}

/**
 * Authentication Manager Tests
 * Comprehensive test suite for authentication and authorization
 */

import { AuthenticationManager } from '../AuthenticationManager';

describe('AuthenticationManager', () => {
  let authManager: AuthenticationManager;

  beforeEach(() => {
    authManager = new AuthenticationManager({
      jwtSecret: 'test-secret',
      jwtRefreshSecret: 'test-refresh-secret',
      accessTokenExpiry: '15m',
      refreshTokenExpiry: '7d',
      bcryptRounds: 4, // Lower for testing performance
      maxSessions: 3,
      requireEmailVerification: false,
    });
  });

  describe('User Registration', () => {
    test('should register new user successfully', async () => {
      const result = await authManager.register({
        email: 'test@example.com',
        username: 'testuser',
        password: 'SecurePass123!',
        acceptTerms: true,
      });

      expect(result.success).toBe(true);
      expect(result.user).toBeDefined();
      expect(result.user?.email).toBe('test@example.com');
      expect(result.user?.username).toBe('testuser');
      expect(result.user?.roles).toContain('user');
      expect(result.token).toBeDefined(); // Auto-login when email verification not required
    });

    test('should reject duplicate email registration', async () => {
      const userData = {
        email: 'duplicate@example.com',
        username: 'user1',
        password: 'SecurePass123!',
        acceptTerms: true,
      };

      await authManager.register(userData);

      const result = await authManager.register({
        ...userData,
        username: 'user2', // Different username
      });

      expect(result.success).toBe(false);
      expect(result.code).toBe('USER_EXISTS');
      expect(result.error).toContain('Email already registered');
    });

    test('should reject duplicate username registration', async () => {
      const userData = {
        email: 'user1@example.com',
        username: 'duplicate',
        password: 'SecurePass123!',
        acceptTerms: true,
      };

      await authManager.register(userData);

      const result = await authManager.register({
        ...userData,
        email: 'user2@example.com', // Different email
      });

      expect(result.success).toBe(false);
      expect(result.code).toBe('USER_EXISTS');
      expect(result.error).toContain('Username already taken');
    });

    test('should hash password securely', async () => {
      const password = 'TestPassword123!';
      const result = await authManager.register({
        email: 'secure@example.com',
        username: 'secureuser',
        password,
        acceptTerms: true,
      });

      expect(result.success).toBe(true);
      expect(result.user?.passwordHash).toBeUndefined(); // Should not expose hash
    });
  });

  describe('User Login', () => {
    beforeEach(async () => {
      await authManager.register({
        email: 'login@example.com',
        username: 'loginuser',
        password: 'LoginPass123!',
        acceptTerms: true,
      });
    });

    test('should login with valid credentials', async () => {
      const result = await authManager.login({
        email: 'login@example.com',
        password: 'LoginPass123!',
      });

      expect(result.success).toBe(true);
      expect(result.user).toBeDefined();
      expect(result.token).toBeDefined();
      expect(result.session).toBeDefined();
      expect(result.token?.tokenType).toBe('Bearer');
      expect(result.token?.accessToken).toBeDefined();
      expect(result.token?.refreshToken).toBeDefined();
    });

    test('should reject invalid email', async () => {
      const result = await authManager.login({
        email: 'nonexistent@example.com',
        password: 'LoginPass123!',
      });

      expect(result.success).toBe(false);
      expect(result.code).toBe('INVALID_CREDENTIALS');
    });

    test('should reject invalid password', async () => {
      const result = await authManager.login({
        email: 'login@example.com',
        password: 'WrongPassword',
      });

      expect(result.success).toBe(false);
      expect(result.code).toBe('INVALID_CREDENTIALS');
    });

    test('should track user sessions', async () => {
      const result = await authManager.login({
        email: 'login@example.com',
        password: 'LoginPass123!',
        ipAddress: '192.168.1.1',
        userAgent: 'Test Browser',
      });

      expect(result.success).toBe(true);

      const sessions = authManager.getUserSessions(result.user!.id);
      expect(sessions).toHaveLength(1);
      expect(sessions[0].ipAddress).toBe('192.168.1.1');
      expect(sessions[0].userAgent).toBe('Test Browser');
    });
  });

  describe('Token Management', () => {
    let userToken: string;
    let refreshToken: string;

    beforeEach(async () => {
      await authManager.register({
        email: 'token@example.com',
        username: 'tokenuser',
        password: 'TokenPass123!',
        acceptTerms: true,
      });

      const loginResult = await authManager.login({
        email: 'token@example.com',
        password: 'TokenPass123!',
      });

      userToken = loginResult.token!.accessToken;
      refreshToken = loginResult.token!.refreshToken;
    });

    test('should verify valid access token', () => {
      const result = authManager.verifyAccessToken(userToken);
      expect(result.valid).toBe(true);
      expect(result.payload).toBeDefined();
      expect(result.payload.email).toBe('token@example.com');
    });

    test('should reject invalid access token', () => {
      const result = authManager.verifyAccessToken('invalid.token.here');
      expect(result.valid).toBe(false);
      expect(result.error).toBeDefined();
    });

    test('should refresh access token with valid refresh token', async () => {
      const result = await authManager.refreshToken(refreshToken);
      expect(result.success).toBe(true);
      expect(result.token).toBeDefined();
      expect(result.token?.accessToken).not.toBe(userToken); // Should be new token
    });

    test('should reject invalid refresh token', async () => {
      const result = await authManager.refreshToken('invalid-refresh-token');
      expect(result.success).toBe(false);
      expect(result.code).toBe('INVALID_REFRESH_TOKEN');
    });

    test('should blacklist token on logout', async () => {
      const logoutResult = await authManager.logout(userToken);
      expect(logoutResult.success).toBe(true);

      // Token should now be invalid
      const verifyResult = authManager.verifyAccessToken(userToken);
      expect(verifyResult.valid).toBe(false);
      expect(verifyResult.error).toContain('blacklisted');
    });
  });

  describe('Role and Permission Management', () => {
    let userId: string;

    beforeEach(async () => {
      const result = await authManager.register({
        email: 'roles@example.com',
        username: 'roleuser',
        password: 'RolePass123!',
        acceptTerms: true,
      });
      userId = result.user!.id;
    });

    test('should add and check roles', () => {
      expect(authManager.hasRole(userId, 'admin')).toBe(false);

      authManager.addRole(userId, 'admin');
      expect(authManager.hasRole(userId, 'admin')).toBe(true);
    });

    test('should remove roles', () => {
      authManager.addRole(userId, 'gm');
      expect(authManager.hasRole(userId, 'gm')).toBe(true);

      authManager.removeRole(userId, 'gm');
      expect(authManager.hasRole(userId, 'gm')).toBe(false);
    });

    test('should check permissions based on roles', () => {
      // User role should have basic permissions
      expect(authManager.hasPermission(userId, 'read:own')).toBe(true);
      expect(authManager.hasPermission(userId, 'manage:scenes')).toBe(false);

      // Admin role should have all permissions
      authManager.addRole(userId, 'admin');
      expect(authManager.hasPermission(userId, 'manage:scenes')).toBe(true);
      expect(authManager.hasPermission(userId, 'manage:users')).toBe(true);
    });

    test('should handle GM permissions', () => {
      authManager.addRole(userId, 'gm');

      expect(authManager.hasPermission(userId, 'manage:scenes')).toBe(true);
      expect(authManager.hasPermission(userId, 'manage:tokens')).toBe(true);
      expect(authManager.hasPermission(userId, 'read:scenes')).toBe(true);
    });
  });

  describe('Session Management', () => {
    let userId: string;

    beforeEach(async () => {
      const result = await authManager.register({
        email: 'sessions@example.com',
        username: 'sessionuser',
        password: 'SessionPass123!',
        acceptTerms: true,
      });
      userId = result.user!.id;
    });

    test('should enforce maximum session limit', async () => {
      const credentials = {
        email: 'sessions@example.com',
        password: 'SessionPass123!',
      };

      // Create multiple sessions (more than maxSessions = 3)
      const sessions = [];
      for (let i = 0; i < 5; i++) {
        const result = await authManager.login({
          ...credentials,
          userAgent: `Browser ${i}`,
        });
        sessions.push(result);
      }

      // Should only have 3 active sessions
      const activeSessions = authManager.getUserSessions(userId);
      expect(activeSessions.length).toBeLessThanOrEqual(3);
    });

    test('should invalidate other sessions', async () => {
      const credentials = {
        email: 'sessions@example.com',
        password: 'SessionPass123!',
      };

      // Create multiple sessions
      const _session1 = await authManager.login(credentials);
      const _session2 = await authManager.login(credentials);
      const session3 = await authManager.login(credentials);

      expect(authManager.getUserSessions(userId)).toHaveLength(3);

      // Invalidate other sessions except current
      const invalidatedCount = authManager.invalidateOtherSessions(
        userId,
        session3.session!.id
      );

      expect(invalidatedCount).toBe(2);
      expect(authManager.getUserSessions(userId)).toHaveLength(1);
    });
  });

  describe('Password Management', () => {
    let userId: string;

    beforeEach(async () => {
      const result = await authManager.register({
        email: 'password@example.com',
        username: 'passworduser',
        password: 'OldPass123!',
        acceptTerms: true,
      });
      userId = result.user!.id;
    });

    test('should change password with valid old password', async () => {
      const result = await authManager.changePassword(
        userId,
        'OldPass123!',
        'NewPass456!'
      );

      expect(result.success).toBe(true);

      // Should be able to login with new password
      const loginResult = await authManager.login({
        email: 'password@example.com',
        password: 'NewPass456!',
      });
      expect(loginResult.success).toBe(true);
    });

    test('should reject password change with invalid old password', async () => {
      const result = await authManager.changePassword(
        userId,
        'WrongOldPass',
        'NewPass456!'
      );

      expect(result.success).toBe(false);
      expect(result.error).toContain('Invalid current password');
    });

    test('should invalidate all sessions on password change', async () => {
      // Create multiple sessions
      await authManager.login({
        email: 'password@example.com',
        password: 'OldPass123!',
      });
      await authManager.login({
        email: 'password@example.com',
        password: 'OldPass123!',
      });

      expect(authManager.getUserSessions(userId)).toHaveLength(2);

      // Change password
      await authManager.changePassword(userId, 'OldPass123!', 'NewPass456!');

      // All sessions should be invalidated
      expect(authManager.getUserSessions(userId)).toHaveLength(0);
    });
  });

  describe('Security Events', () => {
    test('should emit events on user registration', (done) => {
      authManager.on('userRegistered', (data) => {
        expect(data.user.email).toBe('event@example.com');
        done();
      });

      authManager.register({
        email: 'event@example.com',
        username: 'eventuser',
        password: 'EventPass123!',
        acceptTerms: true,
      });
    });

    test('should emit events on successful login', (done) => {
      authManager.on('loginSuccess', (data) => {
        expect(data.user.email).toBe('event@example.com');
        done();
      });

      // First register, then login
      authManager.register({
        email: 'event@example.com',
        username: 'eventuser',
        password: 'EventPass123!',
        acceptTerms: true,
      }).then(() => {
        authManager.login({
          email: 'event@example.com',
          password: 'EventPass123!',
        });
      });
    });

    test('should emit events on failed login', (done) => {
      authManager.on('loginFailed', (data) => {
        expect(data.reason).toBe('user_not_found');
        done();
      });

      authManager.login({
        email: 'nonexistent@example.com',
        password: 'password',
      });
    });
  });

  describe('Error Handling', () => {
    test('should handle malformed JWT tokens gracefully', () => {
      const result = authManager.verifyAccessToken('not.a.valid.jwt.token');
      expect(result.valid).toBe(false);
      expect(result.error).toBeDefined();
    });

    test('should handle expired tokens gracefully', async () => {
      // Create auth manager with very short token expiry
      const shortAuthManager = new AuthenticationManager({
        jwtSecret: 'test-secret',
        jwtRefreshSecret: 'test-refresh-secret',
        accessTokenExpiry: '1ms', // 1 millisecond
        refreshTokenExpiry: '7d',
        bcryptRounds: 4,
        maxSessions: 3,
        requireEmailVerification: false,
      });

      await shortAuthManager.register({
        email: 'expire@example.com',
        username: 'expireuser',
        password: 'ExpirePass123!',
        acceptTerms: true,
      });

      const loginResult = await shortAuthManager.login({
        email: 'expire@example.com',
        password: 'ExpirePass123!',
      });

      // Wait for token to expire
      await new Promise(resolve => setTimeout(resolve, 10));

      const verifyResult = shortAuthManager.verifyAccessToken(loginResult.token!.accessToken);
      expect(verifyResult.valid).toBe(false);
      expect(verifyResult.error).toContain('Invalid token');
    });

    test('should handle database errors gracefully', async () => {
      // Simulate error by trying to register with invalid data
      const result = await authManager.register({
        email: '', // Invalid email
        username: '',
        password: '',
        acceptTerms: false,
      });

      expect(result.success).toBe(false);
      expect(result.error).toBeDefined();
    });
  });

  describe('Performance', () => {
    test('should handle multiple concurrent operations', async () => {
      const operations = Array.from({_ length: 10 }, async (_, _i) => {
        return authManager.register({
          email: `concurrent${i}@example.com`,
          username: `user${i}`,
          password: 'ConcurrentPass123!',
          acceptTerms: true,
        });
      });

      const results = await Promise.all(operations);

      // All operations should succeed
      expect(results.every(r => r.success)).toBe(true);

      // All users should have unique IDs
      const userIds = results.map(r => r.user!.id);
      const uniqueIds = new Set(userIds);
      expect(uniqueIds.size).toBe(userIds.length);
    });

    test('should perform password hashing efficiently', async () => {
      const start = Date.now();

      await authManager.register({
        email: 'perf@example.com',
        username: 'perfuser',
        password: 'PerformancePass123!',
        acceptTerms: true,
      });

      const duration = Date.now() - start;
      expect(duration).toBeLessThan(1000); // Should complete within 1 second
    });
  });
});

/**
 * Input Validator Tests
 * Comprehensive test suite for input validation and sanitization
 */

import { InputValidator } from '../InputValidator';

describe('InputValidator', () => {
  let validator: InputValidator;

  beforeEach(() => {
    validator = new InputValidator();
  });

  describe('Basic Validation', () => {
    test('should validate valid email', () => {
      const result = validator.validate({ email: 'test@example.com' }, 'userLogin');
      expect(result.isValid).toBe(true);
      expect(result.errors).toHaveLength(0);
    });

    test('should reject invalid email', () => {
      const result = validator.validate({ email: 'invalid-email' }, 'userLogin');
      expect(result.isValid).toBe(false);
      expect(result.errors).toContainEqual(
        expect.objectContaining({ field: 'email' })
      );
    });

    test('should validate strong password', () => {
      const result = validator.validate(
        { password: 'SecurePass123!' },
        'userRegistration'
      );
      expect(result.isValid).toBe(true);
    });

    test('should reject weak password', () => {
      const result = validator.validate(
        { password: '123' },
        'userRegistration'
      );
      expect(result.isValid).toBe(false);
      expect(result.errors).toContainEqual(
        expect.objectContaining({ field: 'password' })
      );
    });
  });

  describe('SQL Injection Detection', () => {
    test('should detect basic SQL injection attempts', () => {
      const maliciousInputs = [
        "'; DROP TABLE users; --",
        "1' OR '1'='1",
        "admin'--",
        "1; DELETE FROM users WHERE id=1",
      ];

      maliciousInputs.forEach(input => {
        const result = validator.validate({ query: input }, 'apiGeneric');
        expect(result.isValid).toBe(false);
        expect(result.errors.some(e => e.code === 'SQL_INJECTION')).toBe(true);
      });
    });

    test('should allow safe SQL-like content', () => {
      const safeInputs = [
        "What's your favorite color?",
        "I have 1 cat and 2 dogs",
        "Please select your preference",
      ];

      safeInputs.forEach(input => {
        const result = validator.validate({ content: input }, 'apiGeneric');
        expect(result.isValid).toBe(true);
      });
    });
  });

  describe('XSS Detection', () => {
    test('should detect XSS attempts', () => {
      const xssInputs = [
        '<script>alert("xss")</script>',
        '<img src="x" onerror="alert(1)">',
        'javascript:alert(1)',
        '<iframe src="malicious.com"></iframe>',
        '<div onclick="alert(1)">Click me</div>',
      ];

      xssInputs.forEach(input => {
        const result = validator.validate({ content: input }, 'apiGeneric');
        expect(result.isValid).toBe(false);
        expect(result.errors.some(e => e.code === 'XSS_ATTEMPT')).toBe(true);
      });
    });

    test('should allow safe HTML-like content', () => {
      const safeInputs = [
        'I love &lt;programming&gt;!',
        'Check out this cool feature',
        'Price: $5 < $10',
      ];

      safeInputs.forEach(input => {
        const result = validator.validate({ content: input }, 'apiGeneric');
        expect(result.isValid).toBe(true);
      });
    });
  });

  describe('File Upload Validation', () => {
    test('should validate safe file uploads', () => {
      const safeFiles = [
        { filename: 'image.jpg', size: 1024 * 1024, mimetype: 'image/jpeg' },
        { filename: 'document.pdf', size: 2 * 1024 * 1024, mimetype: 'application/pdf' },
        { filename: 'scene.json', size: 100 * 1024, mimetype: 'application/json' },
      ];

      safeFiles.forEach(file => {
        const result = validator.validateFileUpload(file);
        expect(result.isValid).toBe(true);
      });
    });

    test('should reject dangerous file uploads', () => {
      const dangerousFiles = [
        { filename: 'malware.exe', size: 1024, mimetype: 'application/exe' },
        { filename: 'script.js', size: 1024, mimetype: 'application/javascript' },
        { filename: 'shell.php', size: 1024, mimetype: 'application/php' },
        { filename: 'huge.jpg', size: 100 * 1024 * 1024, mimetype: 'image/jpeg' }, // Too large
      ];

      dangerousFiles.forEach(file => {
        const result = validator.validateFileUpload(file);
        expect(result.isValid).toBe(false);
      });
    });
  });

  describe('Sanitization', () => {
    test('should sanitize HTML content', () => {
      const maliciousHtml = '<script>alert("xss")</script><p>Safe content</p>';
      const sanitized = validator.sanitize(maliciousHtml, { 
        html: true,
        allowedTags: ['p', 'strong', 'em']
      });

      expect(sanitized).not.toContain('<script>');
      expect(sanitized).toContain('<p>Safe content</p>');
    });

    test('should sanitize SQL injection attempts', () => {
      const maliciousSql = "'; DROP TABLE users; --";
      const sanitized = validator.sanitize(maliciousSql, { 
        preventSQLInjection: true 
      });

      expect(sanitized).not.toContain('DROP TABLE');
      expect(sanitized).not.toContain('--');
    });

    test('should preserve safe content during sanitization', () => {
      const safeContent = 'Hello, world! This is safe content.';
      const sanitized = validator.sanitize(safeContent, { html: true });

      expect(sanitized).toBe(safeContent);
    });
  });

  describe('Custom Validation Rules', () => {
    test('should support custom validation rules', () => {
      validator.addValidationRule('customRule', (value) => ({
        isValid: value === 'expected',
        message: 'Value must be "expected"',
      }));

      const validResult = validator.validate({ test: 'expected' }, 'custom');
      expect(validResult.isValid).toBe(true);

      const invalidResult = validator.validate({ test: 'unexpected' }, 'custom');
      expect(invalidResult.isValid).toBe(false);
    });
  });

  describe('Scene Validation', () => {
    test('should validate scene creation data', () => {
      const validScene = {
        name: 'Test Scene',
        width: 800,
        height: 600,
        gridSize: 50,
        backgroundColor: '#ffffff',
      };

      const result = validator.validate(validScene, 'sceneCreation');
      expect(result.isValid).toBe(true);
    });

    test('should reject invalid scene data', () => {
      const invalidScene = {
        name: '', // Empty name
        width: -100, // Negative width
        height: 'invalid', // Non-numeric height
        gridSize: 0, // Zero grid size
      };

      const result = validator.validate(invalidScene, 'sceneCreation');
      expect(result.isValid).toBe(false);
      expect(result.errors.length).toBeGreaterThan(0);
    });
  });

  describe('Token Validation', () => {
    test('should validate token creation data', () => {
      const validToken = {
        name: 'Player Character',
        x: 100,
        y: 150,
        size: 1,
        color: '#ff0000',
        imageUrl: 'https://example.com/token.png',
      };

      const result = validator.validate(validToken, 'tokenCreation');
      expect(result.isValid).toBe(true);
    });

    test('should reject malicious token data', () => {
      const maliciousToken = {
        name: '<script>alert("xss")</script>',
        x: 'DROP TABLE tokens',
        y: 150,
        size: -1,
        imageUrl: 'javascript:alert(1)',
      };

      const result = validator.validate(maliciousToken, 'tokenCreation');
      expect(result.isValid).toBe(false);
      expect(result.errors.length).toBeGreaterThan(0);
    });
  });

  describe('Performance', () => {
    test('should handle large input validation efficiently', () => {
      const largeInput = {
        content: 'a'.repeat(10000), // 10KB of content
        list: Array.from({_ length: 1000 }, (_, _i) => `item${i}`),
      };

      const start = Date.now();
      const result = validator.validate(largeInput, 'apiGeneric');
      const duration = Date.now() - start;

      expect(duration).toBeLessThan(1000); // Should complete within 1 second
      expect(result.isValid).toBe(true);
    });

    test('should cache validation schemas for performance', () => {
      const testData = { email: 'test@example.com' };

      // First validation - schema compilation
      const start1 = Date.now();
      validator.validate(testData, 'userLogin');
      const duration1 = Date.now() - start1;

      // Second validation - should use cached schema
      const start2 = Date.now();
      validator.validate(testData, 'userLogin');
      const duration2 = Date.now() - start2;

      expect(duration2).toBeLessThanOrEqual(duration1);
    });
  });

  describe('Error Handling', () => {
    test('should handle unknown schema gracefully', () => {
      const result = validator.validate({ test: 'value' }, 'unknownSchema');
      expect(result.isValid).toBe(false);
      expect(result.errors).toContainEqual(
        expect.objectContaining({ code: 'UNKNOWN_SCHEMA' })
      );
    });

    test('should handle malformed input gracefully', () => {
      const result = validator.validate(null as any, 'userLogin');
      expect(result.isValid).toBe(false);
      expect(result.errors.length).toBeGreaterThan(0);
    });

    test('should handle circular references in input', () => {
      const circularInput: any = { name: 'test' };
      circularInput.self = circularInput;

      expect(() => {
        validator.validate(circularInput, 'apiGeneric');
      }).not.toThrow();
    });
  });
});

/**
 * Rate Limiter Tests
 * Comprehensive test suite for rate limiting functionality
 */

import { RateLimiter, TokenBucketRateLimiter, AdaptiveRateLimiter, RateLimiterManager, RATE_LIMIT_PRESETS } from '../RateLimiter';

describe('RateLimiter', () => {
  describe('Basic Rate Limiting', () => {
    test('should allow requests within limit', () => {
      const limiter = new RateLimiter({
        windowMs: 60000, // 1 minute
        max: 5,
      });

      for (let i = 0; i < 5; i++) {
        const result = limiter.checkLimit('test-user');
        expect(result.allowed).toBe(true);
        expect(result.info.remainingRequests).toBe(4 - i);
      }
    });

    test('should block requests exceeding limit', () => {
      const limiter = new RateLimiter({
        windowMs: 60000,
        max: 3,
      });

      // Use up the limit
      for (let i = 0; i < 3; i++) {
        limiter.checkLimit('test-user');
      }

      // Next request should be blocked
      const result = limiter.checkLimit('test-user');
      expect(result.allowed).toBe(false);
      expect(result.reason).toBeDefined();
      expect(result.info.retryAfter).toBeGreaterThan(0);
    });

    test('should reset limits after window expires', (_done) => {
      const limiter = new RateLimiter({
        windowMs: 100, // 100ms window
        max: 2,
      });

      // Use up the limit
      limiter.checkLimit('test-user');
      limiter.checkLimit('test-user');

      // Should be blocked
      let result = limiter.checkLimit('test-user');
      expect(result.allowed).toBe(false);

      // Wait for window to reset
      setTimeout(() => {
        result = limiter.checkLimit('test-user');
        expect(result.allowed).toBe(true);
        done();
      }, 150);
    });

    test('should handle multiple users independently', () => {
      const limiter = new RateLimiter({
        windowMs: 60000,
        max: 2,
      });

      // User 1 uses up limit
      limiter.checkLimit('user1');
      limiter.checkLimit('user1');
      const user1Result = limiter.checkLimit('user1');
      expect(user1Result.allowed).toBe(false);

      // User 2 should still be allowed
      const user2Result = limiter.checkLimit('user2');
      expect(user2Result.allowed).toBe(true);
    });

    test('should use custom key generator', () => {
      const limiter = new RateLimiter({_
        windowMs: 60000,
        _max: 1,
        _keyGenerator: (id) => `custom-${id}`,
      });

      limiter.checkLimit('test');
      const result = limiter.checkLimit('test');
      expect(result.allowed).toBe(false);
    });
  });

  describe('Rate Limiter Events', () => {
    test('should emit events on requests and rate limiting', (_done) => {
      const limiter = new RateLimiter({
        windowMs: 60000,
        max: 1,
      });

      let requestEmitted = false;
      let rateLimitedEmitted = false;

      limiter.on('request', () => {
        requestEmitted = true;
      });

      limiter.on('rateLimited', () => {
        rateLimitedEmitted = true;
        expect(requestEmitted).toBe(true);
        expect(rateLimitedEmitted).toBe(true);
        done();
      });

      limiter.checkLimit('test-user');
      limiter.checkLimit('test-user');
    });

    test('should emit reset events', (_done) => {
      const limiter = new RateLimiter({
        windowMs: 60000,
        max: 1,
      });

      limiter.on('reset', (data) => {
        expect(data.identifier).toBe('test-user');
        done();
      });

      limiter.checkLimit('test-user');
      limiter.reset('test-user');
    });
  });

  describe('Statistics', () => {
    test('should provide accurate statistics', () => {
      const limiter = new RateLimiter({
        windowMs: 60000,
        max: 3,
      });

      // Make some requests
      limiter.checkLimit('user1');
      limiter.checkLimit('user1');
      limiter.checkLimit('user2');
      limiter.checkLimit('user1'); // Should exceed limit

      const stats = limiter.getStats();
      expect(stats.activeWindows).toBe(2);
      expect(stats.totalRequests).toBe(4);
      expect(stats.rateLimitedRequests).toBe(1);
    });
  });
});

describe('TokenBucketRateLimiter', () => {
  test('should allow token consumption within capacity', () => {
    const limiter = new TokenBucketRateLimiter({
      capacity: 10,
      refillRate: 1, // 1 token per second
      initialTokens: 10,
    });

    // Consume 5 tokens
    const result = limiter.consume('test-user', 5);
    expect(result.allowed).toBe(true);
    expect(result.info.remainingRequests).toBe(5);
  });

  test('should reject consumption exceeding capacity', () => {
    const limiter = new TokenBucketRateLimiter({
      capacity: 5,
      refillRate: 1,
      initialTokens: 3,
    });

    // Try to consume more tokens than available
    const result = limiter.consume('test-user', 5);
    expect(result.allowed).toBe(false);
    expect(result.info.retryAfter).toBeGreaterThan(0);
  });

  test('should refill tokens over time', (_done) => {
    const limiter = new TokenBucketRateLimiter({
      capacity: 5,
      refillRate: 10, // 10 tokens per second for faster testing
      initialTokens: 1,
    });

    // Consume available token
    let result = limiter.consume('test-user', 1);
    expect(result.allowed).toBe(true);

    // Try to consume another - should fail
    result = limiter.consume('test-user', 1);
    expect(result.allowed).toBe(false);

    // Wait for refill
    setTimeout(() => {
      result = limiter.consume('test-user', 1);
      expect(result.allowed).toBe(true);
      done();
    }, 200); // Wait 200ms for refill
  });

  test('should handle manual token addition', () => {
    const limiter = new TokenBucketRateLimiter({
      capacity: 10,
      refillRate: 1,
      initialTokens: 2,
    });

    // Add tokens manually
    limiter.addTokens('test-user', 5);

    // Should now be able to consume more
    const result = limiter.consume('test-user', 6);
    expect(result.allowed).toBe(true);
  });
});

describe('AdaptiveRateLimiter', () => {
  test('should adapt limits based on system load', (_done) => {
    const limiter = new AdaptiveRateLimiter(
      {
        windowMs: 60000,
        max: 100, // Will be overridden by adaptive config
      },
      {
        baseLimit: 100,
        maxLimit: 200,
        minLimit: 50,
        adaptationFactor: 0.5,
        loadThreshold: 0.7,
      }
    );

    // Set high system load
    limiter.setSystemLoadMonitor(() => 0.9); // 90% load

    let limitAdapted = false;
    limiter.on('limitAdapted', (data) => {
      expect(data.newLimit).toBeLessThan(data.oldLimit);
      limitAdapted = true;
    });

    // Wait for adaptation cycle
    setTimeout(() => {
      expect(limitAdapted).toBe(true);
      done();
    }, 6000); // Wait for adaptation interval
  });

  test('should increase limits under low load', (_done) => {
    const limiter = new AdaptiveRateLimiter(
      {
        windowMs: 60000,
        max: 100,
      },
      {
        baseLimit: 100,
        maxLimit: 200,
        minLimit: 50,
        adaptationFactor: 0.5,
        loadThreshold: 0.7,
      }
    );

    // Set low system load
    limiter.setSystemLoadMonitor(() => 0.3); // 30% load

    let limitAdapted = false;
    limiter.on('limitAdapted', (data) => {
      expect(data.newLimit).toBeGreaterThanOrEqual(data.oldLimit);
      limitAdapted = true;
    });

    setTimeout(() => {
      expect(limitAdapted).toBe(true);
      done();
    }, 6000);
  });
});

describe('RateLimiterManager', () => {
  let manager: RateLimiterManager;

  beforeEach(() => {
    manager = new RateLimiterManager();
  });

  afterEach(() => {
    manager.destroy();
  });

  test('should manage multiple rate limiters', () => {
    const globalLimiter = new RateLimiter(RATE_LIMIT_PRESETS.moderate);
    const apiLimiter = new RateLimiter(RATE_LIMIT_PRESETS.strict);

    manager.setGlobalLimiter(globalLimiter);
    manager.addLimiter('api', apiLimiter);

    const results = manager.checkLimits('test-user', 'api');
    expect(results).toHaveLength(2); // Global + resource-specific
    expect(results.every(r => r.allowed)).toBe(true);
  });

  test('should detect rate limiting across multiple limiters', () => {
    const strictLimiter = new RateLimiter({
      windowMs: 60000,
      max: 1,
    });

    manager.addLimiter('strict', strictLimiter);

    // Use up the strict limit
    manager.checkLimits('test-user', 'strict');

    const { limited,  results  } = manager.isRateLimited('test-user', 'strict');
    expect(limited).toBe(true);
    expect(results.some(r => !r.allowed)).toBe(true);
  });

  test('should provide combined statistics', () => {
    const limiter1 = new RateLimiter(RATE_LIMIT_PRESETS.moderate);
    const limiter2 = new RateLimiter(RATE_LIMIT_PRESETS.strict);

    manager.setGlobalLimiter(limiter1);
    manager.addLimiter('api', limiter2);

    // Make some requests
    manager.checkLimits('user1', 'api');
    manager.checkLimits('user2');

    const stats = manager.getStats();
    expect(stats.global).toBeDefined();
    expect(stats.api).toBeDefined();
  });

  test('should reset all limiters for a user', () => {
    const limiter1 = new RateLimiter({ windowMs: 60000, max: 1 });
    const limiter2 = new RateLimiter({ windowMs: 60000, max: 1 });

    manager.setGlobalLimiter(limiter1);
    manager.addLimiter('api', limiter2);

    // Use up limits
    manager.checkLimits('test-user', 'api');
    manager.checkLimits('test-user', 'api'); // Should be blocked

    let { limited } = manager.isRateLimited('test-user', 'api');
    expect(limited).toBe(true);

    // Reset all
    manager.resetAll('test-user');

    ({ limited } = manager.isRateLimited('test-user', 'api'));
    expect(limited).toBe(false);
  });
});

describe('Rate Limit Presets', () => {
