  'AGPL-3.0-only',
  'GPL-1.0',
  'GPL-2.0-only',
  'GPL-3.0-only'
];

class LicenseChecker {
  constructor() {
    this.violations = [];
    this.reviewRequired = [];
    this.approved = [];
    this.unknown = [];
  }

  async checkLicenses() {
    console.log('🔍 Scanning dependencies for license compliance...\n');

    try {
      // Get dependency tree with licenses
      const output = execSync('pnpm licenses list --json', { 
        encoding: 'utf8',
        cwd: process.cwd()
      });

      const licenseData = JSON.parse(output);

      for (const [packageName, info] of Object.entries(licenseData)) {
        this.analyzeLicense(packageName, info);
      }

      this.generateReport();

    } catch (error) {
      console.error('❌ Failed to check licenses:', error.message);

      // Fallback: check package.json files directly
      console.log('📦 Falling back to package.json analysis...');
      await this.checkPackageJsonLicenses();
    }
  }

  analyzeLicense(packageName, info) {
    const license = info.license || info.licenses;
    const licenseString = Array.isArray(license) ? license.join(' OR ') : license;

    if (!licenseString || licenseString === 'UNKNOWN') {
      this.unknown.push({ package: packageName, license: 'UNKNOWN', ...info });
      return;
    }

    // Check for prohibited licenses
    if (this.containsProhibitedLicense(licenseString)) {
      this.violations.push({ 
        package: packageName, 
        license: licenseString, 
        severity: 'HIGH',
        reason: 'Prohibited license detected',
        ...info 
      });
      return;
    }

    // Check for licenses requiring review
    if (this.requiresReview(licenseString)) {
      this.reviewRequired.push({ 
        package: packageName, 
        license: licenseString,
        reason: 'License requires legal review',
        ...info 
      });
      return;
    }

    // Check for approved licenses
    if (this.isApproved(licenseString)) {
      this.approved.push({ package: packageName, license: licenseString, ...info });
      return;
    }

    // Unknown/custom license
    this.unknown.push({ 
      package: packageName, 
      license: licenseString,
      reason: 'Custom or unrecognized license',
      ...info 
    });
  }

  containsProhibitedLicense(licenseString) {
    return PROHIBITED_LICENSES.some(prohibited => 
      licenseString.toLowerCase().includes(prohibited.toLowerCase())
    );
  }

  requiresReview(licenseString) {
    return REVIEW_REQUIRED.some(review => 
      licenseString.toLowerCase().includes(review.toLowerCase())
    );
  }

  isApproved(licenseString) {
    return APPROVED_LICENSES.some(approved => 
      licenseString.toLowerCase().includes(approved.toLowerCase())
    );
  }

  async checkPackageJsonLicenses() {
    const nodeModulesPath = path.join(process.cwd(), 'node_modules');

    if (!fs.existsSync(nodeModulesPath)) {
      console.log('⚠️  node_modules not found. Run pnpm install first.');
      return;
    }

    const packages = fs.readdirSync(nodeModulesPath)
      .filter(dir => !dir.startsWith('.'))
      .slice(0, 100); // Limit for performance

    for (const packageDir of packages) {
      const packageJsonPath = path.join(nodeModulesPath, packageDir, 'package.json');

      if (fs.existsSync(packageJsonPath)) {
        try {
          const packageJson = JSON.parse(fs.readFileSync(packageJsonPath, 'utf8'));
          const license = packageJson.license || packageJson.licenses;

          this.analyzeLicense(packageJson.name || packageDir, {
            license,
            version: packageJson.version,
            repository: packageJson.repository
          });
        } catch (error) {
          // Skip invalid package.json files
        }
      }
    }
  }

  generateReport() {
    console.log('📊 License Compliance Report');
    console.log('=' .repeat(50));

    // Summary
    console.log(`\n📈 Summary:`);
    console.log(`  ✅ Approved: ${this.approved.length}`);
    console.log(`  ⚠️  Review Required: ${this.reviewRequired.length}`);
    console.log(`  ❌ Violations: ${this.violations.length}`);
    console.log(`  ❓ Unknown: ${this.unknown.length}`);

    // Violations (critical)
    if (this.violations.length > 0) {
      console.log(`\n❌ LICENSE VIOLATIONS (${this.violations.length}):`);
      this.violations.forEach(violation => {
        console.log(`  • ${violation.package} (${violation.license})`);
        console.log(`    Reason: ${violation.reason}`);
      });
    }

    // Review required
    if (this.reviewRequired.length > 0) {
      console.log(`\n⚠️  REVIEW REQUIRED (${this.reviewRequired.length}):`);
      this.reviewRequired.forEach(item => {
        console.log(`  • ${item.package} (${item.license})`);
      });
    }

    // Unknown licenses
    if (this.unknown.length > 0) {
      console.log(`\n❓ UNKNOWN LICENSES (${this.unknown.length}):`);
      this.unknown.slice(0, 10).forEach(item => {
        console.log(`  • ${item.package} (${item.license || 'UNKNOWN'})`);
      });
      if (this.unknown.length > 10) {
        console.log(`  ... and ${this.unknown.length - 10} more`);
      }
    }

    // Generate compliance files
    this.generateComplianceFiles();

    // Exit code
    const exitCode = this.violations.length > 0 ? 1 : 0;

    if (exitCode === 0) {
      console.log('\n✅ License compliance check passed!');
    } else {
      console.log('\n❌ License compliance check failed!');
      console.log('Please resolve violations before proceeding.');
    }

    process.exit(exitCode);
  }

  generateComplianceFiles() {
    const complianceDir = path.join(process.cwd(), 'compliance');

    if (!fs.existsSync(complianceDir)) {
      fs.mkdirSync(complianceDir, { recursive: true });
    }

    // Generate detailed report
    const report = {
      timestamp: new Date().toISOString(),
      summary: {
        approved: this.approved.length,
        reviewRequired: this.reviewRequired.length,
        violations: this.violations.length,
        unknown: this.unknown.length
      },
      approved: this.approved,
      reviewRequired: this.reviewRequired,
      violations: this.violations,
      unknown: this.unknown
    };

    fs.writeFileSync(
      path.join(complianceDir, 'license-report.json'),
      JSON.stringify(report, null, 2)
    );

    // Generate NOTICE file for attribution
    const noticeContent = this.generateNoticeFile();
    fs.writeFileSync(
      path.join(complianceDir, 'NOTICE.txt'),
      noticeContent
    );

    console.log('\n📄 Compliance files generated:');
    console.log('  • compliance/license-report.json');
    console.log('  • compliance/NOTICE.txt');
  }

  generateNoticeFile() {
    let notice = 'VTT - Virtual Tabletop\n';
    notice += '=' .repeat(50) + '\n\n';
    notice += 'This software includes the following third-party components:\n\n';

    this.approved.forEach(item => {
      notice += `${item.package}\n`;
      notice += `License: ${item.license}\n`;
      if (item.repository) {
        notice += `Repository: ${typeof item.repository === 'string' ? item.repository : item.repository.url}\n`;
      }
      notice += '\n';
    });

    return notice;
  }
}

// Run license check
if (require.main === module) {
  const checker = new LicenseChecker();
  checker.checkLicenses().catch(console.error);
}

module.exports = LicenseChecker;

#!/usr/bin/env node

import { execSync  } from 'child_process';
import fs from 'fs';

// Get staged files
const stagedFiles = execSync('git diff --cached --name-only', { encoding: 'utf-8' })
  .trim()
  .split('\n')
  .filter(Boolean)
  .filter(file => fs.existsSync(file)); // Only process files that exist

if (stagedFiles.length === 0) {
  console.log('No staged files found');
  process.exit(0);
}

// Filter files by type and exclude node_modules
const jsFiles = stagedFiles.filter(file => 
  /\.(js|jsx|ts|tsx)$/.test(file) && !file.includes('node_modules')
);
const otherFiles = stagedFiles.filter(file => 
  /\.(css|md|yml|yaml|json)$/.test(file) && !file.includes('node_modules')
);

try {
  // Run prettier on JS files
  if (jsFiles.length > 0) {
    console.log('Running prettier on JS/TS files...');
    execSync(`npx prettier --write ${jsFiles.join(' ')}`, { stdio: 'pipe' });

    console.log('Running eslint on JS/TS files...');
    try {
      execSync(`npx eslint --fix ${jsFiles.join(' ')}`, { stdio: 'pipe' });
    } catch (eslintError) {
      // Continue even if ESLint has warnings
      console.log('ESLint completed with warnings (continuing...)');
    }

    // Re-stage the fixed files
    execSync(`git add ${jsFiles.join(' ')}`);
  }

  // Run prettier on other files
  if (otherFiles.length > 0) {
    console.log('Running prettier on other files...');
    execSync(`npx prettier --write ${otherFiles.join(' ')}`, { stdio: 'pipe' });

    // Re-stage the fixed files
    execSync(`git add ${otherFiles.join(' ')}`);
  }

  console.log('✓ All files processed successfully');
} catch (error) {
  console.error('✗ Linting failed:', error.message);
  process.exit(1);
}

/**
 * Core authentication service
 */

import { User, _Session, AuthTokens, LoginRequest, RegisterRequest, JWTPayload, AuthContext } from './types';
import { JWTManager } from './JWTManager';
import { PasswordManager } from './PasswordManager';
import { SessionManager } from './SessionManager';
import { UserRepository } from './UserRepository';

export class AuthService {
  private jwtManager: JWTManager;
  private passwordManager: PasswordManager;
  private sessionManager: SessionManager;
  private userRepository: UserRepository;

  constructor(
    jwtManager: JWTManager,
    passwordManager: PasswordManager,
    sessionManager: SessionManager,
    userRepository: UserRepository
  ) {
    this.jwtManager = jwtManager;
    this.passwordManager = passwordManager;
    this.sessionManager = sessionManager;
    this.userRepository = userRepository;
  }

  async login(request: LoginRequest, ipAddress?: string, userAgent?: string): Promise<AuthTokens> {
    const { email,  password,  rememberMe = false  } = request;

    // Find user by email
    const user = await this.userRepository.findByEmail(email);
    if (!user) {
      throw new Error('Invalid credentials');
    }

    // Verify password
    if (!user.passwordHash) {
      throw new Error('Invalid credentials');
    }
    const isValidPassword = await this.passwordManager.verify(password, user.passwordHash);
    if (!isValidPassword) {
      throw new Error('Invalid credentials');
    }

    // Check if user is active
    if (!user.isActive) {
      throw new Error('Account is disabled');
    }

    // Generate tokens
    const tokens = await this.generateTokens(user, rememberMe);

    // Create session
    await this.sessionManager.createSession({
      userId: user.id,
      token: tokens.accessToken,
      refreshToken: tokens.refreshToken,
      expiresAt: new Date(Date.now() + tokens.expiresIn * 1000),
      ipAddress,
      userAgent,
    });

    // Update last login
    await this.userRepository.updateLastLogin(user.id);

    return tokens;
  }

  async register(request: RegisterRequest): Promise<User> {
    const { email,  username,  password,  displayName  } = request;

    // Check if user already exists
    const existingUser = await this.userRepository.findByEmail(email);
    if (existingUser) {
      throw new Error('User already exists');
    }

    const existingUsername = await this.userRepository.findByUsername(username);
    if (existingUsername) {
      throw new Error('Username already taken');
    }

    // Validate password strength
    this.passwordManager.validatePassword(password);

    // Hash password
    const passwordHash = await this.passwordManager.hash(password);

    // Create user
    const user = await this.userRepository.create({
      email,
      username,
      displayName,
      passwordHash,
      isEmailVerified: false,
      isActive: true,
      roles: ['player'], // Default role
    });

    // Send verification email (implement separately)
    await this.sendVerificationEmail(user);

    return user;
  }

  async refreshToken(refreshToken: string): Promise<AuthTokens> {
    // Verify refresh token
    const payload = await this.jwtManager.verifyRefreshToken(refreshToken);

    // Get session
    const session = await this.sessionManager.getSessionByRefreshToken(refreshToken);
    if (!session || session.expiresAt < new Date()) {
      throw new Error('Invalid or expired refresh token');
    }

    // Get user
    const user = await this.userRepository.findById(payload.sub);
    if (!user || !user.isActive) {
      throw new Error('User not found or inactive');
    }

    // Generate new tokens
    const tokens = await this.generateTokens(user);

    // Update session
    await this.sessionManager.updateSession(session.id, {
      token: tokens.accessToken,
      refreshToken: tokens.refreshToken,
      expiresAt: new Date(Date.now() + tokens.expiresIn * 1000),
    });

    return tokens;
  }

  async logout(token: string): Promise<void> {
    const session = await this.sessionManager.getSessionByToken(token);
    if (session) {
      await this.sessionManager.deleteSession(session.id);
    }
  }

  async logoutAll(userId: string): Promise<void> {
    await this.sessionManager.deleteAllUserSessions(userId);
  }

  async verifyToken(token: string): Promise<AuthContext> {
    try {
      const payload = await this.jwtManager.verifyAccessToken(token);

      // Get user to ensure they're still active
      const user = await this.userRepository.findById(payload.sub);
      if (!user || !user.isActive) {
        throw new Error('User not found or inactive');
      }

      const permissions = new Set<string>(payload.permissions);

      return {
        user,
        isAuthenticated: true,
        permissions,
        hasPermission: (_permission: string) => permissions.has(permission),
        hasRole: (role: string) => payload.roles.includes(role),
      };
    } catch (_error) {
      return {
        user: null,
        isAuthenticated: false,
        permissions: new Set(),
        hasPermission: () => false,
        hasRole: () => false,
      };
    }
  }

  async changePassword(userId: string, currentPassword: string, newPassword: string): Promise<void> {
    const user = await this.userRepository.findById(userId);
    if (!user) {
      throw new Error('User not found');
    }

    // Verify current password
    if (!user.passwordHash) {
      throw new Error('Invalid current password');
    }
    const isValidPassword = await this.passwordManager.verify(currentPassword, user.passwordHash);
    if (!isValidPassword) {
      throw new Error('Invalid current password');
    }

    // Validate new password
    this.passwordManager.validatePassword(newPassword);

    // Hash new password
    const newPasswordHash = await this.passwordManager.hash(newPassword);

    // Update password
    await this.userRepository.updatePassword(userId, newPasswordHash);

    // Logout all sessions to force re-login
    await this.logoutAll(userId);
  }

  async resetPassword(email: string): Promise<void> {
    const user = await this.userRepository.findByEmail(email);
    if (!user) {
      // Don't reveal if user exists
      return;
    }

    // Generate reset token
    const resetToken = await this.jwtManager.generatePasswordResetToken(user.id);

    // Store reset token (implement token storage)
    await this.userRepository.storePasswordResetToken(user.id, resetToken);

    // Send reset email (implement separately)
    await this.sendPasswordResetEmail(user, resetToken);
  }

  async confirmPasswordReset(token: string, newPassword: string): Promise<void> {
    // Verify reset token
    const payload = await this.jwtManager.verifyPasswordResetToken(token);

    // Check if token is still valid in storage
    const isValidToken = await this.userRepository.isValidPasswordResetToken(payload.sub, token);
    if (!isValidToken) {
      throw new Error('Invalid or expired reset token');
    }

    // Validate new password
    this.passwordManager.validatePassword(newPassword);

    // Hash new password
    const passwordHash = await this.passwordManager.hash(newPassword);

    // Update password
    await this.userRepository.updatePassword(payload.sub, passwordHash);

    // Clear reset token
    await this.userRepository.clearPasswordResetToken(payload.sub);

    // Logout all sessions
    await this.logoutAll(payload.sub);
  }

  async verifyEmail(token: string): Promise<void> {
    const payload = await this.jwtManager.verifyEmailVerificationToken(token);
    await this.userRepository.markEmailAsVerified(payload.sub);
  }

  private async generateTokens(user: User, longExpiry = false): Promise<AuthTokens> {
    const permissions = user.roles.flatMap(role => role.permissions.map(p => p.name));
    const roleNames = user.roles.map(role => role.name);

    const payload: Omit<JWTPayload, 'iat' | 'exp' | 'iss'> = {
      sub: user.id,
      email: user.email,
      username: user.username,
      roles: roleNames,
      permissions,
    };

    const expiresIn = longExpiry ? 30 * 24 * 60 * 60 : 60 * 60; // 30 days or 1 hour
    const accessToken = await this.jwtManager.generateAccessToken(payload, expiresIn);
    const refreshToken = await this.jwtManager.generateRefreshToken(payload);

    return {
      accessToken,
      refreshToken,
      expiresIn,
    };
  }

  private async sendVerificationEmail(user: User): Promise<void> {
    // Generate verification token
    const verificationToken = await this.jwtManager.generateEmailVerificationToken(user.id);

    // TODO: Implement email sending
    console.log(`Send verification email to ${user.email} with token: ${verificationToken}`);
  }

  private async sendPasswordResetEmail(user: User, resetToken: string): Promise<void> {
    // TODO: Implement email sending
    console.log(`Send password reset email to ${user.email} with token: ${resetToken}`);
  }
}

/**
 * User data repository interface and implementation
 */

import { User, Role, Permission } from './types';

export interface CreateUserRequest {
  email: string;
  username: string;
  displayName: string;
  passwordHash: string;
  isEmailVerified: boolean;
  isActive: boolean;
  roles: string[];
}

export interface UserRepository {
  create(data: CreateUserRequest): Promise<User>;
  findById(id: string): Promise<User | null>;
  findByEmail(email: string): Promise<User | null>;
  findByUsername(username: string): Promise<User | null>;
  updateLastLogin(id: string): Promise<void>;
  updatePassword(id: string, passwordHash: string): Promise<void>;
  markEmailAsVerified(id: string): Promise<void>;
  storePasswordResetToken(id: string, token: string): Promise<void>;
  isValidPasswordResetToken(id: string, token: string): Promise<boolean>;
  clearPasswordResetToken(id: string): Promise<void>;
  updateProfile(id: string, data: Partial<Pick<User, 'displayName' | 'avatar'>>): Promise<User>;
  deactivateUser(id: string): Promise<void>;
  activateUser(id: string): Promise<void>;
  assignRole(userId: string, roleId: string): Promise<void>;
  removeRole(userId: string, roleId: string): Promise<void>;
}

export interface RoleRepository {
  create(name: string, permissions: string[]): Promise<Role>;
  findById(id: string): Promise<Role | null>;
  findByName(name: string): Promise<Role | null>;
  findAll(): Promise<Role[]>;
  update(id: string, data: Partial<Pick<Role, 'name'>>): Promise<Role>;
  delete(id: string): Promise<void>;
  addPermission(roleId: string, permissionId: string): Promise<void>;
  removePermission(roleId: string, permissionId: string): Promise<void>;
}

export interface PermissionRepository {
  create(name: string, resource: string, action: string): Promise<Permission>;
  findById(id: string): Promise<Permission | null>;
  findByName(name: string): Promise<Permission | null>;
  findAll(): Promise<Permission[]>;
  findByResource(resource: string): Promise<Permission[]>;
  delete(id: string): Promise<void>;
}

// In-memory implementation for development/testing
export class InMemoryUserRepository implements UserRepository {
  private users: Map<string, User & { passwordHash: string; isActive: boolean; passwordResetTokens: Set<string> }> = new Map();
  private roles: Map<string, Role> = new Map();
  private permissions: Map<string, Permission> = new Map();
  private userIdCounter = 1;
  private roleIdCounter = 1;
  private permissionIdCounter = 1;

  constructor() {
    this.initializeDefaultData();
  }

  async create(data: CreateUserRequest): Promise<User> {
    const id = `user_${this.userIdCounter++}`;
    const now = new Date();

    const userRoles = await Promise.all(
      data.roles.map(roleName => this.findRoleByName(roleName))
    );

    const validRoles = userRoles.filter((role): role is Role => role !== null);

    const user = {
      id,
      email: data.email,
      username: data.username,
      displayName: data.displayName,
      isEmailVerified: data.isEmailVerified,
      createdAt: now,
      updatedAt: now,
      roles: validRoles,
      passwordHash: data.passwordHash,
      isActive: data.isActive,
      passwordResetTokens: new Set<string>(),
    };

    this.users.set(id, user);

    // Return user without sensitive data
    const { _passwordHash, _passwordResetTokens,  ...publicUser  } = user;
    return publicUser;
  }

  async findById(id: string): Promise<User | null> {
    const user = this.users.get(id);
    if (!user) return null;

    const { _passwordHash, _passwordResetTokens,  ...publicUser  } = user;
    return publicUser;
  }

  async findByEmail(email: string): Promise<User | null> {
    for (const user of this.users.values()) {
      if (user.email === email) {
        const { passwordHash, _passwordResetTokens,  ...publicUser  } = user;
        return { ...publicUser, passwordHash }; // Include passwordHash for auth
      }
    }
    return null;
  }

  async findByUsername(username: string): Promise<User | null> {
    for (const user of this.users.values()) {
      if (user.username === username) {
        const { _passwordHash, _passwordResetTokens,  ...publicUser  } = user;
        return publicUser;
      }
    }
    return null;
  }

  async updateLastLogin(id: string): Promise<void> {
    const user = this.users.get(id);
    if (user) {
      user.lastLoginAt = new Date();
      user.updatedAt = new Date();
    }
  }

  async updatePassword(id: string, passwordHash: string): Promise<void> {
    const user = this.users.get(id);
    if (user) {
      user.passwordHash = passwordHash;
      user.updatedAt = new Date();
    }
  }

  async markEmailAsVerified(id: string): Promise<void> {
    const user = this.users.get(id);
    if (user) {
      user.isEmailVerified = true;
      user.updatedAt = new Date();
    }
  }

  async storePasswordResetToken(id: string, token: string): Promise<void> {
    const user = this.users.get(id);
    if (user) {
      user.passwordResetTokens.add(token);
    }
  }

  async isValidPasswordResetToken(id: string, token: string): Promise<boolean> {
    const user = this.users.get(id);
    return user ? user.passwordResetTokens.has(token) : false;
  }

  async clearPasswordResetToken(id: string): Promise<void> {
    const user = this.users.get(id);
    if (user) {
      user.passwordResetTokens.clear();
    }
  }

  async updateProfile(id: string, data: Partial<Pick<User, 'displayName' | 'avatar'>>): Promise<User> {
    const user = this.users.get(id);
    if (!user) {
      throw new Error('User not found');
    }

    if (data.displayName !== undefined) user.displayName = data.displayName;
    if (data.avatar !== undefined) user.avatar = data.avatar;
    user.updatedAt = new Date();

    const { _passwordHash, _passwordResetTokens,  ...publicUser  } = user;
    return publicUser;
  }

  async deactivateUser(id: string): Promise<void> {
    const user = this.users.get(id);
    if (user) {
      user.isActive = false;
      user.updatedAt = new Date();
    }
  }

  async activateUser(id: string): Promise<void> {
    const user = this.users.get(id);
    if (user) {
      user.isActive = true;
      user.updatedAt = new Date();
    }
  }

  async assignRole(userId: string, roleId: string): Promise<void> {
    const user = this.users.get(userId);
    const role = this.roles.get(roleId);

    if (user && role) {
      if (!user.roles.some(r => r.id === roleId)) {
        user.roles.push(role);
        user.updatedAt = new Date();
      }
    }
  }

  async removeRole(userId: string, roleId: string): Promise<void> {
    const user = this.users.get(userId);

    if (user) {
      user.roles = user.roles.filter(r => r.id !== roleId);
      user.updatedAt = new Date();
    }
  }

  private async findRoleByName(name: string): Promise<Role | null> {
    for (const role of this.roles.values()) {
      if (role.name === name) {
        return role;
      }
    }
    return null;
  }

  private initializeDefaultData(): void {
    // Create default permissions
    const permissions = [
      { name: 'game:create', resource: 'game', action: 'create' },
      { name: 'game:edit', resource: 'game', action: 'edit' },
      { name: 'game:delete', resource: 'game', action: 'delete' },
      { name: 'game:join', resource: 'game', action: 'join' },
      { name: 'character:create', resource: 'character', action: 'create' },
      { name: 'character:edit', resource: 'character', action: 'edit' },
      { name: 'character:view', resource: 'character', action: 'view' },
      { name: 'map:edit', resource: 'map', action: 'edit' },
      { name: 'combat:manage', resource: 'combat', action: 'manage' },
      { name: 'users:manage', resource: 'users', action: 'manage' },
    ];

    permissions.forEach(p => {
      const id = `perm_${this.permissionIdCounter++}`;
      this.permissions.set(id, { id, ...p });
    });

    // Create default roles
    const adminPermissions = Array.from(this.permissions.values());
    const gmPermissions = adminPermissions.filter(p => !p.name.startsWith('users:'));
    const playerPermissions = adminPermissions.filter(p => 
      p.name.includes('character:') || p.name === 'game:join'
    );

    this.roles.set('role_1', {
      id: 'role_1',
      name: 'admin',
      permissions: adminPermissions,
    });

    this.roles.set('role_2', {
      id: 'role_2',
      name: 'game_master',
      permissions: gmPermissions,
    });

    this.roles.set('role_3', {
      id: 'role_3',
      name: 'player',
      permissions: playerPermissions,
    });
  }
}

